INSERT INTO `Jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `mangaPanini`, `tvBrasileira`, `range`, `rank`, `handSeals`) VALUES
(1514, 'Liberação de Água: Técnica da Grande Cachoeira', 'Esta técnica estende água sobre ondas em larga escala e sobe até várias dezenas de metros de altura. Em seguida, ele leva para o chão em uma cascata grande, como uma cachoeira gigantesca. Ao fazê-lo, ele se assemelha a uma onda enorme, com grande poder que pode escavar o solo. O que resta após a utilização dessa técnica é o que lembra nada menos do que a sequência de uma catástrofe natural. Este é um ninjutsu consideravelmente avançado, e ativar esta técnica exige uma boa quantidade de chakra.', '水遁・大瀑布の術すいとん・だいばくふのじゅつ', 'Suiton: Daibakufu no Jutsu', 'Liberação de Água: Técnica da Grande Cachoeira', NULL, 'Estilo da Água: Técnica do Vórtex Gigante', 'Estilo Água: Jutsu Vórtex Gigante', 'Médio a Longo alcance', 'Rank A', 'Tigre → Boi → Macaco → Coelho → Carneiro → Javali → Boi → Cavalo → Macaco → Tigre → Cao → Tigre → Cobra → Tigre → Boi → Macaco → Coelho → Passaro'),
(1515, 'Liberação de Água: Técnica da Lâmina de Água', 'Essa é uma técnica de Liberação de Água criada por Rasa, o Quarto Kazekage.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1516, 'Liberação de Água: Técnica da Tranca de Água', 'Tobirama reúne uma quantidade considerável de água em torno do oponente, que segura-o no lugar, dessa forma, prendendo-o pelos braços e puxando-o para baixo. Logo em seguida, com o inimigo vulnerável, o Hokage pode emendar outro ataque, como, por exemplo, saltar ao ar e atacar o oponente com uma broca de água extremamente poderosa.', '水遁・水錠の術すいとん・すいじょうのじゅつ', 'Suiton: Suijō no Jutsu', 'Liberação de Água: Técnica da Tranca de Água', 'Estilo Água: Jutsu do Bloqueio de Água', NULL, NULL, 'Curto alcance', NULL, NULL),
(1517, 'Liberação de Água: Técnica de Bolhas', 'Depois de soprar dezenas de bolhas, o usuário manipula-as para cercar o inimigo em todas as direções. Apenas a menor quantidade de contato físico com o adversário obriga essas bolhas a estourarem, resultando em uma pequena, mas poderosa explosão quando o fazem. O usuário pode então fazer todas detonarem simultaneamente com um estalar de seus dedos.', '水遁・泡沫の術', 'Suiton: Hōmatsu no Jutsu', 'Liberação de Água: Técnica de Bolhas', NULL, 'Estilo da Água: Técnica das Bolhas', NULL, 'Todos os alcances', NULL, NULL),
(1518, 'Liberação de Água: Técnica de Bolhas — Grande Rio', 'Saiken, ou Utakata transformado em sua besta, libera dois tiros de ácido corrosivo e termina com uma saraivada de bolhas explosivas na direção de seu oponente.', '水遁・泡沫の術　大河', 'Suiton: Hōmatsu no Jutsu — Taiga', 'Liberação de Água: Técnica de Bolhas — Grande Rio', 'Estilo Água: Jutsu do Grande Rio de Bolhas', NULL, NULL, 'Curto alcance', NULL, NULL),
(1519, 'Liberação de Água: Técnica de Ocultação na Chuva', 'O usuário se mistura com a chuva sem ser detectado pelo o inimigo, semelhante à Liberação de Terra: Técnica de Ocultação nas Pedras. Quando usado por Tsurugi, ele pode invocar a chuva em torno dele.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1520, 'Liberação de Água: Técnica de Ocultação na Garoa', 'O usuário gera uma garoa para ocultar a si mesmo e aos seus companheiros presentes em uma garoa. No entanto, a técnica é inútil em locais com pouca umidade no ar, uma vez que a impede de ter os efeitos naturais da técnica.', '水遁・霧雨隠れの術', 'Suiton: Kirisamegakure no Jutsu', 'Liberação de Água: Técnica de Ocultação na Garoa', NULL, NULL, NULL, NULL, NULL, 'Selo do Confronto'),
(1521, 'Liberação de Água: Técnica de Ocultação na Água', 'O usuário se mistura na água, para não ser detectado pelo inimigo, semelhante à Liberação de Terra: Técnica de Ocultação nas Pedras. Foi mostrado que esta técnica é útil mesmo em água tão rasa quanto uma poça de água, como pode ser visto quando os Irmãos Demônios usaram para emboscar Tazuna e seus guarda-costas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1522, 'Liberação de Água: Técnica do Espelho de Água', 'O usuário cria uma grande forma circular de água, lisa e larga, na frente de seu oponente. Sua superfície age como um espelho, refletindo perfeitamente os alvos que atacam. Logo, então, ele engancha e gira a 90° o \'espelho\' com o seu bō, fazendo com que os reflexos emerjam e se materializem a partir do espelho, onde colidem com os inimigos espelhados. Como estas reflexões usam exatamente as mesmas técnicas do inimigo — independentemente do seu tipo — eles contra-atacam seus respectivos alvos completamente, enquanto repelindo-los no processo. No entanto, no momento do impacto, os reflexos se dispersam novamente em água.', '水遁・水鏡の術', 'Suiton: Mizukagami no Jutsu', 'Liberação de Água: Técnica do Espelho de Água', NULL, 'Estilo da Água: Técnica do Espelho d\'Água', NULL, 'Curto alcance', NULL, NULL),
(1523, 'Liberação de Água: Técnica do Exército do Demônio de Água', 'Esta técnica reúne uma grande quantidade de água para formar uma grande corrente espiral de água. Ele pode ser guiada para atingir um alvo em vários ângulos. Esta técnica foi utilizada por dois desconhecidos shinobi de Konoha para apagar um incêndio na Residência do Hokage.', '水遁・水軍水魔の術すいとん・すいぐんすいまのじゅつ', 'Suiton: Suigun Suima no Jutsu', 'Liberação de Água: Técnica do Exército do Demônio de Água', NULL, NULL, NULL, 'Curto à Médio Alcance', NULL, 'Javali → Cao → Cavalo → Tigre'),
(1524, 'Liberação de Água: Técnica do Grande Braço de Água', 'Ao colocar a Técnica de Hidratação em uso prático, os músculos do braço ficam temporariamente ampliados e reforçados. A umidade é recolhida a partir do corpo inteiro e prensada ​​em todo o braço, como uma bomba instantânea. No entanto, uma vez que é essencial para controlar adequadamente o equilíbrio de umidade no interior do corpo, o grau de dificuldade desta técnica é muito elevado. A técnica dá ao usuário força sobre-humana, capaz de romper os muros de pedra e portas de aço. No anime, Suigetsu pode aplicar esta técnica em sua perna.', '水遁・豪水腕の術すいとん・ごうすいわんのじゅつ', 'Suiton: Gōsuiwan no Jutsu', 'Liberação de Água: Grande Braço de Água', NULL, NULL, NULL, NULL, 'Rank B', NULL),
(1525, 'Liberação de Água: Técnica do Grande Projétil de Tubarão', 'A Liberação de Água: Técnica do Grande Projétil de Tubarão é uma versão mais forte da Liberação de Água: Técnica do Projétil do Tubarão de Água.', '水遁・大鮫弾の術すいとん・だいこうだんのじゅつ', 'Suiton: Daikōdan no Jutsu', 'Liberação de Água: Técnica do Grande Projétil de Tubarão', 'Estilo Água! Jutsu da Super Bomba do Tubarão Aquático[2]', 'Estilo da Água: Grande Tubarão Explosivo', 'Estilo Água: Jutsu da Super Bomba Tubarão', 'Longo alcance', 'Rank A', 'Batida → Cobra → Selos Especificos'),
(1526, 'Liberação de Água: Técnica do Projétil de Água', 'Esta é uma técnica de Liberação de Água altamente versátil. Depois de amassar chakra no estômago, o utilizador expele uma grande quantidade de água sob a forma de uma corrente forte até o objetivo pretendido.', '水遁・水弾の術', 'Suiton: Suidan no Jutsu', 'Liberação de Água: Técnica do Projétil de Água', NULL, 'Estilo da Água: Técnica do Jato D\'Água', NULL, 'Todos os alcances', NULL, 'Tigre → Boi → Tigre → Rato'),
(1527, 'Liberação de Água: Técnica do Projétil do Dragão de Água', 'A Liberação de Água: Técnica do Projétil do Dragão de Água molda uma grande quantidade de água em um gigante e poderoso dragão, que atinge o adversário com poder formidável, causando dano físico. O lugar ideal para usá-lo é perto de um corpo de água, mas se a habilidade do usuário permite, é possível usá-lo mesmo em um lugar onde não há nenhuma. A quantidade de água utilizada será em proporção com a habilidade do usuário.', '水遁・水龍弾の術すいとん・すいりゅうだんのじゅつ', 'Suiton: Suiryūdan no Jutsu', 'Liberação de Água: Técnica do Projétil do Dragão de Água', 'Estilo Água: Jutsu do Dragão da Água', 'Estilo da Água: Ataque do Dragão Marinho', 'Estilo Água: Jutsu Dragão de Água', 'Todos os alcances', 'Rank B', 'Boi → Macaco → Lebre → Rato → Javali → Passaro → Boi → Cavalo → Pássaro → Rato → Tigre → Cao → Tigre → Cobra → Boi → Carneiro → Serpente → Javali → Carneiro → Rato → Yang Água → Macaco → Pássaro → Dragão → Pássaro → Boi → Cavalo → Carneiro → Tigre → Cobra → Rato → Macaco → Lebre → Javali → Dragão → Carneiro → Rato → Boi → Macaco → Pássaro → Yang Água → Rato → Javali → Pássaro'),
(1528, 'Liberação de Água: Técnica do Projétil do Tubarão de Água', 'O usuário molda a água na forma de um grande tubarão e, empurrando a sua mão para a frente, envia-o para o oponente em alta velocidade, resultando em um impacto poderoso. O usuário pode também montar no tubarão dentro de água para aumentar drasticamente a sua velocidade de natação.', '水遁・水鮫弾の術', 'Suiton: Suikōdan no Jutsu', 'Liberação de Água: Técnica do Projétil do Tubarão de Água', 'Estilo Água: Bomba do Tubarão', 'Estilo da Água: Técnica da Bomba do Tubarão', 'Estilo Água: Jutsu da Bomba do Tubarão de Água', 'Curto a Médio alcance', 'Rank B', 'Tigre → Boi → Dragao → Coelho → Cão → Passaro → Rato → Selo de Clonagem → Dragão → Carneiro'),
(1529, 'Liberação de Água: Técnica do Tiro do Tubarão de Água', 'Kisame bate com sua mão no chão, exala água e liberta vários tubarões de água, que se movimentam rapidamente rente ao solo, um após o outro ou mesmo em conjunto. Quando chegam perto do inimigo, os tubarões voam ferozmente contra ele, causando dano.', '水遁・水鮫散弾の術', 'Suiton: Suikōsandan no Jutsu', 'Liberação de Água: Técnica do Tiro do Tubarão de Água', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1530, 'Liberação de Água: Técnica do Turbilhão do Dragão de Água', 'Gengetsu distrai o oponente e lança um turbilhão de água e envia-o para o alto.', NULL, NULL, NULL, 'Estilo Água: Jutsu do Turbilhão do Dragão de Água', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1531, 'Liberação de Água: Técnica dos Projéteis Dispersados', 'O usuário envia uma grande rajada de balas de água em seu oponente, entregando vários golpes sequenciais.', '水遁・散弾の術', 'Suiton: Sandan no Jutsu', 'Liberação de Água: Técnica dos Projéteis Dispersados', 'Estilo Água: Jutsu dos Tiros Dispersos', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1532, 'Liberação de Água: \'Onda Selvagem\'', 'Depois de reunir e converter o chakra em seu estômago, o usuário ataca seu adversário com um jato d\'água como uma grande onda. Ele da origem a uma considerável quantidade de água, suficiente para encharcar toda a área.', '水遁・波濤すいとん・はとう', 'Suiton: Hatō', 'Liberação de Água: \'Onda Selvagem\'', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1533, 'Liberação de Água: Água Cortante Contínua', 'Depois de amassar chakra no estômago e convertê-lo em água, o usuário expele-o como um fluxo de alta pressão a fim de cortar o oponente mesmo a longas distâncias. Caso a pressão da água não seja o suficiente para cortar o alvo, ela espalha-se normalmente.', '水遁・水絶斬', 'Suiton: Suizetsusan', 'Liberação de Água: Água Cortante Contínua', 'Estilo da Água: Corte de Água', NULL, NULL, 'Todos os alcances', NULL, 'Selo de confronto'),
(1534, 'Liberação de Água da Marionete: Dança da Chuva Mística', 'O usuário envolve o inimigo entre suas marionetes, que liberam fios de chakra de seu interior para restringir seus movimentos e, para, em seguida, expelir água contra o alvo. Esta técnica foi especialmente eficaz em Gaara, impedindo de manipular sua areia ora lama.', '傀儡水遁・妖雨の舞', 'Kugutsu Suiton: Yōu no Mai', 'Liberação de Água da Marionete: Dança da Chuva Mística', NULL, NULL, 'Marionete do Estilo Água: Dança Mística', 'Médio a Longo alcance', NULL, NULL),
(1535, 'Liberação do Selo de Teletransporte de Quatro Cantos', 'Minato faz os selos manuais requeridos da técnica e o selo é desfeito. Esta técnica foi citada como totalmente forte, uma vez que apenas Jiraiya e Minato poderiam realizá-la.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Rato → Macaco → Passaro → Cavalo → Tigre'),
(1536, 'Ligamento da Língua Lutadora', 'Depois de focar chakra do senjutsu nela, a língua de Shima cresce até o seu limite máximo e é reformulada com um rosto. A língua, então, busca o aroma de seu alvo. Quando o alvo é encontrado, ela enlaça-o para evitar a fuga e libera ácido no corpo do alvo. Depois que o inimigo é encontrado Fukasaku pode usar o Corte da Língua Lutadora nele.', '舌戦縛ぜっせんばく', 'Zessenbaku', 'Ligamento da Língua Lutadora', 'Língua de Ligação', NULL, 'Jutsu de Detecção de Criatura', 'Todos os alcances', 'Rank –', 'Carneiro'),
(1537, 'Limbo: Lado da Prisão', 'Limbo: Lado da Prisão é uma técnica que usa o poder do Rinnegan.', '輪墓・辺獄', 'Rinbo: Hengoku', 'Roda de Sepultura: Lado da Prisão', NULL, 'Crepúsculo do Limbo', NULL, 'Todos os alcances', 'Rank –', NULL),
(1538, 'Linha Relâmpago', 'Ao concentrar chakra relâmpago em seu chifre, o Kuchiyose Final criar rajadas de relâmpago e atira-as contra seus oponentes, lançando-os longe.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1539, 'Locomoção Vulcânica', 'O golem que Momoshiki Ōtsutsuki cria usando a Liberação de Lava se desfaz para o estado de rocha fundida e se locomove rapidamente pela superfície do solo, se reconstruindo em outro lugar. A técnica pode ser usada tanto para pegar o adversário desprevenido e lhe desferir um ataque surpresa, como também em forma de esquiva ou retirada.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1540, 'Lodo Ácido', 'O usuário cospe uma pequena porção de um líquido roxo no alvo, que evapora em forma ácida, quando em contato com o alvo. A técnica é mostrada sendo poderosa o suficiente para incapacitar Kushina Uzumaki, embora ela tenha apenas sido atingida com uma porção pequena do líquido em sua perna.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1541, 'Lonicera Japônica', 'Kimimaro cria um pico de osso no joelho e ataca o oponente, depois cria ossos em suas mãos e ataca-o novamente e rapidamente vai para baixo do oponente, criando picos de ossos em suas costas, empalando o oponente.', 'スイカズラ', 'Suikazura', 'Lonicera Japônica', 'Madressilva', NULL, NULL, 'Curto alcance', NULL, NULL),
(1542, 'Lorentz Gun', 'O usuário conecta dois pontos distintos ao liberar uma corrente elétrica, criando um campo eletromagnético. Qualquer objeto metálico, como uma kunai, que adentrar o campo irá acelerar rapidamente de um ponto para outro, chegando a ultrapassar a velocidade do som. Isso torna o objeto incrivelmente destrutivo, uma vez que para chegar ao seu destino, uma kunai seria capaz de atravessar árvores em seu caminho e, por fim, destruir um penhasco.', '電磁誘導投擲', 'Rōrentsugan', 'Disparo por Indução Eletromagnética', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1543, 'Lua Obscura', 'Kakashi invoca todos seus oito ninken para segurar o oponente, logo em seguida, ele entrega um corte mortal rapidamente com sua espada.', '朧月おぼろずき', 'Oborozuki', NULL, 'Lua Obscura', NULL, NULL, 'Curto alcance', NULL, NULL),
(1544, 'Lua Remanescente', 'Obito inicia desferindo em seu oponente uma série de golpes com suas Esferas da Busca da Verdade, transforma-as em uma lâmina, desfere um rápido corte em seu inimigo e então ele transforma a lâmina em uma esfera, que se torna grande, prende o inimigo e depois explode.', '残月ざんげつ', 'Zangetsu', 'Lua Remanescente', 'Visibilidade da Lua do Amanhecer', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1545, 'Luar', 'Sasuke ataca com sua espada, apunhala-a no chão, equilibra-se sobre ela e chuta o adversário longe. Seu chute é usado normalmente na parte superior do abdômen até a área do pescoço.', '月影つきかげ', 'Tsukikage', 'Luar', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1546, 'Lullaby Downtown', 'Killer B fica de ponta-cabeça no ar e começa a rodar repetidamente para atacar o inimigo com os pés. Ele ainda pode utilizar uma de suas espadas para um dano mais mortal.', 'ララバイダウンタウン', 'Rarabaidauntaun', 'Cantiga para Descansar em Paz', NULL, NULL, NULL, NULL, NULL, NULL),
(1547, 'Luz Rotatória da Folha', 'Rock Lee começa com uma rasteira para lançar o oponente para o alto, gira chutando o oponente no rosto antes de terminar com um poderoso chute para baixo, que irá atirar o oponente contra o chão, emitindo uma feixe de luz pelo caminho.', '木ノ葉旋光', 'Konoha Senkō', 'Luz Rotatória da Folha', 'Luz Espiral da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1548, 'Lótus Frontal', 'Esta técnica requer a abertura do primeiro dos Oito Portões, dando ao utilizador cinco vezes a sua força normal. O utilizador lança o oponente para o ar e depois usa a Sombra da Folha Dançante para chegar até as costas do oponente. Uma vez atrás do oponente, o usuário o prende e leva-o girando em alta velocidade de cabeça para baixo em direção ao chão, soltando-o no último minuto. Porque ele usa um dos portões de chakra, o usuário fica extremamente cansado, tornando esta técnica uma \'faca de dois gumes\'.', '表蓮華おもてれんげ', 'Omote Renge', 'Lótus Frontal', NULL, 'Lótus Exterior', 'Lótus Primária', 'Curto alcance', 'Rank B', NULL),
(1549, 'Lótus Frontal Individual', 'Uma versão singular de sua técnica parente, Lee em vez disso começa a girar por si mesmo e cria ventos fortes ao redor dele e mergulha para baixo sobre o adversário.', '一人表蓮華ひとりおもてれんげ', 'Hitori Omote Renge', 'Lótus Frontal Individual', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1550, 'Lótus Frontal: Oito Trigramas Palmas Giratórias do Céu', 'Esta técnica combina defesa com uma força de ataque final, para efeitos devastadores. Em primeiro lugar, Neji pula no ar, enquanto Lee envolve-o em suas bandagens. Ao mesmo tempo em que Lee realiza a Lótus Primária, Neji libera chakra de todo o seu corpo. Esta técnica consiste na união das Oito Trigramas Palmas Giratórias do Céu de Neji, junto com a Lótus Frontal de Lee. Os resultados são devastadores.', '表莲华・八卦掌回天おもてれんげ・はっけしょうかいてん', 'Omote Renge: Hakkeshō Kaiten', 'Lótus Frontal: Oito Trigramas Palmas Giratórias do Céu', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1551, 'Lótus Oculta: Em Direção ao Sol Poente', 'Após entrar no estado de Oito Portões, Lee corre em direção ao seu oponente, e em seguida ele salta e libera uma aura amarela fazendo o chão trincar no processo, e em seguida ele agarra seu oponente, e colide em várias formações rochosas, sua velocidade é tão grande que distorce o espaço ao seu redor, e em seguida ele arrasta o corpo de seu oponente vários metros do chão, com seu oponente inconsciente, Lee fica parado e observa o sol se pondo.', NULL, NULL, NULL, 'Lótus Oculta: Em Direção ao Sol Poente', NULL, NULL, 'Curto alcance', NULL, NULL),
(1552, 'Lótus Reversa', 'Esta técnica é uma versão mais avançada e destrutiva da Lótus Frontal, que requer pelo menos três dos Oito Portões abertos (em todos os casos em que têm sido utilizado, cinco portões são abertos). Uma vez que os portões estão abertos, o usuário joga o alvo no ar e o ataca a uma velocidade sobre-humana antes de batê-lo com um braço e um pé, mandando o alvo direto para o chão. A técnica é geralmente fatal devido à velocidade e ao poder por trás do ataque.', '裏蓮華うられんげ', 'Ura Renge', 'Lótus Reversa', NULL, NULL, 'Lótus Oculta', 'Curto a Médio alcance', 'Rank A', NULL),
(1553, 'Lâmina Curva: Espada da Mão Celestial', 'Sasuke infunde sua espada com um relâmpago de cor púrpura, aumentando em várias vezes seu poder ofensivo. Com isso ele é capaz de eletrocutar seus oponentes e deixá-los inconscientes, bem como derrotar vários dos dragões de sangue de Chino em rápida sucessão.', '瞬刃・天手刀しゅんじん・てんてがたな', 'Shunjin: Tentegatana', 'Lâmina Curva: Espada da Mão Celestial', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1554, 'Lâmina Fluida', 'Shizune dá um chute de fiação varredura no adversário e, antes de girar de volta para sua posição original, ela corta o adversário com um Bisturi de Chakra.', '流刃', 'Ryūjin', 'Lâmina Fluida', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1555, 'Lâmina Shuriken Imparável', 'O usuário lança a Kubikiribōchō no oponente como uma shuriken, causando grandes danos.', NULL, NULL, NULL, 'Lâmina Shuriken Imparável', NULL, NULL, 'Longo alcance', NULL, NULL),
(1556, 'Lâmina da Chama Uchiha: Possessão de Fogo', 'Izuna dispara uma série de bolas de fogo contra o oponente. Em seguida, ele corta o inimigo com sua espada.', 'うちは炎刃・火憑うちはえんじん・ひひょう', 'Uchiha Enjin: Hihyō', 'Lâmina da Chama Uchiha: Possessão de Fogo', NULL, NULL, NULL, 'Curto alcance', NULL, 'Tigre'),
(1557, 'Lâmina da Luz Branca', 'Usando o Sabre de Chakra da Luz Branca, o usuário desfere um poderoso corte diagonal descendente, que deixa um rastro de chakra branco.', '白光刃はっこうじん', 'Hakkōjin', 'Lâmina da Luz Branca', 'Espada da Luz Branca', NULL, NULL, 'Curto alcance', NULL, NULL),
(1558, 'Lâmina de Areia', 'O usuário cria uma grande lâmina de areia, que pode ser atirada ou empunhada a fim de romper as defesas do oponente.', '砂の刃', 'Suna no Ha', 'Lâmina de Areia', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1559, 'Lâmina de Chakra: Linha Reta', 'Após infundir chakra em sua faca trincheira e aplicar a transformação da forma para alongar seu alcance, Asuma desfere um corte no pescoço do oponente para decapitá-lo.', 'チャクラ刀・真一文字', 'Chakura Tō: Maichimonji', 'Lâmina de Chakra: Linha Reta', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1560, 'Lâmina de Vento', 'Esta técnica é um ataque cortante preciso, onde o usuário emite chakra das pontas de seus dedos e o materializa em uma arma invisível que ataca o inimigo em uma rajada de vento. Se a Técnica da Foice da Doninha pode ser considerada como um enxame de kunai de vento, então esta técnica é semelhante a uma espada longa e inevitável, a qual pode deixar o corpo do alvo mutilado. As lâminas de vento podem ser criadas e lançadas, como projéteis, em rápida sucessão.', '風の刃', 'Kaze no Yaiba', 'Lâmina de Vento', NULL, NULL, NULL, 'Curto a Médio alcance', 'Rank A', NULL),
(1561, 'Lírio da Aranha Negra', 'Depois de prender o inimigo com a Técnica da Costura pelas Sombras, o usuário manipula as sombras para fazer com que os inimigos sejam puxados até um ponto determinado. Esta técnica pode ser usada para botar os inimigos dentro da faixa de alcance da Técnica da Performance de Dança da Mente de Inoichi.', '黒彼岸花', 'Kuro Higanbana', 'Lírio da Aranha Negra', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Rato'),
(1562, 'Lírio de Tigre', 'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente repetidamente com ataques de sua espada óssea, executa um ataque de sua cauda e depois finaliza seu oponente com um poderoso remate de suas espinhas dorsais.', '鬼百合', 'Oniyuri', 'Lírio de Tigre', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1563, 'Lírio de Tigre (Ultimate Ninja 3)', 'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente repetidamente com ataques de sua espada óssea, executa um ataque de sua cauda e depois finaliza seu oponente com um poderoso remate de suas espinhas dorsais.', '鬼百合', 'Oniyuri', 'Lírio de Tigre', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1564, 'Lírio de Tigre (Ultimate Ninja Storm)', 'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, cria picos ósseos que saem de seus punhos e joga o oponente para cima, chuta o oponente para cima, e finaliza com um ataque de sua broca óssea.', '鬼百合', 'Oniyuri', 'Lírio de Tigre', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1565, 'Lápis de Liberação de Relâmpago', 'Através de um controle de chakra refinado, o usuário flui chakra de Liberação de Relâmpago em um lápis, aumentando sua agudeza ao ponto dele ser capaz de perfurar em linha reta, um grande tronco de árvore e até mesmo através do solo.', '雷遁えんぴつらいとんえんぴつ', 'Raiton Enpitsu', 'Lápis de Liberação de Relâmpago', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1566, 'Machado', 'Usando sua transformação, Jūgo pode formar um machado enorme em seu braço. O machado é forte o suficiente para bloquear espadas reais sem danos.', '鉞', 'Masakari', 'Machado', 'Machado de Batalha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1567, 'Machado do Julgamento', 'Kinshiki materializa um machado de chakra e desfere um corte transversal diagonal em seu oponente, então ele salta e o finaliza com um corte descendente com força o suficiente para demolir o terreno próximo.', NULL, NULL, NULL, 'Machado do Julgamento', NULL, NULL, 'Curto alcance', NULL, NULL),
(1568, 'Malabarista Letal', 'O Malabarista Letal é uma técnica de ambidestro utilizada por Isono em uma competição de comida.', '致死ジャグラー', 'Chishi Jagurā', 'Malabarista Letal', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1569, 'Maldição Mandala', 'Depois de realizar vários selos de mão, o usuário é capaz de realizar uma antiga arte ninja que só é conhecida por algumas gerações do clã Fūma. Arashi era capaz de realizar esta técnica depois de absorver os corpos de Jigumo e Kamikiri. A técnica prende a vítima em uma gaiola de chakra em forma de pirâmide que vai lentamente se contraindo o quanto o usuário mover as mãos para ficarem mais próximas. Qualquer um pego na técnica, é contraído até ser totalmente esmagado.', '呪縛曼荼羅じゅばくまんだら', 'Jubaku Mandara', 'Maldição Mandala', NULL, NULL, 'Arte Ninja Fuuma: Feitiço da Mandala', NULL, NULL, 'Ativar: Javali → Cao → Rato → Rato → Cobra → Passaro → Cão → Cão → Macaco → Dragão → Javali → Javali → Rato → Javali → Dragão,Disparar: Juntar as Maos,Comprimir: Selo Desconhecido[Nota 1]'),
(1570, 'Maldição da Marionete', 'Hiruko usa essa técnica para assumir o controle de pessoas. Ela pode ser ativada muito tempo após a técnica ser aplicada pela primeira vez. A aparência é um olho vermelho com um outro círculo nele que se assemelha a pupila, com quatro linhas derivadas a partir da linha de cima do olho e duas grandes linhas grossas decorrentes a partir do lado do olho. Este poder parece controlar as mentes de outros como ele apareceu na testa de Kakashi, desapareceu e, em seguida, assumiu o controle sobre seu corpo. Os olhos da pessoa que for pego por esta técnica ficam vermelhos enquanto eles estão sendo controlados.', '傀儡の呪いくぐつののろい', 'Kugutsu no Noroi', 'Maldição da Marionete', NULL, NULL, NULL, NULL, NULL, NULL),
(1571, 'Manipulação da Força de Mil Mãos', 'A partir do braço esquerdo da marionete, abra-se um compartimento que libera outros. Estes compartimentos são incorporados com inúmeros selos de invocação, suscitando um grande número de braços de marionetes longos que pode dobrar e acompanhar o adversário, em seguida, para esmagá-lo com sua força devastadora. Essa técnica pode ser impulsionada, tornando-se grande o suficiente para romper o solo ou desintegrar o corpo duma pessoa. Os braços são modificados com lanças, os quais também podem emitir gases venenosos; os fios são ligados com uma kunai-âncora ou regulares. Sasori pôde substituir o braço por uma serra circular.', '千手操武せんじゅそうぶ', 'Senju Sōbu', 'Manipulação da Força de Mil Mãos', NULL, NULL, 'Manipulação dos Mil Braços', 'Curto a Médio alcance', 'Rank C', NULL),
(1572, 'Manipulação da Força de Mil Mãos: Impulso', 'Sasori ataca seu oponente usando sua marionete do Terceiro Kazekage, fazendo com que múltiplos braços entrem em erupção a partir dos selos em seu braço esquerdo, dizimando o oponente no meio do ar.', '千手操武・突', 'Senju Sōbu: Totsu', 'Manipulação da Força de Mil Mãos: Impulso', 'Mil Mãos: Impulso', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1573, 'Manipulação de Cartas', 'Uma técnica que emprega o uso de cartas que podem ser manipuladas de acordo com a vontade do usuário. Usando isso, o usuário pode cortar objetos e criar barreiras que são capazes de suportar shuriken.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1574, 'Manipulação do Moinho de Vento de Lâminas Triplas', 'O usuário anexa primeiro as cordas previamente na shuriken e na kunai jogadas lançando depois disso, em seguida, uma shuriken é puxada para trás usando uma árvore como um eixo, com base no princípio do Ioiô. A outra shuriken e kunai são iscas, e o verdadeiro ataque da shuriken que é puxado para trás usando a força da kunai lançada. O truque para tornar esta técnica bem sucedida é de alguma forma ser capaz de ler os movimentos do inimigo, que normalmente exigiria o Sharingan.', '操風車三ノ大刀', 'Sōfūshasan no Tachi', 'Manipulação do Moinho de Vento de Lâminas Triplas', NULL, NULL, 'Ataque Triplo Sharingan Moinho de Vento', 'Médio alcance', 'Rank C', NULL),
(1575, 'Manipulação do Tempo', 'Dependendo de sua emoção, Yota pode criar chuva, neve ou raios, mesmo que seja um dia ensolarado. Quando Yota fica triste começa a chover, quando Yota fica feliz o sol brilha ou começa a nevar, e quando Yota fica com raiva, ele é capaz de convocar um raio roxo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcance', NULL, NULL),
(1576, 'Manto da Raposa Demônio', 'Em sua forma de quatro caudas, Naruto enterra sua mão no chão criando uma réplica de seu braço que emerge da terra e, em seguida, agarra e bate o inimigo no chão. Naruto então dispara uma rajada pequena, mas violenta, que se expande rapidamente no impacto, indo muito além do inimigo, deixando uma enorme cratera no chão.', '妖狐の衣', 'Yōko no Koromo', 'Manto da Raposa Demônio', 'Manto da Nove-Caudas', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1577, 'Marcação Dinâmica', 'Nesta técnica, Akamaru urina no alvo, marcando-o com um forte odor e permite que ele e Kiba o sigam facilmente pelo cheiro. Akamaru é muito hábil nisso, sendo capaz de atingir alvos com muita precisão, e até mesmo vários alvos de uma vez. Este é um pré-requisito para usar a Presa Lobo Presa, com a velocidade da técnica, se torna impossível para o usuário rastrear alvos. A urina pode também ser útil para temporariamente cegar o alvo se atingir os seus olhos. Essa técnica também pode ser feita enquanto Kiba e Akamaru se transformam usando o Transformação Combinada da Besta Humana: Lobo de Duas Cabeças.', 'ダイナミックマーキング', 'Dainamikku Mākingu', 'Marcação Dinâmica', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1578, 'Marcação de Areia de Ferro', 'Shinki usa sua areia de ferro para marcar seu inimigo. Quando este estiver marcado, todos os ataques posteriores serão atraídos para o ponto definido por Shinki, eliminando a possibilidade de falha.', '砂鉄のマーキングさてつのマーキング', 'Satetsu no Mākingu', 'Marcação de Areia de Ferro', NULL, NULL, NULL, NULL, NULL, NULL),
(1579, 'Marionete Armada', 'Kankurō manipula a marionete Corvo e faz com que sua cabeça abra e revele uma afiada lâmina-agulha. Com esse membro, ele pode afastá-lo para perfurar um alvo específico, bem como ele pode banhar a lâmina em veneno, tornando o golpe mais nocivo ao sistema imunológico de seu alvo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1580, 'Marionete Preparada: Oito Ondas de Agulhas', 'Sasori, enquanto dentro de sua marionete Hiruko, faz o boneco cuspir um fluxo rápido de inúmeras agulhas de sua boca, numa forma semelhante a uma metralhadora. Ele também pode incluir agulhas banhadas em veneno em meio às normais.', '仕込み傀儡・針々八波', 'Shikomi Kugutsu: Shinshin Happa', 'Fantoche Preparado: Oito Ondas de Agulhas', NULL, NULL, NULL, 'Todos os alcances', 'Rank S', NULL),
(1581, 'Marionete Shinobi dos Quatro Símbolos', 'Os membros da Akatsuki recebem um selo especial em forma de humano criado por Nagato que tem uma forma física conhecida como uma marionete, para lutar ou parar um inimigo. Essa marionete vai para um local distante para criar numerosas cópias de si mesmo. A única maneira de parar a técnica é interceptando novas criações dessas marionetes.', '四象偶忍', 'Shishō Gūnin', 'Marionete Shinobi dos Quatro Símbolos', 'Shinobi Vazio', NULL, NULL, NULL, NULL, NULL),
(1582, 'Marionete: Ataque Rotativo', 'Controle de Chakra de Ryūmyaku', '傀儡・回旋撃', 'Kugutsu: Kaisengeki', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1583, 'Marionete: Ataque de Punição da Primavera', 'Usando sua Performance da Marionete: Conquista Habilidosa com um Corpo Humano, Chiyo lança Sakura para o alto. Sakura então usa sua imensa força para atacar o adversário por cima. Esta técnica foi poderosa o suficiente para destruir a defesa final de Sasori, a marionete Hiruko.', '傀儡·春誅撃くぐつ·はるちゅうげき', 'Kugutsu: Haruchūgeki', 'Marionete: Ataque de Punição da Primavera', 'Marionete: Punição do Ataque de Primavera', NULL, NULL, 'Curto alcance', NULL, NULL),
(1584, 'Marionete: Chuva de Verão', 'Kankurō utiliza uma de suas marionetes para percorrer o campo de batalha e liberar bombas, que explodem depois de algum tempo ou em contato com o oponente. A fim de aprimorar o alcance e poder destrutivo da técnica, além do Corvo, Kankurō também envia o Formiga Negra para plantar mais bombas.', '傀儡・五月雨', 'Kugutsu: Samidare', 'Marionete: Chuva de Verão', 'Marionete: Chuva de Maio', NULL, NULL, NULL, NULL, NULL),
(1585, 'Marionete: Dança da Flor de Cerejeira', 'Chiyo usa seus Fios de Chakra para controlar Sakura e várias dezenas de Kunai ao mesmo tempo. Sakura corre até o oponente e Chiyo lança as kunai atrás dela, usando Sakura para fazer com que o oponente não veja as kunai. Ao mesmo segundo, Sakura é puxada para trás com o fio, fazendo com que o oponente seja atacado pelas kunai. Então, Sakura pula bem alto e desce preparando o Impacto da Flor de Cerejeira, com Chiyo impulsionando-a com os fios, para Sakura descer mais rápido. Finalmente, Sakura soca o oponente criando uma cratera gigante.', '傀儡・桜舞', 'Kugutsu: Sakura no Mai', 'Marionete: Dança da Flor de Cerejeira', 'Marionete: Dança da Cerejeira de Flor', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1586, 'Marionete: Degeneração', 'Kankurō envia rapidamente sua marionete Corvo, que corre em direção ao oponente. A marionete usa suas seis mãos, e em seguida, ataca e esfaqueia o oponente, jogando-o no chão extremamente ferido. A marionete Corvo então, continua seus ataques e bate no oponente, enquanto ainda caído no chão pelo esfaqueamento anterior.', '傀儡・堕羅繰', 'Kugutsu: Daraku', 'Marionete: Degeneração', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1587, 'Marionete: Inesgotáveis Lâminas Explosivas', 'Com os quatro braços de Corvo abertos é disparado uma rajada de kunai com papéis explosivos anexados à elas, que caem sobre o adversário e explodem com o impacto.', '傀儡・無尽爆刃', 'Kugutsu: Mujin Bakujin', 'Marionete: Inesgotáveis Lâminas Explosivas', 'Lâmina de Explosões Inesgotáveis', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1588, 'Marionete: Lâmina da Broca Celestial Giratória', 'Kankurō faz Karasu brotar vários venenos cobertos com lâminas nos braços e na boca e envia-a girando em direção ao oponente como um tornado, cortando-os várias vezes. Depois de lançar o oponente para o ar, Karasu se explode perto deles. Karasu entretanto sai ileso da explosão.', '傀儡・廻刃天穿', 'Kugutsu: Kaijin Tensen', 'Marionete: Lâmina da Broca Celestial Giratória', 'Girando a Lâmina da Broca do Submundo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1589, 'Marionete: Vomitando Balas', 'Kankurō desanexa a cabeça de sua marionete, Corvo, assim como o adversário está se aproximando. Sua cabeça, em seguida, projeta um aumento de sua boca, apunhalando o adversário.', '傀儡・噴弾', 'Kugutsu: Fundan', 'Marionete: Vomitando Balas', 'Marionete: Vomitando Tiros', NULL, NULL, 'Curto alcance', NULL, NULL),
(1590, 'Marionetes Acidentadas', 'Desenvolvido por Orochimaru, permite ao usuário absorver parcialmente um cadáver morto recentemente, transformando-o em uma extensão do corpo do usuário. Apesar da absorção permitir ter acesso a técnicas do alvo, o uso das Marionetes Acidentadas pode deformar a mente do usuário e o corpo, fazendo com que o usuário fique altamente instável, com apenas um grande choque o fazendo retomar seus sentidos.', '死者傀儡', 'Shisha Kugutsu', 'Marionetes Acidentadas', NULL, NULL, 'Arte Ninja: Bonecos de Cadáveres', NULL, NULL, NULL),
(1591, 'Marretada', 'Ao bater sua Kabutowari no chão, Jinin Akebino faz com que a terra entre em erupção debaixo dos pés de seus oponentes. Essa técnica é efetiva para derrubar múltiplos oponentes.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1592, 'Martelo Sagrado', 'Kinshiki desfere vários socos fortalecidos com chakra em seu oponente, então ele manifesta um martelo de chakra e lhe dá uma martelada, atirando-o contra o chão.', NULL, NULL, NULL, 'Martelo Sagrado', NULL, NULL, 'Curto alcance', NULL, NULL),
(1593, 'Martelo da Super Besta Voadora', 'Yamato ataca o oponente com um pilar de madeira, jogando-o para o alvo, onde os falcões de tinta de Sai atacam o oponente e Yamato bate o oponente ao chão com outro pilar de madeira.', '超獣飛翔槌', 'Chōjū Hishō Tsui', 'Martelo da Super Besta Voadora', NULL, NULL, NULL, NULL, NULL, NULL),
(1594, 'Massa de Água Viscosa', 'Nurari extrai o líquido estranho de seu traje especial e forma-o em uma bola oval. Em seguida, ele joga em seu oponente para prendê-lo, neutralizá-lo, ou para fixá-lo para baixo. Ele também pode extraí-lo para formar uma membrana para parar os ataques de projéteis de entrada.', '粘水塊ねんすいかい', 'Nensuikai', 'Massa de Água Viscosa', NULL, NULL, 'Água Pegajosa', 'Curto a Médio alcance', NULL, NULL),
(1595, 'Matança Silenciosa', 'A Matança Silenciosa, como o nome sugere, é simplesmente um método muito tranquilo de matar um oponente, geralmente por trás com uma lâmina. Além disso, uma vez que o usuário se desloca em silêncio, seus ataques não podem ser defendidos, já que não podem ser previstos. O usuário, muitas vezes, corta a garganta do oponente, impedindo qualquer grito que ele passa dar.', '無音殺人術', 'Sairento Kiringu', 'Técnica de Assassinato sem Som', NULL, 'Técnica do Assassinato Silencioso', 'Assassinato Silencioso', NULL, NULL, NULL),
(1596, 'Medicina Negra', 'Esta técnica requer o chakra das serpentes de Yomi para ser realizada. As cobras manipulam alguns equipamentos cirúrgicos do usuário, e executam algum tipo de procedimento cirúrgico que mata a vítima sem deixar marcas.', '暗黒医療', 'Ankoku Ijutsu', 'Medicina Negra', NULL, NULL, 'Jutsu de Medicina Negra', 'Curto alcance', NULL, NULL),
(1597, 'Melodia Estridente', 'Após se separar de Sakon, Ukon surge em alta velocidade atrás do oponente, executando um rápido e brutal golpe com as duas mãos, que lança o oponente no chão com grande força.', NULL, NULL, NULL, 'Melodia Estridente', NULL, NULL, 'Curto alcance', NULL, NULL),
(1598, 'Mente Ampla da Folha', 'Guy chuta seu oponente duas vezes — um por cima e outro por baixo, antes de tacá-lo no chão.', '木ノ葉豪放磊落', 'Konoha Gōhōrairaku', 'Mente Ampla da Folha de Árvore', 'Mente Ampla da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1599, 'Mergulho', 'Jūgo salta sobre o seu oponente compulsivamente e joga-o no chão, em seguida, com seu braço direito ele golpeia fatalmente seu oponente.', '急降下', 'Kyūkōka', 'Mergulho', 'Voo Picado', NULL, NULL, 'Curto alcance', NULL, NULL),
(1600, 'Mergulho Gordinho', 'Depois de realizar a Técnica do Super Tamanho Múltiplo, Chōji gigante salta no ar e cai sobre o oponente.', 'ポッチャリダイブ', 'Potchari Daibu', 'Mergulho Gordinho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1601, 'Meteoro do Instrumento Precioso Mortal', 'O Meteoro do Instrumento Precioso Mortal é uma habilidade de Bukijutsu especial de Matsuri com suas maestrias usando o Jōhyō.', '必殺宝具流星', 'Hissatsu Hōguryūsei', 'Meteoro do Instrumento Precioso Mortal', NULL, NULL, 'Precioso Instrumento', 'Curto alcance', NULL, NULL),
(1602, 'Mil Agulhas Voadoras de Água da Morte', 'Usando esta técnica, Haku reúne um pouco de água e produz mil agulhas afiadas e longas. Em seguida, ele as direciona para um alvo específico e em sequência, lança-as em alta velocidade em seu alvo. Antes que possa ser atacado, Haku salta para trás. Apesar das agulhas cercarem o alvo de todos os lados, elas não aparecem na região superior do alvo, disponibilizando uma rota de fuga para o alvo. Haku é capaz de realizar este jutsu com selos de uma mão, permitindo-lhe fixar um braço contra um ataque do oponente, impedindo-o de realizar técnicas no momento de realização.', '千殺水翔せんさつすいしょう', 'Sensatsu Suishō', 'Mil Agulhas Voadoras de Água da Morte', 'Mil Agulhas da Morte', NULL, 'Jutsu Secreto: Mil Agulhas Mortais', 'Curto alcance', 'Rank B', 'Selo do Confronto → Fogo → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Terra → Selo do Confronto'),
(1603, 'Mil Anos de Morte', 'Uma técnica muito simples, com um nome excessivamente dramático, Mil Anos de Morte, no qual o usuário simplesmente insere os dedos médios (semelhante ao selo de mão do Tigre) no ânus do adversário, causando constipação, dor e/ou constrangimento. Ele não tem nenhum efeito sobre alvos inconscientes. Testemunhas que vêem a técnica reagem estranhamente (Temari fica corada, Pakkun se pergunta se é realmente uma técnica e Gamakichi mostra um olhar de decepção), enquanto aqueles que usaram ou experimentaram a técnica mostram um grande temor em serem atacados com ela.', '千年殺し', 'Sennen Goroshi', 'Mil Anos de Morte', NULL, NULL, 'Jutsu do Dedo Secreto da Aldeia da Folha: Mil Anos de Morte', 'Curto alcance', 'Rank E', 'Tigre'),
(1604, 'Milhares de Agulhas da Morte', 'Kin controla uma vasta quantidade de agulhas de uma só vez utilizando fios, e ataca o inimigo.', NULL, NULL, NULL, 'Milhares de Agulhas da Morte', NULL, NULL, 'Médio alcance', NULL, NULL),
(1605, 'Minas Terrestres Explosivas', 'Depois de criar o dragão C2, Deidara cria muitas minas de argila que o dragão de argila desova através de sua boca e seu parceiro enterra-as no subsolo, permitindo assim que vítimas inocentes andem sobre as minas que explodem em seguida. Estas minas explodem automaticamente ao serem pisadas. Como tal, elas não estão sob o controle de Deidara.', '起爆地雷きばくじらい', 'Kibaku Jirai', 'Minas Explosivas', 'Mina Detonadora', NULL, NULL, NULL, NULL, 'Carneiro'),
(1606, 'Mini-Rasenshuriken', 'Naruto, enquanto está no Modo de Chakra da Nove Caudas, cria dois pequenos braços de chakra em seu dedo indicador, com os quais ele cria uma miniatura do Rasenshuriken. Embora a shuriken tenha menos poder de ataque do que a original, ainda é muito eficaz, sendo capaz de cortar uma perna dos clones fundidos do Exército de Zetsu Branco com relativa facilidade.', 'ミニ螺旋手裏剣', 'Mini-Rasenshuriken', 'Mini Shuriken Espiral', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1607, 'Mini Clone do Três-Caudas', 'Quando o Três-Caudas devora qualquer coisa, seu corpo produz milhares de clones menores de si mesmo com seu intestino. Esses clones de anticorpos, como atacam e devoram o organismo invasor, supostamente ajudam na digestão e destroem quaisquer perigos potenciais.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1608, 'Mini Rasengan', 'O usuário cria uma miniatura do Rasengan após ter perdido muito chakra. O efeito da técnica é desconhecido, pois após utilizá-la Naruto entrou em colapso.', 'ちび玉螺旋丸ちびたまらせんがん', 'Chibitama Rasengan', 'Mini Rasengan', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1609, 'Modificação Física Suave', 'Originalmente desenvolvida para atividades de espionagem, esta técnica pode ser feita após uma cirurgia e modificação do corpo real. O usuário se desloca suas articulações, em seguida, controla o seu corpo amolecido com chakra, permitindo-lhes se esticar e torcer qualquer parte de seu corpo em qualquer ângulo, alongando e até mesmo enrolando em torno de seu inimigo. Isto também pode ser utilizado para fazer o corpo do utilizador mais maleável e como de uma cobra. A versão de Orochimaru lhe permite esticar qualquer das partes de seu corpo bem além de seu comprimento normal e lhe permite mover-se na forma de uma cobra.', '軟の改造', 'Nan no Kaizō', 'Modificação Física Suave', NULL, NULL, NULL, NULL, 'Rank –', NULL),
(1610, 'Modo Borboleta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1611, 'Modo Manto de Vento', 'Temari usa a Liberação de Vento para criar uma corrente de ar em volta de si, que é capaz de repelir projéteis e aumenta seus ataques relacionado a vento.', '風の衣モードかぜのころもモード', 'Kaze no Koromo Mōdo', 'Modo Manto de Vento', 'Modo Dança do Leque Celestial[1]', NULL, NULL, NULL, NULL, NULL),
(1612, 'Modo Médico', 'Este modo envolve o usuário em um fluxo de chakra branco e lhes permite curar-se constantemente no meio da batalha, ao mesmo tempo, aumentando a sua força e velocidade. Com o tempo, este modo consome o chakra do usuário até que finalmente desaparece por si só, apesar do usuário ou, mais especificamente, Sakura e Ino, poderem cancelá-lo sempre que desejar. Shizune e Karin, no entanto, não podem.', '医療モード', 'Iryō Mōdo', 'Modo Médico', NULL, NULL, NULL, 'Curto a Longo alcance', NULL, NULL),
(1613, 'Modo Sannin', 'Este modo aumenta a todas as estatísticas do usuário. Também aumenta o alcance, poder e velocidade dos ataques do usuário. Enquanto estiver neste modo, o usuário ganha acesso ao seu Jutsu Final de Despertar.', '三忍モード', 'Sannin Modo', 'Modo Ninja Triplo', 'Modo Sannin', NULL, NULL, NULL, NULL, NULL),
(1614, 'Modo Sharingan', NULL, '写輪眼モード', 'Sharingan Mōdo', 'Modo do Olho Copiador Giratório', 'Modo Sharingan', NULL, NULL, NULL, NULL, NULL),
(1615, 'Modo Sábio', 'O Modo Sábio é um estado que pode ser ativado pela mistura de energia natural com o chakra, criando o chakra do senjutsu. Ele permite aos usuários explorar a força natural do mundo, concedendo novas técnicas para eles e permitindo-lhes poder aplicar o chakra do senjutsu em suas técnicas comuns.', '仙人モードせんいんモード', 'Sennin Mōdo', NULL, NULL, 'Honorável Modo Sábio', 'Modo Sennin', NULL, 'Rank –', NULL),
(1616, 'Modo Sábio dos Seis Caminhos', 'O Modo Sábio dos Seis Caminhos (六道仙人モード, Rikudō Sennin Mōdo) é uma técnica sábia utilizada por Naruto após receber o poder do Sábio dos Seis Caminhos. A aparência deste modo é muito semelhante ao Modo Sábio, exceto pela ausência da típica pigmentação padrão. Como Naruto utiliza este modo combinado com o chakra de Kurama, seus olhos adquirem uma combinação das pupilas característicos dos sapos com as da besta.', '六道仙人モードりくどうせんいんモード', 'Rikudō Sennin Mōdo', 'Modo Sábio do Seis Caminhos', NULL, NULL, NULL, NULL, 'Rank –', NULL),
(1617, 'Modo Tsukuyomi', 'Essa técnica é uma variação do Tsukuyomi, um genjutsu de alto nível. O usuário transforma o Tsukuyomi em uma barreira que retarda o oponente para baixo. Isso não acontece fisicamente, mas sim mentalmente. Faz o adversário pensar que está sendo retido por uma barreira. Enquanto estiver usando esta técnica, a vitalidade e o chakra de Itachi lentamente abaixam. Se Itachi usa essa técnica quando sua barra de saúde está baixa, ele poderia finalizar-se.', '月読モード', 'Tsukuyomi Mōdo', 'Modo de Leitor da Lua', 'Modo Tsukuyomi', NULL, NULL, 'Curto alcance', NULL, NULL),
(1618, 'Modo de Chakra de Liberação de Lava', 'O Modo de Chakra de Liberação de Lava é um estado elevado de Rōshi após a manifestação de uma das caudas de Son Goku.', '熔遁チャクラモードようとんチャクラモード', 'Yōton Chakura Mōdo', 'Modo de Chakra de Liberação de Lava', NULL, NULL, NULL, NULL, NULL, 'Juntar as Maos'),
(1619, 'Modo de Chakra de Liberação de Relâmpago', 'O Modo de Chakra de Liberação de Relâmpago é um estado elevado do Quarto e Terceiro Raikage.', '雷遁チャクラモードらいとんチャクラモード', 'Raiton Chakura Mōdo', 'Modo de Chakra de Liberação de Relâmpago', 'Armadura Relâmpago[2]', 'Armadura de Relâmpago', 'Manto de Relâmpago', NULL, 'Rank B', NULL),
(1620, 'Modo de Chakra de Liberação de Água', 'Suien cobre todo o seu corpo com chakra de natureza água, criando um robusto traje como uma armadura, que pode resistir a qualquer ataque físico feito por seus inimigos. A armadura também pode ser passada para outra pessoa.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1621, 'Modo de Chakra do Nove-Caudas', 'Modo de Chakra do Nove-Caudas é uma transformação exclusiva para os jinchūriki da Kurama.', '九尾チャクラモードきゅうびチャクラモード', 'Kyūbi Chakura Mōdo', 'Modo de Chakra do Nove-Caudas', NULL, 'Modo Chakra da Kyūbi', NULL, NULL, 'Rank –', 'Ativar: Bater as Maos[4],Desativar: Selo de Confronto[5]'),
(1622, 'Modo de Chakra do Tenseigan', 'O Modo de Chakra do Tenseigan é uma poderosa transformação única para aqueles que possuem o Tenseigan. Toneri Ōtsutsuki pode entrar nesta forma devido a ser um portador do dōjutsu.', '転生眼チャクラモードてんせいがんチャクラモード', 'Tenseigan Chakura Mōdo', 'Modo de Chakra do Olho da Reencarnação', NULL, NULL, NULL, NULL, NULL, 'Carneiro → Tigre → Javali → Tigre'),
(1623, 'Moinho de Vento', 'Kimimaro cria ossos nas mãos e joga o oponente para cima em movimentos circulares diagonais, aparecendo posteriormente acima do oponente, e girando para baixo como uma broca gigante, ao estender uma mão para cima de sua cabeça e outra para o lado, para rasgar o corpo do oponente.', '風車', 'Kazaguruma', 'Moinho de Vento', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1624, 'Monitoramento', 'O Monitoramento (監視, Kanshi) é uma capacidade possuída pelo Zetsu Negro que permite gravar eventos que ele testemunhou pessoalmente.', '監視', 'Kanshi', 'Monitoramento', NULL, NULL, NULL, NULL, NULL, NULL),
(1625, 'Montanha Flor–Frutífera', 'Enquanto em seu estado transformado, o usuário cria um pequeno vulcão que explode violentamente, enviando rocha derretida voando em todas as direções, como uma flor gigante. O calor da lava é o suficiente para queimar uma besta com cauda tão forte como Gyūki.', '花果山かかざん', 'Kakazan', 'Montanha Flor–Frutífera', 'Montanha Huaguo', 'Florescer do Vulcão', NULL, 'Todos os alcances', NULL, NULL),
(1626, 'Montanha de Ferro Inclinada', 'Depois de criar um clone de insetos, Shino e o clone correm em direção ao adversário e simplesmente o atacam. A força desse ataque é forte o suficiente para enviar o alvo voando longe deles.', '鉄山靠てつざんこう', 'Tetsuzankō', 'Montanha de Ferro Inclinada', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1627, 'Mordida Pegajosa', 'Aproveitando-se de seu corpo pegajoso e maleável, Saiken abre sua grande boca e avança no oponente, mordendo e mastiga-o. Mesmo sem dentes, a besta pode seriamente causar danos graves no inimigo a ponto de matá-lo. Além disso, a besta pode aumentar sua grande cabeça para engolir um corpo humano inteiro.', '粘咬', 'Nenkō', 'Mordida Pegajosa', NULL, NULL, NULL, 'Curto alcance', NULL, NULL);
