INSERT INTO `Jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `mangaPanini`, `tvBrasileira`, `range`, `rank`, `handSeals`) VALUES
(1628, 'Mordida de Bicho', 'Enquanto em seu estado transformado, Fū ataca o inimigo com um poderoso golpe cintilante, aparentemente mordendo-o no processo.', '蟲食い', 'Mushikui', 'Mordida de Inseto', NULL, 'Devorador de Insetos', NULL, 'Curto alcance', NULL, NULL),
(1629, 'Motim Barulhento: Desenho de Imitação da Super Besta da Imagem do Pergaminho', 'Naruto e Sai executam uma série de ataques físicos em seu oponente. Naruto executa um chute, e Sai finaliza usando três leões de tinta.', '喧乱・超獣戯画絵巻', 'Kenran: Chōjū Giga Emaki', 'Motim Barulhento: Desenho de Imitação da Super Besta da Imagem do Pergaminho', 'Batalha Deslumbrante: Réplica do Pergaminho da Besta', NULL, NULL, 'Curto alcance', NULL, NULL),
(1630, 'Motim Encantador do Carro da Folha', 'Anko chuta seu oponente para o ar, em seguida chuta novamente mais alto e salta sobre seus ombros, girando-os até o chão.', '木ノ葉甘乱車', 'Konoha Kanransha', 'Motim Encantador do Carro da Folha de Árvore', 'Roda-gigante da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1631, 'Movimento de Taijutsu em Alta Velocidade', 'Um método de taijutsu onde o usuário pode mover-se em altas velocidades que são dificilmente acompanhadas a olho nu, que permite realizar uma série de ataques físicos consecutivos.', '体術運動超高速', 'Taijutsu Undō Chō Kōsoku', 'Movimento de Técnica Corporal em Alta Velocidade', 'Técnica Impulsionante[Nota 1]', NULL, NULL, NULL, NULL, NULL),
(1632, 'Mudança de Curso da Cor', 'Esta técnica é usada por Fūka para alternar a sua consciência entre os vários corpos que ela tem acesso. Ela precisa fazer isso por causa de seu Shikei Seppun: já que um único corpo não poderia lidar com tanto chakra, ela teve de usar vários para distribuir todo o chakra absorvido durante a sua vida. Desenrolando um pergaminho, ela esconde o seu corpo em um rápido movimento e muda para outro.', '色転進', 'Shiki Tenshin', 'Mudança da Cor de Curso', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1633, 'Muito Oculto: Veneno do Inseto Peçonhento', 'Torune pousa uma série de golpes no oponente, envenenando-o com seus Rinkaichū.', '極秘伝・毒蟲毒', 'Goku Hiden: Dokuchūdoku', 'Muito Oculto: Veneno do Inseto Peçonhento', 'Jutsu Extremo Secreto: Insetos Venenosos', NULL, NULL, 'Curto alcance', NULL, NULL),
(1634, 'Multiplicação Mútua dos Papéis Explosivos', NULL, '互乗起爆札', 'Gojō Kibaku Fuda', 'Multiplicação Mútua dos Papéis Explosivos', 'Conjunto de Papéis Bomba', 'Papéis Explosivos de Detonação Múltipla', NULL, 'Curto a Médio alcance', 'Rank A', 'Selo do Confronto'),
(1635, 'Muro de Lanças', 'Obito reúne as Esferas da Busca da Verdade de suas costas e forma uma parede que dispara uma série de espinhos contra seu oponente.', '槍衾やりぶすま', 'Yaribusuma', 'Muro de Lanças', 'Parede de Lanças', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1636, 'Muro de Ventos Rápidos', 'Após executar os selos de mão, Ashura cria um poderoso remoinho de vento em torno de si para se defender de ataques inimigos. Com isso ele foi capaz de se defender de uma técnica de relâmpago de Indra. A técnica também pode ser expandida para proteger outras pessoas próximas ao usuário.', '風迅壁', 'Fūjinheki', 'Muro de Ventos Rápidos', NULL, NULL, NULL, 'Curto alcance', NULL, 'Boi → Cao → Rato'),
(1637, 'Múltiplas Shuriken Fantasmas', 'Esta técnica é muito semelhante em termos de execução e resultado a Técnica do Clone da Sombra de Shuriken. É um ninjutsu cooperativo onde três usuários, depois de jogar shuriken em seu alvo e formar os selos de mão necessários, podem criar réplicas corporais de shuriken — transformando uma pequena quantidade de shuriken em uma grande quantidade em um instante e com as quais, bombardeiam o inimigo. Ao contrário da Técnica do Clone da Sombra de Shuriken, no entanto, esta técnica não faz uso da Técnica dos Clones das Sombras.', '幻影多重手裏剣', 'Gen\'ei Tajū Shuriken', 'Múltiplas Shuriken Fantasmas', NULL, NULL, NULL, 'Curto à Médio Alcance', NULL, 'Cao → Coelho → Macaco → Bater Palmas'),
(1638, 'Múltiplos Abraços Infinitos', 'Esta técnica tem exatamente o mesmo efeito que o Abraço Infinito, mas esta versão é muito maior e mais forte, uma vez que poderia completamente subjugar e incapacitar Naruto Uzumaki, apesar de ele usar o chakra da Kyūbi. O Grupo Magaki coloca seu adversário em uma barreira que empurra-o para baixo, impedindo-os de fugir e drenando seu chakra. Quanto mais esforço colocado na técnica, mais aumenta a sua capacidade de dominar e drenar chakra do alvo.', '多重無限抱擁たじゅうむげんほうよう', 'Tajū Mugen Hōyō', 'Múltiplos Abraços Infinitos', NULL, NULL, NULL, 'Curto à Médio alcance', NULL, 'Tigre + Cao + Cavalo + Dragão + Javali Simultaneamente'),
(1639, 'Múltiplos Canhões do Lótus Altruísta Conectados', 'Enquanto no segundo estado de sua Transformação do Selo Amaldiçoado, Jūgo faz crescer mais dos apêndices reforçados com jato, estendendo-os e usando-os para reunir chakra e liberar uma poderosa explosão de chakra que também abrange uma ampla área. À medida que o chakra é recolhido, ele em seguida, é liberado em grande velocidade, tornando-se impossível escapar da técnica por métodos normais.', '多蓮不自連砲', 'Taren Fujizuho', 'Múltiplos Canhões do Lótus Altruísta Conectados', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1640, 'Múltiplos Clones de Fumaça', 'Depois de criar um único Clone de Fumaça como tática para ser destruído, Kigiri certifica-se de que a explosão feita pelo clone espalhe a fumaça por uma área visivelmente grande, possibilitando a criação de novos clones de fumaça em um número maior.', '多重煙分身たじゅうけむりぶんしん', 'Tajū Kemuri Bunshin', 'Múltiplos Clones de Fumaça', NULL, NULL, 'Multi Clones de Fumaça', NULL, NULL, NULL),
(1641, 'Múltiplos Punhos Conectados', 'Os braços de Ukon brotam em um de Sakon e socam o adversário, triplicando o dano normal. Este ataque é forte o suficiente para quebrar uma rocha sólida.', '多連拳', 'Tarenken', 'Múltiplos Punhos Conectados', NULL, 'Punhos Múltiplos', NULL, 'Curto alcance', NULL, NULL),
(1642, 'Múltiplos Punhos Conectados: Rakshasa', 'Sakon e Ukon criam uma distância entre si mesmos e seu oponente com um chute. Eles se dividem e invocam Rashōmon atrás do mesmo, antes ativarem seu selo amaldiçoado e se fundirem. Em seguida, eles acertam vários socos no oponente contra o Rashōmon, até que ele quebre.', '多連拳・羅刹たれんけん・らせつ', 'Tarenken: Rasetsu', 'Múltiplos Punhos Conectados: Rakshasa', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1643, 'Múltiplos Pés Conectados', 'As pernas de Ukon brotam de uma das pernas de Sakon e chutam o adversário, triplicando o dano normal.', '多連脚たれんきゃく', 'Tarenkyaku', 'Múltiplos Pés Conectados', NULL, 'Chutes Múltiplos', NULL, 'Curto alcance', NULL, NULL),
(1644, 'Múltiplos Redemoinhos Conectados', 'Sakon e Ukon usam suas pernas para girar com grande força para desviar quaisquer ataques.', '多連旋風', 'Tarensenpū', 'Múltiplos Redemoinhos Conectados', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1645, 'Múltiplos Tiros de Lama', 'Ampliando sua técnica parente, esta versão permite que o usuário dispare múltiplos tiros contra diversos alvos ao mesmo tempo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1646, 'Mão Perfurante do Relâmpago Sábio', 'Hagoromo converte chakra em eletricidade, podendo desferir um soco poderoso o suficiente para atravessar carne com relativa facilidade e matar o seu alvo. Essa técnica mostrou-se poderosa o suficiente para quebrar uma grande rocha. Seus efeitos são potencializados com o uso da energia natural infundida com seu chakra.', '仙雷の穿手せんらいのせんて', 'Senrai no Sente', 'Mão Perfurante do Relâmpago Sábio', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1647, 'Mãos de Múltiplas Serpentes Ocultas na Sombra', 'Ao aumentar a quantidade de cobras invocadas com o Mãos de Serpente Oculta na Sombra, a diversidade deste ninjutsu é aumentada. O enxame de grandes serpentes aparecem em um instante, cada cobra individualmente fazendo algo intimidante, desviando, ou capturando o inimigo em um ataque de cooperativo. Sem lhes dar tempo para reagir, o inimigo é envolvido pelas serpentes.', '潜影多蛇手', 'Sen\'ei Tajashu', 'Mãos de Múltiplas Serpentes Ocultas na Sombra', NULL, 'Técnica das Serpentes Múltiplas das Sombras', 'Ataque Múltiplo das Cobras da Sombra', 'Curto a Médio alcance', 'Rank B', NULL),
(1648, 'Mãos de Serpente Oculta na Sombra', 'Esta técnica permite que o usuário ejete cobras de seu pulso ou manga, geralmente após um movimento de pancada. As cobras são usadas ​​principalmente para atacar à distância e, sendo cobras, podem causar várias feridas venenosas no corpo da vítima ou mantê-la no lugar. As cobras também podem abrir a boca para alargar lâminas como a Kusanagi.', '潜影蛇手', 'Sen\'ei Jashu', 'Mãos de Serpente Oculta na Sombra', NULL, 'Técnica da Serpente das Sombras', 'Sombra Combatente da Serpente', 'Curto alcance', 'Rank C', 'Cobra → Tigre'),
(1649, 'Mãos de Serpente Selvagem Oculta na Sombra', 'O usuário conjura uma grande quantidade de cobras que emergem do subsolo para atacar o alvo.', '潜影乱蛇手せんえいらんじゃしゅ', 'Sen\'ei Ranjashu', 'Mãos de Serpente Selvagem Oculta na Sombra', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1650, 'Método de Abate dos Gêmeos Konjiki', 'O Método de Abate dos Gêmeos Konjiki consiste em combinar as kekkei genkai de Liberação de Lava e Aço em uma pseudo-kekkei tōta.', NULL, NULL, 'Método de Abate dos Gêmeos Konjiki', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1651, 'Método de Aceleração', 'Murasame muda a forma como o seu chakra flui, o que lhe permite aumentar consideravelmente sua velocidade, diminuindo significativamente as oportunidades para seus oponentes reagirem. Ao fluir o chakra através do coração, Murasame pode aumentar o seu batimento cardíaco, aumentando o fluxo de seu chakra ainda mais, incluindo sua velocidade. Em sua velocidade máxima, Murasame também pode confundir seus adversários com as imagens residuais criadas a partir de seu movimento de alta velocidade.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Boi → Carneio → Javali → Tigre'),
(1652, 'Método de Formação da Barreira', 'Uma série de quatro papéis explosivos são colocados em conjunto em torno da fronteira de uma área. Se alguém ou alguma coisa tentar atravessar a fronteira, os selos vão detonar quando o intruso chegar ao centro, matando o alvo preso.', '結界法陣', 'Kekkai Hōjin', 'Método da Barreira de Acampamento', 'Barreira Perimetral', 'Formação de Contenção', NULL, NULL, 'Rank B', NULL),
(1653, 'Método de Formação da Barreira — Fórmula Tripla', 'Uma versão mais abrangente do Exemplo — Fūinjutsu, Iruka aplica três fórmulas de selamento simultaneamente no chão, uma do lado da outra. Cada uma causa uma explosão se o oponente for pego nelas, explodindo-se depois se nenhum oponente cair nela.', '結界法陣　三連式けっかいほうじん　さんれんしき', 'Kekkai Hōjin Sanrenshiki', 'Método de Formação da Barreira — Fórmula Tripla', 'Camada Tripla da Barreira Perimetral', NULL, NULL, 'Curto alcance', NULL, NULL),
(1654, 'Método de Formação da Explosão Intermitente', 'Sakon faz um selo circular no chão, com a adição de quatro papéis explosivos ao seu redor, se o adversário se aproximar o suficiente, o selo irá explodir e o oponente ficará coberto por chamas púrpuras.', '爆滅法陣', 'Bakumetsu Hōjin', 'Método de Formação da Explosão Intermitente', 'Formação da Explosão Destruidora', NULL, NULL, 'Curto alcance', NULL, NULL),
(1655, 'Método de Formação do Selo Vinculador', 'Uma matriz de papéis explosivos são colocadas em torno de uma área. Elas explodirão com um selo de mão ou se o adversário tentar se mover. Uma vez que o alvo é incapaz de se mover, um companheiro de equipe, então, é capaz de atacá-lo sem nenhum problema. As etiquetas podem ser desligadas pelo controlador.', '封縛法陣', 'Fūbaku Hōjin', 'Método de Formação do Selo Vinculador', NULL, NULL, 'Arte Ninja da Barreira: Captura do Selo de Perímetro', 'Curto alcance', NULL, 'Macaco → Cavalo → Cao → Carneiro'),
(1656, 'Método de Interseção', 'Com o Kōsa Hō (交差法, Kōsa Hō), o utilizador é capaz de interceptar e contra-atacar uma técnica de taijutsu inimiga, elevando o joelho, e conduzindo para baixo do cotovelo ao mesmo tempo, prendendo assim o membro em que o inimigo usou o taijutsu. Isso causa um dano ao membro do inimigo, pois entre os seus membros, o usuário impulsiona o cotovelo para baixo com muita força, efetivamente quebrando o osso. Genshō usou essa técnica enquanto disfarçado de um ninja de Konohagakure, Yagura, para \'quebrar\' o Furacão da Folha de Rock Lee interceptando a perna de Lee. O tornozelo de Lee não foi quebrado devido sua proteção de peso no tornozelo, ele foi forçado a parar completamente a sua formação até que o ferimento fosse curado.', '交差法', 'Kōsa Hō', 'Método de Interseção', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1657, 'Método de Liberação da Cauda', 'Após preparar o terreno para deixar o jinchūriki vulnerável, o usuário pode forçar a liberação da besta com cauda do corpo do hospedeiro. Obito teve que esperar por um longo tempo até que o selo de Kushina fosse fraco o suficiente para usar esta técnica para extrair Kurama.', '解尾法印かいびほういん', 'Kaibi Hōin', 'Método de Liberação da Cauda', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1658, 'Método de Liberação do Mal', 'O Método de Liberação do Mal é uma contrapartida direta do Método de Selamento do Mal, esta técnica é utilizada para liberar selos induzidos pela técnica em uma pessoa, em vez de isolar. Em comparação com o Método de Selamento do Mal, o Método de Liberação do Mal requer muito menos preparação. Sasuke usou esta técnica para reviver Orochimaru, misturando um pedaço de carne modificado de Kabuto Yakushi para criar um corpo para a consciência de Orochimaru que foi deixada no Selo Amaldiçoado do Céu de Anko Mitarashi.', '解邪法印', 'Kaija Hōin', 'Método de Liberação do Mal', 'Maldição: Liberar', 'Selo de Cancelamento da Maldição', NULL, 'Curto alcance', NULL, 'Tigre → Cobra → Cavalo → Carneiro → Tigre → Coelho → Rato'),
(1659, 'Método de Mudança Corporal das Sombras Espelhadas', 'Essa técnica é uma das várias outras que foram passadas por gerações no País dos Demônios. Ela inicialmente se estrou como Taruho sendo usuário, para se passar por Shion.', '影鏡身転の法', 'Kage Kagami Shinten no Hō', 'Método de Mudança Corporal das Sombras Espelhadas', NULL, NULL, NULL, 'Curto alcance', NULL, 'Selos de mao especifico da técnica'),
(1660, 'Método de Respiração dos Sete Paraísos', 'Trata-se de um método de respiração envolvendo taijutsu inventado por Shira. Através do acúmulo de grandes quantidades de oxigênio para quadruplicar a capacidade de seus pulmões, Shira adquire uma força equivalente à dos Oito Portões com a diferença da quantidade de estágios. As alterações no corpo são as mesmas também, como a cor de pele escura e olhos completamente brancos. Ativar esta técnica exala uma aura amarela.', '七天呼法', 'Shichi Tenkohō', 'Método de Respiração dos Sete Paraísos', NULL, NULL, 'Sete Suspiros Celestiais', NULL, NULL, NULL),
(1661, 'Método de Selamento Escuro', 'Este selo é utilizado em conjunção com a Formação dos Quatro Nevoeiros Pretos. Após o Quarteto do Som selar o indivíduo no recipiente, este selo é adicionado como mais uma camada de proteção. Para criar o selo, Sakon escreve o encantamento de sangue no papel de selamento e, em seguida, atribui-o ao recipiente antes de ativá-lo com um selo de mão.', '封黒法印', 'Fūkoku Hōin', 'Método de Selamento Escuro', NULL, 'Selo de Contenção da Escuridão', 'Arte Ninja: Selo das Trevas', 'Curto alcance', 'Rank B', NULL),
(1662, 'Método de Selamento de Fogo', 'Este jutsu é um tipo de fūinjutsu que envolve o selamento dos efeitos de uma técnica dentro de uma localização específica. Por se adaptar a ninjutsu de Fogo, essa técnica requer um usuário bem informado na arte exata das \'Fórmulas de Selamento\', assim como em ninjutsu. O espaço de selamento do pergaminho é deixado em branco, para assim a fórmula ser inscrita nele. Ao realizar os selos de mão precisos, um vapor aparece do papel e envolve as chamas. Elas são sugadas até o pergaminho, e a aparição do kanji de selar (封) certifica a conclusão da técnica. Assim que o selamento é realizado, os efeitos da técnica não podem ser soltos novamente enquanto uma técnica que anule o selamento não for aplicada. Essa técnica é efetiva para evitar qualquer dano secundário que uma técnica inimiga perigosa possa causar. Ela foi até mesmo capaz de selar as chamas negras do Amaterasu.', '封火法印', 'Fūka Hōin', 'Método de Selamento de Fogo', NULL, 'Aprisionamento de Fogo', NULL, 'Curto alcance', 'Rank B', 'Rato → Passaro → selo especifico → Tigre'),
(1663, 'Método de Selamento do Mal', 'Essa técnica é usada para suprimir marcas induzidas por uma técnica numa pessoa. Marcas de poder mais fraco podem ser completamente suprimidas por essa técnica, mas marcas de poder mais alto como o Selo Amaldiçoado do Céu de Sasuke, fazem com que o poder do selo se torne dependente da força de vontade do recipiente até certo ponto, especialmente se o selador não é experiente o bastante para fortalecer o selo com seu próprio poder. O selo também pode segurar certas kekkei genkai, tais como a do clã Kurama, assim como Kurenai tentou fazer.', '封邪法印ふうじゃほういん', 'Fūja Hōin', 'Método de Selamento do Mal', NULL, 'Selo de Libertação da Maldição', NULL, 'Curto alcance', 'Rank A', 'Coelho → Cobra → Cavalo → Carneiro → Passaro → Tigre → Coelho → Cobra → Cavalo → Carneiro → Pássaro → Tigre → Coelho → Cobra → Cavalo → Carneiro → Pássaro → Cao → Rato'),
(1664, 'Método do Corte e Sugue', 'Trata-se de um procedimento padrão de primeiro socorros utilizado para tratar pacientes picados por animais venenosos.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1665, 'Método do Mundo da Bola de Pó de Ouro', 'Rasa reúne uma certa quantidade de Pó de Ouro acima de sua mão e transforma essa massa dourada em uma esfera, que ele envia-a em direção ao oponente e rapidamente se expande, criando uma cadeia de espinhos de grande dureza e fio, perfurando tudo ao seu alcance.', '砂金界法玉さきんかいほうだま', 'Sakin Kaihōdama', 'Método do Mundo da Bola de Pó de Ouro', 'Pó de Ouro: Desencadeamento do Orbe', NULL, NULL, 'Curto alcance', NULL, NULL),
(1666, 'Método do Mundo da Grande Árvore', 'Colocando a palma da mão no chão, Danzō cria uma árvore gigante no meio de seu oponente, danificando-o severamente.', '大樹界法', 'Taiju Kaihō', 'Método do Mundo da Grande Árvore', 'Desencadeamento da Grande Árvore', NULL, NULL, 'Curto alcance', NULL, NULL),
(1667, 'Método do Mundo de Areia de Ferro', 'Esta técnica utiliza areia de ferro para criar uma esfera gigantesca que ramifica nos pontos. Primeiro o usuário cria duas massas de areia de ferro de campos magnéticos opostos e mescla-os. Isto faz com que as forças magnéticas aumentem instantaneamente e a força repelente criada pelos dois campos magnéticos opostos, espalhando a areia de ferro através de uma vasta área.', '砂鉄界法さてつかいほう', 'Satetsu Kaihō', 'Método do Mundo de Areia de Ferro', NULL, NULL, 'Areia de Ferro: Solte', 'Todos os alcances', 'Rank –', NULL),
(1668, 'Método do Pavão Misterioso', 'Trata-se de uma habilidade de marca registrada de Hoshigakure sob o uso do Poder do Meteorito como fonte principal de desenvolvimento.', '孔雀妙法くじゃくみょうほう', 'Kujaku Myōhō', 'Método do Pavão Misterioso', NULL, NULL, 'Arte Ninja: Kujaku', 'Todos os alcances', NULL, 'Selo de mao especifico da técnica,Corda de Chakra: Macaco → Javali → Carneiro → Macaco → Passaro[1]'),
(1669, 'Método do Pavão Misterioso: Aparição', 'Com a manipulação do chakra roxo e prestes a morrer, Natsuhi pode usar o próprio sangue para desenhar um símbolo no chão para, quando finalmente morrer, ser recriada pelo chakra roxo, como um fantasma.', '孔雀妙法・幻影くじゃくみょうほう・げんえい', 'Kujaku Myōhō: Gen\'ei', 'Método do Pavão Misterioso: Aparição', NULL, NULL, 'Arte Ninja: Aparição Kujaku', NULL, NULL, NULL),
(1670, 'Método do Pavão Misterioso: Hipnotização', 'Com a manipulação do chakra roxo, o usuário forma uma bola incandescente de chakra do meteorito de cor rosa que girar incessantemente. Quando o alvo vê a esfera, a cor reflete em seus olhos e o submete sob uma hipnose.', '孔雀妙法・催眠くじゃくみょうほう・さいみん', 'Kujaku Myōhō: Saimin', 'Método do Pavão Misterioso: Hipnotização', NULL, NULL, 'Arte Ninja: Hipnotização Kujaku', NULL, NULL, NULL),
(1671, 'Mísseis de Flecha Ardente', 'Após as modificações com o corpo do Caminho Asura, o usuário pode desconectar a mão, puxando uma corda fina com mísseis ligados a ela. Estes mísseis podem ser disparados ao acaso para uma grande área de efeito ou pode em um alvo específico.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ofensivo', NULL, NULL),
(1672, 'Míssil', 'Mecha-Naruto lança um único ou uma infinidade de mísseis a partir de sua cavidade torácica mecanizada. Quando Mecha-Naruto faz isso, sua bandana forma a palavra \'míssil\' (ミサイル, misairu).', 'ミサイル', 'Misairu', 'Míssil', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1673, 'Míssil Envenenado', 'Hiruko ataca o seu oponente com sua cauda e atira um míssil envenenado no oponente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1674, 'Nascente da Coluna Separadora', 'Transformado em Isobu, o usuário bate no chão, fazendo com que a água surja até seu oponente, jogando-o no ar.', '貫訣泉', 'Kankessen', 'Nascente da Coluna Separadora', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1675, 'Neblina Preta', 'É uma técnica utilizada por Boro com o auxílio de uma ferramenta ninja científica acoplada em seu corpo, que ele usa para produzir um tipo de vírus aerosol.', '黒いモヤ', 'Kuroi Moya', 'Neblina Preta', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Passaro'),
(1676, 'Nevasca de Grau Zero Absoluto', 'Momoshiki acumula chakra de natureza gelo em sua mão e dispara na forma de uma poderosa nevasca.', '吹雪が絶対零度ふぶきがぜったいれいど', 'Fubukiga Zettaireido', 'Nevasca de Grau Zero Absoluto', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1677, 'Nevoeiro de Pregos Voadores', 'Gosunkugi, a partir de sua boca, exala uma corrente de pequenos pregos para o alto, a fim de atingir o inimigo a longa distância.', '飛釘霧翔ひていむしょう', 'Hitei Mushō', 'Nevoeiro de Pregos Voadores', NULL, NULL, 'Arte Ninja: Névoa de Pregos Flutuantes (忍法・飛釘霧翔, Ninpō: Hitei Mushō)', 'Médio a Longo alcance', NULL, NULL),
(1678, 'Nevoeiro dos Ossos das Cinzas Mortais', 'Kaguya desfere uma série de golpes com seus ossos e, em seguida, abre um portal que atira uma onda ácido em seu oponente, com seu oponente caído, Kaguya cria uma explosão de ácido que o joga para o alto, então ela atira vários ossos e o golpeia.', NULL, NULL, 'Nevoeiro dos Ossos das Cinzas Mortais', 'Névoa dos Ossos das Cinzas Mortais', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1679, 'Ninjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1680, 'Ninjutsu Espaço-Tempo', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1681, 'Ninjutsu Estilo-Tenrō: Técnica de Transformação da Besta-Humana', 'Essa é uma técnica exclusiva para os membros do clã Tenrō, que lhes permite transformarem-se em criaturas semelhantes à lobos, aumentando sua força física e velocidade. A transformação é revertida quando seu usuário é derrotado.', '天狼流忍術・獣人化の術', 'Tenrō-Ryū Ninjutsu: Jūjinka no Jutsu', 'Técnica Ninja Estilo-Tenrō: Técnica de Transformação da Besta-Humana', 'Ninjutsu Estilo-Tenro: Jutsu Homem-Besta', NULL, NULL, NULL, NULL, NULL),
(1682, 'Ninjutsu Médico', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1683, 'Ninjutsu Médico: Flash Forte', 'Sakura usa o Bisturi de Chakra para desferir um golpe com suas duas palmas no oponente, lançando-o para longe.', '医療忍術・剛閃いりょうにんじゅつ・ごうせん', 'Iryō Ninjutsu: Gōsen', NULL, 'Ninjutsu Médico: Flash Feroz', NULL, NULL, 'Curto alcance', NULL, NULL),
(1684, 'Ninjutsu Secreto Ankorodō: Liberação de Mochi: Bolinhos Infinitos', 'O usuário faz com que bolinhos de feijão doce que estão próximos cresçam em tamanhos exorbitantes e numa velocidade absurda. O crescimento constante faz com que seja difícil atacar os bolinhos, uma vez que são muito macios. Apenas Chōji Akimichi, com sua incrível habilidade de comer tudo o que pode, conseguiu parar a técnica.', 'あんころ堂秘伝忍術・餅遁・無限白玉', 'Ankorodō Hiden Ninjutsu: Mochiton: Mugen Shiratama', 'Técnica Ninja Secreta da Geleia de Feijão Doce: Liberação de Mochi: Bolinhos Infinitos', NULL, NULL, NULL, 'Médio alcance', NULL, 'Ativacao: Cobra → Cão → Tigre → Mãos espalmadas para frente,Manter: Rato'),
(1685, 'Ninjutsu de Bolha de Sabão', 'O Ninjutsu de Bolha de Sabão é uma ramificação do ninjutsu a partir da linha de bolha (バブル系, Baburukei) da transformação da natureza de Liberação de Água.', 'しゃぼん玉の忍術しゃぼんだまのにんじゅつ', 'Shabondama no Ninjutsu', 'Ninjutsu de Bolha de Sabão', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1686, 'Ninjutsu de Medicina Negra', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1687, 'Nintaijutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1688, 'Noite de Lua Nublada', 'Yūgao corre em direção a seu oponente com um golpe de espada, deixando um rastro de pós-imagem.', '朧月夜', 'Oborozukiyo', 'Noite de Lua Nublada', NULL, NULL, 'Noite Enluarada', 'Curto alcance', NULL, NULL),
(1689, 'Nova Criação: Rasengan do Relâmpago Rápido', 'Boruto e Sarada se preparam para atacar, em seguida Sarada atira várias shuriken e Boruto ataca com o Punho Gentil. Logo, Sarada aparece por trás e o joga para o alto com um soco reforçado com chakra. Antes do oponente cair no chão, Boruto e Sarada o finalizam com um Rasengan Desaparecedor e várias kunai infundidas com chakra relâmpago, que culminam em uma explosão de energia.', '新鋭・雷迅螺旋丸しんえい・らいじんらせんがん', 'Shin\'ei: Raijin Rasengan', 'Nova Criação: Esfera Espiral do Relâmpago Rápido', NULL, NULL, NULL, NULL, NULL, NULL),
(1690, 'Nova Técnica Ninja Pervertida', 'Uma versão melhorada da Técnica Sensual. Naruto se transforma em uma bela mulher vestida com uma roupa de diaba reveladora, enquanto posa sedutoramente. Naruto descobre que apresentar a mulher vestida com esta roupa é mais eficaz do que apresentar ela totalmente nua. Isso porque, a roupa é cortada e moldada para fazer com que a vítima fique obcecada em querer ver mais do corpo da mulher, combinado com um olhar de \'garota má\'.', NULL, NULL, 'Nova Técnica Ninja Pervertida', NULL, NULL, NULL, NULL, NULL, NULL),
(1691, 'Nove Bestas Mascaradas', 'As Nove Bestas Mascaradas (九面獣, Kyūmenjū) são entidades criadas por Menma através do uso do chakra do Nove-Caudas Negro. Cinco dessas bestas são descritas como bestas divinas (神獣, shinjū), enquanto as outras quatro são referidas como sacerdotes divinos (神官, shinkan).', '九面獣', 'Kumenjū', 'Nove Bestas Mascaradas', NULL, NULL, NULL, NULL, 'Rank S', 'Tigre'),
(1692, 'Novo: Invocação do Impasse de Três-Caminhos', 'Naruto, Sasuke e Sakura invocam Gamakichi, Aoda e Katsuyu, respectivamente. Enquanto as invocações atacam, cada um salta da cabeça de sua invocação e acertam um soco em seu alvo. Naruto utilizando um braço de chakra de sua Besta com Cauda, Sasuke com um Susanoo parcialmente manifestado, e Sakura com sua força aprimorada, causando grande dano ao inimigo.', '新・口寄せ三竦みしん・くちよせさんすくみ', 'Shin: Kuchiyose Sansukumi', 'Novo: Invocação do Impasse de Três-Caminhos', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1693, 'Nuvem de Areia', 'Gaara reúne uma quantidade de areia no ar e rapidamente lança-a como um corte contra seu oponente, derrubando-o instantaneamente.', '砂塵さじん', 'Sajin', 'Nuvem de Areia', 'Tempestade de Areia', NULL, NULL, 'Longo alcance', NULL, NULL),
(1694, 'Névoa Ilusória', 'Isobu libera uma névoa criada por seu chakra para colocar intrusos sob uma ilusão. A ilusão atormenta o alvo com eventos do seu passado. Animais e insetos ficam desnorteados ao entrarem em contato com a névoa. A ilusão desaparecerá se a vítima quebrar a ilusão sozinho ou com a ajuda de outro(s).', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1695, 'Névoa Venenosa', NULL, '毒霧', 'Dokugiri', 'Névoa Venenosa', 'Arte Ninja: Névoa Venenosa[2]', NULL, NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(1696, 'Névoa Venenosa de Ibuse', 'Hanzō invoca Ibuse a partir do solo, que lança uma nuvem de veneno, a qual viaja em uma linha reta e envenena o oponente, o prendendo.', 'イブセの毒霧', 'Ibuse no Dokugiri', 'Névoa Venenosa de Ibuse', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1697, 'Oferecimento de Flores do Funeral', 'Ino joga um buquê de flores com um papel explosivo oculto no oponente, que é automaticamente ativado ao entrar em contato com ele. Ino mantém uma distância, para se proteger da explosão, e o inimigo é pego na explosão.', '供花送りくげおくり', 'Kuge Okuri', 'Oferecimento de Flores do Funeral', 'Envio de Flores ao Memorial', NULL, NULL, 'Curto alcance', NULL, NULL),
(1698, 'Oito Deuses do Trovão', 'Movendo sua mão para baixo, Momoshiki Ōtsutsuki é capaz de desencadear um poderoso relâmpago descendente, capaz de debilitar mesmo o Modo de Chakra da Besta com Cauda de Naruto Uzumaki revestido com o Susanoo de Sasuke Uchiha.', '八雷神', 'Hachiraishin', 'Oito Deuses do Trovão', 'Clamor dos Deuses', NULL, NULL, 'Longo alcance', NULL, NULL),
(1699, 'Oito Portões', 'Os Oito Portões são oito tenketsu específicos do Sistema de Circulação de Chakra de um indivíduo. Eles limitam o fluxo global de chakra dentro do corpo de uma pessoa. A base para a ideia dos portões de chakra vem de limites do corpo sobre as funções dentro dele. Isso faz o corpo muito mais fraco, mas previne o corpo de esgotar tão cedo. Submetendo-se a treinamento intenso, pode-se aprender como abrir estes portões, permitindo que o usuário ultrapasse seus próprios limites físicos à custa de danos extremos de seu próprio corpo.', '八門はちもん', 'Hachimon', 'Oito Portões', NULL, 'Oito Portais', 'Oito Portões Internos', NULL, NULL, NULL),
(1700, 'Oito Torções da Besta com Cauda', 'Depois de assumir a sua plena transformação de oito caudas, Killer B gira suas caudas em torno de si, antes de estendê-lo com uma enorme quantidade de força física. Isto faz com que ele gire rapidamente, resultando na geração de tremendas rajadas de vento formando uma área circundante. Esta técnica foi suficientemente forte para nivelar o terreno dentro de uma grande área em volta do Hachibi.', '尾獣八巻きびじゅうはちまき', 'Bijū Hachimaki', 'Oito Torções da Besta de Cauda', NULL, 'Torção da Fera de Oito Caudas', NULL, 'Todos os alcances', 'Rank –', NULL),
(1701, 'Oito Trigramas Ataque de Desintegração dos Leões Gêmeos', 'Esta técnica é uma versão aperfeiçoada do Passo Gentil dos Punhos de Leões Gêmeos, utilizada por Hinata Hyūga após ganhar o chakra de Hamura Ōtsutsuki, onde devido o ganho de qualidade de chakra, aumenta o poder da técnica consideravelmente.', '八卦双獅子崩撃はっけそうじしほうげき', 'Hakke Sōjishi Hōgeki', 'Oito Trigramas Ataque de Desintegração dos Leões Gêmeos', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1702, 'Oito Trigramas Britador da Montanha', 'Uma variação mais eficaz do Oito Trigramas Palma de Vácuo. O usuário acerta o alvo e cria uma forte oscilação de chakra, assim ele lança-o a uma incrível extensão e coloca o inimigo com sérios ferimentos.', '八卦破山撃はっけはさんげき', 'Hakke Hasangeki', 'Oito Trigramas Britador da Montanha', 'Oito Trigramas Ataque de Ruptura da Montanha', NULL, 'Oito Trigramas Golpe Hasan', 'Curto alcance', NULL, NULL),
(1703, 'Oito Trigramas Cento e Vinte e Oito Palmas', 'O Oito Trigramas Cento e Vinte e Oito Palmas é uma técnica do estilo de luta do Punho Gentil. É essencialmente uma versão duplicada do Oito Trigramas Sessenta e Quatro Palmas, realizada no dobro da velocidade. Esta técnica é usada para fechar o fluxo de chakra acertando cento e vinte e oito tenketsu do Sistema de Circulação de Chakra do oponente. Isso elimina sua capacidade de usar chakra por algum tempo e faz com que seja difícil para ele se mover. Alternativamente, ela pode ser usada para atingir um grande número de alvos muito rapidamente.', '八卦百二十八掌はっけひゃくにじゅうはちしょう', 'Hakke Hyaku Nijūhachi Shō', 'Oito Trigramas Cento e Vinte e Oito Palmas', NULL, NULL, 'Oito Trigramas Cento e Vinte e Oito Golpes', 'Curto alcance', NULL, NULL),
(1704, 'Oito Trigramas Estilo Trezentos e Sessenta e Um', 'Esta técnica permite que o usuário ataque todos os 361 pontos de chakra do oponente, paralisando-o com cada golpe e imediatamente matando-o no golpe final. Quando se utiliza esta técnica, a velocidade do utilizador é comparável à da Lótus Reversa.', '八卦三百六十一式', 'Hakke Sanbyakurokujū Isshiki', 'Oito Trigramas Estilo Trezentos e Sessenta e Um', 'Oito Trigramas: Estilo Trezentos e Sessenta e Um', NULL, NULL, 'Curto alcance', NULL, NULL),
(1705, 'Oito Trigramas Palma Aiki', 'Hinata levanta os braços defensivamente, sendo um contragolpe. Quando o oponente a acerta, ela agarra o oponente e envia-o voando para longe. Ela, então, os atinge com um ataque poderoso com sua palma.', '八卦合気掌', 'Hakke Aikishō', 'Oito Trigramas Palma de Espírito Harmonioso', 'Oito Trigramas Palma Espírita', NULL, NULL, 'Curto alcance', NULL, NULL),
(1706, 'Oito Trigramas Palma da Parede de Vácuo', NULL, '八卦空壁掌はっけくうへきしょう', 'Hakke Kūhekishō', 'Oito Trigramas Palma da Parede de Vácuo', NULL, 'Golpe da Parede de Vácuo dos Oito Trigramas', NULL, 'Curto a Médio alcance', NULL, NULL),
(1707, 'Oito Trigramas Palma de Rede', 'O usuário bate as suas mãos no solo, criando um conjunto de redes que infligem dor nas vítimas e drenam seu chakra.', '八卦盤掌はっけばんしょう', 'Hakke Banshō', NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Tigre → Carneiro → Boi → Passaro'),
(1708, 'Oito Trigramas Palma de Vácuo', 'Esta técnica é semelhante, na prática, à Oito Trigramas Palmas Giratórias do Céu, mas tem um alvo específico, em vez de uma área geral. Para realizá-lo, o usuário aponta precisamente para os pontos vitais do inimigo com o Byakugan e libera um impulso com a palma da mão em alta velocidade. A rajada de vácuo compactado usando o Punho Gentil é formada para atacar os órgãos vitais do oponente de uma certa distância, lançando-os para longe com uma força tremenda antes mesmo de perceber que eles foram atingidos.', '八卦空掌はっけくうしう', 'Hakke Kūshō', 'Oito Trigramas Palma de Vácuo', 'Oito Trigramas Palma do Céu', 'Golpe do Vácuo de Oito Trigramas', 'Oito Trigramas Palma Aérea', 'Curto a Médio alcance', 'Rank –', NULL),
(1709, 'Oito Trigramas Palma de Vácuo do Céu', 'Primeiramente, Hinata acerta o inimigo e lança-o no ar, logo o usuário salta até o céu e realiza um incrível Oito Trigramas Palmas Giratórias do Céu, entretanto a esfera maciça aumenta seu tamanho constantemente e assim o inimigo começa a forçar-se contra o solo, assim ele chega a atravessar o solo e sumir no meio da esfera de lâminas. Finalmente, o usuário começa a conter sua rotação, em seguida, ele cai no centro de uma cratera imensa feita com a incrível rotação de lâminas de Chakra.', '八卦掌空天', 'Hakkeshō Kūten', 'Oito Trigramas Palma de Vácuo do Céu', 'Oito Trigramas Palma do Céu', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1710, 'Oito Trigramas Palmas Giratórias do Céu', 'Um taijutsu secreto que é passado oralmente apenas para os membros da casa principal do clã Hyūga.', '八卦掌回天はっけしょうかいてん', 'Hakkeshō Kaiten', 'Oito Trigramas Palmas Giratórias do Céu', 'Rotação', 'Rotação das Palmas de Oito Trigramas', 'Oito Trigramas Palma Rotativa', 'Curto alcance', 'Rank –', NULL),
(1711, 'Oito Trigramas Quatro Palmas Celestiais', 'Neji utiliza o Oito Trigramas Palma de Vácuo três vezes sobre o seu oponente, lançando-o ferozmente à uma rocha gigante, onde em seguida, Neji ataca novamente com mais uma palma de vácuo ainda mais forte, destruindo a rocha gigante em cima do oponente.', '八卦四天空掌はっけしてんくうそう', 'Hakke Shitenkūshō', 'Oito Trigramas Quatro Palmas Celestiais', 'Oito Trigramas Vento Definitivo', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1712, 'Oito Trigramas Sessenta e Quatro Palmas', 'Os Oito Trigramas Sessenta e Quatro Palmas é um taijutsu exclusivo passado dentro da casa principal dos Hyūga.', '八卦六十四掌はっけろくじゅうよんしょう', 'Hakke Rokujūyon Shō', 'Oito Trigramas Sessenta e Quatro Palmas', NULL, 'Estilo Jūken: Oito Trigramas Sessenta e Quatro Palmas', 'Arte do Punho Suave: Oito Trigramas Sessenta e Quatro Golpes', 'Curto alcance', NULL, NULL),
(1713, 'Oito Trigramas Sessenta e Quatro Palmas Espirituais', 'Neji usa o Oito Trigramas Sessenta e Quatro Palmas, mais ou menos 8 à 16 ataques, na qual, lança seu oponente para o alto. Hinata então o ataca com a Denominação de Oito Trigramas Onda de Palmas, mas nos dois últimos golpes aparece uma onda gigantesca de chakra, que manda o oponente novamente até Neji, onde por fim, ele ataca seu alvo com a anterior concentração de chakra em seus dedos.', NULL, NULL, 'Oito Trigramas Sessenta e Quatro Palmas Espirituais', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1714, 'Oito Trigramas Trinta e Duas Palmas', 'Esta é uma técnica do estilo de luta do Punho Gentil. É essencialmente uma versão menor do Oito Trigramas Sessenta e Quatro Palmas, assim ela se torna menos eficaz. Essa técnica é feita com o intuito de violar o fluxo de Chakra ao acertar uma meta de trinta e dois Pontos de Chakra do Sistema de Circulação de Chakra do seu inimigo. Isso elimina as chances do alvo em usar ou controlar seu Chakra em algum tempo e faz o inimigo ficar vulnerável e com muita falta de movimentação.', '八卦三十二掌はっけさんじゅうにしょう', 'Hakke Sanjūni Shō', 'Oito Trigramas Trinta e Duas Palmas', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1715, 'Olho Noturno', 'Este é um dōjutsu utilizado por Yome para rastrear os inimigos através da reflexão de minúsculas gotículas de água, após a dilatação das pupilas de seus olhos. De acordo com Sen, uma vez que Yome tenha fixado os olhos, seu alcance visual chega à dezenas de quilômetros. Yome também pode usar essa habilidade para aumentar habilidades de evasão durante um combate.', '夜目よめ', 'Yome', 'Olho Noturno', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1716, 'Olho da Mente de Kagura', 'Esta habilidade permite que Karin possa encontrar e sentir indivíduos por meio de sua corrente de chakra, podendo localizá-los em uma área de mais de dez quilômetros. Isso é feito concentrando-se e abrindo o olho da mente, embora Karin o mantenha ativo com frequência. Esta habilidade também permite descobrir quando uma pessoa está mentindo por meio da perturbação do chakra da pessoa que ocorre por causa de emoções desonestas ou detectar se alguém, incluindo ela própria, está em um genjutsu. Além disso, concentrando-se em um chakra em particular, Karin pode perceber a sua localização e movimento com grande detalhe.', '神楽心眼', 'Kagura Shingan', 'Olho da Mente de Kagura', 'Kagura: O Olho da Mente', NULL, NULL, 'Todos os alcances', 'Rank –', NULL),
(1717, 'Olho de Sapo', 'Utilizando um sapo olheiro como meio, o usuário compartilha da visão do anfíbio, tornando-se capaz de ver tudo que o animal vê. Muito útil para missões de infiltração, reconhecimento e observação. Ele ainda é capaz de compartilhar essa visão com o óculos de Amado em tempo real.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1718, 'Onda de Choque Precoce', 'Sakura, usando sua superforça, soca o chão para fazê-lo tremer e, eventualmente, criar uma onda de choque que derruba todos em sua volta.', '衝波乱れ咲きしょうはみだれさき', 'Shōha Midare Saki', 'Onda de Choque Precoce', 'Onda de Choque da Flor', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1719, 'Onda de Choque da Estátua Demoníaca do Caminho Exterior', 'Se cercado por inimigos, a Estátua Demoníaca do Caminho Exterior solta uma enorme onda de choque que empurra os inimigos de volta.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1720, 'Onda de Corais em Ascensão', 'Batendo sua mão ou bastão no chão, Yagura é capaz de criar uma fileira de corais crescentes a partir do solo e, em seguida, atacar o oponente. Para um efeito mais bruto, ele pode aumentar drasticamente um coral na forma de um pilar, atacando o oponente violentamente e jogando-o para cima. Essa versão mais violenta é conhecida como Ataque dos Corais em Ascensão (珊瑚昇撃, Sango Shōgeki).', '珊瑚漣昇さんごれんしょう', 'Sango Renshō', 'Onda de Corais em Ascensão', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1721, 'Onda de Escuridão', 'Shinnō pode desencadear uma onda negra generalizada feita a partir de seu chakra escuro no adversário com apenas um movimento de sua mão ou através de sua mente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1722, 'Onda de Palma', 'Shizune ataca seu oponente algumas vezes, então ela gira ao redor com seu braço estendido para atingi-los com um Bisturi de Chakra. Ela acaba por bater o seu adversário para o alto com outro Bisturi de Chakra.', '掌波しょうは', 'Shōha', 'Onda de Palma', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1723, 'Onda de Som do Cabelo Uivante', 'Ao balançar mechas de seus cabelos, o usuário gera uma onda de som de alta frequência que é capaz de simultaneamente desorientar o oponente por pressionar seu ouvido e também é capaz de cortar o alvo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1724, 'Onda do Deserto', 'Uma técnica principalmente defensiva que usa areia para criar uma enorme parede de areia que é presumivelmente capaz de proteger de quaisquer ataques frontais. Ela também pode ser usada para separar as pessoas que envolvem-se entre si.', '砂漠波', 'Sabakuha', 'Onda do Deserto', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1725, 'Ondas Sonoras Decapitadoras', 'Usando os tubos de ar implantados em seus braços, Zaku pode controlar a pressão do ar, criando explosões supersônicas de ar. Estas explosões são poderosas o suficiente para destruir uma pedra e podem ser usadas para amolecer o chão.', '斬空波', 'Zankūha', 'Ondas Sonoras Decapitadoras', NULL, 'Onda Sonora Cortante', 'Onda de Som Cortante', 'Curto alcance', 'Rank D', 'Cobra → Coelho → Cavalo → Javali'),
(1726, 'Ondas de Vento', 'O usuário faz ondas na mão e cria múltiplas lâminas de vento, que se combinam para formar uma lâmina, grande e única de ar, que pode ser lançado em direção do oponente. A técnica era poderosa o suficiente para parar os movimentos do Susanoo, mas não foi capaz de romper a sua defesa.', '風波かざなみ', 'Kazanami', NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1727, 'Orbes Explosivos das Chamas Escaldantes do Inferno', 'O usuário cria grandes esferas de chakra envoltas em chamas azuis, em seguida, manipula essas esferas, a fim de perseguir o inimigo antes de explodir. Inimigos atingidos pelas explosões de uma dessas esferas têm suas capacidades ofensivas reduzidas por um tempo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1728, 'Orbes de Luz', 'Esta técnica cria uma quantidade de numerosas pequenas bolas de luz para atacar o alvo. Apenas um único golpe de uma delas pode resultar em sentir uma sensação de queimação.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1729, 'Orbes de Roda do Nove-Caudas', 'O Nove-Caudas cria orbes de chakra preto e vermelho que giram em torno de seu corpo e danifica qualquer um que lhe toca. Os orbes depois se espalham pelo local, enquanto continuam a girar mais rápido até desaparecerem.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1730, 'Oscilação-Baixa Desajeitada de Gamaken', 'Com o oponente no ar, Gamaken saca sua sasumata, prende o inimigo no meio e com uma força brutal prensa-o no solo, destruindo os arredores.', NULL, NULL, NULL, 'Oscilação-Baixa Desajeitada de Gamaken', NULL, NULL, 'Curto alcance', NULL, NULL),
(1731, 'Ossos das Cinzas Mortais', NULL, '共殺の灰骨ともごろしのはいこつ', 'Tomogoroshi no Haikotsu', NULL, NULL, 'Ossos das Cinzas Mortais', NULL, 'Curto a Médio alcance', 'Rank –', NULL),
(1732, 'Ouro Pegajoso da Aranha', 'Kidōmaru usou esta técnica para endurecer o metal dourado que ele lança como o suor. As armas criadas a partir desse processo formam o núcleo de seu estilo de luta. Este metal endurece como aço quando em contato com o ar, por isso, mastigando-o na boca e mudando sua forma Kidōmaru pode criar armas adequadas a cada batalha. Porque instantaneamente endurece, ataques diretos a uma distância são possíveis.', '蜘蛛粘金', 'Kumo Nenkin', 'Ouro Pegajoso da Aranha', NULL, 'Forja Aracnídea', NULL, NULL, NULL, NULL),
(1733, 'Ouro do Céu', 'Levantando seu lingote gigante para o cima, uma luz dourada se acende no céu e lança vários lingotes contra um grupo de oponente.', NULL, NULL, NULL, 'Ouro do Céu', NULL, NULL, 'Médio alcance', NULL, NULL),
(1734, 'Pacote Pequeno', 'Konan cria uma tempestade de papel que gira em torno de seu oponente e o corta.', '小包み', 'Kodutsumi', 'Pacote Pequeno', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1735, 'Padrão de Selo Amaldiçoado', 'Trata-se de marcas naturais espalhadas por todo o corpo de areia de Shukaku que, através do contato físico direto, pode utilizá-lo para imobilizar e selar seus alvos.', '呪印の紋じゅいんのもん', 'Juin no Mon', 'Padrão de Selo Amaldiçoado', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1736, 'Paladar Pegajoso da Língua Ácida', 'Uma pasta ácida altamente concentrada é subitamente liberada a partir da boca. O ácido apresenta tal densidade elevada e concentração que é ainda capaz de diluir pedra, vaporizando-a. Não há movimentos de pré-requisitos para fazer antes da ativação desta técnica. Devido a isso, Katsuyu pode facilmente atacar rapidamente e pegar o inimigo desprevenido com este ataque.', '舌歯粘酸', 'Zesshi Nensan', 'Paladar Pegajoso Ácido da Língua', NULL, 'Gosma Ácida', 'Limo Ácido', 'Todos os alcances', NULL, NULL),
(1737, 'Palma Coral', 'Enquanto em seu estado transformado, Yagura atinge o oponente com um golpe poderoso, fazendo uma grande formação de coral crescer rapidamente a partir do ponto inicial de contato. Isso, então, rapidamente se solidifica e restringe a mobilidade do inimigo, como a substância continua a crescer, eventualmente, cobrindo todo seu corpo.', '珊瑚掌さんごしょう', 'Sangoshō', 'Palma Coral', NULL, 'Punho de Corais', NULL, 'Curto alcance', NULL, NULL),
(1738, 'Palma Dilacerante da Besta', 'A Palma Dilacerante da Besta é uma poderosa técnica de natureza de chakra de vento que pode cortar através de qualquer material. Sora a ativa liberando chakra através de sua mão direita, e, em seguida, uma onda de chakra cortante é liberada, o que parece ser controlada pela vontade e pensamentos de Sora. Ele também é capaz de usar a técnica em rápidos e contínuos disparos, em movimento sucessivo.', '獣破掌', 'Jūha Shō', 'Palma Dilacerante da Besta', NULL, NULL, 'Golpe de Vento da Besta', 'Médio a Longo alcance', NULL, NULL),
(1739, 'Palma Inferior', 'Esse ataque consiste em um golpe rápido e preciso da palma dos usuários no corpo de um oponente. Como outras técnicas do Punho Gentil, ele envia chakra no alvo para atordoar o inimigo ou, se uma quantidade suficiente de chakra for usada, pode causar graves danos internos em um golpe.', '掌底しょうてい', 'Shōtei', 'Palma Inferior', NULL, NULL, 'Ataque da Palma Calcanhar', 'Curto alcance', NULL, NULL),
(1740, 'Palma Inferior do Portão da Prisão', 'A inicia a combinação com uma joelhada, que lança o oponente para cima, rebate com uma cotovelada descendente, que derruba o inimigo contra o solo e finaliza com um ataque ascendente com a palma no adversário, enviando-o muito alto, em direção ao céu.', '獄門掌底', 'Gokumon Shōtei', 'Palma Inferior do Portão da Prisão', 'Ataque da Palma de Prisão', NULL, NULL, 'Curto alcance', NULL, NULL),
(1741, 'Palma Perfurante de Madeira', 'Yamato atinge o oponente com uma série de socos e chutes que levam-no saltando para o ar e libera uma lança de madeira da palma da mão que é direcionada para o oponente e usada para levá-lo ao chão.', NULL, NULL, 'Palma Perfurante de Madeira', 'Palma Perfuradora de Madeira', NULL, NULL, 'Curto alcance', NULL, NULL),
(1742, 'Palma Perturbadora', 'Sakura carrega chakra em seu punho e soca o seu adversário, o que que impulsa seu alvo, prejudicando o seu sistema nervoso central.', NULL, NULL, NULL, 'Palma Perturbadora', NULL, NULL, 'Curto alcance', NULL, NULL),
(1743, 'Palma Vendaval Dilacerante da Besta', 'A Palma Vendaval Dilacerante da Besta é uma versão melhorada da Palma Dilacerante da Besta. Ela usa chakra para infundir com e condensar uma quantidade maciça de chakra de vento para formar uma maciça garra demoníaca que se estende desde a mão do usuário para atacar e agarrar um alvo, bem como desviar qualquer arma ou ataque. Dependendo da sua utilização, o utilizador pode manipular o tamanho e comprimento da garra para atacar e defender a qualquer alcance. Além de tornar-se uma arma mortal, a garra que se estende também carrega uma força tremenda que pode devastar tudo em seu caminho, sem afetar o usuário, tornando-o útil para a remoção dos objetos sólidos e pesados.', '獣破烈風掌', 'Jūha Reppū Shō', 'Palma Vendaval Dilacerante da Besta', NULL, NULL, 'Palma da Onda Dilacerante da Besta', 'Todos os alcances', NULL, NULL),
(1744, 'Palma da Lâmina de Água', 'Tobirama ataca o oponente com uma combinação de taijutsu e Liberação de Água. Ele começa com um simples soco seguido por três chutes e então levanta o oponente para o ar onde com sua maestria em Liberação de Água e uma lâmina de água materializada em suas mãos, ele destrói o oponente jogado ao ar.', '水掌刃すいしょうじん', 'Suishōjin', 'Palma da Lâmina de Água', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1745, 'Pampas', 'Kimimaro usa sua espada de osso para golpear o oponente com velocidade surpreendente, em diferentes áreas vitais do corpo. Este ataque pode ser usado repetidamente em sucessão para não dar ao oponente a chance de atacar de volta.', '芒', 'Susuki', 'Pampas', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1746, 'Pancada!', 'Konohamaru salta sobre eu oponente e acerta um único e poderoso golpe.', 'ごっつんこ!', 'Gottsunko!', 'Pancada!', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1747, 'Pancada do Punho', 'A usa o Modo de Chakra de Liberação de Relâmpago e ataca o oponente com seus dois punhos.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL);
