INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(1999, E'Salgueiro do Estilo Konoha', E'O usuário faz movimentos ondulares para trás com sua espada, que, quando visto pelo alvo bloqueia-os em um genjutsu que lhes causa alucinação de múltiplos braços ondulando. O movimento se assemelha ao de uma árvore de salgueiro soprando no vento. Com o inimigo desprevenido, o usuário é livre para cortá-lo.', E'木ノ葉流柳', E'Konoharyū Yanagi', E'Salgueiro do Estilo Folha da Árvore', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2000, E'Salto das Cinco Montanhas', E'Usando a força reforçada, concedida pela sua Armadura de Vapor, Han da um poderoso soco em seu adversário, e enviá-o pro ar. Ele então pula atrás dele e se transforma em Kokuō, ele da uma cabeçada e em seguida esmaga o adversário sob o seu casco tão fortemente que ele rompe o solo e gera uma onda de choque impetuosa.', E'五山跳びござんとび', E'Gozan Tobi', E'Salto das Cinco Montanhas', E'Pulo das Cinco Montanhas', NULL, NULL, E'Curto alcance', NULL, NULL),
(2001, E'Secreto: Formação do Vendaval Trovejante da Flor de Cerejeira', NULL, E'極意・風雷桜花ノ陣ごくい・ふうらいおうかのじん', E'Gokui: Fūraiōka no Jin', E'Secreto: Formação do Vendaval Trovejante da Flor de Cerejeira', E'Técnica Misteriosa: Formação de Vento, Relâmpago e Flor de Cerejeira', NULL, NULL, E'Curto alcance', NULL, NULL),
(2002, E'Seis Caminhos — Chibaku Tensei', NULL, E'六道　地爆天星りくどう ちばくてんせい', E'Rikudō — Chibaku Tensei', E'Seis Caminhos — Estrela da Devastação', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2003, E'Seis Caminhos: Kunitsukami', E'O Kunitsukami é um modo de chakra despertado por aqueles que possuem o Chakra Sábio do Seis Caminhos em questão. O modo permite que o usuário entre em um auge absoluto, concedendo-lhe até mesmo as Esferas da Busca da Verdade e um espírito de luta destrutivamente comparável ao de um Susanoo de corpo inteiro.', E'六道・国津守', E'Rikudō: Kunitsukami', E'Seis Caminhos: Guardião do País', NULL, NULL, NULL, NULL, NULL, E'Juntar as Maos'),
(2004, E'Seis Caminhos: Shinra Tenkyō', E'Tirando proveito de seu poder, Naruto toca em Sasuke e lhe transfere o chakra das bestas com cauda. Com isso, Naruto manifesta seu avatar colossal de Kurama com três cabeças e usa sua poderosa técnica empregando o uso do Rasenshuriken, da mesma forma, Sasuke adota a forma gigantesca do Susanoo reforçado com o chakra das bestas com caudas e usa a Flecha de Indra. Ambos lançam suas técnicas simultaneamente contra seu alvo que, ao colidirem, causam uma poderosa explosão de energia e raios que cobre todo o céu.', E'六道・神羅天叫りくどう・しんらてんきょう', E'Rikudō: Shinra Tenkyō', E'Seis Caminhos: Grito Celestial do Deus Onipresente', E'Seis Caminhos: Grito Todo Poderoso', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2005, E'Seis Caminhos: Ultra-Grande Bola Rasenshuriken', E'Naruto, enquanto em seu modo Kurama de Ashura, cria uma Liberação de Vento: Ultra-Grande Bola Rasenshuriken em seus braços direitos e uma Grande Bola: Rasenshuriken da Besta com Cauda em seus braços esquerdos, ambos infundidos com energia natural. Isso é forte o suficiente para combater a mais forte técnica ofensiva de Sasuke.', E'六道・超大玉螺旋手裏剣りくどう・ちょうおうだまらせんしゅりけん', E'Rikudō: Chōōdama Rasenshuriken', E'Seis Caminhos: Ultra-Grande Bola Shuriken Espiral', E'Seis Caminhos: Rasen Shuriken Gigante', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2006, E'Seis Caminhos de Pain', E'Os Seis Caminhos de Pain (ペイン六道, Pein Rikudō) é uma técnica do Caminho Exterior concebida por Nagato depois de ter sido prejudicado por Hanzō e se tornado magro pelos inúmeros receptores de chakra embutidos em suas costas pela Estátua Demoníaca do Caminho Exterior. Perfurando cadáveres com receptores de chakra, um portador do Rinnegan pode manipular os corpos como se fossem seus próprios.', E'ペイン六道ペインりくどう', E'Pein Rikudō', E'Seis Caminhos de Pain', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2007, E'Seis Caminhos de Saṃsāra', E'Os Seis Caminhos de Pain atacam simultaneamente o adversário, então o Caminho Asura lança-o no ar e os outros caminhos o empalam com as Lâminas de Rompimento de Chakra.', E'輪廻六道', E'Rinne Rikudō', E'Seis Caminhos de Saṃsāra', E'Seis Caminhos da Reencarnação', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2008, E'Seis Formações Vermelhas Yang', E'Uma barreira extremamente poderosa, do mais alto nível, que não pode ser usado por qualquer outra pessoa, exceto por aqueles abençoados com o Poder do Seis Caminhos. É superior à Quatro Formações Vermelhas Yang, tanto em tamanho, quanto em durabilidade. Aqueles que despertaram o Rinnegan, utilizam os receptores negros e disparam-os para vários locais no mapa, em seguida, o usuário manifesta a barreira colossal.', E'六赤陽陣むせきようじん', E'Musekiyōjin', E'Seis Formações Vermelhas Yang', NULL, E'Formação dos Seis Sóis Vermelhos', NULL, E'Longo alcance', NULL, E'Cobra → Rato → Selo de Técnica Especifica → Rato Invertido'),
(2009, E'Selamento Combinado: Supressão Yang do Macaco', E'Após plantar etiquetas de selamento na área, os usuários criam uma barreira de selamento que toma a forma de uma mão de macaco transparente, a qual captura o alvo. A técnica requer uma quantidade substancial de chakra para completar o selo, podendo rapidamente cansar o usuário e permitindo que o alvo escape.', E'コンボ封印・猿陽封殺', E'Combo Fūin: Enyō Fūsatsu', E'Selamento Combinado: Supressão Yang do Macaco', NULL, NULL, NULL, NULL, NULL, NULL),
(2010, E'Selamento da Besta com Cauda', E'Sempre que Naruto Uzumaki entrar em uma forma de cauda, o Colar do Primeiro Hokage ativa essa técnica para contê-lo. Não se sabe se a técnica apenas restringe Naruto até Yamato suprimir a influência da Nove-Caudas ou se suprime a influência por si só já que a Nove-Caudas cancelou a técnica esmagando o colar. Ele também alerta Yamato de quantas caudas Naruto desenvolveu no momento.', E'尾獣封印びじゅうふういん', E'Bijū Fūin', E'Selamento da Besta com Cauda', NULL, NULL, NULL, NULL, NULL, NULL),
(2011, E'Selamento do Grande Funeral do Deserto em Camadas', E'Utilizando uma grande quantidade de areia, o usuário cobre o inimigo com várias camadas sucessivas do material, antes de comprimi-lo na forma de uma pirâmide enorme que serve para prender o alvo após a sua conclusão. Podem ser inseridas etiquetas de selamento sobre a pirâmide, para aumentar seu poder de selamento, ou Shukaku pode fortalecer o selo com o juinjutsu que cobre seu corpo.', E'砂漠層大葬封印さばくそうたいそうふういん', E'Sabaku Sōtaisō Fūin', E'Selamento do Grande Funeral do Deserto em Camadas', E'Enterro de Areia em Camadas Gigantes', E'Grande Pirâmide Mortuária de Areia', NULL, E'Todos os alcances', NULL, NULL),
(2012, E'Selamento do Poder Budista', E'Trata-se de um fūinjutsu que pode ser usado em conjunto com Técnica de Selamento do Poder Budista: Sen no Rikyū para selar uma besta com cauda dentro de uma pessoa, tornando-a um jinchūriki. O selo em si adquire a forma da caligrafia japonesa.', E'法力封印', E'Hōriki Fūin', E'Selamento do Poder Budista', NULL, NULL, E'Selo Dharma', E'Curto alcance', NULL, NULL),
(2013, E'Selo Amaldiçoado Animal', E'Este é um selo amaldiçoado único que foi aplicado por Orochimaru ainda em seus estágios experimentais e, portanto, estava incompleto e instável.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2014, E'Selo Amaldiçoado de Genjutsu', E'Uma técnica que permite ao usuário manifestar uma marcação espiral em uma pessoa. Ao executá-la, o genjutsu permite que a vítima seja manipulada para acreditar nas informações contadas pelo usuário. A técnica, aparentemente, requer reaplicações regulares, já que gradualmente a marca desaparece, fazendo com que as verdadeiras memórias da vítima voltem a surgir em uma dor de cabeça induzida por enxaqueca.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2015, E'Selo Amaldiçoado do Clã Iburi', E'Esse selo amaldiçoado é aplicado por Orochimaru nos membros do clã Iburi a fim de estabilizar a sua kekkei genkai.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2016, E'Selo Amaldiçoado do Quarteto do Som', E'Cada membro do Quarteto do Som usa um selo amaldiçoado. Seus selos, no entanto, não são tão poderosos quanto os selos do Céu e da Terra, embora tenham resultados finais semelhantes. Como todos os selos amaldiçoados, cada um deles vigorosamente atrai o chakra do usuário, enquanto alimenta de volta com o chakra do senjutsu, dando a cada indivíduo uma transformação única e impulso atributo. No entanto, Sakon comenta que depois de usar o segundo nível de seu selo amaldiçoado e sair dela, eles perdem muita energia.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2017, E'Selo Aprisionador do Veado e da Borboleta', E'Shikamaru imobiliza o oponente com sua sombra enquanto Chōji expande seu corpo para esmagá-lo com um pisão.', E'鹿蝶封結かちょうふうけつ', E'Kachōfūketsu', E'Selo Aprisionador do Veado e da Borboleta', E'Kachofuketsu', NULL, NULL, E'Curto alcance', NULL, NULL),
(2018, E'Selo Consumidor do Demônio Morto', E'O Selo Consumidor do Demônio Morto é um selo desenvolvido pelo Clã Uzumaki para invocar o poder do Shinigami.', E'屍鬼封尽', E'Shiki Fūjin', E'Selo Consumidor do Demônio Morto', NULL, E'Selo do Aprisionamento Demoníaco', E'Jutsu de Selamento: Selo do Ceifeiro da Morte', E'Todos os alcances', E'Rank S', E'Cobra → Javali → Carneiro → Coelho → Cao → Rato → Passaro → Cavalo → Cobra → Juntar as Mãos'),
(2019, E'Selo Consumidor do Demônio Morto: Liberar', E'Esta técnica envolve a invocação do Shinigami usando uma máscara do Templo Armazenador de Máscara do Clã Uzumaki. A partir daí, o invocador é possuído pelo Shinigami permitindo-lhe o controle sobre ele, ele pode forçá-lo a cortar o seu próprio estômago resultando na liberação das almas seladas dentro. Devido à posse no entanto, o mesmo dano vai refletir sobre o próprio corpo do invocador.', E'屍鬼封尽・解しきふうじん・かい', E'Shiki Fūjin: Kai', E'Selo Consumidor do Demônio Morto: Liberar', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2020, E'Selo Gedō: Liberar', E'Essa técnica foi criada por Hiruzen na Segunda Guerra Mundial Ninja preocupando-se com os efeitos cruéis da Técnica Proibida: Selo Gedō. Esta técnica pode curar os efeitos do Selo do Caminho Exterior. Tsunade usa essa técnica para curar o Sharingan de Kakashi.', E'外道の印・解げどうのいん・かい', E'Gedō no In: Kai', E'Selo Caminho Exterior: Liberar', E'Técnica Proibida: Marca do Gedō: Liberar', NULL, NULL, E'Curto alcance', NULL, E'Cachorro → Rato → Passaro → Cobra → Javali → Tigre → Dragao → Macaco → Cobra → Boi → Carneiro → Pássaro'),
(2021, E'Selo Proibido da Maldição Individual', E'Este é um selo amaldiçoado, que é colocado diretamente no coração do alvo.', E'禁個呪の札きんこじゅのふだ', E'Kinkoju no Fuda', E'Selo Proibido da Maldição Individual', NULL, NULL, NULL, NULL, NULL, NULL),
(2022, E'Selo Torii', E'Quando Naruto adquiriu o Modo de Chakra do Nove-Caudas, a aparência exterior de seu selo anterior se transformou em uma forma de uma das magatamas do Sábio dos Seis Caminhos, junto com um redemoinho que se assemelhava ao símbolo de Uzushiogakure.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2023, E'Selo Yin: Liberar', E'Quando o usuário do Selo da Força de Uma Centena faz uso dessa técnica, o selo em formato de diamante alastra-se no usuário como \'fitas\', e instantaneamente solta a reserva de chakra diretamente no organismo do utilizador. Se houver um restante de chakra ao terminar de fazer uso da reserva de chakra, o utilizador consegue usar o restante na reforma do selo.', E'陰封印・解いんふういん・かい', E'Infūin: Kai', E'Selo Yin: Liberar', NULL, E'Liberação do Selo Yin', E'Selo Reserva: Liberar', NULL, E'Rank S', NULL),
(2024, E'Selo da Armadura de Ferro', E'O Selo da Armadura de Ferro é o fūinjutsu usado por Kumogakure para selar Gyūki em seus respectivos hospedeiros.', E'鉄甲封印てっこうふういん', E'Tekkō Fūin', E'Selo da Armadura de Ferro', E'Selo do Escudo de Aço', E'Selo de Blindagem de Ferro', NULL, NULL, NULL, NULL),
(2025, E'Selo da Escultura de Dedo', E'O usuário concentra chakra em seu dedo e, usando o calor deste, pode aplicar a escrita em tudo o que ele quiser numa superfície. Esta técnica exige, contudo, que ele seja muito delicado com seu controle de chakra. Esta técnica causa um leve desconforto em um alvo vivo e, se ele é de carne, cicatrizará.', E'指刻封印しこくふういん', E'Shikoku Fūin', E'Selo da Escultura de Dedo', NULL, NULL, NULL, NULL, E'Rank C', NULL),
(2026, E'Selo da Força de Uma Centena', E'Um selo com a fama de ser o auge do controle de chakra. Quando armazena-se um imenso valor de chakra durante muito tempo em um único local do corpo — geralmente na testa — o selo então é formado, manifestando-se como uma marcação na forma de um losango. Uma vez formado, o usuário garante um controle de chakra muito alto, conseguindo executar técnicas sem nenhum gasto de energia. Quando libera-se o selo, ele começa a se alastrar no rosto do usuário ou até mesmo no corpo inteiro, e em seguida, o chakra armazenado é liberado, aumentando em muito a força das técnicas do usuário.', E'百豪の印びゃくぎうのいん', E'Byakugō no In', E'Selo da Força de Uma Centena', NULL, E'Selo das Cem Forças', E'Selo Reserva', NULL, E'Rank S', NULL),
(2027, E'Selo de Apagar Memória', E'Esta técnica elimina certas lembranças de todos aqueles que estão no selo, incluindo a do usuário, com o comando \'destruir\' (滅, metsu). No entanto, é possível que o alvo possa, mais uma vez lembrar o que foi apagado como um sonho vago.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre'),
(2028, E'Selo de Ataúde do Dez-Caudas do Seis Caminhos', E'O Selo de Ataúde do Dez-Caudas do Seis Caminhos (六道十尾柩印, Rikudō Jūbi Kyūin) é uma técnica de selamento usada por Obito e Madara Uchiha para selar o Dez-Caudas em si mesmos, tornando-se assim o jinchūriki da besta. Depois que um usuário do Rinnegan executa os selos de mão necessários, enquanto em estreita proximidade com o Dez-Caudas, a besta é rapidamente absorvida para o corpo do usuário.', E'六道十尾柩印りくどうじゅうびきゅういん', E'Rikudō Jūbi Kyūin', E'Selo de Ataúde do Dez-Caudas do Seis Caminhos', NULL, NULL, NULL, E'Curto alcance', E'Rank –', E'Carneiro espelhado'),
(2029, E'Selo de Auto-Amaldiçoamento', E'Trata-se de uma técnica similar à Técnica de Erradicação da Língua Amaldiçoada, este selo também pode ser aplicado durante um combate, e pode ser secretamente colocado no corpo do oponente. Quando o usuário o ativa, as marcas do selo amaldiçoado espalham-se pelo corpo do oponente, paralisando-o. No entanto, é possível se livrar do selo através de uma liberação de chakra forte o suficiente.', E'自業呪縛の印じごうじゅばくのいん', E'Jigō Jubaku no In', E'Selo de Auto-Amaldiçoamento', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2030, E'Selo de Chakra da Besta com Cauda', E'Esta é uma técnica de colaboração em que oito pessoas realizam simultaneamente o selo de mão do Tigre, formando uma grande fórmula de selagem com um jinchūriki em seu centro. A fórmula, então, começa a brilhar e suprimir o chakra da Bijuu.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto Alcance', NULL, E'Tigre'),
(2031, E'Selo de Contrato', E'Quando o usuário executa esta técnica em um invocador, ele remove quaisquer controles do invocador sobre uma criatura invocada. O contato, para a implementação do selo, é necessário. Minato Namikaze usou esta técnica para remover Kurama do controle do Sharingan de Tobi. Ainda assim, mesmo removendo a capacidade do invocador sobre uma criatura, o usuário também não é capaz de controlá-la.', E'契約封印けいやくふういん', E'Keiyaku Fūin', E'Selo de Contrato', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2032, E'Selo de Drenagem de Chakra', E'Este é um fūinjutsu projetado para drenar chakra. Ao colocar o selo no alvo, o chakra é drenado e transferido para o usuário. Kabuto utilizou para absorver os restos remanescentes do chakra de Orochimaru que estava dentro do selo amaldiçoado de Anko Mitarashi.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2033, E'Selo de Quatro Símbolos', E'Esse é uma técnica de selamento baseada nos fūinjutsu de Uzushiogakure. Essa fórmula de selamento é gravada num corpo humano ou num objeto, e é principalmente usada quando um inimigo gigante ou um espirito do mal precisa ser selado. Para usá-lo, é necessário uma grande habilidade do usuário.', E'四象封印', E'Shishō Fūin', E'Selo de Quatro Símbolos', NULL, NULL, NULL, E'Curto alcance', E'Rank –', NULL),
(2034, E'Selo de Teletransporte de Quatro Cantos', E'Se qualquer um passar pela etiqueta com o selo, sapos são invocados para parar os inimigos que desejam passar através do selo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2035, E'Selo de Transcrição: Amaterasu', E'Esta é uma técnica utilizável apenas por aqueles que despertaram o Mangekyō Sharingan, é um ninjutsu de selamento da Liberação de Fogo. A técnica sela o fogo de mais alto nível, Amaterasu, no Sharingan de outro ninja, uma vez que o Sharingan ao qual a técnica foi selada vê o alvo especificado, o selo é quebrado e a chama negra que consome até mesmo chamas ataca sua presa.', E'転写封印・天照てんしゃふういん・あまてらす', E'Tensha Fūin: Amaterasu', E'Selo de Transcrição: Céu Iluminado', NULL, NULL, NULL, E'Curto alcance', E'Rank –', NULL),
(2036, E'Selo de Transcrição: Izanagi', E'Esta é uma técnica que sela a técnica Izanagi no Sharingan do alvo. Depois de ser selado dentro da pessoa, é involuntariamente ativado quando certos requisitos, que são pré-determinados pelo usuário, sejam satisfeitos. Madara Uchiha usou isso em seu próprio olho direito, para a técnica ser ativada depois que ele morrer, a fim de reescrever a sua morte em um mero sonho.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2037, E'Selo de Transcrição: Kotoamatsukami', E'Esta é uma técnica que sela o Kotoamatsukami no Sharingan do alvo. Depois de ser selado dentro da pessoa, ele é involuntariamente ativado quando certos requisitos, que são pré-determinados pelo usuário, sejam satisfeitos.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2038, E'Selo do Corte Relâmpago', E'Empunhando sua espada infundida com relâmpago, o usuário rapidamente desfere um corte no oponente, selando temporariamente sua capacidade de usar chakra para executar novas técnicas.', E'雷切封らいきりふう', E'Raikirifū', E'Selo do Corte Relâmpago', E'Corte do Selamento', NULL, NULL, E'Curto alcance', NULL, NULL),
(2039, E'Selo do Hexagrama de Sensoriamento', E'O usuário cria um selo em forma de hexagrama no solo o qual o permite a sensoriar alvos num raio de grande amplitude através da transmissão de ondas pelo selo. Esta técnica também permite identificar a classificação de um ninja.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2040, E'Selo do Santuário', E'O Selo do Santuário é um selo localizado no País dos Demônios, e contém o corpo do espírito maligno de Mōryō.', E'社の封印', E'Yashiro no Fūin', E'Selo do Santuário', NULL, NULL, NULL, NULL, NULL, E'Hatsu → Jin → Kai → Min → Shin → Gan → Reppyo → Shozen → Mika → Dan → Raku → Sho → Fu → Sai → Dan → Gik → Ragu → Bagi'),
(2041, E'Selo do Santuário: Liberar', E'Depois de realizar os selos de mão (ou melhor, cantar em voz alta como um encantamento), o usuário libera uma onda de choque escuro que destrói os talismãs sobre o Selo do Santuário, e quebra a porta de pedra, libertando a alma aprisionada do Mōryō. Isto pode ser uma variante do Selo do Santuário, ou mesmo a contraparte, uma vez que contém uma porção dos selos de mão que a técnica de selamento faz.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, E'Min → Shin → Gan → Reppyo → Shozen → Mika → Dan → Raku → Sho'),
(2042, E'Selo do Sino Místico de Segurança: Liberar', E'Técnica utilizada por Shion, consiste em liberar o poder através do Sino de Segurança Místico, e se transformar. Isto permite que ela envie um enorme raio de luz que pode penetrar a escuridão de Mōryō. Nesta forma, ela teve a capacidade de (tentar) destruir completamente Mōryō junto com ela mesma. No entanto, Shion acabou sendo puxada por Naruto antes que pudesse fazê-lo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2043, E'Selo dos Cinco Elementos', E'Essa técnica produz um selo poderoso que é usado para bloquear ou atrapalhar o fluxo do chakra num alvo. Assim que o selo é completo, o alvo ficará inconsciente e incapaz de lutar.', E'五行封印', E'Gogyō Fūin', E'Selo dos Cinco Elementos', NULL, NULL, E'Selo de Cinco Pontos', E'Curto alcance', E'Rank A', E'Passaro'),
(2044, E'Senbon de Agulha de Cabelo', E'É uma técnica em que o cabelo é enrijecido e disparado em sequência no inimigo como se fosse uma chuva de espinhos, transformando o alvo em uma montanha de agulhas.', E'毛針千本', E'Kebari Senbon', E'Senbon de Agulha de Cabelo', NULL, NULL, E'Arte Ninja: Agulha Senbon', E'Curto a Médio alcance', E'Rank –', NULL),
(2045, E'Senbon de Escama', E'Amachi abre os braços e lança suas escamas em alta velocidade no alvo. Esta técnica tinha poder o suficiente para dissipar um clone sombra e jogar Naruto Uzumaki para trás em uma distância considerável. Além disso, ele tinha uma gama de alcance bastante ampla para acabar com seis clones de sombra de uma vez.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2046, E'Senjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2047, E'Sensoriamento Negativo', E'É uma técnica sensorial exclusiva para Kurama e seus jinchūriki. Usando esta técnica, o usuário é capaz de detectar as emoções negativas, como ódio, de qualquer indivíduo em torno deles.', E'悪意感知あくいかんち', E'Akui Kanchi', E'Sensoriamento Negativo', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2048, E'Sensoriamento Triunfante', E'Graças aos árduos treinos de concentração, o usuário é capaz de sentir a presença de seus inimigos durante uma batalha ao fechar os olhos, um estilo que Guy chama de \'Olhos do Coração\' (心の目, Kokoro no Me). Esta técnica torna-se muito útil quando o usuário tem alguns de seus cinco sentidos básicos afetados, por exemplo enquanto ele estiver em meio de uma cortina de fumaça odorizada.', E'ガッツセンサー', E'Gattsusensā', E'Sensoriamento Triunfante', NULL, NULL, E'Sensor de Espírito', E'Curto alcance', NULL, NULL),
(2049, E'Sensoriamento de Medo', E'Devido à Caixa da Felicidade Suprema, o corpo de Satori pode detectar medo e intenções assassinas, o que faz com que ele consiga instintivamente esquivar de quaisquer ataques de seus inimigos que têm medo inclusos. Esta capacidade, combinada com seus reflexos surpreendentemente ágeis, lhe permitiram esquivar todos os ataques dos 11 de Konoha e Naruto Uzumaki, mesmo enquanto ele estava no Modo Sábio.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2050, E'Sensual: Técnica da Menina com Menina', E'A Técnica do Harém de Naruto, personalizada por Konohamaru. É feita utilizando a Técnica dos Clones das Sombras e consecutivamente a Técnica de Transformação, por ter de usar o próprio usuário e o clone transformado em pessoas diferentes. Quando várias meninas diferentes parecem sedutores, as chances de satisfazer as preferências do alvo são elevadas significativamente. Konohamaru inventou uma forma alternativa da técnica para trabalhar em mulheres, Sensual: Técnica do Menino com Menino.', E'おいろけ・女の子どうしの術', E'Oiroke: Onnanoko Dōshi no Jutsu', E'Sensual: Técnica da Menina com Menina', NULL, NULL, NULL, NULL, E'Rank D', NULL),
(2051, E'Sensual: Técnica do Menino com Menino', E'Uma técnica que implementa o Sensual: Técnica da Menina com Menina. Uma técnica que confunde e excita as mulheres, transformando-se em dois homens nus. Ao contrário de quando se transformando em meninas, outras coisas são vitais além proporções, como a seleção e combinação dos homens em que se transforma e como eles representam. Ao ser bem familiarizado com a pessoa que a técnica é direcionada para a transformação e adaptação em conformidade, pode-se entregar um impacto ainda maior.', E'おいろけ・男の子どうしの術', E'Oiroke: Otokonoko Dōshi no Jutsu', E'Sensual: Técnica do Menino com Menino', NULL, NULL, NULL, NULL, E'Rank D', NULL),
(2052, E'Sepultura de Areia', E'Gaara, manipulando sua areia, cria uma curta linha embaixo de seus pés, que tem um alcance pequeno. Em seguida, ele vibra essa areia e se agacha tocando o chão com sua mão, assim a linha de areia se estende até embaixo dos pés do oponente, vibrando agora em uma potência maior, atingindo o oponente.', NULL, NULL, NULL, E'Sepultura de Areia', NULL, NULL, E'Curto alcance', NULL, NULL),
(2053, E'Sermão', E'Iruka acerta o oponente com diversos golpes de kunai e chutes, finalizando com um grito estrondoso que manda o oponente para longe.', E'お説教おせっきょう', E'Osekkyō', E'Sermão', E'Reclamação', NULL, NULL, E'Curto alcance', NULL, NULL),
(2054, E'Serpente de Aço Líquido', E'Depois de usar a Invocação: Serpente de Duas Cabeças, serpentes emergem da boca da cobra invocada, assim entrando em uma ordem contínua do processo na tentativa de capturar seu alvo. Como as cobras continuam a se multiplicar, elas se tornam cada vez menores e diminui o potencial da técnica.', E'蛇鋼網', E'Dakōmō', NULL, NULL, NULL, NULL, E'Curto a Médio', NULL, NULL),
(2055, E'Sete Espadas da Névoa Sangrenta', E'Os Sete Espadachins Ninja da Névoa reúnem-se, e uma névoa espessa envolve o campo de batalha. Ameyuri dirige um ataque, entregando um corte horizontal reforçado com eletricidade com as Kiba, seguido por Kisame cortando com a Samehada na direção contrária. Jinin lança o oponente no ar com a parte machado da Kabutowari, onde é encontrado pelo ataque de Jinpachi utilizando a Shibuki, que cria uma explosão. De certa distância, Mangetsu corta o oponente com a forma de espada longa de Hiramekarei, e Kushimaru esfaqueia-o repetidas vezes, amarrando-os no lugar com fios de aço. Finalizando, Zabuza aterra um golpe final com a Kubikiribōchō.', E'血霧の七刀', E'Chigiri no Shichigatana', E'Sete Espadas da Névoa Sangrenta', E'Sete Lâminas da Névoa Sangrenta', NULL, NULL, E'Curto alcance', NULL, NULL),
(2056, E'Sharingan', E'O Sharingan (写輪眼; Literalmente significa \'Olho Copiador Giratório\'; Significado (Panini) \'Olho Espelhador Giratório\') é um dōjutsu kekkei genkai, que aparece em alguns membros do clã Uchiha. Apesar de seus poderes serem vistos pela primeira vez em Kaguya Ōtsutsuki, com o Rinne Sharingan, sua verdadeira forma se manifestou pela primeira vez em Indra Ōtsutsuki, o ancestral mais antigo do clã Uchiha, e mais tarde veio a ser considerado como um dos Três Grandes Dōjutsu (三大瞳術, Sandai Dōjutsu; Literalmente significa \'Três Grandes Técnicas Oculares\'), juntamente com o Byakugan e o Rinnegan.', E'写輪眼しゃりんがん', E'Sharingan', E'Olho Copiador Giratório', NULL, E'Olho Espelhador Giratório', E'Olho de Sharingan', NULL, NULL, NULL),
(2057, E'Shinra Tensei', E'Shinra Tensei é a habilidade do Caminho Deva para manipular a força repulsiva (斥力, sekiryoku) à vontade do usuário para empurrar a matéria ou técnicas ninja à distância. Somente os portadores do Rinnegan que dominaram todas as transformações básicas da natureza têm acesso a esta técnica.', E'神羅天征しんらてんせい', E'Shinra Tensei', E'Subjugação Celestial do Deus Onipresente', NULL, E'Domínio Divino', E'Punição Divina', E'Todos os alcances', E'Rank –', E'Juntar as Maos[4]'),
(2058, E'Shuradō', E'O Caminho Asura (修羅道, Shuradō) concede ao usuário a capacidade de invocar armadura mecanizada, aumentando seu corpo com membros extras e vários armamentos mecânicos. Em vida, Nagato canalizou o Caminho Asura através do corpo de um ninja titereiro.', E'修羅道しゅらどう', E'Shuradō', E'Caminho Asura', NULL, E'Reino da Guerra', NULL, NULL, NULL, NULL),
(2059, E'Shuriken de Areia', E'Uma técnica utilizada por Gaara enquanto no processo de transformação para sua forma perfeita. Ele molda a areia que compõe seus braços em incontáveis shuriken e balança os ambos os membros violentamente. Aproveitando-se dessa formidável força centrífuga, as lâminas de areia atiradas tornam-se perigosas armas e, dessa forma, o usuário dispara uma sucessão interrupta de shuriken de areia, que voam selvagemente rasgando tudo em seu caminho.', E'砂手裏剣', E'Suna Shuriken', E'Shuriken de Areia', E'Técnica da Shuriken de Areia (砂手裏剣の術, Suna Shuriken no Jutsu)[2]', E'Shuriken da Areia', NULL, E'Curto a Médio alcance', NULL, NULL),
(2060, E'Shuriken de Flor: Flores em Queda e Folhas em Espalhamento', E'Esta técnica faz com que as flores pairem no ar antes de endurecer e girarem em altíssima velocidade. As flores vão em direção ao alvo, e por estarem totalmente endurecidas pelo chakra, elas recebem a função de uma shuriken normal, que realiza cortes no corpo do alvo.', E'花手裏剣・飛花落葉はなしゅりけん・ひからくよう', E'Hana Shuriken: Hikarakuyō', E'Shuriken de Flor: Flores em Queda e Folhas em Espalhamento', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(2061, E'Shuriken de Papel', E'Esta é uma técnica que consiste em despejar chakra em um pedaço de papel em uma fração de segundo, endurecendo e dando forma, então ele pode ser usado como uma shuriken. A sua forma é igual à de uma shuriken feita de metal. O utilizador pode aumentar ainda mais a força da shuriken, transformando-a em determinadas formas e ainda manusear as mesmas sem precisar ter contato. O tempo de contato e o esforço colocado no papel fazem a eficiência do corte se elevar ao extremo. Konan é vista usando-as juntamente com a Liberação de Vento: Palma Vendaval de Nagato, agilizando a shuriken e permitindo-lhes penetrar o corpo do inimigo ainda mais.', E'紙手裏剣かみしゅりけん', E'Kami Shuriken', E'Shuriken de Papel', NULL, NULL, NULL, E'Médio a Longo alcance', E'Rank D', NULL),
(2062, E'Shuriken de Telha', E'Esta técnica permite ao usuário controlar remotamente, levitar e arremessar telhas girando em uma área próxima. Além de ser capaz de controlar remotamente as telhas, o utilizador pode exercer pressão sobre elas com chakra para torná-las muito mais fortes do que o normal.', E'瓦手裏剣', E'Kawara Shuriken', E'Shuriken de Telha', NULL, NULL, E'Arte Ninja: Shuriken de Telha', E'Curto à Médio alcance', NULL, E'Tigre → Dragao → Rato → Cavalo → Tigre'),
(2063, E'Shuriken de Todas Direções', E'Uma técnica que cerca perfeitamente o inimigo em todas as direções com sua própria \'sombra\'. Naruto e seus clones das sombras arremessam várias shuriken de uma só vez. Evitar toda a chuva de shuriken é difícil, mesmo para as pessoas mais ágeis. O único modo de se evitar esse ataque é se o inimigo conseguir focar seus sentidos nas shuriken de cima. Seu próximo passo é atacar o ponto cego, porém, ele dificilmente é criado. À primeira vista, parece ser um movimento desesperado, mas é um ataque inteligente, com uma trama oculta para conduzir o inimigo em uma situação difícil, dando um senso de domínio de Naruto sobre o método de assassinato com os clones das sombras.', E'四方八方手裏剣', E'Shihōhappō Shuriken', E'Shuriken Indefensável', NULL, E'Shuriken pra Todo Lado', NULL, E'Curto a Médio alcance', E'Rank A', NULL),
(2064, E'Shurikenjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2065, E'Shurikenjutsu de Relâmpago do Estilo Uchiha: Triplicação', E'Uma técnica que emprega conceitos de shurikenjutsu transmitidos dentro do clã Uchiha, com a qual o usuário prepara três shurikens e as infunde com chakra de relâmpago, aprimorando seu poder destrutivo. As shurikens então são lançadas para três direções distintas, acertando múltiplos alvos ou um único alvo em diferentes ângulos.', E'うちは流手裏剣術 「雷」・三連うちはりゅうしゅりけんじゅつ 「いかずち」・さんれん', E'Uchiha-Ryū Shurikenjutsu — \'Ikazuchi\': Sanren', E'Shurikenjutsu de Relâmpago do Estilo Uchiha: Triplicação', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2066, E'Soco 100% Único', E'O usuário canaliza uma grande quantidade de chakra em sua mão e ataca o oponente no estômago.', E'100％シングルパンチ', E'100-Pāsento Shingurupanchi', E'Cem Por Cento Soco Único', E'Soco 100% Único', NULL, NULL, E'Curto alcance', NULL, NULL),
(2067, E'Soco Naruto', E'Mecha-Naruto ataca o oponente com um soco alimentado com um jato.', E'ナルトパンチ', E'Naruto Panchi', E'Soco Naruto', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2068, E'Soco de 1000 Metros', E'O usuário concentra uma grande quantidade de chakra em seu punho e soca o adversário, enviando-o à pelo menos um quilômetro (1.000 metros) de distância.', E'1000Mパンチ', E'1000M Panchi', E'Soco de Mil Metros', E'Soco de 1000m', NULL, NULL, E'Curto alcance', NULL, NULL),
(2069, E'Soco de 100 Metros', E'Uma versão pequena do Soco de 1000 Metros. O usuário concentra seu chakra em seu punho e usa um golpe devastador sobre o adversário, fazendo ele voar para pelo menos 100 metros de distância de onde o usuário se encontrava.', E'100mパンチ', E'100 Panchi', E'Soco de Cem Metros', E'Soco de 100m', NULL, NULL, E'Curto alcance', NULL, NULL),
(2070, E'Som dos Sinos', E'Kin usa essa técnica com os sinos que ela atira junto com suas senbon. O toque do sino produz uma onda sonora especial, que viaja a partir da orelha interna, o que afeta o cérebro do adversário diretamente. Isso faz com que seu alvo tenha alucinações, vendo várias imagens dela, e tornando muito difícil dizer o que é real e o que são ilusões. Além disso, o alvo perde suas habilidades motoras com a exposição continuada ao toque. Esta técnica é normalmente utilizada depois que ela distrai o inimigo com as Agulhas da Sombra.', E'鐘の音', E'Kane no Oto', E'Som dos Sinos', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(2071, E'Sombra da Folha Dançante', E'Uma técnica onde se segue o adversário ou o movimento do seu corpo, tal como uma folha que dança no ar que é seguida pela sua sombra. A técnica é geralmente iniciada por um chute alto que irá lançar o alvo no ar. É usado principalmente para mudar a posição do oponente em uma posição vulnerável aérea. Este movimento, por si só é inofensivo, mas é utilizável como um trampolim para um grande número de técnicas poderosas, como a Lótus Frontal e o Combo do Leão. Dito isto, também tem um grau bastante elevado de dificuldade.', E'影舞葉かげぶよう', E'Kage Buyō', E'Sombra da Folha Dançante', NULL, NULL, NULL, E'Curto alcance', E'Rank C', NULL),
(2072, E'Sonchus Asper', E'O usuário, enquanto em sua forma de selo amaldiçoado nível dois, dá um pulo estilo mortal e ataca o oponente com sua cauda criada através do selo amaldiçoado.', E'オニノゲシ', E'Oninogeshi', E'Sonchus Asper', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2073, E'Sonho da Folha Carmesim', E'Kurenai ataca seu oponente com um ataque combo triplo resumindo em um golpe de cotovelo e dois chutes antes de desaparecer em uma névoa na frente do oponente e, em seguida, aparecer novamente atrás dele em uma névoa rosa golpeando-o com uma kunai por trás, que, em seguida, lança o oponente no ar.', E'紅葉夢', E'Kōyōmu', E'Sonho da Flor Carmesim', E'Sonho da Folha Vermelha', NULL, NULL, E'Curto alcance', NULL, NULL),
(2074, E'Sopro de Vento do Gunbai', E'O usuário usa seu gunbai como um leque para criar poderosas rajadas de vento, fortes o bastante para afastar vários inimigos de uma só vez.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2075, E'Soulbane', E'Soulbane é um genjutsu poderoso conhecido por ser proibido entre os ninjas de Konohagakure.', NULL, NULL, NULL, E'Soulbane', NULL, NULL, NULL, NULL, NULL),
(2076, E'Subida do Pardal', E'Sai, enquanto em um curto alcance, libera de um pergaminho pássaros feitos de tinta em tamanho médio, dos quais primeiro levantam o oponente para cima, que está sendo levantado ainda mais a cada segundo pelos mesmos.', E'登燕雀', E'Tōenjaku', NULL, NULL, NULL, NULL, E'Curto Alcance', NULL, NULL),
(2077, E'Subida do Pássaro Triturador', E'Sasuke atinge o oponente sob o queixo com a bainha da espada. Então ele chuta-o três vezes enviando-o ainda mais para o alto. Como o oponente cai no chão, Sasuke aparece onde ele está prestes a cair e bate-o poderosamente no intestino, levando-o a colidir com o que quer que esteja no caminho.', E'翔け鳥崩し', E'Kakedori Kuzushi', E'Subida do Pássaro Triturador', E'Esforço do Pássaro Triturador', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2078, E'Substituição Orizuru', E'Antes de ser atingido por um ataque, Urashiki ativa seu Rinnegan e troca de lugar com dezenas de orizurus vermelhos, a fim de evitar ser atingido. Após a troca, o chakra do usuário é completamente mascarado enquanto a fuga é realizada.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2079, E'Substituição de Água', E'Quando o usuário é atingido por um ataque esta técnica vai transformá-lo em água para assim evitar levar danos diretos de quaisquer formas de investida.', E'水変わり身みずかわりみ', E'Mizu Kawarimi', E'Substituição de Água', NULL, NULL, E'Arte Ninja: Técnica da Submissão de Água', NULL, NULL, NULL),
(2080, E'Substituição por Marionete', E'A Substituição por Marionete é uma técnica variante da Técnica de Substituição do Corpo que concede ao usuário a capacidade de substituir seu corpo por uma de suas marionetes, e utilizar como um método de distração para a realização de alguma técnica de captura.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2081, E'Sucessão de Fogo, Água, Terra e Relâmpago', E'A Sucessão de Fogo, Água, Terra e Relâmpago trata-se de uma formação de ataques elementais utilizada pela Primeira Divisão para conter Kinkaku.', E'火、水、土と雷の連続ひ,みず,つちとかみなりのれんぞく', E'Hi, Mizu, Tsuchi to Kaminari no Renzoku', E'Sucessão de Fogo, Água, Terra e Relâmpago', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2082, E'Sucção Esmagadora das Três Jóias', E'Esta técnica é uma performance das marionetes 6, 7 e 8 da coleção de Chikamatsu. As três marionetes entram em uma formação triangular e abrindo os mecanismos marcados com \'Buda\', \'Dharma\' e \'Sangha\', respectivamente, eles formam a técnica. Um tornado terrível suga os alvos para o centro da formação. Tudo o que é sugado para o centro é completamente esmagado com uma força tremenda, sendo jogados para fora da formação em seguida. No entanto, quando vácuo é sobrecarregado, os três bonecos se tornam vulneráveis aos ataques.', E'三宝吸潰さんぼうきゅうかい', E'Sanbō Kyūkai', E'Sucção das Três Jóias do Esmagamento', NULL, E'O Triturador dos Três Tesouros', E'Destruição no Vácuo dos Três Tesouros', E'Todos os alcances', E'Rank B', NULL),
(2083, E'Sujeição do Sapo — Técnica de Manipulação das Sombras', E'Após infundir chakra na sombra da vítima, o usuário faz o seu próprio corpo ficar o mais plano possível. Então, tornando-se um com a sua sombra, esta técnica estabelece temporariamente o controle sobre a mente e o corpo da vítima. Enquanto a técnica está em efeito, a vítima fica temporariamente atordoada e pode servir como escudo humano. O usuário tem que prender a respiração, mas ele pode falar através da vítima. Assim que a respiração do usuário recomeça, a técnica é anulada e o seu corpo ultra-fino se expande outra vez, retornando a sua espessura original.', E'蝦蟇平　影操りの術がまだいら　かげあやつりのじゅつ', E'Gamadaira — Kageayatsuri no Jutsu', E'Sujeição do Sapo — Técnica de Manipulação das Sombras', NULL, NULL, NULL, E'Curto alcance', E'Rank B', NULL),
(2084, E'Sukunahikona', E'Ao apontar sua mão, Jigen é capaz de encolher objetos para tamanhos microscópicos e, do mesmo modo, fazê-los retornar ao seu tamanho original de forma instantânea, de modo que olhos normais não podem detectar, fazendo parecer que os objetos surgiram do nada. Esse efeito parece se limitar à objetos inanimados ou ao próprio usuário.', E'少名毘古那スクナヒコナ', E'Sukunahikona', E'Pequeno Homem de Renome', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2085, E'Super Bofetada Abrupta', E'Karin dá um tapa em seu oponente a fim de anexar um papel explosivo a seu rosto. Quando o oponente cai longe, o selo é detonado automaticamente.', E'激ツンビンタげきつんびんた', E'Geki Tsunbinta', E'Super Bofetada Abrupta', E'Super Tapa Agressivo', NULL, NULL, E'Curto alcance', NULL, NULL),
(2086, E'Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três', E'A Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três é uma técnica criada por Minato Namikaze e que emprega o uso de sua Técnica do Deus Voador do Trovão.', E'螺旋閃光超輪舞吼参式', E'Rasen Senkō Chō Rinbukō Sanshiki', E'Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três', NULL, E'Terceira Forma da Dança do Super-Rugido de Lampejo em Espiral Estágio Três', NULL, E'Todos os alcances', NULL, NULL),
(2087, E'Super Grande Bola Rasenshuriken das Bestas com Cauda', E'Naruto entra em seu Modo de Chakra do Nove-Caudas e manifesta o corpo de Kurama em sua forma plena, então ele cria nove Rasenshuriken em suas caudas, cada um infundido com as propriedades elementais das respectivas Bestas com Caudas. Kurama, então, arremessa os Rasenshuriken em direção ao seu oponente, culminando em múltiplas explosões.', E'超尾獣大玉螺旋手裏剣ちょうおけものだいたまらせんしゅりけん', E'Chō Bijū Ōdama Rasenshuriken', E'Super Grande Bola Rasenshuriken das Bestas com Cauda', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2088, E'Super Luz do Pé', E'Minato corre em direção ao seu oponente e chuta-o no joelho. Em seguida, ele desliza para trás e chuta novamente o mesmo joelho, de tal maneira que o oponente voa para o ar.', E'超光脚', E'Chōkōkyaku', E'Super Luz do Pé', E'Super Raio de Luz do Pontapé', NULL, NULL, E'Curto alcance', NULL, NULL),
(2089, E'Super Mini-Bola da Besta com Cauda', E'Enquanto no Modo de Chakra do Nove-Caudas, Naruto reúne e equilibra o chakra positivo preto e negativo branco necessários para formar uma miniatura condensada da Bola da Besta com Cauda. Com o impacto, a bola explode, resultando em um raio de explosão muito considerável. Tal como acontece com o Rasengan, ele pode estender a bola para a frente com o uso de um braço de chakra. Naruto pode usar um clone da sombra para ajudá-lo a formar a bola. Enquanto no Modo Bijū ele pode formá-la por conta própria.', E'超ミニ尾獣玉', E'Chō Mini Bijūdama', E'Super Mini-Bola da Besta com Cauda', E'Super Mini Bomba Bijū', E'Super-Mini Bomba Bijū', E'Super Mini Bomba da Besta com Cauda', E'Todos os alcances', NULL, NULL),
(2090, E'Super Presa do Lobo Predador', E'O homem e seu animal se transformam em um único animal gigantesco de duas cabeças pelo Transformação Combinada da Besta Humana: Lobo de Duas Cabeças, e tal como a sua técnica parente, ele tem sua rotação do corpo em um violento golpe contra o inimigo. O poder é grande o suficiente para romper três portões Rashōmon. Além do contrário de sua técnica parente que não exige primeiro o uso da Marcação Dinâmica.', E'超牙狼牙', E'Chō Garōga', E'Super Presa Lobo Presa', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(2091, E'Super Punho do Renascimento', E'Esta técnica funciona de forma semelhante ao Rasengan. Ao reunir chakra escuro na mão, o usuário pode criar uma esfera escura, com dois anéis escuros em torno dela. O usuário, então, lavra o orbe para o adversário, resultando no adversário ser atingido com tremenda força quebrando os ossos. De acordo com Shinnō, esta é a técnica suprema derivada do chakra escuro.', E'超活性拳', E'Chō Kasseiken', E'Super Punho do Renascimento', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2092, E'Super Rasengan Uzumaki', E'O Super Rasengan Uzumaki é um ninjutsu cooperativo usado por Boruto com o auxílio de Naruto.', E'超うずまき螺旋丸ちょううずまきらせんがん', E'Chō Uzumaki Rasengan', E'Super Esfera Espiral Uzumaki', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2093, E'Super Rasengan do Chakra de Naruto e Shion', E'Este é um Rasengan enorme criado com chakra de Naruto e Shion, apoiado por seu Sino de Segurança Místico. A técnica foi usada para destruir Mōryō.', E'ナルトとシオンの超チャクラ螺旋丸', E'Naruto to Shion no Chō Chakura Rasengan', E'Esfera Espiral do Super Chakra de Naruto e Shion', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2094, E'Super Tapa', E'Depois de utilizar a Técnica do Tamanho Múltiplo para aumentar o seu tamanho, o usuário acumula chakra nas suas mãos, o que provoca marcas especiais que aparecem nas mesmas. O peso e a potência das mãos são aumentadas, devido à maior dimensão, os músculos são ativados com chakra concentrado, e a espessura também é aumentada. A quantidade de chakra é tanta que se torna visível, as palmas do usuário tornam-se como um martelo de ferro. Isso dá a esse ataque o poder de nivelar a terra, causando danos de esmagamento extensivo a qualquer coisa que possa ser capturada abaixo delas. Este ataque é também mostrado ter uma ampla gama, devido ao aumento do tamanho das palmas das mãos.', E'超張り手ちょうはりて', E'Chōharite', E'Super Tapa', NULL, E'Super Bofetada', E'Britadeira Humana', E'Curto a Médio alcance', E'Rank B', NULL),
(2095, E'Suporte de Pino', E'Kimimaro, enquanto em seu selo amaldiçoado nível dois, salta no ar, agarra o oponente, lança-o para trás de si e gira-o para o lado, criando vários espinhos ósseos de seu corpo, acertando seu oponente.', E'剣山', E'Kenzan', E'Suporte de Pino', E'Rã', NULL, NULL, E'Curto alcance', NULL, NULL),
(2096, E'Suporte de Pino (Ultimate Ninja 3)', E'Kimimaro, enquanto em seu selo amaldiçoado nível dois, salta no ar, agarra o oponente, lança-o para trás de si e gira-o para o lado, criando vários espinhos ósseos de seu corpo, acertando seu oponente.', E'剣山', E'Kenzan', E'Suporte de Pino', E'Rã', NULL, NULL, E'Curto alcance', NULL, NULL),
(2097, E'Suporte de Pino (Ultimate Ninja Storm)', E'É uma versão em pequena escala da Dança das Mudas de Samambaia.', E'剣山', E'Kenzan', E'Suporte de Pino', E'Porta-Pino', NULL, NULL, E'Médio alcance', NULL, E'Bater ambas as maos no chão'),
(2098, E'Susanoo', E'O Susanoo é um humanoide feito de chakra daquele que luta em seu nome. É a terceira habilidade concedida àqueles que despertaram todas as habilidades de ambos os Mangekyō Sharingan.', E'須佐能乎スサノオ', E'Susanoo', E'Ele com a habilidade de ajudar por todos os meios', NULL, E'Deus da Tormenta', NULL, E'Todos os alcances', E'Rank –', NULL),
(2099, E'Susanoo: Advento do Relâmpago dos Deuses Gêmeos', E'Sasuke e Itachi ativam o Corpo Completo — Susanoo. Itachi lança sua Yasaka Magatama em seu seu oponente enquanto Sasuke o corta com sua Espada de Kagutsuchi, como resultado as chamas criam nuvens de relâmpago. Os dois voam para o céu, unindo suas espadas, então Sasuke a infunde com relâmpago e Itachi com chamas negras. Eles finalizam voando para baixo e atacando seu oponente com a espada, causando um impacto tão grande que é capaz de limpar o céu.', E'須佐能乎・双神雷臨', E'Susanoo: Sōjin Rairin', E'Ele com a habilidade de ajudar por todos os meios: Advento do Relâmpago dos Deuses Gêmeos', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2100, E'Susanoo: Chidori', E'Susanoo: Chidori é uma técnica utilizada por Sasuke Uchiha, que emprega o uso do Chidori ao Corpo Completo — Susanoo.', E'須佐能乎・千鳥スサノオ・ちどり', E'Susanoo: Chidori', E'Ele com a habilidade de ajudar por todos os meios: Mil Pássaros', E'Extremo Absoluto: Susanoo com Chidori (絶空・千鳥須佐能乎, Zekkū: Chidori Susanoo)[2]', NULL, NULL, E'Curto alcance', NULL, NULL),
(2101, E'Susanoo: Corte Relâmpago do Kamui', E'Após despertar o Mangekyō Sharingan em ambos os olhos, Kakashi manifesta o Corpo Completo — Susanoo e forma o Corte Relâmpago do Kamui imbuído com o Chakra dos Seis Caminhos em sua mão direita. Em seguida, ele descarrega a técnica em seu adversário, culminando em uma explosão de eletricidade.', E'須佐能乎・神威雷切スサノオ・カムイらいきり', E'Susanoo: Kamui Raikiri', E'Ele tem a capacidade de ajudar por todos os meios: Corte Relâmpago da Autoridade dos Deuses', E'Susanoo: Espada Relâmpago Kamui', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2102, E'Susanoo: Perfurar', E'O Susanoo: Perfurar é uma técnica utilizada por Sasuke Uchiha, através do uso da besta de seu Susanoo.', E'須佐能乎・貫', E'Susanoo: Kan', E'Ele com a habilidade de ajudar por todos os meios: Perfurar', E'Susanoo: Perfurar', NULL, NULL, E'Longo alcance', NULL, NULL),
(2103, E'Susanoo: Tsukumo', E'Após Shisui manifestar a parte superior do corpo de seu Susanoo, ele abre a caixa torácica para expor seu peito antes de lançar uma enorme enxurrada de grandes agulhas feitas de chakra de cor verde.', E'須佐能乎・九十九', E'Susanoo: Tsukumo', E'Aquele com a capacidade de ajudar por todos os meios: Noventa e Nove', E'Susano\'o: Tsukumo', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2104, E'Susanoo: Técnica da Grande Bola de Fogo', E'Sasuke Uchiha, usando o Corpo Completo — Susanoo como um meio, dispara uma rajada de bolas de fogo da boca projetada pelo Susanoo. Os Projéteis podem variar em tamanho, podendo se tornar tão grande quanto uma Bijū. Como a técnica não explode no impacto, as bolas de fogo podem ser repelidas com força suficiente. Ao lançar a técnica no céu, pode-se cria nuvens de raios, tornando possível o uso do Kirin.', NULL, NULL, NULL, NULL, NULL, NULL, E'Longo Alcance', NULL, NULL),
(2105, E'Suspensão do Deserto', E'A Suspensão do Deserto é um dos vários métodos auxiliares que Gaara utiliza durante o combate.', E'砂漠浮遊さばくふゆう', E'Sabaku Fuyū', E'Suspensão do Deserto', NULL, E'Flutuação de Areia', NULL, NULL, NULL, NULL),
(2106, E'Taijutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2107, E'Taijutsu de Agitação', E'Taijutsu de Agitação (攪乱体術, Kakuran Taijutsu) refere-se ao estilo de luta único e usual de Killer B.', E'攪乱体術かくらんたいじゅつ', E'Kakuran Taijutsu', E'Técnica Corporal de Agitação', NULL, NULL, NULL, E'Curto alcance', E'Rank A', NULL),
(2108, E'Taijutsu dos Múltiplos Clones das Sombras', E'O Taijutsu dos Múltiplos Clones das Sombras consiste em um estilo de combate corpo a corpo combinado ao uso de clones das sombras mediante a execução de formações pré-estabelecidas.', E'多重影分身の体術たじゅうかげぶんしんのたいじゅつ', E'Tajū Kage Bunshin no Taijutsu', E'Técnica Corporal dos Múltiplos Clones das Sombras', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2109, E'Takamimusubi-no-kami', E'Takamimusubi-no-kami é a técnica assinatura de Momoshiki Ōtsutsuki, que ele utiliza através do seu Rinnegan exclusivo.', E'高皇産霊神タカミムスビノカミ', E'Takamimusubi-no-kami', E'Grandioso Deus da Criação', NULL, NULL, NULL, NULL, NULL, NULL),
(2110, E'Takemikazuchi', E'Takemikazuchi é uma técnica criada por Sasuke Uchiha, que consiste em criar um Chidori em sua mão esquerda e infundi-lo com chamas negras para aumentar o seu poder destrutivo. Ele foi poderoso o suficiente para destruir as estátuas do Vale do Fim ao colidir com o Rasengan de Naruto, além de destruir metade do seu braço.', E'建御雷神タケミカヅチ', E'Takemikazuchi', E'Honorável Deus Criador do Trovão', E'Takemikazuchi-no-kami', NULL, NULL, E'Curto alcance', NULL, NULL),
(2111, E'Talhada de Corte Aprisionador', E'Consiste em desferir um golpe usando o cotovelo no oponente. O golpe ainda pode causar maiores danos se o outro braço for usado como apoio para aplicar mais força.', E'斬獄手刀', E'Zangoku Shutō', E'Talhada de Corte Aprisionador', E'Corte Infernal[1]', NULL, NULL, E'Curto alcance', NULL, NULL);
