INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(2112, E'Tanque da Bala Humana', E'O Tanque da Bala Humana é uma técnica destrutiva utilizada pelo clã Akimichi.', E'肉弾戦車にくだんせんしゃ', E'Nikudan Sensha', E'Tanque da Bala Humana', NULL, E'Carro de Combate, Tanque de Carne', E'Jutsu Massa Humana', E'Curto a Médio alcance', E'Rank C', NULL),
(2113, E'Tapa Ardente da Juventude', E'Após concentrar chakra em sua mão direita, Guy empurra o oponente à uma grande distância.', E'青春の熱き張り手せいしゅんのあつきはりて', E'Seishun no Atsuki Harite', E'Tapa Ardente da Juventude', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2114, E'Tarja da Barreira de Selamento', E'Um Kekkaijutsu que envolve o uso de várias etiquetas de selamento escondidas sob a superfície. Quando alguém pisa na área, ele fica contido por muitos segmentos da fórmula. A força é forte o suficiente para aniquilar completamente uma pessoa reencarnada.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto Alcance', NULL, NULL),
(2115, E'Teatro do Corpo de Konohamaru', E'Konohamaru, Moegi e Udon fazem uma sequência de ataques com socos, tapas e chutes no oponente. Em seguida, os três acertam um último soco simultaneamente no oponente, lançando-o longe com o impacto.', E'木ノ葉丸軍団劇場', E'Konohamaru Gundam Gekijō', E'Teatro do Corpo de Konohamaru', E'Teatro do Esquadrão Ninja de Konohamaru', NULL, NULL, E'Curto alcance', NULL, NULL),
(2116, E'Teisho', E'Trata-se de um golpe semelhante à um soco, porém ele é executado de mão aberta e tem como área de impacto a base da mão. Ele é normalmente utilizado para atingir o rosto, queixo, costelas e plexo solar.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2117, E'Telepatia da Besta com Cauda', E'Uma habilidade inata possuída por todas as bestas com cauda, o que permite que elas se comuniquem umas com as outras em uma planície mais profunda de sua mente subconsciente conhecida como mundo mental. Este tipo de comunicação é diferente do utilizado pelos jinchūriki e suas bestas com cauda, já que não há selos ou outras restrições e é acessível para todas as bestas com cauda e seus respectivos jinchūriki. No entanto, para entrar neste mundo é necessário que um jinchūriki e sua besta sejam totalmente ligados. Além disso, é impossível para as influências de fora alcançar este plano, mesmo que as bestas com cauda estejam sendo controladas.', NULL, NULL, NULL, NULL, NULL, E'Psique Profunda da Besta com Cauda', E'Todos os alcances', NULL, NULL),
(2118, E'Tempestade da Folha', E'Lee se concentra por um momento, e desfere um chute giratório. Lee abrange uma distância curta ao girar no chão, e depois salta enquanto ainda girando, batendo no oponente.', E'木ノ葉暴風', E'Konoha Bōfū', E'Tempestade da Folha', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2119, E'Temporal do Esmagamento Celestial', E'Sarada dá uma voadora no oponente e, rapidamente, move-se para atrás dele agora com o Sharingan ativado, a fim de chutá-lo para o alto. Em seguida, Sarada salta e ataca o oponente com uma combinação única de chute, um ataque com o antebraço e um soco para o atirar brutalmente contra o chão. Em queda, o oponente é surpreendido com uma bola de fogo gigante seguida, entre chamas, por um poderoso soco de chakra e é esmagado contra o solo.', E'夕立天崩しゆうだしあまくずし', E'Yūdachi Amakuzushi', E'Temporal do Esmagamento Celestial', E'Esmagamento da Chuva Súbita', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2120, E'Tendō', E'O Caminho Deva (天道, Tendō) concede ao usuário a capacidade de manipular forças atrativas e repulsivas, permitindo mover objetos e pessoas, bem como voar. Estas técnicas de manipulação da gravidade, no entanto, não podem ser realizadas numa sucessão rápida, a quantidade de tempo necessária para recarregar após a utilização varia de acordo com a escala da técnica utilizada, sendo cinco segundos a mínima de base. O Caminho Deva também concede outra habilidade: Chibaku Tensei, que permite ao usuário criar um satélite natural, através de um núcleo gravitacional, como uma esfera negra ou até mesmo uma pessoa.', E'天道', E'Tendō', E'Caminho Deva', NULL, E'Reino do Céu', NULL, NULL, NULL, NULL),
(2121, E'Tengai Ryūsei', E'Utilizando o Poder do Seis Caminhos combinado com o poder do seu Rinnegan, Madara libera vários núcleos gravitacionais que atraem o terreno ao redor para si, criando vários satélites, que são posteriormente jogados contra seus alvos como uma chuva de meteoros.', E'天涯流星', E'Tengai Ryūsei', E'Meteoro do Horizonte', E'Meteoro na Linha do Céu', NULL, NULL, E'Todos os alcances', NULL, E'Juntar as Maos'),
(2122, E'Tengai Shinsei', E'Madara faz três selos de mão simultaneamente com o seu Susanoo, a fim de extrair meteoritos maciços a partir da atmosfera superior para baixo para um local específico do chão. Eles descem em sucessão ao longo da mesma trajetória, se um desses meteoritos é interrompido, outros colidem com o primeiro como um martelo enorme, quebrando boa parte e garantindo que ele caía em seu destino.', E'天碍震星てんがいしんせい', E'Tengai Shinsei', E'Obstáculo Celestial da Estrela Trêmula', E'Paraíso Escondido', NULL, NULL, E'Longo alcance', NULL, E'Carneiro + Cobra + Passaro Simultaneamente[2]'),
(2123, E'Tenpenchii', E'Ao utilizar a sua gigantesca quantidade de chakra para manipular toda a energia natural presente ao seu redor em larga escala, o Dez-Caudas é capaz de criar simultaneamente uma variedade de desastres naturais que assolam a terra, com o próprio agindo como seu epicentro. Os fenômenos envolvidos nesta técnica incluem: terremotos, inundações, tempestades e tornados. Estes eventos simultâneos resultam em uma explosão semelhante a uma explosão nuclear, mesmo formando uma vasta nuvem de cogumelo.', E'天変地異', E'Tenpenchii', E'Desastre Natural Sucedido do Céu e Terra', NULL, E'Cataclismo', NULL, E'Todos os alcances', E'Rank –', NULL),
(2124, E'Tensō Tsuisei', E'Obito joga o oponente no ar durante a utilização de sua Liberação de Madeira: Técnica Cortante. O oponente pousa e Obito envolve-o em uma bola de madeira. Madara, então, chama seu Tengai Shinsei, que cai para baixo sobre o oponente.', E'天挿槌星', E'Tensō Tsuisei', E'Inserção Celestial do Martelo Estelar', E'Estrelas do Céu', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2125, E'Tentáculos de Areia', E'Gaara alonga sua areia a partir de sua base flutuante, formando grossos tentáculos que podem ser usados para capturar ou esmagar o inimigo. A velocidade desta técnica é suficiente para pressionar um Ōtsutsuki a se esquivar sem chances de contra-ataque.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(2126, E'Terceiro Olho', NULL, E'第三の眼だいさんのめ', E'Daisan no Me', E'Terceiro Olho', NULL, NULL, E'Olho de Areia', NULL, E'Rank –', E'Cobra → Macaco → Rato → Macaco → Passaro → Macaco → Dragao → Macaco → Rato → Macaco'),
(2127, E'Terra Carmesim', E'Usando sua Arma Mecânica de Perfuração em seu braço direito, Toki dispara na direção que ele deseja com tanta força que ele é capaz de literalmente mudar a geografia da área afetada. Ele usou esta técnica para criar uma bifurcação do rio que era usada como um ponto de estrangulamento, onde ele podia atacar seus alvos.', E'土紅', E'Tsuchi Kurenai', E'Terra Carmesim', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2128, E'Terra Queimada da Busca da Verdade', E'O usuário reúne as Esferas da Busca da Verdade de suas costas e libera seu poder em um feixe de energia que causa graves danos ao oponente.', E'求道焦土ぐどうしょうど', E'Gudō Shōdo', E'Terra Queimada da Busca da Verdade', E'Calor do Caminho da Verdade', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2129, E'Tessenjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2130, E'Tigela do Gato de Fogo', E'Yugito libera uma de suas caudas e começa a girar, criando um envoltório de fogo azul em torno de si e se transformando em Matatabi. Com a besta no controle, ela levanta sua pata e bate com força no chão, arranhando o oponente e enviando-o junto com várias bolas de fogo para trás, as quais se são guadas contra o inimigo, resultando numa explosão azul cintilante generalizada.', E'猫火鉢', E'Nekohibachi', E'Tigela do Gato de Fogo', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(2131, E'Tigre Diurno', E'O Tigre Diurno, contrariando sua aparência, é puramente taijutsu na natureza e é nada mais do que um punho de pressão de ar extremamente rápido conforme Might Guy observou.', E'昼虎ひるどら', E'Hirudora', E'Tigre Diurno', NULL, E'Tigre da Tarde', NULL, E'Curto a Médio alcance', E'Rank A', E'Sho → Selo especifico da técnica'),
(2132, E'Tinta Preta', E'A Tinta Preta é uma habilidade inata utilizada por Gyūki e seus jinchūriki, embora outras pessoas ainda possam replicá-la.', E'黒墨', E'Kurosumi', E'Tinta Preta', NULL, E'Jato de Tinta', NULL, E'Curto a Médio alcance', NULL, E'Selo do Confronto'),
(2133, E'Tirania Fumegante Perigosa', NULL, E'蒸危暴威じょうきぼうい', E'Jōki Bōi', E'Tirania Fumegante Perigosa', E'Menino Tirânico Perigo Fumegante', E'Fúria a Vapor', NULL, E'Todos os alcances', E'Rank –', NULL),
(2134, E'Tiro Aquático', E'O usuário exala uma bola de água de sua boca e a lança em alta velocidade em direção ao seu alvo. O impacto da esfera é forte o suficiente para jogar seu oponente a vários metros.', E'アクアシュート', E'Akua Shūto', E'Tiro Aquático', NULL, NULL, NULL, E'Médio alcance', NULL, E'Passaro'),
(2135, E'Tiro Preparado de Agulhas', E'Usando um dispositivo montado no pulso, o usuário pode lançar agulhas em seu oponente. O dispositivo é geralmente usado para surpreender o inimigo, como ele pode ser escondido sob mangas compridas. As agulhas também podem ser mergulhadas em um veneno mortal, aumentando sua letalidade.', E'仕込針弾', E'Shikomishindan', E'Tiro Preparado de Agulhas', E'Barragem de Agulhas de Veneno', E'Agulhas Envenenadas', E'Arte Ninja: Agulhas de Veneno', E'Curto a Médio alcance', E'Rank C', NULL),
(2136, E'Tiro da Chama Explosiva', E'Com a criação de uma faísca de suas mãos, esta técnica permite que o usuário jogue várias bolas de fogo, que são capazes de causar ou explosões de fogo com o impacto ou definir o alvo em chamas.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto à Médio alcance', NULL, NULL),
(2137, E'Tiro de Agulha na Névoa de Veneno', E'Shizune utiliza primeiro a Névoa Venenosa em seu oponente e, em seguida, ataca com seus bisturi de chakra algumas vezes. Finalmente, ela salta no ar e joga agulhas em seu oponente.', NULL, NULL, NULL, E'Bala de Agulha da Névoa', NULL, NULL, NULL, NULL, NULL),
(2138, E'Tiro de Fogo', E'Com o auxílio da Lâmina do Dragão, Naruto dispara uma grande bola de fogo contra seu oponente. Quando lançada a partir do ar, a técnica se ramifica em múltiplas esferas menores que caem sobre o alvo.', NULL, NULL, NULL, E'Tiro de Fogo', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2139, E'Tiro de Muco Explosivo', E'Katsuyu libera uma pasta amarela e ácida de sua boca no chão, que se eleva criando uma formação semelhante à uma parede, capaz de danificar um alvo ou fornecer uma defesa eficaz.', E'爆液放射', E'Bakueki Hōsha', E'Tiro de Muco Explosivo', E'Explosão de Muco', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2140, E'Tiro do Mecanismo Destrutivo', E'O usuário usa sua marionete para atirar várias kunai com veneno a partir de sua boca em seu oponente.', E'カラクリ散弾破', E'Karakuri Sandan Yabu', E'Tiro do Mecanismo Destrutivo', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2141, E'Todas as Armas Acima do Céu', E'Esta técnica aumenta todos os aumentos de status (Velocidade, Ataque, Defesa) para todos os personagens em combate com Tenten.', NULL, NULL, NULL, E'Todas as Armas Acima do Céu', NULL, NULL, E'Curto alcance', NULL, NULL),
(2142, E'Tonfa Voadora', E'Empunhando suas tonfas, Kokuyō joga uma delas em alta velocidade, criando uma áurea de vácuo em torno da mesma e uma onda de impacto devido à resistência do vento. A força do impacto é tão grande que é capaz de arrancar o topo de um morro e bloquear a passagem dos inimigos. Depois de cumprir o trajeto designado, a tonfa volta para as mãos de Kokuyō, como um bumerangue.', NULL, NULL, NULL, NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(2143, E'Tonjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2144, E'Tornado Alto', E'Após acertar em seu oponente vários socos e chutes com um clone das sombras, Boruto impulsiona-se liberando um tornado de vento abaixo de seus pés e chuta seu oponente, jogando-o para o alto.', E'トルネイドハイ', E'Toruneido Hai', E'Tornado Alto', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2145, E'Tornado Negro', E'Os membros da Família Kurosuki sobem um em cima do outro, formando um pilar de cinco pessoas, e em seguida, mostram lâminas retráteis de suas luvas e levantam seus braços. Eles, então, começam a girar, criando um ataque semelhante à um tornado negro. A velocidade faz com que o ataque seja tão poderoso que as lâminas podem facilmente cortar pedras.', E'黒竜巻', E'Kuro Tatsumaki', E'Tornado Negro', NULL, NULL, NULL, NULL, NULL, NULL),
(2146, E'Tornado Relâmpago', E'Tornado Relâmpago (旋風雷光撃, Torunēdo Raitoningu) é uma técnica utilizada por Ranke ao extrair energia da Pedra de Gelel.', E'旋風雷光撃せんぷうらいこうげき', E'Torunēdo Raitoningu', E'Ataque Relâmpago do Redemoinho de Vento', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2147, E'Tornado de Fogo', E'O usuário lança um turbilhão de fogo que engole seu oponente, incinerando-o.', NULL, NULL, E'Tornado de Fogo', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(2148, E'Tornado de Vento Direto', E'Ao balançar suas asas, o Kuchiyose Final é capaz de criar um enorme tornado de vento capaz de empurrar um humano ou mesmo desferir pequenos cortes.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(2149, E'Tornado de Água', E'Essa técnica utiliza água para criar um turbilhão de água em volta do usuário. O turbilhão de água atua como uma barreira tanto defensiva como ofensiva. O poder do turbilhão é poderoso o suficiente para deixar o oponente inconsciente.', E'水竜巻', E'Mizu Tatsumaki', E'Tornado de Água', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Carneiro → Cavalo → Passaro'),
(2150, E'Tornado do Dragão Relâmpago', E'Empunhando suas duas espadas juntas, Raiga cria-os no ar e atira um raio para baixo em cima de seu corpo. A corrente é contida dentro e ao redor de Raiga. Ele, então, começa a girar no lugar formando um vórtice rodopiante de vento e eletricidade. O vórtice assume a forma de uma cabeça de dragão e cobre a área em frente a ele. Então ataca contra o seu adversário e dolorosamente prende-o no vórtice. Mesmo que o ataque não se conecte diretamente com seu inimigo, o relâmpago pode saltar do vórtice para atingir um alvo próximo.', E'雷竜の竜巻', E'Rairyū no Tatsumaki', E'Tornado do Dragão Relâmpago', NULL, NULL, E'Arte Ninja: Tornado Relâmpago do Dragão', E'Curto a Médio alcance', NULL, NULL),
(2151, E'Torpedo de Pregos', E'Gosunkugi lança inúmeros pregos ao ar, que caem, e, então, cria uma área cheia de pregos no solo. Uma vez que inimigo não pode enxergar os pregos e anda pela área, Gosunkugi performa um selo de mão e puxa os pregos de volta, a fim de perfurar o inimigo.', E'魚雷針ぎょらいしん', E'Gyoraishin', E'Torpedo de Pregos', NULL, NULL, E'Arte Ninja: Pregos Torpedo', E'Curto a Médio alcance', NULL, E'Dragao'),
(2152, E'Torção da Busca da Verdade', E'Madara ataca o oponente com as Esferas da Busca da Verdade antes de transformá-las em uma broca extremamente mortal e perfurá-lo brutalmente.', E'捩求道ねじぐどう', E'Nejigudō', E'Torção da Busca da Verdade', E'Deformação da Busca da Verdade', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2153, E'Torção da Flor', E'Kimimaro, enquanto em seu selo amaldiçoado nível dois, usa sua cauda para executar um forte golpe no inimigo, podendo derrubá-lo e cortá-lo com os ossos de sua cauda.', E'捩じり花', E'Nejirihana', E'Torção da Flor', E'Suspensão da Flor, Flor Furiosa', NULL, NULL, E'Curto alcance', NULL, NULL),
(2154, E'Trabalho em Equipe Excepcional', E'Naruto, Sakura, e Sasuke chutam seu inimigo em um ataque combinado. Naruto e Sasuke então correm, acertando uma série de combos no alvo. Aproveitando o momento, Sakura revela um fio de aço que ela tem em seus equipamentos e faz um circulo no chão para usá-lo à fim de apertar o adversário, porém, acidentalmente ela também captura Naruto, enquanto Sasuke escapa usando a Técnica de Substituição do Corpo.', E'チームワークは抜群', E'Chīmuwāku wa Batsugun', E'Trabalho em Equipe Excepcional', E'Trabalho de Equipe', NULL, NULL, E'Curto alcance', NULL, NULL),
(2155, E'Traje Majestoso: Susanoo', E'O Traje Majestoso: Susanoo é uma armadura especial extremamente resistente, que pode ser moldada de acordo com a vontade e a necessidade do utilizador. O usuário utiliza a armadura que normalmente seria usada para equipar seu Susanoo, para outras finalidades. Essa armadura é resistente o suficiente de aguentar uma Bola da Besta com Cauda, e consegue resistir parcialmente a uma Bola da Besta com Cauda contendo Senjutsu. Essa armadura é muito eficaz contra tentativas de absorção de chakra e contra técnicas de supressão.', E'威装・須佐能乎', E'Isō: Susanoo', E'Traje Majestoso: Ele com a Capacidade de Ajudar em Todos os Meios', NULL, NULL, NULL, E'Curto alcance', E'Rank –', NULL),
(2156, E'Transferência de Chakra', E'Esta é uma técnica que permite ao usuário que transfira parte de seu chakra para outra pessoa.', E'チャクラ送', E'Chakura Tensō', E'Transferência de Chakra', NULL, NULL, NULL, NULL, NULL, E'Carneiro[2]'),
(2157, E'Transformação Combinada', E'Uma forma avançada da Técnica de Transformação, o Konbi Henge (コンビ変化, Técnica de Transformação Combinada) transforma dois usuários em um único ser. Em alguns casos as etapas são divididas, enquanto um usuário executa os selos de mão necessários, o outro pode fornecer o chakra. Naruto utilizou este método com Gamabunta em sua luta contra Gaara (quando ele usou a forma completa do Shukaku) para assumir a aparência de uma raposa gigante (Kurama no anime). O Quarteto do Som usou essa técnica durante o Exame Chūnin para se disfarçarem como guarda-costas do Yondaime Kazekage (dois em cada). Kiba e Akamaru criaram a sua própria variação desta técnica com o Jinjū Konbi Henge: Sōtōrō.', E'コンビ変化', E'Konbi Henge', E'Técnica de Transformação Combinada', NULL, NULL, NULL, NULL, E'Rank B', E'Carneiro invertido'),
(2158, E'Transformação Combinada da Besta Humana: Lobo de Duas Cabeças', E'Uma transformação combinada, onde homens e animais se tornam uma única entidade. É uma especialidade do clã Inuzuka, um clã que tem andado lado a lado com ninken por gerações. Eles se fundem em um lobo bicéfalo, assim aumentando drasticamente o seu poder. Em seus ataques diretos, eles demonstram grande eficiência. Além disso, utilizando técnicas nessa forma dá origem á um extraordinário poder de destruição.', E'人獣混合変化・双頭狼', E'Jinjū Konbi Henge: Sōtōrō', E'Transformação Combinada da Besta: Lobo de Duas Cabeças', NULL, E'Transformação e Combinação de Animal e Ser Humano: Lobo de Duas Cabeças', E'Combo de Transformação Homem-Animal: Lobo de Duas Cabeças', NULL, E'Rank B', NULL),
(2159, E'Transformação Misturada da Besta Humana — Lobo de Três Cabeças', E'Esta técnica é uma especialidade do clã Inuzuka, seguindo os princípios do clã de utilização de parceiros ninken. Depois de criar um clone da sombra, o usuário combina com este clone e seu companheiro ninken em um lobo gigante, de três cabeças. Isso aumenta drasticamente o seu tamanho e poder, ao mesmo tempo, ganhando garras afiadas e dentes caninos. Em seus ataques diretos, eles demonstram considerável eficiência. Além disso, usando técnicas para além disso dá origem a uma força destrutiva extraordinária.', E'人獣混合変化・参頭狼', E'Jinjū Kongō Henge — Santōrō', E'Transformação da Mistura da Besta Humana — Lobo de Três Cabeças', NULL, E'Estilo Inuzuka: Transformação e Combinação de Animal e Ser Humano: Lobo de Três Cabeças', NULL, NULL, E'Rank –', E'Tigre'),
(2160, E'Transformação: Bastão Adamantino', E'A técnica de transformação do Rei Macaco, Enma, é caracterizada pela dureza do diamante e de sua capacidade à vontade para estender e/ou expandir o Bastão Adamantino, que é a arma de escolha de Hiruzen Sarutobi. Este bastão, quando em uso, foi capaz de destruir facilmente as estruturas de madeira criadas por Hashirama, um usuário de Liberação de Madeira, e também para esmagar Kurama, mandando-o para as fronteiras de Konoha, e destruir os galhos da Árvore Divina.', E'変化・金剛如意へんげ・こんごうにょい', E'Henge: Kongōnyoi', E'Transformação: Bastão Adamantino', NULL, E'Transformação: Bastão Indestrutível', E'Bastão Adamantino', NULL, NULL, NULL),
(2161, E'Transformação do Ataque Suicida de Vento Demoníaco', E'Naruto cria um clone para jogar o oponente no ar onde o Naruto real surge com uma fūma shuriken que corta seu oponente.', E'風魔特攻変化', E'Fūma Tokkō Henge', E'Transformação do Ataque Suicida de Vento Demoníacoto', E'Transformação da Carga do Demônio de Vento', NULL, NULL, E'Curto alcance', NULL, NULL),
(2162, E'Transformação do Clone das Sombras', E'A Transformação do Clone das Sombras é uma técnica que emprega o uso da Técnica dos Clones das Sombras e da Técnica de Transformação. Essa técnica é extremamente útil, uma vez que pode ser utilizada para confundir e enganar seus inimigos, ou até mesmo criar armas para serem usadas em combate.', E'影分身の変化', E'Kage Bunshin no Henka', E'Transformação do Clone das Sombras', NULL, NULL, NULL, NULL, NULL, NULL),
(2163, E'Transmissão Relâmpago', E'Usando o auxílio de um clone, cada um dos dois Kakashi realizam a técnica Corte Relâmpago em mãos opostas. Conectando as suas técnicas em conjunto, eles formam uma corrente de relâmpagos entre si que é capaz de cortar através de objetos como braços de chakra de bestas com cauda com grande facilidade. Kakashi também foi capaz de envolver o relâmpago em torno de um alvo, assim restringindo efetivamente os seus movimentos, como mostrado contra o Estátua Demoníaca do Caminho Exterior.', E'雷伝らいでん', E'Raiden', E'Transmissão Relâmpago', NULL, E'Laço Relâmpago', NULL, E'Curto a Médio alcance', NULL, NULL),
(2164, E'Transmissão da Mente Psicótica', E'O usuário entra no subconsciente do alvo, colocando a mão sobre a cabeça do alvo e extraindo todas as informações necessárias, mesmo se o alvo se esquecesse ou tivesse um bloqueio. Um dispositivo especial pode ser utilizado para diminuir o tempo necessário para ler a mente do alvo e permite que outros ninjas ajudem. Memórias que têm bloqueios mentais sobre elas vão exigir longos períodos de tempo para sondar através e obter no entanto. Dependendo da habilidade do usuário e seu nível de experiência, eles serão capazes de passar dias de memórias do alvo em questão de momentos e peneirar até encontrar o que querem, enquanto outros podem ter que passar por cada memória sua totalidade.', E'催戸伝心', E'Saiko Denshin', E'Transmissão de Exploração-Porta Mente', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2165, E'Transmissão de Madeira', E'Depois de transformar um clone de madeira em uma semente, a semente pode ser usada para rastrear um alvo ao colocada nele, ou na ocasião de o alvo a ingerir. Somente o usuário dessa técnica pode detectar transmissões da semente.', E'送信木そうしき', E'Sōshinki', E'Transmissão de Madeira', NULL, NULL, NULL, NULL, NULL, NULL),
(2166, E'Transmissão de Sensoriamento', E'Ao fazer contato físico com outra pessoa, o usuário é capaz de transmitir o que estão sentindo atualmente diretamente na mente do aliado.', E'感知伝々かんちでんでん', E'Kanchi Denden', E'Transmissão de Sensoriamento', E'Comunicação Sensorial', E'Transferência de Percepção', NULL, E'Curto alcance', E'Rank –', NULL),
(2167, E'Transportando os Corpos', E'Kazuma usa essa técnica para controlar os caixões de Kitane, Nauma, Seito e Tōu.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, E'Controlar: Cachorro → Cobra → Porco,Enterrar: Cobra'),
(2168, E'Transversal Cintilante', E'Shisui agarra seu oponente e o chuta para o alto, movendo-se em alta velocidade, onde ele desfere uma série de cortes com sua tantō.', E'瞬断', E'Shundan', E'Transversal Cintilante', E'Divisão Instantânea', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2169, E'Tratamento Petriquímico', E'O Tratamento Petroquímico é um processo médico onde usuário reúne chakra medicinal em uma esfera branca com brilho azul-esverdeado e o move sobre as áreas feridas do corpo. Sakura usou esta técnica tentando curar Kakeru Tsuki, que foi petrificado pela manopla de Ishidate, mas como já fazia muito tempo desde que ele foi petrificado, ela não foi capaz de curar as feridas e salvar o rei.', E'石化治療せっかちりょう', E'Sekka Chiryō', E'Tratamento Petriquímico', NULL, NULL, NULL, E'Curto alcance', NULL, E'Rato → Carneiro → Cao'),
(2170, E'Tremor Violento do Rato de Relâmpago', E'Após performar alguns selos de mão, Nigai libera uma descarga de eletricidade que dispara vários discos destrutivos de eletricidade. O potencial desta técnica não foi mostrado, uma vez que ela foi anulada antes que pudesse mostrar seu efeito.', E'雷鼠激震らいそげきしん', E'Raiso Gekishin', E'Tremor Violento do Rato de Relâmpago', NULL, NULL, E'Onda de Choque do Rato Relâmpago', NULL, NULL, E'Macaco → Boi → Javali → Tigre'),
(2171, E'Trifoliata', E'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente com sua espada de osso, bate no oponente com sua cauda e finaliza batendo no oponente com seus ossos nas costas, jogando-o para trás.', E'カラタチ', E'Karatachi', E'Trifoliata', E'Laranja Trifoliata', NULL, NULL, E'Curto alcance', NULL, NULL),
(2172, E'Trifoliata (Ultimate Ninja 3)', E'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente com sua espada de osso, bate no oponente com sua cauda e finaliza batendo no oponente com seus ossos nas costas, jogando-o para trás.', E'カラタチ', E'Karatachi', E'Trifoliata', E'Laranja Trifoliata', NULL, NULL, E'Curto alcance', NULL, NULL),
(2173, E'Trifoliata (Ultimate Ninja Storm Generations)', E'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente no ar com suas espadas de osso e, em seguida, libera ossos por toda parte do corpo, atacando-o em forma circular irregular e finalizando com um chute para baixo na cabeça do oponente.', E'カラタチ', E'Karatachi', E'Trifoliata', E'Pônciros', NULL, NULL, E'Curto alcance', NULL, NULL),
(2174, E'Trindade', E'Obito usa a Liberação de Fogo: Técnica da Fênix Sábia de Fogo e Minato joga algumas Kunai do Deus Voador do Trovão no oponente. Kakashi aproveita e forma um Chidori, dando tempo para Obito ativar seu Sharingan e Minato teleportar os dois usando a Técnica do Deus Voador do Trovão. Kakashi acerta o oponente com seu Chidori e Obito chuta o oponente, em seguida, Kakashi dá uma breve corrida e volta com um novo Chidori que acerta novamente o oponente. Minato então se teleporta até o oponente e os três atacam em conjunto, Obito esfaqueando com uma kunai, Minato cortando o oponente com sua kunai e Kakashi com o Sabre de Chakra da Luz Branca.', E'三位一体さんみいったい', E'Sanmiittai', E'Trindade', E'Força-Tripla', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2175, E'Trio Impecável', E'Kakashi, Obito e Rin avançam em direção ao oponente. Kakashi dá alguns cortes no oponente com sua Sabre de Chakra da Luz Branca, Obito acerta um golpe de kunai e Rin chuta o adversário. Enquanto o oponente está atordoado, Rin torce por Kakashi e Obito. Em seguida, Obito atira várias bolas de fogo contra o oponente e Kakashi finaliza com mais um corte de seu Sabre de Chakra da Luz Branca.', E'完全無欠の三人かんぜんむけつのさんにん', E'Kanzen Muketsu no Sannin', E'Trio Impecável', E'Trio Perfeito', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2176, E'Triplo Mecanismo da Borda', E'Kankurō manipula o Corvo para lançar uma barragem de kunai envenenadas através do seu peito até o oponente simultaneamente com o Formiga Negra lançando bombas de fumaça venenosas.', NULL, NULL, E'Triplo Mecanismo da Borda', E'Técnica Secreta de Marionetes: Barragem da Kunai Envenenada', NULL, NULL, E'Curto alcance', NULL, NULL),
(2177, E'Trovoada', E'Sasuke encobre-se de relâmpago, empunha sua espada e rapidamente ataca o oponente repetidas vezes cortando-o e eletrocutando-o, deixando rastros de relâmpago por onde passa, finalizando com um Chidori e incinerando o oponente no final.', E'迅雷', E'Jinrai', E'Trovoada', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2178, E'Trovão Enfurecido', E'Ao focar sua energia Gelel em sua espada e depois de dar um golpe com ela, Temujin pode criar uma barreira poderosa Gelel diante dele. Está técnica foi utilizada na tentativa de anular o Rasengan de Naruto. Os dois ataques resultam em uma enorme explosão que destruiu o pico da montanha onde Naruto e Temujin estavam lutando.', E'魔導昇雷撃', E'Reijingu Sandā', NULL, NULL, NULL, NULL, E'Curto alcance.', NULL, NULL),
(2179, E'Trovão dos Mortos', E'Orochimaru invoca uma cobra e Sasuke usa a Corrente de Chidori e em espiral em torno deles pode atingir o oponente se nas proximidades.', E'亡者の雷', E'Mōja no Kaminari', E'Trovão dos Mortos', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2180, E'Trunfo em Dupla', E'Naruto cria vários clones das sombras para empurrar, segurar o oponente e, em seguida, chutá-lo para o alto. Em queda, o oponente é atingido certeiramente por uma combinação entre o Rasengan de Naruto com o Chidori de Sasuke.', E'二人の切り札ふたりのきりふだ', E'Futari no Kirifuda', E'Trunfo em Dupla', E'Último Recurso Duplo', NULL, NULL, E'Curto alcance', NULL, NULL),
(2181, E'Truque do Gato', E'Ao acessar seu poder de gato, Wasabi salta e desfere um golpe vertical no chão. Em seguida, ela desfere outro golpe com a outra mão, a fim de criar uma cortina de poeira e usar o impulso para escapar do local. Estes movimentos foram o suficiente para cobrir sua retirada com Tsubaki de modo a seus adversários não as notarem.', E'猫騙しねこだまし', E'Nekodamashi', E'Truque do Gato', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2182, E'Três Colorações', E'Kurenai libera um pilar de pétalas de cerejeira no oponente enviando-o para longe do local.', E'三彩', E'Sansai', E'Três Colorações', E'Chute de Coloração da Placa', NULL, NULL, E'Curto alcance', NULL, NULL),
(2183, E'Três Deuses Leais', E'Momoshiki agarra seu oponente pelo pescoço e o lança para o alto e, em seguida, ele se transforma ao consumir uma Panaceia Cinábrio. Já transformado, ele bate uma mão no chão e conjura cinco grandes cães que mordem o oponente, deixando-o suspenso no ar, criando abertura para que o Faisão Caçador acerte o alvo em cheio, criando uma explosão ao atingi-lo. Para finalizar, ele cria um golem de rocha fundida que, ao comando raivoso de seu conjurador, desfere um poderoso soco.', E'三忠神さんちし', E'Sanchūjin', E'Três Deuses Leais', E'Ameaça Tripla', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2184, E'Tsukuyomi', E'Tsukuyomi é notado por ser um dos genjutsu mais poderosos que existem. Diz-se representar o \'Mundo Espiritual e a Escuridão\' (精神界と闇, Seishinkai to Yami), o antípode de Amaterasu — um ninjutsu de poder similar.', E'月読', E'Tsukuyomi', E'Leitor da Lua', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2185, E'Tsukuyomi Infinito', NULL, E'無限月読まげんつくよみ', E'Mugen Tsukuyomi', E'Leitor da Lua Infinito', NULL, NULL, NULL, E'Todos os alcances', E'Rank –', E'Liberar: Rato ou Juntar as Maos[2][Nota 1]'),
(2186, E'Tubarão Ameaçador', E'Kisame usa a Liberação de Água: Onda de Choque Explosiva de Água e corta o oponente debaixo d\'água com a Samehada.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2187, E'Tubarão Perfurador', E'Kisame, fundido com a Samehada, pula no ar e começa a rotacionar em torno de si, usando suas barbatanas adquiridas para raspar e cortar o oponente, absorvendo seu chakra, graças à habilidade da espada.', E'穿鮫', E'Senkō', E'Tubarão Perfurador', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2188, E'Turbilhão Ardente', E'O Dez-Caudas cria uma bola de fogo gigante através de um dos apêndices com olhos ao longo de seu corpo e lança-a para cima. Quando a bola de fogo cai no chão, ela cria um enorme turbilhão em chamas, que é grande o suficiente para ultrapassar a altura do Dez-Caudas em seu estágio inicial. Esse turbilhão foi poderoso o suficiente para engolir e derrubar Kurama por algum tempo, causando grande dano.', NULL, NULL, NULL, NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(2189, E'Turbilhão Varredor', E'Quando transformado em Kokuō, Han ataca seu oponente pela frente, e por todos os outros lados cardeais diversas vezes, atingindo o oponente com seus chifres e girando suas caudas criando um ataque poderoso em seu oponente.', E'旋風払い', E'Senpū Harai', E'Turbilhão Varredor', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2190, E'Técnica Adamantina: Triturador de Crânio', E'Hiruzen salta em cima da elevação de terra que fez, para alcançar o adversário. Em seguida, ele invoca o Rei Macaco: Enma e ataca com ele duas vezes, finalizando com um ataque do seu bastão na cabeça do inimigo.', E'金剛術・頭蓋砕きこんごうじゅつ・ずがいくだき', E'Kongōjutsu: Zugai Kudaki', E'Técnica de Vajra: Triturador de Crânio', E'Jutsu Adamantino: Esmagador de Crânio', NULL, NULL, E'Curto alcance', NULL, NULL),
(2191, E'Técnica Branca Secreta Reversa: Coleção de Uematsu de Dez Marionetes', E'Depois de tomar o controle do corpo de dez indivíduos, o usuário os controla para atacar de vários lados ao mesmo tempo, como uma formação, deixando pouco ou nenhum tempo para reagir.', E'裏白秘技・十機植松の集うらしろひぎ・じゅっきうえまつのしゅう', E'Ura Shirohigi: Jukki Uematsu no Shū', E'Técnica Branca Secreta Reversa: Coleção de Uematsu de Dez Marionetes', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(2192, E'Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu', E'Depois de desenrolar o pergaminho, a coleção de Chikamatsu aparece. Usando a Técnica de Marionetes simples, pode-se controlar à vontade, controlando dez marionetes ao mesmo tempo em cada dedo, com um poder correspondente a mil bonecos juntos. Exige conhecimentos de alta-classe e habilidade, para conseguir controlá-los em seu auge de poder.', E'白秘技・十機近松の集', E'Shirohigi: Jikki Chikamatsu no Shū', E'Técnica Branca Secreta: A Coleção de Chikamatsu das Dez Marionetes', E'Dez Marionetes de Chikamatsu', E'Técnica Branca Secreta: Dez Obras-Primas de Chikamatsu', E'Ataque Branco Secreto: As Dez Marionetes de Chikamatsu', E'Todos os alcances', E'Rank S', NULL),
(2193, E'Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu: Ataque da Terra', E'Usando as marionetes de Monzaemon Chikamatsu, Chiyo usa o braço da marionete 4 e a lâmina de chakra da marionete 3 para lançar a marionete 5 longe, concluindo nela empalando o adversário com os cabos presentes em sua cabeça.', E'白秘技・十機近松の集・地の攻', E'Shirohigi: Jikki Chikamatsu no Shū: Chi no Kō', E'Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu: Ataque da Terra', E'Dez Marionetes: Ataque da Terra', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2194, E'Técnica Especial de Projeção da Shuriken de Sakura', E'Sakura lança nove shuriken em direção à seu oponente, que estão ligadas à fios de chakra o que ajuda na perseguição de seu inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto à Médio alcance', NULL, NULL),
(2195, E'Técnica Hemostática', E'A Técnica Hemostática é um ninjutsu médico utilizado durante procedimentos cirúrgicos para estancar hemorragias. Ao término da cirurgia, a abertura feita durante o procedimento é fechada.', E'止血術', E'Shiketsu Jutsu', E'Técnica Hemostática', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2196, E'Técnica Ninja de Ocultação', E'Esta é uma habilidade geral que permite que um ninja esconda-se dentro de uma caixa onde torna-o invisível aos olhos de quem vê. Esta habilidade simples, embora pareça inútil, mostrou-se ter certa relevância, visto que aqueles que observam a caixa, a examina como um simples objeto inanimado, tornando irrelevante e dando possibilidade de fuga ao ninja que encontra-se dentro da mesma.', E'尾行忍術びこうにんじゅつ', E'Bikō Ninjutsu', E'Técnica Ninja de Ocultação', NULL, NULL, E'Arte Ninja: Sombreamento', NULL, NULL, NULL),
(2197, E'Técnica Pachinko', E'Naruto e Sasuke jogam um objeto duro no adversário com força suficiente para derrubá-lo. Eles realizaram esta técnica usando o Chakra Líquido de Jako que ligava os seus braços juntos, como um estilingue para lançar uma estátua de ouro em Jako.', E'パチンコの術', E'Pachinko no Jutsu', E'Técnica Pachinko', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(2198, E'Técnica Parasita', E'Enquanto em tamanho reduzido, Isshiki adentra o corpo de seu alvo e ali instala-se como parasita. Dentro do hospedeiro, ele começa a absorver nutrientes para sobreviver, e, sequencialmente, a controlar sua mente, falar com sua voz, usar seus braços e pernas, então assumindo ao controle de seu corpo para sempre.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2199, E'Técnica Preta Secreta: Espetáculo de Controle de Chamas do Escorpião', E'Utilizando o Lança-Chamas presente na marionete Sasori, o usuário faz com que um fluxo de fogo seja lançado contra o oponente, podendo controlar sua intensidade. No anime, esse fogo é quente o suficiente incinerar totalmente uma kunai e um corpo em questão de segundos embora grandes pedaços maciços de pedras sofram apenas derretidos superficialmente.', E'黒秘技・操炎蝎劇', E'Kurohigi: Sōen Katsugeki', E'Técnica Preta Secreta: Espetáculo de Controle de Chamas do Escorpião', E'Movimento Secreto Negro: Show do Escorpião de Controle de Chamas', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2200, E'Técnica Preta Secreta: Ferro Negro', E'Shinki captura seu alvo com areia de ferro e então invoca sua marionete que o morde, desencadeando uma fórmula de fūinjutsu para selar seus movimentos temporariamente enquanto o enterra em meio à areia de ferro.', E'黒秘技・黒鉄­', E'Kurohigi: Kurogane', E'Técnica Preta Secreta: Ferro Negro', NULL, NULL, E'Arte Ninja Secreta: Aço Negro', E'Curto a Médio alcance', NULL, NULL),
(2201, E'Técnica Preta Secreta: Magnífico Arremate em Chamas', E'Kankurō manipula sua marionete Salamandra para liberar uma enorme explosão incinerante ou um fluxo de fogo contra o oponente.', E'黒秘技・炎填華', E'Kurohigi: Entenka', E'Técnica Preta Secreta: Magnífico Arremate em Chamas', E'Movimento Secreto Negro: Flor de Fogo', NULL, NULL, E'Curto alcance', NULL, NULL),
(2202, E'Técnica Preta Secreta: Maquinário de Tiro Duplo', E'A versão aprimorada da Técnica Preta Secreta: Maquinário de Tiro Único que não exige Karasu. Kankurō manobra Kuroari atrás do alvo, que então se abre o peito e dispara para fora fios de chakra que envolvem seu alvo. Em seguida, puxa o alvo na sua cavidade abdominal aberta. Com a vítima presa, foices como lâminas aparecem e ceifam através de um lado da marionete para o outro, matando a vítima dentro.', E'黒秘技・機々二発', E'Kurohigi: Kiki Nihatsu', E'Técnica Preta Secreta: Maquinário de Tiro Duplo', NULL, NULL, E'Técnica Secreta Tenebrosa da Segunda Dama de Ferro', E'Curto alcance', NULL, NULL),
(2203, E'Técnica Preta Secreta: Maquinário de Tiro Triplo', E'Esta técnica é uma versão em maior escala da Técnica Preta Secreta: Maquinário de Tiro Único adaptada para dois ou três alvos. Kankurō controla de duas a três marionetes Formiga Negra para capturar os inimigos. Ele, então, desmonta o Corvo para empalar as vítimas, mas também pode contar com o auxílio de um aliado para atacar um dos cativos.', E'黒秘技・機々三発くろひぎ・ききさんぱつ', E'Kurohigi: Kiki Sanpatsu', E'Técnica Preta Secreta: Maquinário de Tiro Triplo', NULL, E'Técnica Negra das Fincadas de Versão Tripla', NULL, E'Curto alcance', E'Rank A', NULL),
(2204, E'Técnica Preta Secreta: Maquinário de Tiro Único', E'Primeiro, o adversário é preso no tronco de Kuroari. Depois disso, Karasu se separa em oito compartimentos. O tronco é deixado para trás, enquanto os braços, pernas e cabeça ficam posicionados ao redor de Kuroari, cada um com uma lâmina afiada. Kankurō manipula as peças separadas em fendas no tronco de Kuroari, espetando e envenenando a vítima no interior.', E'黒秘技・機々一発', E'Kurohigi: Kiki Ippatsu', E'Técnica Preta Secreta: Maquinário de Tiro Único', E'Movimento Secreto Negro: Dama de Ferro', E'Técnica Negra das Fincadas', E'Técnica Secreta Negra: Mulher de Ferro: Golpe Fatal', E'Curto alcance', E'Rank B', NULL),
(2205, E'Técnica Preta Secreta: Salamandra', E'Kankurō esconde Salamandra sob a terra, e, quando o adversário se posiciona na localização da marionete, o usuário a controla para capturar o adversário para dentro.', E'黒秘技・山椒魚', E'Kurohigi: Sanshōuo', E'Técnica Secreta Preta: Salamandra', NULL, E'Técnica Negra da Salamandra', E'Movimento Secreto Negro: Salamandra', E'Curto alcance', NULL, NULL),
(2206, E'Técnica Preta Secreta: Três Grandes Desastres', E'Kankurō usa essa técnica com Karasu, Kuroari e Sanshōuo. Primeiro as marionetes executam uma combinação de ataques contra o oponente, lançando-o no ar e ferindo-o brutalmente diversas vezes, com e sem lâminas. Quando o oponente está quase inconsciente, ele cai em frente da linha de ataque de Sanshōuo, que abre a boca e revela um canhão de chamas, explodindo o oponente no ar.', E'黒秘技・三大悲劇', E'Kurohigi: Sandai Higeki', E'Técnica Preta Secreta: Três Grandes Desastres', E'Movimento Secreto Preto: Três Grandes Tragédias', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2207, E'Técnica Proibida: Destruição do Selo do Mundo Impuro', E'Orochimaru invoca um caixão como é utilizado na Invocação: Reencarnação do Mundo Impuro. Várias mãos fantasmagóricas saem do caixão, e em seguida, se estende através do corpo do oponente, danificando-o, até que uma mão maior agarra-o, e o esmaga, logo em seguida, puxa-o para dentro do caixão. A tampa do caixão se fecha e começa a afundar na escuridão que o rodeia. Orochimaru por fim, ri sinistramente, observando como ele desaparece.', E'禁術・穢土封滅きんじゅつ・えどふうめつ', E'Kinjutsu: Edo Fūmetsu', E'Técnica Proibida: Destruição do Selo do Mundo Impuro', E'Jutsu Proibido: Destruição do Selo', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2208, E'Técnica Proibida: Golem de Lama', E'Dōshin pode fazer duplicatas de seus oponentes a partir da Liberação de Terra. Tais duplicatas, além de adquirir as características do oponente, também adquirem suas técnicas.', E'禁術・泥人形', E'Kinjutsu: Doro Ningyō', E'Técnica Proibida: Golem de Lama', E'Jutsu Proibido: Golem de Lama', NULL, NULL, E'Todos os alcances', NULL, E'Passaro'),
(2209, E'Técnica Proibida: Selo Gedō', E'É um kinjutsu desenvolvido por Orochimaru durante a Segunda Guerra Mundial Shinobi. Ele combina os dois poderes de selamento e confusão em uma técnica, dando-lhes os nomes de \'Selo Gedō: Selar\' (外道ノ印・封, Gedō no In: Fū; Literalmente significa \'Selo do Caminho Exterior: Selar\'; Games em Português \'Marca Gedō: Selar\') e \'Selo Gedō: Confusão\' (外道ノ印・乱, Gedō no In: Ran; Literalmente significa \'Selo do Caminho Exterior: Confusão\'; Games em Português \'Marca Gedō: Confusão\') ao usá-los separadamente.', E'禁術・外道の印きんじゅつ・げどうのいん', E'Kinjutsu: Gedō no In', E'Técnica Proibida: Selo do Caminho Exterior', E'Jutsu Proibido: Marca Gedō', NULL, NULL, E'Curto alcance', NULL, E'Selar: Rato → Hibrido de Rato e Selo de Confronto → Cao → Cavalo → Cobra → Macaco → Cobra,Confusao: Selo da Técnica da Morte Mútua das Cobras Gêmeas → Hibrido de Rato e Selo de Confronto → Cão → Cavalo → Rato → Sho → Coelho Espelhado → Selo da Técnica da Morte Mútua das Cobras Gêmeas → Hibrido de Rato e Selo de Confronto → Selo de Ativacão da Técnica de Troca da Mente do Corpo'),
(2210, E'Técnica Quimera', E'O Kimera no Jutsu é um kinjutsu que foi desenvolvido por Hiruko, um Nukenin de Konohagakure, a fim de compensar sua falta de habilidades especiais naturais.', E'鬼芽羅の術キメラのじゅつ', E'Kimera no Jutsu', E'Técnica de Rebento Demoníaco de Rede de Captura', NULL, NULL, NULL, E'Curto alcance', NULL, E'Boi → Cavalo → Lebre'),
(2211, E'Técnica Reversível', E'Depois de invocar uma de suas cobras gigantes para engolir completamente o alvo, Orochimaru faz a cobra derramar completamente a sua pele. Em seguida, Orochimaru ordena a cobra digerir instantaneamente seu alvo.', E'リバーシブルの術', E'Ribāshiburu no Jutsu', E'Técnica Reversível', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(2212, E'Técnica Saltitante Saltitante', E'Esta é uma variação da Técnica Sensual. Usando as habilidades que Naruto lhes havia ensinado, Konohamaru, Moegi e Udon transformam-se em três lindas mulheres: Konohamaru vai se transformar em a noite na cidade, \'Pudim de Creme\' (カスタードプリン, Kasutādo Purin; Ocidente: \'Modelo\'); Moegi em a ida à praia, \'Pudim Queimado\' (直火焼きプリン, Jikabiyaki Purin; Ocidente: \'Estrela de Cinema\'); Udon em enfermeira, \'Pudim de Leite\' (牛乳プリン, Gyūnyū Purin; Ocidente: \'Recepcionista do Salão de Bronzeamento\'). Todos os três, então, saltam os seios para divertir os seus adversários, e por isso, os três foram capazes de fazer suas vítimas caírem inconscientes. Naruto sentiu que a técnica ainda precisava de \'bon-kyu-bon\' (japonês para \'grande-pequeno-grande\', referindo-se ás \'três medidas ideais\', \'busto-cintura-quadril\'), que foi censurada na versão Ocidental em \'Va Va Voom\'.', E'プリンプリンの術', E'Purin Purin no Jutsu', E'Técnica Pudim Pudim', NULL, NULL, NULL, NULL, NULL, NULL),
(2213, E'Técnica Secreta Improvisada: Técnica de Shuriken nas Esferas de Relâmpago', E'Trata-se de uma técnica colaborativa entre Boruto e Sarada para criar uma série de esferas de relâmpago anexadas às pontas de uma shuriken infundida por relâmpago e, enfim, serem lançadas. Por causa da carga elétrica altíssima, é possível ver o rastro da técnica, bem como causar uma explosão a qualquer contato.', E'即席奥義・雷球手裏剣の術そくせきおうぎ・らいきゅうしゅりけんのじゅつ', E'Sokuseki Ōgi: Raikyū Shuriken no Jutsu', E'Técnica Secreta Improvisada: Técnica de Shuriken nas Esferas de Relâmpago', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(2214, E'Técnica Secreta: Braço de Inseto', E'Shino envia seus insetos que se ligam e formam um braço, que serve para capturar ou imobilizar.', E'秘術・蟲腕ひじゅつ・むしうで', E'Hijutsu: Mushiude', E'Técnica Secreta: Braço de Inseto', E'Jutsu Oculto: Braço de Inseto', NULL, NULL, NULL, NULL, NULL),
(2215, E'Técnica Secreta: Casulo de Insetos', E'Esta técnica exclusiva permite que o usuário acelere o crescimento de seus insetos. Como o corpo do usuário já é uma colmeia da sorte para os insetos, o usuário envolve-se em um casulo real para ajudar ainda mais os insetos. Este é um movimento extremamente útil para os membros do clã Aburame, já que seus ataques centram-se principalmente sobre o uso de insetos. No entanto, o uso desta técnica no meio de uma batalha pode ser potencialmente perigoso, pois deixa o usuário estático e vulnerável. Como tal, quando esta técnica está sendo empregada, o usuário geralmente encontra um local seguro ou de outra forma pode ser guardada por seu companheiro de equipe.', E'秘術・蟲繭', E'Hijutsu: Mushimayu', E'Técnica Secreta: Casulo de Insetos', NULL, NULL, E'Jutsu Oculto: Casulo de Insetos', NULL, NULL, E'Boi → Cao → Passaro → Carneiro'),
(2216, E'Técnica Secreta: Coleta de Insetos', E'Com um simples toque em uma superfície com a palma e os dedos, o usuário libera uma pequena teia de chakra que atrai insetos para esse local. Uma vez que o Aburame pode se comunicar com os insetos, pode ser útil para ganhar informação através da recolha de insetos locais.', E'秘術・蟲寄せ', E'Hijutsu: Mushiyose', E'Técnica Secreta: Coleta de Insetos', NULL, NULL, E'Jutsu de Invocação de Insetos', E'Curto alcance', NULL, E'Carneiro'),
(2217, E'Técnica Secreta: Esfera de Insetos', E'Esta técnica cobre todo o corpo do inimigo com os kikaichū viventes dentro do corpo do membro do clã Aburame. Os kikaichū de sensoriamento de chakra são espalhados por uma vasta gama e no momento em que localizam o inimigo, os insetos, seguindo as ordens do usuário, reúnem-se de uma só vez. Os insetos cercam completamente o alvo, fazendo um globo. Eles contém o inimigo e começam a comer o seu chakra picada por picada. Em pouco tempo, a morte vai chegar. Mesmo se o alvo conseguir escapar, os insetos vão segui-lo imediatamente.', E'秘術・蟲玉ひじゅつ・むしだま', E'Hijutsu: Mushidama', E'Técnica Secreta: Esfera de Insetos', NULL, E'Jutsu Secreto da Esfera de Insetos', NULL, E'Todos os alcances', E'Rank –', NULL),
(2218, E'Técnica Secreta: Garoa', E'Esta técnica é conhecida pelo uso exclusivo dos ninjas-caçadores de Kirigakure. O usuário cria uma potente chuva com gotas d\'água capazes de absorver chakra. Tanto o Ninjutsu de Bolha de Sabão de Utakata como o Rasengan de Naruto foram absorvidos por esta técnica.', E'秘術・霧雨ひじゅつ・きりさめ', E'Hijutsu: Kirisame', E'Técnica Secreta: Garoa', NULL, NULL, E'Jutsu Secreto: Kirisame', E'Todos os alcances', NULL, E'Carneiro → Cao → Rato'),
(2219, E'Técnica Secreta: Invocação: Castelo Crepúsculo', E'Kabuto usa oito etiquetas de selamento para invocar o Castelo Crepúsculo. O castelo pode absorver a vitalidade de vários alvos, capaz de aprisionar-los como fantasmas que vagam pelo castelo após terem toda sua vitalidade absorvida, alem de destruir todo o local a sua volta. Quando o castelo é destruído através da remoção das oito etiquetas de selamento, ele desaparece sem deixar vestígios.', E'秘術・口寄せ・無幻城ひじゅつ・くちよせ・むげんじょう', E'Hijutsu: Kuchiyose: Mugenjō', E'Técnica Secreta: Invocação: Castelo de Fantasmas Infinitos', E'Jutsu Secreto: Invocação: Mugenjo', NULL, NULL, E'Todos os alcances', NULL, NULL),
(2220, E'Técnica Secreta: Liberação de Selamento', E'Tsukino usa suas dois tantō para executar a sua técnica. Ao usar essa técnica, ela pode dissipar quaisquer selos.', NULL, NULL, E'Técnica Secreta: Liberação de Selamento', E'Jutsu Hiden: Destruição de Selamento', NULL, NULL, NULL, NULL, NULL),
(2221, E'Técnica Secreta: Pântano de Insetos', E'Shino envia seus insetos e eles rapidamente envolvem o corpo do oponente. Em seguida, eles esmagam-no com força bruta.', E'秘術・蟲沼', E'Hijutsu: Mushinuma', E'Técnica Secreta: Pântano de Insetos', E'Jutsu Oculto: Pântano de Insetos', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(2222, E'Técnica Secreta: Tornado de Insetos', E'Esta técnica cobre todo o corpo do inimigo com os kikaichū que vivem dentro do corpo do membro do clã Aburame. Os kikaichū sensores de chakra são espalhados por uma vasta gama, e no momento em que localizam o inimigo, os insetos, seguindo ordens do usuário, reúnem de uma só vez completamente em torno do alvo criando um tornado em movimento rápido que aparentemente desintegra o alvo.', E'秘術・蟲竜巻ひじゅつ・むしたつまき', E'Hijutsu: Mushitatsumaki', E'Técnica Secreta: Tornado de Insetos', E'Insetos Parasitas: Tufão[1]', NULL, E'Jutsu Oculto: Tornado de Besouros', E'Todos os alcances', NULL, NULL);
