INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(361, E'Chuva de Aranhas', E'Após Kidōmaru invocar Kyodaigumo, ele libera um saco de ovos cheio de bebês aranhas com aproximadamente o tamanho de um cão pequeno. Kidōmaru, então, corta o saco, fazendo com que as aranhas a chovam sobre o seu adversário. Cada aranha, em seguida, tece um fio de teia enquanto caem, cercando assim o adversário em teias e imobilizando-o.', E'雨蜘蛛あまぐも', E'Amagumo', E'Chuva de Aranhas', NULL, NULL, NULL, E'Todos os alcances', E'Rank B', NULL),
(362, E'Chuva de Areia de Ferro', E'É um ataque de projétil que usa a areia de ferro endurecida em grãos minúsculos para atacar simultaneamente em toda uma vasta gama. As balas são tão rápidas que elas são difíceis de ver. No momento em que o usuário tenha terminado de preparar o ataque, ele evade se tornando extremamente difícil. Usando a força repelente de magnetismo, a velocidade das balas aumenta rapidamente após os disparos. Além disso, o usuário também pode usar um padrão de ataque, onde as balas de areia de ferro são moldadas em agulhas afiadas, aumentando sua capacidade de ferir ou matar o adversário. Uma vez que a areia de ferro tem uma força magnética e também é embebida em veneno, a defesa contra esta técnica é quase impossível. Esta técnica é especialmente ameaçadora para os usuários de marionetes, uma vez que os bonecos podem ser imovelmente renderizados, se a areia se alojar em suas articulações.', E'砂鉄時雨さてつしぐれ', E'Satetsu Shigure', E'Chuva de Areia de Ferro', NULL, NULL, E'Areia de Ferro: Chuvisco de Ferro', E'Todos os alcances', E'Rank –', E'Carneiro'),
(363, E'Chuveiro de Agulhas', E'Depois de o usuário jogar um guarda-chuva especial equipado com molas para o ar, ele cria um \'chuveiro\' granizado de senbon. Este jutsu não pode ser evitado movendo-se para longe uma vez que as agulhas cobrem uma vasta área em todas as direções, controladas por chakra. Embora as agulhas sejam referidas como capazes de perfurar uma chapa de aço com cinco milímetros de espessura, elas podem ainda ser bloqueadas por um escudo suficientemente potente. Elas também podem ser mergulhadas em veneno para aumentar a letalidade.', E'如雨露千本じょうろせんぼん', E'Jouro Senbon', E'Chuveiro de Agulhas', E'Arte Ninja: Chuveiro de Agulhas', NULL, E'Arte Ninja: Tempestade Senbon', E'Médio alcance', E'Rank C', E'Tigre'),
(364, E'Cicatrização Yin de Feridas de Destruição', E'Um Iryō Ninjutsu onde se antecipa o local do inimigo vai atacar e preventivamente aplica tratamento médico a que, reduz os prejuízos ao mínimo. O utilizador concentra chakra para aquela área e começa o processo de recriação da célula antes mesmo da área alvo a ficar danificada. Usando a expressão facial e os movimentos do adversário, velocidade e potência da técnica, o usuário analisa tudo em um instante e com precisão deduz onde o ataque vai pousar. Ainda mais do que prever com precisão o ataque ou instantaneamente medir a situação, uma grande capacidade de resistência é necessária, por isso pode-se dizer que esta técnica é exclusiva para Kabuto.', E'陰癒傷滅', E'In\'yu Shōmetsu', E'Destruição de Ferimentos da Cura Secreta', E'Ativação Celular', NULL, NULL, NULL, E'Rank A', NULL),
(365, E'Ciclone de Vento do Relâmpago', E'As máscaras de Kakuzu utilizam a Liberação de Vento: Atsugai e a Liberação de Relâmpago: Gian e atira-os no oponente, o aprisionando em um ciclone de relâmpago que explode depois de alguns ataques.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(366, E'Cilada', E'Trata-se de uma série de armadilhas para enfraquecer, capturar, enganar, derrubar ou distrair um inimigo.', E'ブービートラップぶうびいとらっぷ', E'Būbītorappu', E'Cilada', E'Armadilha de Esferas de Aço[2]', NULL, NULL, NULL, NULL, NULL),
(367, E'Cinco Mil Combos de Naruto Uzumaki', E'Naruto cria vários clones da sombra e, em seguida, lança seu oponente para o alto. Então, ele cria cinco mil clones da sombra e ataca seu alvo por todos os lados. Finalmente, cada clone acumula o chakra de Kurama em seu punho direito e desferem uma onda de socos, que resulta em uma erupção de chakra alaranjado, que pode ser vista do espaço.', E'うずまきナルト五千連弾', E'Uzumaki Naruto Gosen Rendan', NULL, E'Naruto Barragem 5 mil de Uzumaki', NULL, NULL, E'Curto alcance', NULL, NULL),
(368, E'Cintilação de Clones', E'Shisui cria dois clones das sombras que avançam em alta velocidade em direção ao oponente e desferem um corte com kunai, ganhando tempo para que Shisui realize uma nova sequência de golpes.', E'分瞬ぶんしゅん', E'Bunshun', E'Cintilação de Clones', E'Duplo Flash', NULL, NULL, E'Curto a Médio alcance', NULL, E'Selo de Clonagem'),
(369, E'Cinzas Flamejantes', E'Sasuke lança um dragão de fogo contra o oponente e, quando ele está prestes a cair, ele ativa sua Liberação de Chama: Kagutsuchi para golpear o oponente pelas costas e queimá-lo, causando uma pequena nuvem de fumaça.', E'灰炎', E'Haien', E'Cinzas Flamejantes', NULL, NULL, NULL, E'Curto alcance', NULL, E'Selo do Confronto'),
(370, E'Cipreste', E'Kimimaro ataca repetidamente o oponente com ataques alternados, cria ossos que saem de suas principais juntas e finaliza com um ataque de ossos que saem de ambas as suas mãos.', E'椹', E'Sawara', E'Cipreste', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(371, E'Clethra', E'Kimimaro corta seu oponente começando por baixo com sua espada óssea, que logo é estendida para duas vezes maior que o comprimento de sua espada óssea normal e, em seguida, corta o oponente na altura dos pés, derrubando-o.', E'令法', E'Ryōbu', E'Clethra', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(372, E'Clone Bombardeio Suicida', E'Depois de criar um clone com a Técnica dos Clones das Sombras, Deidara ingere sua Argila Explosiva para causar uma explosão, criando uma distração que pode ser usada para uma fuga. Com um \'anúncio de atentado suicida\' a cautela do inimigo é aumentada e esta oportunidade é usada para trocar de lugar com um clone das sombras. A explosão é forte o suficiente para destruir uma floresta inteira. Esta técnica requer um uso adepto de truques verbais; com este fundamento do uso de muitas \'artes\' para brincar com o inimigo, ele certamente cumpre o seu propósito.', E'自爆分身じばくぶんしん', E'Jibaku Bunshin', E'Clone Bomba Suicida', E'Clone Explosivo', E'Jutsu dos Clones Explosivos', E'Clone de Autodestruição', E'Todos os alcances', E'Rank –', NULL),
(373, E'Clone Parasita', E'Esta técnica permite que Zetsu Branco crie clones de si mesmo, os quais ele também é capaz de controlar remotamente. Diferente da maioria das técnicas dessa natureza, esses clones não desaparecem ao receber danos, morrer ou ter seu usuário principal morto, tornando assim um dos atributos mais notáveis dessa habilidade nativa. No decorrer da série, Zetsu Branco apresentou a forma de usá-los em combinação com a Técnica do Substituto, a fim de assumir a forma de outro ninja antes tocado para tomar suas características, inclusive técnicas.', E'寄生分身きせいぶんしん', E'Kisei Bunshin', E'Clone Parasita', NULL, NULL, NULL, NULL, NULL, NULL),
(374, E'Clone da Besta Humana', E'Esta é uma técnica básica do clã Inuzuka que permite ao animal tomar as características físicas de seu dono.', E'獣人分身', E'Jūjin Bunshin', E'Clone da Besta Humana', NULL, NULL, E'Clone do Homem-Besta', NULL, E'Rank D', E'Tigre'),
(375, E'Clone de Areia', E'Esta técnica é usada para criar clones em forma de si mesmo ou outros, utilizando areia como um meio. A forma do clone pode ser alterada instantaneamente na areia, e do corpo do inimigo pode ser preso. Suas muitas aplicações com ataques de longo alcance podem agir como uma isca e um chamariz. Devido às propriedades da areia, Gaara foi capaz de utilizá-la como uma armadilha, escondendo pó de ouro dentro dela. Este é o lugar onde ele desenha a linha com outros clones.', E'砂分身', E'Suna Bunshin', E'Clone de Areia', NULL, NULL, E'Jutsu Clone de Areia', NULL, E'Rank –', NULL),
(376, E'Clone de Areia de Ferro', E'A técnica consiste em criar um clone a partir da manipulação da areia de ferro para fins variados.', E'砂鉄の分身', E'Satetsu no Bunshin', E'Clone de Areia de Ferro', NULL, NULL, NULL, NULL, NULL, NULL),
(377, E'Clone de Argila', E'Uma técnica altamente conveniente, característica de Deidara. Acima de todas as outras técnicas de Bunshinjutsu, este clone tem um duplo papel. Ele atua como um substituto e uma armadilha para capturar o inimigo. Feito a partir de argila, ele pode atacar e capturar o inimigo, absorvendo-o em seu próprio corpo. Ele mostra a sua forma real após receber um ataque. Embora a dureza do clone seja baixa, o inimigo é facilmente imobilizado. Se a Argila Explosiva for adicionada ao clone, ela é capaz de explodir com o inimigo preso nele.', E'粘土分身ねんどぶんしん', E'Nendo Bunshin', E'Clone de Argila', NULL, NULL, NULL, NULL, E'Rank –', NULL),
(378, E'Clone de Cera de Abelha', E'O usuário faz um clone feito de cera de abelha e quando ferido o suficiente ele se dissolve de volta para dentro da cera.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(379, E'Clone de Cobras', E'Após a implantação do DNA de Orochimaru, Kabuto desenvolve uma grande quantidade de cobras minúsculas que podem fundir-se em um clone de uma pessoa viva, necessitando de uma amostra genética ou em uma serpente maior para combate. Durante o Arco Chikara, Kabuto não só criou um clone de Hidan, mas também um Clone do Nove-Caudas. Os clones das cobras têm uma vida útil curta, fazendo com que eles eventualmente sofram um colapso celular dentro de 48 horas após sua criação.', E'クローン蛇', E'Kurōn Hebi', E'Clone de Cobras', NULL, NULL, NULL, NULL, NULL, NULL),
(380, E'Clone de Cobras: Técnica da Serpente', E'Kabuto bate a mão sobre a superfície da água com suas cobras especialmente desenvolvidos e emite um raio vermelho-escuro, o que faz com que as cobras assumam a forma de uma cobra gigante azul com uma boca vermelha e sem olhos. A cobra, em seguida, forma cilindros hidráulicos e empurra os inimigos debaixo d\'água, na tentativa de afogá-los. Kabuto usou essa técnica dentro do buraco, que é conhecido por acelerar o processo de recriação de suas cobras.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(381, E'Clone de Fumaça', E'Esta técnica cria um clone feito de fumaça. Quando é atingido, se transforma em fumaça semelhante à cortina de fumaça que o usuário cria. O clone de fumaça é usado principalmente como uma tática de distração como outras técnicas de clones.', E'煙分身', E'Kemuri Bunshin', E'Clone de Fumaça', NULL, NULL, E'Clone de Fumaça', NULL, NULL, NULL),
(382, E'Clone de Madeira: Pontapé', E'Yamato faz um pilar de madeira sair do chão e bater no oponente, derrubando-o para trás. Um clone então vira o pilar e bate na perna do oponente, derrubando o oponente no chão.', E'木分身・蹴もくぶんしん・しゅう', E'Moku Bunshin: Shū', E'Clone de Madeira: Pontapé', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(383, E'Clone de Papel', E'Tirando proveito da sua habilidade de se transformar em papel, Konan se torna livre para criar e recriar clones parecidos com ela a partir desse material. Ao infundir seu chakra em todos os clones, ela os molda em uma réplica perfeita de si e os controla remotamente. Quando o clone é atingido, ele se dispersa, ou na melhor das hipóteses, se desfaz para Konan recriá-lo novamente.', E'紙分身', E'Kami Bunshin', E'Clone de Papel', NULL, NULL, E'Clone de Papel', NULL, NULL, NULL),
(384, E'Clone do Espelho de Gelo', E'Usando seu Espelhos Demoníacos de Cristais de Gelo, Haku cria clones resistentes de gelo capazes de lutar a uma velocidade semelhante à de Haku. Quando são derrotados, os clones se dissipam em água.', E'氷鏡分身こおりきょうぶんしん', E'Kōrikyō Bunshin', E'Clone do Espelho de Gelo', E'Clone Espelho de Gelo', NULL, NULL, NULL, NULL, NULL),
(385, E'Clones Assassinos do Dez-Caudas', E'Usado como uma manobra defensiva, o Dez-Caudas tem a capacidade de produzir inúmeras réplicas de si mesmo a partir de seu corpo principal. Enquanto eles têm muitas variações em termos de sua aparência humanoide, com alguns aparecendo e outros que possuem uma forma animalesca e operacional em quatro patas, estas partes duplicatas muitos dos traços distintivos do Dez-Caudas, como sua boca, bem como protuberâncias cravadas na cabeça e costas. Seus tamanhos também variam, como alguns em detrimento de outras torres, aparecendo semelhante em tamanho às grandes invocações. As cópias são capazes de formar vários tipos de armas, incluindo espécies de halteres, também é capaz de criar apêndices semelhantes a machados e foices apêndices, bem como projéteis de lança e paredes fortificadas para fins defensivos. Apesar de suas diferenças, todas as cópias operam com o único objetivo de proteger o corpo principal do Dez-Caudas de ataque.', E'十尾分殺体じゅうびぶんさつたい', E'Jūbi Bunsatsutai', E'Clones Assassinos do Dez-Caudas', E'Clone do Dez-Caudas', NULL, NULL, NULL, NULL, NULL),
(386, E'Clones da Sombra: Superior', E'Naruto cria um clone da sombra para jogá-lo em direção ao seu oponente, dando-lhe um soco superior.', E'影分身・アッパー', E'Kage Bunshin: Appā', E'Clones da Sombra: Superior', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(387, E'Clones das Sombras: Grande Rotação', E'Naruto cria dois clones da sombras, formando uma corrente com cada Naruto segurando o outro pelos tornozelos. Naruto então gira, batendo os seus oponentes com os clones.', E'影分身・大回転かげぶんしん・だいかいてん', E'Kage Bunshin: Daikaiten', E'Clones das Sombras: Grande Rotação', E'Clone das Sombras: Rotação Gigante', NULL, NULL, E'Curto alcance', NULL, NULL),
(388, E'Clones de Areia', E'Gaara manipula a areia presente no ambiente circundante e cria vários clones de areia.', E'多重砂分身たじゅうすなぶんしん', E'Tajū Suna Bunshin', E'Múltiplos Clones de Areia', NULL, NULL, NULL, NULL, NULL, NULL),
(389, E'Clones de Rastro', E'Shisui usa a Técnica de Cintilação Corporal e move-se nas redondezas, criando diversos clones de impressões vindas de sua velocidade. Através dela, ele pode mover-se entre os clones, e acertar ataques com cada um deles, como se tivessem a massa dele. Se for atacado, ele desvia, deixando o ataque de seu adversário passar por um dos seus rastros. Os clones criados por Shisui podem se desmaterializar quando recebem um ataque e se materializarem para realizar um golpe.', E'残像分身ざんぞうぶんしん', E'Zanzō Bunshin', E'Clones de Rastro', NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre → Cao → Rato'),
(390, E'Clêmatis', E'O usuário corre em direção ao oponente, atacando com os ossos de suas costas e finalizando com sua broca de osso no braço.', E'クレマチス', E'Kuremachisu', E'Clêmatis', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(391, E'Cobra (game)', E'Jūgo lança Suigetsu em direção ao oponente e Suigetsu ataca o oponente numerosamente com a Kubikiribōchō.', E'蛇', E'Hebi', E'Cobra', E'Hebi', NULL, NULL, E'Médio alcance', NULL, NULL),
(392, E'Cobra Furiosa', E'Enquanto em seu \'Modo Despertado\', Mitsuki alonga seus braços e desfere uma sequência de socos furiosos e poderosos, aniquilando tudo que estiver em seu caminho. Pelo fato de seus braços se tornarem tão flexíveis, a barragem de socos cria uma cúpula de impacto.', E'猛蛇', E'Takerikuchinawa', E'Cobra Furiosa', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(393, E'Cobra de União Vermelha', E'Orochimaru libera de sua manga várias cobras que atacam e levantam o oponente para o ar, onde as cobras, em seguida, separam-se de Orochimaru e então giram ao redor do oponente enquanto atacam várias vezes, antes de se dispersarem.', NULL, NULL, NULL, E'Cobra de União Vermelha', NULL, NULL, E'Curto alcance', NULL, NULL),
(394, E'Cobras de Absorção de Chakra', E'O usuário retém a vítima com as serpentes que constantemente absorvem seu chakra. Quanto mais a vítima aumenta seu chakra, as cobras o aperta mais.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, E'Apertar: Selo do Confronto'),
(395, E'Coerção Sharingan', E'Ao olhar para o Sharingan do usuário, esta técnica força um genjutsu de pesadelo sobre sua vítima, dominando-a e derrubando-a. Mesmo, Sai, que geralmente não tem emoções foi profundamente afetado por este genjutsu.', E'威圧写輪眼いあつしゃりんがん', E'Iatsu Sharingan', E'Coerção do Olho Copiador Giratório', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(396, E'Colaboração: Arte Final', E'Deidara começa criando aves de argila C1, que Sasori manipula com linhas de chakra para acertar o inimigo com maior precisão, depois Deidara cria seu dragão de argila de C2, que Sasori reveste em Areia de Ferro com a marionete do Terceiro Kazekage, também lhe desferindo um golpe com sua cauda espetada. O dragão ataca seu oponente, resultando em uma explosão com estilhaços de Areia de Ferro.', E'合作・究極芸術がっさく・きゅうきょくげいじゅつ', E'Gassaku: Kyūkyoku Geijutsu', E'Colaboração: Arte Final', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(397, E'Colapso do Céu', E'O usuário primeiro soca o oponente quatro vezes, enviando-o para colidir com uma grande pedra. O usuário, em seguida, pula em cima da pedra, e usa um chute reforçado no oponente, enviando-o para longe e, em seguida, a pedra se desmorona.', E'天崩', E'Tenhō', E'Colapso do Céu', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(398, E'Colapso do Redemoinho de Vento', E'Temari cria um forte tornado de vento a fim de prender o oponente. Com o oponente preso dentro do tornado e sendo dilacerado pelo vento, Temari manipula o tornado para lançá-lo para o alto e bruscamente, com a ajuda de seu Leque Dobradiço Gigante, direciona-o para baixo, esmagando o oponente dentro do tornado.', E'旋風落とし', E'Tsumuji Otoshi', E'Colapso do Redemoinho de Vento', E'Queda por Tornado', NULL, NULL, E'Médio alcance', NULL, NULL),
(399, E'Colisão Explosiva da Samehada', E'Kisame desfere vários golpes em seu oponente com Samehada e libera água através disso, e assim finaliza seu oponente com um golpe vertical — de cima para baixo — resultando na ascensão de uma grande pilar de água no local de impacto.', E'鮫肌衝爆', E'Samehada Shōbaku', E'Colisão Explosiva da Pele de Tubarão', E'Choque do Impacto da Samehada', NULL, NULL, E'Curto alcance', NULL, NULL),
(400, E'Colisão Impulsiva do Braço Voador', E'Fū acerta uma sequência de socos e pontapés, manifesta as asas de Chōmei para mandar uma rápida explosão de vento, e voa até seu oponente, forçando-o com os braços cruzados na frente dela.', E'交腕飛突', E'Kōwan Hitotsu', E'Colisão Impulsiva do Braço Voador', E'Precipitação do Braço Voador', NULL, NULL, E'Curto alcance', NULL, NULL),
(401, E'Colisão de Fluxo de Água', E'Kisame ataca seu o oponente com a Samehada, e então ele adiciona água na Samehada e ataca seu oponente, e então envia uma onda de água.', NULL, NULL, NULL, E'Colisão de Fluxo de Água', NULL, NULL, E'Curto alcance', NULL, NULL),
(402, E'Colmeia de Rocha', E'O usuário cria uma colmeia de abelha feita de rocha. Esta colmeia abriga a larva das abelhas que consomem chakra.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(403, E'Coloração da Flor Perfumada', E'Kurenai cria um clone de flor de cerejeira que ataca o oponente com vários golpes com uma kunai criando ao mesmo tempo um fluxo de pétalas de flor de cerejeira por trás dele, antes de girar em torno e entregar um golpe final que envia o oponente para voar.', E'香咲彩かショウサイ', E'Kashōsai', E'Coloração da Flor Perfumada', E'Florescência do Perfume Brilhante', NULL, NULL, E'Curto alcance', NULL, NULL),
(404, E'Combinação B', E'A Combinação B é uma tática de batalha utilizada por membros da Anbu.', E'コンビネーションBこんびねえしょんB', E'Konbinēshon B', E'Combinação B', NULL, NULL, NULL, NULL, NULL, NULL),
(405, E'Combinação Sádica', E'Kakuzu entrega vários socos no oponente, em seguida, utiliza técnicas de vento e fogo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(406, E'Combinação de Arte', E'Sasori, com a marionete do Terceiro Kazekage usa projéteis de areia de ferro enquanto Deidara acrescenta suas aranhas de argila nas agulhas que envenenam o oponente, e em seguida, explodindo-o.', E'合同芸術ごうどうげいじゅつ', E'Gōdō Geijutsu', E'Combinação de Arte', E'Juntando Arte', NULL, NULL, NULL, NULL, NULL),
(407, E'Combinação de Língua Afiada', E'Tayuya move-se rapidamente para trás do oponente, chuta-o para o ar, e em seguida avança até ele. Ela então ativa o primeiro nível de seu selo amaldiçoado e dá um forte soco na cara dele.', E'毒舌コンボ', E'Dokuzetsu Konbo', E'Combinação de Língua Afiada', E'Combinação da Língua de Chicote', NULL, NULL, E'Curto alcance', NULL, NULL),
(408, E'Combinação de Ninjutsu', E'A Combinação de Ninjutsu é uma poderosa técnica colaborativa elemental utilizada por Gitai, Setsuna e Shizuku.', E'併用忍術へいようにんじゅつ', E'Heiyō Ninjutsu', E'Combinação de Técnicas Ninja', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(409, E'Combinação de Técnica Secreta', E'O usuário e um ou mais companheiros de equipe podem juntar-se e executar um ataque especial em conjunto sobre o advesário. Os movimentos executados variam dependendo do jogo.', E'合体奥義', E'Gattai Ōgi', E'Combinação de Técnica Secreta', NULL, NULL, NULL, NULL, NULL, NULL),
(410, E'Combo Ataque: Shinra Tensei', E'Nagato começa com um ataque de palma, seguido por dois receptores de rompimento de chakra, e uma saraivada de shuriken, girando em direção ao oponente e ataca com as lâminas de rompimento de chakra e termina com um Shinra Tensei.', E'連撃・神羅天征', E'Rengeki: Shinra Tensei', E'Combo Ataque: Subjugação Celestial do Deus Onipresente', E'Combo Ataque: Impulso Todo Poderoso', NULL, NULL, NULL, NULL, NULL),
(411, E'Combo Rápido da Raposa de Nove-Caudas', E'Em sua forma de uma cauda, Naruto joga o oponente no chão, em seguida, lança-o no ar e segue saltando até acertá-lo. Ele, em seguida, repete o mesmo processo diversas vezes até que finalmente mate o oponente.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(412, E'Combo Tonton', E'Shizune transforma Tonton em uma cópia de si mesma e usa, em seguida, um Bisturi de Chakra para atordoar seu oponente. Tonton aparece por trás e salta para cima enquanto Shizune ataca o oponente com um ataque com dois Bisturi de Chakra. Ela pega a ponta da cauda de Tonton enquanto a porca faz uma cambalhota; o oponente entra em colapso. Shizune então percebe que a cauda de Tonton está queimando e começa a curá-la, e Tonton se irrita e bate em Shizune, em seguida.', E'ト ン ト ン コ ン ビ', E'Tonton Konbi', E'Combo Tonton', E'Dupla Tonton', NULL, NULL, E'Curto alcance', NULL, NULL),
(413, E'Combo da Besta com Cauda', E'Naruto, em seu Modo de Chakra do Nove-Caudas, e Minato combinam seus Rasengan e atacam o oponente com ele. Minato, em seguida, lança sua Kunai do Deus Voador do Trovão no adversário. Os dois ninjas então tocam os punhos, Minato entra em seu Modo de Chakra do Nove-Caudas. Pai e filho, em seguida, usam a Técnica do Deus Voador do Trovão e se teletransportam para a kunai lançada, desferindo uma série de ataques no adversário, formando uma silhueta de chakra amarelo e laranja em forma de Rasengan, o que cria uma enorme explosão, causando grande dano no processo.', E'尾獣連弾びじゅうれんだん', E'Bijū Rendan', E'Combo da Besta com Cauda', E'Dueto Bijū', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(414, E'Combo da Dança da Espada', E'O usuário avança em direção a seu adversário e desfere uma série de cortes rápidos com sua espada.', E'剣技連舞', E'Kengi Tsuremai', E'Combo da Dança da Espada', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(415, E'Combo da Espada', E'Com esta técnica, Yūgao faz uma sequência de golpes com sua espada e termina com um poderoso corte que fere gravemente seu oponente.', E'剣連弾', E'Tsurugi Rendan', E'Combo da Espada', E'Barragem da Espada', NULL, NULL, E'Curto alcance', NULL, NULL),
(416, E'Combo da Super Besta Voadora', E'Sai cria um falcão de tinta e com Naruto voando alto no ar junto com alguns clones da sombra e logo em seguida caindo sobre o oponente.', E'超獣飛翔連弾ちょうじゅひしょうれんだん', E'Chōjū Hishō Rendan', E'Combo da Super Besta Voadora', E'Múltiplos Ataques da Super Besta Voadora', NULL, NULL, E'Curto alcance', NULL, NULL),
(417, E'Combo da Zona de Naruto', NULL, E'ナルト一帯連弾ナルトいったいれんだん', E'Naruto Ittai Rendan', E'Combo da Zona de Naruto', NULL, E'Rajada em Área do Naruto Uzumaki', NULL, E'Curto a Médio alcance', E'Rank A', NULL),
(418, E'Combo de 10 Golpes', E'Lars soca o oponente, antes de golpeá-lo no ar com ataques banhados em sua Liberação de Relâmpago. Em seguida, ele salta em uma velocidade incrível e oferece mais um soco, que é alimentado com relâmpago novamente, atingindo fatalmente o oponente no abdômen.', E'10連コンボ', E'Jū Ren Konbo', E'Combo de Dez Golpes', E'Combo de Dez Hits', NULL, NULL, E'Curto alcance', NULL, NULL),
(419, E'Combo de 16 Golpes', E'Um movimento muito eficaz, Ino usa-o para nocautear um oponente. Ela dá tapas e chutes utilizando flores em torno de suas mãos e pés.', E'16連コンボ', E'Jūroku Ren Konbo', E'Combo de 16 Golpes', E'Combo de 16 Hits', NULL, NULL, E'Curto alcance', NULL, NULL),
(420, E'Combo de Balas da Busca da Verdade', E'O usuário espalha suas Esferas da Busca da Verdade no ar logo a sua frente, então ele as dispara em rápida velocidade em direção ao seu oponente. Quando Toneri usou essa técnica, ele infundiu as esferas com seu Rosário de Chakra, aumentando seu poder destrutivo.', E'求道弾連弾ぐどうだんれんだん', E'Gudōdan Rendan', E'Combo de Balas da Busca da Verdade', E'Barragem de Balas da Busca da Verdade', NULL, NULL, E'Todos os alcances', NULL, NULL),
(421, E'Combo de Restrição pela Sombra', E'Shikamaru restringe o oponente com a Técnica da Costura pelas Sombras enquanto Naruto usa seus clones das sombras como finalização.', E'影縛り連弾かげしばりれんだん', E'Kageshibari Rendan', E'Combo de Restrição pela Sombra', E'Múltiplos Ataques da Sombra de Contenção', NULL, NULL, NULL, NULL, NULL),
(422, E'Combo do Corte Relâmpago', E'Este combo começa com uma série de cotoveladas no oponente, então socando-o no rosto, dando uma rasteira nos seus pés, chutando-o com as duas pernas e jogando-o bem alto, então usando o Corte Relâmpago na queda da vítima e derrubando-o no chão.', E'雷切連弾', E'Raikiri Rendan', E'Combo do Corte Relâmpago', E'Strike da Espada Relâmpago', NULL, NULL, E'Curto alcance', NULL, NULL),
(423, E'Combo do Leão', E'O Combo de Leão é um taijutsu único de Sasuke Uchiha e uma variação da Lótus Frontal. Sasuke o desenvolve depois de usar seu Sharingan para copiar a primeira parte do movimento. É um ataque combo ultra-rápido que exige uma condição física avançada e uma certa destreza.', E'獅子連弾ししれんだん', E'Shishi Rendan', E'Combo do Leão', E'Barragem de Leões', NULL, E'Rajada de Leões', E'Curto alcance', E'Rank C', NULL),
(424, E'Combo do Pé Voador', E'Sarada soca seu oponente para cima, em seguida ela salta e desfere dois chutes, e finaliza com um terceiro chute com ambos os pés.', E'飛連脚', E'Hirenkyaku', E'Combo do Pé Voador', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(425, E'Combo dos Múltiplos Ataques da Sombra', E'Naruto bate no inimigo e salta sobre seus ombros para o ar, onde realiza a Técnica dos Múltiplos Clones da Sombra para criar inúmeros clones que se jogam em direção do oponente. Naruto por fim prossegue entrando em sua forma de uma cauda.', E'多影撃連弾', E'Taeigeki Rendan', E'Combo dos Múltiplos Ataques da Sombra', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(426, E'Combo em Espiral de Naruto Uzumaki', E'Naruto usa seu combo com clones, chutando o oponente e criando vários clones no ar, que chutam o oponente para o chão. Naruto pula em cima de um clone que carrega consigo um Rasengan no qual Naruto usa para atacar o oponente.', E'うずまきナルト螺旋連弾', E'Uzumaki Naruto Rasenrendan', E'Combo em Espiral de Naruto Uzumaki', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(427, E'Compartilhamento de Visão do Passado', E'O usuário expele uma esfera de cor violeta da sua boca. A esfera é a experiência de alguém no passado. A esfera ilumina seus arredores com raios violetas. Somente aqueles que são dignos são capazes de ver a visão, enquanto aqueles de fora da área alvo determinada são incapazes de fazê-lo. Depois da visão é transferida, a esfera pode ser retraída para o usuário.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto Alcance', NULL, NULL),
(428, E'Compensação de Som', E'Tocando uma melodia em sua ocarina, Menma é capaz de liberar poderosos genjutsu, como os usados ​​pelos membros de seu próprio clã, como Shiin.', E'相殺の音', E'Sōsai no Ne', E'Compensação de Som', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(429, E'Conexão Invariante', E'Naruto salta no ar, onde ele cria vários clones das sombras que formam uma corrente para Sakura utilizar como chicote. Ela em seguida, gira a corrente como um furacão e acerta os clones em seu oponente com força brutal.', E'変わらない関係', E'Kawaranai Kankei', E'Conexão Invariante', E'Relacionamento Imutável', NULL, NULL, E'Médio alcance', NULL, NULL),
(430, E'Constrição da Cobra Esmagadora', E'Orochimaru coloca o oponente em um genjutsu onde suas cobras surgem do chão e se entrelace no oponente, e com o comando de Orochimaru, elas esmagam o alvo com total força, quebrando todos seus ossos.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(431, E'Contra Genjutsu', E'Um Contra Genjutsu consiste em quebrar um genjutsu ao sobrepô-lo com outro genjutsu, livrando outras pessoas de uma ilusão e do controle inimigo.', NULL, NULL, E'Contra Técnica de Ilusão', NULL, NULL, NULL, NULL, NULL, NULL),
(432, E'Contração da Corrente', E'Obito começa teletransportando-se para atrás do oponente, cortando-o com uma kunai e depois bate no oponente com suas correntes a fim de lançá-lo para o alto, teletransportando-se novamente, mas dessa vez para baixo do solo, afim de causar um ataque surpresa e acertar um chute ascendente giratório, lançando-o longe para cima.', E'鎖縄くさりなわ', E'Kusarinawa', E'Contração da Corrente', E'Bloqueio da Corda', NULL, NULL, E'Curto alcance', NULL, NULL),
(433, E'Controle de Calorias', E'Controle de Calorias (カロリーコントロール, Karorī Kontorōru) é uma técnica secreta do clã Akimichi, para converter as calorias do corpo em chakra a fim de executar suas técnicas.', E'カロリーコントロール', E'Karorī Kontorōru', E'Controle de Calorias', NULL, NULL, NULL, NULL, NULL, E'Passaro'),
(434, E'Controle de Chakra de Ryūmyaku', E'Ao controlar o poderoso chakra fluindo em suas Veias de Dragão (龍脈; Ryūmyaku), o usuário é capaz de converter o chakra e usá-lo de várias maneiras, inclusive para fornecer energia para uma cidade inteira e controlar um grande número de marionetes. Se não detido, o usuário teria uma fonte ilimitada de energia.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(435, E'Controle de Morcegos', E'Rinji tem a habilidade especial de produzir ondas ultra-sônicas e compreendê-las. Desta forma, ele pode falar com morcegos, que usam ondas ultra-sônicas para a comunicação. Ele é capaz de dar-lhes ordens, como de pesquisar a área em torno dele, e eles parecem considerá-lo um amigo. Ele pode usar esses morcegos para liberar poderosas ondas sonoras.', E'蝙蝠掣肘', E'Kōmori Seichū', E'Controle de Morcegos', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Cavalo'),
(436, E'Controle de Morcegos: Ondas Ultra-Sônicas da Mente', E'Uma vez que Rinji controla seus morcegos, e comanda-os a produzir ondas ultra-sônicas de alta frequência que afetam a mente do oponente.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(437, E'Convite Escarlate', E'Kurenai ataca o oponente com uma combinação de um ataque de cotovelo e dois chutes antes de criar múltiplas pétalas de flor de cerejeira que explodem acima do chão, levando o adversário no ar.', E'誘紅寿ゆうこうじゅ', E'Yūkōju', E'Convite Escarlate', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(438, E'Convulsão da Natureza', E'Genshō Ryūdōin fundido com o Espirito Fera cria um tremor sob os pés dos adversários, que faz com que eles percam o sentido do equilíbrio.', E'自然の痙攣', E'Shizen no Keiren', E'Convulsão da Natureza', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(439, E'Cooperação da Liberação de Relâmpago: Bomba de Trovão', E'Esta é um konbijutsu em que os usuários unem as extremidades de suas armas, liberando uma forte descarga elétrica entre as lâminas, que tem como objetivo acertar vários oponentes simultaneamente.', E'雷遁連携・サンダーボムらいとんれんけい・サンダーボム', E'Raiton Renkei: Sandābomu', E'Cooperação da Liberação de Relâmpago: Bomba de Trovão', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(440, E'Cordilheira da Areia de Ferro', E'Sasori usa sua marionete humana Terceiro Kazekage para criar uma parede de Areia de Ferro em sua frente, e em seguida, faz com que vários picos de tamanho variáveis se projetem e atinjam seu oponente.', E'砂鉄連山さてつれんざん', E'Satetsu Renzan', E'Cordilheira da Areia de Ferro', E'Cordilheira do Ferro de Areia', NULL, NULL, E'Curto alcance', NULL, NULL),
(441, E'Corpo Gerador de Propulsão', E'O usuário libera um denso vapor em toda a área, a fim de queimar seus oponentes em contato com o vapor. Esta técnica pode ser usada com o vapor existente no campo.', E'噴陣身ふんじんしん', E'Funjinshin', E'Corpo Gerador de Propulsão', E'Indestrutível', NULL, NULL, E'Curto alcance', NULL, NULL),
(442, E'Corpo de Areia', E'Primeiramente desconstruindo a areia de sua cabaça, o corpo de Gaara também se transforma em areia. Nesta forma, Gaara fica invulnerável a danos e pode viajar por longas distâncias como uma tempestade de areia fluente. A técnica também pode ser aplicada no campo de batalha, transformando partes do corpo para aumentar a capacidade de manobra no campo de batalha.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(443, E'Corrente', E'Jūgo estica seu braço a longa distância, de modo a permiti-lo capturar qualquer um ou qualquer coisa em pleno ar.', E'鎖くさり', E'Kusari', E'Corrente', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(444, E'Corrente Dinâmica do Lótus Relâmpago', E'Rock Lee desmonta seu bastão em várias partes e, em seguida, enrosca seu adversário entre as correntes para trazê-lo para perto. Finalmente, ele o ataca de modo a jogá-lo para o alto, onde Lee desfere uma saraivada de golpes antes de finalizar com um poderoso soco, que esmaga o adversário contra o solo.', E'豪雷蓮鎖ごうらいれんさ', E'Gōrai Rensa', E'Corrente Dinâmica do Lótus Relâmpago', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(445, E'Corrente de Chidori', E'Este é um ninjutsu desenvolvido por Sasuke Uchiha, durante seu treinamento com Orochimaru. Ao liberar o Chidori em todas as direções, uma descarga elétrica flui do corpo inteiro do usuário permitindo que ele afete vários inimigos.', E'千鳥流しちどりながし', E'Chidori Nagashi', E'Corrente de Mil Pássaros', NULL, NULL, E'Corrente Chidori', E'Curto alcance', E'Rank A', NULL),
(446, E'Corrente de Pregos', E'Obito lança continuamente receptores negros em seu oponente através de seu Kamui. Em seguida, ele usa as Correntes da Estátua Demoníaca para puxar as estacas novamente e utiliza-las para realizar uma série de cortes pelo corpo do oponente, o deixando em estado deplorável.', E'杭鎖', E'Kōsa', E'Corrente de Pregos', E'Selo Acumulado', NULL, NULL, E'Todos os alcances', NULL, NULL),
(447, E'Corrente de Puxar', E'Obito usa suas correntes contra seu oponente, para causar grande dano, prende os pés dele com elas e a puxa, a fim de desestabilizá-lo. Ele então se teletransporta para trás do oponente, indo de encontro e com força, desferindo-lhe um chute descendente giratório.', E'鎖引さいん', E'Sain', E'Corrente de Puxar', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(448, E'Correntes da Estátua Demoníaca', E'Uma técnica derivada do Caminho Exterior, o que permite que o usuário manifeste correntes de chakra para prender o seu alvo. Estas correntes são fortes o suficiente para prender vários Bijū a um usuário, mesmo que remotamente. Através da utilização de receptores de chakra, o utilizador pode manifestar as correntes de outros seres, ligando-se a um alvo do utilizador. Estas correntes também são capazes de prender qualquer coisa que toca o receptor chakra, e de acordo com o Cinco-Caudas, causa uma grande quantidade de dor.', E'魔像の鎖まぞうのくさり', E'Mazō no Kusari', E'Correntes da Estátua Demoníaca', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Cobra'),
(449, E'Correntes de Selamento Adamantinas', E'As Correntes de Selamento Adamantinas constituem uma poderosa técnica de selamento utilizada pelos membros do clã Uzumaki.', E'金剛封鎖こんごうふうさ', E'Kongō Fūsa', E'Correntes de Selamento Adamantinas', NULL, NULL, NULL, E'Curto a Médio alcance', E'Rank A', NULL),
(450, E'Corte Alado Celestial', E'Kinshiki empunha uma foice e uma naginata feitas de chakra, então ele corta seu oponente com a foice e finaliza rodopiando em torno do próprio eixo, desencadeando um tornado que corta tudo nas proximidades.', NULL, NULL, E'Corte Alado Celestial', E'Heavenly Winged Slice', NULL, NULL, E'Curto alcance', NULL, NULL),
(451, E'Corte Destrutivo da Dança Selvagem', E'O usuário desfere vários cortes em seu oponente com a Samehada enquanto no ar.', E'削滅乱舞さくめつらんぶ', E'Sakumetsu Ranbu', E'Corte Destrutivo da Dança Selvagem', E'Dança Louca de Aniquilação', NULL, NULL, E'Curto alcance', NULL, NULL),
(452, E'Corte Horizontal de Relâmpago Violento', E'Enquanto coberto no Modo de Chakra de Liberação de Relâmpago, A executa um golpe horizontal contra seu adversário de cima, usando da queda anterior para impulsionar o golpe.', E'雷虐水平千代舞らいぎゃくすいへいチョップ', E'Raigyaku Suihei Choppu', E'Corte Horizontal de Relâmpago Violento', NULL, NULL, NULL, E'Curto alcance', E'Rank C', NULL),
(453, E'Corte Medular', E'Kurenai chuta o oponente pela lateral, conectando-o com o seu pé antes de girar-se ao redor com seu outro pé e enviá-lo para o alto, e em seguida, atingindo-o no alto, lançando-o novamente pro chão.', E'延髄切り', E'Enzuigiri', E'Corte Medular', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(454, E'Corte Relâmpago', E'O Corte Relâmpago é uma forma melhorada e concentrada do Chidori com os mesmos efeitos e desvantagens.', E'雷切らいきり', E'Raikiri', E'Corte Relâmpago', NULL, E'Espada Relâmpago', NULL, E'Curto alcance', E'Rank S', E'Boi → Coelho → Macaco'),
(455, E'Corte Relâmpago: Duplo Terremoto Relâmpago', E'Essa técnica é a aplicação do Corte Relâmpago: Perfuração das Luzes Gêmeas em uma combinação.', E'雷切・双雷震', E'Raikiri: Sōraishin', E'Corte Relâmpago: Duplo Terremoto Relâmpago', E'Lâmina Relâmpago: Arrepio dos Relâmpagos Gêmeos', NULL, NULL, NULL, NULL, NULL),
(456, E'Corte Relâmpago: Grande Bola Rasengan', E'Essa é uma técnica utilizada por Hiruko após absorver o Corte Relâmpago de Kakashi e a Grande Bola Rasengan de Naruto.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(457, E'Corte Relâmpago: Perfuração das Luzes Gêmeas', E'Esta é uma técnica exclusiva para Kakashi.', E'雷切・双穿光', E'Raikiri: Sōsenkō', E'Corte Relâmpago: Perfuração das Luzes Gêmeas', E'Espada Relâmpago Dupla', NULL, NULL, E'Curto alcance', NULL, NULL),
(458, E'Corte Relâmpago: Ápice Único', E'Kakashi aparece embaixo de seu oponente e o chuta para o alto. Em seguida, aparece acima dele e acerta o Corte Relâmpago em seu peito, jogando-o para o chão de volta.', E'雷切・一尖らいきり・いっせん', E'Raikiri: Issen', E'Corte Relâmpago: Ápice Único', E'Cair da Espada Relâmpago', NULL, NULL, E'Curto alcance', NULL, NULL),
(459, E'Corte Relâmpago do Kamui', E'O Corte Relâmpago do Kamui é uma técnica utilizada por Kakashi através de seu Mangekyō Sharingan.', E'神威雷切かむいらいきり', E'Kamui Raikiri', E'Corte Relâmpago da Autoridade dos Deuses', E'Espada Relâmpago Kamui, Lâmina Relâmpago Kamui', E'Espada Relâmpago Divina', NULL, E'Curto alcance', E'Rank –', NULL),
(460, E'Corte Triplo do Sapo', E'Gamakichi reveste sua tantō com chakra e a balança contra o alvo, liberando três ondas crescentes e afiadas.', E'蝦蟇三斬がまさんざん', E'Gamasanzan', E'Corte Triplo do Sapo', E'Corte Triplo da Lâmina do Sapo', NULL, NULL, E'Curto alcance', NULL, NULL),
(461, E'Corte Yosaku', E'Usando seu machado gigante, Sabu ataca o oponente com um golpe lateral baixo.', E'与作切り', E'Yosakugiri', E'Corte da Participação de Trabalho', NULL, E'Corte do Lenhador', E'Talhada Yosaku', E'Curto alcance', NULL, NULL),
(462, E'Corte da Foice Desordenada', E'O usuário empunha sua lâmina e ataca o inimigo repetidas vezes, sem qualquer chance de defesa.', E'乱れ鎌斬みだれかみきり', E'Midare Kamakiri', E'Corte da Foice Desordenada', E'Corte da Foice Louca', NULL, NULL, E'Curto alcance', NULL, NULL),
(463, E'Corte da Kunai da Lâmina Relâmpago', E'O usuário carrega o seu chakra relâmpago em sua kunai, acrescentando poder de corte e perfuração. O usuário também pode lançar a kunai, fazendo com que a kunai seja capaz de perfurar através de múltiplas pedras grandes com facilidade, sem qualquer perda de dinamismo ou mudança na trajetória.', E'雷刃クナイ切らいじんクナイきり', E'Raijin Kunai Kiri', E'Corte da Kunai da Lâmina Relâmpago', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(464, E'Corte da Língua Lutadora', E'Ao amassar chakra do senjutsu, a longa língua de Fukasaku ganha maior solidez e nitidez. Em seguida, ele salta de sua boca com uma enorme velocidade e força, mesmo abrindo um buraco na rocha e através de tubos de metal. Com esse poder divisor de solo, o inimigo é cortado ao meio. A maneira que ambos o inimigo e o próprio solo são cortados em pedaços é uma reminiscência do ataque de uma grande espada.', E'舌戦斬ぜっせんざん', E'Zessenzan', E'Corte da Língua Lutadora', E'Batalha Cortante da Língua de Água', NULL, NULL, E'Todos os alcances', E'Rank –', NULL),
(465, E'Corte da Terra', E'Zabuza enfia sua Kubikiribōchō no chão abrindo uma enorme cratera, causando dano em inimigos enterrados ou sobre o local, em seguida, ele dá cotoveladas e soca o oponente. Para concluir, ele dá um poderoso golpe em seu oponente, que faz ele cair para baixo.', E'地刻じこく', E'Jikoku', E'Corte da Terra', E'Cortador da Terra', NULL, NULL, E'Curto alcance', NULL, NULL),
(466, E'Corte das Andorinhas Gêmeas', E'Tenten acerta repetidamente seu oponente com um par de tonfas, em seguida, ela ataca-o no ar com um bastão de três seções que ela separa para acertar um golpe final para o adversário cair rapidamente.', E'双燕刈', E'Sōenkai', E'Corte das Andorinhas Gêmeas', E'Andorinhas Gêmeas Cortantes', NULL, NULL, E'Curto alcance', NULL, NULL),
(467, E'Corte das Sombras', E'Sasuke infunde sua espada em chamas e desfere um corte em seu oponente, que explode em chamas negras. Essa técnica é poderosa o suficiente para quebrar as armas de chakra de Kinshiki.', E'斬影ざん\'えい', E'Zan\'ei', E'Corte das Sombras', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(468, E'Corte de Ar Celestial Tipo Zero', E'No Modo de Chakra do Nove-Caudas, Minato espalha diversas Kunai do Deus Voador do Trovão em torno do oponente cortando-o cada vez que ele deforma o espaço-tempo.', E'裂空天光斬空閃零式', E'Rekkū Tenkō Zankūsen Reishiki', E'Corte de Ar Celestial Tipo Zero', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(469, E'Corte de Água Indulgente', E'Com sua Kubikiribōchō, Suigetsu desfere uma série de cortes poderosos, capazes de facilmente dilacerar uma porta de ferro maciça.', E'水斬三昧みずきざんまい', E'Mizuki Zanmai', E'Corte de Água Indulgente', E'Suizan Zanmai', NULL, NULL, E'Médio alcance', NULL, NULL),
(470, E'Corte do Deus Voador do Trovão', E'Uma técnica que combina o uso de uma arma laminada e ninjutsu de espaço-tempo. O usuário se teletransporta para um alvo, com a arma na mão, para entregar instantaneamente um rápido e devastador corte a seu oponente. A velocidade pura desta técnica, é tal que mesmo um indivíduo que possui o Sharingan é incapaz de reagir a tempo.', E'飛雷神斬りひらいしんぎり', E'Hiraishingiri', E'Corte do Deus Voador do Trovão', E'Corte do Raijin Voador', E'Corte do Deus do Relâmpago', E'Fatia do Raijin Voador', E'Curto a Médio alcance', E'Rank B', NULL),
(471, E'Corte dos Céus', E'Zabuza aproxima se do seu oponente pulando ou correndo chuta seu oponente, e entrega uma série de cortes com sua Kubikiribōchō, terminando com um corte de baixo pra cima o partindo no meio.', E'天刻むてんきざむ', E'Tenkizamu', E'Corte dos Céus', E'Cortador do Céu', NULL, NULL, E'Curto alcance', NULL, NULL),
(472, E'Corte em Vários Pedaços', E'Trata-se de um movimento repetitivo de um espadachim rotacionando sobre seu próprio eixo usando uma grande espada.', E'微塵斬り', E'Mijingiri', E'Corte em Vários Pedaços', E'Corte Fino', NULL, NULL, E'Curto alcance', NULL, NULL),
(473, E'Costura pelas Sombras: Técnica da Grande Foice da Doninha', E'Shikamaru estende sua sombra para capturar e imobilizar o oponente e, em seguida, Temari balança seu Leque Dobradiço Gigante para limpar completamente o campo. Imobilizado pela sombra, o oponente é incapaz de escapar ou evitar à técnica de Temari.', E'影縫い・大カマイタチの術かげぬい・だいカマイタチの術', E'Kagenui: Dai Kamaitachi no Jutsu', E'Costura pelas Sombras: Técnica da Grande Foice da Doninha', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Shikamaru: Rato → Passaro'),
(474, E'Cotovelada', E'Depois de cobrir-se com seu Modo de Chakra de Liberação de Relâmpago, A empurra o oponente com seu cotovelo com uma força extrema. Esta técnica foi forte o suficiente para fazer Jūgo bater em uma parede, impossibilitando-o de continuar lutando.', E'重流暴', E'Erubō', E'Explosão do Estilo-Pesado', NULL, E'Cotovelada Rústica', NULL, E'Curto alcance', NULL, NULL),
(475, E'Crianças Problemáticas da Academia', E'Naruto, Konohamaru, e Iruka jogam suas Fūma Shuriken no oponente. Então, Naruto e Konohamaru realizam sua Técnica Sensual, fazendo Iruka voar no oponente, mandando-o para longe. Depois, Iruka bate na cabeça de ambos Konohamaru e Naruto.', E'忍者学校の問題児あかでみいのもんだいじ', E'Akademī no Mondaiji', E'Crianças Problemáticas da Academia', E'Criança Problemática da Academia Ninja', NULL, NULL, E'Todos os alcances', NULL, NULL),
(476, E'Criação de Chakra Comestível', NULL, NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(477, E'Criação do Renascimento', NULL, E'創造再生', E'Sōzō Saisei', E'Criação do Renascimento', NULL, E'Regeneração Divina', E'Arte Ninja: Regeneração Mitótica', NULL, E'Rank S', E'Tigre'),
(478, E'Cristal: Shuriken Hexagonal', E'Guren cria várias shuriken de cristal e lança-as no oponente. As shuriken são de seis lados com picos nos lados de cada lâmina, semelhante a flocos de neve. Como observado, Guren tem a capacidade de criar dezenas de uma vez, dando vantagem em seu combate direto, tornando seus ataques mais letais quando em contato com o corpo do inimigo.', E'結晶・六角手裏剣けっしょう・ろっかくしゅりけん', E'Kesshō: Rokkaku Shuriken', E'Cristal: Shuriken Hexagonal', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(479, E'Cristal: Shuriken Hexagonal Gigante', E'Guren forma uma imensa shuriken de cristal, que contêm o modelo de um floco de neve, sendo capaz de lança-la em vários adversários, cortando-os rapidamente. Infelizmente a resistência da técnica não foi suficiente para desfazer as defesas do Três-Caudas.', E'結晶・巨大六角手裏剣', E'Kesshō: Kyodai Rokkaku Shuriken', E'Liberação de Cristal: Shuriken Gigante de Seis Pontas', NULL, NULL, E'Shuriken Hexagonal de Cristal Gigante', E'Todos os alcances', NULL, E'Selo de Confronto'),
(480, E'Cristal: Shuriken Hexagonal: Dança Selvagem', E'O usuário lança uma grande quantidade de shuriken de cristal em seu oponente. Ela pode usar uma forma mais avançada do ataque, onde partículas de cristal a cercam e condensam em numerosas shuriken.', E'結晶・六角手裏剣・乱舞けっそう・ろっかくしゅりけん・らんぶ', E'Kesshō: Rokkaku Shuriken: Ranbu', E'Cristal: Shuriken Hexagonal: Dança Selvagem', NULL, NULL, E'Shuriken Hexagonal de Cristal: Dança Selvagem', E'Todos os alcances', NULL, E'Cobra → Cao → Carneiro → Cavalo → Rato → Macaco → Passaro → Javali → Carneiro'),
(481, E'Cristalização do Gelo', E'Essa é a técnica assinatura de Renga, com a qual ele é capaz de congelar as partículas de umidade no ar para atingir a forma cristalina do gelo.', E'氷の結晶こおりのけっしょう', E'Kōri no Kesshō', E'Cristalização do Gelo', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(482, E'Crueldade', E'O usuário faz uso do Kamui para se teletransportar em uma posição acima de seu alvo, desferindo um golpe para baixo, acertando o inimigo desprevenido. A fim de aumentar a letalidade da técnica, pode se fazer uso de armas laminadas como uma kunai.', E'無惨むざん', E'Muzan', E'Crueldade', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(483, E'Cuidado onde Pisa!', E'Kushina cria um selo no chão que, assim que o oponente pisar, invoca duas grandes frigideiras para caírem sobre sua cabeça.', E'足下注意ッ!あしもとちゅういつ', E'Ashimoto Chūitsu!', E'Cuidado onde Pisa!', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(484, E'Cuspe Pegajoso da Aranha', E'Sugerido pelo nome, Kidōmaru emite um fluxo de teia de aranha pegajosa de sua boca, Kidōmaru agarra um adversário por perto, permitindo-lhe a segurar-los.', E'蜘蛛粘吐くもねんと', E'Kumo Nento', E'Cuspe Pegajoso da Aranha', NULL, E'Vômito Viscoso da Aranha', NULL, E'Curto a Médio alcance', NULL, NULL),
(485, E'Cutiladas Necessárias', E'Kisame posiciona a Samehada para cima e corta o oponente várias vezes.', E'要削りようけずり', E'Yōkezuri', NULL, E'Cutiladas Necessárias', NULL, NULL, E'Curto alcance', NULL, NULL),
(486, E'Cúpula de Proteção de Pano', E'Maki tem demonstrado que ela é capaz de usar seu pano como meio de defesa, por envolvê-lo em torno dela e qualquer aliado próximo, podendo formar uma barreira protetora. O pano pode ficar em torno de um objeto próximo, como uma rocha, para melhor manter a fortificação. Se o pano for molhado através de uma técnica de água, é permitido que a cúpula possa se defender contra poderosos ataques baseados em calor.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(487, E'Cópia de Ninjutsu', E'Kakashi salta no ar e cria dois clones, que logo em seguida fazem selos de mão necessários simultaneamente, um dos clones utiliza a Liberação de Vento: Grande Destruição, o original utiliza a Liberação de Água: Técnica do Projétil do Dragão de Água e o último clone utiliza a Liberação de Fogo: Técnica da Grande Bola de Fogo, as técnicas se chocam causando uma enorme explosão massiva sobre o oponente.', E'コピー忍術コピーにんじゅつ', E'Kopī Ninjutsu', E'Cópia de Ninjutsu', E'Arte Ninja: Cópia de Ninjutsu', NULL, NULL, E'Todos os alcances', NULL, NULL),
(488, E'Córrego de Areia', E'O usuário reúne sua areia abaixo de seus adversários e faz com que eles se afastem-se dele, criando um córrego, sendo usado para incapacitar seus oponentes sem prejudicá-los ou para o usuário se locomover mais facilmente.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(489, E'Céu Esvoaçante', E'Utakata salta no ar, em direção ao oponente, e desfere alguns ataques com seu Cachimbo de Bolhas e com as mãos nuas, terminando a sequência com um chute giratório com ambos os pés, que envia o seu adversário direto ao chão.', E'翻天', E'Honten', E'Céu Esvoaçante', E'Planar', NULL, NULL, E'Curto alcance', NULL, NULL);