INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(490, E'Círculo de Areia', E'Gaara ataca seu oponente com uma pequena formação de areia semelhante a um braço, vindo de baixo. Em seguida, ele usa sua areia embaixo dos pés do oponente para levantá-lo. Então, ele atira vários projéteis de areia, e finaliza criando dois pilares de areia em volta do oponente, que giram simultaneamente, antes de explodirem jogando-o para trás.', NULL, NULL, NULL, E'Círculo de Areia', NULL, NULL, E'Médio alcance', NULL, NULL),
(491, E'Círculo do Grande Atropelamento', E'Quando transformado em Kokuō, Han vira-se, erguendo as patas dianteiras, e vem de volta por baixo esmagando seu oponente gravemente.', E'大踏円', E'Daitōen', E'Círculo do Grande Atropelamento', E'Grande Círculo Vibrador', NULL, NULL, E'Curto alcance', NULL, NULL),
(492, E'Dōjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(493, E'Dōjutsu: Deus do Sol', E'Itachi ativa seu Mangekyō Sharingan, fazendo o oponente sentir uma grande pressão abaixo dele. Após a pressão, uma esfera negra gigante engole o usuário e tudo a sua volta, deixando uma enorme cratera no chão.', E'瞳術・日神どうじゅつ・にっしん', E'Dōjutsu: Nisshin', E'Técnica Ocular: Deus do Sol', E'Jutsu Visual: O Deus do Sol', NULL, NULL, NULL, NULL, NULL),
(494, E'Dança Celestial da Neblina Branca', E'As mãos de Hinata ficam brancas, com um revestimento de chakra que flui através delas. Ela golpeia o inimigo, e finaliza reunindo ambas as mãos contra ele. Isso cria uma grande explosão que danifica e joga para trás o adversário.', E'白朧天舞はくろうてんぶ', E'Hakurō Tenbu', E'Dança Celestial da Neblina Branca', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(495, E'Dança Celestial do Deus Tempestuoso', E'Este é um estilo de luta comum de Momoshiki Ōtsutsuki após sua transformação, utilizando a habilidade de Kinshiki Ōtsutsuki em criar armas livremente de acordo com sua vontade e acumular chakra em sua mão para aprimorar a força de seus golpes.', NULL, NULL, NULL, E'Dança Celestial do Deus Tempestuoso', NULL, NULL, E'Curto alcance', NULL, NULL),
(496, E'Dança Desordenada da Palma Celestial', E'Neji agarra seu oponente, em seguida, girando-o para efetuar três golpes poderosos em suas costas, que o lança para longe.', E'天掌乱舞てんしょうらんぶ', E'Tenshō Ranbu', E'Dança Desordenada da Palma Celestial', E'Dança Selvagem das Palmas Celestiais', NULL, NULL, E'Curto alcance', NULL, NULL),
(497, E'Dança Desordenada do Leque', E'Trata-se de uma combinação rápida de socos, cotoveladas e pontapés executada por Temari usando seu Leque Dobradiço Gigante fechado, a fim de incapacitar ou ferir o adversário. Esta habilidade é extremamente útil contra vários inimigos, como clones das sombras, devido aos movimentos serem precisos, ainda que não seja o princípio da técnica.', E'乱れ扇舞みだれせんぶ', E'Midare Senbu', E'Dança Desordenada do Leque', E'Dança Maluca do Leque', NULL, NULL, E'Curto alcance', NULL, NULL),
(498, E'Dança Desordenada do Pontapé Celestial', E'O usuário chuta o joelho de seu oponente, em seguida, chuta seu rosto duas vezes em um rápido movimento, fazendo o oponente ir girando pro alto.', E'天蹴乱舞てんしゅうらんぶ', E'Tenshū Ranbu', E'Dança Desordenada do Pontapé Celestial', E'Dança Desordenada dos Pontapés Celestiais', NULL, NULL, E'Curto alcance', NULL, NULL),
(499, E'Dança Selvagem', E'O usuário manipula sua marionete para perfurar o oponente. A marionete então começa girando contra o adversário, dilacerando-o diversas vezes, e finaliza jogando uma kunai nele.', E'乱舞', E'Ranbu', E'Dança Selvagem', NULL, NULL, NULL, E'Curto Alcance', NULL, NULL),
(500, E'Dança Selvagem da Kunai', E'Rin desfere uma série de golpes de kunai em seu oponente, lança algumas kunai nele, afim de distraí-lo, e, quando ele menos espera, ela prepara uma kunai com papéis explosivos e lança contra ele, criando uma impactante explosão.', E'クナイ乱舞', E'Kunai Ranbu', E'Dança Selvagem da Kunai', E'Dança Louca da Kunai', NULL, NULL, E'Curto alcance', NULL, NULL),
(501, E'Dança Selvagem da Samehada', E'Kisame corta o inimigo arrastando sua Samehada no ombro dele, em seguida, corta-o, drenando seu chakra antes de esfaquear o inimigo e batê-lo no chão.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(502, E'Dança Selvagem da Shuriken Flamejante', E'Uma técnica de colaboração usada por Sasuke e Sakura. Sakura lança uma série de shuriken que são incendiadas por Sasuke, usando sua Técnica da Grande Bola de Fogo.', E'火炎手裏剣乱舞', E'Kaen Shuriken Ranbu', E'Dança Selvagem da Shuriken Flamejante', E'Dança Selvagem da Shuriken de Chamas', NULL, NULL, E'Curto alcance', NULL, NULL),
(503, E'Dança Selvagem de Ukon', E'Após se separar de Sakon, Ukon se aproxima de seu oponente e entrega um pontapé, fazendo o oponente ir para trás, onde, em seguida, Ukon salta para realizar um golpe fatal com suas garras, fazendo liberar um feixe de luz de coloração roxa.', NULL, NULL, E'Dança Selvagem de Ukon', E'Dança Louca de Ukon', NULL, NULL, E'Curto alcance', NULL, NULL),
(504, E'Dança Selvagem do Fluxo de Fogo', E'Trata-se de uma técnica que incorpora o uso simultâneo de várias habilidades do taijutsu. O usuário inicia com um chute, seguido por um soco e outro chute.', E'迸火乱舞ほうからんぶ', E'Hōka Ranbu', E'Dança Selvagem do Fluxo de Fogo', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(505, E'Dança da Camélia', E'A segunda das cinco danças de Kimimaro, que ele realiza modificando um de seus ossos do braço para criar uma curta espada de óssea e a empunha. Ele então apunhala caoticamente e de forma contínua, fazendo com que os olhos dos oponentes fiquem para trás. A velocidade se assemelha a imagens residuais, a mão com a espada é visivelmente projetada repetidas vezes. E ainda assim, o movimento é irregular, tornando difícil de prever. Com cada investida a trilha da espada é capaz de mudar. O ataque vem de ângulos inesperados, provocando reação de defesa delicada de uma pessoa a sair da guarda. Embora se possa ter grandes reflexos e habilidade de movimento, é quase impossível manter-se esquivando do ataque feroz até que ele pare. Assim, se mostrar uma abertura, um golpe final será desferido por uma facada em um instante.', E'椿の舞つばきのまい', E'Tsubaki no Mai', E'Dança da Camélia', NULL, E'Dança das Camélias', E'Dança Camélia', E'Curto alcance', E'Rank –', NULL),
(506, E'Dança da Clematite: Flor', E'Esta técnica é a segunda metade da quarta dança de Kimimaro. Para obter a arma mais dura absolutamente forte, uma quantidade considerável de chakra é necessário. Aprimorado com o mais alto grau de solidez máxima, devido à densa compressão, a arma de osso é extremamente grande. Desde o seu poder destrutivo não tem sentido se o golpe não se conectar com o oponente, Kimimaro primeiro prende o oponente com uma \'vinha\'.', E'鉄線化の舞・花てっせん か の まい ・ はな', E'Tessenka no Mai: Hana', E'Dança da Clematite: Flor', NULL, E'Dança da Clêmatis: Flor', E'Dança Clematite: Flor', E'Curto alcance', NULL, NULL),
(507, E'Dança da Clematite: Vinha', E'A primeira metade da quarta dança de Kimimaro.', E'鉄線化の舞・蔓てっせん か の まい ・ つる', E'Tessenka no Mai: Tsuru', E'Dança da Clematite: Vinha', E'Videira, Trepadeira', E'Dança da Clêmatis: Trepadeira', NULL, E'Curto alcance', E'Rank –', NULL),
(508, E'Dança da Espada', E'Essa habilidade refere-se à habilidade de Orochimaru com sua Espada de Kusanagi.', E'剣の舞つるぎのまい', E'Tsurugi no Mai', E'Dança da Espada', E'Dança da Lâmina', NULL, NULL, E'Curto alcance', NULL, NULL),
(509, E'Dança da Kusanagi', E'Sasuke ataca o oponente com cortes horizontais de sua espada. Depois de alguns segundos, quando ele embainha sua espada, o corte se expande como uma rajada de vento.', E'草薙の舞', E'Kusanagi no Mai', E'Dança da Ceifadora-de-Grama', E'Dança de Kusanagi', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(510, E'Dança da Lua Crescente', E'O usuário cria dois clones da sombra antes de continuar a atacar o inimigo, com os clones atacando a esquerda e a direita e o usuário atacando por cima. O ataque ocorre em três frentes simultaneamente, é quase impossível pegar o adversário de surpresa. Além disso, os poderosos ataques de espadas são tão graves que recebendo um golpe, mesmo que de apenas um entre os três, resultará inevitavelmente em uma ferida fatal.', E'木ノ葉流三日月の舞', E'Mikazuki no Mai', E'Dança da Lua Crescente', NULL, NULL, E'Estilo Folha: Dança da Lua Crescente', E'Curto alcance', E'Rank A', NULL),
(511, E'Dança da Lâmina da Foice: Lâmina Forte', E'Hanzō gira sua kusarigama e desfere uma série de cortes em seu oponente, terminando por saltar para a frente, pousando um corte poderoso que joga o seu oponente para trás.', E'刃鎌の舞・剛力の刃', E'Hagama no Mai: Gōriki no Yaiba', E'Dança da Lâmina da Foice: Lâmina Forte', E'Dança da Foice: Lâmina Severa', NULL, NULL, E'Curto alcance', NULL, NULL),
(512, E'Dança da Lâmina da Foice: Lâmina Rotativa Vertical', E'Hanzō salta no ar e desfere vários golpes rotativos com o seu kusarigama, jogando seu oponente para baixo no processo.', E'刃鎌の舞・縦旋の刃', E'Hagama no Mai: Jūsen no Yaiba', E'Dança da Lâmina da Foice: Lâmina Rotativa Vertical', E'Dança da Foice: Lâmina Vertical', NULL, NULL, E'Curto alcance', NULL, NULL),
(513, E'Dança da Lâmina da Foice: Lâmina da Elevação Nascente', E'Hanzō desfere uma série de cortes laterais com a sua Kusarigama, e em seguida, utiliza-a para trazer o seu oponente perto dele, e gira-a para cima, cortando o seu oponente e jogando-o no ar.', E'刃鎌の舞・昇揚の刃', E'Hagama no Mai: Shōyō no Yaiba', E'Dança da Lâmina da Foice: Lâmina da Elevação Nascente', E'Dança da Foice: Lâmina Nascente', NULL, NULL, E'Curto alcance', NULL, NULL),
(514, E'Dança da Lâmina de Foice: Queda Descendente da Lâmina', E'Ficando com alguma distância entre ele e seu oponente, o usuário balança sua kusarigama alto no ar, e leva-a para baixo em seu oponente. No entanto, a técnica mostrou-se bloqueável. No mangá, Nagato, através do corpo de Yahiko, foi capaz de bloquear essa técnica com seu Shinra Tensei e Mifune foi capaz de bloqueá-la com sua Kurosawa.', E'刃鎌の舞・落降の刃', E'Hagama no Mai: Rakukō no Yaiba', E'Dança da Lâmina de Foice: Queda Descendente da Lâmina', E'Dança da Foice: Lâmina Mergulhante', NULL, NULL, E'Curto alcance', NULL, NULL),
(515, E'Dança da Rotação da Foice', E'Hidan usa sua Foice de Lâmina Tripla para defender-se de ataques diretos. A fim de ser ofensivo, Hidan pode combinar com um ataque pesado contra o oponente.', E'怪転鎌舞', E'Kaiten Renbu', E'Dança da Rotação da Foice', E'Dança do Rodopio da Foice', NULL, NULL, E'Curto alcance', NULL, NULL),
(516, E'Dança das Mudas de Samambaia', E'A última das cinco danças de Kimimaro.', E'早蕨の舞さわらびのまい', E'Sawarabi no Mai', E'Dança das Mudas de Samambaia', NULL, E'Dança do Broto da Samambaia', NULL, E'Todos os alcances', E'Rank –', NULL),
(517, E'Dança das Rajadas de Vento', E'Usando a Liberação de Vento para criar uma corrente de ar em volta de si, a Dançarina dos Ventos gira com seu Leque Dobradiço Gigante desferindo vários cortes em seu oponente.', E'突風の舞とっぷうのまい', E'Toppū no Mai', E'Dança das Rajadas de Vento', E'Dança do Surto', NULL, NULL, NULL, NULL, NULL),
(518, E'Dança do Avalanche', E'Haku entrega uma série de chutes, socos, e senbon em seu oponente, em seguida, Haku salta pro ar e aparece por trás executando uma espécie de alerta e salta novamente para começar a dispersar mais senbon, atingindo seu oponente novamente.', E'雪崩舞', E'Nadare Mai', E'Dança do Avalanche', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(519, E'Dança do Círculo Selvagem', E'O usuário gira com sua tantō, atingindo o alvo diversas vezes.', E'円陣乱舞えんじらんぶ', E'Enjin Ranbu', E'Dança do Círculo Selvagem', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(520, E'Dança do Dragão Ascendente', E'Tenten salta e, em seguida, gira em alta velocidade, atirando várias armas em seu oponente no processo.', E'昇龍の舞しょうりゅうのまい', E'Shōryū no Mai', E'Dança do Dragão Ascendente', E'Dança do Dragão Ascendente', NULL, NULL, E'Todos os alcances', NULL, NULL),
(521, E'Dança do Genji', E'Kimimaro acerta o oponente com suas lâminas ósseas, e atira numerosas balas de dedos sobre ele, e finaliza com o oponente saltando no ar e libertando numerosos ossos de suas costas.', E'源氏の舞', E'Genji no Mai', E'Dança do Genji', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(522, E'Dança do Lariço', E'A Dança do Lariço é a terceira das cinco danças de Kimimaro.', E'唐松の舞からまつのまい', E'Karamatsu no Mai', E'Dança do Lariço', E'Dança da Conífera', E'Dança dos Alerces', NULL, E'Curto alcance', E'Rank –', NULL),
(523, E'Dança do Salgueiro', E'A primeira das cinco danças de Kimimaro. Para executar esta dança, Kimimaro cria várias lâminas de ossos longos no corpo para usar como armas. Não toma qualquer forma para a Dança do Salgueiro. Assim como um salgueiro que é agitado pelo vento, os ataques adversários são simplesmente defendidos e combatidos. Atípico de um salgueiro, perfura com os ossos endurecidos e danifica o corpo dos adversários. Embora ele usa principalmente duas lâminas crescentes das palmas das suas mãos, ele também usa vários ossos secundários crescidos de cotovelos, joelhos e ombros. Esta forma de dança é extremamente acrobáticas, utilizando giros, arrancadas e longas barras amplas para tornar os ataques mais eficazes e para evitar contra-ataques dos adversários. Kimimaro também pode estender seus ossos para atacar seus oponentes sem ter que ajustar seu próprio movimento.', E'柳の舞やなぎのまい', E'Yanagi no Mai', E'Dança do Salgueiro', NULL, E'Dança do Salgueiro', NULL, E'Curto alcance', E'Rank –', NULL),
(524, E'Dança do Shikigami', E'A Dança do Shikigami é uma técnica exclusiva feita com o esforço de Konan, consistindo em uma união das artimanhas em origami com sua maestria em ninjutsu.', E'式紙の舞しきがみのまい', E'Shikigami no Mai', E'Dança da Fórmula do Papel', E'Dança Shikigami', NULL, NULL, E'Todos os alcances', E'Rank B', E'Carneiro'),
(525, E'Dança do Shikigami: Explosão', E'Depois de incorporar papéis explosivos na Dança do Shikigami, Konan forma um grande vórtice em torno do seu oponente, detonando-o juntamente com os vários explosivos anexados ao seu corpo.', E'式紙の舞・軌爆しきがみのまい・きばく', E'Shikigami no Mai: Kibaku', E'Dança da Fórmula do Papel: Explosão', E'Dança do Shikigami: Explosão', NULL, NULL, E'Todos os alcances', NULL, NULL),
(526, E'Dança do Shikigami: Punição', E'Depois de usar a Dança do Shikigami, o usuário cria um redemoinho de folhas de papel. O usuário então aprisiona o adversário no redemoinho e atira uma lança papel, perfurando-o.', E'式紙の舞・罰', E'Shikigami no Mai: Bachi', E'Dança da Fórmula do Papel: Punição', E'Dança do Shikigami: Punição', NULL, NULL, E'Todos os alcances', NULL, NULL),
(527, E'Dança do Shikigami: Tempestade', E'Konan joga rosas de papel ao redor do(s) inimigo(s), que explodem em pétalas e criam um tornado gigante, consumindo-os.', E'式紙の舞・嵐', E'Shikigami no Mai: Arashi', E'Dança da Fórmula de Papel: Tempestade', E'Dança Shikigami: Tempestade', NULL, NULL, NULL, NULL, NULL),
(528, E'Dança dos Falsos Cutelos', E'Kimimaro retira duas espadas de osso, e depois vai em direção do oponente, cortando-o duas vezes. Ele então desencadeia uma onda de golpes, derrubando o oponente para trás. Ele, então, corre atrás do oponente, desencadeando diversos picos de ossos por todo o corpo empalando o oponente.', E'八重葎の舞', E'Yaemugura no Mai', E'Dança dos Falsos Cutelos', E'Dança do Galium Spurium', NULL, NULL, E'Curto alcance', NULL, NULL),
(529, E'Decapitação Frontal do Estilo Nuvem', E'O usuário balança sua espada em um único movimento de corte rápido e poderoso para atacar um inimigo à sua frente.', E'雲流表斬り', E'Kumoryū Omotegiri', E'Decapitação Frontal do Estilo Nuvem', NULL, E'Estilo de Esgrima da Nuvem: Corte Frontal', NULL, E'Curto alcance', NULL, NULL),
(530, E'Decapitação Oblíqua', E'Darui repetidamente corta seu oponente com sua Espada Cutelo, finalizando com um poderoso ataque que joga seu oponente para trás.', E'斜影斬り', E'Shaeigiri', E'Decapitação Oblíqua', E'Corte Oblíquo', NULL, NULL, E'Curto alcance', NULL, NULL),
(531, E'Decapitação Reversa do Estilo Nuvem', E'O usuário faz uma rotação poderosa, gerando o impulso necessário para cortar qualquer adversário por trás dele. O usuário também pode utilizar este movimento giratório para enganar um inimigo que está na frente dele. O inimigo se engana pensando que o usuário irá atacá-lo, mas o usuário ataca os alvos atrás dele.', E'雲流裏斬り', E'Kumoryū Uragiri', E'Decapitação Reversa do Estilo Nuvem', NULL, E'Estilo de Esgrima da Nuvem: Corte Pelo Verso', NULL, E'Curto alcance', NULL, NULL),
(532, E'Decapitação da Dança de Mil Flashes', E'Haku corta seu oponente por mil vezes em várias direções diferentes com várias senbon muito afiadas, deixando o oponente com muitos cortes e com as senbon perfuradas em seu corpo, incapacitando-o de efetuar um ataque após a finalização de seu combo.', E'千閃斬舞', E'Sensen Zanbu', E'Decapitação da Dança de Mil Flashes', E'Dança Cortante de Mil Flashes', NULL, NULL, E'Curto alcance', NULL, NULL),
(533, E'Decapitação da Decepção do Estilo Nuvem', E'Usando a Técnica de Substituição do Corpo para fingir ser atacado, o usuário tira proveito do choque momentâneo de seu oponente, e carrega para a frente com um corte lateral de espada.', E'雲流騙し斬りくもりゅうだましぎり', E'Kumoryū Damashigiri', E'Decapitação da Decepção do Estilo Nuvem', NULL, NULL, E'Estilo Nuvem: Fatia da Decepção', E'Curto alcance', NULL, NULL),
(534, E'Decapitação da Espada do Sapo', E'Uma técnica que apenas sapos podem usar, empregando os seus recursos físicos. Seus quadris e pernas fortes dão luz a um salto extraordinariamente preciso, com o qual ele corta o seu alvo com a Espada do Sapo. Uma vez que o intervalo é coberto em um piscar de olhos, o oponente não pode nem mesmo tentar escapar. Gamabunta usa-o junto com sua enorme dosu (espada Yakuza). Apesar de normalmente ser usado de forma ofensiva, Gamakichi e Gamabunta utilizaram a técnica de forma defensiva para desviar projéteis.', E'蝦蟇ドス斬', E'Gamadosuzan', E'Decapitação da Espada do Sapo', NULL, E'Decapitação da Lâmina Batráquia', E'Corte da Lâmina do Sapo', E'Médio a Longo alcance', NULL, NULL),
(535, E'Decapitação da Lua Crescente do Estilo Nuvem', E'O usuário movimenta a sua espada em um único e grande arco em forma de lua crescente, que ocorre com uma velocidade feroz de tal forma que a técnica pode ser empregada em situações que se tenha pouco tempo para reagir. O golpe pode ser usado para acertar múltiplos oponentes simultaneamente.', E'雲流三日月斬りくもりゅうみかずきぎり', E'Kumoryū Mikazukigiri', E'Decapitação da Lua Crescente do Estilo Nuvem', NULL, E'Estilo de Esgrima da Nuvem: Corte da Lua Crescente', NULL, E'Curto alcance', NULL, NULL),
(536, E'Decapitação da Névoa Reversa', E'Depois de criar vários clones da sombra que cercam o inimigo e ataca-o simultaneamente forçando-o a saltar para o ar, o verdadeiro usuário, então, ataca-o por cima.', E'裏霞斬り', E'Urakasumigiri', E'Decapitação da Névoa Oculta', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(537, E'Decapitação da Respiração Instantânea', NULL, E'瞬息斬しゅんそくざん', E'Shunsokuzan', E'Decapitação da Respiração Instantânea', E'Corte da Respiração Instantânea', NULL, NULL, E'Curto alcance', NULL, NULL),
(538, E'Decapitação de Chamas do Estilo Nuvem', E'O usuário primeiro impregna a sua lâmina com fogo, antes de cortar na direção do inimigo desejado, resultando na criação de grandes arcos de chama intensa que se seguem a trajetória do movimento anterior. Um usuário hábil desta técnica, tal como Atsui, é capaz de cercar sua área imediata com uma parede circular de chamas, incinerando alvos múltiplos, mesmo se eles se aproximam de ângulos numerosos simultaneamente.', E'雲流火炎斬りくもりゅうかえんぎり', E'Kumoryū Kaengiri', E'Decapitação de Chamas do Estilo Nuvem', NULL, E'Estilo de Esgrima da Nuvem: Corta-Chamas', NULL, E'Médio alcance', NULL, NULL),
(539, E'Decapitação de Iai', E'Aderindo aos princípios de Iaidō, o usuário executa um golpe rápido e poderoso, com sua espada para cortar o alvo pretendido, antes de usar imediatamente a lâmina depois. Quando usado por Mifune, a técnica era forte o suficiente para cortar a grande cabeça da salamandra de Hanzō, Ibuse.', E'居合斬り', E'Iaigiri', E'Decapitação Iai', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(540, E'Defesa Absoluta Rígida Final: Escudo de Shukaku', E'Esta técnica defensiva é usada por Gaara para criar uma réplica do brinquedo de Shukaku usando um chapéu de aba larga rural, que é aproximadamente três vezes o tamanho de Gaara. Porque a réplica é formada sob extrema pressão e é construída dos mais fortes minerais que Gaara poderia derivar da terra, é praticamente indestrutível, tendo ainda de ser penetrado por qualquer força. Como o nome indica, esta é a mais forte defesa de Gaara.', E'最硬絶対防御・守鶴の盾さいこうぜったいぼうぎょ・しゅかくのたて', E'Saikō Zettai Bōgyo: Shukaku no Tate', E'Defesa Absoluta Rígida Final: Escudo de Shukaku', NULL, E'Defesa Absoluta: Escudo de Shukaku', E'Suprema Defesa: Escudo de Shukaku', E'Curto alcance', NULL, NULL),
(541, E'Deflexão', E'É um movimento que permite defletir ataques diretos ao empurrar o braço de seu oponente para o lado e desviar a rota do ataque, podendo inclusive desferir um contra-ataque. Com isso é possível se defender de dois ataques simultaneamente e aplicar o uso do Punho Gentil.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(542, E'Denominação de Oito Trigramas Onda de Palmas', E'Hinata usa um Punho Gentil com as duas mãos e ataca seu oponente várias vezes de todos os ângulos, antes de correr até ele e liberar o chakra de suas mãos, golpeando o oponente e drenando seu chakra.', E'八卦宗掌波はっけしゅうしょうは', E'Hakkeshū Shōha', E'Denominação de Oito Trigramas Onda de Palmas', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(543, E'Derradeira Dispersão de Veneno', E'Saiken, ou Utakata Totalmente transformado na besta, se espreme, liberando um poderoso gás venenoso em torno de si, contaminando todos à sua volta.', E'極毒散', E'Kyokudokusan', E'Derradeira Dispersão de Veneno', E'Ultra Dispersão de Veneno', NULL, NULL, E'Curto alcance', NULL, NULL),
(544, E'Desencadeamento da Hiramekarei', E'O Desencadeamento da Hikamerarei é uma habilidade que permite armazenar chakra na Hiramekarei a fim de aumentar seu poder ofensivo.', E'ヒラメカレイ解放', E'Hiramekarei Kaihō', E'Liberação da Hiramekarei', NULL, E'Liberar Hiramekarei', NULL, NULL, NULL, NULL),
(545, E'Desenho de Imitação da Super Besta', E'Esta é uma técnica que permite ao usuário animar os desenhos feitos a partir do método de pintura de Konoha de forma como se a tinta ganhasse vida e faz com que os desenhos ajam conforme as vontades do usuário, tornando-a extremamente versátil.', E'超獣偽画ちょうじゅうぎが', E'Chōjū Giga', E'Desenho de Imitação da Super Besta', E'Arte Ninja: Pergaminho das Réplicas de Besta', E'Desenho das Superferas', E'Arte Ninja: Pergaminho das Super Bestas', E'Todos os alcances', E'Rank B', E'Ativar: Selo de Confronto,Dissipar: Selo de Confronto ou Cobra[2]'),
(546, E'Desenho de Imitação da Super Besta: Esboço Branco', E'Sai utiliza a técnica Desenho de Imitação da Super Besta para criar três leões e um grande falcão. Os leões atingem o oponente rapidamente e lançam-o para cima, onde o falcão paira e dispara uma rajada de penas em forma de navalhas super afiadas de tinta no oponente, cobrindo-o e jogando-o de volta para o chão.', E'超獣偽画・白描ちょうじゅぎが・はくびょう', E'Chōjū Giga: Hakubyō', E'Desenho de Imitação da Super Besta: Esboço Branco', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(547, E'Desenho de Imitação da Super Besta: Uma Centena de Demônios Vagantes na Noite', E'Sai usa sua técnica de desenhos para criar centenas de leões que caem sobre o oponente como uma chuva de desenhos. Em seguida, Sai produz um dragão gigante, causando sérios danos em seu oponente e deixando-o atordoado por alguns segundos.', E'超獣偽画・百鬼夜行', E'Chōjū Giga: Hyakki Yakō', E'Desenho de Imitação da Super Besta: Uma Centena de Demônios Vagantes na Noite', E'Centenas de Demônios nas Paradas da Noite', NULL, NULL, E'Longo alcance', NULL, NULL),
(548, E'Desenho de Imitação do Super Deus', E'O Desenho de Imitação do Super Deus é uma versão melhorada do Desenho de Imitação da Super Besta.', E'超神偽画ちょうしんぎが', E'Chōshin Giga', E'Desenho de Imitação do Super Deus', E'Arte Ninja: Pergaminho da Super Besta: Punho Alfa Ômega', NULL, NULL, E'Curto a Médio alcance', E'Rank B', NULL),
(549, E'Desenrolar da Teia de Aranha', E'Uma teia que captura e contém todos os inimigos que atacam de frente. Kidōmaru pega seu inimigo desprevenido por liberá-la enquanto estiver no ar. Por rapidamente espalhar a bola da linha com seus seis \'pernas\' da teia abrange uma grande área. Os fios imbuídos de chakra não podem ser cortados até mesmo por uma adaga. As linhas pegajosas e elásticas são tão fortes que até mesmo um elefante puxando em cada extremidade não quebra-as. O inimigo é incapaz de mover um músculo, quanto mais voltar a lutar. Uma vez capturado, o seu destino está nas mãos de Kidōmaru. Além disso, de acordo com Kidōmaru, ele pode conter mais de cinco toneladas. No entanto, uma vez que a teia é trabalhada a partir do chakra, ela pode ser destruída por técnicas refinadas imbuídas de chakra, como o Punho Gentil, e as chamas intensas do Amaterasu.', E'蜘蛛巣開', E'Kumosōkai', E'Desenrolar da Teia de Aranha', NULL, E'Morada das Aranhas', E'Arte Ninja: Espiral de Teia de Aranha', E'Curto a Médio alcance', NULL, NULL),
(550, E'Desenrolar da Teia de Areia', E'Gaara cria uma grande teia de areia que ele atira contra seus inimigos para capturá-los. A teia também serve como suporte para seus aliados caminharem em cima.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(551, E'Deserto: Mão', E'Gaara usa sua areia para criar mãos, que podem ser usadas para vários fins, incluindo a trituração, captura e contenção de inimigos e outros alvos. O brilho desta técnica é a força e o tamanho ou o volume pelo qual Gaara cria; com seu tamanho que rivaliza com a de uma besta com cauda. Uma vez que os braços capturam ou bloqueiam seu alvo, é quase impossível de se libertar, e deve recorrer a outros métodos. Esta técnica foi forte o suficiente para parar completamente as correntes de chakra da Estátua Demoníaca do Caminho Exterior.', E'砂漠・叛奴', E'Sabaku: Hando', E'Deserto: Rebelião do Escravo', NULL, E'Mãos de Areia', NULL, NULL, NULL, NULL),
(552, E'Deslizamento da Grande Bola Explosiva', E'Konohamaru utiliza um pergaminho para invocar uma grande bola coberta de papéis explosivos, a qual ele chuta, e em seguida, lança-a até seu oponente, explodindo ao entrar em contato.', E'起爆大玉転がしきばくおうだまくろがし', E'Kibaku Ōdama Korogashi', E'Deslizamento da Grande Bola Explosiva', E'Deslizamento da Esfera de Bomba Gigante', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(553, E'Deslizamento do Modo da Cobra', E'Orochimaru transforma a metade inferior de seu corpo em uma cauda de serpente, o que aumenta a sua velocidade e agilidade. Uma versão alternativa é Orochimaru derramar sua pele, revelando um novo corpo que já tem uma cauda de serpente.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(554, E'Deslocamento Espaço-Temporal', E'Trata-se de um ninjutsu espaço-tempo exclusivo de Kuroma Tatsushiro.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(555, E'Destruição Bruta', E'Jirōbō acerta uma cotovelada no rosto do oponente, deixando-o vulnerável, então ele o segura e arremessa-o no chão, causando um impacto de pequeno porte. Após isso, ele agarra o oponente pelo o rosto e o arrasta por uma curta distância, até que finalmente finaliza o jogando para longe.', NULL, NULL, NULL, E'Destruição Bruta', NULL, NULL, E'Médio alcance', NULL, NULL),
(556, E'Destruição de Bombas Desreguladas', E'O usuário expele balões de chiclete com bombas em direção ao seu alvo, que explodem quando rompidas. A explosão de uma pequena esfera é poderosa o suficiente para deixar um alvo inconsciente com o impacto. Essa técnica também é útil para manter os oponentes à distância, uma vez que quando usados em grande quantidade, os balões explosivos impedem que eles se aproximem do usuário.', E'破不流爆弾はふりゅうばくだん', E'Hafuryū Bakudan', E'Destruição de Bombas Desreguladas', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(557, E'Destruição do Conto de Fadas', E'Este é uma konbijutsu onde Naruto peida e Sakura se enfurece (chamando sua Sakura Interior), que causa grande dano ao adversário e também o envenena por causa do peido de Naruto.', E'メルヘン崩し', E'Meruhen Kuzushi', E'Destruição do Conto de Fadas', E'Evasão do Conto de Fadas', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(558, E'Desvio da Pétala: Corda de Chakra', E'Ino corre para a frente do adversário e desaparece em uma cortina de pétalas roxas. Ela aparece atrás do adversário e concentra seu chakra em uma corda de pétalas, então atinge o adversário com um soco poderoso.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(559, E'Desvio da Pétala: Fogos de Artifício da Flor', E'Ino corre para a frente do adversário e desaparece em uma cortina de pétalas roxas. Ela aparece atrás do adversário e concentra seu chakra em uma corda de pétalas, em seguida, ela chuta o adversário e joga uma kunai anexada a um papel explosivo nele.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(560, E'Detonador Supremo', E'Em sua forma Mecha-Kurama, Mecha-Naruto dispara um raio roxo de eletricidade.', E'ナルティメットブラスター', E'Narutimetto Burasutā', NULL, E'Detonador Supremo', NULL, NULL, E'Longo alcance', NULL, NULL),
(561, E'Deus Demônio', E'Kaguya libera uma enxurrada de lanças negras que se ramificam de forma irregular, cobrindo uma vasta área, o que torna a fuga quase impossível. Caso o oponente consiga escapar das agulhas, Kaguya ainda é capaz de manifestar mãos negras para golpeá-los.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(562, E'Deus Dragão da Folha', E'Esta é a melhor técnica de taijutsu criada pelo lendário mestre de taijutsu, Chen. É iniciada por um pontapé giratório para cima no ponto em que ele rapidamente se torna um tornado. Formando um dragão com longos bigodes, ele ataca o alvo com o objetivo de engoli-los, no momento em que o alvo está dentro dele, ele é cortado pelos ventos nítidos no interior do ataque. Após o início do ataque, o utilizador permanece protegido no interior na base da furacão, pois a superfície exterior do dragão é suficientemente sólida para que uma pessoa bata e seja jogada para longe, fazendo com que a única maneira de entrar na técnica, é sendo engolido.', E'木ノ葉龍神このはりゅうじん', E'Konoha Ryūjin', E'Dragão Divino da Folha da Árvore', NULL, NULL, E'Dragão Divino da Folha', E'Curto a Médio alcance', NULL, NULL),
(563, E'Deus Maligno', E'Kaguya embrulha o corpo de seu oponente com o Zetsu Negro, que o esmaga até explodir.', E'禍津日神', E'Magatsuhi Kami', E'Deus Maligno', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(564, E'Deus Voador do Trovão — Segundo Passo', NULL, E'飛雷神　ニの段', E'Hiraishin — Ni no Dan', E'Deus Voador do Trovão — Segundo Passo', E'Raijin Voador: Segundo Passo', E'Técnica do Deus do Relâmpago Segundo Estágio', E'Raijin Voador, Nível 2', E'Todos os alcances', E'Rank A', NULL),
(565, E'Deus Voador do Trovão: Flash Furacão do Espaço-Tempo Etapas Sequenciais: Estilo Zero', E'Esta é uma combinação de técnicas que compõem uma estratégia utilizada por Minato Namikaze.', E'飛雷神・時空疾風閃光連の段・零式', E'Hiraishin: Jikū Shippū Senkō Ren no Dan: Zero Shiki', E'Deus Voador do Trovão: Flash Furacão do Espaço-Tempo Etapas Sequenciais: Estilo Zero', E'Raijin Voador: Jiku Shippu Senko Rennodan Zeroshiki', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(566, E'Deus Voador do Trovão: Trovão Guiado', E'Esta é uma técnica de barreira de espaço–tempo (時空間結界, jikūkan kekkai).', E'飛雷神・導雷', E'Hiraishin: Dōrai', E'Deus Voador do Trovão: Trovão Guiado', NULL, NULL, E'Barreira de Teleportação', E'Todos os alcances', NULL, E'Cobra → Cavalo → Rato → Carneiro → Selo Especifico da Técnica'),
(567, E'Deus: Natividade de um Mundo de Árvores', E'Após o Tsukuyomi Infinito ser ativado, o usuário manipula as raízes tanto da Árvore Divina ou da forma de árvore do Dez-Caudas, espalhando elas ao redor de todo o mundo para envolver aqueles que estão presos dentro do genjutsu. Por enganarem todos os seres vivos e conectá-los à árvore, semelhante a casulos, isso vai manter seus corpos agora adormecidos vivos por toda a eternidade. Ao mesmo tempo, este assimila cada vítima da Árvore Divina, reagrupando o chakra em um único local, para brotar um novo fruto.', E'神・樹界降誕しん・じゅかい こうたん', E'Shin: Jukai Kōtan', E'Deus: Natividade de um Mundo de Árvores', E'Nascimento da Floresta Divina', E'Nascimento da Floresta Divina', NULL, E'Todos os alcances', E'Rank –', E'Ativar: Cobra,Liberar: Rato ou Juntar as Maos[2][Nota 1]'),
(568, E'Deus da Estrela de Papel Ardente', E'Através do Caminho Deva, Nagato aprisiona seu oponente com o Chibaku Tensei. Uma vez que a esfera está completa, Konan abraça com suas asas de papel, cobrindo-a de inúmeros papéis explosivos. Ela, então, envia um único pássaro de origami que desencadeia as explosões.', E'神の火紙星', E'Kami no Hi-shi Hoshi', E'Deus da Estrela de Papel Ardente', E'Marte de Papel Divino', NULL, NULL, E'Todos os alcances', NULL, NULL),
(569, E'Deus das Chamas', E'Primeiro o usuário lança uma técnica de vento contra o inimigo, logo então ele a incendeia, criando um turbilhão de chamas que se torna ainda mais mortal.', NULL, NULL, NULL, E'Deus das Chamas', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(570, E'Deus das Marés', E'Após imbuir a ponta de seus dedos com chakra, Kinshiki os crava no chão e propaga toda sua energia através dele, provocando uma onda de choque que repele todos nas proximidades, bem como um tremor que destrói tudo nos arredores. Com isso ele foi capaz de demolir toda uma arena com extrema facilidade.', NULL, NULL, NULL, E'Deus das Marés', NULL, NULL, E'Todos os alcances', NULL, NULL),
(571, E'Deus do Sol', E'Kaguya manipula o terreno de sua dimensão de lava para provocar uma série de erupções de lava que se erguem a partir do chão para queimar seu oponente.', E'今日様こんにちさま', E'Konnichisama', E'Deus do Sol', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(572, E'Deutzia', E'Kimimaro ataca seu oponente com uma de suas espadas de ossos, joga ele para trás de si e oferece uma segunda pancada forte de espada de ossos, jogando o oponente para longe.', E'空木', E'Utsugi', E'Deutzia', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(573, E'Deutzia (Ultimate Ninja 3)', E'Kimimaro ataca seu oponente com uma de suas espadas de ossos, joga ele para trás de si e oferece uma segunda pancada forte de espada de ossos, jogando o oponente para longe.', E'空木', E'Utsugi', E'Deutzia', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(574, E'Deutzia (Ultimate Ninja Storm)', E'Kimimaro golpeia o oponente sucessivas vezes com sua espada óssea, em seguida cria picos de ossos em seu joelho e em seus pulsos para realizar um golpe mortal. Desgastado, o oponente é derrotado por um chute forte do qual o derruba para longe.', E'空木', E'Utsugi', E'Deutzia', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(575, E'Dilaceração do Castelo', E'Zabuza chuta seu oponente e proporciona um corte fatal com sua Kubikiribōchō, após cortá-lo, ele chuta o oponente novamente e termina com uma facada frontal, jogando seu oponente para trás ao retirar a espada do corpo do oponente.', E'破城', E'Hajō', E'Dilaceração do Castelo', E'Muralha Esmagadora', NULL, NULL, E'Curto alcance', NULL, NULL),
(576, E'Disparo de Bolhas em Massa', E'Utakata libera várias bolhas de sabão, que cercam o inimigo, evitando que ele escape na próxima etapa do processo. Em seguida, Utakata entra em sua transformação completa do Saiken e começa a liberar de sua boca algumas bolhas repletas de carga negativa, formando uma Bola da Besta com Cauda translúcida, graças à mistura com seu Ninjutsu de Bolha de Sabão. Quando está completa e instável, a besta dispara contra o oponente, resultando em uma explosão de carga positiva e aniquilando-o, apenas com sobras das bolhas de carga negativa para trás.', E'荷束泡沫かそくほうまつ', E'Kasoku Hōmatsu', E'Disparo de Bolhas em Massa', E'Carga de Bolhas', NULL, NULL, E'Longo alcance', NULL, NULL),
(577, E'Disparo do Ataque Aéreo', E'A marionete de Kankurō, Karasu, voa no ar com uma modificação especial e libera uma série de mísseis em cima do oponente que explode no impacto. Uma versão avançada desta técnica pode ser usada, onde gás venenoso é adicionando nas bombas, causando um dano extra no adversário.', E'空襲弾', E'Kūshūdan', E'Tiro de Incursão Aérea', E'Ataque Aéreo', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(578, E'Disparo do Corpo', E'Em uma velocidade sobre-humana garantida pelo seu Modo de Chakra do Nove-Caudas, Naruto propulsiona-se para o alto, aterrissando logo em seguida um soco sobre o oponente em uma velocidade estonteante.', E'渾身一発', E'Konshin Ippatsu', E'Disparo do Corpo', E'Ataque Total', NULL, NULL, E'Todos os alcances', NULL, NULL),
(579, E'Dispositivo de Substituição Mental', E'Mecha-Naruto chega dentro do alcance e forma um selo de mão que faz então um tipo desconhecido de chakra passar pelas suas mãos e pelo seu adversário; por sua vez trocando suas mentes. O usuário desta técnica é capaz de soar exatamente como e usar qualquer técnica que o original poderia. O usuário também é capaz de usar essa técnica mesmo quando dentro do corpo do oponente.', NULL, NULL, NULL, E'Dispositivo de Substituição Mental', NULL, NULL, E'Curto alcance', NULL, E'Passaro'),
(580, E'Dissipação de Genjutsu', E'A Dissipação de Genjutsu é utilizada para libertar a si ou à outras pessoas de um genjutsu.', E'幻術解げんじゅつかい', E'Genjutsu Kai', E'Dissipação de Técnica Ilusória', E'Quebra de Genjutsu (幻術破り, Genjutsu Buri)[1]', E'Kai: Cancelar', E'Libertar', E'Curto alcance', NULL, E'Tigre'),
(581, E'Divisão da Ruína Rotacional', E'Darui pula no ar, corta seu inimigo com sua espada, realiza uma sequência de dois chutes para levantá-lo e finaliza com um poderoso corte rotacional, jogando seu oponente no chão.', E'廻滅斬り', E'Kaimetsugiri', E'Divisão da Ruína Rotacional', E'Corte da Ruína Rotacional', NULL, NULL, E'Curto alcance', NULL, NULL),
(582, E'Divisão de Chão', E'Com o Modo Sábio ativado, Naruto bate o pé no chão, fazendo-o criar uma rachadura gigante.', E'地面割り', E'Jimenwari', E'Divisão de Chão', E'Divisor de Terra', NULL, NULL, E'Curto alcance', NULL, NULL),
(583, E'Divisão de Henon', E'Lançando o oponente no ar, Sarada salta e finaliza-o com um golpe de seu calcanhar, mandando-o ao chão e gerando uma rachadura no solo.', E'淡竹割', E'Hachikuwari', E'Divisão de Henon', E'Corte do Bambu Preto', NULL, NULL, E'Curto alcance', NULL, NULL),
(584, E'Divisão de Terra', E'Sakura soca o chão usando a técnica Impacto da Flor de Cerejeira, causando muitas pedras, enviando seu oponente para o ar. Ela então, salta sobre as pedras para alcançar o seu oponente no ar e depois soca-lo através de uma pedra na frente deles, enviando-lhe para bater em dois pilares de pedras pequenas e em um muro de pedra gigante.', E'大地割り', E'Daichiwari', E'Divisão de Terra', E'Costeleta da Terra', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(585, E'Divisão de Terreno da Transformação Sábia', E'Após ativar parcialmente a Transformação Sábia, Jūgo bate com um soco no chão, fazendo a terra entrar em erupção até o alvo pretendido. Essa técnica também pode criar efetivamente barricadas capazes de parar ondas gigantes.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(586, E'Doki: Cego', E'Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki para atacar o inimigo em um ataque giratório seguido de um forte pontapé.', E'怒鬼・縛どき・ばく', E'Doki: Baku', E'Demônios Furiosos: Cego', E'Doki: Cego', NULL, NULL, E'Longo alcance', NULL, NULL),
(587, E'Doki: Clava', E'Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki, fazendo ele desferir com sua enorme clava, dois golpes consecutivos no oponente.', E'怒鬼・棍', E'Doki: Kon', E'Demônios Furiosos: Clava', E'Doki: Porrete', NULL, NULL, E'Médio alcance', NULL, NULL),
(588, E'Doki: Garra', E'Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki, fazendo ele desferir dois golpes consecutivos com suas garras no oponente.', E'怒鬼・爪', E'Doki: Tsume', E'Demônios Furiosos: Garra', E'Doki: Garra', NULL, NULL, E'Médio alcance', NULL, NULL),
(589, E'Dragão Gêmeo', E'Com sua tremenda força física, o usuário dá um soco destruidor no solo, gerando grande impacto.', E'双龍そうりゅう', E'Sōryū', E'Dragão Gêmeo', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(590, E'Dragão de Fumaça', E'Desencadeando um fluxo contínuo de fumaça de seu Lançador de Pulso, Kihō pode usar seu chakra para moldar a fumaça na forma de um dragão ligado a seu lançador de pulso. Isto dá-lhe a vantagem de ataques de curto alcance à médio alcance, já que o dragão é totalmente composto por fumaça, ataques físicos são inúteis contra ele. Ele pode formar vários dragões de fumaça de uma vez só. Sua habilidade de batalha principal é envolver e sufocar o alvo. No entanto, como a fumaça é dissolvida quando entra em contato com a água, esta técnica é fraca contra ataques à base de água.', E'煙龍', E'Enryū', E'Dragão de Fumaça', NULL, NULL, NULL, E'Curto à Médio Alcance', NULL, NULL),
(591, E'Dramingo', E'Quando transformado em Son Gokū, Rōshi começa a bater em seu peito, causando ondas de choque que atingem o seu oponente ferozmente.', E'怒羅民猴', E'Doraminkō', E'Gaze Revoltada dos Homens Símios', E'Dramingo', NULL, NULL, E'Curto alcance', NULL, NULL),
(592, E'Drenagem de Chakra', E'A Drenagem de Chakra é uma técnica utilizada através do Caminho Preta, que permite absorver o chakra do oponente através do contato físico e repor as suas próprias reservas.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(593, E'Dupla Calma de Kusanagi', E'Sasuke primeiro atinge o oponente no ar. Orochimaru cospe sua Kusanagi para Sasuke, e ele corta o oponente no caminho. Sasuke pega a Kusanagi com a mão livre e pula em direção ao oponente, cortando-o repetidamente com espadas dele e Orochimaru.', E'二凪の草薙', E'Futanagi no Kusanagi', E'Dupla Calma da Espada Ceifadora de Grama', E'Dupla Kusanagi', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(594, E'Dupla Pantera Negra', E'Depois de criar um clone de si mesmo, Darui e seu clone simplesmente usam a técnica Liberação de Relâmpago: Pantera Negra para atacar seu inimigo.', E'ダブル黒斑差だぶるくろぱんさ', E'Daburu Kuropansa', E'Dupla Diferença Manchada de Preto', NULL, NULL, E'Panteras Negras Duplas', E'Longo alcance', NULL, NULL),
(595, E'Ecdise', E'Trata-se de uma forma única de substituição criada por Orochimaru, a fim de eliminar os danos de um corpo através da troca de pele.', E'脱皮だっぴい', E'Dappī', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(596, E'Eco da Broca Ressonante', E'Esta técnica pode ser usada como um termo geral para a maioria das técnicas de Dosu. O Alto-Falante Ressonante de Eco, um dispositivo de metal poroso localizado em seu braço, é usada para amplificar as ondas de som nos movimentos do braço para atacar seu oponente. Através da geração de som suficiente, Dosu pode usar seu chakra para reorientar as ondas no ar até seu alvo. Por danificar o ouvido interno da vítima, a vítima gradualmente fica sem equilíbrio e sente náuseas. No primeiro caso, Rock Lee fica fisicamente doente pelo ataque.', E'響鳴穿きょうめいせん', E'Kyōmeisen', E'Eco da Broca Ressonante', E'Eco da Broca do Som[2]', NULL, E'Broca de Som Vibratória', E'Curto alcance', E'Rank C', E'Selo do Confronto'),
(597, E'Efêmera', E'A Efêmera é uma técnica de infiltração, o que é uma característica de assinatura do Zetsu Branco e de seus irmãos clones, devido a serem infundidos com as células de Hashirama Senju.', E'蜉蝣かげろう', E'Kagerō', E'Efêmera', NULL, NULL, NULL, E'Todos os alcances', E'Rank –', NULL),
(598, E'Efêmero', E'Esta técnica permite que Itachi coloque o seu adversário dentro de um genjutsu usando seu dedo como um meio. Isto induz o genjutsu no alvo. Quando a técnica foi utilizada em Naruto Uzumaki, os corvos reuniram-se na forma de Itachi e se transformaram em shuriken depois que ele realizou o selo carneiro, bombardeando Naruto e, em seguida, tentaram colocá-lo para dormir. Quando ele resistiu o comando sono, Naruto então experimentou a sensação de partes de seu corpo se transformando em seus amigos mais próximos que então o torturaram psicologicamente. Isso está implícito sendo um estágio extra do genjutsu causado por Itachi depois de realizar o selo carneiro. O mesmo vai junto com seu uso de shuriken.', E'泡沫', E'Utakata', E'Efêmero', E'Genjutsu: Bolhas', NULL, NULL, E'Curto alcance', NULL, NULL),
(599, E'Elefante do Anoitecer', E'O Elefante do Anoitecer é um taijutsu proibido de alto nível, que pode ser realizado por aqueles usuários que abriram todos os Oito Portões.', E'夕象せきぞう', E'Sekizō', E'Elefante do Anoitecer', NULL, E'Elefante da Noite', NULL, E'Curto alcance', E'Rank A', NULL),
(600, E'Emissão de Chakra', E'A Emissão de Chakra é uma técnica defensiva de fluxo de chakra.', E'チャクラ放出', E'Chakura Hōshutsu', E'Emissão de Chakra', NULL, NULL, NULL, NULL, NULL, NULL),
(601, E'Encadernação da Corda do Xarope de Amido', E'Depois de moldar o chakra no estômago e converter em água de viscosidade elevada, o usuário a expele para fora, em forma de cordas que restringem os movimentos do adversário. Devido a viscosidade da água, torna-se mais difícil livrar-se da técnica.', E'水飴縛縄', E'Mizuame Bakujō', E'Encadernação da Corda do Xarope de Amido', NULL, NULL, E'Corda Entrelaçadora do Xarope de Amido', E'Curto a Médio alcance', NULL, NULL),
(602, E'Encadernação da Prisão de Fumaça', E'Kigiri pode manipular sua fumaça para enrolar-se em volta do adversário e restringir seus movimentos.', E'煙縛牢', E'Enmaku Rō', E'Encadernação da Prisão de Fumaça', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(603, E'Encontro de Cobras', E'Se Orochimaru é de alguma forma cortado pelo adversário, cobras saem das metades que foram separadas e se juntam de volta.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(604, E'Enganação da Chama Explosiva', E'Obito lança seu gunbai contra o oponente à uma média distância, puxa o gunbai com o oponente para cima e, enquanto o oponente cai, Obito lança kunai com papéis explosivos anexos, que explodem ao impacto.', E'籠絡の爆炎ろうらくのばくえん', E'Rōraku no Bakuen', E'Enganação da Chama Explosiva', E'Explosão da Chama Tentadora', NULL, NULL, E'Curto alcance', NULL, NULL),
(605, E'Enrolamento Adjacente', E'Sendo uma técnica utilizada apenas por cobras, elas ganham a capacidade de enrolar-se em seu alvo a fim de prendê-lo e, por fim, morde-o, incapacitando-o por algum tempo. Esta técnica mostrou-se ser uma técnica muito forte, já que o alvo é preso entre o corpo resistente de uma cobra. Apesar da resistência da técnica, Katsuyu conseguiu sair da incapacitação a partir da sua técnica de divisão.', E'巻きつき', E'Makitsuki', E'Enrolamento Adjacente', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(606, E'Enrolamento em Tentáculos', NULL, E'触手絡め', E'Shokushukarame', E'Enrolamento em Tentáculos', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(607, E'Enterro da Queda do Turbilhão', E'Gaara manipula sua areia para modelar um pequeno corpo de areia no ar, sendo forte o suficiente para lançar o inimigo ao solo com o deslizamento total da areia.', E'落渦葬', E'Rakkasō', NULL, E'Enterro do Redemoinho', NULL, NULL, E'Curto alcance', NULL, NULL),
(608, E'Entidades de Chakra', E'Uma técnica transmitida na sua família, Gekomatsu cria cópias de chakra da vida shinobi ou falecido, como Jiraiya e Orochimaru. Apenas aqueles que possuem as contas especiais de chakra pode ver as entidades de chakra criados.', NULL, NULL, NULL, E'Entidades de Chakra', NULL, NULL, NULL, NULL, NULL),
(609, E'Entrada Dinâmica', E'A Entrada Dinâmica consiste em um movimento de taijutsu em que o objetivo é basicamente que o usuário execute um chute direcionado ao rosto do oponente impulsionado por um salto. Might Guy usa uma arma, como uma kunai, para distrair o inimigo enquanto ele tira vantagem de seu ponto cego, com o intuito de acertar seu golpe. É obviamente difícil de combater, pois foi capaz de pegar Jiraiya com sua guarda baixa.', E'ダイナミック・エントリー', E'Dainamikku Entorī', E'Entrada Dinâmica', NULL, NULL, NULL, E'Curto alcance', E'Rank D', NULL),
(610, E'Erosão do Mar de Árvores', NULL, E'樹海侵食じゅかいしんしょく', E'Jukai Shinshoku', E'Erosão do Mar de Árvores', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(611, E'Erupção do Pó de Areia', E'Gaara acumula arreia em baixo do oponente a fim de criar um pilar de areia que entra em erupção atacando o oponente e lançá-lo ao ar. A técnica ainda é capaz de defendê-lo contra projéteis.', E'噴塵砂ふんじんさ', E'Funjinsa', E'Erupção do Pó de Areia', E'Erupção da Tempestade de Areia', NULL, NULL, E'Curto alcance', NULL, NULL),
(612, E'Escalada do Pilar de Madeira', E'Yamato faz um pilar de madeira erguer-se do solo e atingir seu oponente, derrubando-o para trás.', NULL, NULL, NULL, E'Escalada do Pilar de Madeira', NULL, NULL, E'Curto alcance', NULL, NULL),
(613, E'Escudo de Areia', E'Esta é uma técnica única para Gaara, desencadeada pela vontade de sua mãe, Karura.', E'砂の盾すなのたて', E'Suna no Tate', E'Escudo de Areia', E'Escudo Robusto (堅牢な盾, Kenrōnatate)[2]', NULL, NULL, E'Curto alcance', E'Rank –', NULL),
(614, E'Escudo de Areia de Ferro', E'Usando a Areia de Ferro, o usuário cria uma barricada espessa à sua frente, a fim de bloquear ataques frontais. Esta capacidade é forte o suficiente para proteger até mesmo contra um forte ataque do Terceiro Kazekage.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(615, E'Escudo de Aço', E'O usuário levanta um escudo de aço a partir do chão, na forma de uma parede defensiva. O escudo é poderoso o suficiente para aguentar uma grande quantidade de balas de areia.', NULL, NULL, E'Escudo de Aço', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(616, E'Escudo de Inseto', E'Tirando proveito de sua proficiência com insetos, o usuário pode criar um escudo forte o suficiente para resistir à queda de vários entulhos ou suportar ataques baseados em fogo. O escudo pode ser usado para defender tanto o usuário da técnica quando aliados nas proximidades.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(617, E'Escudo de Pó de Ouro', E'Usando seu Pó de Ouro, o quarto Kazekage eleva o pó para cima dele para criar uma barricada espessa, a fim de bloquear a entrada de ataques aéreos, usando gestos com as mãos para ditar a forma que a barreira adota. Esta capacidade é forte o suficiente para proteger até mesmo contra o Granizo de Areia de Gaara.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL);