INSERT INTO `Jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `mangaPanini`, `tvBrasileira`, `range`, `rank`, `handSeals`) VALUES
(618, 'Esfera Espiral do Medo', 'Menma cria um Rasengan de chakra negativo e o rodeia com um anel em forma de disco adicional de chakra, que orbita a esfera principal, fazendo-a se assemelhar a um pequeno anel planetário. Ele corresponde ao Rasenshuriken de Naruto no Modo Sábio.', '螺旋輪虞らせんりんぐ', 'Rasenringu', 'Esfera Espiral do Medo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(619, 'Esfera Gigante', 'O usuário forma uma grande esfera de energia da cor de seu chakra a partir de alguma de suas extremidades para atirar em seu inimigo. Um único golpe forçou Gamabunta a recuar e a explosão resultante é maior do que o próprio Hōzukijō.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(620, 'Esfera Protetora de Luz', 'Esta é uma técnica em que o Sino de Segurança Místico de Shion ilumina e nega qualquer ocorrência negativa. Ela era poderoso o suficiente para proteger Shion da escuridão do Mōryō.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(621, 'Esfera da Busca da Verdade', 'As Esferas da Busca da Verdade são esferas negras maleáveis de chakra no qual os usuários podem alterar sua forma e obter diversos tipos de efeitos de acordo com a sua vontade.', '求道玉ぐどうだま', 'Gudōdama', 'Esfera da Busca da Verdade', NULL, 'Esferas do Caminho da Verdade', NULL, 'Todos os alcances', 'Rank –', NULL),
(622, 'Esfera da Busca da Verdade Expansiva', 'A Esfera da Busca da Verdade Expansiva é um ninjutsu de Kekkei Mōra usado por Kaguya Ōtsutsuki. Ela é composta pelas cinco transformações básicas da natureza e a Liberação de Yin–Yang.', '膨張求道玉ぼうちょうぐどうだま', 'Bōchō Gudōdama', 'Esfera da Busca da Verdade Expansiva', 'Esfera da Busca da Verdade Final', 'Esfera Expandida do Caminho da Verdade', NULL, 'Todos os alcances', 'Rank –', NULL),
(623, 'Esfera da Maldição da Marionete', 'Toneri molda seu chakra na forma de uma esfera de cor verde. A esfera pode ser usada para paralisar ou atordoar um alvo, ou realizar uma lavagem cerebral e controlar qualquer alvo à curta distância.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(624, 'Esfera de Absorção Espiral', 'Naruto enquanto no Modo de Chakra do Nove-Caudas, usa seus braços de chakra para formar uma Grande Bola da Esfera Espiral, em sua mão, em seguida, joga-o acima de suas costas. Ele então ele usa os braços de chakra extras para agarrar e esmagar os inimigos na esfera, simulando uma \'absorção\' das vítimas para o Rasengan.', '螺旋吸丸', 'Rasenkyūgan', 'Esfera de Absorção Espiral', NULL, 'Espiral Sugadora', NULL, 'Curto alcance', NULL, NULL),
(625, 'Esfera de Luz', 'O usuário molda seu chakra na palma da mão, formando uma pequena bola de luz que paira sobre ela. A esfera serve como uma lanterna, melhorando a visibilidade. O usuário, por sua vez, pode controlar seu chakra, variando a intensidade da luz ou até mesmo apagá-la.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(626, 'Esfera de Odor', 'O usuário cospe uma esfera de sopro roxa que se estoura e forma uma nuvem fedorenta, cobrindo uma área extensa. Qualquer um que entrar em contato com este gás fica paralisado por um curto período de tempo. Além disso, a técnica pode impedir o uso do olfato de suas vítimas por um período de tempo mais prolongado, como visto o acontecido com Kiba Inuzuka e Akamaru. O odor é devido à fermentação ocorrida dentro do corpo do usuário.', 'スメル玉', 'Sumerudama', 'Esfera de Odor', NULL, NULL, 'Arte Ninja: Esfera de Odor', 'Curto a Médio alcance', NULL, NULL),
(627, 'Esfera de Relâmpago', 'O usuário cria esferas de relâmpago para lançá-las contra o inimigo. Ao entrar em contato com qualquer corpo, as esferas dispõem de uma grande carga elétrica, mesmo capaz de matar. Dependendo da finalidade utilizada, pode-se criar múltiplas esferas em vez de somente uma. O uso também pode ocorrer por meio das Espadas Gêmeas.', '雷球らいきゅう', 'Raikyū', 'Esfera de Relâmpago', NULL, NULL, 'Bola de Raio', 'Curto a Médio alcance', NULL, 'Passaro → Cobra → Macaco → Carneiro[1]'),
(628, 'Esfera de Água', 'Utilizando o Kote, Boruto lança uma esfera feita de água que paira no ar e lança outras várias esferas de água menores contra o oponente, como se fossem projéteis, infligindo dano no inimigo.', '水球すいきゅう', 'Suikyū', 'Esfera de Água', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(629, 'Esfera de Água Sensorial', 'Esta esfera, feita de água, pode ser utilizada para monitorar as assinaturas de chakra numa dada área. Indivíduos ou grupos são representados por variáveis portes de bolhas dentro da esfera, com assinaturas de chakra maiores suas bolhas serão maiores. A esfera em si não é uma ótima forma de detecção e precisa de relatórios do campo para localizar alvos que devem ser mantidos a par. Quando ligado a uma Barreira Sensorial, essa imprecisão é atenuado um pouco.', '感知水球かんちすいきゅう', 'Kanchi Suikyū', 'Esfera de Água Sensorial', NULL, 'Técnica do Radar Sensorial', NULL, 'Longo alcance', NULL, NULL),
(630, 'Esferas Espirais Desordenadas', 'Naruto, enquanto controlando o chakra de Kurama, forma braços de chakra para formar múltiplos Rasengan. Ele pode, então, atingir vários oponentes ao mesmo tempo segurando o Rasengan com os braços de chakra.', '螺旋乱丸らせんらんがん', 'Rasenrangan', 'Esferas Espirais Desordenadas', 'Múltiplas Esferas Espirais', NULL, NULL, 'Curto alcance', NULL, NULL),
(631, 'Esforço da Estrela Especuladora', 'Might Guy abre vários portões de chakra e, em seguida, salta pro céu em forma de estrela, caindo rapidamente e atingindo seu oponente, criando uma enorme explosão e destruição no local.', '噂の努力星うわさのどりょくしょう', 'Uwasa no Doryokushō', 'Esforço da Estrela Especuladora', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(632, 'Esguicho do Mar Agitado', 'Yagura empunha seu bō e golpeia para baixo, inundando o ambiente. Com o cenário transformado em um enorme lago, o Mizukage afunda junto com o oponente, envolve-se em um turbilhão de água, afastando-o e transformando-se no Isobu, que retrai suas patas e nada em alta velocidade, com o apoio de jatos d\'água que saem dos orifícios da casca, onde ficavam as patas, e cabeceando o inimigo para fora da água. Com o adversário no ar, Isobu então cospe um poderoso jato de água diretamente nele.', '荒海飛沫', 'Araumi Shibuki', 'Esguicho do Mar Agitado', 'Espuma do Mar Bravo', NULL, NULL, NULL, NULL, NULL),
(633, 'Esmagar', 'O usuário pula sobre o oponente esmagando-o com sua barriga. O usuário ainda pode aproveitar-se do movimento de mola, proporcionado por sua barriga flácida, para executar um combo contra o oponente.', NULL, NULL, NULL, 'Esmagar', NULL, NULL, 'Curto alcance', NULL, NULL),
(634, 'Espada Fortalecida', 'Esta técnica consiste em reforçar suas lâminas com chakra afim de causar grandes danos e impacto.', '剛剣ごうけん', 'Gōken', 'Espada Fortalecida', 'Espada Forte', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(635, 'Espada Gêmea Hiramekarei: Martelo', 'Chōjūrō flui seu chakra através da Hiramekarei e aplica a transformação da forma, criando um martelo gigante. Com isso, ele pode acertar os seus alvos com força o suficiente para fazê-los voar através de uma distância considerável, atravessando paredes, mesmo com uma forte defesa como o Susanoo.', '双刀ヒラメカレイ・ハマー', 'Sōtō Hiramekarei: Hamā', 'Espada Gêmea Hiramekarei: Martelo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(636, 'Espada Ninja dos Fūma: Zanbatō', 'Hanzaki salta em direção ao seu inimigo e desfere um poderoso corte descendente com sua zanbatō.', 'ふうま忍剣・斬馬刀ふうまにんけん・ざんばとう', 'Fūma Ninken: Zanbatō', 'Espada Ninja dos Fuuma: Zanbatou', NULL, NULL, 'Espada Ninja do Vento Demoníaco: Zambatou', 'Curto alcance', NULL, NULL),
(637, 'Espada Relâmpago', 'Kakashi joga o oponente no ar, onde Yūgao chuta-o e em seguida, ataca-o com sua espada, logo Kakashi finaliza com o Corte Relâmpago.', '雷剣', 'Raiken', 'Espada Relâmpago', NULL, NULL, NULL, 'Curto Alcance', NULL, NULL),
(638, 'Espada Relâmpago Instantânea', 'Minato corre em alta velocidade que até parece um desfoque e bate-o quando ele chega ao lado do seu oponente. Em seguida, ele para atrás do oponente e cortá-o com uma kunai.', '瞬雷刃', 'Shunraijin', 'Espada Relâmpago Instantânea', 'Espada Instantânea do Trovão', NULL, NULL, 'Curto alcance', NULL, NULL),
(639, 'Espada Rotativa Voadora', 'O usuário joga a Kubikiribōchō em seu oponente, causando dano à medida que gira em direção a ele. Se a Kubikiribōchō ficar presa em algo ou acerta o inimigo, o usuário pode ficar sobre ela.', '飛旋剣', 'Hisenken', 'Espada Rotativa Voadora', 'Turno do Corte Voador', NULL, NULL, 'Longo alcance', NULL, NULL),
(640, 'Espada Secreta: Corte Nevasca', 'Tsubaki canaliza seu chakra através de sua katana ainda embainhada e então ela realiza um ataque de battōjutsu — o desembainhamento da espada com o fito de executar um corte. A partir disso, ela desencadeia uma poderosa onda de choque que culmina na formação de um tornado maciço ao seu redor, repelindo tudo o que estiver nas proximidades. A destruição é ainda maior quando ela desembainha a espada completamente, mas o custo de chakra é maior.', '秘剣・風雪斬り', 'Hiken: Fūsetsugiri', 'Espada Secreta: Corte Nevasca', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(641, 'Espada Secreta: Luar', 'Hayate corre em direção ao seu adversário com um golpe de espada, deixando um rastro de imagem posterior.', '秘剣・月影', 'Hiken: Tsukikage', 'Espada Secreta: Luar', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(642, 'Espada: Lâmina de Vento', 'Ashura concentra chakra em sua espada e libera rapidamente contra o inimigo em forma de uma pequena esfera de vento com capacidades cortantes.', '一刀・風刃いっとう・ ふうじん', 'Ittō: Fūjin', 'Espada: Lâmina de Vento', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(643, 'Espada de Assassinato Relâmpago', NULL, '暗殺雷光剣あんさつらいこうけん', 'Ansatsu Raikōken', 'Espada de Assassinato Relâmpago', 'Lâmina Assassina Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(644, 'Espada de Chakra: Grande Ferida de Interseção', 'Shizune usa Tonton para acertar o oponente, dando-lhe tempo suficiente para usar suas duas espadas de chakra para cortar e matar o oponente. As barras deixam duas grandes marcas no chão.', 'チャクラ剣・大交衝', 'Chakura Ken: Daikōshō', 'Espada de Chakra: Grande Ferida de Interseção', 'Espada de Chakra: Grande Cruzamento de Golpe', NULL, NULL, NULL, NULL, NULL),
(645, 'Espada de Kagutsuchi', 'A Espada de Kagutsuchi é uma espada de chamas negras empunhada por Sasuke Uchiha.', '加具土命の剣カグツチのつるぎ', 'Kagutsuchi no Tsurugi', 'Espada da Ferramenta Adicionada do Senhor da Terra', 'Espada de Controle de Chamas', NULL, NULL, 'Curto alcance', NULL, NULL),
(646, 'Espada de Kagutsuchi: Turbilhão de Chamas', 'Sasuke manifesta seu Corpo Completo — Susanoo e executa uma série de cortes com a Espada de Kagutsuchi, desencadeando um turbilhão de chamas negras que engolem seu adversário.', '加具土命の剣・渦炎カグツチのつるぎ・うずえん', 'Kagutsuchi no Tsurugi: Uzuen', 'Espada da Ferramenta Adicionada do Senhor da Terra: Turbilhão de Chamas', 'Espada do Controle de Chamas: Redemoinho de Chamas', NULL, NULL, 'Curto alcance', NULL, NULL),
(647, 'Espada de Kusanagi: Espada Longa do Céu', 'Esta é uma técnica que permite que Orochimaru possa levitar a Espada de Kusanagi e atacar os inimigos a meia-distância. A lâmina se move de acordo com os movimentos dos dedos de Orochimaru. No anime, a lâmina emite uma luz azul brilhante quando ela está levitando.', '草薙の剣・空の太刀', 'Kusanagi no Tsurugi: Kū no Tachi', 'Espada de Kusanagi: Espada Longa do Céu', NULL, NULL, NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(648, 'Espada de Kusanagi: Lâmina de Chidori', 'Esta técnica permite que Sasuke possa canalizar o Chidori através de sua Espada de Kusanagi. Isso torna a lâmina quase indefensável podendo facilmente cortar o aço. Além disso, se uma pessoa é esfaqueada, seu corpo fica dormente devido a uma corrente elétrica que faz com que os músculos fiquem dormentes, tornando impossível para o adversário conseguir se mover. No entanto, pode ser anulada por outras armas que também utilizam o fluxo de chakra da Liberação de Relâmpago, como as espadas usadas por Killer B.', '草薙の剣・千鳥刀くさなぎのつるぎ・ちどりがたな', 'Kusanagi no Tsurugi: Chidorigatana', 'Espada de Kusanagi: Lâmina dos Mil Pássaros', 'Katana Chidori', NULL, NULL, 'Curto alcance', 'Rank B', NULL),
(649, 'Espada de Kusanagi: Lâminas de Todas as Direções', 'Depois de imobilizar o alvo com uma cobra, Orochimaru invoca várias réplicas de sua espada de Kusanagi que atacam o inimigo de todas as direções.', '草薙の剣・八方刃くさなぎのつるぎ・はっぽうは', 'Kusanagi no Tsurugi: Happōha', 'Espada Ceifadora de Grama: Lâminas de Oito Direções', 'Grama Alabarda: Lâmina de Todas Direções', NULL, NULL, 'Curto alcance', NULL, NULL),
(650, 'Espada de Kusanagi: Um Golpe Mortal', 'Orochimaru invoca uma cobra gigante, que bate no chão com sua cauda, lançando o seu alvo para o ar. Então, Orochimaru ordena para Espada de Kusanagi esfaquear o seu alvo.', '草薙の剣・一刺死くさなぎのつるぎ・いっしし', 'Kusanagi no Tsurugi: Isshishi', 'Espada do Corte da Grama: Um Golpe Mortal', 'Alabarda da Grama: Morte em um Golpe', NULL, NULL, 'Curto alcance', NULL, NULL),
(651, 'Espada de Luz de Chidori', 'Sasuke usa sua espada chokutō para canalizar um Chidori extremamente poderoso. Ele parece ter um alcance ainda maior do que a Corrente de Chidori, tornando-o mais eficaz em batalha. Pode notar-se que esta técnica é mais utilizada em água, porque a corrente do raio produzido é transportado em toda a superfície tornando-a mais difícil do inimigo evitar.', '千鳥光剣ちどりこうけん', 'Chidori Kōken', 'Espada de Luz dos Mil Pássaros', 'Espada Chidori', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(652, 'Espada de Nunoboko', 'A Espada de Nunoboko é uma grande espada em forma de dupla hélice, formada a partir da Esfera da Busca da Verdade e originalmente usada por Hagoromo Ōtsutsuki.', 'ぬのぼこの剣ぬのぼこのけん', 'Nunoboko no Ken', 'Espada de Nunoboko', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(653, 'Espada de Relâmpago Infernal', 'Saltando acima do oponente, o usuário empunha sua espada infundida com relâmpago e desfere um poderoso golpe perfurante para baixo, deixando um rastro de relâmpago para trás.', '奈落雷光剣ならくらいこうけん', 'Naraku Raikōken', 'Espada de Relâmpago Infernal', 'Espada Relâmpago Naro', NULL, NULL, 'Curto alcance', NULL, NULL),
(654, 'Espada de Totsuka', 'A Espada de Totsuka, também conhecida como Sakegari no Tachi (酒刈太刀, Espada Longa Cortadora de Saquê), é uma variação da Espada de Kusanagi. Ela é embainhada numa jarra de saquê; a lâmina é de fato o líquido dentro do recipiente que é solto e moldado, ao invés do que uma espada convencional. Ela é uma arma etérea, com uma lâmina encantada capaz de selar qualquer coisa que ela perfure. Aqueles que são perfurados por ela são sugados para dentro da jarra e presos num “mundo de sonhos embriagados” como um genjutsu por toda eternidade; e além disso, Itachi também conseguiu remover o selo amaldiçoado de Sasuke ao perfurar e remover Orochimaru do corpo de Sasuke. Orochimaru procurou pela espada por toda sua vida e Zetsu comentou que ele nunca pôde achá-la.', '十拳剣とつかのつるぎ', 'Totsuka no Tsurugi', 'Espada Longa de Dez Mãos', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(655, 'Espada de Vácuo', 'O usuário balança sua espada e uma rajada de vento é liberada no arco em que foi balançada. A técnica é forte o suficiente para parar projéteis que são lançados no usuário e mesmo cortar algo denso como uma árvore.', '真空剣しんくうけん', 'Shinkūken', 'Espada de Vácuo', NULL, NULL, 'Lâmina de Choque', 'Curto a Médio alcance', NULL, NULL),
(656, 'Espada do Deus do Trovão: Corte', 'É uma variante das técnicas usadas por Aoi Rokushō através da Espada do Deus do Trovão.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(657, 'Espada do Deus do Trovão: Descarga Elétrica', 'Uma técnica desenvolvida por Aoi Rokushō através de suas maestrias utilizando a Espada do Deus do Trovão. Com seu empunhamento, essa se tornou uma de suas técnicas assinaturas.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(658, 'Espadas de Liberação de Relâmpago Super-Vibrantes', 'Trata-se de uma técnica de Killer B com a qual ele infunde um objeto perfurante, normalmente suas espadas, com chakra de natureza relâmpago para atacar ou arremessar em direção a seu alvo. O poder de perfuração do objeto é aprimorado devido ao aumento da frequência de vibração do chakra, ao ponto de superar o de técnicas de vento. Com isso é possível perfurar um tronco com relativa facilidade, mesmo se a arma utilizada for um lápis.', '超ビブラート雷遁刀', 'Chō Biburāto Raitontō', 'Espadas de Liberação de Relâmpago Super-Vibrantes', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(659, 'Especialidade da Academia', 'Iruka atinge o oponente com vários chutes e golpes de kunai, em seguida ele aponta o dedo para o oponente e, ficando enfurecido, pega um livro e bate com ele sobre a cabeça do oponente.', '忍者学校名物あかでみいめいぶつ', 'Akademī Meibutsu', 'Especialidade da Academia', 'Especialidade da Academia Ninja', NULL, NULL, 'Curto alcance', NULL, NULL),
(660, 'Espelho Demoníaco de Gelo Fino', 'Trata-se da versão reduzida a um espelho a partir dos Espelhos Demoníacos de Cristais de Gelo de Haku.', '魔鏡薄氷まきょうはくひょう', 'Makyō Hakuhyō', 'Espelho Demoníaco de Gelo Fino', 'Espelhos dos Cristais de Gelo', NULL, NULL, 'Todos os alcances', NULL, NULL),
(661, 'Espelho de Yata', 'O Espelho de Yata (八咫鏡, Yata no Kagami) foi um escudo etéreo empunhado pelo Susanoo de Itachi. É dito sendo dotado de todas as cinco transformações da natureza e como tais mudanças características próprias de acordo com os do ataque que recebe, fazendo com que o ataque seja inteiramente ineficaz.', '八咫鏡やたのかがみ', 'Yata no Kagami', 'Espelho de Yata', NULL, NULL, NULL, NULL, NULL, NULL),
(662, 'Espelhos Demoníacos de Cristais de Gelo', 'A capacidade abominável e enorme, transmitida apenas dentro do Clã Yuki. É uma técnica envolvida em muitos mistérios. Dizia-se que nenhum método de existência pode derrotar esta técnica. Em um instante, vários espelhos de gelo são criadas em torno do inimigo, refletindo nada, exceto Haku. O adversário está preso em uma cúpula de 21 espelhos flutuantes feitos de gelo. Doze permanecem ao nível do solo, oito flutuam acima dos 12 primeiro e inclinados em direção ao chão, e o espelho final é acima do resto e virado para o chão. Uma vez que Haku entrou nos espelhos, é possível para ele se mover entre os espelhos na velocidade da luz. É quase impossível de seguir ataques enviados a partir deste movimento incrível. Como todo espelho mostra seu reflexo, pode-se dizer que é impossível ver todos os ataques de Haku. Mesmo que o oponente tente atacar o corpo real, Haku já se muda para outro espelho. Sem olhos como o Sharingan, será impossível manter o controle dele.', '魔鏡氷晶まきょうひょうしょう', 'Makyō Hyōshō', 'Espelhos Demoníacos de Cristais de Gelo', 'Jutsu Oculto: Espelhos de Cristais de Gelo', 'Técnica Secreta de Espelho Demoníaco dos Cristais de Gelo', 'Jutsu Secreto: Espelhos de Cristais de Gelo', 'Curto a Médio alcance', 'Rank –', 'Selo de Mao Especifico da Técnica'),
(663, 'Espinho', 'Kimimaro acerta uma série de golpes no oponente com duas espadas de ossos até que ele o envia voando no ar, onde ele acerta uma série de golpes muito rápido.', '茨いばら', 'Ibara', 'Espinho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(664, 'Espinho Giratório de Serpente', 'Depois de usar Invocação: Serpente de Duas Cabeças e fundindo-se com elas através do Técnica Quimera, o usuário pode disparar projéteis giratórios da boca da cobra que têm força de impacto explosivo.', '蛇旋刺', 'Jasenshi', 'Espinho Giratório de Serpente', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(665, 'Espinho da Busca da Verdade', 'Madara ataca o oponente com suas Esferas da Busca da Verdade, jogando-o para cima e, enquanto inimigo ainda está no ar, o usuário envia suas esferas transforma-as em várias pequenas estacas, que aumentam e perfuram o oponente enquanto ainda está em queda.', '求道刺', 'Gudōzashi', 'Espinho da Busca da Verdade', 'Espeto da Busca da Verdade', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(666, 'Espiral Carmesim', 'A Espiral Carmesim é uma forma mais poderosa do Rasengan utilizada por Momoshiki Ōtsutsuki.', '深紅螺旋', 'Shinku Rasen', 'Espiral Carmesim', 'Rasen Carmesim', NULL, NULL, 'Curto alcance', NULL, NULL),
(667, 'Esprei de Perfume', 'Karin pulveriza uma enorme quantidade de perfume rosa que causa danos medianos ou fracos, mas também envenena o oponente no processo. Além de envenenar o alvo, ele também pode diminuir sua velocidade e defesa. Enquanto no \'Modo Despertar\', Karin utiliza uma versão mais agressiva da técnica, chamada de Super Esprei de Perfume (香水スプレー超噴射, Kōsui Supurē Chōfunsha; Bandai: Super Explosão do Spray de Perfume), onde a substância é usada de forma mais concentrada contra o oponente e com maiores danos, além de atuar também como uma investida que destrói a guarda.', '香水スプレー', 'Kōsui Supurē', 'Esprei de Perfume', 'Spray de Perfume', NULL, NULL, 'Todos os alcances', NULL, NULL),
(668, 'Estilo Hanabi: Rotação Celestial', 'Esta técnica é uma manobra do estilo de luta Punho Gentil.', 'ハナビ流・回天', 'Hanabi Ryū: Kaiten', NULL, 'Estilo Hanabi: Rotação', NULL, NULL, 'Curto alcance', NULL, NULL),
(669, 'Estilo Takigakure: Lâmina Decapitadora de Água', 'Uma técnica secreta de Takigakure que permite o usuário manipular a água em forma de uma espada. Enquanto usado por usuários de Kenjutsu, esta técnica pode ser usada para assaltos, como foi demonstrado por Shibuki. Esta espada demonstra ser resistente e também capaz de cortar a maioria das técnicas da Liberação de Fogo.', '滝隠れ流・水斬りの刃', 'Takigakureryū: Mizukiri no Yaiba', 'Estilo Takigakure: Lâmina Decapitadora de Água', NULL, NULL, 'Estilo Cachoeira Secreta: Corte da Espada de Água', 'Curto alcance', NULL, NULL),
(670, 'Estilo Tsuchigumo: Liberação da Técnica Proibida da Vida: Criação do Céu e da Terra', 'Esta técnica libera o selo sobre a técnica Fúria.', '土蜘蛛流・禁生術解放・天地開闢', 'Tsuchigumoryū: Kinseijutsu Kaihō: Tenchi Kaibyaku', 'Estilo Tsuchigumo: Liberação da Técnica Proibida da Vida: Criação do Céu e da Terra', NULL, NULL, 'Estilo Tsuchigumo: Liberação do Jutsu Proibido: Grande Explosão', NULL, NULL, 'Selo do Confronto'),
(671, 'Estilo Uchiha: Dança da Auréola', 'Shisui infunde sua tantō em fogo e com um gesto rápido desencadeia uma onda de chamas em direção ao inimigo.', 'うちは流・日暈の舞うちはりゅう・ひがさのまい', 'Uchiharyū: Higasa no Mai', 'Estilo Uchiha: Dança da Auréola', 'Estilo Uchiha: Dança Auréola', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(672, 'Estilo da Grama Oculta: Palma Destruidora', 'Um ataque de taijutsu puro onde Mui oferece um conjunto de mão-aberta, ou com o punho fechado junto com chutes no seu adversário. Esta série de ataque mal pode ser vista a olho nu.', '草影流發破掌', 'Kusakage Ryū Happa Shō', 'Estilo da Grama Oculta: Palma Destruidora', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(673, 'Estilo de Duas Mãos', 'O Estilo de Duas Mãos é uma técnica de ambidestro utilizada por Tamao em uma competição de comida.', '二ハンド流', 'Ni Handoryū', 'Estilo de Duas Mãos', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(674, 'Estilo de Luta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(675, 'Estilo de Selamento de Oito Trigramas', 'O Estilo de Selamento de Oito Trigramas é um fūinjutsu criado pelo clã Uzumaki e consiste de dois Selos de Quatro Símbolos.', '八卦の封印式はっけのふういんしき', 'Hakke no Fūin Shiki', 'Estilo de Selamento de Oito Trigramas', NULL, NULL, 'Selo de Oito Sinais', 'Curto alcance', 'Rank –', NULL),
(676, 'Estrondo da Flor de Cerejeira', 'Sakura acumula chakra em ambos os punhos e, em seguida, ela desfere uma série de socos, trocando de punho a cada golpe, que liberam ondas de choques. Em questão de minutos, ela pode derrotar inúmeros inimigos.', NULL, NULL, 'Estrondo da Flor de Cerejeira', 'Impacto Certeiro da Flor de Cerejeira (桜花正中衝, Ōkaseichūshō)[2]', NULL, NULL, 'Curto alcance', NULL, NULL),
(677, 'Estrondoso Furacão de Fogo da Folha', 'Rock Lee abre cinco dos oito portões e golpeia seu inimigo com um chute tão rápido que sua perna pega fogo. Lee gira no ar, criando um espiral de fogo antes de trazer a perna para baixo através do centro e para o chão. O fogo explode para fora, explodindo qualquer inimigo em estreita proximidade.', '木ノ葉轟炎疾風', 'Konoha Gōen Shippū', 'Estrondoso Furacão de Fogo da Folha', 'Rugido do Vendaval de Chamas da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(678, 'Estátua Demoníaca do Caminho Exterior (jutsu)', 'O Caminho Naraka agarra o oponente pelo pescoço e invoca o Rei do Inferno, que leva o oponente para a sua boca, e morde-o e depois cospe-o para fora.', '外道魔像', 'Gedō Mazō', 'Estátua Demoníaca do Caminho Exterior', 'Ícone Herético', NULL, NULL, 'Curto alcance', NULL, NULL),
(679, 'Estátua Demoníaca do Caminho Exterior: Invocação de Choque de Relâmpagos', 'Usando suas saliências, a Estátua Demoníaca do Caminho Exterior atinge vários inimigos com raios de chakra devastadores.', '外道魔像・招雷光墜げどうまぞう•しょうらいこうつい', 'Gedō Mazō: Shiyouraikoutsui', 'Estátua Demoníaca do Caminho Exterior: Invocação de Choque de Relâmpagos', NULL, NULL, NULL, NULL, NULL, NULL),
(680, 'Execução por Beijo', 'Esta capacidade de Fūka lhe permitiu absorver todo chakra e técnicas de um oponente por beijá-lo, permitindo que ela possuísse várias afinidades elementares, bem como fazer respeitar os que ela já tem e ter acesso a todas as técnicas elementares de sua vítima. Depois que ela absorveu seu chakra, ela poderia roubar sua alma. Esta técnica também pode ser usada para determinar o que tem afinidades elementares do alvo ao lamber ele. Devido a um corpo sendo incapaz de lidar com tanto chakra, Fūka distribui o chakra ela absorveu em vários corpos, e usa o Mudança de Curso da Cor para mudar para outro, se necessário.', '死刑接吻しけいせっぷん', 'Shikei Seppun', 'Execução pelo Beijo', NULL, NULL, 'Beijo Ceifador', 'Curto alcance', NULL, NULL),
(681, 'Exemplo — Fūinjutsu', 'Iruka utiliza a Formação das Cordas de Luz para prender seu oponente e, em seguida, ele pega um papel explosivo e o coloca sobre a cabeça do oponente, causando uma explosão.', '模範的　封印術', 'Mohanteki — Fūinjutsu', 'Exemplo — Fūinjutsu', 'Jutsu de Selamento Exemplar', NULL, NULL, 'Curto alcance', NULL, NULL),
(682, 'Exemplo — Liberação de Fogo', 'Iruka acerta o oponente com vários ataques de kunai, chutes e uma cotovelada, finalizando com um chute para cima. Enquanto o oponente está no ar, Iruka lança uma rajada de fogo.', '模範的　火遁', 'Mohanteki — Katon', 'Exemplo — Liberação de Fogo', 'Estilo Fogo Exemplar', NULL, NULL, 'Curto alcance', NULL, NULL),
(683, 'Exemplo — Queda do Calcanhar', 'Iruka acerta diversos golpes de kunai e chutes no oponente enquanto no ar, para no fim mandá-lo caindo no chão com um chute.', '模範的　かかと落とし', 'Mohanteki — Kakato Otoshi', 'Exemplo — Queda do Calcanhar', 'Queda do Calcanhar Exemplar', NULL, NULL, 'Curto alcance', NULL, NULL),
(684, 'Exemplo — Técnica de Ocultação nas Folhas', 'Iruka faz com que algumas folhas apareçam ao seu redor. Se o oponente o atacar enquanto ele está oculto nas folhas, ele aparece atrás do mesmo e atira duas Fūma Shuriken.', '模範的　木ノ葉隠れの術もはんてき　このはがくれのじゅつ', 'Mohanteki — Konohagakure no Jutsu', 'Exemplo — Técnica de Ocultação nas Folhas', 'Jutsu da Folha Oculta Exemplar', NULL, NULL, NULL, NULL, NULL),
(685, 'Existência de Decapitação', 'Mifune executa um ataque poderoso, rapidamente puxando a sua espada e cortando seu oponente. Seu oponente só sente o ataque quando Mifune embainha sua espada.', '居斬', 'Ikiru', 'Existência de Decapitação', 'Decapitação do Residente', NULL, NULL, 'Curto alcance', NULL, NULL),
(686, 'Exorcismo', 'Após transformar seu corpo em papel, Konan paira no ar e começa a girar horizontalmente em direção do inimigo, infringindo dano. Suas asas servem como uma extensão do seu corpo, cortando o oponente um pouco mais longe de seu corpo.', '祓い', 'Harai', 'Exorcismo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(687, 'Explosão Gelel', 'Com um pedaço da Pedra de Gelel embutido na parte de trás de sua mão direita, Haido pode explodir objetos sem a necessidade de fazer contato físico com ele. Ele aprendeu tais poderes vendo o Livro de Gelel e felizmente conseguiu desenvolver o poder. Quando usado, Haido é capaz de causar uma pequena onda de choque à distância considerável.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(688, 'Explosão Kunoichi', 'Sakura lança uma shuriken no oponente, e Ino aproveita a distração para usar sua Técnica de Troca da Mente do Corpo, e, finalmente, Sakura acaba com o adversário com um soco poderoso.', NULL, NULL, 'Explosão Kunoichi', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(689, 'Explosão Massiva do Deserto', 'Enquanto em seu estado despertado e depois de executar o selo de mão necessário, Gaara cria um fluxo de vento e areia que protege-o de qualquer ataque.', '砂爆塊防さばくかたまりぼう', 'Sabaku Katamari Bō', 'Explosão Massiva do Deserto', 'Pilha de Areia: Defesa', NULL, NULL, 'Curto alcance', NULL, 'Tigre'),
(690, 'Explosão: Destruição da Montanha de Gelo', 'Mizore usa seu Braço de Cabo Retrátil para lançar a mão de metal para agarrar o oponente, colocá-lo no ar, e lançá-lo em uma montanha de gelo que quebra e partes do seu gelo caem sobre o oponente.', '爆裂・氷山崩し', 'Bakuretsu: Hyōzankuzushi', 'Explosão: Destruição da Montanha de Gelo', NULL, NULL, NULL, 'Ofensivo', NULL, 'Médio alcance'),
(691, 'Explosão da Armadilha de Selamento', 'Tsukino prepara uma armadilha de selamento no terreno que, quando pisada por um oponente, explode.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(692, 'Explosão da Foice da Doninha', 'Temari lança o oponente no ar, e Shikamaru chuta o oponente. Shikamaru em seguida, usa a sua Técnica de Imitação pela Sombra para paralisar o oponente. Então ele lança uma kunai que está acompanhada de um papel explosivo enquanto Temari usa a Técnica da Foice da Doninha para colocar o oponente num tufão e cortá-lo enquanto a kunai explode por causa do papel explosivo.', NULL, NULL, 'Explosão da Foice da Doninha', 'Explosão de Vento da Foice', NULL, NULL, 'Todos os alcances', NULL, NULL),
(693, 'Expressão Montanhosa', 'O usuário ataca seu oponente com dois socos antes de agachar e erguer sua cabeça, dando uma cabeçada fatal no queixo do oponente, lançando-o para uma distância considerável.', '山顔', 'Sangan', 'Expressão Montanhosa', 'Rosto das Montanhas', NULL, NULL, 'Curto alcance', NULL, NULL),
(694, 'Extremas Ondas Sonoras Decapitadoras', 'Zaku cria uma rajada enorme de vento cortante, ainda mais poderosa do que sua técnica Ondas Sonoras Decapitadoras, que é capaz de nivelar a área circundante, acabando com qualquer coisa em seu caminho.', '斬空極波', 'Zankūkyokuha', 'Extremas Ondas Sonoras Decapitadoras', NULL, 'Onda Supersônica Cortante', NULL, 'Curto a Médio alcance', 'Rank C', 'Juntar as Maos[2]'),
(695, 'Exército de Cobras', 'Depois do comando de Manda, cobras do tamanho de humanos saem do chão para dar o bote no oponente e derrubá-lo.', '軍陀ラ', 'Gundara', 'Exército de Cobras', 'Gregário de Cobras', NULL, NULL, 'Longo alcance', NULL, NULL),
(696, 'Facada Infernal', 'A Facada Infernal é a técnica mais forte do Terceiro Raikage, em que ele concentra uma quantidade considerável de chakra relâmpago apenas nas pontas dos dedos de uma única mão e em seguida, executa um movimento de empurrar. Os dedos juntos lhe permitem perfurar um alvo de uma maneira um pouco semelhante ao Chidori e ao Corte Relâmpago.', '地獄突きじごくずき', 'Jigokuzuki', 'Facada Infernal', NULL, 'Ponta Infernal', NULL, 'Curto alcance', 'Rank B', NULL),
(697, 'Facada da Agulha Negra', 'Pain corre em direção do oponente e esfaqueia com a Manifestação de Receptores de Chakra, revertendo seus controles.', '黒針突こくしんとう', 'Kokushintō', 'Facada da Agulha Negra', NULL, NULL, NULL, 'Curto Alcance', NULL, NULL),
(698, 'Facas do Chakra Devastador', 'Kagura imerge várias facas no solo, antes de serem ligadas a fios de chakra, puxando-as com uma enorme força, que faz o oponente ir para o alto. Enquanto o inimigo está no ar, Kagura restringe os movimentos do oponente com os fios de chakra, o puxando para si, ao mesmo tempo em que puxa suas facas novamente, o perfurando. Quando o oponente se mostra incapacitado, ele cai no chão.', NULL, NULL, NULL, 'Facas do Chakra Devastador', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(699, 'Faisão Caçador', 'O Faisão Caçador é uma técnica de Liberação de Fogo utilizada por Momoshiki Ōtsutsuki.', '雉狩きじかり', 'Kiji Kari', 'Faisão Caçador', 'Caça ao Faisão', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(700, 'Falcoaria', 'A técnica consiste no ataque com uma kunai com as duas mãos em movimento descendente. A técnica pode ser combinada com uma sequência de socos e chutes no oponente, a fim de atordoá-lo.', '鷹狩', 'Takagari', 'Falcoaria', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(701, 'Feixe de Luz da Destruição', 'Este é um um feixe de luz especialmente desenvolvido para lidar com inimigos que possuem poderes regenerativos, matando e desfazendo as células a partir disso. Dessa forma, Delta mira em seus oponentes e dispara feixes de luz a partir de seus olhos, a fim de desintegrar tudo o que toca.', '破壊光線', 'Hakai Kōsen', 'Feixe de Luz da Destruição', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(702, 'Ferramentas Manipuladas: Agulhas de Aço da Flor Selvagem', 'Usando um de seus pergaminhos menores, Tenten invoca várias esferas explosivas, que estão espalhados pelo campo de batalha e, em seguida, explodem depois de um tempo, disparando projéteis para fora ao mesmo tempo.', '操具・鋼針乱華そうぐ・こうしんらんか', 'Sōgu: Kōshin Ranka', 'Ferramentas Manipuladas: Agulhas de Aço da Flor Selvagem', 'Ferramenta Ninja: Explosão das Agulhas de Flor', NULL, NULL, NULL, NULL, NULL),
(703, 'Ferramentas Manipuladas: Ataque de Mil Lâminas', 'Esta técnica é muito semelhante à Ascensão dos Dragões Gêmeos, mas, nesta versão, Tenten só invoca inúmeras kunai de seus dois pergaminhos, que são ligados por linhas em seus dedos. Ela, então, joga todas as kunai ao mesmo tempo sobre o oponente.', '操具・襲千刃', 'Sōgu: Shū Senjin', 'Ferramentas Manipuladas: Ataque de Mil Lâminas', 'Lâmina dos Mil Golpes', NULL, NULL, 'Curto alcance', NULL, NULL),
(704, 'Ferramentas Manipuladas: Ataques das Esferas Mortais', 'Tenten puxa um pergaminho e gira em torno de si mesma. Várias bolas de metais pontiagudos irrompem o ataque de qualquer oponente próximo ou de desencorajá-la perto com ataques à distância.', '操具・死球撃', 'Sōgu: Shikyū Geki', 'Ferramentas Manipuladas: Ataques das Esferas Mortais', 'Ferramenta Ninja: Golpe da Esfera Mortal', NULL, NULL, 'Curto alcance', NULL, NULL),
(705, 'Ferramentas Manipuladas: Bola Gigantesca de Ferro', 'Tenten atira cinco bolas de ferro contra o oponente, como distração e então desenrola o seu pergaminho gigante e invoca uma esfera metálica enorme, coberta de espinhos, assemelhando-se a ponta de uma clava ou à Jidanda. Manobrando-a arma com fios de aço conectados em suas mãos, ela bate a arma contra o inimigo distraído.', '操具・極大鉄球', 'Sōgu: Kyokudai Tekkyū', 'Ferramentas Manipuladas: Bola Gigantesca de Ferro', 'Controle de Armas: Bola Gigantesca de Ferro', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(706, 'Ferramentas Manipuladas: Cadeia do Desastre Celestial', 'Uma versão muito mais forte da Ascensão dos Dragões Gêmeos. Tenten lança seu grande pergaminho ao céu, acima de seu oponente, e invoca dezenas de armas diferentes a partir dele, formando uma chuva de armas ninja, capaz de acertar o inimigo e seus arredores. As armas se movem em uma velocidade tão grande que aparentemente deixam manchas brancas no céu, que combinando com o grande alcance, a chuva de armas torna essa técnica muito difícil escapar.', '操具・天鎖壊そうぐ・てんささい', 'Sōgu: Tensasai', 'Ferramentas Manipuladas: Cadeia do Desastre Celestial', NULL, NULL, 'Controle de Arma: Tensasai', 'Todos os alcances', NULL, NULL),
(707, 'Ferramentas Manipuladas: Desastre do Aço Celestial', 'Similar a Ferramentas Manipuladas: Cadeia do Desastre Celestial, Tenten joga e desenrola um grande pergaminho sobre o oponente, invocando incontáveis kunai que chovem sobre ele. Ela, então, invoca quatro grandes cadeias que acertam o oponente, forçando sua queda em direção ao solo.', '操具・天鋼災', 'Sōgu: Tenkōsai', 'Ferramentas Manipuladas: Desastre do Aço Celestial', 'Ferramenta Ninja: Desastre do Metal Celestial', NULL, NULL, 'Todos os alcances', NULL, NULL),
(708, 'Ferramentas Manipuladas: Desempenho do Ataque das Mil Lâminas', 'Tenten corre em zigue-zague e, em seguida, pula pro alto e abre um pergaminho no ar, liberando uma variedade de lâminas, que são dispersadas no oponente.', '操具・襲演千刃', 'Sōgu: Shūen Senjin', 'Ferramentas Manipuladas: Desempenho do Ataque das Mil Lâminas', 'Ferramenta Ninja: Golpes das Mil Lâminas', NULL, NULL, 'Todos os alcances', NULL, NULL),
(709, 'Ferramentas Manipuladas: Dez Mil Lâminas Selvagens Celestiais', 'Tenten invoca várias armas ligadas a fios de aço até as pontas de seus dedos de um dos seus pergaminhos, direcionando-os para o seu oponente em uma formação corrente. Após a última arma atingir o oponente, Tenten usa os fios para fazer as armas voarem acima dele. Movendo os dedos para fazer as armas girarem em torno de seu oponente no ar, ela direciona as armas para baixo para acertar em cheio seu oponente.', '操具・天刃乱万', 'Sōgu: Tenjin Ranman', 'Ferramentas Manipuladas: Dez Mil Lâminas Selvagens Celestiais', 'Ferramenta Ninja: Milhares de Lâminas do Caos', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(710, 'Ferramentas Manipuladas: Dramatização da Andorinha Giratória', 'Tenten bate repetidamente em seu oponente com um par de tonfas, em seguida, joga um pergaminho no ar para invocar um bō para continuar a bater no oponente e, por fim, derrubá-lo no chão. Quando o oponente está caído no chão, ela em seguida, invoca uma grande clava e esmaga-o com ela.', '操具・廻燕劇', 'Sōgu: Kaien Geki', 'Ferramentas Manipuladas: Dramatização da Andorinha Giratória', 'Ferramenta Ninja: Show da Andorinha Giratória', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(711, 'Ferramentas Manipuladas: Esfera Explosiva', 'Tenten libera múltiplas Esferas Explosivas do seu pergaminho, que explode quando entra em contato.', '操具・爆砕玉そうぐ・ばくさいだま', 'Sōgu: Bakusaidama', 'Ferramentas Manipuladas: Esfera Explosiva', 'Ferramenta Ninja: Explosão da Esfera', NULL, NULL, NULL, NULL, NULL),
(712, 'Ferramentas Manipuladas: Grande Protuberância de Ferro', 'Tenten abre um enorme pergaminho em círculo, então liberando uma bola de ferro gigante anexada em uma corrente que, em seguida, ela lança sobre seu oponente, esmagando-o. Após o esmagamento de seu oponente, a bola de metal volta para o seu pergaminho gigante.', '操具・大鉄塊そうご・だいてっかい', 'Sōgu: Dai Tekkai', 'Ferramentas Manipuladas: Grande Protuberância de Ferro', 'Ferramenta Ninja: Gigante Protuberância de Ferro', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(713, 'Ferramentas Manipuladas: Meteoro Anexado', 'Tenten invoca uma manriki-gusari de um de seus pergaminhos e a usa para conter o adversário. Quando seu oponente está contido, ela usa o pergaminho para lançar uma barragem de armas nele e, finalmente, bate-as no chão com o manriki-gusari.', '操具・縛流星そうぐ・ばくりゅうせい', 'Sōgu: Baku Ryūsei', 'Ferramentas Manipuladas: Meteoro Anexado', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(714, 'Ferramentas Manipuladas: Muro da Lâmina Flutuante', 'Tenten abre dois dos seus vários pergaminhos e invoca uma enxurrada de armas de diferentes aspectos para cair sobre seu oponente, fazendo-o ir pro ar após o grande impacto. Ela, em seguida, envolve as pernas de seu oponente em correntes e puxá-o para baixo, lançando-o rapidamente pro chão.', '操具・浮刃壁', 'Sōgu: Fujin Heki', 'Ferramentas Manipuladas: Muro da Lâmina Flutuante', 'Ferramenta Ninja: Muro da Lâmina Flutuante', NULL, NULL, 'Curto alcance', NULL, NULL),
(715, 'Ferramentas Manipuladas: Performance do Ataque de Mil Lâminas', 'Tenten corre em volta do oponente abrindo um pergaminho no ar, liberando uma variedade de armas brancas que são então dirigidas a seu oponente.', '操具・襲演千刃', 'Sōgu: Shūen Senjin', 'Técnica das Ferramentas Manipuladas: Performance do Ataque de Mil Lâminas', NULL, NULL, NULL, NULL, NULL, NULL),
(716, 'Ferramentas Manipuladas: Pontapé da Lâmina', 'Tenten invoca várias armas que automaticamente mostram-se estarem ligadas a fios de aço, movimentando-as de forma útil e inteligente. Ela, em seguida, vai pro alto, e gira para trás, disparando rapidamente várias armas ninja em seu oponente, assemelhando-se à tiros de metralhadora.', '操具・奏刃蹴', 'Sōgu: Sōninshū', 'Ferramentas Manipuladas: Pontapé da Lâmina', 'Ferramenta Ninja: Pontapé da Lâmina', NULL, NULL, 'Longo alcance', NULL, NULL),
(717, 'Ferrolho Relâmpago', 'Killer B salta no ar com suas sete espadas e ataca o oponente enquanto revestido em chakra relâmpago.', '雷斗忍具菩流斗', 'Raitoningu Baruto', 'Luta da Ferramenta Ninja Relâmpago Fluindo Luta Rosa', 'Ferrolho Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(718, 'Fiandeiro Violento', 'Karin corre na direção de seu oponente; em seguida, ela espera um momento até que o mesmo fique com a guarda baixa, executando uma série de tapas atordoadores.', NULL, NULL, 'Fiandeiro Violento', 'Violenta Cabeça Giratória', NULL, NULL, 'Curto alcance', NULL, NULL),
(719, 'Fios Crescentes', 'Kagura atira várias facas ligadas a fios de chakra a partir do solo, como um gêiser.', NULL, NULL, NULL, 'Fios Crescentes', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(720, 'Fios de Chakra', 'Fios de Chakra (チャクラの糸, Chakura no Ito) é uma técnica que normalmente é usada por marionetistas que lhes permite controlar suas marionetes em batalha.', 'チャクラの糸', 'Chakura no Ito', 'Fios de Chakra', NULL, 'Linhas de Chakra', NULL, 'Curto a Médio alcance', NULL, NULL),
(721, 'Fios de Chakra (Fūma)', 'Fios de Chakra é uma técnica homicída-suicída secreta do clã Fūma.', 'チャクラの糸', 'Chakura no Ito', 'Fios de Chakra', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(722, 'Fique Longe de Mim!', 'Konohamaru salta sobre seu oponente, acertando-o com alguns socos e chutes.', 'こっち来るなコレ!', 'Kotchikurunakore!', 'Fique Longe de Mim!', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(723, 'Flash', 'Um rápido ataque à distância, onde o usuário balança sua espada em um alvo após revesti-la em chakra, liberando um chakra crescente acentuado no arco que a lâmina foi balançada.', '一閃', 'Issen', 'Flash', NULL, 'Corte Diagonal', 'Feixe de Lux', 'Todos os alcances', NULL, NULL),
(724, 'Flash Amarelo', 'Enquanto no Modo de Chakra do Nove-Caudas, o usuário é capaz de se mover a uma velocidade tremenda. A velocidade de Naruto ao usar essa técnica é tão grande que Killer B teve problemas para determinar se ele tinha usado a Técnica de Cintilação Corporal quando ataca Kisame Hoshigaki. Ele poderia até mesmo ultrapassar a velocidade máxima de A, bem como o relâmpago extremamente rápido do Terceiro Raikage antes que este tivesse qualquer chance de reagir.', '黄色い閃光きいろいせんこう', 'Kiiroi Senkō', 'Flash Amarelo', 'Flash Amarelo da Folha[2]', NULL, NULL, NULL, NULL, NULL),
(725, 'Flash Relâmpago', 'O usuário infunde chakra de relâmpago à uma shuriken, duplicando seu poder original de corte, o suficiente para cortar metais ou mutilar uma pessoa com relativa facilidade.', '雷閃', 'Raisen', 'Flash Relâmpago', 'Centelha Relâmpago', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(726, 'Flash Relâmpago do Combo do Leão', 'Sasuke lança seu oponente no ar com sua espada de Kusanagi e pula em cima dele rapidamente, não dando chances de ataque ou defesa, e, em seguida, começa a socar e chutar seu oponente, ainda estando no ar. Depois de sua combinação, ele desce pro chão, logo depois de ter lançado seu oponente ferozmente pro chão, levantando seu Chidori para cima e eletrocutando o adversário que cai nele.', '雷光獅子連弾', 'Raikō Shishi Rendan', 'Flash Relâmpago do Combo do Leão', 'Barragem de Leões Relâmpago', NULL, NULL, NULL, NULL, NULL),
(727, 'Flash da Folha', 'Rock Lee se move em alta velocidade fazendo com que várias folhas se espalhem ao redor do campo de batalha e em seguida desequilibra seu oponente e o leva para o ar, ele então chuta seu oponente no estômago e, em seguida, o chuta no rosto e, novamente, ele o chuta no estômago, jogando-o contra o chão e causando grandes danos.', '木の葉閃光', 'Konoha Senkō', 'Flash da Folha', 'Luz da Folha Espiral', NULL, NULL, 'Curto alcance', NULL, NULL),
(728, 'Flash do Pé Rotativo', 'Asuma acerta uma série de socos e ataques terminando com um chute giratório, fazendo com que o oponente seja lançado no ar.', '閃旋脚', 'Sensenkyaku', 'Flash do Pé Rotativo', 'Flash do Chute Espiral', NULL, NULL, 'Curto alcance', NULL, NULL),
(729, 'Flash do Telegrama', 'Com sua equipe emprestando-lhe chakra, Akaboshi pode bater a mão no chão e causar um raio para se espalhar de sua mão para debaixo do oponente.', '電報石火', 'Denpō Sekka', 'Flash do Telegrama', NULL, NULL, 'Denko Sekka', 'Curto a Médio alcance', NULL, 'Coelho + Rato + Cavalo + Carneiro simultâneamente'),
(730, 'Flauta Demoníaca: Correntes do Som Fantasma', 'Uma ilusão que depende do som da flauta demoníaca. Quem ouve será atacado pela sensação viva de que teve os seus membros presos com cordas resistentes, tornando-se incapaz de se mover livremente. Mesmo com a intervenção física de uma força externa, o corpo é roubado de toda mobilidade, e a própria consciência progressivamente desaparece. Ao mesmo tempo, eles são alimentados por alucinações mórbidas, o que torna também possível executá-las mentalmente. Dado que os meios de transmissão são baseados no som, que tem uma gama de eficácia, no topo do qual ele pode ser aplicado mesmo sem o conhecimento da posição do alvo, o que contribui para uma técnica muito conveniente.', '魔笛・夢幻音鎖', 'Mateki: Mugen Onsa', 'Flauta Demoníaca: Correntes do Som Fantasma', NULL, 'Flauta Demoníaca: Correntes Ilusórias de Som', 'Flauta Demoníaca: Correntes da Fantasia', 'Todos os alcances', 'Rank B', NULL),
(731, 'Flauta Demoníaca: Guerreiros Ilusionados Manipulados por Melodia', 'Usando sua Flauta Demoníaca, Tayuya controla seus três Doki à distância sem o uso de palavras. Ao lançar notas específicas, seus Doki agem e atacam de formas específicas. Tayuya também pode alterar a melodia se o oponente descobrir a sequência das notas.', '魔笛・幻武操曲まてき・げんぶそうきょく', 'Mateki: Genbusō Kyoku', 'Flauta Demoníaca: Guerreiros Ilusionádos Manipulados pela Melodia', NULL, 'Flauta Demoníaca: Manipulação Musical dos Guerreiros Fantasma', NULL, NULL, 'Rank B', NULL),
(732, 'Flauta Demoníaca: Notas Flutuantes', 'Tocando sua Flauta Demoníaca, Tayuya cria várias notas flutuantes, de colorações azuladas, que ficam envoltos por uma onda de som transparente circular, e, então, lança-as contra seu oponente. Ao entrar em contato com o oponente, as notas explodem.', '魔笛・浮音まてき・ふおん', 'Mateki: Fuon', 'Flauta Demoníaca: Notas Flutuantes', 'Flauta Demoníaca: Som Flutuante', NULL, NULL, 'Curto alcance', NULL, NULL),
(733, 'Flauta Demoníaca: Onda Espiritual Fantasma', 'Usando sua Flauta Demoníaca, Tayuya invoca os espíritos materializados que ela armazena em seus Doki, sem a invocação dos Doki reais. Os espíritos perseguem seu oponente, drenando sua energia física e atordoando-o se o atingir.', '魔笛・幻霊波', 'Mateki: Genreiha', 'Flauta Demoníaca: Onda Espiritual Fantasma', 'Flauta Demoníaca: Onda Fantasma', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(734, 'Flauta Demoníaca: Três Subordinados do Enterro da Melodia', 'Enquanto em sua segunda fase do seu selo amaldiçoado, Tayuya aprisiona seu oponente em um genjutsu com sua flauta, e enquanto eles estão imobilizados, os Doki ataca seu oponente por baixo.', '魔笛・三従葬曲', 'Mateki: Sanjū Sōkyoku', 'Flauta Demoníaca: Três Subordinados do Enterro da Melodia', 'Flauta Demoníaca: Trio Requiem', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(735, 'Flecha Flamejante da Incrível Habilidade', 'Depois da modificação do corpo feita pelo Caminho Asura, esta técnica lança uma parte destacada de um braço, usando-o como uma arma de projétil. Com uma explosão de pequena escala, causada pelo chakra coletado na extremidade cortada do pulso, a porção da mão ganha poder de propulsão e voa em linha reta com uma força tremenda. Seu poder destrutivo é grande o suficiente para quebrar facilmente através mesmo da espessura de um escudo rochoso. A porção da mão é protegida com chakra, o que lhe permite manter-se incólume. O chakra no punho puxa a mão para trás como um ímã, recolocando-a novamente no corpo.', '怪腕ノ火矢かいわんのひや', 'Kaiwan no Hiya', 'Flecha Flamejante da Incrível habilidade', 'Canhão de Braço', NULL, NULL, 'Todos os alcances', 'Rank –', NULL),
(736, 'Flecha de Indra', 'A Flecha de Indra, de acordo com Sasuke, é seu ataque mais poderoso.', 'インドラの矢インドラのや', 'Indora no Ya', 'Flecha de Indra', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(737, 'Flechas Aladas Celestiais', 'Ao espremer cada pingo de chakra contido em seu corpo, Kinshiki manifesta múltiplas armas de chakra que ele manipula telecineticamente próximas de si. Ao seu comando, uma torrente de armas é disparada em rápida velocidade rasgando tudo em seu caminho.', '天羽々矢', 'Amanohahaya', 'Flechas Aladas Celestiais', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(738, 'Flor Celestial', 'Kimimaro salta e cai contra o oponente com os picos de ossos que crescem em seu braço, finalizando com um ataque de sua broca de ossos.', '天花', 'Tenka', 'Flor Celestial', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(739, 'Flor Explosiva', 'Ino joga um punhado de flores que são acompanhados de papéis explosivos, os quais ficam camuflados entre as pétalas, explodindo no momento do impacto contra o adversário.', '起爆花', 'Kibaku Hana', 'Flor Explosiva', 'Flor Detonante', NULL, NULL, 'Curto a Médio alcance', NULL, NULL);