INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(740, E'Flor de Cerejeira Única', E'Sakura é capaz de usar essa técnica graças ao seu preciso controle de chakra, que ela acumula em seu punho e o expulsa durante um soco. Com isso, ela pode desferir socos em pleno ar, liberando poderosas ondas de choque que podem destruir e repelir tudo o que estiver em seu caminho.', NULL, NULL, E'Flor de Cerejeira Única', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(741, E'Flor de Teia de Aranha', E'Uma rede adesiva especial lançada da boca de Kidōmaru. Estas redes, são criadas com o chakra, para manter suas presas e não deixá-las ir. As redes em si são muito pequenas, mas podem ser criadas em uma fração de segundo, por isso aumenta a taxa de captura por cuspir várias em uma linha. Esta é uma técnica útil contra vários inimigos. Qualquer um pego pelas duas redes em camadas são rebocados a uma parede ou árvore atrás deles.', E'蜘蛛巣花', E'Kumo Sōka', E'Flor da Teia de Aranha', NULL, E'Flores de Teia de Aranha', NULL, E'Curto a Médio alcance', NULL, NULL),
(742, E'Flores Dançantes', E'Kurenai cria diversas pétalas de flor de cerejeira ao desfazer seu clone, que gira ao seu redor, criando uma barreira de pétalas de cerejeira, oportunizando para Kurenai que golpeia o oponente diversas vezes até desmaiá-lo.', E'花卉舞踊かきぶよう', E'Kaki Buyō', E'Flores Dançantes', E'Dança da Flor', NULL, NULL, E'Curto alcance', NULL, NULL),
(743, E'Fluxo Cortante do Relâmpago Sábio', E'Hagoromo converte chakra em eletricidade, podendo criar um fluxo elétrico poderoso o suficiente para quebrar uma grande rocha quando infundido com a energia natural.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(744, E'Fluxo da Cachoeira de Areia Movediça', E'Esta técnica cria uma enorme quantidade de areia na forma de uma onda, cobrindo e \'afogando\' o oponente em um oceano de areia. A partir da areia que Gaara carrega em sua cabaça, ele mói as rochas e minerais no subsolo em um grande mar de areia e manipula-a como um tsunami que engolfa absolutamente todas as coisas em sua linha de visão. Este mar furioso causa uma série de danos por si só, mas pode-se tirar mais proveito da areia para um segundo e um terceiro assalto consecutivo. Gaara pode controlar a areia, permitindo-lhe atacar seu oponente, enquanto ele tenta fugir da onda. Esta técnica tem o efeito adicional de mudar a paisagem. Ela é geralmente seguida por uma técnica de esmagamento de área para matar o oponente preso.', E'流砂瀑流りゅうさばくりゅう', E'Ryūsa Bakuryū', E'Fluxo da Cachoeira de Areia Movediça', E'Tsunami de Areia', E'Tormenta de Areia', E'Tsunami de Areia', E'Todos os alcances', NULL, E'Bater as Maos → Javali → Macaco → Rato → Macaco → Cavalo → Cão → Carneiro → Cavalo → Juntar as Mãos'),
(745, E'Fluxo da Palma de Madeira e Relâmpago', E'Yamato ataca o oponente com a Liberação de Madeira: Técnica da Grande Floresta e em seguida Kakashi ataca a madeira com Corte Relâmpago que atinge o oponente.', E'雷電木流掌らいでんもくりゅうしょう', E'Raiden Mokuryūshō', E'Fluxo da Palma de Madeira e Relâmpago', NULL, NULL, NULL, NULL, NULL, NULL),
(746, E'Fluxo da Parede de Areia', E'Tirando proveito de sua areia, Gaara é capaz de criar três barragens simultâneas em seu oponente, e em seguida, mata-o ao criar três paredes flutuantes de areia que vão para esmagá-lo.', E'流壁砂', E'Ryūhekisa', E'Fluxo da Parede de Areia', E'Parede de Areia Fluída', NULL, NULL, E'Curto alcance', NULL, NULL),
(747, E'Fluxo de Chakra', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(748, E'Fluxo de Tinta', E'É um ninjutsu característico de Sai. Derramando sua tinta em vez de usá-la para desenhar, Sai pode criar uma grande quantidade de criaturas como um ninho de cobras para atacar o alvo. Se o oponente provar forte o suficiente para quebrar a liga se contido, eles podem reformar em torno do alvo, tornando inútil lutar.', E'墨流し', E'Sumi Nagashi', E'Fluxo de Tinta', NULL, NULL, E'Arte Ninja: Jato de Tinta', E'Curto a Médio alcance', NULL, NULL),
(749, E'Fogaréu do Gato de Chamas', E'Yugito, enquanto totalmente transformada em Matatabi, dispara uma enorme bola de fogo espectral feita de chakra fogo, que explode ao contato. Estas explosões são muito destrutivas e podem reduzir seu entorno a escombros.', E'猫炎咆火', E'Byōen Hōka', E'Fogaréu do Gato de Chamas', NULL, NULL, NULL, NULL, NULL, NULL),
(750, E'Fogo Rápido', E'Kusune usa Tsurukame e lança enormes quantidades de senbon.', E'連射', E'Rensha', E'Fogo Rápido', NULL, NULL, NULL, E'Médio Alcance', NULL, NULL),
(751, E'Fogos de Artificio Pretos', E'Ao bater com seu Shakujō no estômago de seu oponente, Obito captura ele com suas Esferas da Busca da Verdade, prende-o em uma maior ao juntar todas, comprime essa esfera várias vezes e libera em uma explosão de energia escura detonando todo o conteúdo do seu interior.', E'黒花火', E'Kuro Hanabi', E'Fogos de Artificio Pretos', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(752, E'Foguete Rasengan', E'Mecha-Naruto usa seu Rasengan, que é de uma cor roxa clara, depois impulsiona-se em direção ao oponente. Quando o Rasengan atinge o oponente, Mecha-Naruto \'dispara\' sua mão junto com o Rasengan, prejudicando ainda mais o oponente.', E'ロケット螺旋丸', E'Roketto Rasengan', E'Foguete de Esfera Espiral', E'Foguete Rasengan', NULL, NULL, E'Curto alcance', NULL, NULL),
(753, E'Forma Kaima', E'Esta técnica, desenvolvida por Amachi por causa do interesse de Orochimaru em criar ninjas supremos que combatessem em baixo d\'água, muda drasticamente a aparência do usuário, dando-lhes uma pele verde escamosa, barbatanas e guelras.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(754, E'Formação', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(755, E'Formação A', E'A Formação A é uma tática de batalha utilizada por Itachi Uchiha e Jūzō Biwa.', E'フォーメーションA', E'Fōmēshon A', E'Formação A', NULL, NULL, NULL, NULL, NULL, NULL),
(756, E'Formação Asa da Garsa', E'Após saltar no ar, o usuário ataca o inimigo em uma formação parecida com asas de garsa. Depois de estar em torno do inimigo, o usuário morde os membros do oponete tornando-o incapaz de se mover e fazendo este despencar até o chão.', E'鶴翼の陣', E'Kakuyoku no Jin', E'Formação Asa da Garsa', NULL, NULL, NULL, NULL, NULL, NULL),
(757, E'Formação Asa da Garsa (Oito Portões)', E'Para realizar esta técnica, pelo menos seis ou mais usuários bem treinados em taijutsu dos oito portões devem abrir simultaneamente o sexto portão. Este ataque combinado é poderoso o suficiente para obliterar a metade de um enorme meteorito vindo da Lua.', E'鶴翼の陣かくよくのじん', E'Kakuyoku no Jin', E'Formação Asa da Garsa', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(758, E'Formação B', E'A Formação B é uma tática de batalha utilizada por Itachi Uchiha e Jūzō Biwa.', E'フォーメーションB', E'Fōmēshon B', E'Formação B', NULL, NULL, NULL, NULL, NULL, NULL),
(759, E'Formação C', E'A Formação C é uma tática de batalha utilizada por Itachi Uchiha e Jūzō Biwa, caso este último seja pego pelos ninjas de Kirigakure.', E'フォーメーションC', E'Fōmēshon C', E'Formação C', NULL, NULL, NULL, NULL, NULL, NULL),
(760, E'Formação Circular', E'O Hōen no Jin (方円の陣, Formação Circular) é uma manobra suplementar usada pelos ninkens de Kakashi, que envolve os cães em pé, formando um círculo. Eles também podem deixar um cão no meio para ser o foco de ataque do adversário, permitindo-lhes realizar outras técnicas.', E'方円の陣ほうえんのじん', E'Hōen no Jin', E'Formação Circular', NULL, NULL, NULL, NULL, NULL, NULL),
(761, E'Formação Escura do Inferno', E'O Quarteto do Som ativa seu segundo nível do selo amaldiçoado e cria uma formação de barreira de quatro vias que libera uma energia escura. Os quatro shinobi então selam o oponente dentro da formação de barreira, criando uma explosão em erupção de energia escura não deixando nenhum vestígio do inimigo.', E'奈落冥陣ならくめいじん', E'Naraku Meijin', E'Formação Escura do Inferno', E'Arte Ninja: Formação do Submundo', NULL, NULL, E'Médio alcance', NULL, NULL),
(762, E'Formação Ino–Shika–Chō', E'Formação Ino–Shika–Chō são formações usadas pelos membros dos clãs Yamanaka, Nara e Akimichi projetadas devido à sua longa história de trabalho em conjunto.', E'フォーメーション猪鹿蝶', E'Fōmēshon Ino–Shika–Chō', E'Formação Javali–Veado–Borboleta', NULL, NULL, E'Formação Ino–Shika–Chō', NULL, NULL, NULL),
(763, E'Formação Jōseki de Konoha', E'Izumo usa a Liberação de Água: Captura do Campo de Xarope de Amido para criar uma grande poça de água. Após isso, ele, ao lado de Asuma e Kotetsu, ocultam-se na água a fim de chegar perto de seu oponente. Os três então pulam para fora da água e acertam seu oponente por trás de suas costas, com Izumo e Kotetsu atacando com uma longa kunai e uma clava simultaneamente, seguidos por Asuma acertando o oponente com sua Lâmina de Chakra fortalecida pela Andorinha Voadora. Em seguida, Shikamaru utiliza sua Técnica do Puxão pela Sombra para puxar seus companheiros de equipe, permitindo a Izumo e Kotetsu atacar o oponente mais uma vez, criando um pilar de água, que endurece após o golpe, seguindo com Asuma, cortando através do pilar e do oponente.', E'木ノ葉定石の陣このはじょうせきのじん', E'Konoha Jōseki no Jin', E'Formação Tática da Folha', E'Formação Padrão da Folha', NULL, NULL, E'Todos os alcances', NULL, NULL),
(764, E'Formação Manji', E'Esta é uma formação muito básica que é usada para proteger um ponto central, para se defender contra ataques vindos de localizações múltiplas, ou para defender um grupo contra um ataque que pode vir de qualquer ângulo desconhecido. Seu nome deriva da forma de um manji, 卍, como a formação requer uma pessoa voltada para fora em todos os quatro pontos, onde o ninja fica de costas um para os outros. Cada pessoa é responsável por proteger suas costas a área em frente deles e para a esquerda, como o manji sugere. Esta formação tem um ponto cego: o centro.', E'卍の陣まんじのじん', E'Manji no Jin', E'Formação Manji', NULL, NULL, E'Formação de Batalha Manji', E'Curto alcance', NULL, NULL),
(765, E'Formação Uzumaki', E'Naruto cria doze clones das sombras, cada um armado com uma kunai, formando um círculo defensivo, com Naruto no meio, tudo preparado para o inimigo atacar. Quando o inimigo se move, os clones de Naruto jogam a kunai no inimigo.', E'うずまきの陣うずまきのじん', E'Uzumaki no Jin', E'Formação do Redemoinho', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(766, E'Formação da Areia Extensa de Cegueira: Ruído', E'O usuário espalha várias partículas de areia em uma área definida, impedindo a habilidade do inimigo de sensorear alvos. No entanto, esta técnica aparenta funcionar apenas em alvos que estão focados no usuário da técnica.', E'盲漠砂陣・濃偉砂', E'Mōbaku Sajin: Noizu', E'Formação da Areia Extensa de Cegueira: Grande Areia Espessa', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(767, E'Formação da Areia Polarizada: Camuflagem de Areia Quente', E'O usuário infunde chakra colocando ambas as mãos no solo, fazendo com que partículas de areia polarizem, o que permite o dobramento da luz. As partículas partem na direção dos companheiros do usuário, cobrindo e tornando-os invisíveis e muito mais difíceis de detectar, com exceção de um shinobi tipo sensor. A técnica aparenta exigir uma grande quantidade de tempo para aperfeiçoá-la para poder acumular chakra.', E'偏光砂陣・熱砂迷彩', E'Henkō Sajin: Nessa Meisai', NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Cobra'),
(768, E'Formação da Chama Uchiha', E'Depois de manifestar pequenas chamas em cada dedo de uma das mãos, o usuário, em seguida, bate a palma da mão no chão, resultando na criação de uma barreira cilíndrica envolvendo um local de sua escolha, protegendo qualquer coisa contida dentro e queimando tudo o que entra em contato com o exterior da barreira.', E'うちは火炎陣', E'Uchiha Kaenjin', E'Formação da Chama Uchiha', NULL, E'Círculo de Fogo dos Uchiha', NULL, E'Longo alcance', E'Rank B', NULL),
(769, E'Formação da Shuriken Uchiha: Relâmpago da Primavera', E'Sarada envia um fluxo de fogo que joga o oponente para o alto. Ela então salta para cima da nuvem de fumaça e, com seu Sharingan, desfere um ataque com várias shuriken que recocheteiam umas nas outras e acertam o alvo. Para finalizar, ela acumula chakra infundido com a natureza relâmpago em seu punho e usa o Impacto da Flor de Cerejeira em seu alvo, que o manda para o chão, causando grande destruição no impacto.', E'うちは手裏剣陣・春雷うちはしゅりけんじん・しゅんらい', E'Uchiha Shurikenjin: Shunrai', E'Formação da Shuriken Uchiha: Relâmpago da Primavera', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(770, E'Formação das Cordas de Luz', E'Esta barreira é projetada para impedir que alguém saia de seus limites. Uma vez dentro de um determinado intervalo da pessoa, o usuário ergue a barreira sob os pés tornando qualquer pessoa dentro dos selos incapaz de escapar. Apesar de sua força, Naruto Uzumaki foi capaz de dominá-la facilmente com o seu Modo de Chakra do Nove-Caudas.', E'一糸灯陣いっしんとうじん', E'Isshi Tōjin', E'Formação das Cordas de Luz', NULL, NULL, NULL, E'Curto alcance', NULL, E'Restringir: Selo do Confronto'),
(771, E'Formação das Múltiplas Cordas de Luz', E'Ao ter mais de uma pessoa usando a Formação das Cordas de Luz, os efeitos da técnica são amplificadas, permitindo, assim, mesmo usuários mais fracos, capturar com sucesso um adversário mais forte se tiverem usuários suficientes.', E'多重一糸灯陣たじゅういっしとうじん', E'Tajū Isshi Tōjin', E'Formação de Múltiplas Cordas de Luz', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(772, E'Formação das Quatro Chamas Violeta', E'Uma sólida e forte formação de barreira, realizada por quatro shinobi. Consiste em uma formação quadrada, os usuários estão coberto de todos os lados. Os rostos da barreira são feitas de chamas roxas. A barreira não permite a ninguém entrar ou sair enquanto o ninja está ileso. O corpo de alguém tocá-la será imediatamente envolvido em chamas. Além disso, não há nenhuma maneira de destruir a formação de dentro da barreira, uma vez que as quatro pessoas têm também uma barreira no interior protegendo-os. Sair é extremamente difícil.', E'四紫炎陣', E'Shishienjin', E'Acampamento de Batalha das Quatro Chamas Violeta', NULL, E'Paredes das Chamas Púrpuras', E'Arte Ninja: Formação Quatro Chamas', E'Médio alcance', E'Rank B', E'Cobra → Bater as maos'),
(773, E'Formação de Areia: Shisa', E'O usuário gera uma ou múltiplas criaturas em forma de leão para atacar ou dominar um alvo. Elas aparentemente se dispersam facilmente se forem atingidas forte o suficiente.', E'砂陣・止威砂', E'Sajin: Shīsā', E'Formação de Areia: Autoridade da Areia Supressora', NULL, NULL, E'Formação de Areia: Shisa', E'Curto a Médio alcance', NULL, E'Cobra → Cao → Carneiro'),
(774, E'Formação de Avalanche de Pedras', E'Vários shinobi entram em uma formação em que estão de pé sobre os ombros uns dos outros, e em seguida, avançam em direção ao inimigo, isolando os adversários para que eles não possam escapar.', E'岩雪崩の陣', E'Iwa Nadare no Jin', E'Formação de Avalanche de Pedras', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(775, E'Formação de Barricada Canina', E'Com a Formação de Barricada Canina (犬壁の陣, Inukabe no Jin), o usuário salta para dentro de um circulo de ar feito por outro usuário. Isto forma um bloqueio impedindo a visão dos oponentes, e também lhes permite manobrar no ar.', E'犬壁の陣', E'Inukabe no Jin', E'Formação de Barricada Canina', NULL, NULL, NULL, NULL, NULL, NULL),
(776, E'Formação de Dez Mil Cobras', E'Com esta técnica, um incontável número de cobras invocadas rasteja para fora da boca de Orochimaru e caça o inimigo. Com tal número esmagador, um verdadeiro \'muro de cobras\' é formado, fazendo defesa e evasão inefetivos, e além disso as cobras se tornam uma parede defensiva e podem bloquear praticamente qualquer ataque frontal. O inimigo não pode fazer nada, além de ser sepultado pelas cobras. Elas podem morder o inimigo com suas presas venenosas e abrir a boca para estender lâminas como a Kusanagi, tornando-as ainda mais mortais.', E'万蛇羅ノ陣', E'Mandara no Jin', E'Formação de Dez Mil Cobras Sedosas', NULL, NULL, NULL, E'Todos os alcances', E'Rank B', NULL),
(777, E'Formação do Círculo Explosivo', E'Hanzō executa um giro com a sua Kusarigama, liberando um círculo de papéis explosivos no processo, o qual, em seguida, explode no impacto com um alvo.', E'爆円陣', E'Bakuenjin', E'Formação do Círculo Explodindo', E'Circulo Explosivo', NULL, NULL, E'Curto alcance', NULL, NULL),
(778, E'Formação do Eixo', E'O usuário envia seus insetos para atacar o alvo em um movimento em espiral, a fim de parar o inimigo de retaliação.', E'紡錘の陣ぼうすいのじん', E'Bōsui no Jin', E'Formação do Eixo', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(779, E'Formação do Furacão Inigualável', E'Sasuke chuta o oponente. Ele, então, corre para trás e soca o adversário. Em seguida, ele salta no ar e executa um pontapé de machado. Ele, então, corre atrás do oponente novamente e varre para fora de seus pés, e vira-o para cima chutando o adversário. Ele, então, salta mais uma vez, desta vez por atrás do oponente, e dá um soco. Por fim, ele chuta o oponente nas costas com o calcanhar esquerdo fazendo o adversário cair no chão.', E'無双疾風陣', E'Musō Shippūjin', E'Formação do Furacão Inigualável', E'Formação do Furacão', NULL, NULL, E'Curto alcance', NULL, NULL),
(780, E'Formação do Grande Vento Inigualável', E'Enquanto em sua forma do selo amaldiçoado nível 2, Sasuke chuta para cima, lançando o oponente no ar. Ele, então, desliza atrás dele e executa outro chute lançando o adversário para bater no chão, em seguida, termina com um Chidori.', E'無双豪風陣むそうごうふうじん', E'Musō Gōfūjin', E'Formação do Grande Vento Inigualável', E'Formação do Ultra Vendaval', NULL, NULL, E'Curto alcance', NULL, NULL),
(781, E'Formação do Mecanismo de Oito Trigramas', E'Uma técnica usada por Konohagakure para prender o inimigo em um labirinto de muros altos. O usuário bate um tambor gigante que faz com que inúmeras paredes aparecem ao redor da aldeia, encurralando o inimigo. Além disso, se tentar escapar escalando as paredes, armadilhas líquidas farão o trabalho da referida fuga.', E'からくり八卦の陣', E'Karakuri Hakke no Jin', E'Formação do Mecanismo de Oito Trigramas', NULL, NULL, NULL, NULL, NULL, NULL),
(782, E'Formação do Quadrado Kagura', E'Karin se envolve em uma esfera luminosa de tom rosado a fim de imobilizar seu inimigo ou de se defender. Sendo assim, ela abre uma brecha para lançar múltiplas senbon no oponente.', E'神 楽 方陣', E'Kagura Hōjin', E'Formação Divina do Entretenimento Quadrado', E'Barreira Kagura', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(783, E'Formação do Redemoinho do Pavão', E'Esta é uma técnica que utiliza a especial Espadas Curtas Voadoras Crescentes da Vila Takumi, para criar um vórtice que afasta todos materiais que prendem o corpo do utilizador, libertando-o. Seimei usou esta técnica para libertar-se da areia de Gaara.', E'孔雀旋風陣', E'Kujaku Senpūjin', E'Formação do Furação Kujaku', NULL, NULL, NULL, NULL, NULL, NULL),
(784, E'Formação dos Oito Portões Liberados', E'O usuário abre todos os Oito Portões simultaneamente, temporariamente ganhando poderes centenas de vezes mais forte do que no seu estado normal, mas morrerá por causa do dano causado ao seu corpo. Isto é foi visto quando Might Guy abriu todos os oito portões e luta com igualdade e gradualmente superando Madara Uchiha — o jinchūriki do Dez-Caudas — fazendo Madara comparar a sua luta contra Guy com a sua luta contra Hashirama Senju no Vale do Fim.', E'八門遁甲の陣はちもんとんこうのじん', E'Hachimon Tonkō no Jin', E'Formação dos Oito Portões Liberados', E'Formação Oito Portões Interiores', NULL, E'Formação dos Oito Portões Internos', NULL, E'Rank A', NULL),
(785, E'Formação dos Quatro Nevoeiros Pretos', E'Um ninjutsu de barreira, realizado por todos do Quarteto do Som, usado durante a \'morte\' temporária daqueles que forçam o avanço do seu selo amaldiçoado ao seu segundo nível. A técnica sela-os em um caixão enquanto o processo ocorre, e nem sequer permite que o Byakugan veja o interior. O Método de Selamento Escuro pode ser usado para uma camada adicional de proteção. O selo parece ser naturalmente deslocado quando o selo amaldiçoado avança para o nível dois.', E'四黒霧陣しこくみじん', E'Shikokumujin', E'Formação dos Quatro Nevoeiros Pretos', NULL, E'Muralha dos Quatro Nevoeiros Negros', E'Formação das Quatro Névoas Negras', NULL, E'Rank B', NULL),
(786, E'Forte Colapso da Árvore Gigante', E'Yamato cria um grande pilar de madeira e Sakura dá um soco no pilar de madeira que cai em cima do oponente.', E'巨木剛力落とし', E'Kyoki Gōriki Otoshi', E'Forte Colapso da Árvore Gigante', NULL, NULL, NULL, NULL, NULL, NULL),
(787, E'Frenesi', E'Obito, tomado pelo delírio, segura no rosto do oponente e derruba-o violentamente contra o chão. Quando o inimigo está caído, Obito monta em cima de seu corpo e desfere dois poderosos socos em sequência e finaliza com um golpe descendente com as duas mãos juntas, como um golpe martelo.', E'狂乱', E'Kyōran', E'Frenesi', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(788, E'Frequência de Transmissão do Ruído de Vento', E'Usando esta técnica, Fū é capaz de criar uma frequência de onda sonora que permite à ela se comunicar com seus aliados. Quando em uso, a frequência cria um som semelhante a uma tempestade.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(789, E'Fukuwajutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(790, E'Funeral Explosivo de Céu e Terra', E'Obito torna-se intangível e corre em direção ao oponente, atravessando-o e deixando um rastro de papéis explosivos para trás dele, que detonam quando ele atravessa o inimigo.', E'天地爆葬', E'Tenchi Bakusō', E'Funeral Explosivo de Céu e Terra', E'Ataúde Explosiva de Céu e Terra', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(791, E'Funeral Giratório', E'Obito absorve seu oponente e lança-o no meio de múltiplos papéis explosivos, que o detona em seguida.', E'転葬てんそう', E'Tensō', E'Funeral Giratório', E'Enterro', NULL, NULL, E'Curto alcance', NULL, NULL),
(792, E'Funeral da Serpente Oculta na Sombra', E'Aproveitando-se de sua forma esguia, o usuário enterra uma parte de seu corpo no solo e, em seguida, desfere um ataque surpresa ou captura o inimigo. Por não ter a forma esguia e super alongada, um usuário humano pode se alongar para o mesmo efeito, a fim de sepultar o oponente.', E'潜影蛇葬せんえいじゃそう', E'Sen\'ei Jasō', E'Funeral da Serpente Oculta na Sombra', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(793, E'Funeral de Areia de Ferro', E'O usuário envia sua Areia de Ferro até o inimigo para envolvê-lo e esmagá-lo em seguida, algo muito difícil de escapar. No entanto, se o oponente estiver preparado, ele pode usar clones a fim de evitar ser pego ou, se ele tiver resistência o suficiente, consegue quebrar o envólucro com sua força.', E'砂鉄送葬さてつそうそう', E'Satetsu Sōsō', E'Funeral de Areia de Ferro', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(794, E'Funeral do Deserto', E'O Funeral do Deserto é uma técnica assinatura de Gaara, sendo também uma variação de suas várias outras versões destrutivas.', E'砂瀑送葬さばくそうそう', E'Sabaku Sōsō', E'Funeral do Deserto', NULL, NULL, E'Enterro de Areia', E'Curto a Médio alcance', E'Rank –', NULL),
(795, E'Funeral do Deserto Destrutivo', E'Gaara cria um zigue-zague de areia sobre o oponente. O zigue-zague então forma o rosto de Shukaku que devora e joga o oponente á 200 metros de distância. Gaara então coloca a mão no chão e a areia faz um impacto com tanta força no chão que cria uma cratera com 200 metros de profundeza, levando o oponente para o nada.', E'滅砂瀑葬', E'Messabakusō', E'Funeral do Deserto Destrutivo', E'Destruição da Cachoeira de Areia', NULL, NULL, E'Todos os alcances', NULL, NULL),
(796, E'Funeral do Deserto Gigante', E'Gaara faz com que a areia suba do chão e circunde seu alvo, deixando-o no centro da técnica sem escapatórias. Gaara, então, faz com que essa areia suba ao céu e caia bruscamente contra o oponente, esmagando-o e se comprimindo no processo.', E'巨大砂瀑送葬', E'Kyodai Sabaku Sōsō', E'Funeral do Deserto Gigante', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(797, E'Funeral do Deserto Quente', E'Gaara enquanto na forma de Shukaku ataca o oponente, fazendo-o ir para o ar, Gaara pega e joga o oponente de volta no chão esmagando-o ao mesmo tempo com areia.', E'熱砂瀑葬', E'Nessabakusō', E'Funeral do Deserto Quente', E'Enterro da Areia Quente', NULL, NULL, E'Curto alcance', NULL, NULL),
(798, E'Liberação de Lava: Técnica Riacho de Rocha Escaldante', E'O usuário expele uma grande quantidade de lava da sua boca, que rapidamente se solidifica em múltiplos pedregulhos de rocha derretida que são disparados em direção ao alvo com uma força tremenda. Devido à sua composição, os pedregulhos podem derreter a maioria dos objetos com facilidade, e já que um número considerável é disperso em uma ampla área em rápida sucessão, eles também são difíceis de evitar.', E'熔遁・灼河流岩の術ようとん・しゃくがりゅうがんのじゅつ', E'Yōton: Shakugaryūgan no Jutsu', E'Liberação de Lava: Técnica Riacho de Rocha Escaldante', E'Estilo Corrosão: Jutsu das Rochas Escaldantes', E'Estilo da Lava: Técnica do Rio de Rochas Flamejantes', NULL, E'Médio alcance', NULL, E'Cao → Boi → Coelho → Cobra'),
(799, E'Nagato', E'Ise (Pai) Fusō (Mãe)', NULL, NULL, E'Wendell Bezerra (Pain)', NULL, NULL, NULL, NULL, NULL, NULL),
(800, E'Susanoo', E'O Susanoo é um humanoide feito de chakra daquele que luta em seu nome. É a terceira habilidade concedida àqueles que despertaram todas as habilidades de ambos os Mangekyō Sharingan.', E'須佐能乎スサノオ', E'Susanoo', E'Ele com a habilidade de ajudar por todos os meios', NULL, E'Deus da Tormenta', NULL, E'Todos os alcances', E'Rank –', NULL),
(801, E'Esfera Espiral do Medo', E'Menma cria um Rasengan de chakra negativo e o rodeia com um anel em forma de disco adicional de chakra, que orbita a esfera principal, fazendo-a se assemelhar a um pequeno anel planetário. Ele corresponde ao Rasenshuriken de Naruto no Modo Sábio.', E'螺旋輪虞らせんりんぐ', E'Rasenringu', E'Esfera Espiral do Medo', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(802, E'Fūinjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(803, E'Fūinjutsu Supressor de Chakra', E'O usuário cria um selo nas costas de um alvo, representado pelo kanji 封 (Fū; literalmente significa: Selo) dentro de um círculo, a fim de suprimir seu chakra, evitando assim que ele seja roubado ou absorvido. No entanto, esta técnica deixa o receptáculo do selo incapaz de usar o próprio chakra, forçando-o a usar apenas técnicas que não necessitam do mesmo, tais como alguns taijutsu e kenjutsu.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(804, E'Fūinjutsu do Esconderijo', E'Após bloquear uma passagem com um objeto, como uma grande pedra, Orochimaru coloca um fūinjutsu sobre o entulho e cria um mecanismo de quebra-cabeças para dificultar o acesso.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(805, E'Fūma Shuriken: Duas Rotações', E'Sarada lança o oponente para cima com um soco e, em seguida, pega duas Fūma Shuriken e salta para trás, lançando-as no oponente.', E'風魔手裏剣・両輪', E'Fūma Shuriken: Ryōrin', E'Shuriken de Vento Demoníaco: Duas Rotações', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(806, E'Funeral Imperial de Pó de Ouro', E'Usando seu pó de ouro, Rasa cria envolve seu oponente em uma pequena duna do material. Logo então ele bate as mãos sobre o pó, esmagando seu oponente restringido.', E'砂金大葬さきんたいそう', E'Sakin Taisō', E'Funeral Imperial de Pó de Ouro', E'Grande Sepultamento de Pó de Ouro', NULL, NULL, E'Todos os alcances', NULL, NULL),
(807, E'Funeral Trovejante: Banquete do Relâmpago', E'O usuário cria várias descargas elétricas que cortam através do chão ou ar até acertar o oponente. Seus efeitos podem ser melhorados através do uso das espadas gêmeas.', E'雷葬・雷の宴らいそう・いかずちのうたげ', E'Raisō: Ikazuchi no Utage', E'Funeral Trovejante: Banquete do Relâmpago', NULL, NULL, E'Enterro Relâmpago: Banquete do Trovão', E'Médio a Longo alcance', NULL, E'Coelho → Javali → Carneiro[1]'),
(808, E'Furacão Forte da Folha', E'Um taijutsu que usa uma combinação de velocidade e poder. A essência desta técnica está concentrada em um chute giratório, com tal velocidade que uma única pessoa não pode acompanhar o movimento, esmagando o inimigo com uma força avassaladora.', E'木ノ葉剛力旋風', E'Konoha Gōriki Senpū', E'Furacão Forte da Folha', NULL, E'Poderoso Furacão da Folha', NULL, E'Curto alcance', E'Rank B', NULL),
(809, E'Furacão da Folha', E'Um taijutsu onde se inicia uma sucessão de chutes altos e baixos. Ao ver o chute alto, o adversário se abaixa para se esquivar dele, aumentando, assim, as chances de o chute baixo acertá-lo. Então, originalmente, o chute alto não se passa de uma distração para abrir uma brecha.', E'木ノ葉旋風このはせんぷう', E'Konoha Senpū', E'Furacão da Folha', NULL, E'Furacões da Folha', NULL, E'Curto alcance', E'Rank D', NULL),
(810, E'Furacão da Lâmina de Vento', E'Baki cria um furacão gigante usando a sua Lâmina de Vento que puxa o inimigo para o furacão, violentamente cortando ele.', NULL, NULL, E'Furacão da Lâmina de Vento', E'Estilo Vento: Elevação do Dragão', NULL, NULL, E'Curto Alcance', NULL, NULL),
(811, E'Furacão de Takigakure', E'Fū acerta seu alvo por cima com uma força poderosa. Sua força flui em torno de si mesma, se assemelhando a um furacão. Esta técnica foi mostrada sendo suficientemente forte para derrotar um gigante escorpião resistente.', E'滝隠れの旋風', E'Takigakure no Senpū', E'Furacão de Takigakure', NULL, NULL, E'Furacão da Cachoeira Oculta', E'Curto alcance', NULL, NULL),
(812, E'Furacão de Trovoada', E'O Grupo Magaki executa os selos de mão necessários para a técnica e estendem sua mão, revelando os kanji que compõem o nome da técnica em suas palmas e, a partir deles, emitem um chakra rosa, que toma a forma de duas linhas, que se conectam com o parceiro à direita e no topo com todas as outras, formando uma pirâmide que encurrala e imobiliza o oponente. Em seguida, eles lembram mais chakra e explodem a barreira, injuriando ou matando a pessoa dentro dela.', E'疾風迅雷', E'Shippū Jinrai', E'Furacão de Trovoada', NULL, NULL, E'Shifū Jinrai', NULL, NULL, E'Macaco → Passaro → Cachorro → Javali'),
(813, E'Furacão do Pavão', E'Utilizando as especiais Espadas Curtas Voadoras Crescentes da Vila Takumi, o usuário gira uma das armas do par para criar uma poderosa rajada de vento rodopiante.', E'孔雀旋風', E'Kujaku Senpū', E'Furacão do Pavão', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(814, E'Fúria', E'Fúria (怒髪天, Dohatsuten) é uma técnica que pode criar uma enorme explosão, capaz de destruir uma vila inteira.', E'怒髪天どはつてん', E'Dohatsuten', E'Fúria', NULL, NULL, E'Jutsu da Fúria', E'Todos os alcances', NULL, E'Javali → Cao → Cobra ou (Carneiro → Cavalo → Dragão → Passaro → Cobra)[1]'),
(815, E'Fúria Frenética', E'Kinshiki manifesta uma clava de chakra com a qual ele golpeia seu oponente no estômago para atirá-lo para o alto e, em alta velocidade, ele o alcança em pleno ar e o golpeia com socos-ingleses, em seguida ele amarra com uma corrente para puxá-lo contra o chão e o puxa de volta para o alto antes de atirar um kamayari para perfurar seu alvo. Para finalizar, Kinshiki surge acima de seu oponente ainda no ar e desfere um poderoso golpe com uma marreta que o atira contra o chão, criando uma grande cratera no processo, então ele cai golpeando o solo com um machado, provocando um rasgo que corta o céu e a terra.', NULL, NULL, NULL, E'Fúria Frenética', NULL, NULL, E'Curto alcance', NULL, NULL),
(816, E'Fúria da Samehada', NULL, E'鮫肌凶乱さめはだきょうらん', E'Samehada Kyōran', E'Fúria da Pele de Tubarão', E'Ataque da Pele de Tubarão[2]', NULL, NULL, E'Curto alcance', NULL, NULL),
(817, E'Fórmula Explosiva da Sombra Oculta', E'Shikamaru crava uma kunai especial no chão que, quando o oponente encosta nela, ele é jogado pra o alto. No ar, ele é atingido por uma série de outras kunai, com a ultima delas estando anexada com um papel explosivo.', E'爆式影潜', E'Bakushiki Eisen', E'Fórmula Explosiva da Sombra Oculta', E'Estilo Explosivo da Sombra Oculta', NULL, NULL, NULL, NULL, NULL),
(818, E'Fórmula da Técnica de Espaço-Tempo: Virada do Submundo', E'A técnica projetada por Orochimaru é utilizada ao liberar o ódio dentro dos selos amaldiçoados do Quarteto do Som reencarnado, para ser usado com o único propósito de vingança. O selo ativa ao redor do usuário, prendendo qualquer um dentro de seu alcance. As almas de ambos os alvos e os usuários são separadas de seus corpos e confinadas a uma barreira especial. Ninja sensor e até mesmo o Byakugan não pode sentir e ver através desta técnica, respectivamente. Os alvos são deixados em um estado semelhante à animação suspensa, ainda vivos, embora com grande dificuldade, grande parte de seu fluxo de chakra pára. Para restaurar as almas presas aos seus corpos, é necessário tocá-los com chakra. Inoichi Yamanaka compara a um cancelamento de genjutsu. Dentro da barreira, as almas daqueles presos ainda sentem-se como eles, e são capazes de usar todas as técnicas que eles conhecem. Mesmo que os usuários desta técnica sejam derrotados, ela não vai acabar. Como tal, é só uma questão de tempo antes que a vida daqueles que estão presos pela barreira termine, já que seus corpos físicos acabarão por morrer. No entanto, ele sacrifica a vida dos usuários de modo que os alvos podem ser presos e a maldade dos usuários não é bloqueada pela barreira para um ninja que pode perceber a má vontade pode localizar a barreira e quebrá-la, libertando as almas presas no interior, enquanto que o usuários desaparecem.', E'時空術式・黄泉転身じくうじゅつしき・よみてんしん', E'Jikūjutsu Shiki: Yomi Tenshin', E'Técnica da Formula do Espaço-Tempo: Virada do Submundo', NULL, NULL, E'Técnica da Formula de Transportação: Jutsu de Viagem ao Submundos', E'Curto alcance', NULL, NULL),
(819, E'Fórmula de Invocação', E'A Fórmula de Invocação é um Ninjutsu Espaço-Tempo que permite teletransportar grandes grupos de pessoas através de longas distâncias em um curto período de tempo.', E'口寄せの式くちよせのしき', E'Kuchiyose no Shiki', E'Fórmula de Invocação', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(820, E'Fórmula de Selamento', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(821, E'Fênix Gentil dos Punhos de Leões Gêmeos Espirais', E'Naruto, enquanto em seu Modo Besta com Cauda, transfere o seu chakra para Hinata, que lança um Oito Trigramas Palma de Vácuo no inimigo para desequilibrá-lo, antes de pegar na mão de Naruto. Ele transfere o seu chakra para ela novamente e eles decolam sob a forma de um pássaro, onde Hinata cria um Passo Gentil dos Punhos de Leões Gêmeos e Naruto forma um Rasengan dentro de suas mandíbulas acertando-o no adversário. O inimigo então é detonado na explosão de uma cúpula gigante, Naruto e Hinata aparecem depois.', E'柔鳳螺旋双獅拳', E'Jūhō Rasen Sōshiken', E'Fênix Gentil dos Punhos de Leões Gêmeos Espirais', E'Rasen Punhos de Leões Gêmeos', NULL, NULL, E'Todos os alcances', NULL, NULL),
(822, E'Fênix Noturna', E'Guy é coberto por chamas, em uma forma específica que se assemelha a uma Fênix. Quando o movimento atinge, o oponente entra em chamas.', E'夜鳳凰', E'Yoruhōō', E'Fênix Noturna', E'Yoruho\'o', NULL, NULL, E'Curto alcance', NULL, NULL),
(823, E'Garoa de Areia', E'Após a coagulação juntando a areia em incontáveis ​​pedaços pequenos, Gaara usa-os para atacar um inimigo de todas as direções. Porque todos os grumos de areia são controlados por chakra, é possível iniciar um ataque, sem quaisquer ângulos mortos. Esta técnica é frequentemente usada como isca na batalha, distraindo um oponente, deixando-o vulnerável ​​a um ataque de baixo. Para que isso funcione, o inimigo é forçado primeiro a olhar para cima com a chuva de areia, simultaneamente trazendo seu campo de atividades sob controle do usuário. Em seguida, uma jogada ousada é feita a partir de debaixo dos pés. Para garantir que a chuva atinja seu alvo, pode ser combinado com o Caixão de Areia para imobilizar o alvo.', E'砂時雨すなしぐれ', E'Suna Shigure', E'Garoa de Areia', NULL, E'Chuva de Areia', E'Chuva de Areia', E'Todos os alcances', E'Rank B', NULL),
(824, E'Garoa de Papel', E'Depois de criar asas de papel, Konan dispara vários papéis contra o inimigo em alta velocidade. Devido a isso, os papéis podem criar uma pequena explosão ao colidir com outra técnica, suficiente para abafar mesmo a Liberação de Fogo: Projétil de Chamas de Jiraiya.', E'紙時雨かみしぐれ', E'Kami Shigure', E'Garoa de Papel', E'Chuva de Papel', NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(825, E'Garoa de Pó de Ouro', E'Rasa reúne pó de ouro no ar lança-o descendentemente sobre o oponente.', E'砂金時雨さきんしぐれ', E'Sakin Shigure', E'Garoa de Pó de Ouro', E'Partículas de Pó de Ouro', NULL, NULL, E'Todos os alcances', NULL, NULL),
(826, E'Garra Veloz da Presa do Lobo', E'Kiba e Akamaru rapidamente partem subitamente para o arredor do oponente e o corta antes de saltar para a frente em lados opostos para uma passagem combinada de mordidas e impactos.', E'爪迅狼牙', E'Sōjin Rōga', E'Garra Veloz da Presa do Lobo', NULL, NULL, NULL, NULL, NULL, NULL),
(827, E'Garra da Nove-Caudas', E'A Garra da Nove-Caudas é acessivel através da transformação física proporcionada pelo chakra de Kurama. O usuário manifesta garras afiadas com as quais ele pode desferir ataques cortantes, permitindo retalhar seus inimigos. Quando revestidas com chakra, as garras tem um poder ofensivo ainda mais letal, permitindo arrancar membros e destruir barreiras com relativa facilidade.', E'九尾の爪', E'Kyūbi no Tsume', E'Garra da Nove-Caudas', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(828, E'Garra de Chakra do Gato', E'Esta técnica emprega um tipo de fluxo de chakra exclusivo para os ninneko. Ele aumenta o alcance e melhora o poder de ataque das garras do gato a tal ponto que ele corta facilmente através de metais.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(829, E'Garra de Ferro', E'Este movimento implica simplesmente em apertar o rosto do alvo com a mão. A usa essa técnica quando precisa punir Killer B. Gyūki tem visto A fazer isso em B várias vezes ao ponto que o mesmo menciona que A daria a garra de ferro em B se ele ajudasse Naruto na fuga de Genbu.', E'アイアンクロー', E'Aian Kurō', E'Garra de Ferro', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(830, E'Garra de Gato', E'Enquanto em seu estado transformado, Yugito corta o oponente com um golpe poderoso e rápido, usando as unhas estendidas em suas mãos.', E'猫爪', E'Nekozume', E'Garra de Gato', NULL, E'Presas Felinas', NULL, E'Curto alcance', NULL, NULL),
(831, E'Garra de Shukaku', E'Após Gaara ter caído no sono e ter sido possuído totalmente por Shukaku, o monstro de areia é capaz de utilizar suas garras no solo, desferindo cortes no oponente e no solo, liberando um fluxo de areia que acompanha seu braço.', E'守鶴の爪', E'Shukaku no Tsume', E'Garra de Shukaku', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(832, E'Gato Dragão', E'Empunhando seu lingote gigante, Fukurokumaru bate no oponente, podendo lançá-lo para cima.', NULL, NULL, NULL, E'Gato Dragão', NULL, NULL, E'Curto alcance', NULL, NULL),
(833, E'Genes do Primeiro', E'Yamato faz sua madeira emergir do solo em formato espiral em torno de seu oponente e, com um aperto de mão, a madeira esmaga-o.', E'初代の遺伝子', E'Shodai no Idenshi', E'Genes do Primeiro', E'Genes do Primeiro Hokage', NULL, NULL, NULL, NULL, NULL),
(834, E'Genjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(835, E'Genjutsu Comunicativo', E'Similar a Técnica do Corpo da Lanterna Mágica de Nagato, por meio desta técnica, o usuário pode organizar encontros em grupo, como meio de compensar a ausência de algum membro nem sempre próximo.', E'幻術通信げんじゅつつうしん', E'Genjutsu Tsūshin', E'Técnica de Comunicação', NULL, E'Genjutsu de Comunição', NULL, E'Todos os Alcances', NULL, NULL),
(836, E'Genjutsu Interrogatório', E'Kakkō usa este genjutsu para quebrar a vontade de seu alvo capturado para fazer-lhe dar o máximo de informação possível. No entanto, se o alvo tiver força de vontade, a técnica será inútil.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(837, E'Genjutsu Nebuloso', E'Esta técnica lança uma ilusão sobre o inimigo que borra a visão do alvo de toda a área. Além disso, ela faz com que a vítima tenha a sensação de estar afundando em areia movediça, efetivamente imobilizando o alvo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(838, E'Genjutsu Programado', E'Com este genjutsu, o usuário é capaz de programar uma rota e um conjunto de ações na mente da vítima, tornando-a vulnerável e agindo tal qual uma marionete. Dessa forma, a vítima pode servir como um chamariz para atrair seus aliados para uma emboscada, bem como um lutador inesperado.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(839, E'Genjutsu: Aprisionamento da Árvore Assassina da Andorinha Voadora', E'Trata-se de um ataque de duas vertentes em que Kurenai primeiro envolve o adversário em um genjutsu, aprisionando-o em árvores e então Asuma, usando as suas Lâminas de Chakra, o finaliza com a sua Andorinha Voadora.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto Alcance', NULL, NULL),
(840, E'Genjutsu: Colina de Flores', E'O usuário convoca flores roxas ilusórias do chão na frente dele, o que causa dano nos inimigos com chance de paralisá-los.', NULL, NULL, E'Técnica de Ilusão: Colina de Flores', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(841, E'Genjutsu: Deslizamento de Pedra', E'Esta técnica cria uma ilusão em uma quantidade considerável de alvos, acreditando que várias explosões estão ocorrendo e a partir disto, causando destruições em massa. A maioria das vítimas pegas nesse genjutsu mostrou entrar em crise, desorientando-se e causando pânico por um período variante, dependendo do emocional de quem foi pego.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(842, E'Genjutsu: Fogo Desconhecido', E'Este genjutsu é lançado apontando para o oponente e uma onda roxa desbotada é liberada do dedo que quando atinge o alvo, provoca-o a ver um anel de fogo azul em torno dele e, em seguida, vê-se inflamar com chamas azuis.', E'幻術・不知火げんじゅつ・しらぬい', E'Genjutsu: Shiranui', E'Técnica de Ilusão: Fogo Desconhecido', E'Genjutsu Shiranui', NULL, NULL, NULL, NULL, NULL),
(843, E'Genjutsu: Fuga de Pétala de Flor', E'Quando presa dentro de um genjutsu ou deseja parecer presa, Kurenai usa essa técnica para dissolver seu corpo em um monte de pétalas de flores, que então consomem o corpo de seu inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre → Javali → Tigre → Cavalo → Boi'),
(844, E'Genjutsu: Ketsuryūgan', E'Trata-se de uma aplicação de genjutsu inespecífico por meio do uso do Ketsuryūgan. Por meio desta técnica, é possível manter um oponente preso numa ilusão, podendo causar-lhe danos reais, como asfixia. A força deste genjutsu é suficiente para manter um membro do clã Uchiha preso por um determinado tempo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(845, E'Genjutsu: Miragem', E'Kurenai se levanta em linha reta com a cabeça baixa e as mãos no peito. Ela então, pisa no chão, criando diversas flores de rosas que a cercam, e criando uma enorme área de névoa. O oponente fica então confuso quanto ao local onde Kurenai está. De repente, o oponente é perfurado para trás e ao tempo que o oponente vira-se para trás, Kurenai aparece na frente dele e lança o oponente no ar.', E'幻術・蜃気楼げんじゅつ・しんきろう', E'Genjutsu: Shinkirō', E'Técnica Ilusória: Miragem', E'Genjutsu: Miragem', NULL, NULL, E'Curto alcance', NULL, NULL),
(846, E'Genjutsu: Névoa', E'Kurenai cria um clone ilusório que salta sobre o oponente, e então explode-se em uma nuvem de pétalas de flores de cerejeira.', E'幻術・霞', E'Genjutsu: Kasumi', E'Genjutsu: Névoa', E'Genjutsu: Neblina', NULL, NULL, E'Curto alcance', NULL, NULL),
(847, E'Genjutsu: Pássaros e Tubarões Yaksha', E'Itachi lança um genjutsu de corvo enquanto Kisame cria vários tubarões de água que saltam sobre e para o oponente.', E'幻術・夜叉鮫鳥', E'Genjutsu: Yasha Kōchō', E'Técnica Ilusória: Pássaros e Tubarões Yaksha', E'Genjutsu: Liberação de Corvos e Tubarões', NULL, NULL, NULL, NULL, NULL),
(848, E'Genjutsu: Sharingan', E'Genjutsu: Sharingan é o nome dado para a ampla variedade de genjutsu lançados tanto pelo Sharingan, Mangekyō Sharingan, e até mesmo pelo Rinnegan, que faz o melhor uso da capacidade de hipnotismo do Sharingan. Em um instante, o alvo é colocado em uma ilusão muito poderosa, tirando completamente o próprio controle corporal.', E'幻術・写輪眼げんじゅつ・しゃりんがん', E'Genjutsu: Sharingan', E'Técnica Ilusória: Olho Copiador Giratório', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(849, E'Genjutsu da Flauta Paralisante', E'Com o uso da Flauta Demoníaca, Shiin era capaz de criar uma ilusão a partir do som que ela emitia. Depois que o usuário toca uma melodia, quem ouve-a se torna vulnerável e paralisa, como se estivessem com corpos pesados com nenhuma força para movimentá-lo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(850, E'Genjutsu de Beijo', E'Gamariki beija o adversário, colocando-o em um mundo ilusório. Quando o adversário está preso na técnica, todos as pessoa vistas neste mundo ilusório terão a cara de Gamakiri. Quando Naruto foi preso no genjutsu, ele viu corações flutuantes e uma névoa roxa, além dos efeitos comuns.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(851, E'Genjutsu de Entrada da Mente', E'O usuário posiciona sua mão perto da cabeça do alvo, emite chakra e torna-se capaz de enxergar dentro da mente do último.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(852, E'Genjutsu de Kaguya Ōtsutsuki', E'Ao entrar em contato visual com o oponente, Kaguya emite seu chakra de forma visível ou invisível fazendo o oponente adormecer. Essa técnica foi de grande influência para Tenji acolher Kaguya.', NULL, NULL, NULL, NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(853, E'Genjutsu de Kirara', E'Revelando seus olhos de cintilantes pupilas violetas, Kirara torna-se capaz de controlar ou paralisar o alvo pretendido ao fazer contato visual. Embora não necessite estar perto a todo momento enquanto o controle está ativo, o alvo é capaz de desfazer ou sobrepôr o genjutsu para escapar.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(854, E'Genjutsu de Liberação de Água: Prisão do Nevoeiro do Fantasma Demoníaco', E'Após Kandachi executar os selos de mão necessários, o Rei Concha abre sua concha e libera uma névoa que coloca o adversário em um Genjutsu. No Genjutsu, o Rei Concha lambe o adversário até que eles são congelados, resultando no adversário sendo superado pelo medo e em uma questão de tempo, entra em um colapso mental. O poder do genjutsu era muito maior pelas forças magnéticas das árvores que cercam o pântano em que estava dentro.', E'水遁幻術・霧幻魔牢', E'Suiton Genjutsu: Mugen Marō', E'Técnica Ilusória de Liberação de Água: Prisão do Nevoeiro do Fantasma Demoníaco', NULL, NULL, E'Genjutsu do Estilo Água: Prisão do Nevoeiro Místico', NULL, NULL, E'Tigre → Cao → Boi → Cobra'),
(855, E'Genjutsu de Paralisia', E'Como o nome sugere, o usuário prende o adversário em um genjutsu, tornando-o incapaz de se movimentar. Como demonstrado por Tsunade quando ela enfrenta os Lendários Irmãos Estúpidos, este genjutsu pode ser utilizado em mais de um alvo, ao mesmo tempo. Uma vez que o alvo é preso, o usuário ou um aliado pode correr até o inimigo e desferir um golpe fatal.', E'幻術縛り', E'Genjutsu Shibari', E'Genjutsu de Paralisia', E'Jutsu de Imobilidade[1]', NULL, NULL, NULL, NULL, NULL),
(856, E'Genjutsu do Cacto', E'Usando o pólen um cacto próximo, o usuário coloca qualquer um que entre em contato com ele sob um forte genjutsu que é quase imperceptível devido à execução discreta da técnica. Neste genjutsu, a vítima tem a impressão de atravessar uma área e outra, mas na realidade, ela não se move.', E'サボテンの幻術', E'Saboten no Genjutsu', E'Técnica Ilusória do Cacto', NULL, NULL, E'Genjutsu do Cacto', NULL, NULL, NULL),
(857, E'Genjutsu do Gato', E'O usuário usa um genjutsu onde todo o lugar muda dentro da mente do alvo. Um novo cenário e um Ninneko ofensivo são criados ilusoriamente para lutar contra a vítima dentro de sua cabeça.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Boi → Javali → Cavalo → Bater Palmas'),
(858, E'Genjutsu do Kuchiyose Final', E'Este genjutsu é emitido pelos olhos da besta enviando raios de cor de arco-irís para os alvos; imobilizando-os.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(859, E'Giro, Giro, Bam!', E'Konohamaru começa a girar seu braço, cada giro com maior velocidade, até que ele libera sua força em um poderoso ataque frontal.', E'ぐるぐどか～ん!', E'Gurugurudokkan!', E'Giro, Giro, Bam!', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(860, E'Golpe Ascendente', E'A cobre sua mão com chakra de natureza relâmpago e desfere um golpe ascendente no oponente, lançando-o com força para cima.', E'雷迅枢圧破呀らいじんぐあっぱあかっと', E'Raijingu Appākatto', E'Golpe Ascendente', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(861, E'Golpe Corporal', E'A acerta diversos golpes no oponente, seguido de um ataque ascendente, e finaliza agarrando-o e derrubando-o no chão, utilizando chakra relâmpago para aumentar o impacto.', E'ボディスラム', E'Bodisuramu', E'Golpe Corporal', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(862, E'Golpe Decapitador', E'Suigetsu pisa sobre seu adversário e acerta um único golpe com sua Kubikiribōchō.', E'ぶつ斬り', E'Butsugiri', E'Golpe Decapitador', E'Golpes da Espada', NULL, NULL, E'Curto alcance', NULL, NULL),
(863, E'Golpe Estomacal', E'A cobre sua mão com chakra de natureza relâmpago e ataca seu oponente com um golpe no estômago, derrubando-o com o impacto.', E'軟斗幕武朧', E'Sutomakkuburō', E'Golpe Estomacal', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(864, E'Golpe Fatal', E'Utilizando sua pata, o usuário desfere um poderoso golpe contra o oponente.', E'致命一撃ちめいいちげき', E'Chimei Ichigeki', E'Golpe Fatal', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(865, E'Golpe Giratório de Linha Dura do Estilo-Nadeshiko', E'O usuário cobre sua perna com chakra vento, e atinge um potente chute no adversário. Este chute foi forte o suficiente para quebrar uma marionete mecânica.', E'撫子流硬派旋回切り', E'Nadeshiko-Ryū Kōha Senkaigiri', E'Golpe Giratório de Linha Dura do Estilo-Nadeshiko', NULL, NULL, E'Chute do Furacão Enfurecido do Estilo-Nadeshiko', E'Curto alcance', NULL, NULL);