INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(866, E'Golpe Nocauteador', E'O usuário desfere um Golpe Nocauteador forte, rápido e imperceptível no oponente, que o faz cair de joelhos no chão. Devido à grande velocidade em que o movimento é executado, o golpe sequer pode ser visto, deixando o adversário sem reação.', E'ノックアウトブロー', E'Nokkuauto Burō', E'Golpe Nocauteador', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(867, E'Golpe de Mineralização', E'O Golpe de Mineralização é uma técnica utilizada por Ishidate em que, por meio de sua manopla, ele capaz de petrificar qualquer coisa que ele tocar.', E'石化の一撃', E'Sekka no Ichigeki', E'Golpe de Mineralização', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(868, E'Golpes Bloqueadores de Tenketsu', E'Trata-se de uma técnica de taijutsu que permite fechar tenketsu ao pressioná-lo com os dois dedos, bloqueando o fluxo de chakra. Ao ter seus tenketsu fechados, o alvo torna-se incapaz de liberar grandes quantidades de chakra, restringindo até mesmo a abertura dos Oito Portões. Ao fecha múltiplos tenketsu, é possível desacordar seu inimigo em questão de segundos. Também é possível desbloquear os tenketsu de seu oponente, com um único impacto do punho.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(869, E'Gozu Ten\'nō', E'O Gozu Ten\'nō é a chave para um portal interdimensional que dá acesso à uma poderosa arma de destruição em massa, o Nue.', E'牛頭天王ごずてんのう', E'Gozu Ten\'nō', E'Rei Celeste da Cabeça de Vaca', NULL, NULL, NULL, NULL, NULL, NULL),
(870, E'Grande Aguaceiro de Areia', E'Gaara lança uma pequena quantidade de areia no ar em que ele controla para jogar em seu inimigo no chão. Antes de atingir seu oponente, a areia se divide em múltiplos tiros adicionando uma grande precisão nos ataques.', E'豪砂甚雨', E'Gōsajin\'u', E'Grande Aguaceiro de Areia', E'Violento Aguaceiro de Areia', NULL, NULL, E'Curto alcance', NULL, NULL),
(871, E'Grande Ataque Eruptivo', E'Enquanto transformado em Son Gokū, Rōshi salta no ar e desce desferindo um poderoso soco no oponente, lançando o mesmo ao chão em alta velocidade, causando um grande impacto, quando em contato.', E'大叩暴', E'Daikōbō', E'Grande Ataque Eruptivo', E'Ataque Explosivo', NULL, NULL, E'Curto alcance', NULL, NULL),
(872, E'Grande Ataque Feroz de Fogo', E'Sasuke desfere uma série de cortes que alternam entre a Espada de Kusanagi: Lâmina de Chidori e sua espada de chamas negras. Em seguida, ele atira uma grande bola de fogo que queima seu oponente.', E'豪火猛襲ごうかもうしゅう', E'Gōka Mōshū', E'Grande Ataque Feroz de Fogo', E'Grande Investida de Fogo', NULL, NULL, E'Curto alcance', NULL, NULL),
(873, E'Grande Barragem de Ataques', E'A técnica consiste em uma série rápida de socos no oponente com as Lâminas de Chakra, dificultando que ele escape e causando um grande dano. A técnica também pode servir como distração para que o usuário engate outra técnica.', E'豪連打々', E'Gōrendada', E'Grande Barragem de Ataques', E'Barragem dos Ataques Ferozes', NULL, NULL, E'Curto alcance', NULL, NULL),
(874, E'Grande Bola Espiral das Esferas Seriais', E'Naruto cria vários clones de sombra que mergulham sobre o oponente como mísseis e o verdadeiro Naruto finaliza com duas grandes bolas Rasengan.', E'大玉螺旋連丸おおだまらせんれんがん', E'Ōdama Rasenrengan', E'Grande Bola Espiral das Esferas Seriais', E'Barragem Rasen', NULL, NULL, E'Curto alcance', NULL, NULL),
(875, E'Grande Bola Rasengan', E'A Grande Bola Rasengan trata-se de uma versão maior e mais poderosa do Rasengan convencional.', E'大玉螺旋丸', E'Ōdama Rasengan', E'Grande Bola da Esfera Espiral', E'Grande Rasengan', NULL, E'Rasengan Gigante', E'Curto alcance', E'Rank A', NULL),
(876, E'Grande Bola Rasenshuriken', E'O Grande Bola Rasenshuriken é uma variante maior do Rasenshuriken Normal.', E'大玉螺旋手裏剣', E'Ōdama Rasenshuriken', E'Grande Bola Espiral Shuriken', NULL, E'Esfera do Shuriken Espiral Gigante', E'Rasen Shuriken Gigante', E'Todos os alcances', NULL, NULL),
(877, E'Grande Bola: Rasenshuriken da Besta com Cauda', E'A Grande Bola: Rasenshuriken da Besta com Cauda é uma versão mais avançada do Rasenshuriken da Bola de Besta com Cauda.', E'大玉・尾獣螺旋手裏剣おおだま・びじゅうらせんしゅりけん', E'Ōdama: Bijū Rasenshuriken', E'Grande Bola: Shuriken Espiral da Besta com Cauda', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(878, E'Grande Canhão de Vácuo', E'Esta técnica utiliza a especial Espadas Curtas Voadoras Crescentes da Vila Takumi. O usuário dispara pequenos ou potentes quase invisíveis rajadas de vento a partir de sua espada que atinge o oponente e lançá-o para longe.', E'豪空砲', E'Gōkūhō', E'Grande Canhão de Vácuo', NULL, NULL, E'Esboço Sólido', E'Curto à Médio alcance', NULL, NULL),
(879, E'Grande Cobra Selvagem', E'Aoda desliza rapidamente em forma cirular, varrendo quaisquer alvos na proximidade. A força e a velocidade desta técnica é grande o suficiente para deixar faíscas no terreno circundante.', E'大乱蛇だいらんじゃ', E'Dairanja', E'Grande Cobra Selvagem', E'Cobra Caótica', NULL, NULL, E'Todos os alcances', NULL, NULL),
(880, E'Grande Combo de Palma dos Hyūga', E'Hanabi, Hinata e Neji iniciam desferindo uma série de golpes no adversário enquanto o levam para o alto e, antes que ele caia no chão, eles usam o Oito Trigramas Palma de Vácuo mandando-o para longe. Em seguida, Hiashi aparece para a surpresa dos outros três, e logo ele usa o Oito Trigramas Palma da Parede de Vácuo. Com isso, os quatro ativam seu Byakugan e utilizam um cooperativo Oito Trigramas Sessenta e Quatro Palmas. Neji e Hiashi usam o Oito Trigramas Palmas Giratórias do Céu com seu alvo entre eles, que é mandado para o alto, onde é novamente golpeado por Hinata e Hanabi. Logo os quatro concentram chakra em suas palmas e golpeiam o alvo simultaneamente, mandando-o voando por vários metros.', E'日向大連掌ひゅうがだいれんしょう', E'Hyūga Dairenshō', E'Grande Combo de Palma dos Hyūga', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(881, E'Grande Céu Violento da Folha', E'Rock Lee ataca o oponente e soca-o para o céu, uma vez nele, Lee realiza um ataque girando de cabeça para baixo e chuta seu oponente no chão. Finalmente, ele levanta elevando os braços e trazendo as pernas junto.', E'木ノ葉大烈空', E'Konoha Dairetsukū', E'Grande Céu Violento da Folha', E'Queimando o Céu da Folha', NULL, NULL, E'Curto alcance', NULL, NULL),
(882, E'Grande Dança de Decapitação', E'Kisame salta no ar e, enquanto desce em giros, corta o oponente com a Samehada.', E'大舞斬りおおまいぎり', E'Ōmaigiri', E'Grande Dança de Decapitação', E'Talho da Dança Gigante', NULL, NULL, E'Curto alcance', NULL, NULL),
(883, E'Grande Devorador de Areia', E'Kaguya manipula o terreno de sua dimensão de deserto para criar uma onda de areia que avança em direção ao seu oponente.', NULL, NULL, NULL, E'Grande Devorador de Areia', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(884, E'Grande Divisão da Lesma', E'Por causa do corpo desossado de Katsuyu, ela tem a capacidade de dividir todo o seu corpo em lesmas menores, e reuni-lo à vontade. Esta técnica faz com que seja impossível capturar Katsuyu. Todos os ataques físicos sem corte também são anulados antes desta técnica, pois não importa o quão violento é o ataque, não será mais do que uma tentativa inútil. Qualquer inimigo que atacar só vai esgotar-se. Katsuyu pode dividir para outros fins, tais como canalizar chakra do seu invocador em um shinobi com uma das lesmas sobre eles para curar ferimentos.', E'蛞蝓大分裂かつゆだいぶんれつ', E'Katsuyu Daibunretsu', E'Grande Divisão da Lesma', NULL, NULL, NULL, NULL, NULL, NULL),
(885, E'Grande Entrada do Ardente Espírito', E'Guy primeiro abre sete dos Oito Portões, e então chuta o oponente para o alto. Ele então, vai na direção do seu oponente chutando o ar e acerta um poderoso soco de chakra para enviá-lo de volta para baixo.', E'駆け上がる熱き魂', E'Kake Agaru Atsuki Tamashii', E'Grande Entrada do Ardente Espírito', E'Precipitação do Espírito Ardente', NULL, NULL, E'Curto alcance', NULL, NULL),
(886, E'Grande Erupção Vulcânica Explosiva', E'Momoshiki é capaz de desencadear uma poderosa erupção vulcânica que eclode a partir do chão, engolindo seus oponentes em lava.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(887, E'Grande Erupção do Rio Escaldante', E'Rōshi agarra seu oponente e dá um grande salto, transformando-se em Son Gokū. Em seguida, ele coloca o seu adversário entre os punhos revestidos de lava, batendo-os no chão. Quando ele levanta os braços, o chão debaixo dele estoura.', E'灼河大噴火しゃくかだいふんか', E'Shakuka Daifunka', E'Grande Erupção do Rio Escaldante', E'Grande Erupção Ardente', NULL, NULL, E'Curto alcance', NULL, NULL),
(888, E'Grande Esfera de Fogo Espiral', E'Naruto cria um Rasengan, e Sasuke em seguida, usa a Liberação de Fogo: Técnica da Grande Bola de Fogo para ele. Naruto então desata o Rasengan coberto de chamas para no oponente, que detona em uma explosão de fogo.', E'螺旋豪火丸', E'Rasen Gōkagan', E'Grande Esfera de Fogo Espiral', E'Rasengan de Tempestade de Fogo', NULL, NULL, NULL, NULL, NULL),
(889, E'Grande Explosão da Bola Explosiva', E'Konohamaru cria um clone da sombra que pula sobre o oponente, derrubando-o. Em seguida, Konohamaru pega duas bolas de papéis explosivos e lança-as em direção ao oponente, causando uma explosão ao entrar em contato.', E'起爆玉大爆発', E'Kibakutama Daibakuhatsu', E'Grande Explosão da Bola Explosiva', E'Grande Explosão da Esfera de Bomba', NULL, NULL, E'Curto alcance', NULL, NULL),
(890, E'Grande Explosão do Clone', E'A Grande Explosão do Clone é uma técnica que pode ser usada para criar um clone da sombra que pode ser detonado.', E'分身大爆破', E'Bunshin Daibakuha', E'Grande Explosão do Clone', E'Jutsu de Clone: Super Explosão', NULL, NULL, E'Curto alcance', E'Rank A', NULL),
(891, E'Grande Flash da Folha', E'Após distrair o oponente com diversos golpes, o usuário salta e desencadeia um poderoso chute lateral com objetivo de acertar a cabeça, lançando o adversário para longe.', E'木ノ葉大閃光このはだいせんこう', E'Konoha Daisenkō', E'Grande Flash da Folha', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(892, E'Grande Flutuação', E'Ao balançar suas asas, Garuda é capaz de criar um enorme tornado de vento capaz de dilacerar vários oponentes de uma única vez.', E'浮く空大うくそらだい', E'Ukusoradai', E'Grande Flutuação', E'Grande Furacão', NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(893, E'Grande Funeral do Deserto', E'A Grande Funeral do Deserto é um ninjutsu utilizado por Gaara. A técnica tem funções semelhantes ao seu Funeral do Deserto, exceto em uma escala muito maior.', E'砂瀑大葬さばくたいそう', E'Sabaku Taisō', E'Grande Funeral do Deserto', NULL, E'Enterro na Cachoeira de Areia', E'Enterro de Areia Gigante', E'Todos os alcances', NULL, NULL),
(894, E'Grande Furacão da Folha', E'Esta técnica é uma série de ataques de chutes, começando com um chute baixo, ligado em um chute de médio e chute alto e depois terminando com uma queda de calcanhar. Com a velocidade da rotação o usuário ganha flutuabilidade, o que eles usam para mudar para ataques gradualmente mais altos.', E'木ノ葉大旋風', E'Konoha Daisenpū', E'Grande Furacão da Folha', NULL, E'Grande Furacão da Folha', NULL, E'Curto alcance', E'Rank C', NULL),
(895, E'Grande Limpeza', E'O Dez-Caudas bate seu braço no chão e, em seguida, ele vem fazendo uma varredura no solo, levando junto o oponente.', E'巨拭', E'Kyoshoku', E'Grande Limpeza', E'Grande Varredura', NULL, NULL, E'Todos os alcances', NULL, NULL),
(896, E'Grande Martelo de Ferro', E'Enquanto controla o poder de sua besta com cauda, o jinchūriki manifesta dois grandes braços criados a partir de si, seja um par de braços de chakra ou manifestando os braços em si, que ele une através das mãos e golpeia seu oponente com um movimento descendente.', E'大鉄槌', E'Daitettsui', E'Grande Martelo de Ferro', E'Martelo Gigante', NULL, NULL, E'Curto alcance', NULL, NULL),
(897, E'Grande Mordida de Kurama', E'Naruto entra em seu Modo de Chakra do Nove-Caudas e manifesta a cabeça de Kurama, que ele usa para morder e agarrar objetos.', E'九喇嘛大咬くらまだいこう', E'Kurama Daikō', E'Grande Mordida de Kurama', E'Mordida de Kurama', NULL, NULL, E'Curto alcance', NULL, NULL),
(898, E'Grande Queda do Pontapé Giratório', E'O usuário ataca novamente com um movimento conhecido do sumô japonês, salta no ar e acerta um fatal pontapé giratório em sua pré-finalização. Observando que o oponente se encontra ativo em batalha, ele se atira contra o oponente e termina com um outro pontapé giratório, em forma de \'estrelinha\', que faz o oponente e quem mais estiver em volta ficar caído no chão atordoado.', E'大回転浴びせ蹴りだいかいてんあびせけり', E'Daikaiten Abisegeri', E'Grande Queda do Pontapé Giratório', E'Grande Pontapé Giratório', NULL, NULL, E'Curto alcance', NULL, NULL),
(899, E'Grande Rotação Brutal', E'Mitsuki estica ambos os braços em direção ao oponente para derrubá-lo. Logo após, ele salta e enterra um deles no solo, o qual ele emerge e enterra várias vezes indo em direção ao oponente, até que ele agarra-o pelos tornozelos. Com a captura surpresa bem-sucedida, ele puxa o oponente para baixo, enterrando-o e puxando para fora pelos mesmos buracos que seu braço passou, causando danos através do atrito com o solo e, em seguida, ele puxa para cima quando chega no último buraco, segurando-o no ar. Dessa forma, ele laceia seu braço elástico e rotaciona-o freneticamente agarrado ao inimigo, criando um turbilhão de vento. Para finalizar a combinação, ele puxa o adversário para baixo, contra o solo, causando um grande impacto e deixando vestígios para trás.', E'荒大旋あらだいせん', E'Aradaisen', E'Grande Rotação Brutal', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(900, E'Grande Rugido', E'Uma Besta com Cauda usa sua enorme reserva de chakra e transforma qualquer coisa a partir de um rugido ou uma onda em uma poderosa força de pressão e destruição. Quando Naruto estava no controle ao usar o poder de Kurama, ele demonstrou poderosas ondas de choque por rugido ou pelo movimento de seus braços. Quando enfurecido ou quando Kurama assumiu o controle de seu corpo, ele foi mostrado usando explosões com a onda poderosa de seus braços. A força bruta por trás de Naruto uma vez que ele entra na versão 2 era poderosa o suficiente para forçar o Shinra Tensei de Pain de volta para ele.', E'大咆哮', E'Daihokō', E'Grande Rugido', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(901, E'Grande Tornado da Borboleta', E'Chōji converte suas calorias em chakra e manifesta um grande par de asas de borboleta em suas costas. Com o bater de suas asas, ele é capaz de desencadear um vento de chakra na forma de um grande tornado, forte o suficiente para combater uma explosão.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(902, E'Grandes Círculos Gêmeos', E'Após ter previamente guardado uma série de Fūma Shuriken em sua Dimensão do Kamui, Obito é capaz de abrir um portal e ejetá-las em alta velocidade em direção ao seu oponente.', E'大双輪', E'Daisōrin', E'Grandes Círculos Gêmeos', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(903, E'Grandeza do Mal', E'Orochimaru primeiro soca o adversário na cara e bate-o de volta. Ele, então, envolve o adversário com inúmeras cobras que cria um domo ao redor dele e prende-o. Em seguida, ele salta e apunhala-o com sua Espada de Kusanagi finalizando-o.', E'邪傑じゃけつ', E'Jaketsu', E'Grandeza do Mal', E'Gigante do Mal', NULL, NULL, NULL, NULL, NULL),
(904, E'Granizo de Areia', E'Sendo uma versão em escala mais destrutiva da Barragem da Garoa de Areia, o procedimento para utilizar a técnica é semelhante. Gaara reúne sua areia em grandes aglomerações, formando rochas maciças, e então as deixa cair do céu para atingir seus adversários. Ela abrange uma grande área, o que a torna uma técnica difícil de se esquivar. Como a sua versão em menor escala, a técnica pode distrair o inimigo com o ataque aéreo, deixando-os expostas a um ataque oriundo vindo de baixo.', E'砂霰', E'Suna Arare', E'Granizo de Areia', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(905, E'Granizo de Areia: Selamento da Prisão Restritora de Areia', E'Gaara usa o Granizo de Areia contra seu oponente, atacando-o com uma chuva incessável de areia e, em seguida, cria uma Prisão Restritora de Areia, que restringe seus movimentos. Com o inimigo preso num mar de areia, Gaara completa sua técnica selando-o.', E'砂霰・砂縛牢封印すなあられ・さばくろうふういん', E'Suna Arare: Sabakurō Fūin', E'Granizo de Areia: Selamento da Prisão Restritora de Areia', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(906, E'Guy Noturno', NULL, E'夜ガイ', E'Yagai', E'Guy Noturno', NULL, E'Anoitecer de Gai', NULL, E'Curto alcance', E'Rank S', NULL),
(907, E'Górgona Atacante de Água', E'Esta técnica invoca uma grande quantidade de água que é então usada para atacar o adversário de diversas maneiras versáteis. Shizuku transforma a água em um dragão com uma boca aberta para o ataque. A água utilizada fica parada até que seja enviada ofensivamente em direção ao oponente.', E'水襲剛流豪雨', E'Suishū Gorugon', E'Chuva do Forte Fluxo Atacante de Água', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Dragao → Tigre'),
(908, E'Habilidade Desconhecida', E'Obito teletransporta-se atrás do oponente e se prepara para lançar uma bomba, só que deixa-a cair acidentalmente em seus pés. Para se salvar, ele corre rapidamente e a bomba detona, criando uma enorme explosão.', E'未知なる能力', E'Michinaru Nōryoku', E'Habilidade Desconhecida', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(909, E'Habilidade Geral', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(910, E'Habilidade de Regeneração', E'A Habilidade de Regeneração é um poder que encontra-se no interior das células de Hashirama Senju que possuem uma imensa capacidade de regeneração, capaz de curar as lesões de alguém em meros momentos.', E'再生能力', E'Saisei Nōryoku', E'Habilidade de Regeneração', NULL, NULL, NULL, NULL, NULL, NULL),
(911, E'Herói do Sharingan', E'Obito executa a Liberação de Fogo: Técnica da Grande Bola de Fogo e após o oponente ficar queimado pelas chamas, ele aparece atrás do oponente e apunhala-o com uma kunai.', E'写輪眼の英雄', E'Sharingan no Eiyū', E'Herói do Olho Copiador Giratório', E'Herói do Sharingan', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(912, E'Hiden', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(913, E'Hiden: Técnica de Ocultação no Pó de Escama', NULL, E'秘伝・鱗粉隠れの術', E'Hiden: Rinpungakure no Jutsu', E'Secreto: Técnica de Ocultação no Pó de Escama', NULL, E'Estilo Secreto: Técnica da Cortina de Pó de Escama', NULL, E'Curto a Médio alcance', NULL, E'Tigre → Juntar as Maos'),
(914, E'Hiden: Técnica do Ataque Envolvente do Pó de Escama', E'Fū, transformada em Chōmei, bate suas asas para liberar um pó peculiar. Enquanto neste estado Fū também pode usar este pó gerado para atirar várias pequenas esferas no oponente.', E'秘伝・鱗粉抱撃の術ひでん・りんぷんほうげき', E'Hiden: Rinpun Hōgeki no Jutsu', E'Hiden: Técnica do Ataque Envolvente do Pó de Escama', E'Secreto: Barragem da Bola Rasteira Escamada', NULL, NULL, E'Todos os alcances', NULL, NULL),
(915, E'Hiden: Técnica do Pó de Escama', E'Fū manifesta as asas de Chōmei e bate-as no oponente, mandando uma onda de pó de escama.', E'秘伝・鱗粉の術', E'Hiden: Rinpun no Jutsu', E'Hiden: Técnica do Pó de Escama', E'Secreto: Jutsu da Bola Rasteira Escamada', NULL, NULL, E'Curto alcance', NULL, NULL),
(916, E'Horizontal de Relâmpago Violento', E'Coberto pelo Modo de Chakra de Liberação de Relâmpago, A executa um poderoso golpe horizontal contra o adversário, de tal forma a ser poderoso o suficiente para rachar os ossos da caixa torácica do Susanoo tanto de Sasuke Uchiha como de Madara.', E'雷虐水平らいぎゃくすいへい', E'Raigyaku Suihei', E'Horizontal de Relâmpago Violento', NULL, E'Lâmina Transgressora do Relâmapgo', NULL, E'Curto alcance', NULL, NULL),
(917, E'Horror do \'Amanhecer\'', E'Esta é uma combinação única dos membros da Akatsuki, onde cada um deles usa uma de suas especialidades.', E'戦慄の\'暁\'せんりつの「あかつき」', E'Senritsu no \'Akatsuki\'', E'Horror do \'Amanhecer\'', E'Horror da \'Akatsuki\'', NULL, NULL, E'Todos os alcances', NULL, NULL),
(918, E'Hyūga: Grande Céu Giratório', E'Uma técnica colaborativa utilizada por dois membros do Clã Hyūga. Um executa a técnica Proteção de Oito Trigramas Sessenta e Quatro Palmas e o outro usa Oito Trigramas Palmas Giratórias do Céu em torno dele. Isso cria uma enorme esfera com tanto poder ofensivo quanto defensivo, causando grande dano ao adversário.', E'日向・大回天ひゅうが・だいかいてん', E'Hyūga: Daikaiten', E'Hyūga: Grande Céu Giratório', E'Hyūga: Grande Rotação', NULL, NULL, E'Curto alcance', NULL, NULL),
(919, E'Iaidō', E'O termo Iaidō (居合道) refere-se a um estilo específico de kenjutsu que se preocupa com os movimentos suaves e controlados de sacar a espada da bainha, golpeando ou cortando um adversário, retirando o sangue da lâmina, e, em seguida, colocando a espada na bainha. O movimento linear e força aplicada para desenhar a espada de seu resultado resulta em cortes muito mais rápidos do que os realizados com a espada já desenhada.', E'居合道いあいどう', E'Iaidō', E'Caminho de Iai', E'Battōjutsu[1]', E'Iai', NULL, E'Curto alcance', NULL, NULL),
(920, E'Ilusão Causada pelas Mariposas Venenosas', E'Fuen primeiro usa um veneno que é fino e pequeno o suficiente para ser levado no ar. Seu alvo irá inalar o veneno e começará a ver as coisas que Fuen escolhe, como cavernas, mariposas, ou aliados do alvo tentando matá-lo. Quando seu alvo está preso em seu genjutsu, Fuen ataca e mata seu alvo. Sakura Haruno foi capaz de criar um antídoto para a técnica que lhe permitiu derrotar Fuen muito facilmente. É ideal para espaços confinados.', NULL, NULL, NULL, NULL, NULL, NULL, E'Longo Alcance', NULL, NULL),
(921, E'Ilusão Demoníaca: Aprisionamento da Árvore Assassina', E'Este é um dos genjutsu transmitidos desde os tempos antigos em Konohagakure. Primeiro o usuário desaparece da vista de todos como uma névoa, a fim de se aproximar do alvo sem ser detectado. Uma vez perto o suficiente, o alvo será, então, completamente privado de sua mobilidade, vendo a miragem de uma árvore crescendo rápido e enrolando-se ao redor dele. Tendo em conta que o alvo permanece consciente, esta técnica se mostra extremamente eficiente para a coleta de informações. Uma vez que a vítima fica imóvel, o usuário pode atacar o inimigo, geralmente depois de emergir a partir do tronco da árvore.', E'魔幻・樹縛殺', E'Magen: Jubaku Satsu', E'Ilusão Demoníaca: Aprisionamento da Árvore Assassina', E'Ilusão do Demônio: Aprisionamento da Árvore Assassina', E'Ilusão Demoníaca: Galhos da Restrição Fatal', NULL, E'Curto alcance', E'Rank B', E'Cao → Cobra → Macaco → Boi → Tigre'),
(922, E'Ilusão Demoníaca: Confronto do Canto de Sapo', E'Ilusão Demoníaca: Confronto do Canto de Sapo é um genjutsu à base de som transmitido do Monte Myōboku. Porque a aprendizagem requer muitos anos de treinamento, apenas os Dois Grandes Sábios Sapos, Fukasaku e Shima, têm as habilidades para realizar isso.', E'魔幻・蝦蟇臨唱まげん・がまりんしょう', E'Magen: Gama Rinshō', E'Ilusão Demoníaca: Confronto do Canto de Sapo', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(923, E'Ilusão Demoníaca: Destruição da Árvore Aprisionadora', E'Depois de utilizar a Ilusão Demoníaca: Aprisionamento da Árvore Assassina, a árvore que prende o oponente é perfurada por várias kunai.', E'魔幻・樹縛滅殺まげん・じゅばくめっさつ', E'Magen: Jubaku Messatsu', E'Ilusão Demoníaca: Destruição da Árvore Aprisionadora', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(924, E'Ilusão Demoníaca: Encantamento de Persuasão', E'Trata-se um genjutsu muito forte que permite o usuário a colocar um oponente sob uma ilusão usando apenas seu discurso. Enquanto encontra-se sob os efeitos do genjutsu, o alvo pode ser facilmente influenciado pela sua vontade e autosegurança. A ilusão não pode ser desfeita, ainda que a pessoa perceba que que o genjutsu está sob efeito, porém fortes conexões emocionais, como o amor, podem ajudar a dispersar a técnica.', E'魔幻・蠱惑の甘言', E'Magen: Kowaku no Kangen', E'Ilusão Demoníaca: Encantamento de Persuasão', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(925, E'Ilusão Demoníaca: Estrutura Fumegante de Vários Andares', E'Um genjutsu de longo alcance (広範囲幻術, Kōhani Genjutsu), em que o Gengetsu Hōzuki, em conjunto com a sua invocação, é capaz de manifestar miragens realistas usando a névoa exalada pelo molusco. Como é impossível para observadores distinguir a coisa real, através de métodos normais, esta propriedade faz com que a técnica seja ideal para esconder a localização real de um indivíduo, resultando em confusão entre a oposição como seus ataques são ineficazes e tentam determinar a verdadeira posição do par. No entanto, a fim de manter as ilusões, o molusco deve continuamente exsudar a névoa de seus sifões, durante toda a duração da técnica.', E'魔幻・気蒸の楼閣まげん・きじょうのろうかく', E'Magen: Kijō no Rōkaku', E'Ilusão Demoníaca: Estrutura Fumegante de Vários Andares', NULL, E'Genjutsu de Miragem', NULL, E'Todos os alcances', E'Rank B', NULL),
(926, E'Ilusão Demoníaca: Explosão do Aprisionamento da Árvore Assassina', E'Kurenai usa a Ilusão Demoníaca: Aprisionamento da Árvore Assassina para aprisionar o seu alvo que, em seguida, explode.', NULL, NULL, NULL, NULL, NULL, NULL, E'Longo alcance', NULL, E'Tigre'),
(927, E'Ilusão Demoníaca: Flor da Névoa', E'Kurenai prende o oponente em um botão de flor, e detona-o em uma explosão florida.', E'魔幻・花霞', E'Magen: Hanagasumi', E'Ilusão Demoníaca: Flor da Névoa', E'Ilusão Demoníaca: Flor da Neblina', NULL, NULL, E'Curto alcance', NULL, E'Cobra → Selo de mao especifico da técnica'),
(928, E'Ilusão Demoníaca: Miragem do Corvo', E'Lançando corvos reais contra o inimigo, este genjutsu intercepta a vítima em uma poderosa ilusão, projetando uma torrente de corvos e deixando o adversário à mercê do usuário, permitindo uma conversa forçada ou uma distração para um ataque.', E'魔幻・蜃鴉まげん・しんからす', E'Magen: Shinkarasu', E'Ilusão Demoníaca: Corvo Amêijoa', E'Ilusão Demoníaca: Miragem de Corvos', NULL, NULL, E'Curto alcance', NULL, NULL),
(929, E'Ilusão Demoníaca: Mudança do Espelho do Céu e da Terra', E'O usuário decifra um genjutsu que foi aplicado sobre ele e lança de volta uma técnica com efeitos idênticos no oponente. É uma forma de inversão de ilusão, mas para ler instantaneamente através de uma técnica e lançá-la de volta, o poder do Sharingan é necessário. Como genjutsu é inicialmente usado para confundir o inimigo, essa técnica, que envia-o de volta ao seu conjurador, causa uma grande quantidade de dano mental, mais do que qualquer outra coisa.', E'魔幻・鏡天地転まげん・きょうてんちてん', E'Magen: Kyō Tenchi-ten', E'Ilusão Demoníaca: Mudança do Espelho do Céu e da Terra', NULL, E'Ilusão Demoníaca: Inversão do Céu e Terra', NULL, E'Curto alcance', NULL, NULL),
(930, E'Ilusão Demoníaca: Paralisia de Pregos', E'O usuário, enquanto no Modo Tsukuyomi, invoca grandes picos brancos para prender o oponente dentro da barreira.', NULL, NULL, E'Ilusão Demoníaca: Paralisia de Pregos', E'Ilusão do Demônio: Prego Paralisador', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(931, E'Ilusão Demoníaca: Sombra Voadora', E'Kurenai lança o adversário sob seu genjutsu, fazendo com que o adversário fique confuso. Asuma então ataca-o com sua Andorinha Voadora, derrubando o adversário para fora.', E'魔幻・飛影まげん・ひえい', E'Magen: Hiei', E'Ilusão Demoníaca: Sombra Voadora', E'Ilusão do Demônio: Sombra Voadora', NULL, NULL, E'Curto alcance', NULL, NULL),
(932, E'Ilusão Demoníaca: Técnica da Loucura do Espírito Lutador', E'Genshō Ryūdōin cria uma ilusão pilares roxos que têm a aparência de um rosto no meio e usa-os alternadamente para infringir danos aos inimigos. Esta técnica foi forte o suficiente para deixar os 11 de Konoha inconscientes, com exceção de Naruto que Genshō não atacou, bem como absorver seu chakra.', E'魔幻・狂気精神戦闘機の術まげん・きょうきせいしんせんとうきのじゅつ', E'Magen: Kyōki Seishin Sentōki no Jutsu', E'Ilusão Demoníaca: Técnica da Loucura do Espírito Lutador', E'Ilusão do Mal: Jutsu da Loucura do Espírito Lutador', NULL, NULL, E'Curto alcance', NULL, NULL),
(933, E'Ilusão Demoníaca: Técnica da Visão do Inferno', E'Este é um genjutsu que revela os medos que habitam dentro do coração das pessoas. Todo mundo tem uma imagem da única coisa que não quer nunca ver. Este genjutsu é uma técnica que chama para fora essa tal imagem a partir de dentro do coração e o modifica para a realidade. Primeiro, um círculo imaginário de folhas vai girar ao redor e envolver o alvo, fazendo-o cair logo depois. Após um curto período, a ilusão começará. Este faz com que a ilusão seja muito convincente, uma vez que o usuário provavelmente for movido antes a ilusão se ajusta dentro e se a imagem mental é horrível, o choque será grande nesse sentido.', E'魔幻・奈落見の術まげん・ならくみのじゅつ', E'Magen: Narakumi no Jutsu', E'Ilusão Demoníaca: Técnica da Visão do Inferno', E'Ilusão do Mal: Jutsu da Miragem da Morte', NULL, E'Jutsu de Ilusão: Controle Mental', E'Curto alcance', E'Rank D', E'Cobra → Rato'),
(934, E'Ilusão Demoníaca: Técnica de Estacas de Agrilhoar', E'Primeiramente, esta técnica requer a utilização do Genjutsu: Sharingan. O adversário é pego no mundo ilusório criado pelo usuário, e a vítima é atormentada com a sensação de ter estacas impulsionadas através de seus membros, tirando a liberdade do seu corpo. Ao mesmo tempo, a dor física que acompanha a ilusão revela que a maior utilidade desta técnica é a tortura.', E'魔幻・枷杭の術', E'Magen: Kasegui no Jutsu', E'Ilusão Demoníaca: Tranca de Estacas', NULL, NULL, NULL, E'Curto alcance', E'Rank –', NULL),
(935, E'Ilusão Demoníaca: Técnica do Arredor Falso', E'Esta técnica é um genjutsu que permite enganar os outros para persuadir o adversário, fazendo-o acreditar que está em outro local, impedindo-o de ver certos aspectos do cenário em que está, criando brechas para armadilhas que nem sequer poderão ser vistas. A principal característica desta técnica é que ela pode ser lançada sobre uma superfície extensa, portanto, tudo e qualquer coisa que entrar na área de efeito da ilusão cairá sobre a técnica. Dito isso, ela pode não funcionar tão bem em pessoas que são bem versadas ​​ou possuem outras formas hábeis em genjutsu.', E'魔幻・此処非の術まげん・ここにあらずのじゅつ', E'Magen: Kokoni Arazu no Jutsu', E'Ilusão Demoníaca: Técnica do Arredor Falso', NULL, NULL, E'Genjutsu: Lugar Falso', E'Todos os alcances', E'Rank C', NULL),
(936, E'Ilusão Demoníaca: Técnica do Arredor Falso Duplo', E'Esta técnica coloca uma ilusão dentro de outra ilusão criada pelo usuário. Quando o alvo dissipa a primeira ilusão, eles não vão perceber que a segunda está no lugar.', E'魔幻・二此処非の術', E'Magen: Nijū Kokoni Arazu no Jutsu', E'Ilusão Demoníaca: Técnica do Arredor Falso Duplo', NULL, NULL, NULL, NULL, NULL, NULL),
(937, E'Ilusão Demoníaca: Técnica do Inferno Decrescente', E'Após os selos de mão necessários, o usuário cria a ilusão de uma imensa bola de fogo caindo do céu em uma área designada. Os alvos envolvidos na ilusão literalmente sentem a temperatura quente da bola se aproximando.', E'魔幻・地獄業火の術まげん・じごくごうかのじゅつ', E'Magen: Jigoku Gōka no Jutsu', E'Ilusão Demoníaca: Técnica do Inferno Decrescente', NULL, NULL, E'Ilusão Demoníaca: Jutsu de Fogo', E'Médio alcance', NULL, E'Javali → Cao → Passaro → Macaco → Carneiro'),
(938, E'Ilusão do Aprisionamento na Corda de Feijão', E'Uma técnica comum entre os membros do clã Kurama, o usuário faz-se desaparecer, enquanto o alvo vê várias vinhas de feijão que crescem a partir do chão. Estas vinhas prendem o corpo da vítima, levantando-a no ar. Em seguida, uma vagem de feijão eleva-se e se abre para revelar o usuário, pronto para atacar. No entanto, um usuário qualificado de genjutsu como Kurenai pode reverter os efeitos para o usuário.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, E'Cachorro → Coelho → Cobra → Carneiro'),
(939, E'Imagem do Gato Escondido na Areia', E'Imagem do Gato Escondido na Areia é um técnica sensorial que utiliza matéria, como a areia de Gaara.', E'潜砂絵猫', E'Sensa Kaibyō', E'Imagem do Gato Escondido na Areia', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(940, E'Impacto Dilacerante da Flor de Cerejeira', E'Sakura soca o chão para desencadear uma onda de chakra, que persegue e explode junto ao oponente.', E'桜花裂衝おうかれっしょう', E'Ōka Resshō', E'Impacto Dilacerante da Flor de Cerejeira', E'Explosão da Flor de Cerejeira', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(941, E'Impacto Vazio', E'Utilizando a Técnica do Tamanho Múltiplo Parcial, o usuário bate no chão com seus punhos aumentados, fazendo com que os destroços sejam lançados para o ar. Rapidamente então ele bate nos escombros que ainda estão no ar e lança-os contra o alvo, esmagando o que tocar. A técnica foi poderosa o suficiente para fazer a Areia de Ferro de Shinki escorrer enquanto na forma de capa, mas não foi capaz de penetrar suas defesas. No entanto, um oponente rápido o suficiente é capaz de escapar dos projéteis.', E'からのドーン', E'Kara no Dōn', E'Boom', NULL, NULL, NULL, NULL, NULL, NULL),
(942, E'Impacto Verde', E'Naruto realiza Furacão da Folha para desabilitar o adversário, então varrê-o com os seus pés usando o Vendaval da Folha. Depois disso, ele lança o oponente para o céu e usa o Rasengan. Naruto só pode usar esta técnica com o terno de Guy.', E'碧い衝撃', E'Aoi Shōgeki', E'Impacto Verde', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(943, E'Impacto da Armadura de Rocha', E'Esta é uma técnica de força sobre-humana que usa a durabilidade da Armadura de Rocha.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(944, E'Impacto da Flor de Cerejeira', E'Esta é uma técnica de força sobre-humana que usa de fundamentos médicos, tais como a capacidade de concentração e controle de chakra minucioso, para ser realizada.', E'桜花衝おうかしょう', E'Ōkashō', E'Impacto da Flor de Cerejeira', NULL, E'Ataque Terrestre da Hercúlea', E'Colisão da Flor de Cerejeira', E'Curto alcance', E'Rank C', NULL),
(945, E'Impacto do Fluxo de Água', E'Kisame converte seu chakra em água e, em seguida, expele-a para frente. Com a água em torno de si, ele a manipula para bater e derrubar o oponente, enfraquecendo-o ou afogando-o.', E'水流衝', E'Suiryūshō', E'Impacto do Fluxo de Água', E'Colisão do Fluxo de Água', NULL, NULL, E'Curto alcance', NULL, NULL),
(946, E'Impacto do Rochedo Celestial', E'O usuário, utilizando-se de sua força sobre-humana soca o chão e o destrói por completo. Em meio à destruição causada por seu soco, ele puxa um enorme fragmento e lança com força contra seu oponente.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(947, E'Impermanência de Coisas Mundanas', E'O Grupo Magaki executa os selos de mão necessários e emite seu chakra para o céu, criando um losango de chakra sólido com imensa pressão interna, o que a faz explodir e lançar diversas lascas que perfuram o inimigo. De acordo com Yamato, a técnica é muito formidável.', E'諸行無常', E'Shogyō Mujō', E'Impermanência de Coisas Mundanas', NULL, NULL, NULL, NULL, NULL, E'Cavalo + Cobra + Tigre + Cobra Simultaneamente'),
(948, E'Impulso da Flor', E'Kimimaro, enquanto em seu selo amaldiçoado nível dois, corre em direção a seu alvo e golpeia-o com os ossos de suas costas. Essa técnica foi poderosa o suficiente para atravessar uma das defesas de Gaara.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(949, E'Impulso do Pilar de Água', E'Tobirama usa sua maestria na Liberação de Água para materializar um pequeno corpo de água para levantar-se debaixo do oponente e empurrá-lo para o ar.', E'水柱突', E'Suichūtotsu', E'Impulso do Pilar de Água', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(950, E'Impulso do Punho Certeiro', E'O usuário corre contra o oponente e ataca-o com um soco reforçado com chakra, enviando-o para longe. A potência do soco é tão grande que foi capaz de causar sequelas graves em Shin Uchiha ao ponto da necessidade de um transplante de órgãos.', E'正拳突きせいけんつき', E'Seikentsuki', E'Impulso do Punho Certeiro', E'Impulso Reto', NULL, NULL, E'Curto alcance', NULL, NULL),
(951, E'Inclinação da Flor', E'Kurenai ataca o oponente com um golpe de cotovelo, seguido com dois chutes e uma ataque com uma kunai antes de terminar com um chute por baixo arrebatador que gera um anel de pétalas de flor de cerejeira em torno dela.', E'枝垂れ華', E'Shidarehana', E'Inclinação da Flor', E'Flor Desbotada', NULL, NULL, E'Curto alcance', NULL, NULL),
(952, E'Incêndio da Chama do Macaco', E'Son Gokū ou Rōshi transformado, expele chamas verdes no adversário.', E'猿炎放火', E'En\'en Hōka', E'Incêndio da Chama do Macaco', E'Lançamento da Chama do Macaco', NULL, NULL, E'Curto alcance', NULL, NULL),
(953, E'Infinita Dança Selvagem', E'Depois de abrir alguns dos Oito Portões, Lee desestabiliza o oponente e ataca-o muitas vezes na velocidade da luz, movendo-se tão rápido que parece que há três Lee atacando ao mesmo tempo. Para terminar, Lee soca o inimigo no céu três vezes, lançando-o de volta para o chão.', E'無限乱舞', E'Mugen Ranbu', E'Infinita Dança Selvagem', E'Lótus da Dança da Infinidade', NULL, NULL, E'Curto alcance', NULL, NULL),
(954, E'Ingestão de Chakra', E'Através do contato físico, Yoroi é capaz de absorver o chakra de uma vítima. Sua habilidade é ligeira de modo a lhe possibilitar o roubo de uma grande quantidade de chakra de Sasuke em poucos segundos.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre'),
(955, E'Inscrição Absoluta', E'Com sua espada, o usuário rapidamente corta tudo em torno de si.', E'絶銘', E'Zetsumei', E'Inscrição Absoluta', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(956, E'Inseto Explosivo', E'O usuário cobre o inimigo com uma cortina de fumaça para dispersar larvas que serão inaladas, elas vão crescer dentro do corpo e virar uma aranha parasita que explode, matando seu hospedeiro. O crescimento do inseto depende da quantidade de chakra usada, se o hospedeiro usar uma enorme quantidade de chakra pode acelerar o processo de crescimento. O inseto pode, contudo, ser removido por meio de cirurgia.', E'爆裂虫ばくれつちゅう', E'Bakuretsuchū', E'Inseto Explosivo', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(957, E'Inseto Parasita Gigante — Mordida de Inseto', E'Na sequência de um membro do clã Aburame colocar um número de kidaichū na pele de um inimigo, esses insetos enterram-se imediatamente no corpo da vítima e começam a devorar sua carne e chakra. Este processo faz com que os insetos a cresçam rapidamente em tamanho, até que o corpo do hospedeiro é completamente corroído de dentro para fora.', E'寄大蟲　虫食いきだいちゅう　むしくい', E'Kidaichū — Mushikui', E'Inseto Parasita Gigante — Mordida de Inseto', NULL, E'Grande Inseto Parasita Devorador Voraz', NULL, E'Curto alcance', E'Rank –', NULL),
(958, E'Insetos Miméticos: Rastejamento', E'Shino convoca seus kikaichū em sua volta para engolfar o oponente em uma pequena barreira que implode nele.', E'擬態蟲・這', E'Gitaichū: Sha', E'Insetos Miméticos: Rastejamento', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(959, E'Insetos Parasitas Destrutivos: Eclipse', E'Shino manipula seus kikaichū para formar um pilar gigante que desmorona em forma de onda que engole seu oponente. Os insetos em seguida, criam um redemoinho que arrasta o oponente para abaixo do solo.', E'寄壊蟲・蝕きかいちゅう・しょく', E'Kikaichū: Shoku', E'Insetos Parasitas Destrutivos: Eclipse', E'Insetos Parasitas: Digerir', NULL, NULL, E'Todos os alcances', NULL, NULL),
(960, E'Insetos Parasitas Destrutivos: Enxame', E'Shino libera vários kikaichū atrás de seu oponente, que começam se envolver em torno deles. Ao mesmo tempo, Shino envia mais insetos em um padrão espiral. Todos os insetos fazem um enxame no oponente, danificando-o e, em seguida, dispersando.', E'寄壊蟲・簇きかいちゅう・むら', E'Kikaichū: Mura', E'Insetos Parasitas Destrutivos: Enxame', E'Insetos Parasitas: Enxame', NULL, NULL, E'Curto alcance', NULL, NULL),
(961, E'Insetos Parasitas Destrutivos: Formas', E'O usuário envia seus insetos para criar variadas formas que podem ser usadas para exemplificar algo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Selo de Confronto'),
(962, E'Insetos Parasitas Destrutivos: Granizo', E'Shino libera milhares de insetos de seu corpo no ar. Em seu comando, os insetos atacam o oponente em cinco ondas. Os insetos fundem-se se transformando em uma grande nuvem que chove sobre o oponente, e, em seguida, os insetos formam um inseto gigante como uma esfera de meteoros desaba sobre o oponente.', E'寄壊蟲・霰', E'Kikaichū: Arare', E'Insetos Parasitas Destrutivos: Granizo', E'Insetos Parasitas: Tempestade de Granizo', NULL, NULL, E'Curto alcance', NULL, NULL),
(963, E'Insetos Parasitas Destrutivos: Lança', E'Os kikaichū de Shino combinam em duas formas de lança, como que em espiral em direção ao oponente e leva-o para o ar, onde brutalmente banqueteiam-se com seu chakra.', E'寄壊蟲・槍きかいちゅう・そう', E'Kikaichū: Sō', E'Insetos Parasitas Destrutivos: Lança', E'Insetos Parasitas: Lança', NULL, NULL, E'Curto alcance', NULL, NULL),
(964, E'Insetos Parasitas Destrutivos: Oco', E'Shino manipula os kikaichū para formar uma grande nuvem no ar que desce sobre o oponente. Depois que os insetos estão no terreno em torno do oponente, os enxame de insetos cai em cima dele, e rapidamente entra em erupção em uma explosão de insetos.', E'寄壊蟲・虚', E'Kikaichū: Utsuro', E'Insetos Parasitas Destrutivos: Oco', E'Insetos Parasitas: Decepção', NULL, NULL, E'Médio alcance', NULL, NULL),
(965, E'Instante', E'O usuário rapidamente aparece na frente de seu adversário e corta-o, movendo-se para trás dele. O adversário posteriormente entra em colapso quando o usuário termina, dando tempo de embainhar a lâmina novamente. Este movimento é forte o suficiente para matar um alvo, enquanto está no selo amaldiçoado nível 2.', E'刹那', E'Setsuna', E'Instante', E'Lâmina Instantânea', NULL, NULL, E'Curto alcance', NULL, NULL),
(966, E'Instrução de Punho Gentil', E'Hanabi atinge seu oponente com vários movimentos do Punho Gentil, fazendo-o cair no chão. Hanabi em seguida, vai em direção a seu pai, Hiashi, que lhe pergunta como ela conseguiu. Hiashi percebe então que o oponente começa a levantar-se, e com uma velocidade incrível desfere um forte golpe, com seu punho no inimigo.', E'柔拳指南じゅうけんしなん', E'Jūken Shinan', E'Instrução de Punho Gentil', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(967, E'Intenção Assassina', E'A Intenção Assassina é simplesmente o ato do usuário exalar uma pura intenção de matar, e afetando seu oponente, eles mesmos, e outros ao seu redor, a ponto de paralisar o oponente de terror. Quando a intenção é particularmente forte, ele pode fazer as vítimas terem visões de sua própria morte. Isto pode confundir o método com um genjutsu, apesar de ser apenas uma habilidade geral.', E'殺気さっき', E'Sakki', E'Intenção Assassina', E'Intenção de Matar', NULL, E'Intenção Mortífera', NULL, NULL, NULL),
(968, E'Inukai Takeru no Mikoto', E'Inukai Takeru no Mikoto permite personificar o chakra em cães selvagens que emergem a partir do terreno circundante. Eles perseguem seu alvo implacavelmente e, quando o alcançam, o oprimem com suas fortes mandíbulas, restringindo e esmagando-o; sua velocidade de rastreamento foi grande ao ponto de capturar Sasuke Uchiha.', E'犬飼健命いぬかいたけるのみこと', E'Inukai Takeru no Mikoto', NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(969, E'Inundação das Ondas Explosivas de Areia', E'Gaara aprisiona o oponente em um vórtice de areia, que, em seguida, tem seu tamanho reduzido. Com o oponente contido e um vácuo no deserto, Gaara fecha a mão para esmagá-lo. A areia da qual prendia o oponente retorna ao tamanho original, criando, assim, ondas no deserto.', E'濤砂瀑氾とうさばくはん', E'Tōsabakuhan', E'Inundação das Ondas Explosivas de Areia', E'Onda de Inundação da Cascata de Areia', NULL, NULL, E'Todos os alcance', NULL, NULL),
(970, E'Investida Aérea de Múltiplos Clones do Seis Caminhos', E'No ar, Naruto cria alguns clones que formam o Rasengan e são lançados como projéteis para chocarem-se contra o oponente em uma velocidade absurda, jogando-o no chão com extrema compulsão.', NULL, NULL, NULL, E'Investida Aérea de Múltiplos Clones do Seis Caminhos', NULL, NULL, E'Médio alcance', NULL, NULL),
(971, E'Invocação: Bastão Adamantino', E'Aproveitando o corpo transformado em bastão de Enma, Hiruzen empunha-o e desfere uma sucessão de golpes rápidos e poderosos contra o alvo. A técnica foi mostrada sendo forte o suficiente para cortar até mesmo a Liberação de Madeira de Hashirama e as raízes da Árvore Divina.', E'口寄せ・金剛如意くちよせ・こんごうにょい', E'Kuchiyose: Kongōnyoi', E'Invocação: Bastão Adamantino', NULL, NULL, NULL, NULL, NULL, NULL),
(972, E'Invocação: Boilergama', E'O usuário invoca um gigante sapo fumegante. O imenso tamanho do sapo é um perigo para qualquer um abaixo da área de invocação.', E'口寄せ・蒸気蝦蟇', E'Kuchiyose: Boira Gama', E'Invocação: Sapo Fumegante', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(973, E'Invocação: Chamas da Montanha Flamejante', E'Koji desenha um círculo ritualístico no chão abaixo de seu alvo e, ao juntar as mãos, ele conjura chamas da distante Montanha Flamejante para incinerá-lo. Uma vez que são chamas naturais e não envolvem chakra em sua composição, é impossível absorvê-las com técnicas absorvedoras de chakra.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, E'Juntar as Maos'),
(974, E'Invocação: Câmara de Tortura', E'Essa técnica é utilizada por Ibiki que invoca uma câmara de tortura de ferro em torno de si mesmo e do oponente. Primeiramente, os braços e as pernas do inimigo são capturados por quatro correntes e, então, as paredes da gaiola aparecem planas no chão, rapidamente dobrando-se como uma caixa juntas. Finalmente, uma grande estátua cai em cima da gaiola, que é então fortemente presa em correntes.', E'口寄せ・拷問部屋', E'Kuchiyose: Gōmon Heya', E'Invocação: Câmara de Tortura', NULL, NULL, NULL, E'Curto alcance', NULL, E'Javali → Cao → Passaro → Macaco → Carneiro'),
(975, E'Invocação: Dança da Decapitação Rápida', E'Temari invoca Kamatari, uma doninha de um olho só segurando uma foice gigante com outras foices menores ao redor dela, ao colocar sangue em seu Leque Dobradiço Gigante como pagamento do contrato e, em seguida, girando-o. O leque chicoteia um vendaval poderoso e impulsiona Kamatari — que cavalga no vento, cortando através de tudo o que toca. Os efeitos da técnica se estendem a várias centenas de metros, arrastando e dilacerando árvores, pedras e outras formas de detritos, que, em seguida, caem sobre o inimigo indefeso. É altamente eficaz quando cercado por todos os lados, quando a posição do inimigo é relativamente desconhecida, ou quando este está escondido atrás de um escudo.', E'口寄せ・斬り斬り舞くちよせ・きりきりまい', E'Kuchiyose: Kirikiri Mai', E'Invocação: Dança da Decapitação Rápida', NULL, E'Invocação: Dança da Navalha', E'Invocação: Dança da Espada', E'Todos os alcances', E'Rank B', NULL),
(976, E'Invocação: Dança de Colapso do Leque', E'Temari invoca Kamatari e, em seguida, Kamatari gira com sua kama e corta o oponente para afastá-lo, cortando depois em pedaços tudo o que estiver pelo caminho em um combo, podendo causar uma avalanche para esmagar o inimigo.', E'口寄せ・扇舞降ろし', E'Kuchiyose: Senmai Oroshi', E'Invocação: Queda da Dança do Leque', NULL, NULL, NULL, NULL, NULL, NULL),
(977, E'Invocação: Espada Gigante', E'Ao bater a mão no chão, o selo de invocação aparece e invoca uma espada gigante para usar em batalha.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(978, E'Invocação: Estátua Demoníaca do Caminho Exterior', E'Invocação: Estátua Demoníaca do Caminho Exterior, é uma técnica concedida pelo portador do Rinnegan.', E'口寄せ・外道魔像', E'Kuchiyose: Gedō Mazō', E'Invocação: Estátua Demoníaca do Caminho Exterior', NULL, E'Jutsu de Invocação: Estátua do Demônio do Inferno', E'Jutsu de Invocação: Estátua Gedo', E'Todos os alcances', E'Rank –', NULL),
(979, E'Invocação: Fūma Shuriken', E'Ao executar o selo de mão necessário, o usuário invoca uma Fūma Shuriken para usar em batalha.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, E'Juntar as Maos[1] ou Selo do Confronto[2]'),
(980, E'Invocação: Ibuse', E'Hanzō invoca Ibuse, que cria uma nuvem de veneno na área geral a sua frente, envenenando seu oponente.', E'口寄せ・イブセ', E'Kuchiyose: Ibuse', E'Invocação: Ibuse', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(981, E'Invocação: Inferno de Rashōmon', E'Sakon e Ukon separam-se e usam uma técnica de invocação combinada para invocar o Rashōmon no chão. O portão demoníaco abre-se e engole o oponente em outra dimensão.', E'口寄せ・羅生門奈落', E'Kuchiyose: Rashōmon Naraku', E'Invocação: Inferno de Rashōmon', E'Invocação: Abismo de Rashōmon', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(982, E'Invocação: Kunai', E'Ao abrir uma de suas mãos, Sakon invoca uma kunai para usar em batalha.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(983, E'Invocação: Liberação de Terra: Técnica da Presa Rastreadora', E'Um ninjutsu de rastreamento ofensivo de alta qualidade, onde é invocado ninken, para que eles possam perseguir o alvo pelo subsolo e atacá-lo simultaneamente com as suas presas.', E'口寄せ・土遁・追牙の術くちよせ・どとん・ついがのじゅつ', E'Kuchiyose: Doton: Tsuiga no Jutsu', E'Invocação: Liberação de Terra: Técnica da Presa Rastreadora', NULL, NULL, E'Invocação: Estilo Terra: Jutsu Perseguição de Garras', E'Todos os alcances', E'Rank B', E'Tigre → Cobra → Dragao → Cão'),
(984, E'Invocação: Marionete', E'Esse fūinjutsu é utilizado pelos marionetistas a fim de trasportar suas marionetes dentro de pergaminhos e, quando necessário, convocá-las para a batalha.', E'口寄せ・傀儡くちよせ・くぐつ', E'Kuchiyose: Kugutsu', E'Invocação: Marionete', E'Drama dos Marionetes[3]', NULL, NULL, NULL, NULL, NULL),
(985, E'Invocação: Portão da Água de Chuva', E'Trata-se de uma forma da Técnica de Invocação, trazendo um único portão resistente o suficiente para suportar um ataque de taijutsu de Rock Lee. No entanto, um papel explosivo vindo por trás é capaz de destruir o portão.', E'口寄せ・雨水門', E'Kuchiyose: Usuimon', E'Invocação: Portão da Água de Chuva', NULL, NULL, NULL, E'Curto alcance', NULL, E'Javali → Cao → Passaro → Macaco → Bater a mão no solo');