INSERT INTO "Jutsu" ("id", "name", "description", "kanji", "romaji", "portugues", "games", "mangaPanini", "tvBrasileira", "range", "rank", "handSeals") VALUES
(1, E'ADN', E'Por meio do poder do senjutsu, Kabuto é capaz de usar livremente as habilidades, talentos e kekkei genkai de outros ninjas cujo material genético está integrado dentro de si mesmo. Como alternativa, ele pode canalizar suas habilidades coletivas em respectivos clones irracionais de cada shinobi. Essas cópias são geradas em de seu umbigo de cobra.', E'伝異遠影ディーエヌエー', E'Dīenuē', E'ADN; DNA; Incompatível Herança Distante das Sombras', NULL, NULL, NULL, NULL, E'Rank –', NULL),
(2, E'A Herança da Vontade do Fogo', E'Hashirama, Tobirama e Hiruzen usam uma combinação de madeira, fogo e água para empurrar o oponente. Hashirama usa sua Técnica Secreta da Liberação de Madeira: Natividade de um Mundo de Árvores para criar uma grande floresta em que Tobirama e Hiruzen atacam o oponente usando uma combinação de fogo e água. Os três Hokage então atacam o oponente, após Hashirama usar o Punho Adamantino, Tobirama chuta o oponente e Hiruzen invoca Enma que então ataca o oponente com o bastão adamantino. A força do ataque é tanta que cria uma explosão gigante na floresta no processo da conclusão da técnica.', E'受け継がれる火の意志うけつがれるひのいし', E'Uketsugareru Hi no Ishi', E'A Herança da Vontade do Fogo', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(3, E'Abate Duplo das Varetas de Metal', E'Tenten invoca uma ferramenta do tamanho de seu oponente para atacá-lo.', E'双金棒打ち落とし', E'Sōkanabō Uchiotoshi', E'Abate Duplo das Varetas de Metal', E'Queda Dupla da Haste de Ouro', NULL, NULL, E'Curto alcance', NULL, NULL),
(4, E'Abelha Perfurante', E'Obito demarca a área de ataque com suas Esferas da Busca da Verdade, então ele cria várias lanças que o perfuram e em seguida ele cria uma erupção de energia que joga seu oponente para o alto.', E'穿蜂', E'Ugachibachi', E'Abelha Perfurante', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(5, E'Abertura Bucal da Cobra Grande', E'Depois de ser invocado por Orochimaru, Manda desliza em direção ao oponente, em seguida, submerge e atravessa-o.', E'蛇大开口じゃだっこう', E'Jyadakkō', E'Abertura Bucal da Cobra Grande', E'Gongo da Boca do Mau', NULL, NULL, E'Curto alcance', NULL, NULL),
(6, E'Abertura dos Cinco Elementos', E'A Abertura dos Cinco Elementos é uma técnica de contrapartida direta do Selo dos Cinco Elementos.', E'五行解印ごぎょうかいん', E'Gogyō Kaiin', E'Abertura dos Cinco Elementos', NULL, NULL, E'Liberar Selamento de Cinco Elementos', E'Curto alcance', E'Rank A', NULL),
(7, E'Abertura e Encerramento do Universo', E'Obito usa o Kamui para enviar o oponente até a sua dimensão e então manipula os blocos presentes no local, criando uma formação deles. Com isso, ele abaixa o dedo verticalmente para baixo, fazendo com que todos os blocos caiem em direção ao adversário e os esmaguem.', E'天地開閉', E'Tenchi Kaihei', E'Abertura e Encerramento do Céu e da Terra', E'Criação do Mundo', NULL, NULL, NULL, NULL, NULL),
(8, E'Abertura em Chamas Sopradas', E'Matatabi, ou Yugito completamente transformada, salta no ar – de forma a ficar com a cabeça para baixo –, expele um fluxo de fogo em seu oponente e pousa entre as chamas, incinerando e esmagando tudo ao alcance delas.', E'躍炎吹踏', E'Yakuen Suitō', E'Abertura em Chamas Sopradas', E'Salto do Sopro de Chama', NULL, NULL, E'Curto alcance', NULL, NULL),
(9, E'Abordagem Acolhedora: Mil Braços Assassinos', E'Esta é a técnica final do Templo do Fogo ensinado apenas aos seus monges ninjas, e requer o Dom do Grupo Eremita para ser utilizada.', E'来迎・千手殺らいごう・せんじゅさつ', E'Raigō: Senjusatsu', E'Abordagem Acolhedora: Mil Braços Assassinos', NULL, NULL, E'Raigô: Golpe de Mil Braços', E'Curto alcance', NULL, NULL),
(10, E'Abordagem de Relâmpago', E'Em sua forma de inseto, Taisa se cobre de eletricidade e ataca o oponente.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(11, E'Abraço Ardente da Juventude', E'O usuário soca seu oponente, e em seguida, como uma de suas artimanhas, ele o abraça. O abraço é forte o suficiente para fraturar as costas de seu oponente.', E'青春の熱き抱擁せいしゅんのあつきほうよう', E'Seishun no Atsuki Hōyō', E'Abraço Ardente da Juventude', E'Abraço Apaixonante da Juventude', NULL, NULL, E'Curto alcance', NULL, NULL),
(12, E'Abraço Infinito', E'Uma barreira especial que subjuga o inimigo, empurrando-o para baixo e rapidamente drenando seu chakra. É muito difícil resistir a ela, mas Naruto Uzumaki conseguiu desfazê-la usando o chakra de Kurama.', E'無限抱擁むげんほうよう', E'Mugen Hōyō', E'Infinito Abraço', NULL, NULL, E'Mugenhōyō', E'Curto alcance', NULL, NULL),
(13, E'Abraço da Marionete', E'Ao manipular Karasu, Kankurō aperta sua mão ligada a fios de chakra fazendo com que a marionete abrace o oponente e restrinja seus movimentos. Em seguida ele pode esmagar o seu tronco, quebrando assim seus ossos.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(14, E'Abraço do Polvo', E'B manifesta duas caudas do Gyūki e as usa para segurar o oponente, avançando em direção ao inimigo e socando-o, com a mão também transformada na besta, libertando-o da prisão feita com os tentáculos pouco antes de acertá-lo.', E'億怒端数破愚', E'Okutopasu Hagu', E'Cem Milhões, Irritado, Fração, Rasgando a Insensatez', E'Abraço do Polvo', NULL, NULL, E'Curto alcance', NULL, NULL),
(15, E'Absorção de Todas as Coisas', E'Trata-se de uma habilidade inata manifestada por alguns membros do clã Ōtsutsuki, que lhes permite absorver chakra.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(16, E'Aceleração', E'O usuário libera o chakra do Oitavo Portão e aumenta a sua velocidade em várias vezes, permitindo uma rápida sequência de golpes. A velocidade alcançada é tão alta que o usuário enxerga o tempo em câmera lenta.', E'加速', E'Kasoku', E'Aceleração', E'Fluxo', NULL, NULL, NULL, NULL, NULL),
(17, E'Acorrentamento Celestial', E'Obito passa através de seu oponente, materializa-se e prende-o com suas corretes, jogando o inimigo no ar. Em seguida, ele se teletransporta para cima de seu adversário, acumula chakra em seu punho e desfere um poderoso soco nele, lançando-o para o chão.', E'鎖天', E'Saten', E'Acorrentamento Celestial', E'Corrente Celestial', NULL, NULL, E'Curto alcance', NULL, NULL),
(18, E'Acorrentamento da Prisão Explosiva', E'Hanzō prende seu oponente no lugar com a ponta final pesada de sua kusarigama, e cria uma esfera de papéis explosivos ao redor dele, que explodem.', E'鎖牢爆さろうばく', E'Sarōbaku', E'Acorrentamento da Prisão Explosiva', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(19, E'Acorrentamento por Fūinjutsu', E'Após identificar um terreno com pilares, o usuário é capaz conjurar inscrições que acorrentam um alvo a eles. Para fortificar o aprisionamento, é possível entrelaçar as \'correntes\' entre os pilares. No entanto, a técnica pode ser liberada com uma poderosa emissão de chakra e impacto ou com kunai em pontos estratégicos.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(20, E'Acrobata', E'O Acrobata é um taijutsu usado por Killer B em conjunto com suas Espadas de Liberação de Relâmpago Super-Vibrantes e a técnica padrão do seu Taijutsu de Perturbação (攪乱体術, Kakuran Taijutsu, Literalmente significa: Técnica de Perturbação Corporal) único.', E'荒線鷺伐刀', E'Akurobatto', E'Garça-Real da Linha Áspera da Espada Impressionante', NULL, NULL, E'Acrobata', E'Curto alcance', NULL, NULL),
(21, E'Aglomeração Explosiva do Deserto de Areia', E'Depois de aglomerar um punhado de areia e arenito no ar, o usuário arremessa em alta velocidade contra o oponente, esmagando-o. Gaara ainda pode combinar a técnica tanto com a Caixão de Areia para capturar o alvo antes desse ataque, quanto com o Grande Funeral do Deserto para esmagá-lo brutalmente.', E'砂爆砂塊', E'Sabaku Sakai', E'Aglomeração Explosiva do Deserto de Areia', E'Cacho de Areia', NULL, NULL, E'Curto a Médio alcance', NULL, E'Passaro'),
(22, E'Aglomeração de Árvores', E'Hashirama faz emergir algumas raízes do solo, que se entrelaçam e lançam o oponente no ar, fazendo com que que o inimigo abaixe sua guarda ou mesmo desestabilize sua defesa. Dessa forma, a tática permite que Hashirama golpeie-o ainda no ar.', E'縫連樹', E'Hōrenju', E'Aglomeração de Árvores', E'Forrado da Floresta', NULL, NULL, E'Curto alcance', NULL, NULL),
(23, E'Agulha Fria', E'Shizune ataca seu adversário algumas vezes, então ela vira e bate-os com um Bisturi de Chakra. Ela termina com eles acertando-os com várias senbon.', E'凛針りんしん', E'Rinshin', E'Agulha Fria', E'Agulhas Penetrantes', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(24, E'Agulha Infernal', E'Depois de usar as Agulhas Jizō, estendendo seu cabelo ao redor de seu corpo, o usuário dispara o cabelo em forma de agulha endurecida em múltiplas direções ao inimigo.', E'針地獄', E'Hari Jigoku', E'Agulha Infernal', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(25, E'Agulha Senbon da Besta-Humana', E'Em seu estado transformado, o shinobi do clã Tenrō pode disparar seus cabelos como senbon em todas as direções, dando poucas oportunidades para seu oponente desviar.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(26, E'Agulha de Cabelo do Coelho', E'A Agulha de Cabelo do Coelho é um taijutsu de Kekkei Mōra usado por Kaguya Ōtsutsuki.', E'兎毛針とげばり', E'Togebari', E'Agulha de Cabelo do Coelho', NULL, NULL, NULL, E'Médio a Longo alcance', E'Rank –', NULL),
(27, E'Agulha de Chakra', E'Agulha de Chakra é a técnica assinatura de Hinoko, desenvolvida durante seu tempo na Academia. Graças a essa técnica, ela foi recrutada para o Anbu desde formada.', E'チャクラの針ちゃくらのはり', E'Chakura no Hari', E'Agulha de Chakra', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Cavalo → Carneiro'),
(28, E'Agulha de Água', E'Usando o controle de chakra concedido pelo Punho Gentil, Hinata condensa água nas proximidades e molda-a em agulhas para lançar em seus alvos. Usando o Byakugan, esta técnica pode ser utilizada com grande precisão para atingir alvos muito pequenos, tais como as abelhas.', E'水針', E'Mizu Hari', E'Agulha de Água', E'Jutsu Senbon de Água', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(29, E'Agulhas Carregadas', E'O usuário prepara algumas senbon dentro de sua boca ou mesmo uma que está em sua boca e as dispara em alta velocidade em seu inimigo. Shizune demonstrou ter a capacidade de mergulhar a agulha em veneno e disparar com uma precisão alternativa, o que pode ampliar ou diminuir as suas chances de êxito no inimigo consideravelmente.', NULL, NULL, NULL, NULL, NULL, E'Agulhas Ocultas', E'Curto alcance', NULL, NULL),
(30, E'Agulhas Jizō', E'Com esta técnica o usuário se recobre em um número incontável de agulhas afiadas que são, na realidade, seus cabelos espetados. O chakra concede aos fios de cabelo uma dureza e a forma de agulhas ou espinhos, tornando-o dotado de uma capacidade de ferir e matar igualmente a armas metálicas. Além disso, se ativado em resposta a um ataque do oponente, ele pode também ser usado como um contra-ataque, bem como pode ser usado para aprimorar outras técnicas de contato. No anime, o composto do cabelo é duro o suficiente para que ele possa suportar golpes de certos tipos de armas, como flechas.', E'針地蔵', E'Hari Jizō', E'Agulhas de Jizō', NULL, E'Estátua de Espinhos', E'Arte Ninja: Agulha Jizo', E'Curto alcance', E'Rank B', E'Tigre → Cavalo → Javali → Carneiro → Rato → Cobra'),
(31, E'Agulhas da Sombra', E'Uma técnica simples usando Senbon, o utilizador lança uma Senbon com sinos anexados e outra Senbon sem sinos ao mesmo tempo. A intenção é que o adversário só vá reagir ao som dos sinos e não notar as silenciosas, sendo assim, atingido pela ultima Senbon. Esta técnica se concentra em como o cérebro funciona, instintivamente: reagir ao som, o cérebro \'esquece\' sobre as informações fornecidas pelos outros sentidos. No entanto, é muito fácil de escapar do ataque se o adversário reconhece o truque simplesmente se baseia nas Senbon lançadas.', E'影千本', E'Kage Senbon', E'Agulhas da Sombra', E'Senbon de Sino', NULL, NULL, E'Curto e Médio Alcance', NULL, NULL),
(32, E'Agulhas de Areia Eletrizadas', E'Essa é uma técnica muito semelhante à Garoa de Areia. Gaara lança uma grande quantidade de areia no céu chuvoso sobre o adversário. A areia então se forma em agulhas que se colam no chão e criam uma carga elétrica que drena e neutraliza a eletricidade. Gaara usa isso para desativar a Bola de Plasma de Ranke.', E'砂雷針すならいしん', E'Suna Raishin', E'Agulhas de Areia Eletrizadas', E'Areia de Shukaku (守鶴の砂, Shukaku no Suna)', NULL, NULL, E'Todos os alcances', NULL, NULL),
(33, E'Akuta', E'Esta é uma técnica de Liberação de Terra criada a partir do kinjutsu do País da Terra resgatado por Ōnoki em sua incansável pesquisa. Esta técnica foi designada pelo Terceiro Tsuchikage para proteger a vila de invasores e diminuir o número de vítimas.', E'芥', E'Akuta', E'Descartáveis', NULL, NULL, NULL, NULL, NULL, NULL),
(34, E'Alucinação', E'Kamira é capaz projetar uma Alucinação para prender seu oponente, que faz com que a área ao seu redor seja envolvida pela escuridão. Uma vez dentro do espaço negro, o alvo vai ver figuras grotescas que, em seguida, o pegam e o levam para o vazio onde ele estará à mercê de um dragão espectral. O oni horripilante pode até mesmo estender-se de seu próprio corpo do alvo para atacá-los. Todas essas criaturas podem petrificar a vítima em choque enquanto Kamira aparece a partir acima para sufocar e matar o adversário.', E'幻覚', E'Genkaku', NULL, NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(35, E'Amaterasu', E'O Amaterasu é uma técnica ocular do Mangekyō Sharingan. Dito como a técnica de fogo de mais alto nível e a representaçào do \'Mundo Material e Luz\' (物質界と光, Busshitsukai para Hikari), Amaterasu é a antípoda do Tsukuyomi.', E'天照あまてらす', E'Amaterasu', E'Iluminação Celestial', NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre[2]'),
(36, E'Amaterasu: Camada Dupla', E'Sasuke e Itachi usam simultaneamente o Amaterasu sobre o inimigo, jogando-o para trás, e então cada um invoca seu Susanoo, antes de disparar uma flecha e um grupo de Yasaka Magatama, respectivamente. O impacto resulta em uma explosão das chamas negras, após aplicarem uma segunda camada de Amaterasu, resultando em uma gigante explosão de chamas negras.', E'天照・二重あまてらす・にじゅう', E'Amaterasu: Nijū', E'Iluminação Celestial: Camada Dupla', E'Amaterasu: Camada Dupla', NULL, NULL, E'Todos os alcances', NULL, NULL),
(37, E'Amaterasu: Fogo Persistente', E'Sasuke cria um orbe de Amaterasu na terceira mão do seu Susanoo, de então são expelidos outros quatro orbes de chamas negras contra os oponentes, explodindo ao entrar em contato com eles. Os orbes são usados como armadilhas a curta distância.', E'天照・残火あまてらす・ざんか', E'Amaterasu: Zanka', E'Iluminação Celestial: Fogo Persistente', E'Amaterasu: Cinzas', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(38, E'Amaterasu: Fogo do Envoltório de Chamas', E'Após manifestar as costelas do Susanoo, Sasuke é capaz de aumentar seu poder defensivo ao cobri-las com as chamas do Amaterasu, envolvendo-se com um escudo de chamas negras. Ele também pode conduzir as chamas para sua frente e moldá-las em espinhos, para empalar quem quer que tente atacá-lo. Além dos danos que as chamas podem causar, isso tem o efeito psicológico de fazer com que os inimigos hesitem em atacá-lo.', E'天照・炎纏火あまてらす・えんてんか', E'Amaterasu: Entenka', E'Iluminação Celestial: Envoltório de Chamas', E'Amaterasu: Acumulação de Chama', NULL, NULL, E'Curto alcance', NULL, NULL),
(39, E'Ame-no-himuro', E'Kaguya manipula a umidade presente no ar e a condensa em uma câmara de gelo para aprisionar seu oponente, que explode com a pressão.', E'天之氷室アメノヒムロ', E'Amenohimuro', E'Câmara de Gelo dos Céus', E'Câmara Divina de Gelo', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(40, E'Ame-no-mihashira', E'Amenomihashira (天御柱) é uma técnica original criada por Asura Ōtsutsuki, que inclui a transformação da natureza de Liberação de Vento.', E'天御柱アメノミハシラ', E'Amenomihashira', E'Honorável Pilar Celestial', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Tigre → Cao'),
(41, E'Ame-no-minaka', E'Usando seu terceiro olho, Kaguya é capaz de instantaneamente teleportar alguém, em um determinado intervalo, para dimensões diferentes. Aqueles afetados por ela são pegos completamente desprevenidos, como parece não ter nenhum aviso antes da sua utilização e encontraram-se mergulhando em um rio de lava, e de repente são envoltos em um bloco de gelo. Tendo sido enviado para uma dimensão diferente, a presença de seu chakra é completamente apagada do mundo normal e não pode ser detectada através de meios tradicionais.', E'天之御中アメノミナカ', E'Amenominaka', E'Administração dos Céus', E'Ame no Minaka', NULL, NULL, E'Todos os alcances', E'Rank –', NULL),
(42, E'Ame-no-subaruboshi no Mikoto', E'Depois de balançar sua vara de chakra, o usuário cria uma barragem de anzóis para perfurar e imobilizar o alvo, ou derrubá-lo com uma forte explosão.', E'天昴宿命アメノスバルボシノミコト', E'Amenosubaruboshi no Mikoto', E'Majestosa Chefe Peluda dos Céus', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(43, E'Ame-no-subaruboshi no Mikoto: Caixa de Jade', E'Urashiki ergue sua mão em direção a seu alvo e, a partir de sua palma, cria uma esfera de chakra em alta rotação que ele pode usar para golpear seus oponentes à queima roupa ou atirar, explodindo no impacto. Ele também mostrou-se capaz de disparar múltiplas esferas em rápida sucessão, golpeando seus inimigos repetidas vezes até que suas forças se esgotem e não consigam mais ficar de pé.', E'天須波流星命・玉匣', E'Ame-no-subaruboshi no Mikoto: Gyokkō', E'Majestosa Chefe Peluda dos Céus: Caixa de Jade', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(44, E'Ame-no-subaruboshi no Mikoto: Rei Dragão', E'Com seu braço estendido em direção ao seu alvo, Urashiki molda seu chakra em uma massiva esfera de energia acompanhada pela aparição de um dragão marinho que a empunha entre suas presas. O dragão carrega a esfera até seu destino e tem poder destrutivo capaz de competir com o Super Rasengan Uzumaki.', E'天須波流星命・竜宮­', E'Ame-no-subaruboshi no Mikoto: Ryūgū', E'Majestosa Chefe Peluda dos Céus: Rei Dragão', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(45, E'Ame-no-tejikara', NULL, E'天手力', E'Amenotejikara', E'Poder da Mão Celestial', NULL, NULL, NULL, E'Curto a Médio alcance', E'Rank –', NULL),
(46, E'Ame-no-tejikara: Chidori', E'Sasuke lança dois conjuntos de kunai infundidas com chakra, o primeiro é usado como distração para imobilizar o alvo, o segundo para usar seu Ame-no-tejikara, trocando de lugar com elas, teletransportando-se e aproximando de seu oponente, em seguida, desferindo um Chidori infundido em chamas negras.', E'天手力・千鳥アメノテジカラ・ちどり', E'Ame-no-tejikara: Chidori', E'Poder da Mão Celestial: Mil Pássaros', E'Poder da Mão Celestial: Chidori', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(47, E'Ame no Ohabari', E'Esta trata-se da variação de golpes utilizados com a Espada de Totsuka, sem o intuito de selar.', E'天之尾羽張', E'Ame no Ohabari', E'Caudas e Penas Espalhadas Através do Céu', E'Corte de Vento Celestial', NULL, NULL, E'Curto alcance', NULL, NULL),
(48, E'Amieiro', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(49, E'Amieiro (Ultimate Ninja 3)', E'Kimimaro corre até seu oponente, e, usando-o como suporte, salta sobre ele. Em seguida, crescem pontas de ossos de suas costas, acertando o oponente.', E'夜叉五倍子', E'Yashabushi', E'Amieiro', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(50, E'Amieiro (Ultimate Ninja Storm)', E'Kimimaro golpeia o oponente com sua espada óssea, cria outra em seu cotovelo e ataca o oponente, cria picos de ossos em seus pulsos e ataca o oponente novamente, corta o oponente com sua espada óssea em sentido diagonal e finaliza com forte ataque diagonal contra o oponente, atravessando-o.', E'夜叉五倍子やしゃぶし', E'Yashabushi', E'Amieiro', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(51, E'Anātman', E'Gaara executa os selos de mão necessários e a areia circundante sob seus pés puxe-o para baixo. Após o término de sua execução, o usuário faz com que a areia leve-o de volta para a superfície. Esta técnica foi útil para escapar da Rotação do Vento Feroz de Seimei.', E'無我むが', E'Muga', E'Altruísmo', E'Anatta', NULL, NULL, E'Curto alcance', NULL, NULL),
(52, E'Andorinha Voadora', E'Um ninjutsu de adição que aumenta o poder de matar de uma lâmina — como uma kunai ou espada — fluindo chakra nela. O chakra materializado é moldado como uma lâmina, cortando tudo o que toca. Também é possível alterar a distância do ataque da arma, ajustando a quantidade de chakra colocado na lâmina. Esta técnica é muito difícil de ser desviada.', E'飛燕', E'Hien', E'Andorinha Voadora', NULL, E'Voo da Andorinha', NULL, E'Curto alcance', E'Rank B', NULL),
(53, E'Anel de Fogo', E'O usuário expande um anel de chamas fundidas, que podem ser seladas dentro de um projétil de aço para derretê-lo e transformá-lo em uma forma semelhante a uma espiral. As chamas seladas, em seguida, fazem com que uma onda de choque de alta pressão derreta o projétil, que explode em uma tempestade de chamas.', NULL, NULL, E'Anel de Fogo', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(54, E'Anestesia Secreta', E'Esta técnica requer o chakra das cobras de Yomi para ser realizada. A cobra morde o inimigo, paralisando-o temporariamente. A área em torno da marca da mordida em seguida, torna-se arroxeada.', E'麻睡窃術', E'Masui Sejutsu', E'Anestesia Secreta', E'Jutsu da Anestesia', NULL, NULL, E'Curto alcance', NULL, NULL),
(55, E'Aniquilação', E'Obito desfere uma série de socos e chutes em seu oponente. Enquanto em combate físico, ele se materializa e desmaterializa por todo o corpo do oponente várias vezes a fim de realizar danos físicos potentes, causando uma injúria ainda mais letal.', E'滅却めっきゃく', E'Mekkyaku', E'Aniquilação', E'Destruição Total', NULL, NULL, E'Curto alcance', NULL, NULL),
(56, E'Aparência Ilusória', E'Para aplicar este genjutsu, o usuário coloca um gatilho para ativar com o alvo, que tem sua mente manipulada, causando a mudança da aparência de objetos ou aliados em sua visão. Quando a aparência transformada é hostil, o alvo provavelmente entrará em conflito com a pessoa que serviu de gatilho.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(57, E'Aperto Assassino', E'Esta técnica utiliza fios de aço reforçados com chakra a fim de torná-lo capaz de cortar o alvo, bem como é capaz de cortar um portão de aço ou o casco de um navio com pouca dificuldade. O usuário também pode usar o fio para moldar formas como bem entender, e lançá-la como um ataque de corte poderoso.', E'殺め取り', E'Ayametori', E'Aperto Assassino', NULL, NULL, NULL, E'Todos os alcances', NULL, E'Tigre → Javali → Macaco → Tigre'),
(58, E'Apodrecimento do Lobo Sábio', E'O Apodrecimento do Lobo Sábio é um ninjutsu único de Saiken.', E'知狼朽', E'Chirōkyū', E'Apodrecimento do Lobo Sábio', E'Dispersão Ácida (酸散, Sansan)', NULL, NULL, E'Todos os alcances', NULL, NULL),
(59, E'Arco da Aranha: Ventania', E'Enquanto em seu selo amaldiçoado nível dois e após invocar Kyodaigumo, Kidōmaru sobe em cima dela, e utiliza seu arco para disparar uma poderosa flecha em direção ao oponente, flecha está que, é muito maior do que as outras, além de que quando entra em contato com o oponente, gera uma enorme explosão de energia.', NULL, NULL, NULL, E'Arco da Aranha: Ventania', NULL, NULL, E'Longo alcance', NULL, NULL),
(60, E'Arco de Guerra da Aranha: Terrível Divisão', E'Arco de Guerra da Aranha: Terrível Divisão é uma técnica de tiro destrutivo com arco que é o perfeito matar em um acerto, utilizado por Kidōmaru.', E'蜘蛛戦弓・凄裂', E'Kumo Senkyū: Suzaku', E'Arco de Guerra da Aranha: Terrível Divisão', NULL, E'Arco da Aranha: Grande Dilaceração', NULL, E'Longo alcance', NULL, NULL),
(61, E'Areia Expansiva', NULL, NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, E'Abrir a mao[1]'),
(62, E'Areia de Ferro: Asas de Ferro Negro', E'Esta técnica permite ao usuário a habilidade de voar, usando asas criadas a partir de Areia de Ferro. Enquanto no alto, Shinki pode disparar penas para atacar o inimigo.', E'砂鉄・黒鉄乃翼さてつ・くろがねのつばさ', E'Satetsu: Kurogane no Tsubasa', E'Areia de Ferro: Asas de Ferro Negro', NULL, NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(63, E'Argila Explosiva', E'A Argila Explosiva (起爆粘土, Kibaku Nendo) foi desenvolvida a partir de uma técnica proibida que Deidara roubou de Iwagakure. Usando a capacidade da técnica de amassar chakra em objetos através das bocas nas palmas de suas mãos, Deidara pode infundir argila com chakra. Ele, então, molda-o em uma forma, dependendo da capacidade que ele deseja ter e cospe para fora. Depois de moldar a argila, Deidara pode ampliá-lo em vários tamanhos, dependendo da finalidade da argila. Uma vez alargada, as criações de argila pode ser trazidas à vida e controladas remotamente pelo usuário. Ele ainda pode fazer a argila explodir utilizando o selo de confronto e usualmente diz Katsu (喝) como assinatura. Embora os explosivos sejam poderosos, elas são todas as técnicas baseadas na Liberação de Terra e, como tal, podem ser neutralizadas por uma técnica de Liberação de Relâmpago. Um único aglomerado dessa argila pode explodir em uma vasta área.', E'起爆粘土きばくねんど', E'Kibaku Nendo', E'Argila Explosiva', E'Argila Detonadora', NULL, E'Argila Explosiva', E'Todos os alcances', E'Rank –', NULL),
(64, E'Argolas Elétricas', E'É uma técnica exclusiva da Espada do Deus do Trovão que permite o usuário criar anéis de relâmpago em torno do inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(65, E'Arma Interna', E'O Caminho Asura executa uma combinação de ataques, começando pela Flecha Flamejante da Incrível Habilidade e terminando com o Ataque Asura, aniquilando totalmente o oponente.', E'体内兵器', E'Tainai Heiki', E'Arma Interna', E'Armamento Interno', NULL, NULL, E'Curto alcance', NULL, NULL),
(66, E'Arma de Xarope de Amido', E'O usuário molda seu chakra, juntamente com a energia natural e, em seguida, converte-o em grande quantidade de água de alta viscosidade dentro de sua boca, antes de cuspi-la para fora com uma quantidade incrível de força. A força semelhante ao projétil da técnica combinada com o seu volume monstruoso lhe dá a capacidade de rapidamente cobrir uma vasta área.', E'水飴鉄砲みずあめてっぽう', E'Mizuame Teppō', E'Arma de Xarope de Amido', NULL, E'Tiro Gosmento', NULL, E'Todos os alcances', NULL, NULL),
(67, E'Armadilha', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(68, E'Armadilha Esférica de Genjutsu', E'Uma série de armadilhas na dimensão que interligou a Terra com a Lua. Foi criada por uma substância perigosa na forma de bolha a qual é capaz de capturar as suas vítimas num genjutsu muito poderoso produzido pelo Guardião do Portão, um caranguejo gigante que residia e protegia a entrada para a Lua.', NULL, NULL, NULL, NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(69, E'Armadilha Fūinjutsu', E'Uma vez que a técnica é ativada, o alvo é selado em um espaço dimensional diferente, no interior da localização do selamento. Antes de notar que existiam algumas maneiras de escapar do selo, Kakashi tentou quebrar o selo com seu Corte Relâmpago, mas sem sucesso. O único meio de fuga parecia ser criar o selo de sangue adequado.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(70, E'Armadilha Rasteira com Fio', E'Trata-se de uma armadilha em que o usuário anexa uma série de fios de aço a papéis explosivos no ambiente. Quando a vítima esbarra acidentalmente no fio, os papéis explosivos são acionados e causam uma série de explosões. O usuário ainda pode combinar essa armadilha com os Papéis da Resma Explosiva para montar a armadilha de forma mais eficaz ou mesmo combiná-la com outra armadilha para um efeito mais certeiro. Shikamaru foi capaz de combinar esta armadilha com sua Técnica do Puxão pela Sombra para prender Hidan completamente com a armadilha por todo o seu corpo.', NULL, NULL, NULL, NULL, NULL, E'Armadilha Rasteira com Fio', E'Todos os alcances', NULL, E'Explodir: Carneiro[2]'),
(71, E'Armadilha com Clone', E'Esta é uma armadilha alternativa desenvolvida por Hidari.', E'分身トラップ­ぶんしんトラップ', E'Bunshin Torappu', E'Armadilha com Clone', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(72, E'Armadilha da Destruição do Esconderijo', E'Uma armadilha temporizada conjunta usada por Orochimaru para destruir qualquer vestígio de um velho esconderijo seu com o parecer de um vórtice gigante de lama. Ela pode ser ativada ou não, mesmo se existirem pessoas no esconderijo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(73, E'Armadilha da Liberação de Vento', E'Rin planta no chão um selo previamente preparado que contém uma técnica de Liberação de Vento e então pula para trás. O selo então ativa, liberando um turbilhão crescente de vento, ficando cada vez mais forte até o ápice, enfraquecendo e se dissipando. O turbilhão é forte o suficiente para jogar o oponente alto e causar certo dano.', E'風遁トラップふうとんトラップ', E'Fūton Torappu', E'Armadilha da Liberação de Vento', E'Armadilha de Estilo Vento', NULL, NULL, E'Curto alcance', NULL, NULL),
(74, E'Armadilha de Genjutsu', E'A Armadilha de Genjutsu trata-se de uma técnica ofensiva utilizada para impedir que a mente de determinado indivíduo seja invadida e tenha suas informações roubadas. Assim que a mente da pessoa é adentrada, o invasor cai em um genjutsu em que seu campo de visão é completamente tingido por um oceano que se propaga em vermelho sob seus pés e então, ele começa a ondular como ondas gigantes em resposta ao intruso.', E'幻術トラップげんじゅつ トラップ', E'Genjutsu Torappu', E'Armadilha de Técnica Ilusória', NULL, NULL, NULL, NULL, NULL, NULL),
(75, E'Armadura Devoradora', E'Log derrete sua armadura para originar um líquido preto do qual ele pode manipular livremente. Devido a isso, o líquido pode ser usado tanto para imobilizar e esmagar um inimigo ou, ainda, em sua forma mais dura, para defender o usuário.', E'鎧食いよろいぐい', E'Yoroigui', E'Armadura Devoradora', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(76, E'Armadura de Areia', E'Usando esta técnica, Gaara pode revestir seu corpo com uma camada de areia, proporcionando uma defesa adicional caso seu Escudo de Areia falhar. Essa técnica é considerada uma defesa absoluta, quando usada em conjunto com o escudo automático. Embora bastante eficaz, manter a armadura requer uma grande quantidade de chakra e resistência. Além disso, nenhum lugar é tão resistente quanto o Escudo de Areia, uma vez que se desprende no momento do impacto, indicando que o único propósito da armadura é absorver o impacto canalizado até o corpo de Gaara durante a batalha. Outra fraqueza é o peso adicional da areia o que leva a uma diminuição dos níveis de velocidade e mobilidade.', E'砂の鎧すなのよろい', E'Suna no Yoroi', E'Armadura de Areia', NULL, NULL, E'Armadura Arenosa', E'Curto alcance', E'Rank –', NULL),
(77, E'Armadura de Chakra de Bando', E'Bando libera uma quantidade enorme de chakra por todo seu corpo, criando uma armadura de chakra impenetrável que resiste por bom tempo a ataques. Todos os danos graves que ele receber nesse tempo, serão drasticamente diminuídos em uma escala assustadora, assim como todos os seus ataques a partir desse momento passarem a receber mais força destrutiva.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(78, E'Armadura de Cristal', E'Esta técnica de Guren é capaz de envolver seu corpo com uma fina camada de cristal que aprimora sua resistência contra ataques físicos. Além disso, esta armadura concede ataques físicos mais potentes a Guren. O cristal é invisível ao olho nu, sendo visto somente quando a luz é refletida.', E'結晶の鎧けっしょうのよろい', E'Kesshō no Yoroi', E'Armadura de Cristal', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(79, E'Armadura de Ouro Pegajoso', E'Excretando o seu Ouro Pegajoso da Aranha de seus poros, Kidōmaru pode criar rapidamente uma armadura temporária que é capaz de bloquear chakra que endurece quando exposta ao oxigênio, podendo até mesmo bloquear o Punho Gentil do Clã Hyūga.', E'粘金の鎧', E'Nenkin no Yoroi', E'Armadura de Ouro Pegajoso', NULL, E'Armadura de Metal Viscoso', NULL, E'Curto alcance', NULL, NULL),
(80, E'Armadura de Rocha', E'Esta é uma técnica de armadura usada por Fudō a fim de aprimorar suas capacidades físicas.', E'岩の鎧いわのよろい', E'Iwa no Yoroi', E'Armadura de Rocha', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(81, E'Armadura do Ataque Relâmpago', E'A Armadura do Ataque Relâmpago permite que seu usuário acumule chakra e o libere na forma de uma potente descarga elétrica que envolve todo o seu corpo. Isso garante um aumento de velocidade ao usuário e o protege, eletrocutando quem faz contato. Esta armadura parece ser muito forte, pois é capaz de fazer frente ao Punho Embriagado de Rock Lee.', E'雷撃の鎧らいげきのよろい', E'Raigeki no Yoroi', E'Armadura do Ataque Relâmpago', NULL, NULL, E'Arte Ninja: Armadura Relâmpago', NULL, NULL, E'Carneiro'),
(82, E'Arranque a Vapor', E'Depois de aumentar a temperatura de seu chakra ao ponto de ebulição, o corpo de Han libera vapor incessantemente para arrancar contra o oponente.', E'蒸気驀進じょうきばくしん', E'Jōki Bakushin', E'Arranque a Vapor', NULL, NULL, E'Corrida a Vapor', E'Curto alcance', NULL, NULL),
(83, E'Arremesso de Fio', E'O usuário faz com que a marionete, com o cordão de ponta afiada instalado, desenrole ele de sua barriga para diversos fins. O cordão serve para perfurar o oponente, amarrá-lo, cortar Fios de Chakra ou como um impulsionador de voo ao se puxar através dele. A fim de aumentar o potencial da técnica, o usuário pode adicionar um Veneno Mortal para caso ele erre, mas raspe no corpo do inimigo. Além disso, o cordão serve como condutor de eletricidade para manter um prisioneiro.', E'ワイヤー射出', E'Waiyā Shashutsu', E'Arremesso de Fio', E'Ejetor de Fio', NULL, NULL, E'Longo alcance', NULL, NULL),
(84, E'Arte Final', E'Enquanto anda com o Dragão C2, Deidara cria um Clone de Argila e derruba sobre o seu oponente lá do alto, explodindo em seguida e resultando em uma enorme explosão.', E'究極芸術', E'Kyūkyoku Geijutsu', E'Arte Final', NULL, NULL, NULL, E'Longo alcance', NULL, NULL),
(85, E'Arte Ninja Canina: Clones Caninos', E'Usando o cabelo que ele lançou, Pakkun cria dezenas de clones de si. Estes clones são capazes de flutuar no ar, de uma forma semelhante que o cabelo é capaz de permanecer no ar.', E'忍犬忍法・猟犬分身', E'Ninken Ninpō: Ryōken Bunshin', E'Arte Ninja Canina: Clones Caninos', NULL, NULL, E'Arte Ninja Canina: Clones do Cabelo Canino', NULL, NULL, NULL),
(86, E'Arte Ninja Canina: Dança da Nuvem de Poeira', E'Com a Arte Ninja Canina: Dança da Nuvem de Poeira, o usuário, usando suas patas, cria uma nuvem de poeira que pode ser usada para esconder seus movimentos. Também pode ser usada para criar buracos em um curto período de tempo.', E'忍犬忍法・砂塵の舞', E'Ninken Ninpō: Sajin no Mai', E'Arte Ninja Canina: Dança da Nuvem de Poeira', NULL, NULL, NULL, NULL, NULL, NULL),
(87, E'Arte Ninja Magnética: Cintilação Eletromagnética', E'Usando seus poderes magnéticos, Jiga é capaz de se mover em alta velocidade, deixando uma imagem residual para confundir seus oponentes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(88, E'Arte Ninja Magnética: Infinitos Meteoros', E'Jiga usa essa técnica, após magnetizar as paredes de rocha de um poço profundo, para dar-lhe a capacidade de atacar um alvo com muitas pedras. Uma vez magnetizado, Jiga pode manipular completamente a forma das rochas a seu critério. Ele também fez uso desta técnica, a fim de enterrar seu irmão Ruiga ainda vivo.', E'磁石忍法・無限隕石', E'Jishaku Ninpō: Mugen Inseki', E'Arte Magnética: Infinitos Meteoros', NULL, NULL, E'Arte Magnética Ninja: Infinitos Meteoros', NULL, NULL, NULL),
(89, E'Arte Ninja Magnética: Visão Eletromagnética', E'Esta técnica desencadeia uma onda de areia de ferro, que para as ondas cerebrais de alguém que é pego no ataque. Ao interromper as ondas elétricas do cérebro, o adversário fica incapaz de pensar ou se mover.', E'磁石忍法・電磁幻夢', E'Jishaku Ninpō: Denji Genmu', E'Arte Magnética: Visão Eletromagnética', NULL, NULL, E'Arte Ninja dos Imãs: Ilusão Eletromagnética', E'Curto a Médio alcance', NULL, E'Tigre → Selo especifico da técnica'),
(90, E'Arte Ninja: Beleza do Luar', E'Ino inicia a técnica saltando em frente a lua, em seguida, ela libera e controla várias pétalas, assim ela cega, cerca e confunde o seu adversário com elas. Finalmente, Ino inicia seu declínio e instantaneamente ela cai com os dois pés em cima do rosto do inimigo com uma imensa fúria, dizimando o inimigo e constituindo uma imensa cratera.', E'忍法・月下美人にんぽう・げっかびじん', E'Ninpō: Gekka Bijin', E'Arte Ninja: Beleza do Luar', E'Arte Ninja: Rainha da Noite', NULL, NULL, E'Curto alcance', NULL, E'Cobra → Tigre'),
(91, E'Arte Ninja: Capa de Gato', E'Wasabi coloca seu capuz e desenrola um pergaminho, o qual ela ativa. A garota então é envolvida em chakra, que lhe dá características semelhantes às dos gatos, tal como cauda, orelhas e olhos fendidos, bem como unhas estendidas. A técnica pode selecionar uma de muitas espécies diferentes de gatos, garantindo para ela os atributos do gato selecionado. Esta técnica possui um custo de chakra muito alto, visto que Wasabi não pode mantê-la por muito tempo.', E'忍法・猫被りにんぽう・ねこかぶり', E'Ninpō: Nekokaburi', E'Arte Ninja: Capa de Gato', NULL, NULL, NULL, NULL, NULL, NULL),
(92, E'Arte Ninja: Casulo', E'Com a ajuda dos Fios de Chakra de Chōmei, o usuário cria um casulo que permite suprimir a absorção de chakra de fontes externas, e promover uma defesa adicional ao mesmo tempo. O casulo é extremamente duro, e só pode ser perfurado por uma lâmina de chakra em um ponto fraco específico.', E'忍法・コクーン', E'Ninpō: Kokūn', E'Arte Ninja: Casulo', NULL, NULL, NULL, E'Curto alcance', NULL, E'Selo do Confronto'),
(93, E'Arte Ninja: Dança da Neve da Flor de Cerejeira', E'Sakura conecta-se com seu oponente através de um genjutsu e começa à lançar quatro pequenas bolsas brancas com papéis explosivos, que estão disfarçados de minúsculos pedaços de papeis brancos aos olhos humanos. A bolsa abre-se sozinha no ar, e Sakura joga diversas kunai em direção as bolsas, onde os papéis explosivos começam à explodir e cair no oponente. Por último, Sakura conecta uma kunai unida por um papel explosivo nos pés de seu oponente, detonando-o e, em seguida, os outros selos que não haviam caído por total das bolsas começam a cair semelhante à uma chuva no oponente. Após a conclusão da técnica, é capaz ver o oponente gravemente ferido ou morto.', E'忍法・雪桜の舞にんぽう・ゆきさくらのまい', E'Ninpō: Yuki Sakura no Mai', E'Arte Ninja: Dança da Neve da Flor de Cerejeira', E'Arte Ninja: Dança de Neve da Sakura', NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(94, E'Arte Ninja: Energia Eólica da Foice da Doninha', E'Temari usa seu leque e atinge o solo, criando um turbilhão e coletando algumas rochas. O turbilhão lança seu oponente para muito longe além do ar, onde são atingidos tanto pelo corte de vento como as rochas.', E'忍法・カマイタチ最大風力', E'Ninpō: Kamaitachi Saidai Fūryoku', E'Arte Ninja: Energia Eólica da Foice da Doninha', E'Velocidade Máxima da Foice de Vento', NULL, NULL, E'Longo alcance', NULL, NULL),
(95, E'Arte Ninja: Estrutura de Muco Nasal', E'Este é um estilo de luta utilizado por Udon Ise com propriedades de Liberação de Água, permitindo-lhe controlar seu catarro da forma que preferir.', E'忍法・ハナミズキにんぽう・はなみずき', E'Ninpō: Hanamizuki', E'Arte Ninja: Estrutura de Muco Nasal', NULL, NULL, E'Arte Ninja: Nariz Escorrendo', E'Curto e Médio alcance', NULL, NULL),
(96, E'Arte Ninja: Formação do Majestoso Ciclo Sexagenário de Genbu: Estilo de Número 108: Liberar', NULL, E'忍法・玄武六甲覇王陣・第百八式 改', E'Ninpō: Genbu Rokkō Haōjin: Dai Hyakuhashiki — Kai', E'Arte Ninja: Formação do Majestoso Ciclo Sexagenário de Genbu: Estilo de Número 108: Liberar', NULL, NULL, NULL, E'Curto alcance', NULL, E'Tigre[1]'),
(97, E'Arte Ninja: Furacão', E'Temari salta e balança seu Leque Dobradiço Gigante para criar duas poderosas rajadas de vento que vão em direção ao oponente, causando danos graves no oponente.', E'忍法・旋風', E'Ninpō: Senpū', E'Arte Ninja: Redemoinho', E'Arte Ninja: Furacão', NULL, NULL, E'Longo alcance', NULL, NULL),
(98, E'Arte Ninja: Palma do Dragão Feroz', E'Rokkaku Ryūdōin invoca um dragão feito de chakra para atacar o inimigo, causando grandes danos ao oponente. Este ataque também pode reduzir a defesa dos alvos.', E'忍法・激龍掌', E'Ninpō: Gekiryūshō', E'Arte Ninja: Palma do Dragão Feroz', NULL, NULL, NULL, E'Médio alcance', NULL, NULL),
(99, E'Arte Ninja: Profusão de Dez Mil Flores', E'Ino saca dois maços uma espécie de flor venenosa, pula no ar e, em seguida, gira elegantemente, lançando uma enxurrada de flores roxas venenosas no oponente. As flores explodem no momento do impacto, lançando uma chuva de névoa venenosa e pétalas roxas.', E'忍法・万花繚乱', E'Ninpō: Manka Ryōran', E'Arte Ninja: Profusão de Dez Mil Flores', E'Arte Ninja: Inundado com Flores', NULL, NULL, E'Médio a Longo alcance', NULL, NULL),
(100, E'Arte Ninja: Profusão de Flores', E'Ino lança uma ilusão que engana o inimigo, fazendo com que eles vejam uma tempestade de flores. Esta técnica tem diferentes variações que vão desde um campo com pétalas flutuantes a um estouro de pétalas que cercam o inimigo. Uma vez que Ino tem uma chance clara, ela lança a flor acônito venenosa contra o inimigo batendo-lhe na cabeça do oponente.', E'忍法・百花繚乱', E'Ninpō: Hyakka Ryōran', E'Arte Ninja: Profusão de Flores', E'Arte Ninja: Coberta com Flores', NULL, NULL, E'Curto a Médio alcance', NULL, E'Cachorro → Selo especifico da técnica → Tigre'),
(101, E'Arte Ninja: Roda da Flor de Lua Nova', E'Ino gira um maço de flores da lua no ar que se transforma em uma lâmina circular, semelhante a um chakram. Ela, então, joga a lâmina no oponente, cortando qualquer coisa em seu caminho. Uma vez que chega no adversário, ela corta através dele e explode em pétalas de flor da lua.', E'忍法・新月一輪', E'Ninpō: Shingetsu Ichirin', E'Arte Ninja: Roda da Flor de Lua Nova', E'Arte Ninja: Corte da Flor de Lua', NULL, NULL, E'Longo alcance', NULL, NULL),
(102, E'Arte Ninja: Técnica da Fantasia', E'Esta é uma habilidade geral em que um ninja disfarça-se com um traje de fantasia para enganar o oponente. Ela pode ser usada em conjunto com a Técnica de Transformação, para um efeito mais realista ou mesmo em conjunto com alguma técnica de confusão visual, como convocar gaivotas e outros pássaros. O usuário ainda pode adaptar sua voz para confundir o usuário com essa técnica. Esta habilidade simples, embora pareça inútil devido à Técnica de Transformação, mostrou-se útil, visto que o ninja não faz uso de chakra para se disfarçar.', E'忍法・着ぐるみの術にんぽう・きぐるみのじゅつ', E'Ninpō: Kigurumi no Jutsu', E'Arte Ninja: Técnica da Fantasia', NULL, NULL, NULL, NULL, NULL, NULL),
(103, E'Arte Ninja: Técnica da Passagem Através da Parede', E'A fim de tentar impressionar Tenten, Lee desenvolveu esta arte ninja falsa para lhe permitir atravessar paredes e entrar em uma sala. Lee usou este movimento extremista para mover-se de um lado para o outro em um local e, em seguida, abrir a porta do mesmo.', E'忍法・壁抜けの術', E'Ninpō: Kabenuke no Jutsu', E'Arte Ninja: Técnica da Passagem Através da Parede', NULL, NULL, E'Arte Ninja: Jutsu Fantasma', NULL, NULL, E'Carneiro'),
(104, E'Arte Ninja: Técnica do Corpo da Shuriken Gigante', E'O usuário lança uma Fūma Shuriken em direção ao oponente e usa seu chakra para ampliá-la maciçamente, brutalmente golpeando o oponente e enviando-o para voar.', E'忍法・手裏剣巨身の術', E'Ninpō: Shuriken Goshin no Jutsu', E'Arte Ninja: Técnica do Corpo da Shuriken Gigante', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, E'Tigre'),
(105, E'Arte Ninja: Verdadeira Captura de Espada com as Mãos Nuas', E'Com as mãos nuas, é possível parar o ataque de uma espada através do atrito com o lados da lâmina, unindo ambas as mãos com a espada entre elas. O usuário ainda pode utilizar mãos criadas por outras técnicas ou de marionetes para executá-la.', E'忍法・真剣白刃取りにんぽう・しんけんしらはどり', E'Ninpō: Shinken Shirahadori', E'Arte Ninja: Verdadeira Captura de Espada com as Mãos Nuas', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(106, E'Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena', E'A Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena é uma Técnica Proibida acessível somente aos usuários da técnica da Criação do Renascimento.', E'忍法創造再生 百豪の術', E'Ninpō Sōzō Saisei — Byakugō no Jutsu', E'Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena', NULL, E'Mitótica Regeneração: Uma Centena de Curas', NULL, NULL, E'Rank S', E'Tigre[2]'),
(107, E'Arte Ninja da Espada Longa: Costura da Aranha da Terra', E'Depois de preparar o terreno com o longo fio de aço anexado na Nuibari, o usuário executa o selo de mão necessário, enterra a espada no solo e puxa o fio, que pressiona o membro do oponente que está na área entre os nós do fio. A pressão feita pelo fio é tão grande que é capaz de portar músculo e ossos facilmente. A força exercida pelo fio pode ser controlada pelo usuário através da aplicação de uma força adicional.', E'長刀忍法・地蜘蛛縫いちょうとうにんぽう・じぐもぬい', E'Chōtō Ninpō: Jigumo Nui', E'Arte Ninja da Espada Longa: Costura da Aranha da Terra', NULL, NULL, E'Arte Ninja da Espada Longa: Costura da Aranha', E'Todos os alcances', NULL, E'Cobra'),
(108, E'Arte Ninja da Espada Longa: Crucificação de Arame', E'O usuário lança a Nuibari a fim de perfurar e prender seus oponentes com os fios de aço de uma forma a deixar seus corpos como se eles estivessem crucificados. O golpe final é dado ao perfurá-los com Nuibari, deixando-os para uma morte lenta e dolorosa.', NULL, NULL, NULL, NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(109, E'Arte Ninja da Imitação', NULL, E'擬忍法', E'Gi Ninpō', E'Arte Ninja da Imitação', NULL, NULL, E'Arte Ninja de Mimetismo', NULL, NULL, E'Tigre'),
(110, E'Arte Ninja de Flor', E'A Arte Ninja de Flor (花忍法, Hana Ninpō) é um subconjunto do ninjutsu e genjutsu envolvendo técnicas secretas orientados de flor usadas por um clã no País dos Vegetais. Os dois usuários conhecidos da Arte Ninja de Flor são Kikunojō e Yurinojō.', E'花忍法はなにんぽう', E'Hana Ninpō', E'Arte Ninja de Flor', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(111, E'Arte Ninja de Flor: Desabrochar das Cem Flores', E'Esta técnica transforma qualquer flor, pétala ou broto ao redor em bombas que podem ser detonadas como papéis explosivos.', E'花忍法・百花斉放はなにんぽう・ひゃっかさいほう', E'Hana Ninpõ: Hyakkasaihō', E'Arte Ninja das Flores: Desabrochar das Cem Flores', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(112, E'Arte Ninja de Flor: Espelho da Flor da Água', E'O usuário cria uma ilusão que envolve o alvo em pétalas de flores, escurecendo sua visão e incapacitando-o de moldar seu chakra, durante a ilusão o ninja cria pétalas reais para envolverem seu oponente, fazendo com que ao se libertar do genjutsu ele tenha a impressão que falhou e ainda está preso.', E'花忍法・鏡花水はなにんぽう・きょうかすい', E'Hana Ninpō: Kyōkasui', E'Arte Ninja de Flor: Espelho da Flor da Água', NULL, NULL, NULL, E'Curto alcance', NULL, NULL),
(113, E'Arte Ninja de Flor: Liberação de Muitas Flores', E'Após liberar flores e pétalas ao ar o usuário encurrala seu adversário com elas, em seguida se inflama as flores. Estas chamas, em seguida, atiram-se em direção ao alvo e explodem podendo mudar sua direção durante o voo.', E'花忍法・百花繚乱はなにんぽう・ひゃっこりょうらん', E'Hana Ninpō: Hyakkoryōran', E'Arte Ninja de Flor: Liberação de Muitas Flores', NULL, NULL, NULL, E'Curto a Médio alcance', NULL, NULL),
(114, E'Arte Ninja de Kagerō: Efêmero', E'Kagerō forma quatro asas de chakra para desencadear poderosas explosões de energia, além de também conceder-lhe a capacidade de teletransporte instantâneo. Esta técnica só pode ser usada apenas uma vez na vida, pois a quantidade de chakra usada é enorme, sendo assim, o usuário fica, em estado crítico, à beira da morte.', E'蜉蝣忍法・泡沫カゲロウにんぽう・うたかた', E'Kagerō Ninpō: Utakata', E'Arte Ninja de Kagerō: Efêmero', NULL, NULL, E'Arte Ninja Kagero: Efêmera', E'Todos os alcances', NULL, E'Serpente → Passaro → Macaco → Boi'),
(115, E'Arte Secreta da Técnica Branca Secreta: Coleção de Ilusões', E'Depois de tomar o controle de vários corpos ao mesmo tempo, o usuário deixa-os atacar em todo um campo de batalha, resultando em impactos constantes e devastadores. Essa técnica se torna muito útil em combate contra vários inimigos, considerando que os inimigos abaixarão sua guarda em favor da vida do companheiro controlado.', E'白秘技奥義・夢幻の集', E'Shirohigi Ōgi: Mugen no Shū', E'Arte Secreta Técnica Secreta Branca: Coleção de Ilusões', NULL, NULL, NULL, E'Todos os alcances', NULL, NULL),
(116, E'Arte Sábia: Cadeia de Trovão do Lado da Prisão', E'Madara posiciona suas sombras mantidas numa formação quadrática e cada uma cria um feixe de relâmpago, indo de sombra a sombra, a qual se aproxima do oponente em meio a formação.', NULL, NULL, E'Arte Sábia: Cadeia de Trovão do Lado da Prisão', NULL, NULL, NULL, E'Curto a médio alcance', NULL, NULL),
(117, E'Arte Sábia: Chamada do Sapo', E'Depois de focar o chakra do Senjutsu para suas gargantas, ambos Fukasaku e Shima coaxam juntos em uníssono, liberando ondas de som grandes, altas e imensamente poderosas que efetivamente irritam, distraem e paralisam temporariamente todos os alvos atingidos completamente.', E'仙法・蛙鳴きせんぽう・かわずなき', E'Senpō: Kawazu Naki', E'Arte Sábia: Chamada do Sapo', NULL, E'Arte Eremita: O Lamento do Bratáquio', E'Arte Sábia: Canção do Sapo', E'Curto a Médio alcance', E'Rank –', E'Bater as maos'),
(118, E'Arte Sábia: Despacho Relâmpago da Liberação de Yin', E'A Arte Sábia: Despacho Relâmpago da Liberação de Yin é um ninjutsu utilizado por Madara Uchiha, após a transformação em Sábio dos Seis Caminhos.', E'仙法・陰遁雷派せんぽう・いんとんらいは', E'Senpō: Inton Raiha', E'Arte Sábia: Despacho Relâmpago da Liberação de Yin', E'Estilo Oculto: Onda do Trovão', E'Arte Eremita: Estilo Oculto: Onda do Trovão', NULL, E'Curto a Médio alcance', NULL, E'Carneiro Espelhado'),
(119, E'Arte Sábia: Feixe de Luz da Liberação de Tempestade', E'Após combinar a Liberação de Tempestade com a energia natural, o usuário emite um feixe de luz para bifurcar o inimigo, capaz, inclusive, de cortar a Esfera da Busca da Verdade. Uma vez que a técnica se propaga na velocidade da luz, torna-se quase impossível de desviar.', E'仙法・嵐遁光牙せんぽう・らんとんこうが', E'Senpō: Ranton Kōga', E'Arte Sábia: Feixe de Luz da Liberação de Tempestade', NULL, E'Arte Eremita: Estilo da Tempestade: Presa de Luz', NULL, E'Curto a Médio alcance', NULL, NULL),
(120, E'Arte Sábia: Goemon', E'Trata-se de uma técnica colaborativa usada por Jiraiya com Shima e Fukasaku.', E'仙法・五右衛門せんぽう・ごえもん', E'Senpō: Goemon', E'Arte Sábia: Goemon', NULL, E'Arte Eremita: Execução em Óleo', NULL, E'Todos os alcances', E'Rank –', NULL);