INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(121, 'Arte Sábia: Grande Bola Rasengan', 'Esta é a versão do Modo Sábio da Grande Bola da Esfera Espiral. A técnica comum requer que Naruto faça um clone das sombras para formar o ataque e, em seguida, empunhá-lo. Ao usar a sua variante no Modo Sábio no entanto, ele é capaz de usar dois clones para formar o ataque em ambas as mãos e, em seguida, dar à Grande Bola da Esfera Espiral para os clones de empunharem por si mesmos. Também é maior do que a sua técnica mãe. Esta técnica foi capaz de explodir nas invocações de Pain no alto.', '仙法・大玉螺旋丸', 'Senpō: Ōdama Rasengan', 'Arte Eremita: Grande Bola da Esfera Espiral', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(122, 'Arte Sábia: Liberação de Madeira: Desempenho da Dança do Dragão de Madeira', 'Hashirama cria uma gigantesca estátua de madeira que faz com que um dragão de madeira entre em erupção a partir do solo e ataque seu oponente.', '仙法・木遁・木龍演舞', 'Senpō: Mokuton: Mokuryū Enbu', 'Arte Sábia: Liberação de Madeira: Desempenho da Dança do Dragão de Madeira', 'Arte Sábia: Estilo Madeira: Dança do Dragão de Madeira', NULL, NULL, 'Todos os alcances', NULL, NULL),
(123, 'Arte Sábia: Liberação de Madeira: Pulso do Solo', 'A estátua gigante de Hashirama encosta um dedo no chão, causando uma erupção de madeira pelo chão, que ataca seu oponente.', '仙法・木遁・大地脈導', 'Senpō: Mokuton: Daichi Myakudō', 'Arte Sábia: Liberação de Madeira: Pulso do Solo', 'Arte Sábia: Estilo Madeira: Pulso do Solo', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(124, 'Arte Sábia: Nuvem de Poeira da Liberação de Vento', 'Shima exala uma nuvem enorme de poeira de sua boca, que é grossa o suficiente para que ela seja capaz de bloquear a visão do alvo completamente. A nuvem de poeira também é grande o suficiente para esconder uma invocação tão grande como Gamabunta.', '仙法・風遁砂埃', 'Senpō: Fūton Sunabokori', 'Arte Sábia: Nuvem de Poeira da Liberação de Vento', NULL, NULL, 'Arte Sábia: Estilo Vento: Poeira de Areia', NULL, NULL, 'Bater as maos'),
(125, 'Arte Sábia: Portão do Grande Deus', 'É uma técnica de selamento que é usada no Modo Sábio, o usuário manifesta um número seleto de maciços torii vermelhos que caem de cima para baixo, a fim de fixar um alvo pretendido entre o portão e o chão. O tamanho e a força desses torii é tal que até mesmo pode ser usado para imobilizar o Dez-Caudas. Estes torii são bastante versáteis e podem ser utilizados em várias formas diferentes, este foi exibido após Obito Uchiha tornar-se jinchūriki da besta, Hashirama empilhou os torii uns sobre os outros, numa tentativa de aumentar a sua capacidade para imobilizar o alvo.', '仙法・明神門せんぽう・みょうじんもん', 'Senpō: Myōjinmon', 'Arte Sábia: Portão do Grande Deus', 'Arte Sábia: Portal Divino', 'Arte Eremita: Portão Divino', NULL, 'Longo alcance', 'Rank –', 'Carneiro'),
(126, 'Arte Sábia: Rasengan de Liberação de Magnetismo', NULL, '仙法・磁遁螺旋丸せんぽう・じとんらせんがん', 'Senpō: Jiton Rasengan', 'Arte Sábia: Rasengan de Liberação de Magnetismo', NULL, 'Arte Eremita: Estilo do Magnetismo: Rasengan', NULL, 'Curto alcance', NULL, NULL),
(127, 'Arte Sábia: Rasenshuriken de Liberação de Lava', NULL, '仙法・熔遁螺旋手裏剣せんぽう・ようとんらせんしゅりけん', 'Senpō: Yōton Rasenshuriken', 'Arte Sábia: Rasenshuriken de Liberação de Lava', NULL, 'Arte Eremita: Estilo da Lava: Rasen Shuriken', NULL, 'Todos os alcances', NULL, NULL),
(128, 'Arte Sábia: Reencarnação Inorgânica', 'Uma técnica capaz de injetar vida em objetos inorgânicos, através da energia natural presente na atmosfera. Devido à manipulação de energia natural, este poder só pode ser utilizado por usuários do Modo Sábio.', '仙法・無機転生せんぽう・むきてんせい', 'Senpō: Muki Tensei', 'Arte Sábia: Reencarnação Inorgânica', 'Arte Sábia: Reanimação Inorgânica', 'Arte Sábia: Animação Inorgânica', NULL, 'Curto a Médio alcance', 'Rank –', 'Boi'),
(129, 'Arte Sábia: Super Rasenshuriken das Bestas com Cauda', 'Esta técnica foi utilizada por Naruto após ele receber o poder do Sábio dos Seis Caminhos. Juntamente com oito clones das sombras, Naruto cria nove Rasenshuriken, cada um infundido com as propriedades especiais do chakra de uma Besta com Cauda específica. Depois de lançar todos eles, eles criam várias e grandes explosões. Ele é descrito como \'o ninjutsu mais maravilhoso de todos os tempos\' (空前絶後の忍術, Kūzenzetsugo no Ninjutsu).', '仙法・超尾獣螺旋手裏剣せんほう・ちょうおじゅうらせんしゅりけん', 'Senpō: Chō Bijū Rasenshuriken', 'Arte Sábia: Super Shuriken Espiral das Bestas com Cauda', 'Super Besta de Cauda Rasen Shuriken', 'Arte Eremita: Super Shuriken Espiral das Bestas com Cauda', NULL, 'Todos os alcances', NULL, NULL),
(130, 'Arte Sábia: Série Contínuas de Ultra-Grandes Esferas Espirais', 'Depois de criar vários clones das sombras com cada um deles segurando um Rasengan, Naruto então entra no Modo Sábio. Os clones, em seguida, expandem o seu Rasengan em Arte Sábia: Ultra-Grande Bola Rasengan e, simultaneamente, atacam seu inimigo, resultando em uma enorme quantidade de danos, que atordoou até mesmo Kurama.', '仙法・超大玉螺旋多連丸', 'Senpō: Chōōdama Rasen Tarengan', 'Arte Sábia: Série Contínuas de Ultra-Grandes Esferas Espirais', 'Arte Sábia: Mega Barragem de Rasengan Massivos', 'Estilo do Sennin: Super-Esferas Múltiplas de Espiral Gigante', 'Arte Sábia: Barragem de Rasengan Massivos', 'Curto alcance', NULL, NULL),
(131, 'Arte Sábia: Técnica Anfíbia', 'A Arte Sábia: Técnica Anfíbia é uma técnica usada por Shima e Fukasaku para se fundir com Jiraiya. Ela maximiza o chakra sábio para a pessoa que eles são fundidos e lhes permite acessar Modo Sábio. Isso também permite que Fukasaku e Shima continuamente reúnam a energia natural para Jiraiya, como ele não pode fazê-lo a si mesmo enquanto estiver em movimento, eliminando assim a fraqueza de esgotar toda a sua energia natural na batalha. Fukasaku e Shima não foram capazes de fundir com Naruto Uzumaki, porque Kurama os rejeitou.', '仙法・両生の術', 'Senpō: Ryōsei no Jutsu', 'Arte Sábia: Técnica Anfíbia', NULL, 'Estilo Sennin: Jutsu do Anfíbio', NULL, NULL, NULL, NULL),
(132, 'Arte Sábia: Técnica Branca Violenta', 'Depois de formar os selos de mão necessários, o usuário expele uma entidade como dragão de sua boca que carrega uma esfera em sua pata dianteira esquerda. Como o dragão firmemente bobina-se em todo o orbe, ele libera tanto a luz ofuscante e som ensurdecedor. Isto por si só dificulta enormemente aqueles na proximidade, mas, devido à intensidade das vibrações resultantes, a dor paralisante também é induzida. Assim, a técnica tanto brevemente inibe os sentidos do oponente, bem como técnicas que exigem a sua concentração.', '仙法・白激の術', 'Senpō: Hakugeki no Jutsu', 'Arte Sábia: Técnica Branca Violenta', 'Arte Sábia: Ataque Branco Extremo', NULL, NULL, 'Curto a Médio alcance', 'Rank –', 'Selo especifico técnica → Rato → Passaro'),
(133, 'Arte Sábia: Técnica da Disseminação Raivosa', 'Kabuto expele inúmeros dragões brancos — os quais carregam uma esfera roxa em sua pata dianteira esquerda — que atacam o inimigo, bobinando-se em volta da esfera e gerando fortes luzes que cegam o adversário.', '仙法・散激の術', 'Senpō: Sangeki no Jutsu', 'Arte Sábia: Técnica da Disseminação Raivosa', 'Técnica Eremita: Jutsu da Fúria Dispersa', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(134, 'Arte Sábia: Ultra-Grande Bola Rasengan', 'A Arte Sábia: Ultra-Grande Esfera Espiral é um Rasengan gigantesco criado por Naruto Uzumaki, reforçado com chakra sábio. Embora seus efeitos destrutivos não foram vistos, foi usado para distrair Kurama. Presumivelmente, uma vez que é utilizado no Modo Sábio, é ainda mais forte do que a outra versão, que foi dita sendo capaz de esculpir uma montanha se explodida.', '仙法・超大玉螺旋丸', 'Senpō: Chō Ōdama Rasengan', 'Arte Sábia: Ultra-Grande Esfera Espiral', NULL, 'Estilo do Sennin: Super-Esfera de Espiral Gigante', 'Arte Sábia: Super Rasengan Gigante', 'Curto alcance', NULL, NULL),
(135, 'Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras', 'A Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras é uma técnica que cria uma estátua de madeira titânica de muitas mãos, que pode ser controlada livremente pelo usuário.', '仙法木遁・真数千手せんぽうもくとん・しんすうせんじゅ', 'Senpō Mokuton: Shin Sūsenju', 'Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras', 'Arte Sábia: Estilo Madeira: Mil Mãos Verdadeiras', 'Arte Eremita: Estilo da Madeira: As Verdadeiras Mil Mãos', NULL, 'Curto a Médio alcance', 'Rank –', NULL),
(136, 'Arte da Espada da Névoa Sangrenta: Mutilação de Ossos', 'Usando a Hiramekarei, o usuário cria uma grande quantidade de agulhas de chakra azul claro para atacar o inimigo. Uma vez empalado, as agulhas causam dano no Sistema de Circulação de Chakra, enquanto é restringido pouco a pouco de acordo com o selo de mão do usuário. Em vez de receber uma morte rápida, o inimigo sofre lentamente.', '血霧刀法・骨抜きちぎりとうほう・ほねぬき', 'Chigiri Tōhō: Honenuki', 'Arte da Espada da Névoa Sangrenta: Mutilação de Ossos', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Restringir: Rato'),
(137, 'Arte da Matança Combinada', 'Usando sua argila, Deidara cria várias aranhas explosivas e as joga no ar, em seguida, Sasori usa sua marionete do Terceiro Kazekage para criar estilhaços de Areia de Ferro em forma de lâminas que transportam as aranhas até o oponente. O oponente é forçado contra o chão onde as aranhas detonam. A explosão é tão poderosa que Sasori usa sua marionete do Terceiro Kazekage para criar uma barreira de areia de ferro para defender-se.', '合殺芸術がっさいげいじゅつ', 'Gassai Geijutsu', 'Arte da Matança Combinada', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(138, 'Arte do Punho Gentil Golpe de Um Corpo', 'Uma técnica nascida a partir de sua habilidade inata de expelir chakra de cada tenketsu em seu corpo, um membro do clã Hyūga pode bater seu oponente com uma rajada de chakra que irá enviá-lo voando para longe do usuário. Mostrou-se também que esta técnica pode ser utilizada com precisão concentrada para atingir o ponto fraco de uma técnica, exemplificado quando Neji Hyūga foi preso pela Técnica da Prisão de Água de Kisame Hoshigaki, e foi capaz de dissipá-la.', '柔拳法一撃身じゅうけんほういちげきしん', 'Jūkenhō Ichigekishin', 'Arte do Punho Gentil Golpe de Um Corpo', NULL, 'Arte do Punho Leve Impacto Corporal', 'Arte do Punho Gentil: Golpe do Corpo', 'Curto alcance', NULL, NULL),
(139, 'Arte do Punho Gentil: Palma Rotatória de Vácuo', 'Hanabi realiza um movimento rotatório que atinge o oponente e o arremessa para o alto ou para longe.', '柔拳法·空転掌', 'Jūkenpō: Kūtenshō', 'Arte do Punho Gentil: Palma Rotatória de Vácuo', 'Punho Gentil: Palma Aérea Rotatória', NULL, NULL, 'Curto alcance', NULL, NULL),
(140, 'Artimanha de Entrada Impulsiva', 'Esta é uma formação usada para forçar a entrada em um local protegido, mantendo ao menos uma pessoa de cada lado da entrada bloqueada, um atacante e um protetor. Dessa forma, é possível quebrar as defesas inimigas e ainda se precaver de possíveis ataques de dentro do local.', 'ボタンフックエントリー', 'Botan Fukku Entorī', 'Artimanha de Entrada Impulsiva', NULL, NULL, 'Operação Entrada em Gancho', 'Médio alcance', NULL, NULL),
(141, 'Aríete de Lodo Ultra-Viscoso', 'Hiruko usa um líquido viscoso estranho ao seu redor durante o ritual de imortalidade do eclipse. Ele é capaz de manipular a viscosidade do líquido, de modo que ele pode controlá-lo. O movimento do líquido é similar a uma mola de metal. Esses pedaços de líquido podem acertar um inimigo com força suficiente para derrubá-lo temporariamente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(142, 'Asa Decapitadora', 'Usando as asas de Chōmei, Fū envia uma onda de choque contra seu oponente, cortando seu corpo em pedaços.', '翅斬り', 'Hanegiri', 'Asa Decapitadora', 'Corte no Ventilador', NULL, NULL, 'Curto à Médio alcance', NULL, NULL),
(143, 'Asa Rotativa', 'Transformada em Chōmei, Fū gira com a cauda sem-asa, criando e direcionando um pequeno tornado até seu oponente.', '翅旋', 'Hanetsumuji', 'Asa Rotativa', 'Lâminas Rotativas', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(144, 'Asas Mecânicas', NULL, 'カラクリ羽', 'Karakuri Hane', 'Asas Mecânicas', 'Asa da Marionete', NULL, NULL, 'Curto e Médio alcance', NULL, NULL),
(145, 'Asas Vibrantes Brilhantes', 'Transformada em Chōmei, Fū desliza até seu oponente com sua cauda sem-asa, seguido por um ataque de chifre, e terminando com um deslize de todo seu abdômen.', '輝々翻翅', 'Kiki Honshi', 'Asas Vibrantes Brilhantes', 'Lâmina Ofuscante', NULL, NULL, 'Curto alcance', NULL, NULL),
(146, 'Ascensão Destruidora de Rocha da Folha', 'Uma técnica de taijutsu onde o usuário esmaga com seu cotovelo seu oponente usando o outro braço como apoio. Trata-se de um poderoso golpe no alvo e como o nome sugere, é ainda forte o suficiente para destruir coral.', '木ノ葉壊岩升このはかいがんしょう', 'Konoha Kaiganshō', 'Ascensão Destruidora de Rocha da Folha', NULL, 'Quebra-Rocha da Folha', 'Esmagamento de Rocha da Folha', 'Curto alcance', NULL, NULL),
(147, 'Ascensão dos Dragões Gêmeos', 'A única técnica de Tenten que tira proveito de suas habilidades superiores com armas. Primeiro, ela coloca dois pergaminhos pequenos em uma posição vertical no chão. Quando ativados, os dois pergaminhos são liberados, enquanto voam e giram ao ar com a forma de dois dragões feitos de fumaça. Tenten então salta entre os dois pergaminhos, e começa a convocar e lançar rapidamente todos os tipos de armas como uma barragem poderosa. Se a barragem inicial vier a falhar, ela pode controlar as armas descartadas com fios ligados à ponta dos seus dedos para um segundo ataque surpresa. No entanto, por as armas serem jogadas, elas são facilmente contrariadas por técnicas de vento.', '双昇龍そうしょうりゅう', 'Sōshōryū', 'Ascensão dos Dragões Gêmeos', 'Ferramenta Ninja: Lâmina de 1000 Greves, Arma de controle: Tensasai', NULL, 'Dragões Gêmeos Ascendentes; Controle do Dragão Nascente', 'Médio alcance', NULL, 'Tigre → Dragao → Macaco → Coelho → Serpente'),
(148, 'Assalto da Aglomeração de Areia de Ferro', 'O Assalto da Aglomeração de Areia de Ferro é uma técnica que reúne um grande volume de areia de ferro e comprime-o em uma forma de alta densidade.', '砂鉄結襲さてつけっしゅう', 'Satetsu Kesshū', 'Ataque da Aglomeração da Areia da Ferro', NULL, NULL, NULL, 'Todos os alcances', 'Rank –', NULL),
(149, 'Assalto da Aglomeração de Areia de Ferro: Cinco Unhas Solares', 'Sasori manipula a marionete humana do Terceiro Kazekage para liberar areia de ferro e formá-la em múltiplas lanças longas para cercar e atacar o oponente, tornando a chance de escapar difícil.', '砂鉄結襲・五寸釘さてつけっしゅう・ごすんくぎ', 'Satetsu Kesshū: Gosunkugi', 'Assalto da Aglomeração de Ferro da Areia: Cinco Unhas Solares', 'Ataque da Aglomeração de Areia de Ferro: Seis Polegadas Solares', NULL, NULL, 'Todos os alcances', NULL, NULL),
(150, 'Assalto da Aglomeração de Areia de Ferro: Martelo', 'Sasori usa sua marionete humana do Terceiro Kazekage para manipular uma grande quantidade de areia de ferro em um grande bloco, o qual ele pode usar para esmagar seu oponente.', '砂鉄結襲・鎚さてつけっしゅう・つち', 'Satetsu Kesshū: Tsuchi', 'Assalto da Aglomeração de Ferro da Areia: Martelo', 'Aglomeração de Areia de Ferro: Clava', NULL, NULL, 'Todos os alcances', NULL, NULL),
(151, 'Assalto da Aglomeração de Areia de Ferro: Queda', 'Depois de formar a areia de ferro em um bloco gigante, o usuário eleva-o acima do oponente e deixa-o cair sobre ele. Devido a ser um objeto pesado, a gravidade propicia um puxão mais forte nele, fazendo com que o tempo que isso leva para chegar ao chão seja muito rápido e com uma grande força por trás dele, tornando-se difícil escapar.', '砂鉄結襲・落さてつけっしゅう・らく', 'Satetsu Kesshū: Raku', 'Assalto da Aglomeração de Areia de Ferro: Queda', 'Areia de Ferro em Massa', NULL, NULL, 'Curto alcance', NULL, NULL),
(152, 'Assassinato Instantâneo', 'Asuma Sarutobi espera seus inimigos atacarem e os golpeia com uma cotovelada forte no estômago, atordoando-os.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(153, 'Assassinato dos Espelhos Demoníacos de Gelo', 'Depois que a vítima é presa nos espelhos de gelo de Haku, ele ataca a vítima em alta velocidade, como ele faz com a técnica Espelhos Demoníacos de Cristais de Gelo. Depois disso, um choque de luz azul explode no interior da cúpula, danificando a vítima gravemente. Antes disso acontecer, Haku escapa no último segundo, para evitar danos a si mesmo. Os espelhos depois quebram em um milhão de pedaços de gelo.', '魔鏡氷殺まきょうひょうさつ', 'Makyō Hyōsatsu', 'Assassinato dos Espelhos Demoníacos de Gelo', 'Jutsu Oculto: Profundidade do Diabo Região de Gelo', NULL, NULL, 'Curto alcance', NULL, NULL),
(154, 'Assassinos Mais Malignos', 'Itachi cria um clone de água para perfurar e prender o oponente. Kisame corta-o no ar enquanto usa os restos do clone água destruído para invocar um tubarão de água para atacar o oponente. O oponente cai na esteira do Tsukuyomi de Itachi em que o oponente se esvai como se fosse água.', '最凶の刺客さいきょうのしかく', 'Saikyō no Shikaku', 'Assassinos Mais Malignos', 'Os Maléficos Assassinos', NULL, NULL, 'Curto alcance', NULL, NULL),
(155, 'Assimilação', 'Trata-se de uma habilidade suplementar utilizada especialmente em infiltrações. Tal como o nome sugere, a técnica possibilita ao usuário a assimilação — isto é, camuflagem — de seu corpo com algum local.', '同化どうか', 'Dōka', NULL, NULL, NULL, 'Jutsu de Prevenção ao Ataque', NULL, NULL, 'Javali[3]'),
(156, 'Assimilação: Caixão de Areia', 'Esta técnica é uma cópia feita por Nagare da técnica de Gaara, diferenciando apenas na composição de tal, que é usado pó de rocha em vez de areia.', '同化・砂縛柩', 'Dōka: Sabaku Kyū', 'Assimilação: Caixão de Areia', NULL, NULL, 'Assimilação: Ataúde de Areia', NULL, NULL, NULL),
(157, 'Assimilação: Cortina Vermelha de Veneno', 'Depois de assumir as propriedades do solo único, vermelho situado na Sétima Frente, o Exército de Zetsu Branco ganha propriedades venenosas. Sempre que expirarem ou serem cortados em pedaços e mortos, eles expelem um gás venenoso vermelho capaz de deixar seus oponentes inconscientes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(158, 'Assimilação: Funeral do Deserto', 'Esta técnica é uma cópia feita por Nagare da técnica de Gaara, com a excepção da utilização de pó de rocha, ao invés de areia.', '同化・砂瀑送葬', 'Dōka: Sabaku Sōsō', 'Assimilação: Funeral do Deserto', NULL, NULL, 'Assimilação: Enterro de Areia', 'Curto a Médio alcance', NULL, NULL),
(159, 'Assimilação: Tanque de Rocha', 'Esta técnica é uma versão copiada do Tanque da Bala Humana do Clã Akimichi, a única diferença é que o usuário utiliza a Técnica de Assimilação da Criação de Todas as Coisas para se fundir com uma pedra próxima e coletar a massa necessária para realizá-lo, em vez de criar por si próprio através da conversão de gordura corporal em chakra.', '同化・岩石戦車', 'Dōka: Ganseki Sensha', 'Assimilação: Tanque de Rocha', NULL, NULL, 'Assimilação: Tanque de Pedra', 'Curto alcance', NULL, NULL),
(160, 'Assimilação: Vendaval de Rocha', 'Depois de executar a Técnica de Assimilação da Criação de Todas as Coisas e se fundir com um objeto, Nagare controla as rochas que compõem o seu \'corpo\' e pele de seu oponente com as rochas capazes de regenerar-se imediatamente depois de qualquer ataque.', '同化・岩吹雪', 'Dōka: Iwafubuki', 'Assimilação: Vendaval de Rocha', NULL, NULL, 'Assimilação: Chuva de Pedras', 'Curto alcance', NULL, NULL),
(161, 'Atacando em Ambos os Lados', 'Izumo e Kotetsu se preparam em ângulos diferentes do seu inimigo, geralmente em seus respectivos lados direito e esquerdo, e em seguida, correm velozmente para atacar o adversário com suas lâminas super afiadas. Como as lâminas têm formatos diferentes, o objetivo é principalmente cortar o adversário por igual, de trás pra frente e vise-versa, ou esfaqueá-lo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(162, 'Ataque Absoluto Rígido Final: Funeral do Deserto', 'Gaara bate as mãos, criando uma montanha gigante de areia para levantar seu oponente, aprisionando-o e segurando qualquer parte do corpo, exceto a cabeça. Ele em seguida, invoca uma grande quantidade de areia na forma do Ataque Absoluto Rígido Final: Lança de Shukaku, no qual ele joga em direção a montanha de areia em uma velocidade intensa, onde de fato seu oponente se encontra preso. Devido ao tamanho da lança, ela rasga seu oponente, matando-o e causando uma explosão em uma grande quantidade de areia.', '最硬絶対攻撃・砂瀑槍葬', 'Saikō Zettai Kōgeki: Sabaku Sōsō', 'Ataque Absoluto Rígido Final: Funeral do Deserto', 'Último Ataque: Lança de Areia do Funeral', NULL, NULL, 'Longo alcance', NULL, 'Juntar as Maos'),
(163, 'Ataque Absoluto Rígido Final: Lança de Shukaku', 'A Lança de Shukaku é a contrapartida de ataque do Escudo de Shukaku. Como o escudo, Gaara esmaga os materiais mais duros do chão usando areia, desta vez na forma de uma alabarda, na forma da mão de Shukaku. Assim como o escudo que ainda não encontrou nada que pudesse quebrar a sua defesa, a alabarda ainda não encontrou uma defesa que não pode romper.', '最硬絶対攻撃・守鶴の矛', 'Saikō Zettai Kōgeki: Shukaku no Hoko', 'Ataque Absoluto Rígido Final: Lança de Shukaku', NULL, NULL, 'Derradeiro Ataque Supremo: Lança de Shukaku', 'Curto a Médio alcance', NULL, NULL),
(164, 'Ataque Asura', 'A partir das habilidades do Caminho Asura, o usuário pode liberar um canhão, em alguma parte de seu corpo para disparar uma enorme explosão de chakra, com poder capaz de devastar regiões inteiras.', '修羅ノ攻しゅらのこう', 'Shura no Kō', 'Ataque Asura', 'Ataque Shura', NULL, NULL, 'Todos os alcances', NULL, NULL),
(165, 'Ataque Contra os Pontos Fracos', 'Trata-se de um golpe que é desferido pelo usuário em algum ponto frágil do corpo de seu inimigo, podendo atordoar ou até mesmo desacordar o inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(166, 'Ataque Desvairado do Selo Amaldiçoado', 'Jūgo transforma parcialmente uma parte de seu corpo e, em seguida, corre em alta velocidade para golpear seu inimigo. Dependendo da eficiência do golpe, Jūgo pode matar seu inimigo com um soco.', '呪印乱撃じゅいんらんげき', 'Juinrangeki', NULL, 'Cerimônia de Inauguração (地鎮祭, Jichinsai)[2]', NULL, NULL, 'Curto alcance', NULL, NULL),
(167, 'Ataque Duplo da Espada de Areia', 'Gaara cria dois riachos de areia para perseguir o oponente, um riacho vai pelo ar enquanto o outro vai pelo chão. O riacho de areia no chão ataca o oponente e carrega ele até o ar. Enquanto o que está no ar se une com o do chão para esmagar o oponente colocando ele no centro da colisão.', '砂刃双撃', 'Sajin Sōgeki', 'Ataque Duplo da Espada de Areia', 'Lâmina Dupla de Areia', NULL, NULL, 'Longo alcance', NULL, NULL),
(168, 'Ataque Especial Rasengan', 'Naruto soca seu oponente no chão e cria dois clones, um clone cria um Rasengan mais fraco na mão do outro, que ataca o oponente.', '特攻螺旋丸', 'Tokkō Rasengan', 'Ataque Especial Esfera Espiral', 'Rasengan Carregado', NULL, NULL, 'Curto alcance', NULL, NULL),
(169, 'Ataque Forte da Garra do Gato', 'Matatabi, Yugito em seu Modo Besta com Cauda ou mesmo Nekomata, desfere uma batida sólida com sua pata direita, podendo ser ela na vertical, para aproveitar seu próprio peso, ou horizontal, para aproveitar um impulso.', '大猫爪撃', 'Daibyō Sōgeki', 'Ataque Forte da Garra do Gato', 'Grande Garra do Gato', NULL, NULL, 'Curto alcance', NULL, NULL),
(170, 'Ataque Frio da Lâmina Demoníaca', 'Primeiramente, Zabuza realiza os selos manuais para formar um enorme dragão de água, que em seguida, é completamente revestido pelo gelo de Haku. Em um rápido movimento, o dragão de gelo consome o oponente e o deixa totalmente imóvel, dando a oportunidade de Zabuza e Haku atacarem simultaneamente e finalizarem o seu oponente.', '鬼刃の冷撃きじんのれいげき', 'Kijin no Reigeki', 'Ataque Frio da Lâmina Demoníaca', 'Golpeamento Frio da Lâmina Demoníaca', NULL, NULL, 'Todos os alcances', NULL, 'Passaro + Selo de mao especifico da técnica simultaneamente'),
(171, 'Ataque Gigante', 'O Dez-Caudas levanta um de seus braços e acerta um poderoso golpe em seu oponente.', '巨叩', 'Kyokō', 'Ataque Gigante', 'Grande Ataque', NULL, NULL, 'Longo alcance', NULL, NULL),
(172, 'Ataque Massivo do Deserto', 'O usuário comprime sua areia em forma de esfera afim de aumentar a sua dureza. A esfera pode ser pequena, objetivando diminuir a resistência do ar e atacar adversários com tanta rapidez e força a ponto de deixar seus oponentes inconscientes ou grande o suficiente para derrubar uma pessoa adulta. Como uma pequena esfera, torna-se mais fácil alterar sua trajetória, derrubando múltiplos alvos.', '砂瀑塊撃さばくかいげき', 'Sabaku Kaigeki', 'Ataque Massivo do Deserto', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(173, 'Ataque Nindō', 'Naruto utilizando dois clones da sombra executa uma rápida sequência de pontapés, esbarrões e rasteiras no oponente, até que finaliza com ambos os clones golpeando o oponente para frente.', NULL, NULL, 'Ataque Nindō', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(174, 'Ataque Rápido da Lança de Areia', 'Gaara cria uma lança de areia semelhante a uma broca com a qual ele pode perfurar seu oponente com tremenda força e ferocidade.', '砂迅槍撃さきんそうげき', 'Sajin Sōgeki', 'Ataque Rápido da Lança de Areia', 'Prisão de Areia: Ataque de Lança', NULL, NULL, 'Curto alcance', NULL, 'Selo do Confronto'),
(175, 'Ataque Super Hiper Excelente', 'Os dois usuários colocam capacetes de futebol americano e, então, correm de uma distância considerável, a fim de criar um vórtice de impacto para derrotar o adversário com o forte impacto.', 'スーパーエクセレントハイパーアタック', 'Sūpā Ekuserento Haipā Atakku', 'Ataque Super Hiper Excelente', NULL, NULL, 'Ataque Super Mega Hiper Excelente', 'Curto alcance', NULL, NULL),
(176, 'Ataque da Espada de Fogo Cintilante', 'Shisui corre em alta velocidade em direção ao seu oponente e desfere um corte com sua tantō, então ele a infunde com fogo e desfere um segundo corte pelas costas.', '瞬火襲刀しゅんかしゅうとう', 'Shunka Shūtō', 'Ataque da Espada de Fogo Cintilante', 'Ataque da Espada Combustível', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(177, 'Ataque da Lótus da Juventude da Folha', 'Lee ativa os Oito Portões e oferece uma rajada de socos e chutes em seu oponente, proporcionando um pontapé final.', '青春木ノ葉蓮撃せいしゅんこのはれんげき', 'Seishun Konoha Rengeki', 'Ataque da Lótus da Juventude da Folha', 'Ataque da Lótus Juvenil da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(178, 'Ataque da Marionete Branca', 'Hamura, com o auxilio de seu Byakugan, acumula chakra em seus punhos e desfere uma barragem de incontáveis socos em seu oponente até nocauteá-lo.', '傀儡の白撃くぐつのしろげき', 'Kugutsu no Shirogeki', 'Ataque da Marionete Branca', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(179, 'Ataque da Meia-Idade', 'Depois de canalizar chakra através da mão, o usuário agarra o alvo. Os efeitos da técnica são desconhecidos, mas parecem ser capazes de deixar o inimigo sob uma séria ameaça, visto que Minato Namikaze pediu para Guy parar de executar a técnica contra os sapos do Monte Myōboku, porque ele não queria que eles se machucassem.', 'ミドルエージドアタック', 'Midoru Ējido Atakku', 'Ataque da Meia-Idade', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(180, 'Ataque da Sasumata Explosiva', 'Gamaken ataca o inimigo usando sua sasumata, que provoca enormes explosões após os golpes.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(181, 'Ataque da Super Arma', 'O usuário manipula sua marionete para liberar uma bomba com uma grande quantidade de veneno. O usuário também pode lançar um fluxo venenoso, aumentando a eficácia da técnica.', '超武器攻撃', 'Chōbuki Kōgeki', 'Ataque da Super Arma', 'Jutsu da Marionete Secreta: Chuveiro da Fumaça Venenosa', NULL, NULL, 'Curto alcance', NULL, NULL),
(182, 'Ataque de Combinação Mortal', 'Trata-se de um método colaborativo usado entre Sakon e Ukon para encurralar seu inimigo, e em seguida, assassiná-lo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(183, 'Ataque de Lâminas Manipuladas', 'Uma técnica que envolve shuriken visando uma barragem de kunai em um ponto preciso. O usuário libera chakra de sua mão sobre uma área em que ele pode manter a kunai em suspensão. Como resultado, o inimigo pode ser pego de surpresa e não ter qualquer via de escape, ao mesmo tempo. Com um movimento de mão simples, o usuário é capaz de disparar todas as kunai de uma vez.', '操襲刃', 'Sōshūjin', 'Ataque de Lâminas Manipuladas', 'Chuva de Outono de 1000 Lâminas', 'Avanço das Lâminas Manipuladas', 'Punhais Voadores', 'Curto a Médio alcance', 'Rank C', NULL),
(184, 'Ataque de Relâmpago', 'O usuário cria uma corrente elétrica que pode atingir várias pessoas ao mesmo tempo. Além disso, estende-se em distâncias incríveis e pode evitar obstáculos. Essa técnica é semelhante à Lança Afiada de Chidori, mas não tem o controle avançado da natureza.', '雷撃', 'Raigeki', 'Ataque de Relâmpago', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Tigre → Cobra → Cao'),
(185, 'Ataque de Voo', 'Ōnoki cria uma pedra para lançar o oponente no ar, voa para socá-lo e aumenta o próprio peso ainda no ar, a fim de esmagá-lo contra o chão.', '飛行撃', 'Hikōgeki', 'Ataque de Voo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(186, 'Ataque de Vácuo de Oitenta Deuses', 'O Ataque de Vácuo de Oitenta Deuses é um taijutsu de Kekkei Mōra usado por Kaguya Ōtsutsuki.', '八十神空撃やそがみくうげき', 'Yasogami Kūgeki', 'Ataque de Vácuo de Oitenta Deuses', NULL, NULL, NULL, 'Curto a Médio alcance', 'Rank –', NULL),
(187, 'Ataque do Dedo Mínimo', 'Depois de abrir os Oito Portões, Guy ataca o oponente com um poderoso chute baixo. Guy usou este ataque contra a Estátua Demoníaca do Caminho Exterior para literalmente atacar seu dedinho do pé, fazendo com que ela caísse.', '小指攻撃こゆびこうげき', 'Koyubi Kōgeki', 'Ataque do Dedo Mínimo', NULL, 'Ataque do Mindinho', 'Ataque Rosado', 'Curto alcance', NULL, NULL),
(188, 'Ataque do Dragão Explosivo', 'Usando um dos seus pergaminhos, Tenten invoca um grande dragão de chama que explode ao entrar em contato físico com o adversário.', '爆龍撃ばくりゅうげき', 'Bakuryūgeki', 'Ataque do Dragão Explosivo', NULL, NULL, 'Bomba do Dragão', 'Longo alcance', NULL, NULL),
(189, 'Ataque do Grande Combo da Folha', 'Rock Lee corre na direção de seu oponente, realizando rapidamente o Furacão da Folha, seguido do Vendaval da Folha, e terminando seu combo com o Vento Nascente da Folha, lançando o oponente para longe.', '木ノ葉大連撃', 'Konoha Dairengeki', 'Ataque do Grande Combo da Folha', 'Ataque do Combo da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(190, 'Ataque do Jato Impulsionador', 'Jūgo, enquanto em seu estado transformado, cria várias protuberâncias semelhantes a jatos em suas costas a fim de criar uma poderosa explosão. Dessa forma, ele pode pular longas distâncias rapidamente e é capaz de manobrar no ar, aumentando ainda mais o potencial destrutivo de seus ataques e velocidade que eles são executados.', '噴射式加速攻撃', 'Funshashiki Kasoku Kōgeki', 'Ataque do Jato Impulsionador', 'Ataque Acelerado Tipo Jato', NULL, NULL, NULL, NULL, NULL),
(191, 'Ataque do Parafuso de Relâmpago', 'O usuário soca o oponente duas vezes com o seu punho. Logo em seguida, lança-o para o alto. O usuário, então, salta para cima dele e soca-o novamente, lançando-o para baixo. Em seguida, o usuário desabilita o oponente com grande shuriken vinda do alto, aterrando vários golpes poderosos e girando semelhante a uma broca, perfurando o oponente no chão.', NULL, NULL, NULL, 'Ataque do Parafuso de Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(192, 'Ataque do Salto Trampolim', 'Quando transformado em Kokuō, Han ataca seu oponente por todos os lados com as suas patas da frente durante três vezes, deixando seu oponente incapacitado por um considerável período.', '前踏踊', 'Zentōyō', 'Ataque do Salto Trampolim', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(193, 'Ataque Único da Garra Brutal', 'Com o seu selo amaldiçoado nível 2 ativado, Sakon se aproxima do oponente e desfere um golpe fatal com suas garras, liberando um feixe de luz roxo.', NULL, NULL, NULL, 'Ataque Único da Garra Brutal', NULL, NULL, 'Curto alcance', NULL, NULL),
(194, 'Ataques Simultâneos', 'Vários shinobi entram em posição de defesa e se preparam em meio a várias balistas. Esses equipamentos são capazes de dispersarem várias flechas sucessivamente, o que torna a captura de um alvo no céu fácil. As flechas lançadas através do equipamento também podem variar de tamanho, e serem anexadas a papéis explosivos para um maior dano, tornando acessível a uma explosão em massa de acordo com a quantidade de flechas lançadas em um período curto de tempo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(195, 'Atos Malignos', 'Obito saca uma kunai, fica intangível e, em seguida, corre em direção ao seu oponente, no último momento se materializando e cortando o seu oponente com a kunai.', '業魔', 'Gōma', 'Atos Malignos', 'Obra Demoníaca', NULL, NULL, 'Todos os alcances', NULL, NULL),
(196, 'Autorregra', 'Guy chuta o oponente para baixo e faz cem flexões em cima dele em cinco segundos. Depois disso, ele gira rapidamente para ganhar impulso, terminando por chutar o oponente para longe com pernas cobertas de fogo.', '自分ルールじぶんるーる', 'Jibun Rūru', 'Autorregra', 'Regra Particular', NULL, NULL, 'Curto alcance', NULL, NULL),
(197, 'Avalanche de Pedras', 'O usuário causa um deslizamento de rochas ao enviar uma onda de energia elétrica em um penhasco. No entanto, esta técnica é extremamente baseada no local e necessita ser feita em uma subida de uma montanha para que seja eficaz.', '岩雪崩', 'Iwa Nadare', 'Avalanche de Pedras', NULL, NULL, 'Arte Ninja: Avalanche de Pedras', 'Médio a Longo alcance', NULL, 'Cobra → Tigre de cabeca para baixo → Bater maos juntas'),
(198, 'Avestruz Aéreo', 'É um taijutsu simples, onde Condor dá um chute aéreo em seu oponente.', '駝鳥制空斬', 'Dachō Seikūzan', 'Avestruz Aéreo', NULL, NULL, NULL, 'Curto Alcance', NULL, NULL),
(199, 'Azevinho', 'Kimimaro projeta picos de ossos em suas mãos, vai em direção ao oponente girando rapidamente, semelhante à uma broca, e atinge o oponente rapidamente.', '柊', 'Hiiragi', 'Azevinho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(200, 'Azevinho (Ultimate Ninja 3)', 'Kimimaro projeta picos de ossos em suas mãos, vai em direção ao oponente girando rapidamente, semelhante à uma broca, e atinge o oponente rapidamente.', '柊', 'Hiiragi', 'Azevinho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(201, 'Azevinho (Ultimate Ninja Storm)', 'Kimimaro corta seu oponente repetidas vezes com sua espada óssea, chuta o oponente e termina projetando ossos para fora de seu corpo e rodando em movimento circular', '柊', 'Hiiragi', 'Azevinho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(202, 'Ação Dinâmica', 'O usuário corre rapidamente em direção ao oponente e o ataca com uma rápida série de socos e chutes.', 'ダイナミック・アクション', 'Dainamikku Akushon', 'Ação Dinâmica', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(203, 'Ação Suicida', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(204, 'Bōjutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(205, 'Bōjutsu: Bastão Seccionado em Três', 'Tenten invoca um bastão longo, no qual ela pode dividi-lo em três partes separadas por correntes de metal e tendo a possibilidade de ser usado ofensivamente ou defensivamente.', '棒術・三節棍', 'Bōjutsu: Sansetsukon', 'Técnica de Bastão: Bastão Seccionado em Três', 'Arte do Bastão: Secção dos Três Bastões', NULL, NULL, 'Curto alcance', NULL, NULL),
(206, 'Bala de Chamas', 'Com a ajuda de sua kekkei genkai de Liberação de Lava, o usuário cria e atira uma grande quantidade de projéteis flamejantes.', NULL, NULL, 'Bala de Chamas', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(207, 'Balanço Destrutivo', 'Kurama bate a pata no solo para criar grande explosão, de modo a atacar ou se defender de seu inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(208, 'Balanço Gigante', 'A agarra seu oponente pelas pernas, começa a girar e lança-o para longe.', '射威庵斗吸胤倶', 'Shaianto Suingu', 'Praça de Intimidação, Absorvendo a Questão Tanto de Tiro', 'Balanço Gigante', NULL, NULL, 'Curto alcance', NULL, NULL),
(209, 'Balão Manequim', 'Mecha-Naruto libera três balões manequins de si mesmo. Entrando em contato com o oponente, eles explodem imediatamente.', 'ダミーバルーン', 'Damībarūn', 'Balão Manequim', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(210, 'Balão de Chiclete', 'O Balão de Chiclete é um estilo de luta exclusivo utilizado por Yurui.', 'ガム風船がむふうせん', 'Gamu Fūsen', 'Balão de Chiclete', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(211, 'Balão de Chiclete: Prisão', 'O usuário expele um balão de chiclete para aprisionar seu alvo, podendo suportar até pelo menos três pessoas em seu interior. Ao contrário dos balões de chiclete explosivos, essa variante parece ser muito mais resistente.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(212, 'Bandagens Cortantes', 'Hiruko pode desdobrar as ataduras em torno de seus braços, e infunde chakra nelas para ser usadas, como tentáculos para ataques de longo alcance. As ataduras são feitas de um material tão forte que elas podem facilmente cortar uma rocha e são muito rápidas.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(213, 'Banshō Kokuin', 'Madara cria um orbe negro com propriedades atrativas e joga em direção ao alvo. A esfera é capaz absorver detritos e explode ao atingir o oponente.', '万象黒引', 'Banshō Kokuin', 'Atração Negra de Todas as Coisas', 'Puxão Negro', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(214, 'Banshō Ten\'in', 'Banshō Ten\'in é uma técnica derivada do poder do Rinnegan que manipula forças atrativas em que o usuário puxa o objeto em questão para o usuário.', '万象天引ばんしょうてんいん', 'Banshō Ten\'in', 'Atração Celestial de Toda Criação', 'Puxada Universal', 'Atração Universal', NULL, 'Todos os alcances', 'Rank –', NULL),
(215, 'Barragem da Garoa de Areia', 'A versão mais forte da Garoa de Areia de Gaara. Com essa técnica, Gaara dispara uma infinidade de balas de areia, atirando precisamente no adversário, que podem levar à morte. A velocidade e força que elas são efetuadas é inimaginável, levando a surpreender quando em ação. No confronto de Gaara e Sasuke, essa técnica junto das outras em colaboração possivelmente teria causado graves ferimentos no último, bem como quebrar as costelas frontais do Susanoo.', '連弾砂時雨', 'Rendan Suna Shigure', 'Barragem da Garoa de Areia', 'Barragem: Chuveiro de Areia', 'Chuva de Areia: Disparos Múltiplos', NULL, 'Todos os alcances', NULL, NULL),
(216, 'Barragem da Lâmina de Vento', 'Baki cobre suas mãos com a Lâmina de Vento, entrega uma barragem de socos no oponente, e então acaba com ele com um soco que cria uma grande rajada de vento, jogando o adversário para o ar.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(217, 'Barreira Esférica da Armadilha Devoradora', 'Em algum ponto, o usuário dessa técnica cria uma série de selos de barreira, que são camuflados pelo ambiente. Se o alvo não está ciente dos selos camuflados, a presença do alvo acende a armadilha automaticamente. Uma vez ativada, um chakra esférico vermelho escuro é formado, enjaulando o alvo dentro. Qualquer um pego na armadilha, uma vez que preso será comprimido em seu recipiente, devorado, e esmagado.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(218, 'Barreira Estrondosa do Leque de Vento', 'Temari usa seu Leque Dobradiço Gigante para criar um turbilhão de vento contra o oponente. Quando o oponente é atingido pelo turbilhão, novamente ela usa o leque, porém para fazer com que o oponente seja jogado para alto.', '扇風轟壁せんぷうごうへき', 'Senpū Gōheki', 'Barreira Estrondosa do Leque de Vento', 'Parede de Vento Estrondoso[1]', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(219, 'Barreira Expansiva', 'Cobrindo-se de chakra por todos os lados, o usuário cria um domo etéreo perfeitamente redondo da cor de seu chakra em torno de si. A barreira pode ser expandida para repelir tudo nas proximidades, o que permitiu destroçar a técnica de selamento de Gaara depois de algum tempo de concentração de Urashiki.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(220, 'Barreira Norte Sul', 'Menma Uzumaki usa o Hokuto Sennin e Nanto Sennin, que executam selos ritualísticos e batem seus cajados contra o chão, erguendo barreiras para aprisionar múltiplos alvos. Essa técnica abre brecha para que o Shinigami drene o chakra dos alvos.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(221, 'Barreira Sensorial', 'A Barreira Sensorial (感知結界, Kanchi Kekkai) é uma barreira gigante, em forma de cúpula colocada sobre uma área designada. A barreira não é visível ao olho padrão; quando vistos com certos dōjutsu como o Sharingan, pode ser visto que existe uma concepção de vários círculos que têm o kanji para fronteira (界), e de barreira (結界, kekkai) inscrito neles. Quando ligado a uma Esfera de Água de Sensoriamento, todos dentro e fora da barreira podem ser detectados e identificados.', '感知結界', 'Kanchi Kekkai', 'Barreira de Sensoriamento', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(222, 'Barreira Talismã: Olho Blindado', 'Esta técnica foi criada pelos Oinin de Kirigakure e de acordo com Ao, eles são os únicos que sabem como cancelar a técnica. Quando uma parte específica do corpo do usuário é alvejada, esta técnica é ativada automaticamente. Os talismãs se movem para cobrir a parte específica do corpo e formam uma barreira para protegê-lo.', '結界符・眼装甲', 'Kekkaifu: Gansōkō', 'Barreira Talismã: Olho Blindado', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(223, 'Barreira Violenta de Ventos Fortes', 'Temari manuseia o Leque Dobradiço Gigante para criar um forte vendaval. O oponente é rodeado pelo vento, que puxa-o abruptamente para o céu. Como há nuvens de chuva, o oponente é molhado e eletrocutado por três relâmpagos, em seguida caindo ao chão, imóvel.', '豪風暴壁ごうふうぼうへき', 'Gōfū Bōheki', 'Barreira Violenta de Ventos Fortes', 'Muralha do Vendaval Indomável', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(224, 'Barreira: Método de Formação da Cobertura', 'Esta técnica cria uma barreira de detecção esférica com o utilizador no centro. Ao comando do usuário, a barreira de detecção pode expandir, envolvendo tudo no espaço. O utilizador é capaz de detectar qualquer coisa que se move no interior do espaço da barreira com seus próprios \'sentidos\'. O usuário também pode mover-se, sendo que a barreira irá segui-lo para onde ele for.', '結界・天蓋法陣', 'Kekkai: Tengai Hōjin', 'Barreira: Formação do Método da Cobertura', NULL, NULL, NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(225, 'Barreira: Prisão do Sapo Cabaça', 'Esta técnica invoca um pequeno sapo em forma de cabaça, chamado de \'Sapo Cabaça\' (瓢箪蝦蟇, Hyōtan Gama), uma espécie única de sapo indígena do Monte Myōboku. Esta técnica é utilizada para capturar e isolar uma vítima, arrastando-o no estômago do sapo. Isolado do mundo exterior por uma barreira, o espaço dentro do estômago do Sapo Cabaça é muito maior do que o tamanho da superfície do próprio Sapo Cabaça.', '結界・蝦蟇瓢牢', 'Kekkai: Gama Hyōrō', 'Barreira: Prisão do Sapo Cabaça', NULL, NULL, NULL, NULL, 'Rank B', NULL),
(226, 'Barreira de Auto-Reparação', 'A Barreira de Auto-Reparação é um konbijutsu que, ligando o chakra da equipe de ninja, criará um barreira que, se estiver danificada, será constantemente reparada com o referido chakra que emana de seus corpos. A barreira também impede que os que estão fora dela sintam qualquer coisa dentro dela através de métodos tradicionais.', '再生結界', 'Saisei Kekkai', 'Barreira de Auto Reparação', NULL, 'Barreira de Trinta e Seis Camadas de Auto-Reparação', NULL, 'Longo alcance', NULL, NULL),
(227, 'Barreira de Cinco Selos', 'Esta técnica cria uma barreira em torno de um lugar, colocando quatro selos que proíbem a passagem de um indivíduo em diferentes localidades ao redor do local a ser protegido, fazendo uma marca de cinco sobre aquele local. As etiquetas são colocadas sobre uma superfície plana, e estão ligadas com o chakra do utilizador. Esta técnica torna toda a gama rodeada pelas etiquetas para um espaço de barreira, e no interior do espaço, a destruição material é \'vedada\'. O uso de qualquer tipo de agressão física para tentar destruir algo na barreira é inútil, pois só vai levar a lesões. Para cancelar isso, é necessário procurar as quatro marcas e rasgá-las simultaneamente, o que significa que deve haver ao menos cinco pessoas em uma equipe para cancelá-la.', '五封結界', 'Gofū Kekkai', 'Barreira dos Cinco Selos', NULL, NULL, NULL, 'Todos os alcances', 'Rank C', NULL),
(228, 'Barreira de Confinamento', 'Um tipo de Kekkaijutsu utilizado pelo Time de Barreira de Konoha. Após usar a si mesmos como perímetro para a barreira, uma barreira é erguida e então toma a forma de um domo capaz de segurar mesmo o Nue.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(229, 'Barreira de Contração de Furacões', 'Temari balança o Leque Dobradiço Gigante para criar dois poderosos furacões. Enquanto um rodeia o oponente, o outro suga-o fortemente. Então, os furacões se unem e dilaceram o corpo do inimigo de modo a restar nada.', '旋風挟壁せんぷうきょうへき', 'Senpū Kyōheki', 'Barreira de Contração de Furacões', 'Barreira de Furacões', NULL, NULL, 'Médio alcance', NULL, NULL),
(230, 'Barreira de Ferramentas Ninja: Formação de Escamas Reversas', 'Esta é uma técnica usada pelos Homens dos Quatro Símbolos Celestiais. Concebido para combater a areia de Gaara, a formação baseia-se em suas armas, dispostos em um triângulo com Gaara no centro. Os três membros usam três canais subterrâneos para conectar a sua formação, que irá convergir sob o seu alvo. Após as armas estarem carregadas com o seus respectivos chakra, eles vão ativar a técnica, enviando uma onda de poder através das vias navegáveis para o alvo central. Isso fará com que um dragão de água suba a partir do solo e envolva o alvo, no caso, Gaara, absorvendo a sua areia e tornando a areia muito mais pesada. Isso faz com que a areia se aglutine e se torne quase impossível de utilizar.', '忍具結界・逆鱗の陣', 'Ningu Kekkai: Gyakūrōku no Jin', 'Barreira de Ferramentas Ninja: Formação de Escamas Reversas', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(231, 'Barreira de Liberação de Terra: Domo da Prisão de Terra', 'Esta técnica prende uma ou mais vítimas em um domo de terra que é autorreparante ao sofrer danos. Jirōbō ainda era capaz de absorver o chakra de quem estivesse preso dentro do domo, algo que ele aparentemente gostava muito. No entanto, o chakra de Jirōbō não estava distribuído uniformemente por toda a cúpula, uma vez que o polo mais distante dele, em relação ao lado que ele se encontrava, seu chakra era muito menos concentrado, tornando a cúpula mais fraca à ataques nesse ponto, embora ataques físicos fortes ainda fossem necessários para quebrá-la.', '土遁結界・土牢堂無どとんけっかい・どろうどうむ', 'Doton Kekkai: Dorō Dōmu', 'Barreira de Liberação de Terra: Câmara Nula da Prisão de Terra', 'Barreira do Estilo Terra: Prisão do Domo de Terra', 'Barreira de Terra: Cúpula do Confinamento Vazio', 'Barreira de Estilo Terra: Cúpula de Terra', 'Curto a Médio alcance', 'Rank B', 'Bater as maos no chão'),
(232, 'Barreira de Selamento', 'Num grupo de duas a três pessoas, os usuários se dividem igualmente em torno do oponente e aplicam uma fórmula de selamento sobre ele, que se eleva a partir da superfície em onde é desenvolvida e envolve o alvo, danificando-o e selando suas habilidades mais poderosas.', '封印結界ふういんけっかい', 'Fūin Kekkai', 'Barreira de Selamento', 'Selo de Barreira', NULL, NULL, 'Curto alcance', NULL, 'Selo do Confronto'),
(233, 'Barreira de Selamento de Quatro-Cantos', 'A Barreira de Selamento de Quatro-Cantos é um Fūinjutsu projetado por Tsunade com a finalidade de selar o Isobu.', '結界四方封陣けっかいしほうふうじん', 'Kekkai Shihō Fūjin', 'Barreira de Selamento de Quatro-Cantos', NULL, NULL, 'Barreira de Selamento de Quatro Pontas', NULL, NULL, 'Iniciar: Cao → Cobra → Javali → Tigre,Procurar: Selo do Confronto,Restringir: Juntar as Maos,Selar: Juntar as Maos'),
(234, 'Barreira de Tempestade de Areia', 'Com o auxílio de areia pré-existente, o usuário cria uma barreira de tempestade de areia que é capaz de se sobrepor em outras barreiras ativas, como a Barreira Sensorial. A técnica também atrapalha habilidades sensoriais dos inimigos. A barreira pode ser usada ofensivamente para cercar os seus oponentes e limitar o seu campo de visão.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(235, 'Barreira do Poder Budista: Sinal Auspicioso', 'Seis pilares verdes emergem do solo, elevando uma barreira em todos os lados e na parte de cima para repelir todos os ataques inimigos que se aproximam. Como uma proteção adicional, a técnica também pode liberar uma série de agulhas, a partir selo em forma de hexagrama no topo da barreira, atacando os oponentes.', '法力結界・チャバシラ', 'Hōriki Kekkai: Chabashira', 'Barreira do Poder Budista: Sinal Auspicioso', NULL, NULL, 'Barreira de Dharma: Pilares Celestiais', 'Curto a Médio alcance', NULL, NULL),
(236, 'Bashōsen: Hélice de Fogo', 'Usando o Bashōsen, o usuário desencadeia ondas a partir do leque, provocando um enorme rastro de torrentes de chamas intensas, que vão na direção de seu alvo pretendido.', '芭蕉扇・火の巻ばしょうせん・ひのまき', 'Bashōsen: Hi no Maki', 'Leque do Palmo de Banana: Hélice de Fogo', 'Pergaminho do Fogo Bashosen', 'Bashōsen Abanar o Fogo', NULL, 'Médio alcance', NULL, NULL),
(237, 'Bashōsen: Hélice de Relâmpago', 'Acenando o Bashōsen para baixo, o usuário faz com que um relâmpago apareça em torno dos adversários.', '芭蕉扇・雷の巻ばしょうせん・かみなりのまき', 'Bashōsen: Kaminari no Maki', 'Leque do Palmo de Banana: Hélice de Relâmpago', 'Pergaminho do Relâmpago Bashosen', NULL, NULL, 'Todos os alcances', NULL, NULL);