INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(1207, 'Liberação de Lava: Técnica de Congelamento de Cal', 'Essa técnica faz uso da kekkei genkai de Liberação de Lava, onde o usuário expele uma grande quantidade de cal viva de sua boca. Esta habilidade também pode ser manipulada de modo para restringir os movimentos do alvo pretendido, tal como depois da explosão inicial, os restos da substância podem atuar de um modo semelhante ao cimento de secagem rápida. No entanto, a técnica requer água para desencadear a reação necessária para fazer a substância endurecer. Isto pode ser facilmente conseguido através da utilização de uma técnica de Liberação de Água, que também pode servir para aumentar a área coberta pela substância, aumentando ainda mais a gama potencial da técnica.', '熔遁・石灰凝の術ようとん・ようかいのじゅつ', 'Yōton: Sekkaigyō no Jutsu', 'Liberação de Lava: Técnica de Congelamento de Cal', NULL, 'Estilo da Lava: Técnica da Calcificação', 'Estilo Corrosão: Jutsu de Congelamento de Cal', 'Médio alcance', NULL, 'Coelho → Boi → Cao → Cobra'),
(1208, 'Liberação de Lava: Técnica do Selo da Pedra de Cinzas', 'O usuário envolve seu alvo com uma pedra de cinzas circundante, incapacitando bastante os seus movimentos. Aparentemente, concentrando-se em seu destino, o usuário pode melhorar as propriedades de ligação da técnica.', '熔遁・灰石封の術ようとん・かいせきふうのじゅつ', 'Yōton: Kaisekifū no Jutsu', 'Liberação de Lava: Técnica do Selo da Pedra de Cinzas', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Expelir: Tigre,Restringir: Rato'),
(1209, 'Liberação de Madeira: Aceno Adamantino da Árvore', 'Hashirama usa essa técnica para fazer algumas gavinhas grossas de madeira cresçam ao seu redor. Isso pode ser usado para se defender contra ataques de curta distância ou para atacar um oponente quando eles estão perto.', '木遁・金剛招木', 'Mokuton: Kongō Shōboku', 'Liberação de Madeira: Aceno Vajra da Árvore', 'Estilo Madeira: Chamado da Floresta Adamantina', NULL, NULL, 'Curto alcance', NULL, NULL),
(1210, 'Liberação de Madeira: Advento de um Mundo de Árvores Florescentes', 'Aproveitando-se das propriedades inatas da Liberação de Madeira que permite ao usuário forçar árvores a crescer em qualquer superfície em um instante, o usuário cria uma densa floresta de árvores floridas. Durante a formação da floresta, o usuário pode usar o ramo para conter qualquer alvo(s) que tentam atacar o usuário antes da formação da floresta estar completa. O pólen produzido por estas flores é então liberado para a atmosfera e, quando inalado, torna qualquer alvo atingido inconsciente. O usuário também pode controlar onde o pólen se espalha de modo que não afete-o, nem os seus companheiros. O pólen também é capaz de penetrar as defesas do Susanoo.', '木遁・花樹界降臨もくとん・かじゅかいこうりん', 'Mokuton: Kajukai Kōrin', 'Liberação de Madeira: Advento de um Mundo de Árvores Florescentes', NULL, 'Estilo da Madeira: Advento da Floresta Florida', NULL, 'Todos os alcances', 'Rank –', 'Cobra'),
(1211, 'Liberação de Madeira: Anel de Espinhos de Madeira', 'O usuário cria um semicírculo de madeira em torno de um alvo capturado, e então forma picos dentro do anel, apontando para o alvo, impedindo-o de fazer qualquer movimento brusco.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1212, 'Liberação de Madeira: Barreira Vegetal', 'Um ninjutsu desconhecido utilizado por Moegi em Boruto: Naruto Next Generations.', '木遁・草木結界', 'Mokuton: Sōmoku Kekkai', 'Liberação de Madeira: Barreira Vegetal', NULL, NULL, NULL, NULL, NULL, NULL),
(1213, 'Liberação de Madeira: Bloqueio da Parede de Madeira', 'Um movimento extremamente versátil, esta técnica usa chakra para ativar as raízes no chão e, usando o usuário da técnica como um ponto de referência, fazendo pilares de madeira aparecerem da esquerda e da direita. Os pilares de madeira une-se em uma forma de cúpula com o usuário no centro. Este processo pode ser usado para capturar o inimigo em um ataque de dupla face. Além disso, no caso de um confronto com o inimigo a uma curta distância, o inimigo pode ser trancado dentro da cúpula, definindo a situação para uma luta um-contra-um. Esta cúpula também pode entrar em colapso ou ser queimada com o inimigo dentro para infligir danos. Além disso, ela funciona como um abrigo de fácil contra ataques de longa distância do inimigo, como shuriken e kunai.', '木遁・木錠壁', 'Mokuton: Mokujōheki', 'Liberação de Madeira: Bloqueio da Parede de Madeira', NULL, NULL, 'Estilo Madeira: Jutsu Domo de Madeira', 'Curto alcance', 'Rank –', 'Rato → Cao → Tigre'),
(1214, 'Liberação de Madeira: Cabeças de Cães', 'Uma técnica concebida para ser utilizada para absorver chakra dos Bijū. O usuário produz o kanji \'sentar\' (座, suwaru) em sua palma da mão e cria uma série de pilares de madeira, que são gravados com o que se assemelham a rostos de cães de foo e têm pavios acesos em cima deles. Quando o usuário bate a palma da mão no chão, as estátuas de madeira percorrem em direção ao alvo e absorvem seu chakra.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1215, 'Liberação de Madeira: Captura Completa', 'Yamato aprisiona seu oponente em uma cúpula de madeira, e cria várias vigas de madeira que a compele, destruindo a cúpula e atingindo o oponente várias vezes simultaneamente.', '木遁・完拿', 'Mokuton: Kanna', 'Liberação de Madeira: Captura Completa', 'Estilo Madeira: Captura Total', NULL, NULL, 'Curto alcance', NULL, NULL),
(1216, 'Liberação de Madeira: Desintegração da Floresta', 'O usuário emerge árvores a partir do chão, que crescem rapidamente sob o oponente, apunhalando-o e jogando-o para longe. No entanto, enquanto no Modo Sábio, Hashirama inicia a combinação com um soco brutal e finaliza da mesma forma, mas sem encostar no chão.', '木遁・樹林大崩もくとん・じゅりんたいほう', 'Mokuton: Jurin Taihō', 'Liberação de Madeira: Desintegração da Floresta', 'Liberação de Madeira: Destruição da Floresta', NULL, NULL, 'Curto alcance', NULL, 'Cobra'),
(1217, 'Liberação de Madeira: Enterro Glorioso pela Contenção na Árvore', 'Depois de preparar o terreno amassando-o e umedecendo-o, o usuário invoca uma árvore em torno de seu oponente a fim de prendê-lo enquanto ele afunda na lama criada. Depois disso, com o inimigo entre as raízes da árvore, o usuário é capaz de esmagá-lo.', '木遁・樹縛栄葬', 'Mokuton: Jubaku Eisō', 'Liberação de Madeira: Enterro Glorioso pela Contenção na Árvore', NULL, NULL, 'Estilo Madeira: Enterro do Ligamento da Árvore', 'Curto a Médio alcance', NULL, 'Cobra → Rato → Dragao → Cão → Tigre'),
(1218, 'Liberação de Madeira: Escudo de Madeira', 'A fim de auxilia-lo em batalhes de curto alcance, Yamato usa sua Liberação de Madeira para criar um escudo sobre sua mão. O escudo mostrou-se resistente o suficiente para defletir ataques de armas perfuro-cortantes sem que sua integridade seja comprometida.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1219, 'Liberação de Madeira: Lança da Grande Árvore', 'Obito levanta várias grossas raízes do solo, e as usa para esmagar seus adversários.', '木遁・大槍樹', 'Mokuton: Daisōju', 'Liberação de Madeira: Lança da Grande Árvore', 'Estilo Madeira: Lança da Grande Árvore', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1220, 'Liberação de Madeira: Manipulação da Árvore Divina', 'Trata-se de uma técnica de Liberação de Madeira utilizada para manipular o tronco e as raízes da Árvore Divina, permitindo que seu usuário molde-a de acordo com a sua necessidade.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1221, 'Liberação de Madeira: Martelo de Madeira', 'Yamato cria vários ramos de madeira que emergem do chão, dando forma a um martelo. Em seguida, o oponente é brutalmente apanhado por este.', '木遁・木槌', 'Mokuton: Kiduchi', 'Liberação de Madeira: Martelo de Madeira', 'Estilo Madeira: Marreta Amadeirada', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1222, 'Liberação de Madeira: Nascimento Turbulento da Grande Árvore', 'Hashirama emerge pequenas raízes do chão e as usa como trampolim, voando em direção ao oponente. Logo após, Hashirama pousa com um soco no oponente e bate-o no chão, criando pequenas árvores desordenadas que crescem a partir do inimigo e lançam-o para longe.', '木遁・大樹轟誕', 'Mokuton: Taiju Gōtan', 'Liberação de Madeira: Nascimento Turbulento da Grande Árvore', 'Estilo Madeira: Grande Árvore', NULL, NULL, 'Curto alcance', NULL, NULL),
(1223, 'Liberação de Madeira: Natividade de um Mar de Árvores', 'Depois de formar os selos necessários, Yamato é capaz de transformar uma área em uma vasta floresta em questão de momentos com árvores completas e até mesmo a grama cresce em qualquer tipo de terreno que ele esteja. Esta técnica é muito semelhante a técnica de assinatura de Hashirama Senju, embora numa escala ligeiramente menor.', '木遁・樹海降誕もくとん・じゅかいこうたん', 'Mokuton: Jukai Kōtan', 'Liberação de Madeira: Natividade de um Mar de Árvores', 'Jutsu Oculto do Estilo Madeira: Surgimento da Floresta Escura', NULL, 'Estilo Madeira: Surgimento da Floresta Escura', 'Curto a Médio alcance', NULL, 'Cobra'),
(1224, 'Liberação de Madeira: Natividade de Árvores', 'Yamato emerge sua Liberação de Madeira de cima e golpeia o oponente com grande impacto.', '木遁・樹木降誕', 'Mokuton: Jumoku Kōtan', 'Liberação de Madeira: Natividade de Árvores', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1225, 'Liberação de Madeira: Parede do Mundo de Árvores', 'O usuário cria inúmeras raízes de madeira que crescem em alta velocidade a partir do solo. As raízes se entrelaçam para formar uma grande parede, com um impressionante poder defensivo. Yamato também foi capaz de manipular a forma do crescimento das raízes, formando um domo.', '木遁・樹界壁', 'Mokuton: Jukai Heki', 'Liberação de Madeira: Mundo da Parede de Árvores', 'Estilo Madeira: Parede de Madeira', NULL, 'Estilo Madeira: Barreira da Parede de Árvore', 'Curto alcance', NULL, 'Passaro → Coelho → Cobra'),
(1226, 'Liberação de Madeira: Pilar Esmagador', 'Yamato cria um clone de madeira, e em seguida aprisiona seu oponente em uma prisão de madeira. Logo, ele transforma seu clone em um pilar gigante que, por fim, esmaga o oponente.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1227, 'Liberação de Madeira: Prego Cipreste', 'Yamato empala o oponente com a Liberação de Madeira: Técnica da Grande Floresta e martela a madeira com um martelo de madeira gigante que esmaga o oponente em pedaços.', '木遁・椹釘', 'Mokuton: Sawara Kugi', 'Liberação de Madeira: Prego Cipreste', 'Estilo Madeira: Prego Cipreste', NULL, NULL, NULL, NULL, NULL),
(1228, 'Liberação de Madeira: Prisão Medieval', 'Yamato usa uma prisão de madeira para suprimir o chakra dos alvos com sua Liberação de Madeira. Há também dois apêndices semelhantes a velas em cada extremidade do dispositivo semelhante à técnica usada durante o treinamento de Naruto.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Cobra'),
(1229, 'Liberação de Madeira: Revolta do Inferno', 'Obito perfura seu oponente com suas estacas de madeira, desfere um soco em suas costas, seguido de um chute que o manda para o ar. Ainda no ar, Obito salta em direção ao seu adversário e lhe dá uma joelhada, atravessando seu corpo em seguida, e finaliza convocando enormes raízes subterrâneas que o esmagam.', '木遁・地獄ノ乱もくとん・じごくのらん', 'Mokuton: Jigoku no Ran', 'Liberação de Madeira: Revolta do Inferno', 'Estilo Madeira: Caos Infernal', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1230, 'Liberação de Madeira: Toda a Criação', 'Hashirama provoca múltiplas gavinhas de madeira para levantar-se do chão, tecendo em uma parede de madeira em torno dos membros dos oponentes, vinculando-os no lugar. Ele então concentra seu chakra em seu punho e depois ataca o adversário, quebrando-os livre da parede de madeira tecida.', '木遁・森羅万象もくとん・しんらばんしょう', 'Mokuton: Shinra Banshō', 'Liberação de Madeira: Todas as Florestas de Cobertura e Dez Mil Coisas', 'Estilo Madeira: Universo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1231, 'Liberação de Madeira: Todos Cavacos do Céu', 'Hashirama bate seu punho contra o chão, criando duas vigas de madeira que lançam o oponente para o ar. Em seguida, Hashirama se posiciona abaixo do oponente e, enquanto o último cai, o Hokage soca brutalmente o adversário nas costas, causando grande impacto.', '木遁・一天木端もくとん・いってんこば', 'Mokuton: Itten Koba', 'Liberação de Madeira: Todos Cavacos do Céu', 'Estilo Madeira: Todas as Lascas', NULL, NULL, 'Curto alcance', NULL, NULL),
(1232, 'Liberação de Madeira: Transformação', 'Yamato realiza os selos de mão necessários, e em seguida, surge várias vigas de madeira que cobre todo o seu corpo com uma nuvem de fumaça ou chakra, revelando a transformação. Uma vez que a técnica é feita, a transformação é realmente perfeita. Ela atua mais como uma fantasia do que uma transformação, como ela é capaz de deixar isso para trás, se necessário. Ela também demonstra a capacidade de ajustar a sua voz.', '木遁・変化', 'Mokuton: Henge', 'Liberação de Madeira: Transformação', NULL, NULL, 'Transformação Estilo Madeira', 'Curto alcance', NULL, 'Passaro → Cao → Cobra'),
(1233, 'Liberação de Madeira: Técnica Cortante', 'Uma técnica versátil, onde o usuário pode produzir estacas afiadas de madeira de seu corpo para tanto moldar armas de curto alcance semelhantes a uma espada improvisada, ou então usá-las como projéteis de longo alcance, onde o crescimento dos ramos podem ser acionados remotamente, espetando o inimigo como um resultado.', '木遁・挿し木の術もくとん・さしきのじゅつ', 'Mokuton: Sashiki no Jutsu', 'Liberação de Madeira: Técnica de Estaquia', 'Estilo Madeira: Lança de Carregamento', 'Estilo da Madeira: Técnica das Estacas', 'Estilo Madeira: Técnica do Ramo Perfurante', 'Todos os alcances', 'Rank –', 'Carneiro'),
(1234, 'Liberação de Madeira: Técnica Hōbi', 'Uma ténica de vedação utilizada por Hashirama Senju, que consiste em criar uma defesa capaz de neutralizar ataques poderosos, como uma Bola da Besta com Cauda.', '木遁・榜排の術', 'Mokuton: Hōbi no Jutsu', 'Liberação de Madeira: Técnica Hōbi', NULL, 'Estilo da Madeira: Técnica da Rejeição', NULL, 'Curto alcance', NULL, NULL),
(1235, 'Liberação de Madeira: Técnica Hotei', 'Depois de realizar os selos de mão necessários, o usuário cria uma série de mãos de madeira gigantes que explodem para cima de debaixo da terra, que circundam e, em seguida, tentam conter um inimigo escolhido. O tamanho de cada uma destas mãos é em torno do tamanho de uma besta com cauda. Hashirama é mostrado sendo capaz de manifestar duas mãos apenas com força de vontade, sem o uso de selos de mão.', '木遁・皆布袋の術もくとん・ほうていのじゅつ', 'Mokuton: Hotei no Jutsu', 'Liberação de Madeira: Técnica do Saco de Pano', 'Jutsu do Monge Risonho', 'Estilo da Madeira: Técnica do Deus da Sorte', NULL, 'Médio a Longo alcance', 'Rank –', 'Juntar as Maos → Selo especifico da técnica'),
(1236, 'Liberação de Madeira: Técnica da Casa de Quatro Pilares', 'Ao enviar chakra no solo, as raízes das plantas são convertidas em matérias-primas. Esta técnica permite que um edifício seja construído, forçando o crescimento acelerado. Assim como com o Liberação de Madeira: Técnica da Prisão de Quatro Pilares, o tamanho e a forma do edifício são tomados a partir da imaginação do usuário ou memórias. Ao colocar marcas ao redor da casa, o usuário cria uma barreira, tornando impossível para o inimigo detectá-lo e permitindo que o usuário \'acampe\' em paz.', '木遁・四柱家の術もくとん・しちゅかのじゅつ', 'Mokuton: Shichūka no Jutsu', 'Liberação de Madeira: Técnica da Casa de Quatro Pilares', NULL, NULL, 'Estilo Madeira: Jutsu Casa de Quatro Pilares', 'Curto alcance', 'Rank –', 'Cobra → Rato → Boi → Carneiro → Cobra'),
(1237, 'Liberação de Madeira: Técnica da Grande Floresta', 'Uma técnica que muda o próprio braço em grandes árvores. Usando chakra, os tecidos se transformam em árvores no nível celular. Em seguida, ativando as árvores, estimulando-as a crescer rapidamente, elas alongam em alta velocidade e forquilham em vários ramos. É possível capturar o inimigo com esses ramos e, ao mesmo tempo, se a pessoa muda as extremidades em estacas afiadas, transformá-as em incontáveis armas afiadas perfurantes semelhantes a lança.', '木遁・大樹林の術もくとん・だいじゅりんおじゅつ', 'Mokuton: Daijurin no Jutsu', 'Liberação de Madeira: Técnica da Grande Floresta', NULL, NULL, 'Jutsu da Floresta Gigante', 'Todos os alcances', 'Rank –', 'Tigre → Cavalo → Tigre → Cobra'),
(1238, 'Liberação de Madeira: Técnica da Ligação Sufocante', 'Ao transformar seu braço em uma árvore, o usuário pode criar vários ramos, que vai crescer rapidamente em torno do alvo, imobilizando-o. Esta técnica é forte o suficiente para conter uma Lula Gigante.', '木遁・黙殺縛りの術もくとん・もくさつしばりのじゅつ', 'Mokuton: Mokusatsu Shibari no Jutsu', 'Liberação de Madeira: Técnica da Encadernação Sufocante', NULL, 'Estilo da Madeira: Técnica do Laço Estrangulador', NULL, 'Curto a Médio alcance', NULL, 'Cobra'),
(1239, 'Liberação de Madeira: Técnica da Ponte de Dois Pilares', 'Yamato ergue duas árvores a partir do solo e cria uma ponte de madeira entre elas. Se necessário, o usuário pode estender o comprimento da ponte executando o selo cobra.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, 'Ativar: Javali → Rato → Cachorro → Tigre → Cobra[1],Alongar: Cobra'),
(1240, 'Liberação de Madeira: Técnica da Prisão de Pilares Circulares', 'Yamato cria pilares de madeira em uma formação circular para prender e esmagar o oponente.', '木遁・周柱牢の術', 'Mokuton: Shūchūrō no Jutsu', 'Liberação de Madeira: Técnica da Prisão de Pilares Circulares', 'Liberação de Madeira: Técnica de Prisão', NULL, NULL, NULL, NULL, NULL),
(1241, 'Liberação de Madeira: Técnica da Prisão de Quatro Pilares', 'O usuário converte chakra em madeira sob a terra e o faz crescer rapidamente, enquanto forma-o em uma prisão de madeira. O tamanho da prisão baseia-se na imaginação do utilizador. Desde uma gaiola de inseto até uma grande prisão, pode-se transformá-la em tudo o que quiser, regulando a quantidade de chakra usado. A madeira propriamente dita é tratada com um revestimento de chakra, tornando extremamente difícil a sua destruição. Durante a Guerra, ela tem a grande capacidade de deter prisioneiros, mas ela também pode ser usada como uma sanção para discutir com subordinados.', '木遁・四柱牢の術', 'Mokuton: Shichūrō no Jutsu', 'Liberação de Madeira: Técnica da Prisão de Quatro Pilares', NULL, NULL, 'Prisão de Quatro Colunas', 'Curto alcance', 'Rank –', 'Carneiro → Boi → Javali → Cobra'),
(1242, 'Liberação de Madeira: Técnica das Casas de Pilar em Série', 'Uma versão em maior escala da Liberação de Madeira: Técnica da Casa de Quatro Pilares, onde em vez de criar uma única casa, Yamato cria várias casas de uma só vez. Devido ao grande volume de estruturas criadas com esta técnica, parece esgotá-lo rapidamente.', '木遁・連柱家の術', 'Mokuton: Renchūka no Jutsu', 'Liberação: Técnica das Casas de Pilar em Série', NULL, 'Estilo Madeira: Técnica dos Pilares em Série', 'Estilo Madeira: Jutsu da Fileira de Casas', NULL, NULL, 'Cao → Carneiro → Cobra'),
(1243, 'Liberação de Madeira: Técnica do Dragão de Madeira', 'O usuário cria um dragão de madeira gigante, que ele pode usar para uma variedade de propósitos, incluindo restringir um alvo, ou para fins ofensivos. O dragão que o usuário cria pode variar em termos de aparência. Embora em ambos os casos, criou um dragão serpentino, dragão chinês, sendo que o primeiro tinha um nariz como tronco curto, enquanto os outros dois mais tarde criados não. Ele também tem a capacidade de absorver chakra do seu alvo, lentamente, tornando-o incapaz de usar técnicas. No anime, Hashirama foi capaz de fazer com que o dragão detonasse em seu alvo, resultando em uma grande explosão.', '木遁・木龍の術もくとん・もくりゅうのじゅつ', 'Mokuton: Mokuryū no Jutsu', 'Liberação de Madeira: Técnica do Dragão de Madeira', 'Estilo Madeira: Jutsu do Dragão de Madeira', 'Estilo da Madeira: Técnica do Dragão de Madeira', NULL, 'Todos os alcances', 'Rank –', 'Cobra → Carneiro → Coelho → Cao → Selo especifico da técnica ou Cobra'),
(1244, 'Liberação de Madeira: Técnica do Humano de Madeira', 'Depois de formar os selos de mão necessários, o usuário cria uma criatura enorme como estátua de madeira. Esta criatura humanoide com um corpo cheio e uma face semelhante a oni é geralmente criado com um dragão de madeira envolto em torno de seu torso, e é geralmente usada em batalha como um avatar. Esta criatura é poderosa o suficiente para pegar uma Bola da Besta com Cauda descompactada disparada pelo Nove-Caudas e confrontar contra os semelhantes do Susanoo perfeito de Madara Uchiha. Se o usuário não tem tempo suficiente para manifestar todo o corpo, ele pode manifestar primeiro um membro ou dois, a ganhar tempo suficiente para o corpo inteiro se formar.', '木遁・木人の術もくとん・もくじんのじゅつ', 'Mokuton: Mokujin no Jutsu', 'Liberação de Madeira: Técnica Humano de Madeira', 'Estilo Madeira: Jutsu do Homem de Madeira', 'Estilo da Madeira: Técnica do Homem de Madeira', NULL, 'Curto a Médio alcance', 'Rank –', 'Cobra'),
(1245, 'Liberação de Madeira: Técnica do Verdor', 'Um ninjutsu desconhecido utilizado por Moegi em Boruto: Naruto Next Generations.', '木遁・緑色の術', 'Mokuton: Ryokushoku no Jutsu', 'Liberação de Madeira: Técnica do Verdor', NULL, NULL, NULL, NULL, NULL, NULL),
(1246, 'Liberação de Madeira: Técnica dos Grilhões de Madeira', 'Yamato cria um pilar de madeira em espiral que faz o oponente ser levantado em alta velocidade, e em seguida, ele ataca e causa vários danos ao oponente, além de jogá-lo a vários pés de distância.', '木遁・木桎梏の術', 'Mokuton: Kishikkoku no Jutsu', 'Liberação de Madeira: Técnica dos Grilhões de Madeira', 'Estilo Madeira: Jutsu dos Grilhões de Madeira', NULL, NULL, 'Curto alcance', NULL, NULL),
(1247, 'Liberação de Madeira: Vigas', 'Yamato cria várias vigas de madeira que crescem a partir do solo e atingem o oponente.', '木遁・梁木', 'Mokuton: Yanagi', 'Liberação de Madeira: Vigas', 'Liberação de Madeira: Colunas de Árvores', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1248, 'Liberação de Madeira: Árvore Voadora', 'Primeiramente, Yamato corre até o inimigo e chuta-o, lançando-o rapidamente para o alto, assim ele toma vantagem da inconsciência do inimigo e invoca uma imensa coluna de madeira, em seguida, a coluna acerta o inimigo. E finalmente, ele realiza um selo e salta na frente da coluna, e assim ele revela um feixe fino e extenso em sua mão, logo o feixe atinge o inimigo e o força contra a coluna, e o feixe consegue estilhaçar a coluna e acerta o inimigo até esmagá-lo no chão.', '木遁・飛乃木もくとん・ひのき', 'Mokuton: Hinoki', 'Liberação de Madeira: Árvore Voadora', 'Estilo Madeira: Árvore Voadora', NULL, NULL, 'Curto alcance', NULL, NULL),
(1249, 'Liberação de Magnetismo: Foco da Abelha das Lâminas Gêmeas', NULL, '磁遁・省蜂の双刃じとん・じゃくほうのそうじん', 'Jiton: Jakuhō no Sōjin', 'Liberação de Magnetismo: Foco da Abelha das Lâminas Gêmeas', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1250, 'Liberação de Magnetismo: Funeral Imperial de Pó de Ouro', 'Rasa faz com que uma quantidade considerável de Pó de Ouro para entrar em erupção debaixo da superfície, colocando ambas as palmas das mãos no chão em frente a ele, antes de manipular a substância, resultando em uma onda de proporções das marés. A quantidade de pó produzido por esta habilidade é comparável à da areia utilizada durante o Fluxo da Cachoeira de Areia Movediça de Gaara, mas como o ouro é o mais pesado dos dois, o primeiro era capaz de anular o último quando colidiram.', '磁遁・砂金大葬じとん・さきんたいそう', 'Jiton: Sakin Taisō', 'Liberação de Magnetismo: Funeral Imperial de Pó de Ouro', 'Fluxo da Cachoeira de Pó de Ouro (流砂金瀑流, Ryūsakin Bakuryū)', NULL, NULL, 'Médio a Longo alcance', 'Rank –', 'Carneiro'),
(1251, 'Liberação de Magnetismo: Grande Funeral de Pó de Ouro', 'Rasa convoca seu pó de ouro no entorno do oponente e aprisiona-o sob uma duna espiral feita do material e, após formar o selo de mão necessário, a duna é comprimida, esmagando quem está dentro.', '磁遁・砂金大葬送じとん・さきんたいそうそう', 'Jiton: Sakin Taisōsō', 'Liberação de Magnetismo: Grande Funeral de Pó de Ouro', 'Estilo Magnético: Remessa do Funeral Imperial de Pó de Ouro', NULL, NULL, 'Longo alcance', NULL, 'Esmagar: Selo desconhecido[Nota 1]'),
(1252, 'Liberação de Magnetismo: Técnica do Deus do Relâmpago', 'Usando seu chakra magnético, o usuário absorve massa metálica em torno de si, expandindo a sua aparência escalar e adquirindo a forma de um gigante. Resumidamente, trata-se de uma técnica que cria uma imensa forma humanoide de Areia de Ferro.', '磁遁・雷神我の術じとん・らいじんがのじゅつ', 'Jiton: Raijinga no Jutsu', 'Liberação de Magnetismo: Técnica do Deus do Relâmpago', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1253, 'Liberação de Poeira: Técnica de Divisão do Desprendimento do Mundo Primitivo', 'Mū divide-se, com uma de suas metades jogando seu oponente no ar, desaparecendo de vista em seguida. Essa metade reaparece depois, e as duas disparam uma Liberação de Poeira, pegando o oponente entre suas técnicas.', '塵遁・分裂原界剥離の術', 'Jinton: Bunretsu Genkai Hakuri no Jutsu', 'Liberação de Poeira: Técnica de Divisão do Desprendimento do Mundo Primitivo', 'Estilo Partícula: Jutsu de Desmontagem da Fissão Atômica, Estilo Partícula: Jutsu de Fissão da Decomposição Atômica', NULL, NULL, 'Curto a Médio alcance', NULL, 'Cobra → Dragao → Cão'),
(1254, 'Liberação de Poeira: Técnica do Desprendimento do Mundo Primitivo', 'O usuário aplica a Liberação de Poeira para criar uma estrutura com uma esfera localizada em seu centro entre as palmas das suas mãos. A estrutura se expande rapidamente em tamanho quando é impulsionada em direção ao seu alvo, o prendendo. A esfera, em seguida, parece explodir com uma tremenda quantidade de força, enquanto as paredes exteriores da estrutura conseguem restringir o tamanho do raio da explosão, resultando na destruição de tudo o que estiver dentro dele à medida que são pulverizados em partículas diminutas de pó. A técnica não se limita a apenas afetar a matéria física, mas também pode afetar as defesas de chakra.', '塵遁・原界剥離の術じんとん・げんかいはくりのじゅつ', 'Jinton: Genkai Hakuri no Jutsu', 'Liberação de Poeira: Técnica do Desprendimento do Mundo Primitivo', NULL, 'Estilo do Pó: Técnica do Desprendimento Atômico', 'Estilo Partícula: Jutsu de Deslocamento Atômico', 'Todos os alcances', 'Rank –', 'Rato → Urso → Cobra'),
(1255, 'Liberação de Porco: Fogos de Artifícios Enlouquecidos', 'Shizune rodeia seu oponente com sua névoa venenosa, e em seguida, salta para o alto, atirando várias senbon que perfuram-o. Para finalizar, Tonton finaliza o oponente lançando oito grandes bombas no mesmo, que está atualmente preso na névoa venenosa. Depois de jogar as oito bombas, ocorre uma enorme explosão que devasta a área onde o oponente estava.', '豚遁・乱れ花火とんとん・みだれはなび', 'Tonton: Midare Hanabi', 'Liberação de Porco: Fogos de Artifícios Enlouquecidos', 'Estilo Porco: Loucos Fogos de Artifícios', NULL, NULL, 'Longo alcance', NULL, NULL),
(1256, 'Liberação de Porco: Tonton', 'Tonton é jogado no oponente por Shizune, que, em seguida, ataca constantemente o oponente em várias direções. Tonton então gira ferozmente para cima sob o seu oponente, deixando-o no ar, que faz com que um acidente uma vez que o oponente bater no chão.', '豚遁・トントン', 'Tonton: Tonton', 'Liberação de Porco: Tonton', 'Estilo Porco: Tonton', NULL, NULL, 'Curto alcance', NULL, NULL),
(1257, 'Liberação de Relâmpago: Alicerce de Relâmpago Ilusório', 'Usando seu corpo como alicerce, C emana uma luz intensa capaz de cegar seus inimigos. Uma vez pego, o inimigo alucina no mesmo instante.', '雷遁・雷幻雷光柱らいとん・らいげんらいこうちゅう', 'Raiton: Raigen Raikōchū', 'Liberação de Relâmpago: Alicerce de Relâmpago Ilusório', NULL, 'Colunas de Raios da Ilusão', 'Ilusão Relâmpago: Flash do Pilar', 'Todos os alcances', NULL, 'Carneiro → Cavalo → Cobra → Tigre'),
(1258, 'Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares', 'Uma versão mais potente da Liberação de Relâmpago: Quatro Pilares Vinculados, na qual o usuário cria dezesseis pilares gigantes, que, em seguida, formam uma estrutura imensa como um forno para interceptar o inimigo. É usada após a Liberação de Terra: Queda da Terra Pegajosa para cobrir o inimigo de lama, e é seguida pela Liberação de Fogo: Técnica da Cerâmica Queimada, criando chamas no interior do forno para assar e endurecer a lama, imobilizando o inimigo.', '雷遁・十六柱しばり', 'Raiton: Jūrokuchū Shibari', 'Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Rato → Macaco → Cavalo → Tigre'),
(1259, 'Liberação de Relâmpago: Assassinato Eletromagnético', 'Esta técnica permite que o usuário crie uma onda de eletricidade a partir de suas mãos. O usuário pode variar seu poder a partir de uma pequena onda de choque em um adversário à uma poderosa corrente de relâmpagos capaz de rasgar através da rocha sólida. Também é utilizada em conjunto com a Liberação de Água.', '雷遁・磁場死', 'Raiton: Jibashi', 'Liberação de Relâmpago: Assassinato Eletromagnético', NULL, NULL, NULL, 'Curto alcance', NULL, 'Javali → Carneiro → Cobra → Cavalo → Dragao'),
(1260, 'Liberação de Relâmpago: Bastão do Relâmpago Pulverizador', 'O usuário canaliza os raios em seu porrete, aumentando a força dos ataques lançados.', '雷遁・雷砕棒', 'Raiton: Kanasaibō', 'Liberação de Relâmpago: Bastão do Relâmpago Pulverizador', NULL, NULL, NULL, 'Curto alcance 0-5m', NULL, NULL),
(1261, 'Liberação de Relâmpago: Chuva de Raios', 'Por intermédio de alguma fonte de raios, seja natural ou artificial, o usuário manipula e libera poderosas descargas elétricas em forma de raios que caem sobre as proximidades, eletrocutando e paralisando aqueles que são atingidos.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1262, 'Liberação de Relâmpago: Descarga Elétrica', 'O usuário acumula seu chakra e o libera na forma de uma potente descarga elétrica que envolve todo o seu corpo por um curto período de tempo, eletrocutando quem faz contato com ele.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Carneiro'),
(1263, 'Liberação de Relâmpago: Duplo Dez Mil Volts', 'Trata-se de uma técnica colaborativa da qual dois usuários com chakra relâmpago lançam suas técnicas em sincronia a fim de eletrocutar o mesmo alvo. O poder combinado das técnicas desencadeia uma corrente elétrica de voltagem ainda mais elevada e poder.', '雷遁・二重万ボルト', 'Raiton: Nijūman Boruto', 'Liberação de Relâmpago: Duplo Dez Mil Volts', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1264, 'Liberação de Relâmpago: Elevação Inversa', 'Darui desfere uma série de ataques com sua Espada Cutelo, infundida com relâmpago, que joga seu oponente para trás, finalizando ao lançar uma rajada de relâmpago através do chão com sua espada, jogando seu adversário no ar.', '雷遁・逆揚げらいとん・ぎゃくあげ', 'Raiton: Gyakuage', 'Liberação de Relâmpago: Elevação Inversa', 'Estilo Relâmpago: Elevação Inversa', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1265, 'Liberação de Relâmpago: Flash do Terremoto Relâmpago', 'O usuário libera uma descarga de relâmpago roxa na palma de sua mão, a qual carrega até seu oponente e desfere um golpe. Esta técnica é semelhante ao Corrente de Chidori, pois ambos têm o efeito de alongamento para efetuar algum golpe.', '雷遁・雷震閃', 'Raiton: Raishinsen', 'Liberação de Relâmpago: Flash do Terremoto Relâmpago', 'Estilo Relâmpago: Flash Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(1266, 'Liberação de Relâmpago: Flecha Trovejante', 'Trata-se de um ninjutsu de relâmpago que é usado para disparar um raio em forma de flecha, eletrocutado seu alvo. Ao combiná-lo com um ninjutsu de água, Boruto obteve maior eficácia no uso.', '雷遁・迅雷箭らいとん・じんらいせん」', 'Raiton: Jinraisen', 'Liberação de Relâmpago: Flecha Trovejante', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Carneiro'),
(1267, 'Liberação de Relâmpago: Gian', 'A máscara de Liberação de Relâmpago de Kakuzu emite feixes de relâmpago a partir de sua boca para eletrocutar e perfurar o inimigo. Um único feixe pode partir rochas ou fragmentá-las por completo. A quantidade também pode ser ajustada, a fim de obter maior sucesso, mas, em compensação, os feixes ficam devagar, ainda que capazes de desferir sérios danos ao inimigo.', '雷遁・偽暗らいとん・ギアン', 'Raiton: Gian', 'Liberação de Relâmpago: Falsa Escuridão', NULL, 'Estilo do Relâmpago: A Desconfiança das Trevas', NULL, 'Todos os alcances', 'Rank B', 'Cobra'),
(1268, 'Liberação de Relâmpago: Ligação do Trovão', 'Esta técnica permite que Kitane crie uma parede de três lados com eletricidade para conter seu adversário. Kitane para criar os pontos de condução, deve ter colocado três varas em cada ponta da barreira, assim criando os pontos. Além disso, uma vez que o inimigo está dentro da barreira, se entrar em contato com a parede, ele irá ser eletrocutado. A única maneira existente de anular esta técnica, é tendo um companheiro de equipe que ataque um dos lados da parede da barreira com um ataque poderoso, assim então, anulando todos os outros lados da barreira.', '雷遁・雷縛りらいとん・かみなりしばり', 'Raiton: Kaminari Shibari', 'Liberação de Relâmpago: Ligação do Trovão', NULL, NULL, NULL, 'Curto alcance', NULL, 'Boi → Cavalo → Dragao'),
(1269, 'Liberação de Relâmpago: Lâmina Elétrica', 'Uma técnica em que o usuário infunde chakra de natureza relâmpago em uma arma de arremesso, como uma kunai ou shuriken, para atordoar seu alvo com um choque ao perfurá-lo. Embora seja considerada uma técnica de baixo nível, é muito versátil, uma vez que a arma pode ser utilizada em combates corpo a corpo ou até mesmo arremessada sem que seus efeitos sejam perdidos.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Kunai: Tigre → Cao[1],Shuriken: Tigre[2]'),
(1270, 'Liberação de Relâmpago: Manipulação da Cobra Elétrica', 'O usuário cria uma descarga elétrica que pode ser lançada em forma de uma cobra ou pode cobrir seu corpo para eletrocutar em quem ele encostar. Essa técnica pode ser potencializada por um ambiente aquático. Uma vez usada num ambiente propenso a uma carga elétrica maior, o usuário também parece ficar vulnerável a receber danos.', '雷遁・蛇御雷らいとん・へびみかずち', 'Raiton: Hebi Mikazuchi', 'Liberação de Relâmpago: Manipulação da Cobra Elétrica', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Bater as maos → Macaco ou (Carneiro espelhado)[1]'),
(1271, 'Liberação de Relâmpago: Mergulho Violento do Relâmpago', 'Darui canaliza chakra de relâmpago através de sua Espada Cutelo e coloca-a no chão, paralisando seu oponente, e em seguida ele ataca seu adversário, enviando-o para longe.', '雷遁・雷激浸らいとん・らいげきしん', 'Raiton: Raigekishin', 'Liberação de Relâmpago: Mergulho Violento do Relâmpago', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1272, 'Liberação de Relâmpago: Onda de Inspiração', 'Esta é uma técnica de fusão (融合術, Yūgōjutsu) onde o usuário emprega Liberação de Água e Liberação de Relâmpago. Depois de criar um fonte de água, ele então forma os selos de Liberação de Relâmpago, misturando assim um relâmpago à água. Se esta técnica atinge um inimigo, ele instantaneamente recebe um choque elétrico.', '雷遁・感激波', 'Raiton: Kangekiha', 'Liberação de Relâmpago: Onda de Inspiração', NULL, 'Estilo do Relâmpago: Onda Chocante', NULL, 'Médio alcance', NULL, 'Coelho → Cao → Javali → Carneiro → Rato'),
(1273, 'Liberação de Relâmpago: Overdrive', 'Mediante ao uso de chakra de natureza relâmpago, o usuário é capaz de aumentar suas sinapses neurais para acelerar a comunicação de seu cérebro com os músculos, aumentando a velocidade de ataque e reação efetivamente. A técnica também concede ao usuário capacidades cortantes com ataques de mãos nuas.', '雷遁・オーバードライブらいとん・おおばあどらいぶ', 'Raiton: Ōbādoraibu', 'Liberação de Relâmpago: Ultra-impulso', NULL, NULL, NULL, NULL, NULL, NULL),
(1274, 'Liberação de Relâmpago: Pantera Negra', 'Depois de gerar um Relâmpago Negro de seu corpo que toma a forma de uma pantera, o usuário pode manipulá-lo para afetar múltiplas vítimas de uma só vez eletrocutando-as. Tal como várias outras técnicas de Liberação de Relâmpago, a potência desta habilidade pode ser aumentada através da aplicação de água.', '雷遁・黒斑差', 'Raiton: Kuropansa', 'Liberação de Relâmpago: Diferença Manchada de Preto', NULL, 'Estilo do Relâmpago: Contraste da Pantera Negra', NULL, 'Curto a Médio alcance', 'Rank B', 'Carneiro'),
(1275, 'Liberação de Relâmpago: Para-Raios', 'Depois de entrar em contato físico com o oponente, o usuário levanta o braço no ar, gerando um raio poderoso de energia elétrica que percorre seu corpo no corpo do oponente.', '雷遁・避雷針', 'Raiton: Hiraishin', 'Liberação de Relâmpago: Para-Raios', NULL, NULL, 'Estilo Relâmpago: Faísca da Agulha Elétrica', 'Curto alcance', NULL, NULL),
(1276, 'Liberação de Relâmpago: Portão de Trovão', 'Usando as Kiba, o usuário primeiro enterra as espadas no chão e, em seguida, envia uma carga elétrica para o céu que retorna como um grande relâmpago, dizimando a área imediatamente ao redor do usuário, queimando tudo e transformando em cinzas.', '雷遁・雷門', 'Raiton: Sandāgēto', 'Liberação de Relâmpago: Portão de Trovão', NULL, NULL, 'Estilo Relâmpago: Portão de Trovão', 'Todos os alcances', NULL, NULL),
(1277, 'Liberação de Relâmpago: Presa de Relâmpago', 'Usando Kiba, o usuário envia raios de luz para eletrocutar seus inimigos.', '雷遁・雷牙', 'Raiton: Raiga', 'Liberação de Relâmpago: Presa de Relâmpago', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1278, 'Liberação de Relâmpago: Punho Trovejante', 'Concentrando relâmpago em seu punho, o usuário desfere uma descarga elétrica no alvo.', '雷遁・迅雷拳', 'Raiton: Jinraiken', 'Liberação de Relâmpago: Punho Trovejante', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1279, 'Liberação de Relâmpago: Quatro Pilares Vinculados', 'O usuário invoca quatro grande pilares de pedra em torno do inimigo para, em seguida, eletrocutá-lo, de modo a desestabilizar o inimigo por um longo período de tempo.', '雷遁・四柱しばりらいとん・しちゅうしばり', 'Raiton: Shichū Shibari', 'Liberação de Relâmpago: Quatro Pilares Vinculados', NULL, 'Estilo do Relâmpago: Prisão dos Quatro Pilares', 'Estilo Relâmpago: Armadilha dos Quatro Pilares', 'Todos os alcances', NULL, 'Carneiro → Javali → Cao → Passaro → Lebre'),
(1280, 'Liberação de Relâmpago: Raio', 'O usuário estende ambos os braços, liberando uma alta descarga através de ambas as palmas das mãos, tendo como alvo vários adversários ao mesmo tempo. É possível aumentar a potência utilizando esta técnica, em combinação com uma fonte de água.', '雷遁・サンダーボルト', 'Raiton: Sandāboruto', 'Liberação de Relâmpago: Raio', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Tigre → Cobra → Tigre → Passaro → Tigre → Carneiro → Tigre'),
(1281, 'Liberação de Relâmpago: Rastreamento de Presa da Besta Relâmpago', 'Ao aplicar a transformação de forma em seu Corte Relâmpago, Kakashi consegue moldar um lobo feito de relâmpago que persegue os seus alvos.', '雷遁・雷獣追牙らいとん・らいじゅうついが', 'Raiton: Raijū Tsuiga', 'Liberação de Relâmpago: Rastreamento de Presa da Besta Relâmpago', 'Técnica da Besta Corredora Relâmpago', NULL, NULL, 'Todos os alcances', 'Rank C', NULL),
(1282, 'Liberação de Relâmpago: Relâmpago Explosivo', 'Usando as espadas Kiba, o usuário cobre seu corpo com uma poderosa carga elétrica capaz de eletrocutar o oponente com um mero toque. Uma vez eletrificado o usuário pode disparar a carga elétrica de qualquer parte de seu corpo.', '雷遁・爆雷らいとん・ばくらい', 'Raiton: Bakurai', 'Liberação de Relâmpago: Relâmpago Explosivo', NULL, NULL, 'Estilo Relâmpago: Carga Profunda', 'Todos os alcances', NULL, NULL),
(1283, 'Liberação de Relâmpago: Relâmpago Roxo', 'Esta é uma técnica original de Kakashi Hatake, criada para suprir a falta de seu Corte Relâmpago, o qual incapacitou-lhe de utilizar com segurança após perder o Sharingan.', '雷遁・紫電らいとん・しでん', 'Raiton: Shiden', 'Liberação de Relâmpago: Relâmpago Roxo', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1284, 'Liberação de Relâmpago: Sinal de Fogo Relâmpago', 'Depois de formar os selos de mão necessários, o usuário aponta as mãos para uma certa direção e dispara uma descarga elétrica, que então explode em uma forma específica. Quando as mãos são levantadas para o céu, esta técnica pode ser usada como um sinalizador.', '雷遁・電光狼煙', 'Raiton: Denkō Noroshi', 'Liberação de Relâmpago: Sinal de Fogo Relâmpago', NULL, NULL, NULL, 'Longo alcance', NULL, 'Dragao'),
(1285, 'Liberação de Relâmpago: Teia de Aranha', 'Depois de amassar o chakra no corpo e convertê-lo para relâmpago, o usuário coloca sua mão no chão que cria uma onda de energia que se origina dele e se estende por fora em torno do usuário criando uma teia de eletricidade em torno dele. Qualquer um preso dentro desta teia é eletrocutado por efeito do raio.', '雷遁・網蜘蛛', 'Raiton: Amigumo', 'Liberação de Relâmpago: Teia de Aranha', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1286, 'Liberação de Relâmpago: Tesouras de Tigre', 'Semelhante a uma armadilha de urso, esta técnica é, no entanto, muito maior e repleta de relâmpagos. A técnica é capaz de materializar-se a partir do solo e bloquear o alvo com sua lâmina de fluxo de raio.', '雷遁・虎鋏', 'Raiton: Torabasami', 'Liberação de Relâmpago: Tesouras de Tigre', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1287, 'Liberação de Relâmpago: Tiro da Bomba Relâmpago', 'Indra flui chakra através de seu corpo e o converte em eletricidade, então ele o dispara na forma de um projétil em direção ao seu oponente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1288, 'Liberação de Relâmpago: Trovoada', 'Vários usuários se reúnem em torno de um pergaminho com o kanji de relâmpago nele, e recolhem seu chakra. O símbolo começa brilhar, e o chakra é enviado para o céu, as nuvens de tempestade, então desencadeiam um grande raio.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1289, 'Liberação de Relâmpago: Trovão', 'Com o movimento de seu pulso, o usuário é capaz de disparar inúmeras pequenas faíscas de relâmpagos em direção a um alvo.', '雷遁・散雫らいとん・さんだ', 'Raiton: Sanda', 'Liberação de Relâmpago: Trovão', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1290, 'Liberação de Relâmpago: Técnica Clone das Sombras', 'Esta técnica permite ao usuário fazer um clone da sombra infundido com relâmpago. Por ser um clone de sombra, é capaz de executar técnicas e interagir com o ambiente devido a ele ter substância física; e também distribui uniformemente o chakra do usuário para cada clone. Se o clone é ferido, ele voltará ao seu estado de relâmpago natural, ao mesmo tempo eletrocutando tudo o que é tocado.', '雷遁・影分身', 'Raiton: Kage Bunshin no Jutsu', 'Liberação de Relâmpago: Técnica Clone das Sombras', NULL, 'Clone das Sombras do Estilo do Relâmpago', 'Estilo Relâmpago: Jutsu Clone da Sombra', NULL, 'Rank A', NULL),
(1291, 'Liberação de Relâmpago: Técnica da Lâmina Relâmpago', 'É uma técnica original criada por Indra Ōtsutsuki, que envolve a transformação da natureza de Liberação de Relâmpago.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Selo especifico da técnica → Boi → Coelho → Macaco'),
(1292, 'Liberação de Relâmpago: Técnica do Projétil de Relâmpago', 'Depois de amassar chakra no estômago, o usuário o expele sob a forma de uma poderosa corrente de relâmpago até o alvo pretendido.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Cobra → Porco → Macaco[1]'),
(1293, 'Liberação de Tempestade Esotérica: Tempestade do Dragão Demoníaco', 'Hiruko usa a Liberação de Tempestade, adquirida através da Técnica Quimera, para produzir uma enorme nuvem tempestuosa com capacidade de absorção de chakra infinita, que cresce em tamanho, força e ferocidade. A quantidade de chakra recebida também aumenta conforme a nuvem. Aparentemente é possível canalizar a energia absorvida para dentro de si, ampliando o limite da técnica.', '嵐遁奥義・嵐鬼龍らんとんおうぎ・らんきりゅう', 'Ranton Ōgi: Rankiryū', 'Liberação de Tempestade Esotérica: Tempestade do Dragão Demoníaco', NULL, NULL, 'Estilo Vendaval: Dragão do Vendaval', 'Todos os alcances', NULL, 'Rato → Dragao → Coelho → Tigre'),
(1294, 'Liberação de Tempestade: Caçada Negra', 'Empregando o chakra tempestade em seu Relâmpago Negro, a fim de deixar a técnica mais fluida, Darui envia várias panteras negras que são guiadas em direção ao oponente, infligindo dano conforme passam. As panteras, em seguida, mesclam-se em uma única pantera gigante atrás do inimigo, que morde-o, comprime-se e expande-se rapidamente em uma explosão.', '嵐遁・漆黒斑纏遇らんとん・ぶらっく はんちんぐ', 'Ranton: Burakku Hantingu', 'Liberação de Tempestade: Deleite do Jato Envolvido de Mancha Negra', 'Estilo Vendaval: Caçada Negra', NULL, NULL, 'Curto a Médio alcance', NULL, 'Carneiro'),
(1295, 'Liberação de Tempestade: Circo de Laser', 'Esta técnica cria vários feixes e os atira contra o inimigo. Primeiro, uma auréola de energia luminosa circunda as mãos do utilizador, que então são envolvidas por essa mesma luz. Com isso, vigas de energia destrutiva são disparadas contra o inimigo. O usuário é capaz de manipular a direção destes feixes, fazendo-os perseguir os inimigos de forma tão precisa que eles conseguem desviar de até mesmo reféns que estão estreitamente próximos do alvo. Caso deseje, o utilizado também pode aumentar o número de vigas que são disparadas.', '嵐遁・励挫鎖苛素らんとん・れいざあさあかす', 'Ranton: Reizā Sākasu', 'Liberação de Tempestade: Circo de Laser', 'Estilo Tempestade: Circo de Laser', 'Estilo da Tempestade: Laser Circus', 'Estilo Vendaval: Circo de Laser', 'Todos os alcances', NULL, 'Tigre → Cao → Cobra → Dragão'),
(1296, 'Liberação de Tempestade: Onda Interna da Nuvem Tempestuosa', 'Hiruko usa a Liberação de Tempestade para gerar um grosso anel de nuvens tempestuosas carregadas ao seu redor, criando uma proteção ofensiva e defensiva. Ele pode, então, usar essas nuvens para disparar poderosas rajadas de raios em seus inimigos, e como o perímetro é eletrificado; pode prevenir eficazmente os seus inimigos de aproximarem-se.', '嵐遁・雷雲腔波らんとん・らいうんくうは', 'Ranton: Raiunkūha', 'Liberação de Tempestade: Onda Interna da Nuvem Tempestuosa', NULL, NULL, 'Estilo Vendaval: Onda Nula da Nuvem Tempestuosa', 'Curto', NULL, 'Selo especifico da técnica → Javali → Cavalo'),
(1297, 'Liberação de Terra: Abertura de Terra da Nascente de Escavação', 'Com esta técnica, o usuário pode trazer alguma coisa que está no subterrâneo profundo para a superfície, lançando os alvos no ar com grande força e velocidade e resultando na aparência e tamanho semelhantes a um vulcão em erupção. Ele pode forçar um grande número de soldados escondidos no subsolo a emergirem de uma só vez quando usada colaborativamente.', '土遁・開土昇掘', 'Doton: Kaido Shōkutsu', 'Liberação de Terra: Abertura de Terra da Nascente de Escavação', NULL, 'Estilo da Terra: Brotamento de Rochas', NULL, 'Longo alcance', NULL, NULL),
(1298, 'Liberação de Terra: Armadura de Rocha da Aranha Demoníaca', 'Devido à Prescrição de Fortalecimento: Injeção de Chakra, Gitai cria uma versão melhorada de sua Liberação de Terra: Técnica de Endurecimento. Ao contrário da versão anterior, o usuário cria uma armadura de rocha que envolve seu corpo com os olhos demoníacos, alguns braços estendidos e uma cor mais escura. A aparência do utilizador se assemelha a uma aranha de rocha.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1299, 'Liberação de Terra: Bolinho Mausoléu de Terra', 'Com o controle de chakra e força sobre-humana, Jirōbō eleva a própria superfície da Terra. O pedaço de terra é gigantesco e o poder destrutivo é óbvio. Mas até mesmo o impacto visual pode ser o suficiente para fazer inimigos perderem seus espíritos de combate e fugirem. A bola da terra é, então, atirada em seu oponente, achatando tudo em seu caminho.', '土遁・土陵団子どとん・どりょうだんご', 'Doton: Doryō Dango', 'Liberação de Terra: Bolinho Mausoléu de Terra', 'Estilo Terra: Esfera de Sepulturas', 'Estilo da Terra: Biscoito de Terra Sepulcral', NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(1300, 'Liberação de Terra: Caixão de Pedra Basiliar', 'Esta técnica permite ao usuário controlar múltiplas seções de rocha e movê-las em torno de seu oponente. O usuário, então, forma duas seções gigantescas que esmagam o inimigo.', '土遁・岩盤柩', 'Doton: Ganban Kyū', 'Liberação de Terra: Caixão de Pedra Basiliar', NULL, NULL, 'Estilo Terra: Caixão de Pedra', 'Médio a Longo alcance', NULL, 'Selo especifico da técnica'),
(1301, 'Liberação de Terra: Canhão da Montanha de Rocha Eruptiva', 'Jirōbō enquanto em sua forma do selo amaldiçoado nível 2 com a sua força sobre-humana puxa para cima a partir do solo um pedaço gigantesco de terra e joga-o no ar na frente do oponente, onde ele ataca com um soco poderoso quebrando a rocha e fazendo com que os fragmentos bombardeiem o oponente.', '土遁・噴岩砲岱どとん・ふんがんほうたい', 'Doton: Fungan Hōtai', 'Liberação de Terra: Canhão da Montanha de Rocha Eruptiva', 'Estilo Terra: Jorrada da Bola de Canhão da Montanha de Rocha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1302, 'Liberação de Terra: Cascalho', 'Depois de performar o selo de mão necessário, o usuário rapidamente move a terra embaixo do oponente em camadas a fim de circundá-lo, deixando-o completamente imóvel. Conforme a necessidade, o usuário pode executar outro selo de mão a fim de libertar o prisioneiro.', '土遁・細れ石', 'Doton: Sazareishi', 'Liberação de Terra: Cascalho', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Ativar: Selo especifico da técnica,Desativar: Cao'),
(1303, 'Liberação de Terra: Clone das Sombras', 'Esta técnica cria um clone das sombras do usuário feito de barro. Desde que é feito de lama, ele pode continuar a reformar-se e moldar-se de volta à sua forma original. Uma vez revertido para a lama, o clone pode servir como uma restrição poderosa que é capaz de travar completamente os movimentos do oponente.', '土遁・影分身', 'Doton: Kage Bunshin', 'Liberação de Terra: Clone das Sombras', NULL, NULL, 'Clone das Sombras do Estilo de Terra', NULL, NULL, NULL);
