INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(1748, 'Papéis da Resma Explosiva', 'O usuário anexa um papel explosivo em uma kunai e lança como um projétil contra o oponente, fazendo com que o selo exploda no alvo.', '連爆札れんばくふだ', 'Renbaku Fuda', 'Papéis da Resma Explosiva', 'Jutsu da Kunai Bomba', NULL, NULL, NULL, NULL, NULL),
(1749, 'Par de Donzelas', 'Kankurō invoca a Formiga Negra e faz este esgueirar-se por trás do oponente, imobilizando seus braços. Kankurō depois usa Corvo que solta uma bomba de sua boca e, em seguida, corre em direção ao oponente fazendo com que a bomba exploda.', '一対の処女', 'Ittsui no Shojo', 'Par de Donzelas', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1750, 'Parafusos de Flor', 'Sasuke corta o oponente com sua espada, depois disso, ele e Sakura desferem socos ao oponente no ar e, em seguida, chuta-o para o chão.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1751, 'Paraíso da Terra Pura', 'Tsunade soca o chão, criando um tremor que lança seu oponente no ar. Em seguida, ela concentra chakra em seu punho e soca o oponente no estômago com uma força inacreditável, lançando o oponente no ar novamente e causando uma imensa destruição ao redor, devido ao impacto.', '極楽浄土ごくらくじょうど', 'Gokuraku Jōdo', 'Paraíso da Terra Pura', 'Terra Pura do Paraíso', NULL, NULL, 'Curto alcance', NULL, NULL),
(1752, 'Parcela da Legenda', 'Tsunade soca o oponente para Jiraiya, que invoca Gama, como um sapo guerreiro, para atacar o oponente para o chão. Gama então esmaga o oponente ao cair sobre ele.', '伝説の片鱗', 'Densetsu no Henrin', 'Parcela da Legenda', 'Fragmento de uma Lenda', NULL, NULL, 'Curto alcance', NULL, NULL),
(1753, 'Pardais Sequenciais', 'Sai usa o Desenho de Imitação da Super Besta para liberar uma rápida enxurrada de aves de tamanho pequeno. Essa técnica foi forte e rápida o suficiente para colidir com a Senbon de Agulha de Cabelo de Fuguki Suikazan. No anime, Inojin usa essa técnica em menor escala, porém com eficácia suficiente para impedir os movimentos de Chōji ao se defender.', '連燕雀れんえんじゃく', 'Ren\'enjaku', 'Pardais Sequenciais', 'Pardais em Série', NULL, NULL, 'Curto alcance', NULL, NULL),
(1754, 'Parede Dianteira de Areia', 'Gaara cria dois pilares sólidos de areia que ele pode usar para atacar seu oponente, comprimindo-o em meio das paredes de areia.', '砂陣壁', 'Sunajin Kabe', 'Parede Dianteira de Areia', 'Parede de Areia', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1755, 'Parede Protetiva de Areia Aérea', 'Esta técnica de defesa aérea cria um escudo gigante, suspenso no ar. Composto por uma grande quantidade de areia rica em minerais, dando uma grande resistência a tal. Além disso, a areia tem a sua força defensiva aumentada pelo chakra fluindo através dela. Ele era forte o suficiente para proteger Sunagakure do C3 de Deidara e auxiliar Ōnoki a parar um dos meteoros lançados por Madara.', '空砂防壁くうさぼうへき', 'Kūsa Bōheki', 'Parede Protetiva de Areia Aérea', NULL, NULL, NULL, 'Médio a Longo alcance', 'Rank –', NULL),
(1756, 'Parede de Ferro Selada', 'Um selo colocado na porta do Templo do Fogo, a fim de se defender contra os intrusos do mundo exterior. As duas imagens colocadas em ambos os lados da porta formam uma barreira. O selo só pode ser cancelado com o chakra Senzoku no Sai. No entanto, como mostrado quando Kakuzu usou a Liberação de Terra: Domu para atacar o portão, um poderoso ataque físico pode destruir o selo.', '封印鉄壁', 'Fūin Teppeki', 'Parede de Ferro Selada', NULL, NULL, 'Selo da Parede de Aço', NULL, NULL, NULL),
(1757, 'Parede de Liberação de Relâmpago', 'Esta técnica é uma exclusividade dos bandidos e shinobi de Yumegakure que tornam-se capazes de criar uma parede de com chakra relâmpago, que repele qualquer um que toca e provoca vários danos à ele, como a eletrocussão.', '雷遁の壁らいとんのへき', 'Raiton no Heki', 'Parede de Liberação de Relâmpago', 'Parede do Estilo Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(1758, 'Parede de Prisão Adamantina', 'Transformando-se em um bastão adamantino, Enma clona a si mesmo e em seguida prende o inimigo em um lattice. O muro da prisão tem uma incrível resistência e é impossível de se quebrar. Quando usado para defesa, o inimigo não tem chances de atacar.', '金剛牢壁こんごうろうへき', 'Kongō Rōheki', 'Parede da Prisão Vajra', NULL, 'Prisão de Diamante', NULL, 'Curto alcance', NULL, NULL),
(1759, 'Parede de Quarentena', 'Usando seu escudo, Renga cria uma copacta barreira de chakra. É resistente o suficiente para defender uma imensa explosão de bomba.', '遮断壁', 'Shadanheki', NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1760, 'Passo Gentil da Besta com Cauda dos Punhos de Leões Gêmeos', 'Naruto e Hinata combinam o Rasengan com o Passo Gentil dos Punhos de Leões Gêmeos para atacar seu oponente e, por fim, Naruto finaliza com uma Bola da Besta com Cauda.', '柔歩尾獣双獅拳じゅうほびじゅうそうしけん', 'Jūho Bijū Sōshiken', 'Passo Gentil da Besta com Cauda dos Punhos de Leões Gêmeos', NULL, NULL, NULL, 'Curto a Longo alcance', NULL, NULL),
(1761, 'Passo Gentil das Palmas Gêmeas', 'O usuário prepara seu Punho Gentil e corre contra seu oponente, acertando-o com dois golpes de palma reforçada, danificando-o e repelindo-o.', '柔歩双掌じゅうほそうしょう', 'Jūho Sōshō', 'Passo Gentil das Palmas Gêmeas', 'Precipitação da Palma Dupla', NULL, NULL, 'Curto alcance', NULL, NULL),
(1762, 'Passo Gentil dos Punhos de Leões Gêmeos', 'O Passo Gentil dos Punhos de Leões Gêmeos é uma técnica secreta do estilo do Punho Gentil de alto nível, uma técnica somente ministrada na família fundamental do Clã Hyūga.', '柔歩双獅拳じゅうほそうしけん', 'Jūho Sōshiken', 'Passo Gentil dos Punhos de Leões Gêmeos', 'Passo Gentil: Punho dos Leões Gêmeos', 'Técnica do Passo Leve Punho dos Leões Gêmeos', NULL, 'Curto alcance', 'Rank –', NULL),
(1763, 'Passo Gentil dos Punhos de Leões Gêmeos Espirais', 'Os usuários unem seus chakra e formam uma face de leão gigante com um Rasengan no interior, formando-os onde se encontra as mãos unidas dos usuários. A técnica mostrou-se forte o suficiente para destruir o Tenseigan de Hamura por completo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1764, 'Pavão da Manhã', 'O Pavão da Manhã (朝孔雀 Asa Kujaku) é um taijutsu de morte-certa que nasceu na filosofia de Might Guy. O princípio deste taijutsu é de aumentar rapidamente as habilidades do corpo, abrindo, inclusive, o sexto dos oito portões, e, em seguida, atacar o inimigo com inúmeros socos.', '朝孔雀あさくじゃく', 'Asa Kujaku', 'Pavão da Manhã', NULL, 'Pavão do Amanhecer', 'Asa Kujaku', 'Curto alcance', 'Rank A', NULL),
(1765, 'Pedregulho Descendente', 'Tirando proveito de seu grande porte físico, Akatsuchi salta no ar e cai sentado sobre seu oponente, esmagando-o.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1766, 'Pedregulho de Areia Explosiva', 'Gaara condensa sua areia em várias rochas flutuantes. Então, ele pode usá-las como projéteis ou explosivos, criando um campo minado em qualquer lugar em que o inimigo estiver a fim de impedi-lo de escapar.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1767, 'Peguei Você!', 'Konohamaru usa a Técnica da Capa da Invisibilidade para se ocultar do oponente e, quando este o ataca, ele salta no ar, acertando-o com um golpe.', 'かかったな!', 'Kakattana!', 'Peguei Você!', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1768, 'Peido', 'Naruto peida próximo ao inimigo para envenená-lo. Esta habilidade é muito eficaz contra membros do clã Inuzuka, por exemplo, que, devido a seus olfatos aguçados, tornam-se vulneráveis ao odor.', 'おなら', 'Onara', 'Peido', 'Peido Preso (オナラ縛り, Onara Shibari)', NULL, NULL, 'Curto a Médio alcance', NULL, 'Tigre'),
(1769, 'Peixe de Relâmpago', 'O usuário produz uma grande estrutura de relâmpagos semelhante a um tubarão que nada de forma independente sob a água com objetivo de atingir o alvo e eletrocutá-lo.', '雷魚', 'Raigyo', 'Peixe de Relâmpago', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Carneiro'),
(1770, 'Pelota de Osso', 'Kimimaro dispara de três a cinco pontas afiadas de osso no oponente.', '骨飛礫', 'Hone Tsubute', 'Pelota de Osso', 'Pelota de Osso Voador', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1771, 'Penas Explosivas', 'O pássaro de San libera numerosas penas com grande precisão que explodem de forma semelhante ao papel explosivo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1772, 'Penetração das Emoções Ardentes', 'Lee abre vários portões de chakra, e depois avança contra o oponente, levantando-o para o céu como um foguete. Depois de atingir uma certa distância, Lee bate atacando-o para o solo.', '突き抜ける熱き思いつきぬけるあつきおもい', 'Tsukinukeru Atsuki Omoi', 'Penetração das Emoções Ardentes', 'Esmagamento de Paixão', NULL, NULL, 'Curto alcance', NULL, NULL),
(1773, 'Performance Mecânica: Queda da Cortina', 'Kankurō usa essa técnica com Karasu e Kuroari. Primeiro, Karasu prende seu oponente e o atira no ar, então Kuroari salta sobre ele, esmagando-o contra o chão.', 'カラクリ演技・緞帳降ろし', 'Karakuri Engi: Donchō Oroshi', 'Performance Mecânica: Queda da Cortina', 'Show da Marionete: Queda da Cortina', NULL, NULL, 'Curto alcance', NULL, NULL),
(1774, 'Performance da Dança Carmesim Profunda do Estilo-Nadeshiko', 'Shizuka lança várias kunai repetitivamente no alvo, com uma precisão mortal. Esta técnica é aparentemente difícil de desviar, o que levou Naruto a elogiar o desempenho da mesma.', '撫子流真紅演舞', 'Nadeshiko-Ryū Shinku Enbu', 'Performance da Dança Carmesim Profunda do Estilo-Nadeshiko', NULL, NULL, 'Dança Aérea do Estilo-Nadeshiko', 'Curto a Médio alcance', NULL, NULL),
(1775, 'Performance da Marionete: Conquista Habilidosa com um Corpo Humano', 'Uma técnica de execução com marionete, onde linhas de chakra ficam ligadas a vários pontos no corpo de uma pessoa, a cabeça, tronco, dois braços e duas pernas. A pessoa que está sendo manipulada ganha a habilidade de usar não só as suas próprias técnicas, mas as habilidades do marionetista também.', '操演・人身冴功', 'Sōen: Hitomi Gokū', 'Performance da Marionete: Conquista Habilidosa com um Corpo Humano', NULL, NULL, NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(1776, 'Performance da Marionete: Decapitação Maquiavélica', 'Usando as marionetes Mãe e Pai, Chiyo faz com que elas saquem suas armas — uma espada e um chicote com lâminas, respectivamente — e ataquem o oponente. O primeiro ataque é feito pela marionete Mãe, que corta horizontalmente o oponente, enquanto o segundo ataque é feito pela marionete Pai, que bate seu chicote verticalmente. Para finalizar a sequência, a marionete Mãe posiciona a espada na altura do abdômen e gira verticalmente em torno de si enquanto no ar, causando uma dilaceração sequencial.', '操演・斬奸', 'Sōen: Zankan', 'Performance da Marionete: Decapitação Maquiavélica', 'Performance da Marionete: Corte do Mal', NULL, NULL, 'Longo alcance', NULL, NULL),
(1777, 'Performance de Dança: Segundo Passo', 'O usuário gira num eixo em torno de si, em alta velocidade, semelhante a um mini-tornado. Enquanto gira, o usuário dispara diversas kunai no alvo, ao mesmo tempo.', '演舞・二の段', 'Enbu: Ni no Dan', 'Performance de Dança: Segundo Passo', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1778, 'Perfurar como Punição', 'É uma técnica que faz uso da versatilidade do Kusarigama.', '串刺しの刑', 'Kushizashi no Kei', 'Perfurar como Punição', 'Punição por Espeto', NULL, NULL, 'Médio alcance', NULL, NULL),
(1779, 'Perfuração Brutal', 'Utilizando de sua capacidade de aumentar seu braço, aumentando também a resistência para empunhar a Kubikiribōchō, Suigetsu corre contra o inimigo e perfura-o com a espada brutalmente, com impacto suficiente para lançá-lo para o outro lado da arena.', 'ぶっ刺し', 'Buzzashi', 'Perfuração Brutal', 'Espetar', NULL, NULL, 'Curto alcance', NULL, NULL),
(1780, 'Perfuração Trovejante', 'Sarada transforma seu chakra em relâmpago e o transmite a seu punho, a fim de tornar seus socos mais fortes e capazes de eletrocutar o oponente.', '迅雷穿ちじんらいうがち', 'Jinrai Ugachi', 'Perfuração Trovejante', 'Broca Relâmpago', NULL, NULL, 'Curto alcance', NULL, NULL),
(1781, 'Perfuração da Dança do Leque do Céu', 'Temari gera um enorme ciclone de vento que emite de seu Leque Dobradiço Gigante, em seguida, gira o leque para fazer uma varredura com o ciclone, nivelando uma grande área ao redor dela.', '練空踊扇', 'Renkū Yōsen', 'Perfuração da Dança do Leque do Céu', 'Dança do Leque Aéreo Polonês', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1782, 'Permeação Ácida', 'Utakata sopra bolhas de ácido em seu oponente, que pode queimar o adversário quando estouram.', '酸透過', 'Santōka', 'Permeação Ácida', NULL, NULL, NULL, NULL, NULL, NULL),
(1783, 'Perseguição da Onda de Areia', 'Gaara utiliza sua areia para criar três vezes uma barragem em seu oponente e finaliza com um corte de uma corda gigante feita de areia.', '波追砂', 'Hatsuisa', 'Perseguição da Onda de Areia', 'Onda de Areia da Perseguição', NULL, NULL, 'Curto alcance', NULL, NULL),
(1784, 'Perseguição dos Imortais', 'Aproveitando-se da imortalidade de Hidan, Kakuzu faz uso de uma poderosa combinação para engolfar Hidan em chamas, que remota todo o dano ao inimigo amaldiçoado. No final, Hidan desfaz a maldição e corta o inimigo continuamente por satisfação.', '不死の猛進ふしのもうしん', 'Fushi no Mōshin', 'Perseguição dos Imortais', 'Perseguição do Imortal Aquecido', NULL, NULL, 'Curto alcance', NULL, NULL),
(1785, 'Personificação da Sakura Interior', 'Esta técnica consiste basicamente na personificação da personalidade oculta de Sakura Haruno.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1786, 'Personificação de Chakra', 'A Personificação de Chakra é usada a fim de distorcer a forma como ninjas do tipo sensor detectam o chakra do usuário e de seus colegas.', 'チャクラの偽装ちゃくらのぎそう', 'Chakura no Gisō', 'Personificação de Chakra', NULL, NULL, NULL, NULL, NULL, NULL),
(1787, 'Perturbação da Valsa Maligna', 'Coberto pelo Susanoo, Madara arremessa sua espada de chakra no oponente, girando como uma shuriken. Durante sua batalha com Hashirama Senju, Madara tinha revestido Kurama em seu Susanoo e perfurou a Bola da Besta com Cauda com a espada para um resultado mais prejudicial.', '凶騒円舞きょうそうえんぶ', 'Kyōsō Enbu', 'Perturbação da Valsa Maligna', 'Dança do Caos', NULL, NULL, NULL, NULL, NULL),
(1788, 'Peteleco', 'O Peteleco é uma técnica de taijutsu utilizada por Tsunade através de sua força sobre-humana.', 'デコピン', 'Dekopin', 'Peteleco', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1789, 'Peônia da Neve', 'Kurenai ataca seu oponente primeiro, diminuindo sua posição e depois gira para o oponente para desferir um golpe no joelho, seguido de um chute que empurra o oponente para trás, onde ela então pega o oponente, levantando-o no ar. Ela finalmente oferece uma enxurrada de vários chutes no estômago, chutando-o no chão.', '牡丹雪', 'Botanyuki', 'Peônia da Neve', 'Peônia dos Flocos de Neve', NULL, NULL, 'Curto alcance', NULL, NULL),
(1790, 'Picada Divina', 'Depois de ser invocado por Orochimaru, Manda atravessa o cenário com sua cabeça e acerta uma picada contra seu oponente.', '神咬', 'Kamigami', 'Picada Divina', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1791, 'Pilar de Areia', 'Gaara envolve a metade inferior de seu corpo na areia e manipula a areia para suspende-lo e manobrar-se no ar em altas velocidades. A areia mantém o contato com o solo, criando assim uma formação semelhante à um pilar.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1792, 'Pilar de Água', 'O usuário, empunhando uma das Sete Espadas da Névoa Sangrenta, bate ela contra o chão e adiciona seu chakra de água, criando uma cadeia de pilares de água na direção da ponta da espada e causando dano ao oponente, lançando-o para o alto e para longe.', '水柱', 'Suichū', 'Pilar de Água', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1793, 'Pilares de Gelo Sinuosos', 'Kaguya manipula o terreno de sua dimensão de gelo para criar estalagmites que se erguem a partir do chão para perfurar seu oponente.', '九十九氷柱つくもひょうちゅう', 'Tsukumo Hyōchū', 'Pilares de Gelo Sinuosos', 'Noventa e Nove Pilares de Gelo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1794, 'Pimenta Vermelha Sanguinária', 'Enquanto em seu Modo Despertar, Kushina combina uma série de socos, pontapés e ataques da Correntes de Selamento Adamantinas. Depois de seus ataques, Kushina eventualmente volta à sanidade.', '赤い血潮のハバネロあかいちしおのハバネロ', 'Akai Chishio no Habanero', 'Pimenta Vermelha Sanguinária', 'Esmagamento da Tomate (蕃茄圧潰, Banka Akkai)[1]', NULL, NULL, 'Curto alcance', NULL, NULL),
(1795, 'Pisoteamento Fatal', 'Tayuya executa uma rápida rasteira, que leva o oponente ao chão. Em seguida, ela pisa em seu oponente, e finaliza com uma versão mais acentuada, que faz com que uma considerável quantidade de poeira surja ao seu redor.', NULL, NULL, NULL, 'Pisoteamento Fatal', NULL, NULL, 'Curto alcance', NULL, NULL),
(1796, 'Poder Adamantino: Acala', 'Hashirama chuta o oponente para longe e levanta algumas gavinhas de madeira para golpear o inimigo. Com o oponente deitado no chão, Hashirama reúne uma grande quantidade de chakra em seu punho direito, salta e leva-o no tronco de seu inimigo. O impacto faz com que o chão quebre e uma pequena floresta entre em erupção a partir dele.', '金剛力・不動', 'Kongōriki: Fudō', 'Poder Vajra: Acala', 'Poder Adamantino: Acala', NULL, NULL, 'Curto alcance', NULL, NULL),
(1797, 'Poder Budista: Silhueta', 'Com o auxílio do som de um instrumento, o usuário dá vida à sombra do oponente, forçando-a a estrangular e a sufocar o alvo.', '法力・影法師ほうりき・かげぼうし', 'Hōriki: Kagehōshi', 'Poder Budista: Silhueta', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1798, 'Poder Completo da Juventude (game)', 'Rock Lee chuta o adversário para cima e, em seguida, abre seus portões internos de chakra e vai em direção ao oponente com um chute, desfere diversos golpes no ar e finaliza chutando-o em direção ao solo em uma velocidade considerável. Lee, em seguida, chega ao chão antes do oponente e desfere um soco em suas costas, levando-o para o céu em um feixe de luz amarelo.', '青春フルパワーせいしゅんフルパワー', 'Seishun Furu Pawā', 'Poder Completo da Juventude', 'Juventude em Pleno Poder!!', NULL, NULL, 'Curto alcance', NULL, NULL),
(1799, 'Poder da Gelel', 'Quando Haido se transforma em uma criatura humanoide monstruosa através dos poderes da Pedra de Gelel, ele é capaz de disparar um laser gigante na parte de trás da sua mão direita, onde a pedra está incorporada. A explosão é muito poderosa e pode matar a maioria das pessoas com apenas uma descarga. O laser pode ser ainda mais aumentado para disparar uma explosão muito maior.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(1800, 'Ōdachinagi', NULL, '大太刀薙ぎおおだちなぎ', 'Ōdachinagi', 'Rasteira da Grande Espada de Guerra', 'Rasteira de Odachi', NULL, NULL, 'Curto alcance', NULL, NULL),
(1801, 'Liberação de Água: Tate Eboshi', 'Depois de usar sua Técnica de Hidratação para se fundir com uma fonte de água, Suigetsu pode formar uma onda gigante que se parece com um peixe demoníaco. Suigetsu pode utilizar este forma para lutar contra adversários maiores, ou ele pode usá-la para proteger os aliados de ataques. Apesar de ter sido forte o suficiente para empurrar de volta fisicamente Killer B em seu Modo Bijū, não foi forte o suficiente para suportar a Bola da Besta com Cauda a queima-roupa, que deixou Suigetsu inconsciente, fazendo-o cair em um estado gelatinoso.', '水遁・隋烏帽子すいとん・たてえぼし', 'Suiton: Tate Eboshi', 'Liberação de Água: Enterro das Crianças do Chapéu de Corvo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1802, 'Liberação de Água: Tiro da Arma', 'O usuário molda o chakra, e converte-o em água e, em seguida, cospe-o para fora, na forma de bolas condensadas. A alta velocidade de ataque que a bola de água possui tem o poder tanto para matar como para dizimar fogo real. Embora esta seja uma técnica de Liberação de Água, que pode ainda ser empregue um lugar onde não há água disponível, que é uma grande vantagem. Também é possível aumentar o poder de destruição das próprias esferas construindo o chakra dentro delas. O número de balas é determinada enquanto molda o chakra. Ao tirar vantagem da gravidade, as balas podem aumentar brutalmente no poder. Com um usuário do tamanho de Gamabunta, as balas podem realmente ser mais como balas de canhão, tornando-as muito mais mortal.', '水遁・鉄砲玉', 'Suiton: Teppōdama', 'Liberação de Água: Tiro', NULL, 'Estilo da Água: Canhão Aquático', 'Estilo Água: Tiro d\'água', 'Todos os alcances', 'Rank C', 'Tigre'),
(1803, 'Poder do Hokage', 'Tsunade entra no Modo Sannin e soca o abdômen de seu inimigo (alertando-o para se preparar) e em câmera lenta parece que ele ou ela vai ser se partir em dois. Ele é empurrado para trás a uma velocidade tremenda, porém, levando tudo por um campo de árvores até que sejam finalmente parado por uma grande pedra, que faz com que o oponente de Tsunade leve ainda mais danos. Pouco tempo depois, um vulcão entra em erupção no fundo como resultado do poder do soco, atingindo o campo de batalha. Tsunade, endireitando-se para cima, fica se perguntando se ela talvez foi longe demais.', '火影の力', 'Hokage no Chikara', 'Poder do Hokage', 'O Poder da Hokage', NULL, NULL, 'Curto alcance', NULL, NULL),
(1804, 'Poder do Nove-Caudas', 'Kurama é capaz de disparar um grande fluxo de chamas através de sua boca. As chamas tem poder suficiente para quebrar pedras enormes, e até mesmo causar danos ao Chibaku Tensei de Nagato.', '九尾の力', 'Kyūbi no Chikara', 'Poder do Nove-Caudas', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1805, 'Poder do Rugido Dourado', 'Tirando proveito de sua grande força física, Kongō corre em direção ao seu oponente e o acerta com um movimento de laço na área do pescoço, derrubando-o.', '金轟力', 'Kin Todoroki-Ryoku', 'Poder do Rugido Dourado', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1806, 'Poder do Sábio', 'Jiraiya estende seu cabelo para atacar seu oponente, de modo a jogá-lo para longe, e, em seguida, ele cospe fogo a fim de incinerá-lo. Atordoado, o oponente é incapaz de acompanhar os movimentos de Jiraiya, que, logo, cria um Rasengan em chamas. Esse Rasengan explode e, quando em contato com o corpo do oponente, gera um pequeno redemoinho de fogo, longe de Jiraiya.', '仙人の力せんにんのちから', 'Sennin no Chikara', 'Poder do Sábio', 'Poder Sábio', NULL, NULL, 'Curto alcance', NULL, NULL),
(1807, 'Poderosa Lança de Pó de Ouro', 'Rasa cria uma maciça e poderosa lança em forma de broca usando seu pó de ouro, perfurando o oponente.', '砂金剛槍さきんごうそう', 'Sakin Gōsō', 'Poderosa Lança de Pó de Ouro', 'Lança de Pó de Ouro', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1808, 'Poeira da Palma Esquerda e Fíbula da Perna Direita', 'Sakon e Ukon correm contra o oponente e separam-se para distrair o oponente, passando por ele e mantendo-o no meio. Ao chegar nas costas do oponente, Sakon e Ukon chutam o oponente nas costas, onde é difícil defender.', '掌左の塵と右脚腓骨てのひらひだりのちりとみぎあしひこつ', 'Tenohira Hidari no Chiri to Migi Ashi Hikotsu', 'Poeira da Palma Esquerda e Fíbula da Perna Direita', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1809, 'Pontapé Flash da Folha', 'Rock Lee salta e executa um chute descente com o calcanhar contra o oponente, usando pura força física. Esse golpe é forte o suficiente para destruir rochas.', '木ノ葉閃蹴', 'Konoha Senshū', 'Pontapé Flash da Folha de Árvore', 'Pontapé Flash da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1810, 'Pontapé Giratório dos Clones das Sombras', 'Naruto usa seus clones das sombras para desferir uma série de golpes em seu oponente, e terminando com o giro de um de seus clones das sombras a fim de atacar o oponente. Como um método alternativo, o clone é lançado para chutar o oponente.', '影分身回し蹴りかげぶんしんまわしげり', 'Kage Bunshin Mawashigeri', 'Pontapé Giratório dos Clones das Sombras', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1811, 'Pontapé Serial', 'O usuário chuta seu oponente várias vezes em rápida sucessão e termina com um chute poderoso que envia o adversário à voo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto Alcance', NULL, NULL),
(1812, 'Pontapé Voador', 'Depois de manifestar as asas de Chōmei, Fū voa no ar e desce, acertando um pontapé no oponente.', '飛翔一脚ひしょうひとあし', 'Hishō Hitoashi', 'Pontapé Voador', 'Pé Único Voador', NULL, NULL, 'Curto alcance', NULL, NULL),
(1813, 'Pontapé do Leão', 'O usuário cria uma distração no oponente, seja através de golpes de taijutsu ou com algum parceiro distraindo, e finaliza com um poderoso pontapé em seu oponente, lançando-o longe.', '獅子一脚', 'Shishi Hitoashi', 'Pontapé do Leão', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1814, 'Pontos de Pressão de Dano e Morte', 'Guren abre dois Tenketsu; Dano e Morte, para aumentar consideravelmente seu chakra, mudando sua força de vida em chakra, permitindo o uso de técnicas como o Liberação de Cristal: Corda da Glória. A quantidade de chakra liberada é tanta que faz com que a água forme um vórtice em torno dela, semelhante como é causado pela abertura do Keimon dos Oito Portões, também com os olhos momentaneamente completamente brancos, seu chakra é tão denso que se torna visível ao seu redor.', '死傷点穴', 'Shishō Tenketsu', 'Pontos de Pressão de Dano e Morte', NULL, NULL, 'Pontos de Chakra: Vida e Morte', NULL, NULL, NULL),
(1815, 'Portal Espaço-Temporal', 'São técnicas que permitem aos usuários manipular o espaço-tempo. Ao manipular um ponto específico do espaço, o usuário pode deformar qualquer alvo em um vácuo dimensional e teleportar para outro local instantaneamente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1816, 'Possessão da Cobra Branca', 'Orochimaru transfere sua consciência para uma pequena cobra branca onde se desliza para dentro do corpo de uma pessoa, permitindo que Orochimaru ganhe o controle de seu corpo e mente. Semelhante à técnica do clã Yamanaka, a Técnica de Troca da Mente do Corpo, o corpo original do usuário fica indefeso depois de tomar o controle do corpo de alguém. Orochimaru usou esta técnica para controlar Kajika e assim testar a extensão dos poderes de Sasuke Uchiha.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1817, 'Presa Decapita Presa', 'Kiba e Akamaru combinam um em cada ponta para criar uma perfuração gigante que atravessa o oponente, lançando-o no ar, onde Kiba ataca o oponente com um dos pontos de perfuração e através dele, jogando-o de lado.', '牙斬牙', 'Gazanga', 'Presa Decapita Presa', 'Presa Corta Presa', NULL, NULL, 'Curto alcance', NULL, NULL),
(1818, 'Presa Giratória de Presa', 'O usuário e seu parceiro ninken rolam a uma velocidade feroz em forma de rolo, o que causa muitos danos poderosos quando o contato é feito com o alvo.', '牙転牙がてんが', 'Gatenga', 'Presa Giratória de Presa', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1819, 'Presa Gêmea Celestial', 'Kiba e Akamaru saltam e utilizam sua técnica Presa Passando Presa. Os dois então se fundem criando uma versão maior e mais forte da Presa Passando. Eles então, atacam seu oponente com uma força monstruosa, que destrói o solo.', '天双牙', 'Tensōga', 'Técnica Secreta da Arte Marcial da Besta Humana: Presa Gêmea Celestial', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1820, 'Presa Lobo Presa', 'O homem e a besta se transformam em uma única criatura gigantesca com o uso da Transformação Combinada da Besta Humana: Lobo de Duas Cabeças, giram o seu corpo e então atacam o inimigo violentamente. A velocidade da rotação cria um vácuo ao redor do usuário, que danifica o inimigo mesmo sem tocá-lo diretamente. A excessiva velocidade da técnica bloqueia o campo de visão do usuário, sendo que a localização do alvo é feita ao marcar o alvo com antecedência, seguindo-o através do cheiro. No anime, se o usuário é habilidoso o suficiente, isso é desnecessário. O poder da técnica foi tão grande que se mostrou capaz de amassar o Rashōmon. No entanto, ela possui seus malefícios, uma vez que cansa o usuário rapidamente.', '牙狼牙がろうが', 'Garōga', 'Presa Lobo Presa', NULL, 'Trespassar das Presas Lupinas', 'Presa do Lobo Sobre Presa', 'Médio a Longo alcance', 'Rank B', NULL),
(1821, 'Presa Passando', 'O usuário gira a uma velocidade feroz e pousa muitos ataques poderosos como besta quando o contato é feito com o alvo. O dano que ela pode infligir a um alvo humano pode ser extremamente prejudicial, pois pode facilmente rasgar com galhos grossos de árvores e muro de terra aprimorado com chakra; todos os quais com um raio mais espesso do que o próprio usuário.', '通牙つうが', 'Tsūga', 'Presa Passando', NULL, 'Redemoinho de Presas', 'Presa de Tunelamento', 'Curto a Médio alcance', 'Rank D', NULL),
(1822, 'Presa Passando Presa', 'O usuário e seu parceiro ninken rotacionam a uma velocidade feroz e desferem muitos ataques poderosos como bestas quando o contato é feito com o alvo. A força desse ataque é forte o suficiente para perfurar uma pedra. Este ataque também pode ser realizado individualmente.', '牙通牙がつうが', 'Gatsūga', 'Presa Passando Presa', 'Taijutsu do Homem-Besta: Presa Sobre Presa', 'Presa Sobre Presa', 'Taijutsu Final do Homem-Besta: Presa Sobre Presa', 'Curto alcance', 'Rank C', NULL),
(1823, 'Presa de Inseto Gigante: Sessenta e Quatro Palmas', 'O Time Kurenai se prepara para atacar. Kiba cria um clone da sombra, Shino prepara seus insetos e Hinata concentra chakra em suas mãos. Em seguida, Kiba, seu clone e Akamaru se transformam em um Lobo de Três Cabeças e usam a Cauda Perseguidora de Presa da Presa Giratória de Presa. Hinata se junta ao golpe, deixando-o azul devido a seu chakra, e acerta sessenta e quatro golpes no oponente, aplicando os insetos de Shino. Shino usa um último golpe, que consome o oponente, finalizando com uma grande explosão.', '大蟲牙・六十四掌だいちゅうが・ろくじゅうよんしょう', 'Daichūga: Rokujūyon Shō', 'Presa de Inseto Gigante: Sessenta e Quatro Palmas', 'Grande Presa de Inseto: 64 Palmas', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1824, 'Presas Relâmpago', 'Usando suas espadas especiais e posicionando-as uma de cada lado de seu corpo, Ameyuri rapidamente gira liberando um fluxo de chakra relâmpago pelas espadas, sendo capaz de controlar sua extensão.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1825, 'Presas de Raios', 'Ao reunir suas espadas gêmeas, Raiga pode enviar uma essência elétrica para as nuvens, permitindo-lhe criar relâmpagos em qualquer local desejado.', '雷の牙', 'Ikazuchi no Kiba', 'Presas de Raios', NULL, NULL, 'Arte Ninja: Presa Relâmpago', 'Todos os alcances', NULL, NULL),
(1826, 'Presas do Rei Macaco', 'Hiruzen dá um pontapé no intestino do oponente, então ele amplia o Rei Macaco: Enma na sua forma de bastão. Enma aparece na ponta do bastão, atacando o alvo com suas presas.', '猿王の牙えんおうのきば', 'En\'ō no Kiba', 'Presas do Rei Macaco', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1827, 'Prescrição de Fortalecimento: Injeção de Chakra', 'Esta técnica cria cobras negras que forçosamente se conectam com uma pessoa e as injeta chakra medicamente alterado. Este chakra extra permite que o usuário utilize ninjutsu elemental de alto nível. Ele também permite que o usuário use técnicas elementais que ele não seria capaz de dominar normalmente. O chakra não é infinito, e deve ser reposto ao longo do tempo. Além disso, apenas um tipo de chakra elemental pode ser injetado na pessoa de cada vez, como Neji observou que Shizuku foi visto com um elemento completamente diferente do encontro anterior.', '強化処方・チャクラ注入', 'Kyōka Shohō: Chakura Chūnyū', 'Prescrição de Fortalecimento: Injeção de Chakra', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1828, 'Pressurização', 'Jūgo, após alterar sua musculatura através de sua transformação, golpeia o oponente sucessivamente.', '加圧', 'Kātsu', 'Pressurização', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1829, 'Pressão de Pó de Ouro', 'Rasa cria um enorme cubo de pó de ouro acima de sua cabeça, leva-o acima do oponente e esmaga-o com o cubo maciço.', '砂金圧搾器さきんあっさくき', 'Sakin Assakuki', 'Pressão de Pó de Ouro', 'Pressão Gigante de Pó de Ouro', NULL, NULL, 'Curto alcance', NULL, NULL),
(1830, 'Prisão Celestial da Espada de Fogo', 'O usuário acende uma grande chama em sua mão direita, que pouco depois toma a forma de uma espada. Ao atingir o inimigo, ela incendeia o corpo dele por completo. A espada se mostrou capaz de perfurar carne facilmente.', '天牢火劍てんろうかけん', 'Tenrō Kaken', 'Prisão Celestial da Espada de Fogo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1831, 'Prisão Restritora de Areia', 'Uma técnica de captura característica de Gaara. Usando areia comum, um espaço perfeitamente hermético é criado, em que o inimigo é capturado. Uma enorme quantidade de areia envolve completamente o alvo de todas as direções e captura-o. A velocidade, a dureza, e do âmbito da areia dependem da quantidade de chakra vertido sobre ela. Gaara pode ajustar as propriedades da areia para o tamanho e as características do alvo. Não importa se o alvo está no chão ou voando no ar, é quase impossível escapar desta técnica. No entanto, mesmo se alguém escapa com sucesso a partir dela, Gaara pode facilmente usar uma de suas técnicas ofensivas, como o Caixão de Areia, para atacar imediatamente o alvo.', '砂縛牢さばくろう', 'Sabakurō', 'Prisão Restritora de Areia', 'Caixão da Tempestade de Areia', NULL, 'Prisão de Areia', 'Todos os alcances', 'Rank B', NULL),
(1832, 'Prisão da Boca da Cobra', 'O usuário prende o adversário e ele mesmo na boca de uma cobra. O estômago da cobra é muito parecido com elástico, podendo até mesmo resistir a Gamabunta, uma criatura de tamanho similar, sendo invocado dentro dele.', '蛇口縛り', 'Hebi Kuchi Shibari', 'Prisão da Boca da Cobra', NULL, NULL, 'Arte Ninja: Armadilha da Boca de Cobra', 'Todos os alcances', NULL, NULL),
(1833, 'Prisão de Arame', 'Esta técnica utiliza fios de aço, para formar uma forte cúpula de proteção. Esta cúpula pode repelir a maioria dos ataques básicos de taijutsu. O utilizador pode ainda alterar a forma do fio no interior da cúpula, conforme necessário, transformando-o de uma estrutura defensiva para uma corrente com facilidade.', '籠の目', 'Kago no Me', 'Prisão de Arame', NULL, NULL, NULL, 'Curto alcance', NULL, 'Tigre → Cobra → Javali'),
(1834, 'Prisão de Fumaça do Cheiro Destruidor', 'Enquanto luta contra um inimigo e caso queira fugir da batalha ou se ocultar, Kigiri é capaz de produzir uma cortina de fumaça extremamente grossa que imediatamente faz o adversário ficar com tosse e ocultar a visão do mesmo, ela também abrange até o cheiro do usuário e seus aliados. Ele então pode atacar a vontade. O utilizador e aqueles que treinaram sobre a técnica podem ver claramente através da cortina de fumaça.', '滅臭煙牢めっしゅうえんろう', 'Messhū Enrō', 'Prisão de Fumaça do Cheiro Destruidor', NULL, NULL, 'Prisão da Fumaça de Fuga', 'Todos os alcances', NULL, NULL),
(1835, 'Prisão do Funeral de Areia', 'Gaara usando sua habilidade de manipular a areia, afrouxa o chão abaixo do inimigo e pega-o em um redemoinho de areia. O inimigo engolido afunda até duzentos metros de profundidade, enquanto completamente incapaz de mover um músculo. Em seguida, a areia colada seus membros se aplica pressão sobre todo o seu corpo, por isso, eles não podem sequer se contorcer um dedo. A pressão resultante de ser enterrado em uma profundidade tão extrema certamente mata qualquer ser humano normal, se não de asfixia, então a partir do fato de que todos os seus ossos se quebram.', '獄砂埋葬', 'Gokusa Maisō', 'Prisão do Funeral de Areia', NULL, 'Funeral na Prisão de Areia', 'Funeral da Prisão de Areia', 'Curto a Médio alcance', NULL, 'Carneiro'),
(1836, 'Prisão em Forma de Rede', 'Com esta técnica, o usuário pode manipular teias, emitindo teia de sua boca ou mãos e manipulá-la como uma rede, na intenção de prender os adversários.', '網状牢', 'Mōjōrō', 'Prisão em Forma de Rede', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Javali → Carneiro → Cao → Rato[1] ou (Cão)[2]'),
(1837, 'Processamento de Areia', 'O Processamento de Areia consiste na confecção e manipulação da areia através da infusão de chakra.', '砂処理', 'Sunashori', 'Processamento de Areia', NULL, NULL, NULL, NULL, NULL, NULL),
(1838, 'Profusão das Cem Flores', 'Enquanto estiver usando o poder da Névoa de Veneno Inodoro, Karenbana libera muitas pétalas de flores de seu corpo. Estas pétalas fluem através do ar e um circulam em torno de seu alvo. Karenbana então se move através das flores, fazendo parecer como se ela estivesse dissolvendo em pétalas, usando-as como uma parede de modo que seu alvo não pode ver os ataques recebidos. A fraqueza desta ilusão é que ele só esconde seu visual, quando Naruto liberou uma onda de choque generalizado para bater em suas costas, enquanto Sakura aproveitou ouvindo os passos de Karenbana sobre vidro quebrado para identificar sua localização.', '百花繚乱', 'Hyakkaryōran', NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1839, 'Projéteis de Perfuração dos Dez Dedos', 'Ossos endurecidos dos dedos são atirados no inimigo, com um movimento giratório adicionado às balas esqueléticas. Uma colisão direta escava a pele, carne e osso. Uma vez que os ossos do utilizador podem ser regenerados, a taxa de tiros do arsenal do utilizador é ilimitada. Esta técnica é essencial para Kimimaro para os seus ataques de longa distância. No anime, esse ataque foi mostrado forte o suficiente para facilmente perfurar a armadura de aço usada pelos samurai.', '十指穿弾てしせんだん', 'Teshi Sendan', 'Projéteis de Perfuração dos Dez Dedos', NULL, 'Dez Dedos Perfuradores', 'Tiros dos Dedos', 'Curto a Médio alcance', 'Rank –', NULL),
(1840, 'Projétil Ascendente Dragoníaco do Avestruz', 'Condor dá um chute ascendente no estômago do oponente.', 'ダチョウ昇龍弾', 'Dachō Shōryūdan', 'Projétil Ascendente Dragoníaco do Avestruz', NULL, NULL, NULL, NULL, NULL, NULL),
(1841, 'Projétil Saltitante da Concha Torre', 'Transformado como Isobu, Yagura enrola-se em uma bola e salta, permitindo-se ou machucar um adversário, ou mover-se rapidamente ao redor.', '跳弾甲櫓ちょうだんこうろ', 'Chōdan Kōro', 'Projétil Saltitante da Concha Torre', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1842, 'Projétil da Fumaça Demoníaca', 'Ao usar seu lançador de pulso, Kihō bombardeia o inimigo com uma enxurrada de pequenas granadas de fumaça, explodindo e criando uma espessa nuvem de fumo.', '魔煙弾', 'Maegan', 'Projétil da Fumaça Demoníaca', NULL, NULL, 'Esfera de Fumaça Escura', 'Todos os alcances', NULL, NULL),
(1843, 'Projétil da Queda do Brilho Elevado', 'Fū cega seu oponente com Pó de Escama, agarra-o, voa com ele no ar manifestando as asas de Chōmei, e joga-o de volta para o chão.', '翔耀降弾', 'Shōyō Kōdan', 'Projétil da Queda do Brilho Elevado', 'Batida Descendente Brilhante', NULL, NULL, 'Curto alcance', NULL, NULL),
(1844, 'Projétil de Grande Chama Inigualável', 'Sasuke chuta e salta sobre seu oponente, novamente dando-lhe um chute, agora em sua cabeça. Aproveitando a baixa-guarda, chuta-o para o alto e salta para dar um soco que o joga de volta ao chão. Enfim, Sasuke finaliza com um grande projétil de fogo.', '無双豪炎弾むそうごうえんだん', 'Musō Gōendan', 'Projétil de Grande Chama Inigualável', 'Ultra Projétil de Fogo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1845, 'Projétil de Liberação de Fogo: \'Chamas\'', 'O Projétil de Liberação de Fogo: \'Chamas\' cria uma massa de chamas que pode ser atirada como um projétil ou liberada na forma de um fluxo na direção do oponente.', '火遁弾・「焔」', 'Katon Dan: \'Homura\'', 'Projétil de Liberação de Fogo: \'Chamas\'', NULL, NULL, NULL, 'Longo alcance', NULL, 'Fogo'),
(1846, 'Projétil de Liberação de Relâmpago: Respiração Violenta', 'O Projétil de Liberação de Relâmpago: Respiração Violenta cria um relâmpago condensado através do punho do usuário. Ao empurrar o punho para frente, um poderoso raio de longo alcance é desencadeado, capaz de atravessar a cabeça de uma grande cobra. Ele também pode ser conduzido através de uma fonte de água para atingir múltiplos alvos.', '雷遁弾・威吹', 'Raiton Dan: Ibuki', 'Projétil de Liberação de Relâmpago: Respiração Violenta', NULL, NULL, NULL, 'Longo alcance', NULL, 'Relâmpago'),
(1847, 'Projétil de Liberação de Vento: Repulsão', 'Utilizando o Kote acoplado em seu braço, Katasuke Tōno estende seu braço e cria uma onda de vento capaz de derrubar uma pessoa. No entanto, se o usuário não possuir uma boa base, ele é quem é derrubado.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1848, 'Projétil de Liberação de Vento: Shuriken', 'É uma técnica utilizada por Boruto, que mistura o shurikenjutsu que ele aprendeu com o Sasuke e o conceito de transformação da natureza que ele aprendeu com Konohamaru.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, 'Vento'),
(1849, 'Projétil de Liberação de Água: Orca', 'O Projétil de Liberação de Água: Orca libera uma enchente de água que transborda por todo o campo de batalha, restringindo os movimentos dos oponentes com a intensidade das ondas que a técnica produz. Essa técnica pode ser combinada com uma técnica de relâmpago, a fim de eletrocutar seu alvo.', '水遁弾・王琉華', 'Suiton Dan: Oruka', 'Projétil de Liberação de Água: Orca', NULL, NULL, NULL, 'Médio alcance', NULL, 'Água'),
(1850, 'Projétil do Ataque Suicida de Vento Demoníaco', 'Naruto cria um clone da sombra que ele agarra e transforma em uma fūma shuriken que ele lança em seu adversário. Após cumprir o seu objetivo a shuriken volta para a sua forma de clone das sombras e ataca o oponente desprevenido.', '風魔特攻弾', 'Fūma Tokkōdan', 'Projétil do Ataque Suicida de Vento Demoníaco', 'Bomba do Demônio de Vento', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(1851, 'Projétil do Clarão da Besta com Cauda', 'Naruto, enquanto no Modo de Chakra do Nove-Caudas, realiza com rapidez uma combinação de socos e chutes no oponente, deixando rastros dourados para trás e finalizando com uma rasteira que joga o oponente para cima.', '尾獣閃光弾', 'Bijū Senkōdan', 'Projétil do Clarão da Besta com Cauda', 'Projétil do Flash da Besta com Cauda', NULL, NULL, 'Curto alcance', NULL, NULL),
(1852, 'Projétil do Dragão de Água e Relâmpago', 'Mei Terumī expele água em forma de um dragão e é assistida por A que adiciona sua Liberação de Relâmpago para o dragão de água. A técnica é capaz de entorpecer o alvo para restringir seus movimentos.', '雷水龍弾らいすいりゅうだん', 'Raisuiryūdan', 'Projétil do Dragão de Água e Relâmpago', NULL, 'Ataque-Relâmpago do Dragão Marinho', NULL, 'Longo alcance', NULL, NULL),
(1853, 'Projétil do Furacão da Folha', 'O usuário, estando no ar, acerta um poderoso chute caído, o qual envia o oponente voando aos ares.', '木ノ葉疾風弾', 'Konoha Shippūdan', 'Projétil do Furacão da Folha da Árvore', 'Projétil do Furacão da Folha', NULL, NULL, 'Curto alcance', NULL, NULL),
(1854, 'Projétil do Vendaval', 'Após ativar o nível dois do Selo Amaldiçoado do Céu, Sasuke pula no ar e começa a girar em direção do inimigo, podendo infringir dano. Para um dano adicional, Sasuke pode usar suas unhas para dilacerar o oponente enquanto gira, bem como alternar com outros golpes provenientes das vantagens concedidas pelo seu selo amaldiçoado.', '烈風弾', 'Reppūdan', 'Projétil do Vendaval', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1855, 'Projétil do Óleo de Sapo', 'O usuário converte seu chakra em um óleo extremamente pegajoso e cospe isso como uma enorme massa. Quando este óleo atinge o alvo, todo o seu corpo é coberto, e impede o seu movimento. Simplesmente lavar o óleo com água regular não seria fácil. Além disso, se uma técnica de Liberação de Fogo é lançada no alvo após esta técnica ser utilizada, ele estará envolto em chamas em um instante.', '蝦蟇油弾', 'Gamayudan', 'Projétil do Óleo de Sapo', NULL, 'Bola de Óleo do Sapo', 'Bomba do Óleo de Sapo', 'Curto a Médio alcance', 'Rank C', NULL),
(1856, 'Proteção Contra Genjutsu', 'O usuário pode usar esta técnica para colocar bloqueios mentais na mente do alvo que contém informações valiosas e, que foi capturado e interrogado. As barricadas parecem tradicionais portas estilizadas japonesas de correr. De acordo com Inoichi Yamanaka, é muito eficaz contra leitura mental ou qualquer ataque que venha afetar a mente do indivíduo em si.', '幻術プロテクト', 'Genjutsu Purotekuto', 'Proteção Contra Técnica de Ilusão', NULL, NULL, 'Bloqueio de Genjutsu', NULL, NULL, NULL),
(1857, 'Proteção de Oito Trigramas Cento e Vinte e Oito Palmas', 'Esta técnica é meramente uma versão incrivelmente melhor da Proteção de Oito Trigramas Sessenta e Quatro Palmas. Entretanto, esta técnica transmite um estouro sensacional e assim consegue estilhar a esfera maciça feita anteriormente e instantaneamente a técnica aumenta seu tamanho e aumenta seu nível letal e seu nível de arruinamento.', '守護八卦百二十八掌しゅごはっけひゃくにじゅうはちしょう', 'Shugohakke Hyaku Nijūhachi Shō', 'Proteção de Oito Trigramas Cento e Vinte e Oito Palmas', 'Guardiã de Oito Trigramas Cento e Vinte e Oito Palmas', NULL, NULL, 'Curto à Médio alcance', NULL, NULL),
(1858, 'Proteção de Oito Trigramas Sessenta e Quatro Palmas', 'Criada por Hinata, esta é uma técnica altamente versátil que proporciona capacidades ofensivas e defensivas em que ela emite um fluxo constante de chakra de suas mãos, formando lâminas muito finas e afiadas. Enquanto estiver usando esta técnica ofensivamente, Hinata molda o chakra em finas e afiadas lâminas flexíveis, permitindo a ela atacar centenas de alvos com extrema precisão e pode cortar alvos menores, como as abelhas, em pedaços. Quando usada de forma defensiva, Hinata cria lâminas muito maiores, fortes e flexíveis em forma de um arco de chakra que se espalham completamente por todo o seu alcance de ataque, criando efetivamente uma barricada entre ela e seus adversários, rejeitando até mesmo o maior dos adversários.', '守護八卦六十四掌しゅごはっけろくじゅうよんしょう', 'Shugohakke Rokujūyon Shō', 'Oito Trigramas: Proteção de 64 Golpes', '8 Trigramas 64 Golpes de Defesa', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1859, 'Prática de Caminhada na Superfície da Água', 'Este método de treinamento é usado para obter um melhor controle de chakra. Para fazer isso, o utilizador tem de emitir um fluxo constante de chakra a partir do fundo dos seus pés e usando a força repelente para caminhar sobre a superfície da água. Esta técnica é muito mais difícil de dominar que a Prática de Escalada em Árvore, porque a quantidade de chakra que é necessária muda constantemente.', '水面歩行の業', 'Suimen Hokō no Gyō', 'Prática de Caminhada na Superfície da Água', NULL, NULL, NULL, NULL, NULL, NULL),
(1860, 'Prática de Concentração da Folha', 'Este método de treinamento é feito colocando uma folha sobre a testa de uma pessoa e fazendo-a dirigir todo o seu chakra para a folha, usando-a como um ponto focal.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1861, 'Prática de Escalada em Penhasco', 'Este é um método de treinamento que envolve o escalamento em um penhasco íngreme ou alto utilizando apenas um braço. Este treinamento se destina a melhorar a força e disciplina do usuário.', '崖登り業がけのぼりぎょう', 'Gake Noborigyō', 'Prática de Escalada em Penhasco', NULL, NULL, NULL, NULL, NULL, NULL),
(1862, 'Prática de Escalada em Árvore', 'Prática de Escalada em Árvore é um método de treinamento utilizado para obter mais habilidades com o controle de chakra. Este treinamento envolve focalizar uma quantia fixa de chakra para o fundo do seus pés, e usar isso para subir em uma árvore sem utilizar as mãos. Se o fluxo de chakra é muito fraco, o usuário perderá sua posição na árvore e cairá. Se ele for muito forte, o usuário será empurrado para longe da árvore, fazendo com que a árvore quebre em torno do ponto de contato e o usuário cairá.', '木登り修業', 'Kinobori Shūgyō', 'Prática de Escalada em Árvore', NULL, NULL, 'Jutsu de Escalada em Árvore', NULL, NULL, NULL),
(1863, 'Prática de Marionete Humana', 'Esta é uma pratica desenvolvida por Sasori, durante seu tempo em Sunagakure, permitindo-lhe criar marionetes a partir de cadáveres humanos. Desde sua morte, no entanto, parece ter caído no esquecimento.', '人傀儡の業ひとくぐつのぎょう', 'Hitokugutsu no Gyō', 'Prática de Marionete Humana', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1864, 'Pulando no Buraco de Fogo e Vento', 'Tsunade soca o chão, criando um grande terremoto. Como cobertura, Orochimaru invoca três Rashōmon, criando então em uma forma de triângulo, deixando o oponente sem saída. Então, Orochimaru e Jiraiya usam fogo e vento para criar um grande turbilhão de fogo. Tsunade pula nas chamas e, depois de ativar o Selo Yin, dá um soco tão poderoso que quebra os portões na explosão, causando uma grande explosão.', '火跳風穴かちょうかざあな', 'Kachō Kazaana', 'Pulando no Buraco de Fogo e Vento', 'Buraco de Vento Explosivo', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1865, 'Pular por Cima!', 'Konohamaru dá um salto para trás enquanto Moegi e Udon seguram o oponente. Em seguida, os dois empurram o oponente para que Konohamaru pule por cima dele, acertando-lhe um chute que o manda longe.', '飛び越えろ!', 'Tobikoero!', 'Pular por Cima!', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1866, 'Punhalada da Agulha Negra', 'Pain corre até seu oponente e apunhala-o com sua Manifestação de Receptores de Chakra, revertendo os seus controles.', '黒針突', 'Kokushintō', 'Punhalada da Agulha Negra', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1867, 'Punho Adamantino', 'O Punho Adamantino (金剛拳, Kongōken) é um estilo de luta caracterizado por um controle de chakra preciso e minucioso. Enquanto alguns podem vê-la simplesmente como força sobre-humana, esta técnica é realmente uma aplicação de ninjutsu médico.', '金剛拳こんごうけん', 'Kongōken', 'Punho Adamantino', 'Punho de Ouro Maciço (純金拳, Junkinken)', NULL, NULL, 'Curto alcance', NULL, 'Cachorro → Cobra → Rato → Carneiro[7]'),
(1868, 'Punho Arhat', 'O Punho Arhat (羅漢拳, Rakanken) é um estilo de combate que se baseia em simples ataques físicos aumentados com a grande força de Jirōbō.', '羅漢拳', 'Rakanken', 'Punho Arhat', NULL, 'Punho do Rakan', 'Palma Esmagadora', 'Curto alcance', 'Rank C', NULL);
