INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(1869, 'Punho Embriagado', 'Punho Embriagado é um estilo único de combate, onde, de acordo com Might Guy, \'quanto mais você bebe, mais bêbado que você fica, e mais forte você se torna\'. Esta não pode ser aprendida, ensinada, nem refinada através de treinamento e prática. Lutadores do Punho Embriagado não são simplesmente bêbados: eles se tornam potências humanas que atacarão de maneira imprevisível com uma fúria explosiva e sem segundas intenções sobre suas ações. Uma vez bêbado, o usuário perderá sua capacidade de conhecer o amigo do inimigo e atacará quem quer que esteja em sua volta.', '酔拳', 'Suiken', 'Punho Embriagado', NULL, 'Punho Bêbado', 'Punho Débil', 'Curto alcance', 'Rank C', NULL),
(1870, 'Punho Emergente do Lobo Canino', 'O usuário atinge o oponente com uma série de socos e golpes, terminando com um soco mais poderoso e crescente, levando o oponente para o ar.', '狼牙昇拳ろうがしょうけん', 'Rōga Shōken', 'Punho Emergente do Lobo Canino', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1871, 'Punho Enjoado', 'Semelhante ao Punho Embriagado, o usuário aproveita o mal estar causado a ele e começa a dar-lhes movimentos com um grau de imprevisibilidade em combate. O usuário também pode vomitar em seu adversário para fins de distração.', '船酔い拳', 'Funeyoi Kobushi', 'Punho Enjoado', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1872, 'Punho Forte', 'O Punho Forte (剛拳, Gōken) é o estilo de luta característico de Might Guy e Rock Lee, cujo objetivo é causar danos externos e quebrar ossos. Posteriormente foi ensinado a Metal Lee por seu pai. É exatamente o oposto do Punho Gentil, que é usado principalmente pelo clã Hyūga. Este estilo de luta envolve esmagar o seu adversário, e geralmente é usado somente por shinobi extremamente poderosos e dominadores em seus atributos físicos.', '剛拳ごうけん', 'Gōken', 'Punho Forte', NULL, 'Punho Feroz', NULL, 'Curto alcance', NULL, NULL),
(1873, 'Punho Gentil', 'O Punho Gentil é uma forma de combate corpo-a-corpo, utilizada principalmente por membros do clã Hyūga.', '柔拳じゅうけん', 'Jūken', 'Punho Gentil', NULL, 'Punho Leve', 'Punho Suave', 'Curto alcance', NULL, NULL),
(1874, 'Punho Gentil: Agulha do Tenketsu', 'Focando num único ponto de chakra, o usuário desfere um golpe com dois dedos no oponente, em uma tentativa de afetar o chakra que está sendo usado e interromper seu fluxo. Esse golpe foi poderoso o suficiente para inutilizar e danificar o braço de Hinata Hyūga durante as preliminares dos Exames Chūnin e para cortar as teias de Kidōmaru. No anime, essa técnica também foi útil para libertar Gaara e Fū das correntes de Hōichi.', '柔拳・点穴針じゅうけん・てんけつしん', 'Jūken: Tenketsushin', 'Punho Gentil: Agulha dos Pontos de Pressão', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1875, 'Punho Quebra-Gelo', 'O Punho Quebra-Gelo (砕氷拳, Saihyōken) é uma forma de combate utilizada por Rahyō, onde ele envolve seu punho com gelo para bloquear ataques de taijutsu que venha receber. Com esta técnica, Rahyō foi capaz de parar com sucesso o Furacão da Folha de Might Guy.', '砕氷拳さいきょうけん', 'Saihyōken', 'Punho Quebra-Gelo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1876, 'Punho Silencioso', 'O Punho Silencioso (無音の拳, Muon no Ken) é um estilo de luta único de Shira. Ele é capaz de tornar seus movimentos completamente silenciosos envolvendo-se com chakra. Neji Hyūga compara seu uso com as penas de uma coruja, como o manto de chakra faz com que os movimentos de Shira sejam praticamente imprevisíveis.', '無音の拳むおんのけん', 'Muon no Ken', 'Punho Silencioso', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1877, 'Punho Sísmico', 'Após uma série de socos no oponente, em seguida, o usuário salta para o ar e distribui um soco para baixo, finalizando a técnica.', '空震拳', 'Kūshinken', 'Punho Sísmico', 'Punho do Céu Trêmulo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1878, 'Punho Vendaval de Linha Dura do Estilo-Nadeshiko', 'Cobrindo a própria mão com chakra de natureza de vento, o usuário ataca o inimigo. A força do ataque é grande o suficiente para produzir uma onda de choque poderosa, que pode causar visíveis fissuras sem contato direto.', '撫子流硬派烈風拳なでしこりゅうこうはれっぷうけん', 'Nadeshikoryū Kōha Reppūken', 'Punho Vendaval de Linha Dura do Estilo-Nadeshiko', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1879, 'Punho de Arenito', 'Com o Punho de Arenito (砂岩拳, Saganken) o usuário é capaz acumular areia em seu punho, na qual ele pode moldar para criar ferramentas sólidas, como uma marreta ou um escudo perfeito para um combate de curta distância. Os equipamentos criados são bastante rígidos e de longa duração.', '砂岩拳', 'Saganken', 'Punho de Arenito', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1880, 'Punho de Ferro Negro', 'Shinki cria duas mãos massivas compostas de areia de ferro que ele manipula através de campos eletromagnéticos. O usuário pode usá-las em volta se si objetivando proteger-se de ataques, bem como pode usá-las para atacar um alvo a distância.', '黒鉄乃拳', 'Kurogane no Kobushi', 'Punho de Ferro Negro', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1881, 'Punho de Pistão', 'Esta técnica se baseia em um impulso destrutivo que segue um ataque muito rápido e efetivo.', '活塞拳かっそうくけん', 'Kassokuken', 'Punho de Pistão', 'Pilha de Martelo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1882, 'Punho de Tentáculos', NULL, '触手拳', 'Shokushuken', 'Punho de Tentáculos', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1883, 'Punho do Renascimento', 'Ao reunir chakra escuro em ambas as mãos, o usuário pode desencadear uma onda de choque poderosa e concentrada que, quando atingir uma pessoa de perto, irá causar tremendos danos internos.', '活性拳', 'Kasseiken', 'Punho do Renascimento', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1884, 'Punição Celestial', 'Esta é uma técnica na qual o usuário cobre seu corpo com múltiplos papéis explosivos para cometer suicídio. Pode ser usada como uma tática para assassinar o inimigo próximo.', '天誅てんちゅう', 'Tenchū', 'Punição Celestial', NULL, NULL, 'Punição Divina', 'Curto a Médio alcance', NULL, NULL),
(1885, 'Punição Divina', 'Trata-se de série de combinações criadas por Hidan para afligir dano a seu oponente, envolvendo socos, pontapés e ataques com a Foice de Lâmina Tripla.', '神罰昇天', 'Shinbatsu Shōten', 'Punição Divina', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1886, 'Pó de Escama Explosiva', 'Usando as asas de Chōmei, Fū ataca seu oponente e voa pelo ar enquanto libera escamas, em seguida, transforma-se totalmente em Chōmei e proporciona um ataque da semelhante à broca em seu oponente. Pouco antes do impacto, Fū encaixa o aparelho bucal de Chōmei, criando uma faísca que inflama o tornado cheio de escama criado de sua rotação, resultando em uma enorme explosão.', '鱗粉爆砕りんぷんばくさい', 'Rinpun Bakusai', 'Pó de Escama Explosiva', 'Explosão da Escama', NULL, NULL, 'Curto alcance', NULL, NULL),
(1887, 'Pé Dançante', 'Após desferir vários socos e chutes no oponente, Naruto concentra chakra em seu punho e acerta outro soco.', '舞踏脚', 'Butōkyaku', 'Pé Dançante', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1888, 'Pé Espiritual', 'Shizune gira e chuta seus adversários e envia-os voando para trás.', '心脚', 'Shinkyaku', 'Pé Espiritual', 'Pontapé Espiritual', NULL, NULL, 'Curto alcance', NULL, NULL),
(1889, 'Pé da Cauda do Dragão', 'Zabuza corta o oponente ao meio e depois dá um giro chutando o oponente à distância.', '龍尾脚', 'Ryūbikyaku', 'Pé da Cauda do Dragão', 'Pontapé da Cauda do Dragão', NULL, NULL, 'Curto alcance', NULL, NULL),
(1890, 'Pé da Dor Celestial', 'Esta técnica é simplesmente um pontapé em forma de machado. Tsunade concentra sua incrível força em seu calcanhar e acerta o inimigo. Foi mostrado que, quando ela atinge o solo resulta em uma cratera gigante que pode até matar um adversário preso em sua zona de ataque imediato.', '痛天脚つうてんきゃく', 'Tsūtenkyaku', 'Pé da Dor Celestial', 'Chute da Dor do Céu', 'Golpe do Calcanhar Celestial', NULL, 'Curto alcance', 'Rank C', NULL),
(1891, 'Pétalas Dançantes do Jardim de Flores', 'Ino desliza em direção ao oponente, com chakra imbuído em seus pés e nas flores roxas ao seu redor, e dá uma joelhada contra o oponente no ar. Ela, então, gira graciosamente e desfere um pontapé reforçado com chakra no rosto do adversário.', '花弁舞う花園', 'Hanabiramau Hanazono', 'Pétalas Dançantes do Jardim de Flores', 'Pétalas Dançantes da Flor do Jardim', NULL, NULL, 'Curto alcance', NULL, NULL),
(1892, 'Pássaro Médio Rotativo', 'Esta é uma técnica de enganação utilizada por Sasuke Uchiha, para surpreender o adversário.', '媒鳥旋', 'Baichōsen', 'Pássaro Médio Rotativo', 'Rotação do Pássaro Médio', NULL, NULL, 'Curto alcance', NULL, NULL),
(1893, 'Quarteto do Som: Invocação: Cerco de Formação das Quatro Bestas', 'Cada um dos membros do Quarteto do Som invoca suas respectivas invocações para barricar seus oponentes. Enquanto o Triplo Rashōmon faz da evasão difícil, os Doki e o Golem de Prisão de Terra atacam de frente, enquanto Kyodaigumo ataca de cima.', '音隠れ四人衆・口寄せ・四獣包囲の陣おとがくれよんいんしゅう・くちよせ・しじゅうほういのじん', 'Otogakure Yoninshū: Kuchiyose: Shijū Hōi no Jin', 'Quarteto do Som: Invocação: Cerco de Formação das Quatro Bestas', NULL, NULL, 'Quatro Ninja do Som: Jutsu de Invocação: Ângulo de Formação das Quatro Bestas', NULL, NULL, NULL),
(1894, 'Quatro Estações Turbulentas', 'Itachi e Shisui atacam seu oponente com a Liberação de Fogo: Técnica do Grande Dragão de Fogo. Através do fogo ardente, Itachi lança uma enxurrada de kunai. Depois, Shisui aparece atrás do oponente, agarrando uma das kunai e, em sintonia com Itachi, lança uma onda de ataques com uma kunai em cada mão.', '乱れ雪月花みだれせつげっか', 'Midare Setsugekka', 'Turbulenta Neve, Lua e Flores', 'Quatro Estações Turbulentas', NULL, NULL, 'Curte a Médio alcance', NULL, NULL),
(1895, 'Quatro Formações Vermelhas Yang', 'Uma formação poderosa de barreira notada sendo usada apenas por aqueles de nível Kage, sendo quatro shinobi de pé em uma formação quadrada, erguendo uma grande barreira que é de cor vermelha. Foi indicada sendo várias vezes mais forte do que a Formação das Quatro Chamas Violeta, e é altamente maleável. Quando agredida pela Bola da Besta com Cauda Instantânea do Dez-Caudas, a barreira simplesmente é distorcida a sua forma, forçando o peso do ataque para cima enquanto ela ficou completamente intacta. Seus usuários podem criar aberturas dentro da barreira para permitir a passagem.', '四赤陽陣', 'Shisekiyōjin', 'Quatro Formações Vermelhas Yang', NULL, 'Arte Ninja dos Quatro Sóis Vermelhos', 'Arte Ninja: Barreira dos Quatro Sóis Escarlate', 'Longo alcance', 'Rank A', 'Carneiro + Carneiro + Carneiro + Carneiro simultaneamente'),
(1896, 'Quebra de Chifre', 'Kokuō dirige seus chifres diretamente no oponente usando uma poderosa carga. A força do ataque foi tão grande que ele pode enviar uma outra besta com cauda voando a uma distância considerável e causando grandes danos.', '角折り', 'Tsunoori', 'Investida de Chifres', 'Chifre Destruidor', 'Quebra-Chifre', NULL, 'Curto alcance', NULL, NULL),
(1897, 'Queda Celestial do Colapso Gêmeo', 'Após se separarem, Sakon e Ukon encurralam o oponente. Em seguida, eles agarram o oponente firmemente e em um movimento rápido, eles trocam de lugar para socar o oponente e lançá-lo no ar. Enquanto o oponente está no ar, eles saltam, e o agarram novamente, girando em uma velocidade inexplicável e soltando o oponente no último minuto, fazendo ele cair contra o solo.', '双極落天', 'Sōgoku Rakuten', 'Queda Celestial do Colapso Gêmeo', 'Queda do Colapso Gêmeo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1898, 'Queda da Aranha Gigante', 'Kidōmaru invoca Kyodaigumo em cima de seu oponente, enviando um sinal para que caia em seguida e esmague-o.', '大蜘蛛落とし', 'Ōkumo Otoshi', 'Queda da Aranha Gigante', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1899, 'Queda da Corrente', 'Obito salta no ar, e dá um pontapé e dois socos em seu oponente, terminando com um ataque de suas correntes.', '落鎖', 'Rakusa', 'Queda da Corrente', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1900, 'Queda da Flor de Damasco', 'Kurenai ataca seu oponente com dois chutes e um golpe de cotovelo, em seguida, o atacando com uma poderosa enxurrada de flores de cerejeira que estouram ao entrar em contato com o corpo do oponente, bombardeando-o em seguida.', '杏咲垂', 'Anshōsui', 'Queda da Flor de Damasco', 'Inclinamento da Flor de Damasco', NULL, NULL, 'Curto alcance', NULL, NULL),
(1901, 'Queda da Folha', 'O usuário desfere um chute descendente, forte o suficiente para desmoronar o ambiente ao seu redor.', '木ノ葉落とし', 'Konoha Otoshi', 'Queda da Folha', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1902, 'Queda da Guilhotina', 'Depois de cobrir-se em seu Modo de Chakra de Liberação de Relâmpago, A salta no ar acima de seu oponente e executa um chute baixo, usando o impulso da queda para aumentar o poder por trás do ataque.', '義雷沈怒雷斧', 'Girochin Doroppu', 'Relâmpago Íntegro Afundando a Raiva do Machado Relâmpago', 'Conduzir', 'Guilhotina Relâmpago', 'Queda da Guilhotina', 'Curto alcance', NULL, NULL),
(1903, 'Queda do Céu em Espiral', 'Sakura gira Naruto em espiral e, em seguida, lança-o contra o oponente com um Rasengan.', '天空螺旋落としてんくうらせんおとし', 'Tenkū Rasen Otoshi', 'Queda do Céu em Espiral', 'Bomba Rasen', NULL, NULL, NULL, NULL, NULL),
(1904, 'Queda do Falcão Peregrino', 'O usuário agarra um oponente em queda por seus tornozelos, envolvendo suas pernas em volta de sua cintura, e o leva de cabeça no chão, semelhante à Lótus Frontal.', 'ハヤブサ落とし', 'Hayabusa Otoshi', 'Queda do Falcão Peregrino', 'Queda da Hayabusa', NULL, 'Queda do Falcão', 'Curto alcance', NULL, NULL),
(1905, 'Queda do Impulso Envoltório', 'Em sua forma de quatro caudas, Naruto pula no ar e envolve suas caudas em torno de si. Ele, então, cai no chão, girando como um piledriver. Este movimento pode bloquear técnicas de curto alcance.', '降纏突', 'Kōten Tsuki', 'Queda do Impulso Envoltório', 'Impulso Caído', NULL, NULL, 'Curto alcance', NULL, NULL),
(1906, 'Queima de Arquivo', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Tigre[1]'),
(1907, 'Rachadura no Chão', 'A Rachadura no Chão é um taijutsu utilizado por Tsunade através de sua força sobre-humana.', '地割れじわれ', 'Jiware', 'Rachadura no Chão', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1908, 'Raio', 'Killer B salta no ar com suas sete espadas e atinge o oponente enquanto encontra-se revestido por chakra de relâmpago.', '雷斗忍具菩流斗', 'Raitoningu Baruto', 'Combate da Rosa Fluente de Equipamento Ninja de Combate Relâmpago', 'Raio', NULL, NULL, 'Curto alcance', NULL, NULL),
(1909, 'Raio Divisor do Céu e da Terra', 'O usuário acumula chakra de natureza relâmpago em sua mão e dispara uma série de projéteis que, após colidirem contra seu alvo, liberam poderosas descargas elétricas em forma de raios.', '雷分割天地らいぶんかつてんち', 'Raibunkatsu Tenchi', 'Raio Divisor do Céu e da Terra', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1910, 'Raio Naruto', 'Mecha-Naruto trava a mira seus oponentes e dispara feixes de energia de seus olhos neles.', 'ナルトビーム', 'Naruto Bīmu', 'Raio Naruto', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1911, 'Raio da Lança do Céu do Nove-Caudas', 'Após absorver o chakra da Lança do Céu, as habilidades do Clone do Nove-Caudas são ampliadas e o clone pode disparar raios poderosos de sua cabeça semelhante a um dragão. Esses raios eram poderosos o suficiente para cortar uma rocha sólida e atravessá-la, ferindo até mesmo Naruto, mesmo Naruto estando completamente transformado em Kurama.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1912, 'Rajada Súbita da Folha', 'Usando ataques simples, mas eficazes, o usuário começa a girar rapidamente sobre seu eixo em apenas uma perna, ganhando balanço com a outra que está erguida, libertando sucessivos chutes rotativos ao inimigo em uma rápida sucessão.', '木ノ葉突風', 'Konoha Toppū', 'Rajada Súbita da Folha', NULL, NULL, 'Rajada da Folha', 'Curto alcance', NULL, NULL),
(1913, 'Rajada de Meteoro do Dragão', 'O usuário salta altamente para bater o seu adversário para baixo com ambos pés. Uma onda de choque poderosa é liberada a partir do local do impacto.', '駝鳥流星爆', 'Dachō Ryūseibaku', 'Rajada de Meteoro do Dragão', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1914, 'Rasengan', 'O Rasengan (螺旋丸, Esfera Espiral) é uma técnica poderosa de Rank A inventada pelo Quarto Hokage, que a criou observando a formação da Bola da Besta com Cauda. É a manipulação de chakra puro, em forma de uma esfera espiral de chakra. Minato Namikaze passou três anos desenvolvendo o Rasengan, seu plano era tomar a forma de transformação de chakra para o \'nível mais alto possível\', e então combiná-lo com sua afinidade de chakra da natureza, criando uma técnica que não precisava da ajuda de selos de mão e controle. Ele morreu antes que ele pudesse fazer isso, mas mesmo com só a Transformação da Forma o Rasengan ainda é muito útil, poderoso, versátil, e uma difícil técnica de aprender e ensinar.', '螺旋丸らせんがん', 'Rasengan', 'Esfera Espiral', NULL, NULL, 'Rasengan: Esfera Espiral de Chakra', 'Curto alcance', 'Rank A', NULL),
(1915, 'Rasengan Desaparecedor', 'O Rasengan Desaparecedor é uma técnica desenvolvida acidentalmente por Boruto Uzumaki. Ela consiste em uma forma de Rasengan mesclada com a Liberação de Relâmpago.', '消える螺旋丸きえるらせんがん', 'Kieru Rasengan', 'Esfera Espiral Desaparecedora', NULL, NULL, NULL, 'Curto ou longo alcance', NULL, NULL),
(1916, 'Rasengan Duplo', 'Com o oponente imobilizado ou distraído, Naruto avança contra ele tenta atingir o Rasengan de um lado, enquanto Jiraiya, ou um clone de Naruto, cria rapidamente outro Rasengan para colidir com o de Naruto no oponente, causando uma explosão de chakra.', NULL, NULL, 'Esferas Espirais Duplas', 'Rasengan Duplo', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1917, 'Rasengan Duplo Rápido', 'Primeiramente, Jiraiya realiza um único Rasengan, deformando o chão. Em seguida, o Rasengan entra em contato com o oponente, e então ele cria mais um Rasengan, combinando os dois e formando um enorme Rasengan. Devido ao enorme poder, o solo começa a trincar e logo é destruído.', '双迅螺旋丸そうじんらせんがん', 'Sōjin Rasengan', 'Esfera Espiral Dupla Rápida', 'Rasengan Duplo Rápido', NULL, NULL, 'Curto alcance', NULL, NULL),
(1918, 'Rasengan Final Supremo', 'Naruto funde seu Rasengan com o de Minato para criar um Rasengan que perfura através do oponente causando grandes danos. O Rasengan Final Supremo tem vários fluxos de chakra em espiral em torno do centro, além de diversos fluxos cobrindo completamente os corpos dos usuários, os quais são capazes de desviar ataques. A esfera em si parece instável, com a forma flutuante descontroladamente ao contrário da forma de esfera sólida de outras técnicas do Rasengan.', '太極螺旋丸', 'Taikyoku Rasengan', 'Esfera Espiral Final Suprema', NULL, NULL, NULL, NULL, NULL, NULL),
(1919, 'Rasengan Gelel', 'É um Rasengan infundido com a energia da Pedra de Gelel. Ele apenas é visto sendo usado como parte do Rasengan Serial em combinação com um Yōko Rasengan. Em vez de ser criado com a Técnica dos Clones das Sombras, Temujin simplesmente coloca a mão sobre a mão direita de Naruto. Seu poder se presume ser igual ao poder de fogo de um Yōko Rasengan, pois ele pode queimar uma parte do corpo do oponente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1920, 'Rasengan Kagutsuchi', 'Naruto e Sasuke formam a Liberação de Calor: Auréola do Furacão de Flecha Negra de Estilo Zero de menor escala e a lançam no oponente, atirando-o para longe. Em seguida, Sasuke aparece atrás do oponente com seu Ame-no-tejikara e lhe dá um chute que o lança para o alto. Logo, Sasuke manifesta um antebraço com seu Susanoo e forma um Takemikazuchi em sua mão, enquanto Naruto acumula chakra em seu punho. Ambos correm em direção ao seu alvo e, antes que ele caia no chão, o acertam com suas respectivas técnicas, causando uma explosão que pode ser vista do espaço.', '螺旋丸加具土命', 'Rasengan Kagutsuchi', 'Esfera Espiral da Ferramenta Adicionada ao Senhor da Terra', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1921, 'Rasengan Planetário', NULL, '惑星螺旋丸', 'Wakusei Rasengan', 'Esfera Espiral Planetária', 'Rasengan Planetário', NULL, NULL, 'Curto alcance', 'Rank A', NULL),
(1922, 'Rasengan Serial', 'O Rasengan Serial é simplesmente uma versão em que se utiliza as duas mãos, sendo que cada uma contendo um Rasengan normal.', '螺旋連丸', 'Rasenrengan', 'Esferas Espirais Seriais', 'Barragem Rasengan', NULL, NULL, 'Curto alcance', 'Rank A', NULL),
(1923, 'Rasengan: Deslocamento Brilhante', 'Com o auxilio de Kurama, Naruto é capaz de criar um Rasengan em sua mão e liberar sua energia na forma de um feixe, que ele usa para impulsionar-se para o alto em rápida velocidade ou atingir um inimigo à distância.', '螺旋丸・瞬輝', 'Rasengan: Shunki', 'Esfera Espiral: Deslocamento Brilhante', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1924, 'Rasengan: \'Unissonância\'', 'Boruto e Kawaki ativam seus selos do Karma em conjunto e, graças à ressonância entre os dois, Boruto é capaz de elevar o poder explosivo de seu Rasengan em várias vezes, o suficiente para destruir parte considerável do corpo de seu oponente.', '螺旋丸・「連奏」', 'Rasengan: \'Tsurane\'', 'Esfera Espiral: \'Unissonância\'', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1925, 'Rasengan da Besta com Cauda', 'O Rasengan da Besta com Cauda é uma técnica incompleta de Naruto Uzumaki. Ela é semelhante ao Rasengan e à Bola da Besta com Cauda.', '尾獣螺旋丸', 'Bijū Rasengan', 'Esfera Espiral da Besta com Cauda', NULL, NULL, 'Rasengan da Besta com Cauda', NULL, NULL, NULL),
(1926, 'Rasengan da Coragem', 'Lembrando das palavras de Jiraiya sobre nunca abandonar o seu caminho ninja, Naruto cria um Rasengan totalmente carregado, mas aumenta sua massa e força bruta. Ele tem mostrado energia suficiente para detonar uma parede inteira de uma fortaleza e até mesmo dizimá-la em questão de minutos. Ao contrário de um Rasengan normal, que desaparece depois de bater sua meta, esta versão não desaparece até que Naruto termine de destruir toda a fortaleza.', 'ど根性螺旋丸', 'Dokonjō Rasengan', 'Esfera Espiral da Coragem', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1927, 'Rasengan da Dança da Nuvem de Areia', 'Gaara usa Garoa de Areia para distrair e nocautear o oponente enquanto Naruto finaliza com um combo e o Rasengan, atacando o inimigo no chão.', '砂塵舞う螺旋丸', 'Sajinmau Rasengan', 'Dança da Esfera Espiral na Nuvem de Areia', 'Rotação de Areia do Rasengan', NULL, NULL, 'Curto alcance', NULL, NULL),
(1928, 'Rasengan da Lua Crescente', 'Naruto combina o Rasengan com os raios de uma lua crescente para mergulhá-lo em uma luz branca ofuscante. Isso forma uma lua crescente branca com uma estrela no Rasengan, tornando o ataque maior e mais poderoso.', '三日月螺旋丸', 'Mikazuki Rasengan', 'Esfera Espiral da Lua Crescente', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1929, 'Rasengan da Raposa Demoníaca', 'O Rasengan da Raposa Demoníaca é forma primorada do Rasengan utilizado por Naruto mediante o uso do chakra de Kurama.', '妖狐螺旋丸', 'Yōko Rasengan', 'Esfera Espiral da Raposa Demoníaca', 'Rasengan Vermelho', NULL, NULL, 'Curto alcance', NULL, NULL),
(1930, 'Rasengan de Sete Cores', 'O Rasengan de Sete Cores é uma variação do Rasengan, colorido pela luz refletida do gerador-espelho no País da Neve. Esta coloração fez o chakra utilizado no Rasengan se parecer com um arco-íris. A julgar pela distância que ele faz o oponente voar, pode ser que o chakra multicolorido não altere somente a aparência da técnica, mas de alguma forma aumente a sua potência também.', '七色の螺旋丸', 'Nanairo no Rasengan', 'Esfera Espiral de Sete Cores', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1931, 'Rasengan do Pai com Filho', 'O Rasengan do Pai com Filho é um ninjutsu cooperativo pelo qual dois shinobi, na maioria das vezes um usuário e seu filho como o nome sugere, combinam seus Rasengan em uma esfera massiva, que é então empurrada ao seu alvo.', '親子螺旋丸おやこらせんがん', 'Oyako Rasengan', 'Esfera Espiral do Pai com Filho', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1932, 'Rasenshuriken Planetário da Besta com Cauda', 'Naruto começa com um soco de vento, mandando o oponente para o ar. Ele, então, se transforma em seu Modo Kurama e prepara um Rasenshuriken de tamanho gigante e uma Bola da Besta com Cauda. Ele joga o Rasenshuriken e dispara quatro Bolas da Besta com Cauda em satélites em volta da shuriken. Explode à medida que atinge o oponente, resultando em um enorme domo de energia azul.', '尾獣惑星螺旋手裏剣', 'Bijū Wakusei Rasenshuriken', 'Shuriken Espiral Planetária da Besta com Cauda', 'Rasen Shuriken Planetário de Bijū', NULL, NULL, 'Longo alcance', NULL, NULL),
(1933, 'Rasenshuriken da Bola de Besta com Cauda', 'O Rasenshuriken da Bola de Besta com Cauda é uma poderosa técnica utilizada por Naruto com a ajuda de Kurama.', '尾獣玉螺旋手裏剣びじゅうだまらせんしゅりけん', 'Bijūdama Rasenshuriken', 'Bola da Besta com Cauda Shuriken Espiral', 'Bomba Rasen Shuriken da Bijū', 'Esfera Bijū Rasenshuriken', NULL, 'Todos os alcances', 'Rank S', NULL),
(1934, 'Rato de Fogo', 'Yugito libera uma das caudas de Matatabi, bate-a para frente e espalha três ratos de fogo que explodem ao contato. No entanto, se os ratos não chegam até o oponente, eles ficam por um tempo parados no chão até que o inimigo toque-os e eles explodam.', '火ねずみひねずみ', 'Hinezumi', 'Rato de Fogo', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1935, 'Receptor Negro', 'Os receptores negros são ferramentas peculiares utilizadas por usuários do Rinnegan. Eles podem ser utilizados para transmitir chakra através de longas distâncias.', '黒い受信機', 'Kuroi Jushinki', 'Receptor Negro', 'Agulha Negra (黒針, Kokushin)', NULL, 'Receptores de Chakra', 'Todos os alcances', NULL, NULL),
(1936, 'Recepção da Espada de Vento Cintilante', 'Shisui acerta alguns golpes em seu oponente e realiza uma sequência de ataques de fogo com sua tantō, lançando o oponente para cima.', '瞬風戴刀しゅんふうたいとう', 'Shunfū Taitō', 'Recepção da Espada de Vento Cintilante', 'Ataque da Espada de Ventania', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1937, 'Redemoinho Violento', 'O usuário produz uma poderosa corrente de vento de sua boca. Esta técnica foi poderosa o suficiente para dissipar vários clones das sombras em um instante.', '烈旋風らっせんぷう', 'Ressenpū', 'Redemoinho Violento', NULL, NULL, 'Vendaval do Leque', 'Todos os alcances', NULL, NULL),
(1938, 'Redemoinho da Folha', 'Equilibrado de cabeça para baixo, o usuário pode usar as pernas para dissipar possíveis nevoeiros ou atacar o inimigo com os pés em movimento.', '木ノ葉つむじ風このはつむじかぜ', 'Konoha Tsumujikaze', 'Redemoinho da Folha', NULL, NULL, NULL, NULL, NULL, NULL),
(1939, 'Redemoinho de Vento do Avestruz', 'Condor usa sua perna para dar um forte chute no estômago do oponente.', '駝鳥旋風撃', 'Dachō Senpūgeki', 'Redemoinho de Vento do Avestruz', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1940, 'Reencarnação Explosiva Localizada', 'A Reencarnação Explosiva Localizada (近傍転生爆, Kinbō Tensei Baku) é uma das poderosas técnicas utilizadas por Toneri Ōtsutsuki.', '近傍転生爆きんぼうてんせいばく', 'Kinbō Tensei Baku', 'Reencarnação Explosiva Localizada', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1941, 'Reencarnação da Marionete', 'Como último recurso, Toneri, enquanto sem olhos, convoca incontáveis Byakugan do Tenseigan de Hamura destruído e infunde-os em si mesmo. Os Byakugan aparentemente lhe concedem um poder imenso, que é reivindicado a ser capaz de levar o mundo ao seu fim.', '傀儡転生くぐつてんせい', 'Kugutsu Tensei', 'Reencarnação da Marionete', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1942, 'Reencarnação da Própria Vida', 'A Reencarnação da Própria Vida foi desenvolvida em Sunagakure pela Brigada de Marionetes. O usuário, dando a sua força de vida para o alvo, usa todo seu chakra como um intermediário.', '己生転生', 'Kishō Tensei', 'Reencarnação da Própria Vida', NULL, NULL, 'Jutsu de Reanimação', 'Curto alcance', 'Rank S', NULL),
(1943, 'Reencarnação da Vida do Dragão', 'A Reencarnação de Vida do Dragão é a técnica suprema do clã de Ryūzetsu.', '龍命転生りゅう いのち てんせい', 'Ryūmei Tensei', 'Reencarnação de Vida do Dragão', NULL, NULL, 'Transferência de Vida', 'Curto alcance', NULL, 'Passaro → Selo de Confronto'),
(1944, 'Reencarnação do Cadáver Vivente', 'Esta é uma técnica que foi desenvolvida por Orochimaru, permitindo-lhe transferir sua alma para o corpo de outra pessoa. Se repetido periodicamente, ele concede a imortalidade ao usuário, desde que o usuário possa encontrar novos corpos hospedeiros e mantê-los vivos. Orochimaru só pode executar esta técnica uma vez a cada três anos. Parece também que, quando os três anos estão no término, ele é forçado a trocar para outro hospedeiro, como o anterior começa a enfraquecer e rejeitá-lo. Presumivelmente, se ele encontrar um recipiente adequado, a substituição de hospedeiros não será mais necessária.', '不屍転生ふしてんせい', 'Fushi Tensei', 'Ritual de Transferência', NULL, 'Reencarnação em Vida', NULL, NULL, 'Rank S', NULL),
(1945, 'Regeneração de Absorção Celular', 'Enquanto no seu estado transformado, Jūgo estende uma protuberância em forma de agulha de seu corpo, inserindo-a no seu alvo, sugando rapidamente o seu chakra juntamente de órgãos e/ou carne cuja ele usa para restaurar seu próprio corpo, bem como chakra. Ele usou esta técnica logo após ser deixado à beira da morte pelo Quarto Raikage. Esta técnica também restaurou seu tamanho normal, como ele anteriormente havia reduzido sua carne.', '再胞吸引', 'Saibō Kyūin', 'Regeneração de Absorção Celular', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1946, 'Regeneração de Ejeção Celular', 'Como originador de Juinjutsu de Orochimaru e possuidor de uma capacidade única de seu clã, Jūgo é capaz de dar sua carne para aqueles que são compatíveis com os referidos selos amaldiçoados, curando assim quaisquer ferimentos que possam ter recebido. Devido a ele estar transferindo suas células para o destinatário, o próprio corpo de Jūgo regride em idade, tornando-se com uma aparência mais jovem.', '再胞排出', 'Saibō Haishutsu', 'Regeneração de Ejeção Celular', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1947, 'Regeneração de Vigor', 'A Regeneração de Vigor (体力回復, Tairyoku Kaifuku) é uma habilidade única de Karin, que ela herdou de sua mãe. Essa habilidade foi muito cobiçada por Kusagakure, que estava tendo muitas baixas durante as guerras e não tinha meios para curar seus feridos.', '体力回復たいりょくかいふく', 'Tairyoku Kaifuku', 'Regeneração de Vigor', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1948, 'Regressão Temporal', 'É uma forma de viagem no tempo desempenhada por Urashiki através de seu Rinnegan exclusivo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1949, 'Rei Lótus Kanzeon', 'Enquanto estiver usando Yamato como um meio, Tobi cria uma estátua enorme, semelhante a técnica de Hashirama Senju, Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras. Ashura Ōtsutsuki utilizou essa técnica na luta contra o seu irmão, com a ajuda do chakra de todos os aldeões da vila. Ao contrário da variante muito maior de Hashirama, no entanto, esta criação não utiliza chakra do senjutsu. O portador deste avatar é capaz de utilizar a multiplicidade das mãos para atacar seus oponentes.', '観世音蓮華王', 'Kanzeon Renge-Ō', 'Rei Lótus Kanzeon', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Juntar as Maos[2]'),
(1950, 'Relógio da Contagem Regressiva do Armagedom', 'Após efetuar um selo de mão, um relógio ultra secreto surge na palma da mão da pessoa. Este relógio foi desenvolvido por Konohagakure apenas para ser usado pelos cinco Kage e seus assistentes em que tinham mais confiança. O relógio serve como uma contagem regressiva para a destruição da Terra, após Toneri ter deslocado a Lua de sua órbita.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Selo de Confronto'),
(1951, 'Relâmpago (jutsu)', 'Uma versão menor da Bola da Besta com Cauda que é infundida com chakra relâmpago que o usuário em forma de besta, ou a própria besta pode usar para atravessar as defesas de seu alvo e causar grandes danos.', '雷斗刃駆', 'Raitoningu', 'Condução da Lâmina Relâmpago de Combate', 'Corte de Luz', NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(1952, 'Relâmpago Negro', 'Relâmpago Negro é uma forma única de Liberação de Relâmpago utilizada pelo Terceiro Raikage, que passou para Darui. Aqueles que detêm essa habilidade recebem uma tatuagem estilizada do kanji \'relâmpago\' (雷, kaminari) em seus ombros como um traço distintivo que indica sua posse; Darui à sua esquerda e o Raikage à sua direita. Não se sabe muito sobre seu funcionamento, mas como o nome sugere, o relâmpago produzido pelas técnicas desta habilidade é de cor preta.', '黒い雷くろいかみなり', 'Kuroi Kaminari', 'Relâmpago Negro', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(1953, 'Relâmpago Reto', 'Enquanto coberto no Modo de Chakra de Liberação de Relâmpago, o usuário corre em direção ao oponente e o soca em sua velocidade máxima.', '雷斗忍遇須吐励刀', 'Raitoningu Sutorēto', 'Luta Relâmpago Suportando Encontro Obrigatório de Ejeção Incentivando Espada', 'Relâmpago Reto', NULL, NULL, 'Curto alcance', NULL, NULL),
(1954, 'Repartimento de Chakra', 'Esta técnica consiste em criar até cinco jatos de chakra azul-púrpuras, extremamente velozes e que atravessam o solo. Os jatos são difíceis de evitar, entretanto não causam danos fatais a seus alvos. Eles podem, no entanto, selar o chakra daquele que for atingido por certo tempo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio a longo alcance', NULL, NULL),
(1955, 'Replicação da Besta com Cauda', 'Gensui cria uma etiqueta de selamento especial que, se colocado em seres humanos, os transforma em falsas réplicas de uma das bestas com cauda. O selo não apenas molda a vítima para a besta desejada, como também concede-lhes as habilidades do originais. A etiqueta também pode replicar o jinchūriki da besta com cauda também.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Tigre'),
(1956, 'Resplendor', 'O usuário emite uma corrente de relâmpago para atordoar o inimigo. Esta técnica pode ser usada em conjunto com uma técnica de Liberação de Água para canalizar melhor o relâmpago ou como distração para o oponente evadir e cair numa armadilha.', '残光ざんこう', 'Zankō', 'Resplendor', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1957, 'Resplendor Crepuscular', 'Sasuke avança em alta velocidade em direção ao seu oponente, então ele desfere um corte transversal com sua Espada de Kusanagi.', '残光', 'Zankō', 'Resplendor Crepuscular', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1958, 'Restrição Maldita da Cobra Conjuradora', 'O usuário invoca uma ou duas cobras, que rastejam para fora de uma das mangas, e enrola-se ao redor do inimigo para capturá-lo ou imobilizá-lo. Esta é uma grande técnica para capturar alguém vivo, ou para infligir dano por estrangulamento, e se a situação exigir, as cobras podem morder a vítima com seus dentes de veneno. Com a invocação, previamente, o procedimento pode ser reduzido ao usar sangue do selo de contrato em uma invocação, tornando possível usar a técnica imediatamente. Com uma adição da intenção assassina o usuário pode enfraquecer a determinação de seu alvo.', '蛇睨呪縛じゃげいじゅばく', 'Jagei Jubaku', 'Restrição Maldita da Cobra Conjuradora', NULL, NULL, NULL, 'Curto alcance', 'Rank C', NULL),
(1959, 'Restrição da Aranha', 'Depois de reunir sua teia aranha em sua boca, Kidōmaru cospe para fora em uma ampla teia padrão. Esta teia pode envolver seus inimigos e amarrá-los para uma superfície próxima. Com o chakra fluindo através da teia é difícil de escapar dela.', '蜘蛛縛りくもしばり', 'Kumo Shibari', 'Restrição da Aranha', NULL, 'Teia de Aranha', NULL, 'Curto alcance', NULL, 'Tigre'),
(1960, 'Restrição de Fios de Aço', 'Essa tática engloba a criação de armadilhas utilizando fios de aço.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1961, 'Restrição de Fios do Projétil Manipulado', 'Utilizando um projétil com fios ligados a ele, o usuário pode preparar uma armadilha para seu alvo. Usando shuriken ou kunai para espalhar e colocar os fios ao redor de si ou do alvo, o usuário pode posicioná-los para prender seu alvo, requerendo apenas que provoque uma mudança de posição do conjunta de fios, como cortando um deles, fazendo com que os outros se reorganizem.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1962, 'Reter Água', 'Tobirama desfere um soco em seu oponente, e logo após, acompanha com um chute giratório que o levanta no ar, onde então segue com mais três chutes antes de aparecer por cima do oponente, soltando de sua mão uma rajada de água jogando o oponente para o chão.', '抱水ほうすい', 'Hōsui', 'Reter Água', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1963, 'Retorno Uchiha', 'Ao utilizar o Gunbai Uchiha para bloquear um ataque recebido, o usuário anula os efeitos do ataque convertendo o chakra recebido em transformação da natureza de vento, que é refletida de volta. Como a reflexão de um ataque ocorre de repente e inesperadamente, o ataque refletido pode ser difícil para os adversários escaparem.', 'うちは返しうちはがえし', 'Uchihagaeshi', 'Retorno do Leque', 'Reflexão Uchiha', 'Reflexo Uchiha', NULL, 'Curto alcance', 'Rank B', NULL),
(1964, 'Retorno de Vento', 'Usando as Espadas Curtas Voadoras Crescentes, o usuário dessa técnica pode facilmente cancelar uma outra técnica da Liberação de Vento e redirecioná-la para o seu usuário na forma de um furacão.', '風返し', 'Kaze Gaeshi', NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1965, 'Retorno do Diabo', 'Suigetsu primeiro usa sua Liberação de Água: Técnica do Projétil do Dragão de Água no oponente, danificando-o e deixando-o no ar. Em seguida, ele ataca-o, batendo o adversário no chão com sua Kubikiribōchō.', '悪魔の帰還', 'Akuma no Kikan', 'Retorno do Diabo', 'Retorno do Demônio', NULL, NULL, 'Curto alcance', NULL, NULL),
(1966, 'Reversão do Falcão', 'Obito salta com as pernas sobre seu oponente, prendendo seus pés nos ombros dele. Em seguida, Obito atira-o para o ar, aparece atrás do oponente e acerta-o com um golpe de duas mãos no estômago.', NULL, NULL, 'Reversão do Falcão', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1967, 'Revestimento do Corpo', 'Uma habilidade possuída pelo Zetsu Negro, permitindo-lhe revestir um alvo de sua escolha.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1968, 'Revolta do Mundo do Demônio', 'Tayuya manipula seus três Doki com o som de sua flauta. Sua melodia libera espíritos materializados, que tem efetivamente chakra especial selado dentro dos Doki. Os espíritos materializados são compostos quase inteiramente de energia mental, de modo que o chakra fica em um estado instável. Portanto, eles anseiam energia física, de modo que caçam sua presa interminavelmente.', '魔境の乱まきょうのらん', 'Makyō no Ran', 'Revolta do Mundo do Demônio', NULL, 'Revolução na Terra dos Demônios', 'Nona Passagem: Revolução do Demônio', 'Curto a Médio alcance', 'Rank B', NULL),
(1969, 'Riacho Azul', 'O usuário cria um corpo de água em movimento debaixo dele que se desloca em direção ao oponente, permitindo-o cortar o oponente com uma grande espada diversas vezes ou perfurá-lo.', '青小川あおおがわ', 'Ao Ogawa', 'Riacho Azul', 'Ruptura de Onda[1]', NULL, NULL, 'Curto alcance', NULL, NULL),
(1970, 'Riacho de Sangue da Chuva de Verão', 'Shura usa o interior de seu guarda-chuva, para disparar várias agulhas. Uma vantagem desta técnica é a levitação do usuário e rotação, possibilitando o disparo de mais agulhas, massacrando os alvos numa área considerável.', '五月雨血河さみだれけっか', 'Samidare Kekka', 'Riacho de Sangue da Chuva de Verão', NULL, NULL, 'Arte Ninja: Chuva de Sangue', 'Curto a Médio alcance', NULL, NULL),
(1971, 'Ribalta', 'Ribalta é uma técnica baseada em um relâmpago que é usado quando todos os quatro usuários têm lugar nas direções cardeais. Eles, então, juntam seu chakra relâmpago na forma de quatro fluxos de conexão que disparam para o céu acima da área de alvo. Isto vai, então, criar uma bola maciça de relâmpago poderoso o suficiente para incinerar tudo dentro da área dos quatro usuários. Ele é considerado como sendo uma das técnicas de Liberação de Relâmpago mais poderosas e destrutivas. Os quatro mais fortes dos Doze Guardiões Ninja eram provavelmente os únicos com a habilidade de usar esta técnica, já que a equipe de Furido se deu ao trabalho de ressuscitá-los com uma técnica de reanimação.', '雷夢雷人ライムライト', 'Raimu Raito', 'Sonho Relâmpago de Pessoas Relâmpago', NULL, NULL, 'Jutsu Ribalta', 'Todos os alcances', NULL, NULL),
(1972, 'Rio Lobo', 'Depois de se transformar em um lobo de duas cabeças, Kiba e Akamaru saltam em frente e mordem o oponente com as duas cabeças de lobo.', '狼河', 'Rōka', 'Rio Lobo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1973, 'Risco da Dança da Lâmina', 'Mifune executa um corte rápido em direção ao seu adversário, cortando-o. O adversário só sente o dano quando Mifune guarda sua espada.', '踊刃冒ようじんぼう', 'Yōjinbō', 'Risco da Dança da Lâmina', 'Decapitação de Morador[2]', NULL, NULL, 'Curto alcance', NULL, NULL),
(1974, 'Rocha do Macaco', 'A Rocha do Macaco é uma técnica de Liberação de Lava utilizada por Momoshiki Ōtsutsuki.', '猿岩さるいわ', 'Saruiwa', 'Rocha do Macaco', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1975, 'Roda Dourada da Reencarnação Explosiva', 'A Roda Dourada da Reencarnação Explosiva é uma poderosa técnica utilizada por Toneri Ōtsutsuki.', '金輪転生爆きんりんてんせいばく', 'Kinrin Tensei Baku', 'Roda Dourada da Reencarnação Explosiva', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1976, 'Roda Prateada da Reencarnação Explosiva', 'A Roda Prateada da Reencarnação Explosiva é uma poderosa técnica utilizada por Toneri Ōtsutsuki.', '銀輪転生爆ぎんりんてんせいばく', 'Ginrin Tensei Baku', 'Roda Prateada da Reencarnação Explosiva', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
