INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(238, 'Bashōsen: Hélice de Terra', 'Agitando a Bashōsen no chão, o usuário desencadeia uma coluna de terra que sobe em direção ao oponente.', '芭蕉扇・土の巻ばしょうせん・つちのまき', 'Bashōsen: Tsuchi no Maki', 'Leque do Palmo de Banana: Hélice de Terra', 'Pergaminho da Terra Bashosen', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(239, 'Bashōsen: Hélice de Vento', 'Usando o Bashōsen, o usuário cria uma forte rajada de vento. Essa rajada é poderosa o suficiente para destruir a máscara de raios de Kakuzu.', '芭蕉扇・風の巻ばしょうせん・かぜのまき', 'Bashōsen: Kaze no Maki', 'Leque do Palmo de Banana: Hélice de Vento', 'Pergaminho do Vento Bashosen', NULL, NULL, 'Todos os alcances', NULL, NULL),
(240, 'Bashōsen: Hélice de Água', 'Usando a Bashōsen, o usuário libera uma torrente de água, usando-a para atacar seu inimigo.', '芭蕉扇・水の巻ばしょうせん・みずのまき', 'Bashōsen: Mizu no Maki', 'Leque do Palmo de Banana: Hélice de Água', 'Pergaminho da Água Bashosen', NULL, NULL, 'Longo alcance', NULL, NULL),
(241, 'Bastão do Seis Caminhos', 'Bastão do Seis Caminhos (六道の棒, Rikudō no Bō), é como Naruto chama suas armas criadas a partir de suas Esferas da Busca da Verdade.', '六道の棒りくどうのぼう', 'Rikudō no Bō', 'Bastão do Seis Caminhos', NULL, NULL, NULL, NULL, NULL, NULL),
(242, 'Batida!', 'Konohamaru salta sobre seu oponente e, em seguida, salta no chão sobre ele, que leva dano com o impacto.', 'ぴった～ん!', 'Pittan!', 'Batida!', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(243, 'Batida da Sombra', 'Isobu ou seus respectivos jinchūriki enquanto em seu estado de transformação, rolam como uma bola, intimidando seus adversários, fazendo pleno uso das suas muitas saliências pontiagudas de uma forma semelhante ao Tanque da Bala Humana Espinhenta do clã Akimichi. Esta manobra também pode ser usada para escapar de ataques.', '影撫で', 'Kagenade', 'Batida da Sombra', 'Queda da Concha Torre (甲櫓落し, Kōro Otoshi)', NULL, NULL, 'Curto alcance', NULL, NULL),
(244, 'Beco sem Saída', 'Hidan corta seu oponente com sua foice e o amaldiçoa. Kakuzu, então, usa mistura suas técnicas de fogo, relâmpago e vento contra Hidan. Em ritual, o dano infligido a Hidan é passado ao inimigo.', '恐越死極きょうえつしきょく', 'Kyōetsu Shikyoku', 'Beco sem Saída', 'Morte Suprema Além do Medo', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(245, 'Biguro', 'Esta técnica permite a Kakuzu manipular os fios pretos no subsolo para puxar ou imobilizar seus inimigos pelos pés. Os fios pretos são rápidos e, como estão debaixo do inimgo, dificilmente podem ser evitados. Quando preso, o inimigo, mesmo com uma força absurda, não pode rompê-los.', '毘愚呂ビグロ', 'Biguro', 'Contenção Furtiva', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(246, 'Bisturi de Chakra', 'O Bisturi de Chakra é um ninjutsu médico que transforma o chakra do usuário em uma pequena lâmina afiada.', 'チャクラのメス', 'Chakura no Mesu', 'Bisturi de Chakra', 'Lâmina de Dissecção de Chakra', NULL, 'Bisturi de Chakra', 'Curto alcance', NULL, 'Tigre → Cavalo → Coelho → Rato → Cao'),
(247, 'Bisturi de Chakra: Crueldade', 'O usuário corre em direção ao oponente e ataca sucessivas vezes com seu bisturi de chakra e, em seguida, ataca uma vez no tronco. O oponente fica a mercê do usuário ao ser imobilizado pelo corte do bisturi de chakra nos músculos perto da área respiratória.', 'チャクラ解剖刀・惨', 'Chakura Kaibōtō: Zan', 'Bisturi de Chakra: Crueldade', 'Lâmina de Dissecção de Chakra: Três', NULL, NULL, 'Curto alcance', NULL, NULL),
(248, 'Bisturi de Chakra do Esmagamento da Cobra', 'Kabuto entra no subterrâneo e usa seus bisturis de chakra para cortar as pernas do oponente, deixando-o imóvel. Orochimaru, em seguida, invoca uma cobra gigante que esmaga o oponente com sua cabeça.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(249, 'Bloqueio do Escudo', 'O Bloqueio do Escudo é uma técnica usada por Gamaken tirando proveito de seu escudo maciço. É uma de suas defesas mais poderosas, capazes de até mesmo defendê-lo de um ataque veloz.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(250, 'Bloqueio do Escudo Mecânico de Luz', 'Este escudo de chakra pode ser instalado em qualquer marionete. Um mecanismo dentro da marionete abre os braços em um par de segmentos, através da qual o usuário irradia seu chakra. O chakra se espalha como uma película fina e se transforma em uma folha de proteção, bloqueando todos os ataques físicos e algumas técnicas. Enquanto o escudo bloqueia a Areia de Ferro, mas não impede que ela escoe as articulações das marionetes, que as leva a ser desativadas. Chiyo instalou um em seu próprio braço e nas marionetes dos pais de Sasori. Kankurō posteriormente instalou um no corpo da marionete Sasori.', '機光盾封', 'Kikō Junbū', 'Escudo de Luz Bloqueador Mecânico', NULL, NULL, 'Escudo de Chakra', 'Curto alcance', 'Rank B', NULL),
(251, 'Bofetada Abrupta', 'O usuário dá um forte e inesperado tapa no rosto de seu oponente, que o derruba ou o lança para longe.', 'ツンツンビンタ', 'Tsuntsunbinta', 'Bofetada Abrupta', 'Tapa Ardido', NULL, NULL, 'Curto alcance', NULL, NULL),
(252, 'Bola da Besta com Cauda', 'A Bola da Besta com Cauda é a técnica final de uma besta com cauda e seu jinchūriki.', '尾獣玉びじゅうだま', 'Bijūdama', 'Bola da Besta com Cauda', 'Bola Ameaçadora (威鞠, Imari)', 'Esfera da Bijū', 'Bomba da Besta com Cauda', 'Longo alcance', 'Rank S', NULL),
(253, 'Bola da Besta com Cauda Contínua', 'A besta com cauda ou o jinchūriki que dominou a mudança de forma com maestria, consegue executar esse golpe, que consiste em disparar várias Bolas da Besta com Cauda descompactadas em rápida sucessão no alvo.', '連続尾獣玉', 'Renzoku Bijūdama', 'Bola da Besta com Cauda Contínua', 'Canhão de Chakra Sucessivo (チャクラ砲連発, Chakurahō Renpatsu)', 'Bijūdama em Série', 'Rajada de Bombas da Besta com Cauda', 'Longo alcance', 'Rank S', NULL),
(254, 'Bola da Besta com Cauda Instantânea', 'Bola da Besta Com Cauda Instantânea é uma Bola da Besta com Cauda sobrecarregada de chakra, que é disparada assim que é gerada, sem a necessidade de ter que engoli-la, como ocorre com a sua versão menos poderosa. Devido à necessidade de uma quantidade enorme de chakra para executar essa técnica, ela só pode ser utilizada por Kurama e Gyūki, de maneira individual, devido as suas vastas reservas de chakra, ou por meio colaborativo, como quando ocorreu quando as cinco bestas com caudas combinaram seus poderes na luta contra Naruto em seu Modo de Chakra da Besta com Cauda.', '尾獣玉一閃いっせんびじゅうだま', 'Issen Bijūdama', 'Bola da Besta Com Cauda Instantânea', 'Cauda do Demônio[1]', NULL, NULL, 'Longo alcance', 'Rank S', NULL),
(255, 'Bola de Aço', 'O usuário cria uma esfera feita de aço. O projétil pode ser lançado a uma distância considerável em direção ao alvo, causando um pesado dano contundente. Quando utilizada em união com a Liberação de Lava, que pode derreter o aço, ele se transforme em uma forma semelhante a uma espiral.', NULL, NULL, 'Bola de Aço', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(256, 'Bola de Pelos de Rato', 'O usuário cospe uma bola de fogo de pelos que assume a forma de um rato em chamas, que se divide continuamente para pelos individuais e da mesma forma em chamas. Estes projéteis flamejantes perseguem os alvos pretendidos antes de explodir, uma vez que eles entram em contato com um objeto.', 'ねずみ毛玉', 'Nezumi Kedama', 'Bola de Pelos de Rato', NULL, 'Novelo de Rato', NULL, 'Todos os alcances', NULL, NULL),
(257, 'Bola de Plasma', 'Bola de Plasma (荷電障壁殻, Purazuma Bōru) é uma técnica de espadas utilizada por Ranke ao extrair energia da Pedra de Gelel.', '荷電障壁殻', 'Purazuma Bōru', 'Barreira Protetora de Carga Elétrica', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(258, 'Bolha Esférica', 'As Bolhas Esféricas são esferas de energia utilizadas pelas marionetes de Toneri Ōtsutsuki, que mostraram-se capazes de canalizar o chakra do Tenseigan de Hamura em suas mãos e dispará-las contra seu oponente, explodindo no impacto.', NULL, NULL, 'Bolha Esférica', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Juntar as Maos → Selo Desconhecido'),
(259, 'Bolha Explosiva', 'Após Utakata soprar dezenas de bolhas, elas voam em direção ao oponente e explodem fortemente, derrubando o inimigo para próximo. As explosões também produzem uma grande quantidade de fumaça, que pode ser usada para obstruir a visão do oponente.', '起爆泡', 'Kibakuhō', 'Bolha Explosiva', NULL, NULL, 'Arte Ninja: Bomba da Bolha', NULL, NULL, NULL),
(260, 'Bolhas de Ocultação de Odor', 'O usuário primeiramente sente o odor de seus aliados e produz suor com os seus movimentos, o suor, após se sugado, é então misturado com saliva para criar uma bolha através de sua boca que os permite a ocultarem os seus odores e o próprio odor. Uma versão em maior escala desta técnica pode ser usada para ocultar odor em uma área mais ampla através da produção de bolhas menores criadas pelo mesmo método.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os Alcances', NULL, 'Carneiro'),
(261, 'Bomba Ajusta com Enorme Sucesso!', 'O usuário entra no subterrâneo e atinge seu oponente por baixo, lançando-o para o alto. Em seguida, o usuário coloca uma bomba no local em que seu oponente irá cair. Quando o adversário cair, ele irá se deparar com uma bomba à sua espera, enquanto o usuário escapa.', '爆弾設置大成功!ばくだんせっちだいせいこう', 'Bakudan Secchi Daiseikō!', 'Bomba Ajusta com Enorme Sucesso!', 'Bomba Colocada com Sucesso!', NULL, NULL, 'Curto alcance', NULL, NULL),
(262, 'Bomba Liger', 'Depois de cobrir-se em seu Modo de Chakra de Liberação de Relâmpago, o Quarto Raikage agarra o seu oponente e levanta-lhe no ar. Em seguida, usando sua força extrema, ele esmaga-o de cabeça para o chão em que se assemelha a uma manobra powerbomb. Isso é devastador o suficiente para quebrar uma grande área do próprio solo com o impacto. Darui observou que ninguém jamais sobreviveu a isso antes, e se Sasuke não tivesse usado seu Susanoo, ele teria sido morto. O impacto desta técnica foi forte o suficiente para rachar as costelas do Susanoo de Sasuke.', '雷我爆弾', 'Raigā Bomu', 'Bomba Auto-Relâmpago', NULL, 'Bomba Relâmpago', NULL, 'Curto alcance', 'Rank B', NULL),
(263, 'Bomba Liger dos Dois Pelotões', 'Com o oponente no ar, A e B pulam até a altura que o inimigo está e se entrelaçam nele em um movimento extremamente complexo, finalizando com uma queda extremamente brutal com o oponente imobilizado ao meio.', '雷我弐不雷斗爆弾', 'Raigāzutsūpuratonbomu', 'Duas Bombas de Relâmpago de Auto-Destruição', 'Bomba Liger dos Dois Pelotões', NULL, NULL, 'Curto alcance', NULL, NULL),
(264, 'Bomba Poderosa', 'A salta no ar e ataca seu oponente com golpes poderosos e, em seguida, agarra-o e joga-o no chão.', '撥破揚爆弾', 'Pawaa Bomu', 'Bomba de Tempo', 'Bomba Poderosa', NULL, NULL, 'Curto alcance', NULL, NULL),
(265, 'Bomba de Chakra da Nove-Caudas', 'Mecha-Naruto transforma parte de seu corpo em um canhão e dispara um poderoso laser em seu oponente. Essa habilidade é um tanto perigosa, que quase devastou Konoha inteira.', '九尾チャクラボム', 'Kyūbi Chakura Bomu', 'Bomba de Chakra da Nove-Caudas', 'Canhão de Bijū', NULL, NULL, 'Longo alcance', NULL, NULL),
(266, 'Bombardeio do Projétil de Borboleta', 'Esta técnica é uma ameaça à vida se utilizado em combinação com a pílula alimentar vermelha Akimichi, que queima todo o excesso de gordura do ninja e converte-o em chakra. Como os membros do Clã Akimichi tendem a estar acima do peso para ajudar em suas técnicas, esta pílula pode dar-lhes 100 vezes o seu nível de chakra normal. O chakra intensamente concentrado que é adquirido vaza da espinha dorsal e se torna visível através do atrito com o ombro, aparecendo como uma asa de borboleta. O usuário então concentra o chakra em seu único punho, e em seguida, coloca todo o seu peso corporal por trás do golpe. A enorme quantidade de chakra concentrada é suficiente para mudar a estrutura do corpo do usuário, e o usuário morre, a menos que tenha determinado atendimento médico conforme descrito na Enciclopédia Médica do Clã Nara logo depois.', '蝶弾爆撃ちょうだんばくげき', 'Chōdan Bakugeki', 'Bombardeio da Bala da Borboleta', 'Bomba Borboleta', 'Bombardeio da Borboleta', NULL, 'Curto alcance', 'Rank B', NULL),
(267, 'Bombardeio à Longa Distância', 'O usuário revela duas bombas e lança-as em seu oponente, causando uma explosão e jogando o oponente para trás.', '遠距離爆撃', 'Enkyori Bakugeki', 'Bombardeio à Longa Distância', 'Ataque de Bombas de Longa Distância', NULL, NULL, 'Todos os alcances', NULL, NULL),
(268, 'Bombas de Ovos Verdes', 'O Caminho Animal faz com que o Pássaro Bico-de-Broca Gigante ponha ovos que explodem ao seu comando.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(269, 'Boneco Amaldiçoado', 'O usuário infunde o seu chakra em um boneco que absorve chakra de uma pessoa nas proximidades, tomando sua aparência e personalidade, como um boneco gêmeo. Ele, também, é capaz de criar inúmeros bonecos, bem como roubar a forma humana da maioria dos personagens do jogo.', '呪いの人形', 'Noroi no Ningyō', 'Boneco Amaldiçoado', NULL, NULL, NULL, NULL, NULL, NULL),
(270, 'Borboleta da Agulha de Fogo', 'Ino inicia a técnica criando uma ilusão com inúmeras borboletas encurralando o inimigo. Então, o inimigo instantaneamente se une ao chão, assim ela consegue colocar um papel explosivo no rosto do inimigo e sair em um dissolver da ilusão. Logo a ilusão se cancela, revelando as inúmeras borboletas em uma transformação de papéis explosivos, finalmente os selos estouram e causam uma devastação imensa de chamas incontroláveis, constituindo um imenso incêndio atrás de Ino, que faz uma pose.', '蝶針火', 'Chōshinka', 'Borboleta da Agulha de Fogo', 'Borboleta da Agulha de Chama', NULL, NULL, 'Todos os alcances', NULL, 'Tigre'),
(271, 'Boruto Stream', 'Utilizando a Liberação de Vento, o usuário pode acelerar seus movimentos criando um efeito similar à visão de um túnel. Para conseguir chegar a altas velocidades, o usuário pode fazer que seus aliados ou até mesmo clones das sombras performem a técnica nele ao propulsionar seus pés.', 'ボルトストリームぼるとすとりぃむ', 'Boruto Sutorīmu', 'Corrente Boruto', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(272, 'Braço Monstruoso de Areia', 'Usando grandes quantidades de areia, Gaara pode manifestar os braços de Shukaku para lutar contra o seu adversário, geralmente tentando agarrá-lo e/ou esmagá-lo.', '砂の怪腕すなのかいわん', 'Suna no Kaiwan', 'Braço Monstruoso de Areia', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(273, 'Braço de Areia de Ferro', 'Shinki manipula sua areia de ferro para criar um braço que se estende para pegar o oponente e restringi-lo ou agarrar algo. Ele também pode utilizar isso para cobrir seu próprio antebraço, aumentando o poder ofensivo do mesmo. Ao dar um potente soco endurecido com a maciça areia de ferro, ele pode proteger-se do contato direto com seu alvo.', '砂鉄の腕さてつのうで', 'Satetsu no Ude', 'Braço de Areia de Ferro', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(274, 'Braços de Chakra da Besta com Cauda', 'Trata-se de uma habilidade que um jinchūriki é capaz de realizar, quando em sua forma de besta com cauda. O jinchūriki usa o chakra bruto da sua Besta com Cauda para criar braços de forma que ele pode manipular livremente para atacar seus inimigos e para auxiliar na performance de técnicas, além de ser capaz de atacar um adversário mais rápido do que um usuário do Sharingan possa reagir.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Juntar as Maos[1]'),
(275, 'Braços de Sombra', 'O Zero-Caudas pode criar uma infinidade de braços de seu corpo que perseguem o adversário. Os braços também podem absorver chakra do oponente. Quando usado por Shinnō, ele também poderia formar uma parede para proteger-se com esses braços, o suficiente para bloquear o Rasengan Tornado de Naruto.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(276, 'Broca Cortadora do Lobo Canino', 'Asuma entrega duas barras em todo o peito do oponente e proporciona outra barra para cima para lançar o oponente para o ar, e Asuma salta no ar e oferece duas barras no rosto do oponente e oferece uma barra final para explodir o adversário no chão.', '狼牙伐錐ろうばばっすい', 'Rōga Bassui', 'Broca Cortadora do Lobo Canino', 'Broca da Presa de Lobo', NULL, NULL, 'Curto alcance', NULL, NULL),
(277, 'Broca da Planta Carnívora', 'Zetsu se esconde sob o solo e coloca seu apêndice de planta carnívora como uma armadilha a fim de capturar presas desavisadas. Quando a vítima cai sobre a armadilha, ele fecha esse apêndice para prendê-la e rotaciona para dilacerá-la, abandonando a presa ao finalizá-la.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcances', NULL, NULL),
(278, 'Buddha Superiormente Transformado', 'Depois de criar uma gigantesca estátua de madeira que possui milhares de mãos, o usuário utiliza estas mãos simultaneamente para entregar milhares de golpes poderosos para um alvo. Devido ao grande volume de socos envolvidos, a defesa contra a totalidade deles é quase impossível. Isso pode ser usado como uma técnica de Tiro-Único, aumentando seu poder destrutivo, mas também resultando com as mãos atrás das costas da estátua serem destruídas. Depois, o usuário pode descartar a parte de atrás da estátua para aumentar a mobilidade da estátua verdadeira de milhares de mãos. Ele também pode ser usado de forma mais conservadora como pode ser visto no uso de Tobi, impedindo que as mãos sejam destruídas e permitindo um outro uso da técnica, mas resulta em poder menos destrutivo.', '頂上化仏ちょうじょうけぶつ', 'Chōjō Kebutsu', 'Buddha Superiormente Transformado', NULL, 'Buda Supremo', NULL, 'Todos os alcances', NULL, NULL),
(279, 'Bukijutsu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(280, 'Busca da Verdade Distorcida', 'Obito reúne suas Esferas da Busca da Verdade em uma única grande esfera, que dispara uma série de projéteis em forma de agulhas contra seu oponente.', '滅道', 'Metsudō', 'Busca da Verdade Distorcida', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(281, 'Byakugan', 'O Byakugan (白眼; Literalmente significa \'Olho Branco\'; Significado (Panini) \'Olho Branco que Tudo Vê\') é uma dōjutsu kekkei genkai do clã Hyūga e do clã Ōtsutsuki. Seu poder a princípio surgiu como uma Kekkei Mōra junto com o Rinne Sharingan na ancestral mais antiga conhecida dos clãs Hyūga e Ōtsutsuki: Kaguya Ōtsutsuki. Ficou conhecido como um dos Três Grandes Dōjutsu (三大瞳术, Sandai Dōjutsu; Literalmente significa \'Três Grandes Técnicas Oculares\'), juntamente com o Sharingan e o Rinnegan.', '白眼びゃくがん', 'Byakugan', 'Olho Branco', NULL, 'Olho Branco Que Tudo Vê', NULL, NULL, NULL, 'Rato → Cao → Selo Desconhecido → Rato → Selo Desconhecido[1] ou Cavalo → Selo Desconhecido → Selo Desconhecido → Coelho → Rato → Cão → Cavalo → Cão → Coelho → Rato → Selo Desconhecido → Selo Desconhecido[2]'),
(282, 'C0', 'A forma mais elevada da Argila Explosiva de Deidara, sua \'Arte Final\' (究極芸術, Kyūkyoku Geijutsu), o C0 é uma técnica suicida usada como último recurso. Deidara revela uma boca no peito, que é costurada. Depois de desfazer os pontos, Deidara a alimenta com uma grande bola de argila. A boca no peito tem uma conexão direta com o seu coração para conseguir reunir uma grande quantidade de chakra. Em seguida, ele amassa o chakra diretamente nos meridianos do coração e continua a fazê-lo sem interromper até o momento da explosão. Seu chakra explosivo então se manifesta em suas veias, fluindo através de seu corpo. Estas \'veias\' depois se transformam em uma esfera que, lentamente, começa a criar rachaduras e detona, produzindo uma explosão original que abrange um raio de explosão 10 km.', 'C0', 'Shī Ō', 'C0', NULL, NULL, NULL, 'Todos os alcances', 'Rank –', NULL),
(283, 'C1', 'A forma mais básica e versátil do usuário tirar proveito da argila explosiva, criando formas a partir de uma única boca em sua palma. Estas são pequenas formas animadas que muitas vezes são inspiradas na anatomia animal. Dependendo de qual variação é criada, ela mantém as características e habilidades das criaturas as quais tiveram inspiração, ou seja, os pássaros ainda podem voar, cobras rastejar e peixes nadar. Esta característica, combinada com o seu poder explosivo independente, torna-se excelente para operações encobertas. O usuário pode produzir e liberar um número significativo delas em alta velocidade.', 'C1', 'Shī Wan', 'C1', 'Beleza Destrutiva (破壊の美, Hakai no Bi)[2]', NULL, NULL, 'Todos os alcances', NULL, 'Ativar: Carneiro[3] ou Cao → Carneiro[4],Explodir: Carneiro[5] ou Selo do Confronto'),
(284, 'C2', 'Uma das especialidades de Deidara que requer o uso de ambas as suas bocas da palma, criando um grande dragão. O dragão em si é capaz de voar, e, enquanto Deidara está seguro em suas costas, o dragão pode cuspir figuras de argila menores que atacam o adversário como mísseis guiados.', 'C2', 'Shī Tsū', 'C2', 'C2 Dragão Voador Explosivo (C2 爆裂飛竜, Shī Tsū Bakuretsu Hiryū)', 'C2 Dragão (C2ドラゴン, Shī Tsū Doragon)', NULL, 'Todos os alcances', NULL, NULL),
(285, 'C3', 'C3 é a bomba mais poderosa em larga escala de Deidara, que uma vez ativada torna-se uma enorme estátua que Deidara derruba sobre o alvo. Seu poder explosivo foi grande o suficiente para potencialmente destruir uma grande parte de Sunagakure.', 'C3', 'Shī Surī', NULL, 'Ohako C3', NULL, NULL, 'Todos os alcances', NULL, NULL),
(286, 'C4', 'Esta é uma técnica criada por Deidara para combater o Sharingan e suas técnicas. Usando sua própria boca, em vez das habituais bocas em suas mãos, o usuário cria um boneco gigante de si próprio. A explosão do boneco ocasiona em uma nuvem de bombas microscópicas, quais entram na corrente sanguínea após a inalação do ar. Dado um novo comando, as bombas presentes no sangue de seu inimigo são explodidas, desintegrando o corpo a nível celular e, como consequência, reduzindo-o a pó.', 'C4', 'Shī Fō', NULL, NULL, 'C4 Garula', NULL, 'Todos os alcances', NULL, 'Expelir: Tigre → Cobra → Passaro,Liberar: Cobra,Explodir: Carneiro'),
(287, 'Cabeçada', 'Depois de entrar em sua transformação de quatro caudas, Killer B usa os dois chifres de chakra que saem para fora do seu manto de chakra para cabecear seu adversário. De acordo com Sabu, este ataque é forte o suficiente para decapitar alguém.', '頭八刀', 'Heddo Batto', 'Oitava Espada Cranial', NULL, 'Octocabeçada', NULL, 'Curto alcance', NULL, NULL),
(288, 'Cabeçada (Naruto)', 'O usuário dispara em direção a seu oponente em alta velocidade, e o agarra para executar uma forte cabeçada, que é capaz de derrubar seu adversário ou acordá-lo de alguma incapacitação mental.', '頭突きずつき', 'Zutsuki', 'Cabeçada', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(289, 'Cabeçada Agonizante', 'O usuário ataca seu oponente com três socos, seguido por dois chutes, e para finalizar, ele salta no ar e cai em direção ao seu oponente de cabeça.', '串刺し頭突きくしざしずつき', 'Kushizashi Zutsuki', 'Cabeçada Agonizante', 'Cabeçada Empaladora', NULL, NULL, 'Curto alcance', NULL, NULL),
(290, 'Cadeia do Mundo de Areia de Ferro', 'Sasori usa a marionete Terceiro Kazekage para liberar uma série de longos picos de areia de ferro em torno dele para apunhalar o oponente.', '砂鉄鎖界さてつさかい', 'Satetsu Sakai', 'Cadeia do Mundo de Areia de Ferro', 'Cadeia da Areia de Ferro', NULL, NULL, 'Curto alcance', NULL, NULL),
(291, 'Cadáver Falso', 'Através de um pergaminho, o usuário é capaz de invocar cadáveres usados em experiências e fazê-los ganhar uma aparência diferente, usando-os como distração. Segundo Kabuto, apesar desta técnica ser efetiva com cadáveres, é possível de transformar objetos inanimados.', '偽の死体にせのしたい', 'Nise no Shitai', 'Cadáver Falso', NULL, NULL, NULL, NULL, NULL, NULL),
(292, 'Caixão de Areia', 'O Caixão de Areia é uma das técnicas assinaturas de Gaara, que durante toda sua vida foi usada como meio de assassinato, resultando no cheiro de sua areia utilizada para essa técnica. Em sua primeira aparição, o controle tão perfeito dessa técnica levou o Time Kurenai assustar-se perante a ela.', '砂縛柩さばくきゅう', 'Sabaku Kyū', 'Caixão de Areia', NULL, 'Ataúde de Areia', 'Caixão de Areia', 'Curto a Médio alcance', 'Rank –', NULL),
(293, 'Calamidade das Chamas Ventiladas', 'Tobi expele chamas sobre o oponente e as abana com seu gunbai para fortalecer as chamas, balançando-a para cima no oponente, enviando-o voando no ar.', '煽禍の炎せんかのえん', 'Senka no En', 'Calamidade das Chamas Ventiladas', 'Chamas Sinistras Ventiladas', NULL, NULL, 'Curto alcance', NULL, NULL),
(294, 'Calor Escaldante do Caixão de Areia', 'Gaara envolve seu oponente em uma grande quantidade de areia, e em seguida, ele manobra seus irmãos ao redor dele em plataformas de areia. Neste momento, Kankurō usa sua marionete Sasori para lançar fogo, enquanto Temari sopra as chamas, upando-as. Finalizando o ataque combinado, Gaara termina envolvendo todo o corpo do oponente na areia, e esmagando-o, fazendo com que a prisão de areia flamejante exploda e causa danos supremos no alvo.', '灼熱砂縛柩しゃくねつさばくきゅう', 'Shakunetsu Sabaku Kyū', 'Calor Escaldante do Caixão de Areia', 'Ataúde de Areia em Chamas', NULL, NULL, 'Longo alcance', NULL, NULL),
(295, 'Calor Intenso: Ataque do Pavão do Lótus do Tigre Feroz', 'Guy e Lee ambos ativam os oito portões, Guy ativa o sétimo portão e Lee o sexto. Depois de uma série de golpes o adversário vai ao ar, Lee usa a Lótus Reversa enviá-o para baixo. Antes que eles podem chegar a terra, Guy executa o Pavão da Manhã e o Tigre Diurno, fazendo com que o tigre consuma as chamas do pavão. Lee e o Guy apressam a técnica combinada, e e empalam através de seu adversário, antes do tigre explodir, espalhando as chamas do pavão.', '激熱・猛虎蓮雀の攻', 'Gekiatsu: Mōko Renjaku no Kō', 'Calor Intenso: Ataque do Pavão do Lótus do Tigre Feroz', 'Abrasador Quente: Ataque do Tigre Feroz do Lótus do Pavão', NULL, NULL, 'Curto alcance', NULL, NULL),
(296, 'Caminho Animal', 'O Caminho Animal (畜生道, Chikushōdō) concede a um usuário do Rinnegan a habilidade de invocar vários animais e criaturas para ajudá-lo em batalha. Essas criaturas parecem ser imortais, como eles foram mortos e mais tarde invocados novamente. Digno de nota, invocando criaturas através do Caminho Animal não necessita de sacrifício de sangue ou selos de mão. O Caminho Animal também pode invocar outros corpos, o que exige que os selos de mão, como no caso dos Caminhos de Nagato e Konan. Em táticas sábias, o principal papel do Caminho Animal em batalha parece consistir em múltiplos oponentes esmagadores com uma barragem de invocações.', '畜生道ちくしょうどう', 'Chikushōdō', 'Caminho Animal', NULL, 'Reino dos Animais', NULL, NULL, NULL, NULL),
(297, 'Caminho Exterior — Técnica da Vida Celestial de Saṃsāra', 'Caminho Exterior — Técnica da Vida Celestial de Saṃsāra é um ninjutsu executado pelos portadores do Rinnegan, que permite ressuscitar completamente os mortos.', '外道　輪廻天生の術げどう　りんえてんせいのじゅつ', 'Gedō — Rinne Tensei no Jutsu', 'Caminho Exterior — Técnica da Vida Celestial de Saṃsāra', 'Arte Herética do Renascimento Rinne', 'Reencarnação Divina do Estilo Profano', NULL, NULL, 'Rank –', 'Carneiro → Cobra'),
(298, 'Caminho Humano', 'Este shinobi foi um ninja de Takigakure que Jiraiya encontrou durante a sua jornada para encontrar seu discípulo. Mais tarde, ele foi morto e seu cadáver usado por Nagato para canalizar o poder do Ningendō para seus Caminhos de Pain.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(299, 'Caminho Instantâneo', 'Madara executa uma série de combos usando suas Esferas da Busca da Verdade.', '瞬道しゅんどう', 'Shundō', 'Caminho Instantâneo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(300, 'Caminho Naraka', 'O Caminho Naraka (地獄道, Jigokudō) concede ao usuário duas habilidades principais: de interrogatório e de restauração, usando o Rei do Inferno. Pain canalizou o poder do Caminho Naraka através do corpo de um sacerdote.', '地獄道じごくどう', 'Jigokudō', 'Caminho Naraka', NULL, NULL, NULL, NULL, NULL, NULL),
(301, 'Caminho Preta', 'O Caminho Preta (餓鬼道, Gakidō) concede ao usuário a habilidade de absorver qualquer forma de chakra com a Técnica de Absorção do Selo de Bloqueio. Pain canalizou o Caminho Preta através do cadáver de um shinobi de Kusagakure.', '餓鬼道がきどう', 'Gakidō', 'Caminho Preta', NULL, 'Reino da Fome', NULL, NULL, NULL, NULL),
(302, 'Caminho da Cauda Estultícia', 'Sasori, enquanto dentro de sua marionete Hiruko, estende a cauda de ferro da marionete circulando em torno de seu oponente como um casulo e de repente a contrai, esmagando-o.', '尾道魯', 'Odoru', 'Caminho da Cauda Estultícia', 'Cauda da Estrada Lu', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(303, 'Camuflagem de Cabelo', 'Usando esta técnica, o cabelo de Kurenai irá prolongar e cobrir todo o seu corpo. Ela pode então usar esta forma para viajar através de objetos sólidos e vai emergir como uma massa de cabelo. O cabelo depois encolhe ao seu tamanho normal, de uma forma semelhante à variação das Agulhas Jizō de Jiraiya.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(304, 'Canhão da Concha', 'Depois de Kandachi realizar os selos de mãos necessários, o Rei Concha dispara numerosos projéteis como agulhas dos poros de seu corpo.', '巻き貝砲', 'Makigai Hō', 'Canhão Concha', NULL, NULL, 'Lança da Concha', 'Médio a Longo alcance', NULL, 'Cao → Javali → Cobra'),
(305, 'Canhão de Naruto', 'Naruto cria um clone da sombra que agarra e arremessa-o para um ponto distante da escolha do jogador. Esta técnica não serve para fins de combate, sendo apenas um método usado em Naruto: Ultimate Ninja Storm para se locomover no centro de Konoha mais rápido. Enquanto os jogadores progridem no jogo, as reservas de chakra de Naruto tornar-se maior e ele pode ser jogado mais vezes.', NULL, NULL, NULL, 'Canhão de Naruto', NULL, NULL, 'Todos os alcances', NULL, NULL),
(306, 'Capa da Desconcertante Superfície sem Pó', 'O utilizador apaga a sua presença, fazendo com que ele não tenha nenhuma forma física, nem chakra detectável, enquanto a técnica estiver ativada. Ele não pode ser detectado através de meios tradicionais. Com o domínio de Mū nesta técnica ajudou-o a ganhar o apelido de \'não-pessoa\' (無人, Mujin; Viz \'Homem Nulo\'). Esta técnica utiliza vapor de água para dobrar a luz ao redor do usuário que faz uso de Liberação de Água para fazê-lo, isto reforça os seus efeitos a partir da Técnica de Ocultação com Camuflagem.', '無塵迷塞', 'Mujin Meisai', 'Capa da Desconcertante Superfície sem Pó', NULL, NULL, NULL, NULL, 'Rank B', NULL),
(307, 'Capa de Areia de Ferro', 'A Capa de Areia de Ferro é um manto constituído inteiramente de areia de ferro, que é usado por Shinki e sua marionete.', '砂鉄のマントさてつのまんと', 'Satetsu no Manto', 'Capa de Areia de Ferro', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(308, 'Captura', 'Esta é uma técnica de captura utilizada através do compartimento no corpo de Formiga Negra.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(309, 'Caracteres de Água', 'Esta técnica permite enviar mensagens através da água numa superfície, então criando uma mensagem que, depois dum tempo, desaparece.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(310, 'Cardo', 'Kimimaro ataca seu oponente para cima com uma espada feita de osso e, em seguida, estende os ossos de suas costas para jogar o oponente para o ar violentamente.', '薊', 'Azami', 'Cardo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(311, 'Carga! Corpo de Exército de Konohamaru!', 'Konohamaru, Moegi e Udon, cada um segura uma bola com um papel explosivo ligado a ela. Eles, em seguida, continuam a atacar o adversário, e por sua vez, soltam as bolas que rolam para o adversário e explodem.', '突撃! 木ノ葉丸軍団!', 'Totsugeki! Konohamaru Gundan!', 'Carga! Corpo de Exército de Konohamaru!', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(312, 'Carga Brutal das Bestas com Cauda', 'As bestas com cauda de uma a sete caudas se juntam, além de Gaara, e atacam na seguinte ordem:', '尾獣総進撃びじゅうそうしんげき', 'Bijū Sōshingeki', 'Carga Brutal das Bestas com Cauda', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(313, 'Casaco de Pele do Rato de Fogo', 'Kaguya manipula o magma de sua dimensão de lava para criar uma erupção de lava que queima o seu oponente. Essa também pode ser usada de maneira defensiva, formando uma barreira em torno do usuário.', '火鼠ノ皮衣ひねずみのかわごろも', 'Hinezumi no Kawagoromo', 'Casaco de Pele do Rato de Fogo', 'Casaco do Rato de Fogo', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(314, 'Casulo da Aranha', 'Esta técnica faz um casulo de teia ao redor do oponente, imobilizando-o e deixando-o aberto para outras técnicas. A viscosidade vem de Kidōmaru misturando seu chakra com seu fluido corporal. A técnica é tão refinada, que, mesmo depois de ter deixado seu corpo, seu chakra ainda vai continuar a fluir através do fio para segurar o oponente no lugar.', '蜘蛛繭', 'Kumo Mayu', 'Casulo da Aranha', NULL, NULL, 'Casulo Hermético', 'Curto alcance', NULL, NULL),
(315, 'Cauda Perseguidora de Presa da Presa Giratória de Presa', 'Transformando ao lado de um clone da sombra e ninken em um único cão gigantesco de três cabeças com a Transformação Misturada da Besta Humana — Lobo de Três Cabeças, o usuário se encaracola em uma bola e rola a uma velocidade feroz em direção ao inimigo, como se estivesse correndo atrás da sua própria cauda. A rotação ultra-violenta criada pode rasgar através de vários inimigos, rasgando-os separados com facilidade.', '尾追い牙牙転牙おおいががてんが', 'Ooiga Gatenga', 'Cauda Perseguidora de Presa da Presa Giratória de Presa', NULL, 'Cauda Perseguidora, Presa Giratória', 'Cauda Perseguidora da Presa Giratória', 'Médio alcance', 'Rank –', NULL),
(316, 'Cauda da Besta com Cauda', 'Yugito arranha seu oponente e, em seguida, dá um tapa com uma das caudas de Matatabi.', '尾獣の尾', 'Bijū no O', 'Cauda da Besta com Cauda', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(317, 'Cauda da Cobra Nagi', 'Esta habilidade consiste na cobra balançar sua cauda e acertar seu oponente. A cauda também serve para defender contra projéteis lançados contra o usuário.', '蛇尾那吉', 'Dabinagi', 'Cauda da Cobra de Várias-Cabeças', 'Corte da Cauda da Cobra', NULL, NULL, 'Longo alcance', NULL, NULL),
(318, 'Caça-fantasma', 'Hidan se aproxima do alvo, ergue sua Foice de Lâmina Tripla e bate com muita força para baixo ao chão em sua frente. A força de impacto da foice é poderosa o suficiente para derrotar múltiplos alvos. Ele ainda pode combinar esse movimento com outro ao rotacionar a foice.', '御霊狩りみたまかり', 'Mitamakari', 'Caça-fantasma', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(319, 'Caçada', 'Obito desfere um golpes em espiral com suas Esferas da Busca da Verdade em seu oponente, as separa e trás para perto e, para finalizar, ele o ataca com feixes criados a partir das esferas.', '追落', 'Oiotoshi', 'Caçada', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(320, 'Ceifamento de Chakra', 'O Shinigami de Menma balança sua foice e desencadeia uma série de ondas de chakra que ao atingir o alvo, drenam todo o seu chakra.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(321, 'Centenas de Palmas Furiosas', 'Depois de ativar seu Byakugan, Hinata bate no oponente noventa e nove vezes e, em seguida, no último golpe, ela faz seu oponente voar.', '百烈掌ひゃくれつしょう', 'Hyaku Retsushō', 'Centenas de Palmas Furiosas', 'Centenas de Palmas Poderosas', NULL, NULL, 'Curto alcance', NULL, NULL),
(322, 'Chakra Líquido', 'Esta é uma substância produzida a partir do chakra de Jako.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Selo especifico da técnica'),
(323, 'Chakra Melhorado por Música', 'Tocando sua Ocarina, Menma consegue de alguma forma afetar os tenketsu de seu alvo, impulsionando seu fluxo de chakra.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(324, 'Chakra do Três-Caudas: Liberar', 'Devido à sua misteriosa conexão com o Três-Caudas, Yūkimaru é capaz de controlar pelo menos uma parte do Três-Caudas. Na primeira vez, Yūkimaru só conseguiu fazer a besta aparecer, depois de tomar algumas pílulas de Kabuto. Mais tarde, depois de tomar uma dose maior de comprimidos (combinado com a técnica de Liberação de Cristal de Guren), ele é capaz de fazer a besta aparecer totalmente, mas não conseguiu controlá-la. Mais tarde, quando Guren corria o risco de ser prejudicada pelo Três-Caudas, ele foi capaz de detê-lo completamente sem as pílulas de Kabuto. Quando Guren aparentemente sacrificou sua vida para salvá-lo, Yūkimaru perdeu o controle de si mesmo e usou seus poderes ao máximo, o que aprimorou a força do Três-Caudas que fez ficar furioso e que lhe permitiu romper a Barreira de Selamento de Quatro-Cantos. Isto poderia significar que, igual as Transformação jinchūriki de Naruto, essa capacidade está ligada a suas emoções.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(325, 'Chakram de Papel', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(326, 'Chama Carmesim', 'O usuário acumula chakra de natureza fogo em sua mão e dispara uma série de projéteis que, após colidirem contra seu alvo, explodem em chamas vermelhas. Essa técnica é poderosa o suficiente para demolir todo o território em que ela é usada.', '紅炎くえん', 'Kuen', 'Chama Carmesim', NULL, NULL, NULL, 'Longo alcance', NULL, NULL),
(327, 'Chama Combinada', 'O usuário dispara inúmeras bolas de fogo através de seu lança-chamas anexado nas mãos de sua marionete, que perseguem seu oponente até que o queime.', '火炎連弾', 'Kaen Rendan', 'Chama Combinada', 'Barragem de Fogo', NULL, NULL, 'Todos os alcances', NULL, NULL),
(328, 'Chama Envoltória do Gato de Fogo', 'Matatabi, ou Yugito completamente transformada na besta, salta no ar, gira em alta velocidade e deixa um rastro de fogo em torno de si, um pouco mais frio que seu fogo azul, mas ainda capaz de queimar mortalmente quem encostar.', '纏炎猫火', 'Ten\'en Byōka', 'Chama Envoltória do Gato de Fogo', 'Véu do Gato de Fogo', NULL, NULL, 'Curto alcance', NULL, NULL),
(329, 'Chama do Grande Fogo Abanado', 'Tobi abana seu gunbai para desencadear três tornados de fogo que explodem no adversário.', '煽炎豪火', 'Sen\'en Gōka', 'Chama do Grande Fogo Abanado', 'Grande Fogo Atiçado', NULL, NULL, 'Todos os alcances', NULL, NULL),
(330, 'Chamas da Verdade de Samadhi', 'As Chamas da Verdade de Samadhi constituem uma técnica de Liberação de Fogo de Koji Kashin, que as descreve como as \'Chamas do Purgatório\' (煉獄の炎, Rengoku no Honō).', '三昧の真火ざんまいのしんか', 'Zanmai no Shinka', 'Chamas da Verdade de Samadhi', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Rato'),
(331, 'Chegada do Demônio', 'Zabuza deixa seu inimigo indefeso ataca seu oponente com o Kubikiribōchō, avança sobre ele com uma aura demoníaca ao seu redor e corta seu oponente.', '鬼来', 'Kirai', 'Chegada do Demônio', 'Corte do Demônio', NULL, NULL, 'Curto alcance', NULL, NULL),
(332, 'Chibaku Tensei', 'Chibaku Tensei é uma técnica usada através dos poderes do Rinnegan.', '地爆天星', 'Chibaku Tensei', 'Corpo Celestial Estourado da Terra', 'Devastação Planetária Catastrófica', 'Estrela da Devastação', 'Devastação Planetária', 'Longo alcance', 'Rank –', 'Ativar: Juntar as maos,Compactar: Cobra[3],Mover: Carneiro[4]'),
(333, 'Chibaku Tensei: Queda Celestial', 'Sasuke convoca seu Susanoo e atira uma enxurrada de flechas, então ele manifesta o Corpo Completo — Susanoo e cria vários satélites infundidos em chamas negras, que são jogados sobre seu oponente, causando ondas de choque por todo o planeta e uma explosão que pode ser vista do espaço.', '地爆天星・天堕ちばくてんせい・あまお', 'Chibaku Tensei: Amao', 'Corpo Celestial Estourado da Terra: Queda Celestial', 'Estrela da Devastação: Queda Celestial', NULL, NULL, 'Todos os alcances', NULL, 'Fogo → Terra'),
(334, 'Chicote da Concha', 'Depois de Kandachi realizar os selos de mão necessários, o Rei Concha usa uma ou mais das línguas com seu chakra para chicotear ou restringir o adversário. O usuário também pode bombear seu chakra para o Rei Concha aumentar o comprimento e velocidade da língua. É mostrado que seções de golpes repetidos com a lingueta pode eventualmente matar a vítima.', '巻き貝鞭まきかいベン', 'Makigai Ben', 'Chicote da Concha', NULL, NULL, NULL, 'Media a Longo alcance', NULL, 'Tigre → Cao → Cobra'),
(335, 'Chicote do Amor', 'O usuário (normalmente um sapo) estende sua língua para fora com uma velocidade incrível e força, entregando um poderoso golpe para o oponente. O sapo também pode usar sua língua para envolver o inimigo e esmagá-lo no chão. Gamabunta ainda foi capaz de usar esta técnica defensivamente para proteger Naruto da areia de Gaara.', '愛の鞭あいのむち', 'Ai no Muchi', 'Chicote do Amor', NULL, NULL, 'Chicote de Amor', 'Curto alcance', NULL, NULL),
(336, 'Chidori', 'O Chidori é a primeira técnica original de Kakashi Hatake, embora mais frequentemente usa uma versão Rank S conhecida como o Corte Relâmpago. Ele a criou em torno do tempo em que ele foi promovido a jōnin, e mais tarde ensinou a Sasuke Uchiha.', '千鳥ちどり', 'Chidori', 'Mil Pássaros', 'Cortador de Relâmpago: Corte Original (雷光斬り・真斬, Raikōkiri: Maki)[2]', NULL, NULL, 'Curto alcance', 'Rank A', 'Boi → Coelho → Macaco[4] ou (Macaco → Dragao → Rato → Passaro → Boi → Cobra → Cão → Tigre → Macaco)[5]'),
(337, 'Chidori Batendo Asas', 'O Chidori Batendo Asas é uma forma mais poderosa do Chidori utilizado por Sasuke Uchiha.', '羽撃く千鳥はばたくちどり', 'Habataku Chidori', 'Mil Pássaros Batendo Asas', 'Chidori Lamentado, Chidori Escuro, Lamento do Chidori', 'Revoada dos Mil Pássaros', 'Chidori Negro', 'Curto alcance', NULL, NULL),
(338, 'Chidori Escuro', 'O Chidori Escuro é uma das várias versões do Chidori utilizada por Sasuke Uchiha, após receber metade do poder de Hagoromo.', '黒き千鳥くろきちどり', 'Kuroki Chidori', 'Mil Pássaros Escuros', 'Chidori Ônix', NULL, NULL, 'Curto alcance', NULL, NULL),
(339, 'Chidori Senbon', 'Esta é uma das muitas variações do Chidori que utiliza a transformação da forma. Em comparação com o Chidori padrão, é ainda mais especializado em velocidade, ao ser transformado em inúmeras senbon afiadas, que são apontadas e disparadas contra o inimigo. Ao aumentar a quantidade de chakra usado, o número de senbon também aumenta proporcionalmente. Quando usado em conjunto com o Sharingan, torna-se muito mais eficaz e mortal, uma vez que permite ao usuário disparar com precisão para os pontos vitais do alvo.', '千鳥千本ちどりせんぼん', 'Chidori Senbon', 'Senbon dos Mil Pássaros', NULL, 'Agulhas dos Mil Pássaros', NULL, 'Curto a Médio alcance', 'Rank A', NULL),
(340, 'Chidori: Divisor Planetário', 'Sasuke desfere um poderoso golpe com sua espada em seu oponente, lançando-o para cima e, em seguida, o prende dentro de um Chibaku Tensei, usando-o como núcleo gravitacional para atrair os escombros ao seu redor. O meteoro, então, começa a cair do céu, pegando fogo no processo. Em seguida, o usuário rodeia-se de eletricidade e, com um Chidori em sua mão, golpeia o satélite em queda, resultando em uma explosão que pode ser vista do espaço.', NULL, NULL, 'Mil Pássaros: Divisão Planetária', 'Chidori: Divisão Planetária', NULL, NULL, 'Longo alcance', NULL, NULL),
(341, 'Chidori: Queda', 'Sasuke expele um Chidori para o ar. Ele se move extremamente rápido em um padrão zig-zag, de tal forma que ele deixa pós-imagens. Ele ataca o oponente com apenas um golpe. Esta técnica, em combinação com a velocidade de Sasuke, faz com que seja extremamente difícil, se não impossível de evitar.', '千鳥・楽', 'Chidori: Raku', 'Mil Pássaros: Queda', 'Chidori (Mil Pássaros)', NULL, NULL, 'Curto alcance', NULL, NULL),
(342, 'Chidori: Trovão', 'Uma variante do Chidori, que é uma tempestade com relâmpagos Chidori que devasta a área.', '千鳥・雷鳴ちどり・らいめい', 'Chidori: Raimei', 'Mil Pássaros: Trovão', 'Trovão Chidori', NULL, NULL, 'Curto alcance', NULL, NULL),
(343, 'Chifre do Lobo do Castelo de Areia', 'O usuário envolve um ou mais alvos em um vórtice de areia, que pode esmagá-los em seu interior. Ela também pode ser utilizada como um meio de outras técnicas de areia, como por exemplo a criação de vários olhos para recolher informações ou como preparação para uma técnica destrutiva em massa.', '砂城狼角', 'Sunajō Rōkaku', 'Chifre Lupino do Castelo de Areia', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(344, 'Choque Demolidor', 'O Dez-Caudas bate com três de suas caudas sobre o seu oponente.', '潰砕衝', 'Kaisaishō', 'Choque Demolidor', 'Choque Esmagador', NULL, NULL, 'Curto alcance', NULL, NULL),
(345, 'Choque Desorientador do Corpo', 'Uma técnica extremamente de alto nível, usada como um ninjutsu médico. O corpo humano é controlado com sinais elétricos do cérebro, mas uma pessoa que tiver esses sinais elétricos cortados com esta técnica se tornará incapaz de fazer seu movimento corporal como eles querem. Ao transformar o chakra dentro do corpo de alguém e dar-lhe as propriedades da eletricidade, se cria um campo elétrico. Assim que um ataque da mão pousa, a eletricidade é vertida para o sistema nervoso do inimigo, cortando os sinais e desorganizando seu controle corporal. O corpo do alvo se movem de alguma forma, exceto como eles querem que ele se mova. Para os shinobi comum, em batalha, é mencionado mesmo apenas para andar, torna-se impossível. Por ser uma especialista em tratamentos médicos, Tsunade foi capaz de dominar esta técnica.', '乱身衝', 'Ranshinshō', 'Choque Desorientador do Corpo', NULL, 'Choque da Desorientação', 'Ruptura do Sistema Nervoso', 'Curto alcance', 'Rank A', NULL),
(346, 'Choque do Canhão de Chakra', 'Bandō agarra seu oponente pelo braço e joga-o no chão com uma força tremenda, saltando no processo. Em seguida, enquanto no ar, ele pega o canhão de suas costas e com seu braço direito, ele carrega uma quantidade imensurável de chakra, que é liberada em forma de míssil, explodindo quando em contato com o corpo do oponente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio a Longo alcance', NULL, NULL),
(347, 'Choque do Furacão da Folha', 'Uma variação do Furacão da Folha onde Guy e Lee atacam um único alvo com um chute.', '木ノ葉つむじ旋風このはつむじせんぷう', 'Konoha Tsumuji Senpū', 'Choque do Furacão da Folha', NULL, 'Redemoinho da Folha', NULL, 'Curto alcance', NULL, NULL),
(348, 'Choro Celestial', 'O Choro Celestial é um ninjutsu utilizado por Tobirama Senju.', '天泣', 'Tenkyū', 'Choro Celestial', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(349, 'Choro da Flor', 'Kimimaro, quando em sua forma de selo amaldiçoado nível dois, executa um corte no oponente com sua espada óssea, bate contra o oponente com sua cauda com picos ósseos e finaliza correndo contra o oponente com sua broca óssea.', 'しだれ花', 'Shidarehana', 'Choro da Flor', 'Spiranthes', NULL, NULL, 'Curto alcance', NULL, NULL),
(350, 'Chute Caído', 'O usuário realiza um chute poderoso que envia o oponente voando para longe. Ele foi poderoso o suficiente para lançar Killer B através de várias estruturas de Kumogakure.', 'ドロップキック', 'Doroppu Kikku', 'Chute Baixo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(351, 'Chute Celestial do Pilar de Madeira', 'Yamato ataca seu oponente com uma série de socos e chutes, e em seguida, transforma o seu braço esquerdo em um pilar de madeira que iniciou-se anteriormente do chão, levantando seu oponente pro alto, dando a oportunidade de Yamato ir até o seu alvo e finalizar com um chute fatal.', NULL, NULL, NULL, 'Chute Celestial do Pilar de Madeira', NULL, NULL, 'Curto alcance', NULL, NULL),
(352, 'Chute Derrapante', 'Kisame corta seu oponente com a Samehada, e em seguida, salta executando um chute por baixo.', '蹴滑り', 'Kesuberi', 'Chute Derrapante', 'Chute de Deslize', NULL, NULL, 'Curto alcance', NULL, NULL),
(353, 'Chute Giratório do Pilar de Madeira', 'Yamato faz um pilar de madeira erguer-se a partir do chão e, em seguida, agarra em seu pilar e começa a girar, dando vários chutes em seu oponente.', NULL, NULL, NULL, 'Chute Giratório do Pilar de Madeira', NULL, NULL, 'Curto alcance', NULL, NULL),
(354, 'Chute da Busca da Verdade', 'O usuário chuta a Esfera da Busca da Verdade em seu oponente. O impacto do chute na esfera foi forte o suficiente para deixar uma cratera no tronco da Árvore Divina.', '求道蹴ぐどうしゅう', 'Gudōshū', 'Chute da Busca da Verdade', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(355, 'Chute da Lança do Céu', 'Tsunade salta à grandes alturas e desce colidindo com seu inimigo com uma enorme quantidade de força, sendo poderoso o suficiente para quebrar a caixa torácica do Susanoo de Madara Uchiha.', NULL, NULL, NULL, 'Chute da Lança do Céu', NULL, NULL, 'Curto alcance', NULL, NULL),
(356, 'Chute de Avestruz Brilhante', 'Condor, enquanto no ar, acerta o oponente com um poderoso chute.', 'シャイニング駝鳥キックシャイニングだちょうキック', 'Shainingu Dachō Kikku', 'Chute de Avestruz Brilhante', NULL, NULL, NULL, 'Curto Alcance', NULL, NULL),
(357, 'Chute em Míssil', 'Karin corre contra o oponente e pula com ambos os pés contra o peito dele, lançando-o a metros de distância no chão. O impacto é ainda maior se bater na parede, já que ela possui direcionamento vertical.', 'ミサイルキック', 'Misairu Kikku', 'Chute em Míssil', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(358, 'Chutes Furiosos da Besta-Humana', 'Em seu estado transformado, o shinobi do clã Tenrō desfere uma infinidade de chutes poderosos contra o oponente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(359, 'Chuva Rancorosa', 'Esta técnica requer uma quantidade variada de usuários. Os usuários criam uma grande nuvem de tempestade, que por sua vez produz chuva. A grande quantidade de chuva pode se transformar em uma grande onda de água que é capaz de varrer o inimigo de uma área, desconectando seus pés do chão. Enquanto os alvos são levados pela água, seu chakra será drenado continuamente.', '怨雨うらみあめ', 'Uramiame', 'Chuva Rancorosa', NULL, NULL, 'Arte Ninja: Chuva de Rancor', 'Médio a Longo alcance', NULL, 'Cao'),
(360, 'Chuva Verde de Cura', 'Sarada empunha oito kunais, cada uma com um selo de cura anexado em si, então ela as arremessa para os pontos cardeais, delimitando o terreno. As kunais se infundem com chakra yang e uma barreira esférica é erguida, curando gradativamente todos que estiverem em seu interior enquanto a técnica permanecer ativa.', NULL, NULL, NULL, 'Chuva Verde de Cura', NULL, NULL, 'Curto a Médio alcance', NULL, NULL);