INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(2440, 'Técnica de Selamento: Rugido Final do Leão', 'O Canhão da Cabeça de Leão, é preparado com uma fórmula de selamento que, em seguida, é lançado contra o alvo. Quando a ferramenta cresce e muda de forma, ela rapidamente solta um enorme rugido de leão e entra em contato com o alvo, impedindo assim seu fluxo de chakra, fazendo com que o kanji para \'Fechar\' (閉, Hei) apareça no atrás da mesma.', '封印術・獅子閉哮ふういんじゅつ・ししへいこう', 'Fūinjutsu: Shishi Heikō', 'Técnica de Selamento: Rugido Final do Leão', NULL, NULL, NULL, 'Curto alcance', 'Rank A', NULL),
(2441, 'Técnica de Selamento: Selamento de Espírito', 'É necessário um pergaminho para realizar a técnica. Ela permite ao usuário selar a alma de um ser humano dentro dele.', '封印術・精神封印ふういんじゅつ・せいしんふういん', 'Fūinjutsu: Seishin Fūin', 'Técnica de Selamento: Selamento de Espírito', 'Jutsu de Selamento: Selo do Espírito', NULL, NULL, NULL, NULL, NULL),
(2442, 'Técnica de Selamento: Selo de Ponderação dos Quatro Membros', 'Esta técnica é realizada colocando os grânulos de oração em torno dos pulsos e tornozelos do alvo e, em seguida, pressionando a palma da mão contra o peito do alvo. Isto aumenta o peso dos grânulos de forma exponencialmente, pesando os membros do alvo.', NULL, NULL, NULL, 'Jutsu de Selagem: Selo de Ponderação dos Quatro Membros', NULL, NULL, 'Curto alcance', NULL, NULL),
(2443, 'Técnica de Selamento: Selo de Três Lados', 'O usuário cria dois clones das sombras para formar um selo triangular que isola o oponente dentro. Em seguida, o selo se ilumina intensamente antes de selar o oponente sem deixar vestígios.', '封印術・三方封印ふういんじゅつ・さんほうふういん', 'Fūinjutsu: Sanhō Fūin', 'Técnica de Selamento: Selo de Três Lados', 'Jutsu de Selamento: Selo Triangular', NULL, NULL, 'Curto alcance', NULL, NULL),
(2444, 'Técnica de Selamento: Supressão do Sapo', 'O usuário ergue quatro pilares a partir do chão, cercando seus inimigos. Ao seu comando, os pilares geram uma barreira que sela os movimentos daqueles que estiverem confinados em seu interior. Os efeitos dessa técnica podem ser combatidos através do uso de um selo de liberação.', '封印術・蛙呑み', 'Fūinjutsu: Kawazunomi', 'Técnica de Selamento: Supressão do Sapo', NULL, NULL, NULL, 'Curto alcance', NULL, 'Preparar: Carneiro,Restringir: Cobra'),
(2445, 'Técnica de Selamento da Besta', 'Depois de criar os selos necessários, as formas dos selos se destacam e se tornam múltiplas correntes de chakra que envolvem o alvo. Honoka usou esta técnica para tentar selar a invocação suprema que estava sendo produzida na ilha, mas falhou e morreu. Sua alma ficou vagando na ilha até o momento de concluir o selamento, com a ajuda dos ninjas de Konoha.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2446, 'Técnica de Selamento do Lobo Uivante', 'É uma técnica de selamento, concebida para selar Rōen no santuário do Clã Kodon, ou liberá-lo. Esta técnica inclui uma posição específica. A morte é certa após o uso desta técnica, mas o próprio selo só dura por sete anos, após o qual, a besta será capaz de vagar novamente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio Alcance', NULL, NULL),
(2447, 'Técnica de Selamento do Poder Budista: Sen no Rikyū', 'Com o auxílio de um instrumento musical e de uma fórmula de fūinjutsu no seu peito, o usuário produz um fluxo de chakra que cria muitas correntes de cor roxa que são lançadas pela fórmula até o alvo. As correntes se movem de modo muito rápido, capazes de ultrapassar o Escudo de Areia de Gaara, o qual é conhecido por sua velocidade. Uma vez que encontram-se trancadas no corpo do alvo, elas irão eventualmente alcançar as bestas com cauda e comprimir os seus pescoços, puxando as bestas de dentro do jinchūriki, os quais ficam aparentemente paralisados durante a extração. Outros que tentarem tocar as correntes serão atingidas por um poderoso choque elétrico.', '法力封印術・仙除離吸ほうりきふういんじゅつ・センノリキュウ', 'Hōriki Fūinjutsu: Sen no Rikyū', 'Técnica de Selamento do Poder Budista: Absorção de Separação Removedora Eremita', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(2448, 'Técnica de Selamento do Zero-Caudas', 'A Técnica de Selamento do Zero-Caudas é uma forma única de selamento, que foi transmitida ao País do Céu e, usada para conter o Zero-Caudas.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2449, 'Técnica de Separação de Alma–Corpo', 'Com o uso de um etiqueta de selamento, Sabiru é capaz de criar um selo que afeta uma determinada área em torno do local utilizado. Todos os inimigos dentre dos limites dessa área, terão suas almas separadas de seu corpo e seladas dentro da etiqueta de selamento utilizada. Caso algum inimigo toque ou remova a etiqueta de selamento, no processo, sua alma será liberada de seu corpo.', '魂身別離の術', 'Konshin Betsuri no Jutsu', 'Técnica de Separação de Alma–Corpo', NULL, NULL, 'Jutsu de Sugação da Alma', NULL, NULL, NULL),
(2450, 'Técnica de Shuriken Manipulada', 'Com esta técnica, uma linha translúcida, elástica e altamente resistente, é acoplada a uma shuriken, tornando possível alterar o seu percurso, depois de ter sido jogada. Um especialista tem a habilidade de controlar livremente a shuriken em qualquer direção com apenas um movimento único de seu dedo. O primeiro ataque, desafiando o inimigo, é evitado e após um lapso de tempo de vários segundos, ele vai vir de trás. Dependendo do desempenho, as táticas possíveis podem ser infinitas.', '操手裏剣の術', 'Sōshuriken no Jutsu', 'Técnica de Manipulação de Shuriken', NULL, NULL, NULL, 'Todos os alcances', 'Rank D', NULL),
(2451, 'Técnica de Shuriken Uchiha: Tempestade da Primavera', 'Sarada prepara várias kunai com etiquetas de tratamento e papéis bomba anexados a elas. As kunais são lançadas pelo campo de batalha, explodindo inimigos e curando aliados.', 'うちは手裏剣術・春嵐', 'Uchiha Shurikenjutsu: Shunran', 'Técnica de Shuriken Uchiha: Tempestade da Primavera', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2452, 'Técnica de Substituição do Corpo', 'Com esta técnica, os usuários substituem o seu próprio corpo por algum outro objeto, geralmente um bloco de madeira, no momento em que um ataque pousa. Isso cria uma ilusão de ótica, fazendo com que o inimigo pense que o ataque foi bem sucedido. A partir daí, o usuário pode usar o lapso de atenção do inimigo para atacar ou fugir do campo de batalha. Alguns papéis explosivos podem ser anexados à substituição em uma surpresa adicional. É um ninjutsu básico mesmo ensinado na Academia, mas é uma arte útil que pode ser aplicada em diversas situações.', '変わり身の術かわりみのじゅつ', 'Kawarimi no Jutsu', 'Técnica de Substituição do Corpo', NULL, NULL, 'Jutsu de Substituição', NULL, 'Rank E', 'Carneiro → Javali → Boi → Cao → Cobra'),
(2453, 'Técnica de Transferência Celestial', 'Com esta técnica, Mabui é capaz de transportar objetos em qualquer lugar na velocidade da luz. Ao ser transferido, o objeto é envolvido em luz e se move para qualquer destino desejado através de um feixe estreito, uma reminiscência de um raio. Em todos os casos da técnica apresentada, o objeto transportado foi colocado sobre um tipo de plataforma.', '天送の術', 'Tensō no Jutsu', 'Técnica de Transferência Celestial', NULL, 'Técnica da Transferência Celestial', NULL, 'Todos os alcances', NULL, 'Carneiro'),
(2454, 'Técnica de Transferência da Besta com Cauda', 'Esta técnica permite que uma pessoa transfira o chakra completo de uma besta com cauda selada em um jinchūriki para outra pessoa diferente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Sombra → Selo Desconhecido → Relâmpago → Selo Desconhecido → Selo Desconhecido → Sombra'),
(2455, 'Técnica de Transformação', 'Uma vez que as missões ninja que são atribuídas envolvem batalha, coleta de informações, e outras adversidades, este é um ninjutsu de valor inestimável. Ela é geralmente usada para se transformar em outras pessoas, mas também tem a capacidade de se transformar em animais, plantas e até mesmo objetos inanimados, como armas — algo extremamente difícil uma vez que há a necessidade de copiar uma matéria diferente de sua composição humana. Isto dá a esta técnica uma grande quantidade de usos. A transformação de um shinobi habilidoso será exatamente como o alvo desejado, por isso é impossível dizer qual é cada um. Por outro lado, a transformação realizada por uma pessoa inexperiente terá diferenças óbvias, portanto, será impossível enganar alguém com ela. Este é um dos ninjutsu mais básicos, como tal, a maioria dos shinobi sabem como realizá-lo. Além disso, é possível transformar animais em humanos ou objetos ao invés de seu próprio corpo.', '変化の術へんげのじゅつ', 'Henge no Jutsu', 'Técnica de Transformação', NULL, NULL, 'Jutsu de Transformação', NULL, 'Rank E', 'Ativar: Cao → Javali → Bode,Cancelar: Selo do Confronto[2]'),
(2456, 'Técnica de Transformação da Cobra', 'A Técnica de Transformação da Cobra é um dos kinjutsu criado por Orochimaru a fim de alcançar a imortalidade, o que resultou em uma mudança de forma e poder. É citado que essa técnica é um dos triunfos de Orochimaru, dado que poucos realmente viram ele usando e, sobreviveram ao ver.', '蛇変化の術へびへんげのじゅつ', 'Hebi Henge no Jutsu', 'Técnica de Transformação da Cobra', NULL, NULL, NULL, NULL, NULL, NULL),
(2457, 'Técnica de Transformação de Taisa', 'Taisa Amagiri ativa esta técnica, formando o selo de mão necessário. Uma vez ativada, Taisa se transforma em um besouro blindado semelhante a um kabutomushi. Ele, então, ganha seis pernas que são maioritariamente enfaixadas, cinco chifres e marcas alaranjadas e violetas por toda sua parte de trás do corpo. Nesta forma, Taisa é capaz de usar poderosos ataques carregados com seus chifres e pode liberar uma névoa venenosa de seu corpo. Ele também é capaz de saltar alto no ar e cair para baixo na terra sobre o inimigo com muita força. A única parte vulnerável dessa forma são as suas entranhas, e ele tem que de alguma forma ser derrubado para um ataque acertá-lo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2458, 'Técnica de Transformação de Yomito', 'Yomito ativa esta técnica, colocando a máscara, que permanecia presa ao seu braço direito, no rosto.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2459, 'Técnica de Transformação em Espírito', 'Esta técnica permite que o espírito de Dan deixe seu corpo e tome posse de um alvo dentro de um determinado intervalo, capacitando-o a manipular livremente o corpo da vítima à vontade. Dan afirmou ainda que essa técnica tinha sido usada para matar um grande número de shinobi durante sua vida. A única forma conhecida para contrariar a técnica é prender o usuário dentro de uma barreira, como a Formação das Quatro Chamas Violeta, antes que ele possa ativá-la.', '霊化の術', 'Reika no Jutsu', 'Técnica de Transformação de Espírito', NULL, 'Técnica da Possessão Viva', NULL, NULL, 'Rank S', 'Tigre → Carneiro → Rato → Cavalo → Javali → Lebre'),
(2460, 'Técnica de Translocação', 'Esta técnica permite que Pain aloque uma parte do chakra de um indivíduo e o transfira para um sacrifício humano vivo, permitindo que o original lute através de uma \'cópia\' elaborada. A aparência da pessoa a quem a técnica é realizada é completamente idêntica à pessoa original. A técnica irá imitar quaisquer armas, ferramentas ou kekkei genkai realizada pela pessoa original, permitindo a cópia de seu estilo de luta. O poder da cópia é proporcional ao volume de chakra atribuído, o que é decidido por Pain. As técnicas do original também estão disponíveis, mas uma vez que o chakra alocado está esgotado, a técnica é cancelada e a cópia morre. Depois, a aparência da cópia irá reverter para o corpo do sacrifício. Como ele mesmo utiliza o chakra do sacrifício, esta é uma técnica extremamente brutal.', '象転の術', 'Shōten no Jutsu', 'Técnica de Translocação', 'Jutsu de Imitação[2]', NULL, 'Jutsu de Personificação', 'Todos os alcances', 'Rank –', NULL),
(2461, 'Técnica de Transmissão da Mente do Corpo', 'A Técnica de Transmissão da Mente do Corpo é um hijutsu utilizado pelos membros do clã Yamanaka para se comunicar telepaticamente com pessoas.', '心伝身の術しんでんしんのじゅつ', 'Shindenshin no Jutsu', 'Técnica de Transmissão da Mente do Corpo', NULL, 'Técnica de Transmissão de Mente', NULL, 'Todos os alcances', NULL, 'Carneiro'),
(2462, 'Técnica de Troca da Mente do Corpo', 'A Técnica de Troca da Mente do Corpo (心転身の術, Shintenshin no Jutsu) é a habilidade de assinatura do clã Yamanaka.', '心転身の術', 'Shintenshin no Jutsu', 'Técnica de Troca da Mente do Corpo', NULL, 'Técnica de Transferência de Mente', 'Jutsu Transferência de Mente', 'Todos os alcances', 'Rank C', 'Selo de mao especifico da técnica'),
(2463, 'Técnica de Água e Vento da Onda Selvagem', 'Neste konbijutsu, o usuário de Liberação de Água desencadeia uma onda de água que vai em direção ao oponente, enquanto o usuário de Liberação de Vento aumenta a capacidade destrutiva das ondas com o vento.', '風水乱波の術', 'Fūsui Rappa no Jutsu', 'Técnica de Água e Vento da Onda Selvagem', 'Jutsu da Raiva da Água e Vento', NULL, NULL, NULL, NULL, NULL),
(2464, 'Técnica do Balão de Água', 'Depois de usar a Técnica de Hidratação para formar uma piscina de água em seus pés, o usuário então produz bolhas que flutuam para cima a partir do solo. Estes são, então, lançados em alvos com tal velocidade e força que eles podem causar trauma, já que tem uma grande força contusa.', '水風船の術', 'Mizu Fūsen no Jutsu', 'Técnica do Balão de Água', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2465, 'Técnica do Cabelo Louco', 'Enquanto em seu Modo Sábio, Jiraiya chuta os seus oponentes e os soca antes de atingi-los quatro vezes com o seu cabelo estendido.', '乱れ髪の術みだれかみのじゅつ', 'Midare Kami no Jutsu', 'Técnica do Cabelo Louco', 'Jutsu do Cabelo Desordenado', NULL, NULL, 'Curto alcance', NULL, NULL),
(2466, 'Técnica do Campo Minado', 'Obito prepara algumas bombas de papéis explosivos e entra no subsolo, indo em direção ao oponente, colocando, em seguida, minas terrestres embaixo do oponente e escapando, fazendo com que o inimigo exploda e com que ele se mantenha em segurança.', '地雷原の術', 'Jiraigen no Jutsu', 'Técnica do Campo Minado', 'Jutsu do Campo Minado', NULL, NULL, 'Curto alcance', NULL, NULL),
(2467, 'Técnica do Casulo Humano', 'Shino cria um casulo em torno de si para usá-lo como um \'saco de dormir\', à prova d\'água. Ele também pode ser usado para esconder de seus inimigos ou para espioná-los.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2468, 'Técnica do Chicote das Sombras', 'Após performar o selo de mão necessário, o usuário estica sua sombra energizada na forma de um chicote afunilado que pode ser usado para derrubar os alvos de alturas acima do alcance humano.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Selo desconhecido'),
(2469, 'Técnica do Clone Bolha', 'Utakata cria um clone de si mesmo usando seu Ninjutsu de Bolha de Sabão com seu cachimbo de bolhas. Quando atingido, o clone estala como uma bolha.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2470, 'Técnica do Clone Cadáver', NULL, '屍分身の術しかばねのじゅつ', 'Shikabane no Jutsu', 'Técnica do Clone Cadáver', NULL, NULL, NULL, NULL, NULL, NULL),
(2471, 'Técnica do Clone da Sombra de Bō', 'O usuário lança um bō e cria várias cópias de partir deste, a fim de derrubar ou capturar o inimigo. Esta é uma técnica que combina armas ninja com ninjutsu, no entanto, não há a necessidade de preparação prévia, como estocar diversos bō, uma vez que clones das sombras são reais e não ilusões e, pelo fato dos clones se formarem em apenas um instante, evadir desse ataque completamente é muito difícil.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(2472, 'Técnica do Clone da Sombra de Shuriken', 'Esta é uma técnica de shurikenjutsu combinada com bunshinjutsu inventada por Hiruzen Sarutobi, a qual a conceituação e eficiência com ela certamente podem ser ditas sendo únicas para o \'Professor\'.', '手裏剣影分身の術', 'Shuriken Kage Bunshin no Jutsu', 'Técnica do Clone da Sombra de Shuriken', 'Jutsu dos Clones das Sombras de Shuriken', NULL, 'Arte Ninja: Jutsu Clone da Sombra de Shuriken', 'Curto a Médio alcance', 'Rank A', 'Carneiro → Rato → Passaro → Javali → Tigre'),
(2473, 'Técnica do Clone de Cobra', 'Usando cobras como um catalisador, o usuário pode criar um clone de si mesmo que possui uma função similar ao clone das sombras. Quando causado dano o suficiente, o clone é revertido em cobras novamente. As cobras podem também ser usadas para entrar em áreas que o usuário encontra grande dificuldade de acessar de outra forma. O usuário pode então manipular suas ações e ganhar a morfologia da própria cobra ou transformá-la em um clone, quando dado o comando.', '蛇分身の術', 'Hebi Bunshin no Jutsu', 'Técnica do Clone de Cobra', NULL, NULL, NULL, NULL, NULL, NULL),
(2474, 'Técnica do Clone de Corvo', 'Uma técnica que produz um clone através da projeção de seu próprio chakra com dezenas de corvos. O usuário então usa os corvos como um meio, que exige menos chakra do que a técnica de clonagem normal, enquanto continuam sendo capazes de executar técnicas. Dezenas de corvos se juntam para formar o corpo de um clone. Quando o chakra projetado pelo usuário é cortado, os corvos se dispersam, embora o usuário seja capaz de refazê-lo. O usuário também recebe as informações vividas pelos seus clones.', '烏分身の術', 'Karasu Bunshin no Jutsu', 'Técnica do Clone de Corvos', NULL, NULL, 'Jutsu do Clone de Corvo', NULL, 'Rank C', NULL),
(2475, 'Técnica do Clone de Cristal de Jade', 'Após utilizar a técnica Liberação de Cristal: Espelho de Cristal de Jade, Guren é capaz de formar múltiplos clones de si mesma utilizando do espelho de cristal. No momento em que Hinata Hyūga atacou um clone, foi demonstrado que o clone foi moldado em cristal, logo em seguida, se desfez, transformando-se em mínimos cristais. Os clones gerados nesta técnica são capazes de confundir os utilizadores do Byakugan, isso é feito devido aos reflexos causados pela luz no clone. Porém, quando o utilizador foca completamente sua visão no adversário, os reflexos não interferem sua vista.', '翠晶分身の術すいしょうぶんしんのじゅつ', 'Suishō Bunshin no Jutsu', 'Técnica do Clone de Cristal de Jade', NULL, NULL, 'Clone de Cristal de Jade', NULL, NULL, 'Cavalo → Carneiro'),
(2476, 'Técnica do Clone de Inseto', 'A técnica secreta exclusiva para o clã Aburame, onde milhares de kikaichū estão reunidos em um só lugar para assumir a aparência do usuário ou qualquer outra pessoa que o usuário desejar. A técnica é elaborado o suficiente para ser confundida com o original, e, portanto, pode ser utilizada como um chamariz ou como parte de uma manobra de diversão. Além disso, como se pode colocar os insetos em espera em algum lugar antes e depois tê-los assumir a forma de o clone, essa técnica é indiscutivelmente mais eficaz do que a Técnica dos Clones das Sombras, dependendo da tarefa. O clone se desfaz em insetos quando atingido. Por ser feito de insetos, ele pode se regenerar quase que instantaneamente.', '蟲分身の術', 'Mushi Bunshin no Jutsu', 'Técnica do Clone de Inseto', NULL, 'Técnica dos Clones de Insetos', NULL, NULL, NULL, NULL),
(2477, 'Técnica do Clone de Madeira', 'Um clone que é criado usando chakra para alterar as células do próprio usuário em vegetação. Os clones podem ser tão simples como em função de um boneco de madeira para utilização na Técnica de Substituição do Corpo ou completamente móvel e capaz de executar técnicas. Eles têm a capacidade de viajar para longe do usuário e são capazes de se comunicar com o original. Além disso, uma vez que o clone de madeira tem a capacidade de fundir-se com plantas e árvores, também é excelente para missões de reconhecimento. Ao tocar diretamente no clone de madeira, com a mão, o utilizador pode absorver as informações recolhidas e alterar a forma do clone. Ao contrário de outras técnicas de clone, o clone de madeira não desaparece quando atingido e leva danos a um certo grau. Estes clones também podem ser dissipados como clones normais, no entanto.', '木分身の術もくぶんしんのじゅつ', 'Moku Bunshin no Jutsu', 'Técnica do Clone de Madeira', NULL, 'Estilo Madeira: Técnica dos Clones de Madeira', 'Jutsu Clone de Madeira', NULL, 'Rank –', 'Tigre → Cao → Cobra'),
(2478, 'Técnica do Clone de Neblina', 'À primeira vista, parece ser uma Técnica de Clonagem regular, mas o clone de neblina usa uma ardilosa armadilha para atrair o inimigo. Usando esta técnica, juntamente com a Liberação de Terra: Técnica da Projeção do Peixe Subterrâneo, o inimigo é levado a acreditar que o corpo real está entre os clones. Como os clones de neblina não são fisicamente reais, o ataque vai apenas passar por eles. Enquanto a atenção é atraída para os clones, o corpo real ataca do ponto cego. Durante a ilusão e caos resultante, o inimigo, que está preso na técnica, pode ser facilmente derrotado.', '朧分身の術', 'Oboro Bunshin no Jutsu', 'Técnica do Clone de Neblina', 'Jutsu Clone do Nevoeiro[2]', NULL, 'Arte Ninja: Jutsu do Clone de Nevoeiro', NULL, 'Rank D', 'Cachorro → Tigre'),
(2479, 'Técnica do Clone de Rocha', 'Um clone que é criado de rocha, depois de ter sido expelido da boca do usuário. Ao contrário dos outros clones, aqueles gerados por esse método não desaparecem quando acertados com força, mas se separam.', '岩分身の術', 'Iwa Bunshin no Jutsu', 'Técnica do Clone de Rocha', NULL, 'Clones de Pedra', 'Jutsu do Clone de Pedra', NULL, NULL, NULL),
(2480, 'Técnica do Clone de Tinta', 'Este clone, feito a partir de uma pequena quantidade de tinta, transportando chakra, é diferente do Técnica dos Clones das Sombras na medida em que tem a substância real e massa, tornando-o difícil de ser visto.', '墨分身の術', 'Sumi Bunshin no Jutsu', 'Técnica do Clone de Tinta', NULL, NULL, NULL, NULL, 'Rank C', NULL),
(2481, 'Técnica do Clone de Água', 'A Técnica do Clone de Água, é original de Kirigakure, é semelhante aos clones das sombras, exceto que cria clones de água que têm um décimo do poder da pessoa original. Tal como outras técnicas de clones sólidos, os clones podem ser usados ​​para executar as tarefas do usuário que estiver incapaz ou não quiser fazer por si mesmo. No entanto, O alcance do clone é limitado, uma vez que não pode viajar muito longe do corpo original sem perder o controle. Como outros bunshinjutsu, se os clones de água receberem dano o suficiente eles viram água normal.', '水分身の術みずぶんしんのじゅつ', 'Mizu Bunshin no Jutsu', 'Técnica do Clone de Água', NULL, NULL, 'Jutsu Clone de Água', NULL, 'Rank C', 'Tigre'),
(2482, 'Técnica do Clone do Tentáculo de Polvo', 'Enquanto na sua forma completa de Oito-Caudas, Killer B pode criar um clone a partir de um dos tentáculos do Oito-Caudas que pode então ser utilizado como uma isca, permitindo-lhe escapar. Este clone é impecável o suficiente para enganar ambos o Sharingan de Sasuke Uchiha como a Akatsuki. Quando dissipado, o clone é revertido para o tentáculo novamente.', 'タコ足分身の術', 'Takoashi Bunshin no Jutsu', 'Técnica do Clone do Tentáculo de Polvo', NULL, NULL, NULL, NULL, NULL, NULL),
(2483, 'Técnica do Corpo da Lanterna Mágica', 'Os membros da Akatsuki se sentam e mandam ondas de pensamento (思念波, shinenha), convertidas em chakra. Estas ondas de pensamento são, então, pegas por Pain, agindo como uma espécie de torre de controle. Essa técnica amplifica as ondas de pensamento e as transmite para um local específico através de órgãos ilusórios. Os corpos ilusórios diferidos são meros reflexos, enquanto Pain está afinando os pensamentos dos membros, que podem conversar e utilizar técnicas variadas.', '幻灯身の術げんとうしんのじゅつ', 'Gentōshin no Jutsu', 'Técnica do Corpo da Lanterna Mágica', NULL, NULL, 'Jutsu da Projeção Astral', 'Todos os alcances', 'Rank –', 'Carneiro'),
(2484, 'Técnica do Deus Voador do Trovão', NULL, '飛雷神の術ひらいしんのじゅつ', 'Hiraishin no Jutsu', 'Técnica do Deus Voador do Trovão', NULL, 'Técnica do Deus do Relâmpago', NULL, 'Todos os alcances', 'Rank S', NULL),
(2485, 'Técnica do Deus do Vento e Deus do Trovão', 'Uma técnica de colaboração usada por um usuário de Liberação de Vento e Liberação de Relâmpago. O usuário de Liberação de Vento cria um tornado gigante que gira em direção ao oponente, após o qual o usuário de Liberação de Relâmpago passa uma corrente elétrica através do tornado. Isso tem efeitos devastadores sobre o oponente.', '風神と雷神の術ふうじんとらいじんのじゅつ', 'Fūjin to Raijin no Jutsu', 'Técnica do Deus do Vento e Deus do Trovão', 'Jutsu Fujin e Raijin', NULL, NULL, NULL, NULL, NULL),
(2486, 'Técnica do Encantador de Cobra', 'Esta é uma técnica inspirada na habilidade de Orochimaru em trocar de corpos e sua afinidade com cobras.', '蛇遣いの術へびつかいのじゅつ', 'Hebitsukai no Jutsu', 'Técnica do Encantador de Cobra', 'Jutsu Usuário da Cobra', NULL, NULL, NULL, NULL, NULL),
(2487, 'Técnica do Espinho Agonizante (Kanchō)', 'O usuário acerta um chute descendente no oponente, cabeceia-o, se enterra no subsolo, e em seguida surge através do chão, realizando a prática kanchō no oponente.', '悶絶棘の術 (要するにカンチョー)', 'Monzetsutoge no Jutsu (Yōsuruni Kanchō)', 'Técnica do Espinho Agonizante', 'Picada Agonizante', NULL, NULL, 'Curto alcance', NULL, NULL),
(2488, 'Técnica do Globo de Areia Explosiva', 'Gaara salta no ar no ângulo de seu oponente e, em seguida, ele manipula sua areia e cria um gigante orbe de aparência rochosa que aprisiona-o. Enquanto com o oponente interceptado dentro do orbe de areia, Gaara fecha sua mão, esmagando-o.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2489, 'Técnica do Grande Combo de Cinco Elementos', 'O usuário dispara fluxos elementais de fogo, vento, relâmpago, terra e água de sua boca simultaneamente contra o oponente. Enquanto Hiruzen Sarutobi utilizou quatro clones das sombras para disparar os cinco fluxos elementais, Tobi foi capaz de utilizar a técnica através dos rostos de sua estátua de madeira.', '五遁大連弾の術ごとんだいれんだんのじゅつ', 'Goton Dairendan no Jutsu', 'Técnica do Grande Combo de Cinco Elementos', 'Jutsu de Combinação Massiva dos Cinco Estilos', NULL, NULL, 'Todos os alcances', NULL, NULL),
(2490, 'Técnica do Grande Combo de Três Elementos', 'Essa é uma técnica utilizada pelos praticantes de Ninshū ao combinar seus poderes.', NULL, NULL, NULL, NULL, NULL, NULL, 'Longo alcance', NULL, 'Carneiro + Cobra + Coelho + Tigre simultaneamente'),
(2491, 'Técnica do Grande Harém', 'Konohamaru cria um clone e segura seu oponente. Ele então se transforma com sua Técnica Sensual e os clones dele que estão de biquíni vão e abraçam o adversário. Enquanto o inimigo está distraído, Konohamaru e seu clone correm com o Rasengan e atacam o oponente.', '大ハーレムの術', 'Dai Hāremu no Jutsu', 'Técnica do Grande Harém', 'Jutsu do Grande Harém', NULL, NULL, 'Curto alcance', NULL, NULL),
(2492, 'Técnica do Harém', 'Esta técnica é considerada como sendo uma variação avançada da Técnica Sensual, que é combinada com a Técnica dos Clones das Sombras. Depois de criar cerca de uma dúzia de clones, Naruto usa essa técnica para transformá-los todos na mesma mulher bonita, nua (ou em um maiô, como em alguns episódios do anime e videogames). Naruto usa essa técnica quando ele ou quer uma chance melhor de derrubar sua vítima alvo, ou quando a Técnica Sensual não cumpre seu propósito. Quando Naruto usou esta técnica em uma tentativa fracassada de seduzir Killer B, Naruto criou os clones já transformados em mulheres nuas, em vez de transformá-los depois que eles foram criados. No mangá, a névoa de fumaça que cobre partes íntimas dessas mulheres é muito reduzida em comparação com a Técnica Sensual ou qualquer de suas variações.', 'ハーレムの術', 'Hāremu no Jutsu', 'Técnica do Harém', NULL, NULL, 'Jutsu Harém', NULL, 'Rank A', 'Carneiro → Cobra → Tigre → Carneiro'),
(2493, 'Técnica do Incêndio Elétrico do Lótus Carmesim', 'Esta técnica é uma técnica colaborativa entre Kakashi Hatake e Obito Uchiha.', '紅蓮雷火の術ぐれんらいかのじゅつ', 'Guren Raika no Jutsu', 'Técnica do Incêndio Elétrico do Lótus Carmesim', 'Jutsu da Chama Relâmpago do Lótus Carmesim', NULL, NULL, 'Todos os alcances', NULL, NULL),
(2494, 'Técnica do Inferno nas Sombras', 'Shikamaru controla sua sombra para abrir um buraco e derrubar o oponente para dentro da sombra. O oponente é enviado para um local desconhecido e escuro, com ruídos estranhos em plano de fundo, semelhante ao Inferno. Desmaiado, o oponente é de volta levado à superfície.', '影奈落の術かげならくのじゅつ', 'Kagenaraku no Jutsu', 'Técnica do Inferno nas Sombras', 'Jutsu do Cair das Sombras', NULL, NULL, 'Longo alcance', NULL, 'Rato'),
(2495, 'Técnica do Inseto Espião', 'Ao entrar em contato com um inseto, o usuário é capaz de ver e ouvir tudo que ele consegue transmitir em sua presença, fornecendo uma maneira de longo alcance e relativamente segura e discreta para recolher informações. O usuário também é capaz de saber quando o inseto for morto.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2496, 'Técnica do Instinto de Assassinato Compartilhado', 'Meizu e Gōzu preparam-se e lançam as correntes de suas Manoplas de Metal para o outro lado, puxando-a em sequência. Através de suas manoplas, eles são capazes de prender um inimigo e cortá-lo em pedaços.', '阿吽の暗殺術あうんのあんさつじゅつ', 'Aun no Ansatsu Jutsu', 'Técnica do Instinto de Assassinato Compartilhado', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(2497, 'Técnica do Jarro de Veneno', 'Usando seus insetos venenosos, Torune reúne múltiplas vítimas infectadas e os inclui dentro de uma barreira onde a carne em decomposição irá liberar um gás que com o tempo vai iniciar uma explosão e liberar os insetos venenosos em um enorme enxame que poderia cobrir a paisagem circundante. De acordo com Shino, a barreira acelera a multiplicação dos insetos.', '壺毒の術こどくのじゅつ', 'Kodoku no Jutsu', 'Técnica do Jarro de Veneno', NULL, NULL, 'Jutsu Jarro de Veneno', 'Todos os alcances', NULL, 'Carneiro'),
(2498, 'Técnica do Manto de Areia', 'O usuário cobre o corpo de um aliado com areia, permitindo que o mesmo seja capaz de facilmente atravessar algo tão forte como uma tempestade de areia sem necessidade de esquivar-se, proporcionando uma forma adicional de defesa ao mesmo tempo.', '砂衣の術', 'Sunagoromo no Jutsu', 'Técnica do Manto de Areia', NULL, NULL, 'Jutsu do Manto de Areia', 'Curto alcance', NULL, 'Selos especificos da técnica → Cobra'),
(2499, 'Técnica do Papel da Pessoa de Deus', 'Esta tem a fama de ser a técnica final de Konan, onde, usando uma quantidade inumerável de pequenas folhas de papel, ela faz com que o lago em Amegakure seja dividido ao meio, resultando na rápida criação de um enorme abismo abaixo do alvo. Devido à súbita falta de qualquer posição disponível, o alvo inevitavelmente desce cada vez mais fundo nas profundezas do abismo. A fim de aumentar a letalidade do ataque e limitar possíveis formas de retaliação, Konan mistura seiscentos bilhões de papéis explosivos preparados com seu papel normal, o que é suficiente para dez minutos de explosões sem parar; isso foi feito em específico para combater os cinco minutos de intangibilidade limitada de Obito Uchiha. Estes papéis explosivos também podem ser dirigidos para o alvo enquanto cai. Além disso, a técnica pode assumir a forma não só na água, mas também na terra.', '神の紙者の術', 'Kami no Shisha no Jutsu', 'Técnica do Papel da Pessoa de Deus', NULL, 'Técnica do Anjo de Papel', NULL, 'Todos os alcances', 'Rank A', NULL),
(2500, 'Técnica do Projétil do Dragão de Terra (game)', 'Uma técnica de colaboração entre um usuário de Liberação de Água e um usuário de Liberação de Terra onde água e terra são misturadas criando um dragão de lama que ataca o oponente. A técnica também pode ser utilizada com Gaara, que mistura sua areia com a água, e então o dragão atira lama no oponente.', '土竜弾の術', 'Doryūdan no Jutsu', 'Técnica do Projétil do Dragão de Terra', 'Jutsu do Projétil do Dragão de Terra', NULL, NULL, NULL, NULL, NULL),
(2501, 'Técnica do Puxão pela Sombra', 'Usando de uma única sombra, mas com a mecânica parecida com a da Técnica da Costura pelas Sombras, o usuário controla a sombra para capturar ou penetrar objetos muito pequenos.', '影寄せの術かげよせのじゅつ', 'Kageyose no Jutsu', 'Técnica do Puxão pela Sombra', 'Jutsu da Mão de Sombra, Técnica da Sombra Cravada', 'Jutsu de Invocação das Sombras', 'Jutsu de Puxar da Sombra', 'Curto a Médio alcance', 'Rank –', NULL),
(2502, 'Técnica do Quartzo Terrestre', 'A Técnica do Quartzo Terrestre é um ninjutsu aplicado em técnicas de terra para alterar suas propriedades físicas, contornando sua fraqueza natural contra raio.', NULL, NULL, 'Técnica do Quartzo Terrestre', NULL, NULL, NULL, NULL, NULL, NULL),
(2503, 'Técnica do Raio Obscuro', 'Após performar o selo relâmpago, o usuário aponta seus dedos na forma de uma arma e atira um projétil de coloração magenta com a ponta do dedo indicador, eletrocutando seu oponente. A técnica é poderosa o suficiente para derrubar o inimigo em instantes após atingi-lo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Relâmpago'),
(2504, 'Técnica do Revólver de Água', 'A Técnica do Revólver de Água é uma técnica usada pelo clã Hōzuki.', '水鉄砲の術', 'Mizudeppō no Jutsu', 'Técnica Revólver de Água', 'Jutsu da Pistola de Água', NULL, 'Jutsu do Revólver de Água', 'Todos os alcances', 'Rank B', NULL),
(2505, 'Técnica do Sabre Samurai', 'Esta é uma técnica exclusiva entre os samurais sob o conceito de fluxo de chakra para potencializar o poder de suas espadas e seus poderes de corte.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2506, 'Técnica do Selamento na Chaleira', 'Trata-se de um antigo fūinjutsu transmitido dentro de Sunagakure e utilizado para selar bijū e seus jinchūrikis em uma chaleira.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2507, 'Técnica do Servo da Névoa', 'Este genjutsu cria ilusões estranhas de ninjas pretos, que aparecem um por um, a partir de árvores e rochas e encurralam o inimigo. Seus movimentos são lentos, mas quando atacado eles se multiplicam e desta forma eles aparecem como fantasmas que habitam a névoa. Ferramentas como a kunai, por exemplo, podem ser adicionadas a esta técnica, atirando-as para coincidir com o movimento de ilusões, fazendo com que pareçam reais. Como os clones multiplicando-se, a vítima eventualmente se desgasta.', '霞従者の術かすみじゅうしゃのじゅつ', 'Kasumi Jūsha no Jutsu', 'Técnica do Servo da Névoa', NULL, NULL, 'Jutsu do Seguidor Enevoado', NULL, 'Rank D', NULL),
(2508, 'Técnica do Sistema de Sensoriamento', 'A Técnica do Sistema de Sensoriamento é um tipo de barreira de detecção que pode ser usada ​​para cercar uma área em uma cúpula, e que lhes permite monitorar remotamente uma referida área.', '感知システム術', 'Kanchi Shisutemu Jutsu', 'Técnica do Sistema de Sensoriamento', NULL, 'Técnica da Barreira Sensorial', NULL, 'Longo alcance', NULL, NULL),
(2509, 'Técnica do Substituto', 'Esta técnica permite que Zetsu e seus clones criem uma cópia exata de qualquer pessoa, sempre que entram em contato físico com ele. O usuário desta técnica torna-se uma duplicata perfeita, mesmo possuindo chakra idêntico ao do que eles estão representando, juntamente com qualquer coisa que eles poderiam estar usando ou ferramenta que eles estavam carregando. Eles também podem aparentemente copiar as habilidades de sua vítima, na medida de que são conhecidas para eles. Por exemplo, uma cópia de Kisame Hoshigaki tentou usar a Liberação de Água: Técnica do Grande Projétil de Tubarão do original, e outro copiou a aparência do Byakugan de Hinata Hyūga. Foi apenas no anime, no entanto, foram mostrados usando estas técnicas. No entanto, porque o substituto é mais fraco do que o indivíduo original, o seu objetivo principal é o de funcionar como uma distração mais do que qualquer coisa.', '成りかわりの術', 'Narikawari no Jutsu', 'Técnica do Substituto', NULL, NULL, 'Jutsu de Substituição do Corpo', NULL, 'Rank –', NULL),
(2510, 'Técnica do Super Tamanho Múltiplo', 'A Técnica do Super Tamanho Múltiplo é a versão mais poderosa da Técnica do Tamanho Múltiplo, que multiplica o corpo do usuário para um tamanho inacreditável.', '超倍化の術', 'Chō Baika no Jutsu', 'Técnica da Super Expansão', NULL, 'Técnica da Megaexpansão', 'Jutsu de Super Expansão', NULL, NULL, NULL),
(2511, 'Técnica do Súbito Atacante Espelhado', 'Uma armadilha secundária é colocada sob a Barreira de Cinco Selos. Esta técnica é iniciada automaticamente quando os selos \'proibidos\' são removidos. Uma cópia exata da pessoa que removeu o selo aparece com sua aparência, armas, habilidades de batalha, e assim por diante. Já que a cópia tem uma quantidade fixa de resistência e não sente cansaço, há uma vantagem de luta longa. A fim de cancelar esta técnica e derrotar sua cópia, aquele que removeu o selo não tem escolha a não ser de alguma forma ultrapassar os limites que possuía no momento em que removeu o selo.', '鏡面襲者の術', 'Kyōmen Shūja no Jutsu', 'Técnica do Súbito Atacante Espelhado', NULL, NULL, 'Jutsu Reflexão do Espelho', 'Curto alcance', 'Rank A', NULL),
(2512, 'Técnica do Tamanho Múltiplo', 'A Técnica do Tamanho Múltiplo é uma técnica secreta passada no clã Akimichi, que aumenta o tamanho do corpo do usuário através da conversão de suas calorias em chakra.', '倍化の術', 'Baika no Jutsu', 'Técnica do Tamanho Múltiplo', NULL, 'Jutsu de Expansão', NULL, NULL, NULL, NULL),
(2513, 'Técnica do Tamanho Múltiplo Parcial', 'A Técnica do Tamanho Múltiplo Parcial consiste em uma aplicação prática da Técnica do Tamanho Múltiplo em partes isoladas do corpo.', '部分倍化の術', 'Bubun Baika no Jutsu', 'Técnica da Expansão Parcial', NULL, 'Técnica de Expansão Parcial', NULL, NULL, NULL, NULL),
(2514, 'Técnica do Telescópio', 'Uma técnica que utiliza uma bola de cristal para acompanhar um determinado indivíduo em qualquer lugar que esteja, porém, há algumas restrições sobre a pessoa que é submetida a perseguição. O usuário deve saber o padrão de chakra da pessoa. Se alguém é alvo de perseguição, ele pode estar muito longe, a bola de cristal será capaz de confirmar a sua posição. Como esta técnica é infalível para fins de inteligência, o Terceiro Hokage usou para manter a ordem pública na vila.', '遠眼鏡の術', 'Tōmegane no Jutsu', 'Técnica do Telescópio', NULL, NULL, 'Jutsu da Bola de Cristal', 'Longo alcance', 'Rank A', NULL),
(2515, 'Técnica do Templo de Nirvana', 'É um genjutsu poderoso que gera uma chuva de vibração indutora de sono, penas brancas ilusórias. Quem vê-las caindo, acumulando e cobrindo toda a área em volta, vai cair em um estado de sono tranquilo. Não importa o quanto alguém resiste ao encanto hipnótico, o desejo de dormir é instintivo, e não há como lutar contra isso. No entanto, um shinobi experiente será capaz de dissipar o genjutsu antes que ele entre em vigor.', '涅槃精舎の術', 'Nehan Shōja no Jutsu', 'Técnica do Templo de Nirvana', 'Jutsu de Ilusão de Penas', NULL, NULL, 'Todos os alcances', 'Rank A', 'Tigre'),
(2516, 'Técnica do Tiro de Lama', 'Gozu atira lama de sua boca em um inimigo, a lama melhorada com chakra é capaz de prender e retardar o adversário para baixo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(2517, 'Técnica do Toque Etéreo', 'Infundindo chakra em seu punho o usuário é capaz de introduzir sua mão no interior do corpo de alguém, conectando-se ao seu inconsciente e seu chakra. Feito isso, é possível absorver o chakra do alvo para reabastecer as próprias reservas, bem como extrair porções de bestas com caudas seladas nele.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2518, 'Técnica do Trazedor da Escuridão', 'Esta técnica exerce um efeito sobre a visão alucinatória, efetivamente cegando o oponente. Não importa quão habilidoso um oponente possa ser, eles são forçados a esperar impotentes neste mundo de trevas, já que esta técnica permite que o usuário ataque sem ser visto. Embora impeça a visão, não impede os outros sentidos.', '黒暗行の術こくあんぎょのじゅつ', 'Kokuangyo no Jutsu', 'Técnica do Trazedor da Escuridão', NULL, 'Técnica da Escuridão Eterna', NULL, 'Curto a Médio alcance', 'Rank A', 'Tigre'),
(2519, 'Técnica do Vento Cortador', 'Esta técnica requer as Espadas Curtas Voadoras Crescentes, e permite ao usuário dividir e neutralizar uma técnica da Liberação de Vento, tornando-a inofensiva. O usuário pode até mesmo redirecionar os ataques do oponente para eles próprios.', '風切りの術', 'Kazekiri no Jutsu', 'Técnica do Vento Cortador', NULL, NULL, 'Corte de Vento', 'Curto alcance', NULL, NULL),
(2520, 'Técnica do Vento Empoeirado', 'Temari cria uma rajada de vento em que as camadas de uma área soltam areia e poeira. Se o fizer, também atua como um jato de areia sobre a área circundante. Esta camada de areia que se acumula nas árvores durante esta técnica torna difícil para os outros obterem uma boa base. A rajada inicial pode também deixar temporariamente os adversários cegos por causa do acumulo de areia em seus olhos.', '風砂塵の術ふうさじんのじゅつ', 'Fūsajin no Jutsu', 'Técnica do Vento Empoeirado', NULL, NULL, 'Jutsu do Vento com Poeira', 'Curto alcance', NULL, NULL),
(2521, 'Técnica do Vórtex do Tufão de Água', 'Este ninjutsu combinado utiliza a rotação selvagem da Liberação de Vento: Rasengan para engolir a água corrente criada pela Liberação de Água: Torrente Dilacerante, criando uma grande quantidade de vapor d\'água. Uma vez acertado, um forte vento gira em torno da parte de dentro criando uma névoa altamente concentrada, transformando a técnica em uma barreira sólida. A névoa criada pela rotação violenta do Rasengan, também tem o efeito de retirar o campo de visão do inimigo. É claro, a névoa pode ser usada como uma cortina de fumaça para que se possa escapar, mas a confusão que ela cria também pode ser usada, como por exemplo, em um ataque surpresa. Se o usuário puder usar naturezas diversas, é possível realizar esta técnica sozinho.', '颶風水渦の術ぐふうすいかのじゅつ', 'Gufū Suika no Jutsu', 'Técnica do Vórtex do Tufão de Água', 'Redemoinho Turbulento de Vento e Água', 'Jutsu do Turbilhão de Água e Vento', 'Jutsu Vórtice de Furacão', 'Curto a Médio alcance', 'Rank B', NULL),
(2522, 'Técnica do Vórtice de Turbilhão de Fogo', 'Primeiro, o usuário utiliza Liberação de Fogo: Pederneira de Yagura para criar uma parede de fogo na frente do inimigo e em seguida utiliza Liberação de Vento: Punho do Redemoinho para soprar as chamas contra o inimigo, criando uma tempestade de fogo ardente que deixa a área circundante queimada.', '渦旋風火の術', 'Uzusenpūhi no Jutsu', 'Técnica do Vórtice de Turbilhão de Fogo', 'Jutsu do Turbilhão de Fogo', NULL, NULL, 'Curto alcance', NULL, NULL),
(2523, 'Técnica dos Clones das Sombras', 'A Técnica dos Clones das Sombras é uma técnica desenvolvida pelo Segundo Hokage, Tobirama Senju.', '影分身の術かげぶんしんのじゅつ', 'Kage Bunshin no Jutsu', 'Técnica dos Clones das Sombras', NULL, 'Jutsu dos Clones das Sombras', 'Jutsu Clone da Sombra', NULL, 'Rank B', 'Selo de clonagem ou selo Tigre'),
(2524, 'Técnica dos Fantasmas de Chakra', 'Com seu dōjutsu, Ranmaru é capaz de criar uma ilusão de seres ou silhuetas, como meio de distração. Embora ilusório, esses corpos possuem sistema circulatório de chakra, o que confunde mesmo os usuários do Byakugan. A técnica só pode ser desativada a comando do usuário, aumentando o poder a cada investida do inimigo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2525, 'Técnica dos Homens de Madeira da Manipulação', 'O Mokujin Hyōi Tenshin Sōjutsu (木人憑依転身操術, Mokujin Hyōi Tenshin Sōjutsu) obriga dois adversários para lutar uns contra os outros através de bonecos de treinamento de madeira, com a intenção de enganá-los para lutar uns contra os outros, até que um deles é derrotado. A única forma de quebrar a técnica é ter um taijutsu rápido e forte suficiente, utilizando velocidade tão extrema que os bonecos de treinamento de madeira são incapazes de acompanhar e, assim, quebram, quebrando então a técnica. Se as vítimas são bastante familiarizadas uma com a outra, eles serão capazes de dizer quem é o seu adversário. Eles também podem se comunicar através de meios não-verbais, como o código Morse.', '木人憑依転身操術もくじんひょういてんしんそうじゅつ', 'Mokujin Hyōi Tenshin Sōjutsu', 'Técnica dos Homens de Madeira da Manipulação', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Boi → Cavalo → Coelho'),
(2526, 'Técnica dos Mil Ferrões de Abelha', 'O usuário invoca abelhas que disparam seus ferrões venenosos em seu alvo.', '蜂千本の術はちせんぼんのじゅつ', 'Hachi Senbon no Jutsu', 'Técnica dos Mil Ferrões de Abelha', NULL, NULL, 'Jutsu Mil Picadas de Abelhas', 'Longo alcance', NULL, 'Cobra → Boi → Cao → Carneiro → Passaro → Cavalo → Tigre'),
(2527, 'Técnica dos Múltiplos Clones da Sombra', 'Esta técnica é essencialmente uma versão massiva da Técnica dos Clones das Sombras.', '多重影分身の術', 'Tajū Kage Bunshin no Jutsu', 'Técnica dos Múltiplos Clones da Sombra', NULL, 'Jutsu dos Múltiplos Clones das Sombra', 'Jutsu Multi Clones da Sombra', NULL, 'Rank A', 'Selo de clonagem'),
(2528, 'Técnica dos Múltiplos Clones de Liberação de Madeira', 'Esta técnica é essencialmente uma versão em massa da Técnica do Clone de Madeira, em que o usuário faz o selo de mão para os clones e, em seguida, utilizando a vegetação pré-existente ao seu redor, como um meio, que cria um grande número de clones de madeira.', '多重木遁分身の術', 'Tajū Mokuton Bunshin no Jutsu', 'Técnica dos Múltiplos Clones de Madeira', NULL, NULL, 'Estilo Madeira: Jutsu dos Multi Clones de Madeira', NULL, NULL, 'Selo especifico da técnica'),
(2529, 'Tática de Ataque Surpresa', 'Trata-se de uma manobra surpresa usada especialmente por cães, graças à sua habilidade de cavar rapidamente.', '奇襲戦法', 'Kishū Senpō', 'Tática de Ataque Surpresa', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2530, 'Ultra-Grande Bola Rasengan', 'Esta técnica acrescenta energia natural para a Grande Bola Rasengan. A energia natural aumenta rapidamente que em proporções gigantescas, maior do que o próprio corpo do usuário, e é criada em um instante. Quando esta impacta, há uma grande onda de choque que desencadeia podendo lançar shinobi desavisados.', '超大玉螺旋丸', 'Chōōdama Rasengan', 'Ultra-Grande Esfera Espiral', 'Senjutsu: Chō Ōdama Rasengan, Ultra-Gigante Rasengan, Rasengan Massivo', NULL, 'Rasengan Massivo', 'Curto alcance', NULL, NULL),
(2531, 'Ultra-Grande Série de Esferas Espirais', 'Depois de criar uma enxurrada de clones de sombra, Naruto e seus clones criam um Rasengan com o qual bombardeiam seus alvos para efeitos devastadores.', '螺旋超多連丸', 'Rasen Chō Tarengan', 'Ultra-Grande Série de Esferas Espirais', 'Super Barragem Rasengan', 'Metralhadora de Rasengan', NULL, 'Curto alcance', NULL, NULL),
(2532, 'Ultra: Chibaku Tensei', 'Essa é uma técnica utilizada por Madara, que faz uso do poder do seu Rinnegan e da Liberação de Madeira.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(2533, 'Uncaria', 'O usuário corre em direção ao oponente, atacando com os ossos de suas costas e finalizando com ao pular e rolar com sua cauda contra o oponente.', 'カギカズラ', 'Kagikazura', 'Uncaria', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(2534, 'União', 'Mecha-Naruto anexa seu corpo mecânico com Naruto, que usa-o como espécie de roupa mecanizada. Ao fazer isto o alvo ganha força e algumas habilidades adicionais.', 'ユナイト', 'Yunaito', 'União', NULL, NULL, NULL, NULL, NULL, NULL),
(2535, 'Vapor Vermelho da Sedução', 'Kurenai faz e mantém o selo tigre, criando uma névoa vaporizante em todo o campo. Essa névoa induz o oponente a um genjutsu, onde ela lhe seduz. A vítima perde os sentidos, incapacitado pelo feitiço, oportunizando Kurenai à abrir ataque.', '幻惑紅湯煙げんわくくれないゆけむり', 'Genwaku Kurenai Yukemuri', 'Vapor Vermelho da Sedução', NULL, NULL, NULL, 'Curto alcance', NULL, 'Tigre'),
(2536, 'Vara de Pescar Chakra', 'A Vara de Pescar Chakra é uma arma única empunhada por Urashiki Ōtsutsuki e constitui o principal método de combate do mesmo.', 'チャクラ釣ざおチャクラつりざお', 'Chakura Tsuri Zao', 'Vara de Pescar Chakra', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(2537, 'Varredura da Aranha', 'Kidōmaru utiliza a Técnica de Invocação, para convocar sete pequenas aranhas filhotes de Kyodaigumo, que saltam rapidamente sobre o oponente atacando-o e consequentemente abrindo sua guarda.', NULL, NULL, NULL, 'Varredura da Aranha', NULL, NULL, 'Curto alcance', NULL, NULL),
(2538, 'Velocidade da Luz (Kakashi)', 'Kakashi usa o Corte Relâmpago em ziguezague ao redor do inimigo ganhando uma grande velocidade, evitando ataques e finalizando o oponente com sua técnica.', '電光石火でんこうせっか', 'Denkō Sekka', 'Velocidade da Luz', 'Denko Sekka', NULL, NULL, 'Curto alcance', NULL, NULL),
(2539, 'Vendaval Bruto da Flor de Cerejeira', 'Após liberar o seu Selo Yin, e com os punhos rodeados por chakra, Sakura salta, e com um chute, levanta o oponente, realizando um rápido e intenso movimento rotatório, que é complementado logo em seguida por um poderoso golpe de duas mãos, que envia o oponente para baixo.', NULL, NULL, NULL, 'Vendaval Bruto da Flor de Cerejeira', NULL, NULL, 'Curto alcance', NULL, NULL),
(2540, 'Vendaval Trovejante — Golpe da Espada do Traje Majestoso', 'Naruto manifesta Kurama em sua forma completa, em seguida Sasuke a reveste com a armadura de seu Susanoo. Feito isso, eles manifestam uma espada e cortam seu oponente.', '疾風迅雷　威装之一太刀', 'Shippū Jinrai — Isō no Hitotachi', 'Vendaval Trovejante — Golpe da Espada do Traje Majestoso', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(2541, 'Vendaval da Folha', 'Esta técnica consiste em um giro acompanhado de um pontapé rasteiro, porém dada a força do usuário, o golpe pode lançar o inimigo para longe.', '木ノ葉烈風このはれっぷ', 'Konoha Reppū', 'Vendaval da Folha', NULL, NULL, 'Redemoinho da Folha', 'Curto alcance', 'Rank D', NULL);
