INSERT INTO `jutsu` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`, `range`, `rank`, `hand_seals`) VALUES
(1107, 'Liberação de Cristal: Técnica do Cruzamento de Deuses', 'Guren é capaz de formar cristais completamente afiados e resistentes somente tocando o solo, gerando um amplo caminho de cristal, capturando e cortando vários adversários de forma simultânea, deixando os inimigos totalmente feridos.', '晶遁・御神渡りの術しょうとん・おみわたりのじゅつ', 'Shōton: Omiwatari no Jutsu', 'Liberação de Cristal: Técnica do Cruzamento de Deuses', NULL, NULL, 'Estilo Cristal: Técnica do Caminho Divino', 'Médio a Longo Alcance', NULL, 'Cavalo → Macaco → Carneiro → Javali'),
(1108, 'Liberação de Cristal: Técnica do Labirinto de Cristal de Jade', 'Primeiramente, Guren forma múltiplas flores cristalinas, após formá-las, Guren é capaz de fazê-las nascer e modelarem um imenso labirinto de cristais vermelhos. Esta técnica deixa todos inimigos aprisionados, confusos, isso é feito pela luz que atinge os cristais avermelhados e refletem as imagens dos adversários, detalhes e caminhos que são aprisionados na técnica, intimidando-os, semelhante a um espelho. Além disso, a técnica bloqueia até os usuários do Byakugan, isto acontece pela completa reflexão do campo.', '晶遁・翠晶迷宮の術しょうとん・すいしょうめいきゅうのじゅつ', 'Shōton: Suishō Meikyū no Jutsu', 'Liberação de Cristal: Técnica do Labirinto de Cristal de Jade', NULL, NULL, 'Estilo Cristal: Labirinto de Cristal de Jade', 'Todos os alcances', NULL, 'Javali → Macaco → Cavalo → Cao → Carneiro'),
(1109, 'Liberação de Dinheiro: Técnica de Ajuda', 'A Liberação de Dinheiro: Técnica de Ajuda é uma artimanha inventada por Kunihisa em estímulo das técnicas ninjas de Naruto.', '金遁・たすけての術', 'Kinton: Tasukete no Jutsu', 'Liberação de Dinheiro: Técnica de Ajuda', NULL, NULL, 'Estilo Dinheiro: Salve-me!', 'Curto alcance', NULL, NULL),
(1110, 'Liberação de Dinheiro: Técnica do Clone de Dinheiro', 'A Liberação de Dinheiro: Técnica do Clone de Dinheiro é uma técnica ilusória inventada por Kunihisa, baseada na Técnica dos Clones das Sombras de Naruto Uzumaki.', '金遁・金分身の術', 'Kinton: Kin Bunshin no Jutsu', 'Liberação de Dinheiro: Técnica do Clone de Dinheiro', NULL, NULL, 'Estilo Dinheiro: Clone de Dinheiro', 'Curto alcance', NULL, 'Selo de Clonagem'),
(1111, 'Liberação de Escuridão: Boca Devoradora', 'Essa é uma técnica de Liberação de Escuridão usada por Hiruko, que é capaz de engolir o chakra de qualquer pessoa nas proximidades. Com isso ele é capaz de adquirir sua afinidade elemental e manipula-la livremente. Também é possível absorver e adquirir múltiplos ninjutsu simultaneamente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Rato → Coelho → Passaro'),
(1112, 'Liberação de Escuridão: Boca Inaladora', 'Hiruko usa esta técnica através da Técnica Quimera, a fim de extrair forçadamente a energia física e espiritual de outro ninja e absorvê-lo através do quadrado superior da marca em sua mão esquerda, mesmo a uma distância considerável. Além disso, parece que ele é capaz de remover completamente o chakra da vítima, podendo causar a morte.', '冥遁・吸穴孔めいとん・きゅうけつこう', 'Meiton: Kyūketsukō', 'Liberação de Escuridão: Boca Inaladora', NULL, NULL, 'Estilo Sombra: Buraco Negro', 'Todos os alcances', NULL, 'Cavalo'),
(1113, 'Liberação de Escuridão: Julgamento', 'Hiruko converte o chakra absorvido através do jutsu Liberação de Escuridão: Boca Inaladora em uma energia fantasmagórica semelhante a chamas azuis-claras, jogando de volta em seus inimigos com a força ampliada para jogá-los longe. A energia é expelida através do quadrado inferior da marca na mão esquerda de Hiruko.', '冥遁・邪自滅斗めいとん・じゃっじめんと', 'Meiton: Jajimento', 'Liberação de Escuridão: Julgamento', NULL, NULL, 'Estilo Sombra: Julgamento', NULL, NULL, NULL),
(1114, 'Liberação de Escuridão: Onda Ceifadora', 'Hiruko libera uma onda de escuridão a partir do seu corpo, que se expande para todas as direções, absorvendo o chakra de todos em que ela tocar. Com isso ele foi capaz de dissipar centenas de clones das sombras.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Cavalo'),
(1115, 'Liberação de Explosão: Palma Explosiva', 'O usuário cria uma pequena explosão em sua palma. Se o ângulo for devidamente cronometrado, ela pode ser usada para enviar para trás kunai jogadas pelo inimigo. Gari também usou esta técnica para cortar o pano de ligação que Maki utilizou para limitar Pakura.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1116, 'Liberação de Explosão: Punho de Mina Terrestre', 'Depois de estabelecer contato direto com seu oponente através de um simples soco, o usuário utiliza a Liberação de Explosão para, posteriormente, gerar uma explosão enorme do ponto inicial de contato físico. Esta explosão é poderosa o suficiente para obliterar completamente um adversário muito maior do que o usuário. Devido à direcionalidade da explosão, o usuário minimiza qualquer repercussão potencial, como ser atingido por detritos resultantes e até mesmo recuar parece ser completamente desnecessário.', '爆遁・地雷拳ばくとん・じらいけん', 'Bakuton: Jiraiken', 'Liberação de Explosão: Punho de Mina Terrestre', NULL, 'Estilo da Explosão: Punho de Minas Explosivas', NULL, 'Curto alcance', NULL, NULL),
(1117, 'Liberação de Fervura: Força Inigualável', 'Liberação de Fervura: Força Inigualável é um estilo de luta baseado em ninjutsu de Liberação de Fervura, usado por ambos Han e o Cinco-Caudas, Kokuō.', '沸遁・怪力無双ふっとん・かいりきむそう', 'Futton: Kairiki Musō', 'Liberação de Fervura: Força Inigualável', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1118, 'Liberação de Fervura: Técnica da Lótus Pecaminosa', 'Mei manipula seu chakra para criar vapor a ser lançado a partir do solo que, em seguida, faz com que ele crie explosões corrosivas em diferentes intervalos. A corrosividade da técnica apenas pode causar algum dano grave para o corpo do alvo, mas não possui propriedades ácidas totais que pode derretê-lo instantaneamente. Ela usa essa técnica depois que ela ouve mal Ao.', NULL, NULL, 'Liberação de Fervura: Técnica da Lótus Pecaminosa', 'Estilo Vapor: Jutsu da Lótus Pecaminosa', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1119, 'Liberação de Fervura: Técnica da Névoa Qualificada', 'O usuário cria uma nuvem de névoa que ele libera de sua boca. Esta névoa tem propriedades extremamente corrosivas, capazes de derreter até os ossos do Susanoo de Sasuke Uchiha. A técnica tem uma desvantagem inerente que os seus efeitos são indiscriminados, limitando o seu uso em áreas que não contêm aliados. No caso em que a névoa escapa da área de contenção, o utilizador pode ajustar o nível de pH para níveis mais seguros da névoa.', '沸遁・巧霧の術ふっとん・こうむのじゅつ', 'Futton: Kōmu no Jutsu', 'Liberação de Fervura: Técnica da Névoa Qualificada', NULL, 'Estilo da Ebulição: Técnica da Névoa Engenhosa', NULL, 'Curto a Médio alcance', 'Rank –', 'Cavalo'),
(1120, 'Liberação de Fervura: Técnica do Corpo de Vapor', 'O usuário é capaz de desfazer seu corpo em vapor para se mover sem ser percebido e ficar invulnerável a ataques. Conforme sua vontade, ele pode reunir o vapor para reconstituir seu corpo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1121, 'Liberação de Fogo: Aranha da Terra Carmesim', 'O usuário cospe fogo que envolve o inimigo e, em seguida, se transforma em uma aranha que ataca o inimigo. Esta técnica eficaz intercepta e ataca um adversário, ao mesmo tempo, e pode ser usada para forçá-los a outro ataque.', '火遁・紅地蜘蛛かとん・べにじぐも', 'Katon: Benijigumo', 'Liberação de Fogo: Aranha da Terra Carmesim', NULL, NULL, 'Estilo Fogo: Aranha Vermelha', 'Curto a Médio alcance', NULL, 'Tigre → Cavalo → Rato → Cao → Tigre'),
(1122, 'Liberação de Fogo: Bestas Ardentes', 'O usuário é capaz um lançar um poderoso fogo ardente que assume a forma de um cachorro e um macaco gigantes. As bestas avançam em direção aos seus oponentes e podem atacar múltiplos alvos simultaneamente.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1123, 'Liberação de Fogo: Bombardeio do Tigre de Chamas', 'Jakō Ryūdōin invoca um tigre feito de chamas a partir do pergaminho em suas costas, causando enorme dano para a equipe do jogador. Além disso, tem a habilidade de anexar ao oponente e explodir com muito impacto. Esta técnica é semelhante à Liberação de Fogo: Técnica do Grande Dragão de Fogo e ao Ataque do Dragão Explosivo, exceto pelo fato de usar um tigre ao invés de um dragão. É também um pouco semelhante a um papel explosivo, mas muito mais forte.', '火遁・炎虎爆殺', 'Katon: Enko Bakusatsu', 'Liberação de Fogo: Bombardeio do Tigre de Chamas', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1124, 'Liberação de Fogo: Campo da Cortina em Extinção', 'Jiraiya pisa no chão, criando uma rachadura que vai até o seu oponente. Ele, em seguida, lança fogo na rachadura, fervendo a terra abaixo de seu oponente e batendo as mãos, criando uma explosão grande e ardente, que finaliza com seu oponente.', '火遁・消し幕が原かとん・けしまくらはら', 'Katon: Keshimakuga Hara', 'Liberação de Fogo: Campo da Cortina em Extinção', 'Estilo Fogo: Ataque de Desaparecimento', NULL, NULL, 'Médio alcance', NULL, NULL),
(1125, 'Liberação de Fogo: Captura por Chamas', 'Transformando seu chakra em fogo, o usuário cria um fluxo de chamas na forma de um chicote que pode ser usado para bater em um oponente e lançá-lo longe.', '火遁・捕獲炎', 'Katon: Hokaku En', 'Liberação de Fogo: Captura das Chamas', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1126, 'Liberação de Fogo: Chama Alta', 'Trata-se de uma técnica de Liberação de Fogo utilizada por Ino para gerar uma chama super alta e grelhar os ingredientes das Pílulas de Ração Militar.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, 'Juntar as Maos → Selo Desconhecido → Selo Desconhecido'),
(1127, 'Liberação de Fogo: Dança Selvagem da Onda Explosiva', 'É uma técnica poderosa que combina o poder do Mangekyō Sharingan e a Liberação de Fogo.', '火遁・爆風乱舞', 'Katon: Bakufū Ranbu', 'Liberação de Fogo: Dança Selvagem da Onda Explosiva', 'Estilo Fogo: Tempestade Explosiva', 'Estilo do Fogo: Vendaval Dançante', NULL, 'Curto a Médio alcance', 'Rank B', 'Tigre'),
(1128, 'Liberação de Fogo: Execução de Fogo', 'Esta técnica cria um fluxo de fogo que pode ser manipulado em diversas formas (até agora, anéis de fogo que correm sobre o solo ou um círculo de fogo voando através do ar) antes de atingir o alvo. Ele também pode ser usado em combinação com a Liberação de Vento: Vento Divino para criar um tornado de chamas, ou com a Liberação de Vento: Vento Divino das Montanhas para liberar um inferno de chamas maciço.', '火遁・火走り', 'Katon: Hibashiri', 'Liberação de Fogo: Fogo em Execução', NULL, NULL, 'Estilo Fogo Arte Ninja: Corrida de Fogo', 'Médio alcance', NULL, 'Tigre'),
(1129, 'Liberação de Fogo: Flor da Fênix Sábia do Prego Carmesim', 'O usuário libera uma enxurrada de shuriken com uma mão, e, ao mesmo tempo, exala fogo infundido com chakra nelas, a fim de aumentar o potencial de destruição das armas, tornando-as capazes de causar queimaduras graves em contato direto com o alvo, ou qualquer outro objeto preso dentro de sua trajetória. Esta técnica pode servir como um ataque duplo: o dano causado pelas armas mais o dano causado pelas chamas.', '火遁・鳳仙花爪紅かとん・ほうせんかつまべに', 'Katon: Hōsenka Tsumabeni', 'Liberação de Fogo: Flor da Fênix Sábia do Prego Carmesim', NULL, 'Estilo do Fogo: Flores de Fogo', 'Estilo Fogo: Arte da Flor de Fênix', 'Curto a Médio alcance', NULL, 'Rato → Tigre → Cao → Boi → Coelho → Tigre → Macaco'),
(1130, 'Liberação de Fogo: Formação de Chama Explosiva', NULL, '火遁・起爆炎陣かとん・きばくえんじん', 'Katon: Kibaku Enjin', 'Liberação de Fogo: Formação de Chama Explodindo', NULL, NULL, NULL, 'Curto à Médio alcance', NULL, 'Tigre → Rato → Passaro → Cobra'),
(1131, 'Liberação de Fogo: Grande Aniquilação do Fogo', 'Uma técnica onde chakra amassado dentro do corpo é convertida em fogo, e depois expulso da boca e moldado em uma parede maciça de chamas intensas, que abrange uma ampla gama. Isso faz com que a técnica seja extremamente difícil de evitar ou conter, com isso exigindo que os esforços combinados de vários usuários de Liberação de Água, a fim de extinguir as chamas.', '火遁・豪火滅却かとん・ごうかめっきゃく', 'Katon: Gōka Mekkyaku', 'Liberação de Fogo: Grande Aniquilação do Fogo', 'Estilo Fogo: Grande Inferno', 'Estilo do Fogo: Aniquilação por Fogo', NULL, 'Médio a Longo alcance', NULL, 'Cavalo'),
(1132, 'Liberação de Fogo: Grande Bala de Chama', 'Esta técnica é uma versão senjutsu alterada da Liberação de Fogo: Projétil de Chamas. O usuário preenche sua cavidade bucal com óleo, criado dentro do corpo com o chakra do Senjutsu. Este óleo é, então, expelido, e ao mesmo tempo inflama, o que cria uma chama gigante, equivalente a múltiplos marcadores da chama. Essa chama pode completamente cortar a rota de fuga diante de um inimigo encurralado em um corredor ou sala, ou pode queimar completamente a respectiva rota de fuga.', '火遁・大炎弾かとん・だいえんだん', 'Katon: Dai Endan', 'Liberação de Fogo: Grande Bala de Chama', NULL, 'Estilo do Fogo: Grande Bola de Fogo', NULL, 'Curto a Médio alcance', 'Rank B', NULL),
(1133, 'Liberação de Fogo: Grande Bola de Chamas', 'Itachi exala uma esfera de fogo do tamanho do meteoro que leva o inimigo para longe e causa uma enorme explosão com o impacto. O impacto faz com que o solo ao redor Itachi quebre enquanto ele permanece imperturbável. Isso é muito parecido com a Liberação de Fogo: Técnica da Grande Bola de Fogo, mas em uma versão maior e mais destrutiva.', '火遁・豪焔球', 'Katon: Gōenkyū', 'Liberação de Fogo: Grande Bola de Chamas', 'Estilo Fogo: Bola de Chamas', NULL, NULL, 'Todos os alcances', NULL, 'Cavalo → Tigre'),
(1134, 'Liberação de Fogo: Grande Destruição de Fogo', 'Uma técnica onde chakra é amassado dentro do corpo do usuário e convertido em fogo, depois expelido pela boca em um fluxo enorme de chamas intensas que podem destruir uma vasta área em chamas, engolindo o alvo em um verdadeiro mar de fogo. Se for utilizado em cima de um terreno combustível denso, tal como a floresta, a área confinada pode aumentar muito a possibilidade de incineração do alvo.', '火遁・豪火滅失かとん・ごうかめっしつ', 'Katon: Gōka Messhitsu', 'Liberação de Fogo: Grande Destruição de Fogo', NULL, 'Estilo do Fogo: Devastação por Fogo', 'Estilo Fogo: Destruidor Majestoso', 'Todos os alcances', NULL, 'Rato → Boi → Cao → Cavalo → Macaco → Javali → Tigre'),
(1135, 'Liberação de Fogo: Grande Flor de Chama', 'O usuário molda seu chakra em fogo e, em seguida, dispara uma infinidade de bolas de fogo contra o inimigo. As bolas de fogos causam severos danos no terreno, bem como destruir abrigos de Liberação de Terra ou dominar técnicas de Liberação de Água.', '火遁・豪炎華かとん・ごうえんか', 'Katon: Gōenka', 'Liberação de Fogo: Grande Flor de Chama', 'Estilo Fogo: Flor de Chama', NULL, NULL, 'Longo alcance', NULL, NULL),
(1136, 'Liberação de Fogo: Grande Rasengan em Chamas', 'Jiraiya cria um Rasengan normal, e, em seguida, cobre com chakra fogo, a fim de aumentar o poder de destruição. Ao atingir o oponente, as chamas aumentam e criam um redemoinho de fogo, para consumir o corpo do oponente por completo.', '火遁・豪炎螺旋丸かとん・ごうえんらせんがん', 'Katon: Gōen Rasengan', 'Liberação de Fogo: Grande Esfera Espiral em Chamas', 'Fogo do Inferno: Rasengan', NULL, NULL, 'Curto alcance', NULL, NULL),
(1137, 'Liberação de Fogo: Grande Redemoinho de Fogo', 'Madara abana seu leque de guerra e expele chamas de sua boca nos ventos, criando uma barragem de redemoinhos flamejantes.', '火遁・豪火旋風', 'Katon: Gōka Senpū', 'Liberação de Fogo: Grande Redemoinho de Fogo', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1138, 'Liberação de Fogo: Incêndio da Chama Explosiva', 'Após liberar pólvora no ar, Boro expele fogo a partir de sua boca, causando uma explosão nas proximidades. Isso pode ser especialmente útil quando o usuário está sendo atacado por vários oponentes e em múltiplas direções.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1139, 'Liberação de Fogo: Lanterna Demônio', 'Esta técnica forma vários fantasmas de fogo para atacar o inimigo em conjunto. Os fantasmas atacam rapidamente, então é pouco provável uma escapatória.', '火遁・鬼灯籠かとん・おにどうろう', 'Katon: Onidōrō', 'Liberação de Fogo: Lanterna Demônio', NULL, NULL, NULL, NULL, NULL, 'Tigre'),
(1140, 'Liberação de Fogo: Lança de Chamas', 'Após performar os selos de mão necessários, o usuário infunde sua lança com chakra de natureza fogo, aumentando sua letalidade.', NULL, NULL, 'Liberação de Fogo: Lança de Chamas', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1141, 'Liberação de Fogo: Liberação de Vento: Onda Selvagem de Chamas Ventiladas', 'Ao usar suas máscaras de fogo e vento, Kakuzu combina seus ataques para desencadear uma poderosa explosão de fogo alimentada por fortes rajadas de vento. O ataque é poderoso o suficiente para devastar toda uma região e impedir que uma técnica de água interfere no processo.', '火遁・風遁・炎風の乱波かとん・ふうとん・えんぷうのらっぱ', 'Katon: Fūton: Enpū no Rappa', 'Liberação de Fogo: Liberação de Vento: Onda Selvagem de Chamas Ventiladas', 'Estilo Fogo Estilo Vento: Inferno Áspero de Ondas', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1142, 'Liberação de Fogo: Limite das Chamas Assassinas', 'Após ter o chakra amassado dentro de seu corpo e convertido em fogo, Obito salta uma quantidade considerável de altura e vomita uma grande esfera de chamas em direção ao adversário.', '火遁・炎殺界', 'Katon: Ensakkai', 'Liberação de Fogo: Limite das Chamas Assassinas', 'Estilo Fogo: Barreira de Chamas Assassinas', NULL, NULL, 'Curto alcance', NULL, NULL),
(1143, 'Liberação de Fogo: Meteoros Flamejantes', 'O usuário cria uma legião de pequenas bolas de fogo que são disparadas contra o inimigo.', '火遁・焔流星', 'Katon: Homuranagase', 'Liberação de Fogo: Meteoros Flamejantes', NULL, NULL, NULL, 'Curto a Médio Alcance', NULL, NULL),
(1144, 'Liberação de Fogo: Máscara do Aprisionamento nas Chamas', 'Depois de relativa distância, Kakuzu desencadeia uma saraivada de bolas de fogo para explodir enquanto em contato com o inimigo.', '火遁・獄炎の面かとん・ごくえんのめん', 'Katon: Gokuen no Men', 'Liberação de Fogo: Máscara do Aprisionamento nas Chamas', 'Estilo Fogo: Máscara das Chamas Aprisionadoras', NULL, NULL, 'Longo alcance', NULL, NULL),
(1145, 'Liberação de Fogo: Névoa de Água', 'A Névoa de Água é uma das várias técnicas presentes no arsenal de Kakashi Hatake, o Sexto Hokage.', NULL, NULL, 'Liberação de Fogo: Névoa de Água', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1146, 'Liberação de Fogo: Pederneira de Yagura', 'O usuário aciona um isqueiro e produz uma pequena chama que ele expande e manipula em uma série de formas, como uma parede, um pilar, ou até mesmo uma grande criatura animalesca.', '火遁・火打ち矢倉', 'Katon: Hiuchi Yagura', 'Liberação de Fogo: Pederneira de Yagura', NULL, NULL, NULL, 'Médio alcance', NULL, NULL),
(1147, 'Liberação de Fogo: Pilha Ardente de Cinzas', 'O usuário expele um fluxo de pólvora infundido com chakra de sua boca, que circunda a região. Como a pólvora é composta inteiramente de cinzas, ela permanece no ar ao redor da vítima como uma nuvem, que pode ser usada como uma cortina de fumaça. Depois de cercar o inimigo com as cinzas, o utilizador pode inflamá-la com um isqueiro colocado sobre os dentes previamente para criar uma faísca, o que resulta em uma violenta explosão, queimando o inimigo. No entanto, essa técnica exige sentidos aguçados para ler as correntes de ar, discernimento para ler os movimentos do inimigo, experiência táctica para não envolver também os próprios aliados nas chamas, e uma atenção especial para o momento da ignição.', '火遁・灰積焼かとん・はいせきしょう', 'Katon: Haisekishō', 'Liberação de Fogo: Pilha Ardente de Cinzas', 'Estilo Fogo: Cinza Ardente', 'Estilo do Fogo: Campo das Cinzas Flamejantes', 'Estilo Fogo: Cinzas Abrasadoras', 'Médio alcance', 'Rank B', 'Cobra → Rato → Cobra → Tigre'),
(1148, 'Liberação de Fogo: Prisão Celestial', 'A Liberação de Fogo: Prisão Celestial é uma técnica de aprisionamento desenvolvida por um conhecido clã de Kusagakure. Essa essa técnica é utilizada nos prisioneiros de Castelo Hōzuki.', '火遁・天牢かとん・てんろう', 'Katon: Tenrō', 'Liberação de Fogo: Prisão Celestial', NULL, NULL, 'Estilo Fogo: Prisão Celestial', 'Curto alcance', NULL, 'Selo do Confronto'),
(1149, 'Liberação de Fogo: Prisão Gigantesca Celestial de Fogo', 'Uma versão mais forte da Liberação de Fogo: Prisão Celestial que pode ser usada em alvos maiores.', '火遁・豪火天牢かとん・ごうかてんろう', 'Katon: Gōka Tenrō', 'Liberação de Fogo: Prisão Gigantesca Celestial de Fogo', NULL, NULL, NULL, 'Curto', NULL, NULL),
(1150, 'Liberação de Fogo: Projétil Voador de Chamas', 'Enquanto em sua forma nível dois do selo amaldiçoado, Sasuke exala uma versão mais forte, contudo, mais curta da Liberação de Fogo: Técnica da Grande Bola de Fogo no oponente.', '火遁・飛炎弾', 'Katon: Hiendan', 'Liberação de Fogo: Projétil Voador de Chamas', 'Estilo Fogo: Projétil Voador de Chamas', NULL, NULL, 'Curto alcance', NULL, NULL),
(1151, 'Liberação de Fogo: Projétil de Chama do Dragão de Fogo', 'O usuário amassa seu chakra em chamas, que são manipuladas na aparência de um dragão e direcionadas para atacar um alvo a partir de múltiplas direções. As investidas são focadas, rapidamente reduzindo o alvo em cinzas ao tocar. Devido ao poder desta técnica, bem como a habilidade necessária para controlar as chamas, pouquíssimos ninjas foram capazes de performá-la, sendo o Terceiro Hokage um deles.', '火遁・火龍炎弾', 'Katon: Karyū Endan', 'Liberação de Fogo: Projétil de Chama do Dragão de Fogo', 'Estilo Fogo: Bomba do Dragão Flamejante', 'Estilo do Fogo: Dragão Flamejante', 'Estilo Fogo: Bomba do Dragão Flamejante', 'Curto a Médio alcance', 'Rank B', 'Carneiro → Cavalo → Cobra → Dragao → Rato → Boi → Tigre'),
(1152, 'Liberação de Fogo: Projétil de Chamas', 'Esta técnica é executada pela reunião de óleo que é criado pelo chakra na boca do usuário, cuspindo-o fora, e inflamando-o. Essa técnica simplesmente dispara um míssil de fogo no inimigo. Se a quantidade de óleo que é preparado é grande, o tempo que leva para invocar a técnica é reduzido. Isso permite que o inimigo seja atingido inconscientemente, permitindo que o míssil de fogo contendo óleo o queime completamente. Também é possível disparar uma série de mísseis de chamas dividindo o óleo, ou um contínuo lança-chamas. Se esta técnica é usada depois de cobrir o inimigo com óleo utilizando o Projétil do Óleo de Sapo, ela tem o poder de ferir ou matar. Isso também mantém baixo o consumo de chakra.', '火遁・炎弾かとん・えんだん', 'Katon: Endan', 'Liberação de Fogo: Míssil Flamejante', NULL, 'Estilo do Fogo: Bola de Fogo', NULL, 'Curto a Médio alcance', 'Rank C', 'Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre'),
(1153, 'Liberação de Fogo: Projétil de Chamas do Óleo de Sapo', 'Esta é uma técnica de konbijutsu de duas vias. Gamabunta usa a técnica Projétil do Óleo de Sapo para atirar um jato de óleo de sua boca. Seu invocador inflama então o óleo. Jiraiya usa a Liberação de Fogo: Projétil de Chamas para este fim, enquanto que Naruto, não conhecendo ninjutsu de Liberação de Fogo, optou por usar papéis explosivos em seu lugar. O óleo, agindo como um acelerador, fornece mais combustível para o fogo em um efeito como um lança-chamas, e criando uma conflagração de poder muito maior e magnitude que o ninjutsu original de fogo usado em si poderia ter.', '火遁・蝦蟇油炎弾かとん・がまゆえんだん', 'Katon: Gamayu Endan', 'Liberação de Fogo: Projétil de Chamas do Óleo de Sapo', 'Bombas Flamejantes do Sapo[2]', NULL, 'Estilo Fogo: Bomba da Chama de Sapo', 'Todos os alcances', 'Rank B', 'Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre'),
(1154, 'Liberação de Fogo: Projétil do Dragão de Fogo', 'Após moldar chakra fogo dentro de seu corpo, o usuário exala um sopro de chamas da sua boca. Com objetivo de mais efetividade na técnica, o usuário pode usar em combinação com a Liberação de Terra: Projétil do Dragão de Terra para inflamar os projéteis disparados, tornando o dano mais devastador.', '火遁・火龍弾', 'Katon: Karyūdan', 'Liberação de Fogo: Projétil do Dragão de Fogo', NULL, NULL, 'Estilo Fogo: Chama da Bomba do Dragão', 'Curto alcance', NULL, 'Tigre'),
(1155, 'Liberação de Fogo: Raposa de Fogo', 'O usuário manifesta dez bolas de fogo em um círculo à sua frente e as lança com os movimentos das mãos.', '火遁・狐火', 'Katon: Kitsunebi', 'Liberação de Fogo: Raposa de Fogo', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, 'Serpente → Carneiro → Macaco → Javali → Passaro → Tigre'),
(1156, 'Liberação de Fogo: Redemoinho de Chamas', 'O fogo entra em erupção em torno do corpo do usuário em uma forma em espiral, que é então lançado com as mãos em direção ao alvo. Uma vez que a técnica atinge o alvo, o adversário é enviado para o ar de forma em espiral e seu corpo queima em cinzas.', '火遁・火炎旋風', 'Katon: Kaen Senpū', 'Liberação de Fogo: Redemoinho de Chamas', NULL, NULL, 'Estilo Fogo: Furação de Chamas', 'Curto à Médio alcance', NULL, 'Macaco → Passaro → Cao → Carneiro'),
(1157, 'Liberação de Fogo: Ruído da Chama do Dragão', 'Enquanto no Modo Despertar, Madara cria um único dragão de fogo para explodir assim que entrar em contato com o inimigo. Na explosão, três pequenas chamas são criadas em diferentes ângulos, para, em seguida, dar origem a um vórtice gigante de fogo e engolir o inimigo.', '火遁・龍焔業歌かとん・りゅうえんごうか', 'Katon: Ryūen Gōka', 'Liberação de Fogo: Ruído da Chama do Dragão', 'Estilo Fogo: Jutsu da Canção da Chama do Dragão', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1158, 'Liberação de Fogo: Tela de Vendaval Flamejante', 'O usuário cospe um tornado de fogo da sua boca, que mostrou-se poderoso o suficiente para fazer seus alvos queimarem como papel. Quando em contato com grandes volumes de água, a técnica cria uma densa nuvem de vapor.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1159, 'Liberação de Fogo: Técnica da Cerâmica Queimada', 'O usuário cria chamas dentro de um forno. As chamas podem também ser poderosas o suficiente para queimar em um forno gigante criado pelo Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares, cozinhando os adversários até que eles sejam totalmente derrotados. Esta técnica também é usada em conjunto com a Liberação de Terra: Queda da Terra Pegajosa para endurecer a lama que cobre o corpo do inimigo, posteriormente imobilizando-o.', '火遁・素焼きの術かとん・すやきのじゅつ', 'Katon: Suyaki no Jutsu', 'Liberação de Fogo: Técnica da Cerâmica Queimada', NULL, NULL, 'Estilo Fogo: Jutsu do Biscoito Queimado', 'Todos os alcances', NULL, 'Rato → Dragao → Javali ou Cavalo → Tigre[1]'),
(1160, 'Liberação de Fogo: Técnica da Dança da Chama Mistica', 'Depois de realizar os selos necessários, o usuário é capaz de criar e soprar um gás inflamável a partir de seus pulmões. Uma vez que ele entra em contato com fogo ou faísca, o gás se inflama em uma grande bola de fogo. Kagerō usa esta técnica, enquanto estava disfarçado de Kabuto Yakushi.', '火遁・霞炎舞の術', 'Katon: Kasumi Enbu no Jutsu', 'Liberação de Fogo: Técnica da Dança da Chama Mistica', NULL, NULL, 'Estilo Fogo: Técnica da Dança da Chama Mistica', 'Curto alcance', NULL, 'Cobra → Carneiro → Macaco → Tigre'),
(1161, 'Liberação de Fogo: Técnica da Fênix Sábia de Fogo', 'Esta técnica cria uma saraivada de bolas de fogo variando em tamanho pequeno até médio, que são lançadas em direções imprevisíveis contra o oponente. Além disso, as chamas são controladas uma a uma, com chakra, sendo que evitar todas elas é bastante difícil. As shuriken também podem ser escondidas dentro das chamas, criando um ataque surpresa secundário inesperado. Como as chamas, a shuriken pode ser controlada com chakra. Para a maior parte, no entanto, esta técnica é utilizada como uma distração.', '火遁・鳳仙火の術かとん・ほうせんかのじゅつ', 'Katon: Hōsenka no Jutsu', 'Liberação de Fogo: Técnica da Fênix Sábia de Fogo', 'Estilo Fogo: Jutsu da Fênix de Fogo', 'Estilo do Fogo: Técnica das Flores de Fogo', 'Estilo Fogo: Jutsu Flor de Fênix', 'Curto alcance', 'Rank C', 'Rato → Tigre → Cao → Boi → Coelho → Tigre'),
(1162, 'Liberação de Fogo: Técnica da Fênix Sábia de Fogo — Prego Carmesim', 'Através de seu Susanoo, Shisui lança algumas fūma shuriken infundidas em fogo em seu oponente.', '火遁・鳳仙火の術　爪紅かとん・ほうせんかのじゅつ　つまべに', 'Katon: Hōsenka no Jutsu — Tsumabeni', 'Liberação de Fogo: Técnica da Fênix Sábia de Fogo — Prego Carmesim', 'Estilo Fogo: Jutsu Flor da Fênix: Garra Escarlate', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1163, 'Liberação de Fogo: Técnica da Grande Bola de Fogo', NULL, '火遁・豪火球の術かとん・ごうかきゅうのじゅつ', 'Katon: Gōkakyū no Jutsu', 'Liberação de Fogo: Técnica da Grande Bola de Fogo', NULL, NULL, 'Estilo Fogo: Jutsu Bola de Fogo', 'Curto alcance', 'Rank C', 'Tigre[2] ou Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre'),
(1164, 'Liberação de Fogo: Técnica da Grande Bola de Fogo — Chama Rotatória', 'Tobi abana uma rajada de vento no oponente com seu gunbai, formando um turbilhão de chamas com sua Liberação de Fogo: Técnica da Grande Bola de Fogo.', '火遁・豪火球の術　旋炎かとん・ごうかきゅうのじゅつ　せんえん', 'Katon: Gōkakyū no Jutsu — Sen\'en', 'Liberação de Fogo: Técnica da Grande Bola de Fogo — Chama Rotatória', 'Estilo Fogo: Jutsu da Bola de Fogo de Chama e Vento', NULL, NULL, 'Curto alcance', NULL, 'Tigre'),
(1165, 'Liberação de Fogo: Técnica da Grande Bola de Fogo: Onibi', 'Itachi cria um clone da sombra, que ataca com uma enxurrada de shuriken, enquanto o Itachi real termina a técnica concluindo com uma bola de fogo.', '火遁・豪火球の術・鬼火かとん・ごうかきゅうのじゅつ・おにび', 'Katon: Gōkakyū no Jutsu: Onibi', 'Liberação de Fogo: Técnica da Grande Bola de Fogo: Onibi', 'Estilo Fogo: Jutsu Bola de Fogo: Fogo Negro', NULL, NULL, 'Curto alcance', NULL, NULL),
(1166, 'Liberação de Fogo: Técnica da Grande Chama', 'O usuário amassa chakra em seu estômago e expele um grande fluxo de chamas de sua boca que se alarga progressivamente com o alcance. As chamas são quentes o suficiente para evaporar rapidamente grandes quantidades de água dentro de um período relativamente curto de tempo.', '火遁・豪炎の術', 'Katon: Gōen no Jutsu', 'Liberação de Fogo: Técnica da Grande Chama', NULL, 'Estilo do Fogo: Técnica das Grandes Chamas', NULL, 'Todos os alcances', NULL, NULL),
(1167, 'Liberação de Fogo: Técnica de Ocultação em Pó e Cinza', 'O usuário expele fumaça quente com infusão de chakra que queima quando em contato. A técnica também pode ser usada como um poderoso dissuasor de ataques, bem como uma cortina de fumaça altamente eficaz, também pode ser usada para maneiras de distração para fugas.', '火遁・灰塵隠れの術かとん・はいじんがくれのじゅつ', 'Katon: Haijingakure no Jutsu', 'Liberação de Fogo: Técnica de Ocultação nas Cinzas', NULL, 'Estilo do Fogo: Técnica da Cortina de Cinzas', NULL, 'Todos os alcances', NULL, NULL),
(1168, 'Liberação de Fogo: Técnica de Ocultação no Fogo', 'Usando esta técnica, o usuário pode esconder-se dentro de uma chama. Ao deixar a ocultação, chamas queimam, revelando o usuário. Um membro do Grupo Nokizaru fez uso desta técnica durante uma missão para recuperar um pergaminho roubado por um shinobi de Kumogakure.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1169, 'Liberação de Fogo: Técnica do Dragão de Fogo', 'O usuário cospe fogo, ao longo de um cabo, fio ou qualquer outro tipo de objeto longo, que corre para a frente em linha reta para pegar o inimigo em chamas. Uma linha de inimigos pode ser usada como condutor também. As chamas são destinadas ao alvo e atacam a parte superior do corpo do inimigo como uma arma de projétil.', '火遁・龍火の術', 'Katon: Ryūka no Jutsu', 'Liberação de Fogo: Técnica do Dragão de Fogo', NULL, NULL, 'Estilo Fogo: Jutsu Chama do Dragão', 'Médio alcance', 'Rank C', 'Cobra → Dragao → Coelho → Tigre'),
(1170, 'Liberação de Fogo: Técnica do Grande Dragão de Fogo', 'O usuário comprime uma grande quantidade de chakra construído dentro de seu corpo e moldando-o em uma bola de fogo em forma de cabeça de dragão. O usuário então manipula habilmente o grande fogo, e ataca seu oponente. Mesmo fora do alcance de ataque, sua potência e confiabilidade são estressantes​​. As chamas têm uma alta temperatura, como elas podem criar um movimento ascendente das correntes de ar que por sua vez podem dar origem a nuvens de trovão. Um mestre dessa técnica pode disparar várias chamas em sucessão e quem for pego pela técnica poderia ser potencialmente reduzido a cinzas.', '火遁・豪龍火の術', 'Katon: Gōryūka no Jutsu', 'Liberação de Fogo: Técnica do Grande Dragão de Fogo', 'Estilo Fogo: Jutsu do Dragão de Fogo', 'Estilo do Fogo: Técnica do Grandioso Dragão de Fogo', 'Estilo Fogo: Jutsu do Dragão de Chama', 'Todos os alcances', 'Rank B', 'Tigre'),
(1171, 'Liberação de Fogo: Técnica do Projétil de Fogo', 'Depois de amassar chakra no estômago, o usuário o expele sob a forma de uma poderosa corrente de fogo até o alvo pretendido.', NULL, NULL, NULL, NULL, NULL, NULL, 'Todos os alcances', NULL, 'Passaro → Javali → Tigre[1]'),
(1172, 'Liberação de Fogo: Técnica do Rugido do Dragão de Chamas', 'Tal como acontece com a Liberação de Fogo: Técnica do Grande Dragão de Fogo, o usuário comprime uma grande quantidade de chakra moldada e, em seguida, expele em uma série de bolas de fogo com forma de dragão. É possível controlar a direção e a intensidade do ataque, tornando inevitável uma fuga.', '火遁・龍炎放歌の術かとん・りゅうえんほうかのじゅつ', 'Katon: Ryūen Hōka no Jutsu', 'Liberação de Fogo: Técnica do Rugido do Dragão de Chamas', 'Estilo Fogo: Jutsu do Dragão de Chama Cantando Ruídos', 'Estilo do Fogo: Canção Lança-Chamas do Dragão', NULL, 'Todos os alcances', NULL, 'Tigre'),
(1173, 'Liberação de Fogo: Zukokku', 'A máscara de Liberação de Fogo de Kakuzu dispara uma pequena bola de fogo que explode em contato com qualquer superfície. O poder explosivo faz com que as chamas generalizem por todo o local. Combinada com a máscara de vento, esta técnica compõe uma das mais poderosas combinações de Kakuzu.', '火遁・頭刻苦かとん・ズコック', 'Katon: Zukokku', 'Liberação de Fogo: Máscara do Sofrimento', NULL, 'Estilo do Fogo: Máscara do Sofrimento', 'Estilo Fogo: Enxaqueca Cauterizante', 'Todos os alcances', 'Rank B', 'Rato → Cao → Tigre'),
(1174, 'Liberação de Gelo: Andorinha da Tempestade de Neve', 'Esta técnica cria um conjunto de agulhas de gelo em forma de pequenas andorinhas formadas à partir de gelo pré-existente, em seguida são lançadas no adversário. Estas agulhas podem mudar de direção em pleno ar e podem mutilar o adversário com suas asas afiadas. Esta técnica pode ser eliminada por meio de técnicas baseadas em Fogo.', '氷遁・ツバメ吹雪ひょうとん・つばめふぶき', 'Hyōton: Tsubame Fubuki', 'Liberação de Gelo: Andorinha da Tempestade de Neve', NULL, NULL, 'Estilo Gelo: Nevasca Tsubame', 'Curto a Médio alcance', NULL, 'Tigre'),
(1175, 'Liberação de Gelo: Andorinha de Sincelo', 'O usuário dispara lanças de gelo para perfurar seu alvo. Uma vez atingido, o alva ficará congelado por um período fixo de tempo e não será capaz de se mover.', '氷遁・氷柱燕ひょうとん・つららつばめ', 'Hyōton: Tsurara Tsubame', 'Liberação de Gelo: Andorinha de Sincelo', 'Estilo Gelo: Andorinha Sincelo', NULL, NULL, 'Médio alcance', NULL, NULL),
(1176, 'Liberação de Gelo: Baleia Branca de Um Chifre', 'Esta técnica permite que o usuário crie uma baleia monstruosa com um longo chifre narval de preexistência de gelo para atacar seu oponente. O utilizador pode usar o tamanho da invocação para destruir barreiras. O grande volume desta técnica é capaz de criar uma grande destruição em cima de uma aterragem. No entanto, devido ao seu tamanho e falta de velocidade, esta técnica é utilizada mais como uma distração, ou para bloquear movimentos de um oponente ao invés de ataque.', '氷遁・一角白鯨ひょうとん・いっかくはくげい', 'Hyōton: Ikkaku Hakugei', 'Liberação de Gelo: Baleia Branca de Um Chifre', NULL, NULL, 'Estilo Gelo: Jutsu da Baleia Branca', 'Todos os alcances', NULL, 'Tigre → Passaro → Macaco → Cavalo → Cobra → Cao'),
(1177, 'Liberação de Gelo: Captura no Campo Congelado', 'Depois de realizar o selo de mão necessário para a técnica, Ryōgi congela o solo, de forma instantânea, de modo a congelar os pés do inimigo e imobilizá-lo.', NULL, NULL, NULL, NULL, NULL, NULL, 'Médio alcance', NULL, 'Selo do Confronto'),
(1178, 'Liberação de Gelo: Combo Sincelo', 'Haku desfere uma série de golpes com senbon, seguido de alguns chutes, antes de retomar a cortar o adversário. Ele então chuta o oponente para longe, gerando várias agulhas grandes de gelo no ar no intuito de acertar o oponente.', '氷遁・氷柱連弾ひょうとん・つらられんだん', 'Hyōton: Tsurara Rendan', 'Liberação de Gelo: Combo Sincelo', 'Estilo Gelo: Barragem de Sincelo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1179, 'Liberação de Gelo: Correntes Consecutivas de Gelo Térreo', 'O usuário, por meio de contato físico ou remotamente, faz com que partículas de gelo se formem no corpo do alvo. Se usado em um ser vivo, o alvo congelará até a morte, a menos que ele seja capaz de moldar chakra. Revestindo-se com chakra e dedicando toda a sua energia para se manter quente, o alvo consegue evitar a morte; se o alvo usar seu chakra para executar uma técnica, ele começará a congelar rapidamente novamente. Sua capacidade em neutralização está a par com a Liberação de Fogo: Prisão Celestial.', '氷遁・地鎖連氷ひょうとん・じされんひょう', 'Hyōton: Jisarenhyō', 'Liberação de Gelo: Correntes Consecutivas de Gelo Térreo', NULL, NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1180, 'Liberação de Gelo: Desintegração do Pico de Gelo', 'Haku dispara uma série de senbon, seguido de alguns chutes, antes de cortar o oponente no alto. Em seguida, ele chuta o oponente novamente, antes de criar gelo para atacar o oponente e arrastá-lo para longe. Antes de seu oponente cair no chão, Haku coloca a mão no chão, gerando múltiplas agulhas longas e finas de gelo para espetar o oponente.', '氷遁・氷嶽崩ひょうとん・ひょうがくほう', 'Hyōton: Hyōgakuhō', 'Liberação de Gelo: Desintegração do Pico de Gelo', 'Estilo Gelo: Iceberg Esmagador', NULL, NULL, 'Curto a Médio alcance', NULL, NULL),
(1181, 'Liberação de Gelo: Dez Mil Pétalas de Gelo', 'Ryōgi lança uma barragem de dardos de gelo. A intensidade do frio é tão grande, que se uma pessoa tentar bloqueá-los, isso causará danos severos à referida pessoa.', '氷遁・万華氷ひょうとん・まんげひょう', 'Hyōton: Mangehyō', 'Liberação de Gelo: Dez Mil Pétalas de Gelo', NULL, NULL, NULL, 'Curto e Médio alcance', NULL, 'Tigre'),
(1182, 'Liberação de Gelo: Domo de Aicebergue', 'Haku pode rapidamente criar uma cúpula de gelo em torno de seus aliados e de si mesmo para agir como uma defesa. É forte o suficiente para suportar o ataque de vários papéis explosivos ao mesmo tempo, mantendo-se praticamente intacta.', '氷遁・氷岩堂無ひょうとん・ひょうがんどうむ', 'Hyōton: Hyōgan Dōmu', 'Liberação de Gelo: Câmara Nula de Aicebergue', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1183, 'Liberação de Gelo: Martelo de Forja Quebra-Gelo', 'O usuário envolve seu antebraço com gelo altamente resistente como o aço, de modo a aumentar tanto seu poder de ataque, como, alternativamente, usar para bloquear ataques físicos potentes.', '氷遁・砕氷槌ひょうとん・さいひょうずち', 'Hyōton: Saihyōzuchi', 'Liberação de Gelo: Martelo de Força Quebra-Gelo', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1184, 'Liberação de Gelo: Nevasca do Dragão Negro', 'Uma técnica de Liberação de Gelo formada a partir de gelo já existente. Depois de formar o selo de mão necessário Dotō que estica o braço para mandar um dragão semelhante ao éter negro para atacar seu oponente. Como ele voa pelo ar ele começa a girar. Quando atinge o alvo usa seu movimento para lançar o oponente para o alto.', '氷遁・黒龍暴風雪ひょうとん・こくりゅうぼうふうせつ', 'Hyōton: Kokuryū Bōfūsetsu', 'Liberação de Gelo: Nevasca do Dragão Negro', NULL, NULL, 'Estilo Gelo: Nevasca do Dragão Negro', 'Longo alcance', NULL, 'Passaro → Cobra → Macaco → Cavalo → Cao'),
(1185, 'Liberação de Gelo: Nevasca dos Dragões Gêmeos', 'Uma versão avançada da Liberação de Gelo: Nevasca do Dragão Negro em que Dotō libera dois dragões de neve negra que se fundem em um tornado maciço. Esta técnica manipula o gelo existente em vez de criar gelo esta técnica incapacita o oponete o deixando congelado. seu tornado consegue alcançar uma gigantesca area devastando tudo pelo caminho.', '氷遁・双龍暴風雪ひょうとん・そうりゅうぼうふうせつ', 'Hyōton: Sōryū Bōfūsetsu', 'Liberação de Gelo: Nevasca dos Dragões Gêmeos', NULL, NULL, 'Estilo Gelo: Nevasca dos Dragões Gêmeos', 'Longo alcance', NULL, 'Cobra → Rato → Dragao → Macaco → Tigre → Cão'),
(1186, 'Liberação de Gelo: Parede de Cristal', 'O usuário envolve os inimigos em uma cúpula de gelo. Depois, uma vez que os inimigos olham para os reflexos de si mesmos, os mesmos são atraídos pela ilusão, vendo seus aliados como inimigos.', '氷遁・水晶壁ひょうとん・すいしょうへき', 'Hyōton: Suishōheki', 'Liberação de Gelo: Parede de Cristal', NULL, NULL, NULL, 'Curto e Médio alcance', NULL, 'Tigre'),
(1187, 'Liberação de Gelo: Rasgante Dragão Feroz Tigre', 'Esta técnica permite ao usuário manipular gelo pré-existente e criar um tigre gigante, exigindo, portanto, uma fonte próxima de gelo para usar esta técnica. Ela é fria o suficiente para congelar todas as técnicas baseadas em água quando entra em contato as mesmas.', '氷遁・破龍猛虎ひょうとん・はりゅうもうこ', 'Hyōton: Haryū Mōko', 'Liberação de Gelo: Rasgante Dragão Feroz Tigre', NULL, NULL, 'Estilo Gelo: Dragão Contra Tigre', 'Longo alcance', NULL, 'Tigre → Javali → Cao'),
(1188, 'Liberação de Gelo: Redemoinho de Vento', 'Mirando sua mão contra o adversário, o usuário dispara vários blocos de gelo que, em seguida, fragmentam-se para originar um redomoinho gelado de modo a ofuscar seu campo de visão.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1189, 'Liberação de Gelo: Técnica da Avalanche da Presa de Lobo', 'Esta técnica permite que Nadare crie uma avalanche quando perto de uma montanha e transforma a neve em uma matilha de lobos que atacam o adversário. Esta técnica é especialmente fraca contra técnicas baseadas em Liberação de Relâmpago, como visto quando os lobos dissolveram-se completamente quando atacados pelo Corte Relâmpago de Kakashi Hatake.', '氷遁・狼牙雪崩の術ひょうとん・ろうがなだれのじゅつ', 'Hyōton: Rōga Nadare no Jutsu', 'Liberação de Gelo: Técnica do Avalanche da Presa de Lobo', NULL, NULL, 'Estilo Gelo: Jutsu Avalanche de Lobo', 'Todos os alcances', NULL, 'Rato → Coelho → Cao'),
(1190, 'Liberação de Lama: Boneco de Lama', 'Ameyuki é capaz de criar um boneco feito de lama, e utilizá-lo para diversas finalidades.', '泥遁・泥人形', 'Deiton: Doro Ningyō', 'Liberação de Lama: Boneco de Lama', NULL, NULL, NULL, NULL, NULL, NULL),
(1191, 'Liberação de Lama: Chão de Lama', 'Ameyuki é capaz destruir a área circundante ao converter o terreno em lama.', '泥遁・地盤泥化でいとん・じばんでいか', 'Deiton: Jiban Deika', 'Liberação de Lama: Chão de Lama', NULL, NULL, NULL, 'Todos os alcances', NULL, NULL),
(1192, 'Liberação de Lama: Parede de Barro', 'Uma técnica usada por Ameyuki, utilizando tonjutsu de Liberação de Lama. Ao colocar as mãos no chão, o usuário o transforma em lama que entra em erupção como uma parede e cai rapidamente sobre o inimigo em uma onda de água barrenta.', '泥遁・泥水壁', 'Deiton: Deisuiheki', 'Liberação de Lama: Parede de Barro', NULL, NULL, NULL, NULL, NULL, NULL),
(1193, 'Liberação de Lama: Vazio Profundo de Lama', 'Ameyuki é capaz de criar um lamaçal que engole os pés do inimigo, deixando apenas seus braços para fora.', '泥遁・泥底無', 'Deiton: Deiteimu', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1194, 'Liberação de Lava: Bola de Borracha', 'O usuário expele de sua boca uma grande bola de lava, que rapidamente se solidifica em uma bola esférica e oca, que pode ser usada para proteger uma pessoa, botando ela dentro da bola que é cercada por um material semelhante a borracha. As propriedades dessa substância é que ela pode tanto absorver ou dissipar atrás de um golpe quando atingido, o que a deforma ou a faz saltar pelo impacto.', '熔遁・護謨玉ようとん・ゴムだま', 'Yōton: Gomudama', 'Liberação de Lava: Bola de Borracha', NULL, 'Estilo da Lava: Esfera Protetora de Borracha', NULL, 'Curto alcance', NULL, 'Passaro'),
(1195, 'Liberação de Lava: Bolas de Borracha', 'Após moldar seu chakra, o usuário vomita várias bolas de borracha de tamanhos variados, que flutuam de acordo com o movimento de suas mãos, garantindo um certo controle sobre elas. Ao golpear as bolas de borracha, o usuário é capaz de atira-las contra seu alvo. Uma vez que as bolas estão infundidas com chakra, um único impacto pode ser forte o suficiente para causar pressão nos ossos do alvo.', '熔遁・護謨鞠ようとん・ゴムまり', 'Yōton: Gomumari', 'Liberação de Lava: Bolas de Borracha', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Tigre'),
(1196, 'Liberação de Lava: Chute Escaldante', 'Rōshi cobre o pé de lava e desfere um pontapé em seu oponente, jogando-o para cima.', '熔遁・灼蹴ようとん・しゃくしゅう', 'Yōton: Shakushū', 'Liberação de Lava: Chute Escaldante', 'Estilo Corrosão: Pontapé Ardente', NULL, NULL, 'Curto alcance', NULL, NULL),
(1197, 'Liberação de Lava: Corda de Borracha', 'O usuário cria uma corda com lava convertida num material semelhante a borracha. Ela pode ser usada para rapidamente retirar um aliado da linha de ataque, sendo mais eficiente quando combinada com uma distração.', NULL, NULL, NULL, NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1198, 'Liberação de Lava: Defesa de Borracha', 'O utilizador cria um tubo curto com lava convertido em um material semelhante a borracha que pode ser utilizado para proteger contra ataques de inimigos.', NULL, NULL, 'Liberação de Lava: Defesa de Borracha', NULL, NULL, NULL, 'Curto alcance', NULL, NULL),
(1199, 'Liberação de Lava: Desintegração da Grande Flor Escaldante', 'O usuário infunde seu punho com chakra de Liberação de Lava, aumentando em várias vezes o poder destrutivo do seu soco, com o efeito adicional de queimar seu oponente no impacto.', '熔遁・灼花大崩ようとん・しゃくかたいほう', 'Yōton: Shakuka Taihō', 'Liberação de Lava: Desintegração da Grande Flor Escaldante', 'Estilo Corrosão: Destruição da Flor de Fogo', NULL, NULL, 'Curto alcance', NULL, NULL),
(1200, 'Liberação de Lava: Dissolução', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(1201, 'Liberação de Lava: Explosão Marca-Estrela', 'Graças à sua linhagem sanguínea, a Liberação de Lava, Boro é capaz de manipular uma substancia ácida altamente corrosiva que ele pode utilizar de diferentes maneiras: ao performar o selo do pássaro, ele é capaz de desencadear uma enorme erupção que jorra a partir do chão, inundando toda a região, enquanto, ao executar o selo do tigre, ele é capaz de expelir o ácido a partir de sua boca em direção ao seu alvo; ou ainda, ele é capaz de lançar diversos projéteis antes de executar qualquer selo manual a fim de um ataque rápido.', '溶遁・烙星破', 'Yōton: Rakuseiha', 'Liberação de Lava: Explosão Marca-Estrela', NULL, NULL, NULL, 'Todos os alcances', NULL, 'Passaro ou Tigre'),
(1202, 'Liberação de Lava: Fluxo Escaldante do Pico da Rocha', 'Rōshi cria círculos de lava que irrompem em rocha derretida para o adversário cair dentro.', '熔遁・灼流峰岩ようとん・しゃくりゅうほうがん', 'Yōton: Shakuryūhōgan', 'Elemento Lava: Fluxo Escaldante do Pico da Rocha', 'Estilo Corrosão: Pico Fundido', NULL, NULL, 'Todos os alcances', NULL, NULL),
(1203, 'Liberação de Lava: Parede de Borracha', 'O usuário cria uma torrente enorme de lava do chão na frente dele, que forma então uma parede que possui durabilidade imensa devido à sua consistência de borracha que permite que o material se deforme quando atacado, o que significa que tanto pode absorver e dissipar a força por trás de um golpe em vez de simplesmente impedir isso. Esta técnica pode também ser utilizada para proporcionar um reforço para uma barreira pré-existente.', '熔遁・護謨壁ようとん・ゴムへき', 'Yōton: Gomuheki', 'Liberação de Lava: Parede de Borracha', NULL, 'Estilo da Lava: Parede Protetora de Borracha', NULL, 'Curto a Médio alcance', NULL, NULL),
(1204, 'Liberação de Lava: Punho Blindado Escaldante', 'Rōshi reveste-se em lava e soca o adversário. Mesmo que o soco não consiga se conectar, o intenso calor da lava ainda pode queimar o seu oponente se ele estiver perto o suficiente.', '熔遁・灼鎧甲拳ようとん・しゃくがいこうけん', 'Yōton: Shakugaikōken', 'Liberação de Lava: Punho Blindado Escaldante', 'Estilo Corrosão: Palma da Armadura Incandescente', NULL, NULL, 'Curto alcance', NULL, NULL),
(1205, 'Liberação de Lava: Punho de Fluxo Escaldante', 'Rōshi salta no ar, cobre o punho com lava e fornece um soco em seu oponente, fazendo com que ele caia rapidamente no chão.', '熔遁・灼流拳ようとん・しゃくりゅうけん', 'Yōton: Shakuryūken', 'Liberação de Lava: Punho de Fluxo Escaldante', 'Estilo Corrosão: Palma Fundida', NULL, NULL, 'Curto alcance', NULL, NULL),
(1206, 'Liberação de Lava: Técnica da Aparição Fusionada', 'Mei libera uma grande quantidade de lava de sua boca, que se espalha em um fluxo grande e fino, voando através do ar. Uma vez que é disparado para cima, forma de arco que cai sobre os alvos, impedindo que os indivíduos tenham a intenção de pular para cima para evitar o ataque. Uma grande quantidade de fumaça é criada depois, pelos efeitos da lava, por isso, caso os alvos ainda estejam vivos, um ataque seguido pode ser realizado enquanto a sua visão permanece obscura.', '溶遁・溶怪の術ようとん・ようかいのじゅつ', 'Yōton: Yōkai no Jutsu', 'Liberação de Lava: Técnica da Aparição Fusionada', 'Estilo Lava: Jutsu de Monstro da Lava', 'Estilo da Lava: Técnica da Assombração de Lava', 'Estilo Lava: Jutsu da Lava Fusionada', 'Curto a Médio alcance', 'Rank –', 'Cao → Javali → Tigre → Boi → Rato → Passaro');
