INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "ADN", "Por meio do poder do senjutsu, Kabuto é capaz de usar livremente as habilidades, talentos e kekkei genkai de outros ninjas cujo material genético está integrado dentro de si mesmo. Como alternativa, ele pode canalizar suas habilidades coletivas em respectivos clones irracionais de cada shinobi. Essas cópias são geradas em de seu umbigo de cobra.", null, "Rank –", null, "伝異遠影ディーエヌエー", "Dīenuē", "ADN; DNA; Incompatível Herança Distante das Sombras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "A Herança da Vontade do Fogo", "Hashirama, Tobirama e Hiruzen usam uma combinação de madeira, fogo e água para empurrar o oponente. Hashirama usa sua Técnica Secreta da Liberação de Madeira: Natividade de um Mundo de Árvores para criar uma grande floresta em que Tobirama e Hiruzen atacam o oponente usando uma combinação de fogo e água. Os três Hokage então atacam o oponente, após Hashirama usar o Punho Adamantino, Tobirama chuta o oponente e Hiruzen invoca Enma que então ataca o oponente com o bastão adamantino. A força do ataque é tanta que cria uma explosão gigante na floresta no processo da conclusão da técnica.", "Curto a Médio alcance", null, null, "受け継がれる火の意志うけつがれるひのいし", "Uketsugareru Hi no Ishi", "A Herança da Vontade do Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abate Duplo das Varetas de Metal", "Tenten invoca uma ferramenta do tamanho de seu oponente para atacá-lo.", "Curto alcance", null, null, "双金棒打ち落とし", "Sōkanabō Uchiotoshi", "Abate Duplo das Varetas de Metal", "Queda Dupla da Haste de Ouro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abelha Perfurante", "Obito demarca a área de ataque com suas Esferas da Busca da Verdade, então ele cria várias lanças que o perfuram e em seguida ele cria uma erupção de energia que joga seu oponente para o alto.", "Curto a Médio alcance", null, null, "穿蜂", "Ugachibachi", "Abelha Perfurante", null, null, null);
