INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abertura e Encerramento do Universo", "Obito usa o Kamui para enviar o oponente até a sua dimensão e então manipula os blocos presentes no local, criando uma formação deles. Com isso, ele abaixa o dedo verticalmente para baixo, fazendo com que todos os blocos caiem em direção ao adversário e os esmaguem.", null, null, null, "天地開閉", "Tenchi Kaihei", "Abertura e Encerramento do Céu e da Terra", "Criação do Mundo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abertura em Chamas Sopradas", "Matatabi, ou Yugito completamente transformada, salta no ar – de forma a ficar com a cabeça para baixo –, expele um fluxo de fogo em seu oponente e pousa entre as chamas, incinerando e esmagando tudo ao alcance delas.", "Curto alcance", null, null, "躍炎吹踏", "Yakuen Suitō", "Abertura em Chamas Sopradas", "Salto do Sopro de Chama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abordagem Acolhedora: Mil Braços Assassinos", "Esta é a técnica final do Templo do Fogo ensinado apenas aos seus monges ninjas, e requer o Dom do Grupo Eremita para ser utilizada.", "Curto alcance", null, null, "来迎・千手殺らいごう・せんじゅさつ", "Raigō: Senjusatsu", "Abordagem Acolhedora: Mil Braços Assassinos", null, null, "Raigô: Golpe de Mil Braços");
