INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abraço Ardente da Juventude", "O usuário soca seu oponente, e em seguida, como uma de suas artimanhas, ele o abraça. O abraço é forte o suficiente para fraturar as costas de seu oponente.", "Curto alcance", null, null, "青春の熱き抱擁せいしゅんのあつきほうよう", "Seishun no Atsuki Hōyō", "Abraço Ardente da Juventude", "Abraço Apaixonante da Juventude", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Abraço Infinito", "Uma barreira especial que subjuga o inimigo, empurrando-o para baixo e rapidamente drenando seu chakra. É muito difícil resistir a ela, mas Naruto Uzumaki conseguiu desfazê-la usando o chakra de Kurama.", "Curto alcance", null, null, "無限抱擁むげんほうよう", "Mugen Hōyō", "Infinito Abraço", null, null, "Mugenhōyō");
