INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Absorção de Todas as Coisas", "Trata-se de uma habilidade inata manifestada por alguns membros do clã Ōtsutsuki, que lhes permite absorver chakra.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aceleração", "O usuário libera o chakra do Oitavo Portão e aumenta a sua velocidade em várias vezes, permitindo uma rápida sequência de golpes. A velocidade alcançada é tão alta que o usuário enxerga o tempo em câmera lenta.", null, null, null, "加速", "Kasoku", "Aceleração", "Fluxo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Acorrentamento Celestial", "Obito passa através de seu oponente, materializa-se e prende-o com suas corretes, jogando o inimigo no ar. Em seguida, ele se teletransporta para cima de seu adversário, acumula chakra em seu punho e desfere um poderoso soco nele, lançando-o para o chão.", "Curto alcance", null, null, "鎖天", "Saten", "Acorrentamento Celestial", "Corrente Celestial", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Acorrentamento da Prisão Explosiva", "Hanzō prende seu oponente no lugar com a ponta final pesada de sua kusarigama, e cria uma esfera de papéis explosivos ao redor dele, que explodem.", "Curto alcance", null, null, "鎖牢爆さろうばく", "Sarōbaku", "Acorrentamento da Prisão Explosiva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Acorrentamento por Fūinjutsu", "Após identificar um terreno com pilares, o usuário é capaz conjurar inscrições que acorrentam um alvo a eles. Para fortificar o aprisionamento, é possível entrelaçar as 'correntes' entre os pilares. No entanto, a técnica pode ser liberada com uma poderosa emissão de chakra e impacto ou com kunai em pontos estratégicos.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Acrobata", "O Acrobata é um taijutsu usado por Killer B em conjunto com suas Espadas de Liberação de Relâmpago Super-Vibrantes e a técnica padrão do seu Taijutsu de Perturbação (攪乱体術, Kakuran Taijutsu, Literalmente significa: Técnica de Perturbação Corporal) único.", "Curto alcance", null, null, "荒線鷺伐刀", "Akurobatto", "Garça-Real da Linha Áspera da Espada Impressionante", null, null, "Acrobata");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aglomeração Explosiva do Deserto de Areia", "Depois de aglomerar um punhado de areia e arenito no ar, o usuário arremessa em alta velocidade contra o oponente, esmagando-o. Gaara ainda pode combinar a técnica tanto com a Caixão de Areia para capturar o alvo antes desse ataque, quanto com o Grande Funeral do Deserto para esmagá-lo brutalmente.", "Curto a Médio alcance", null, "Passaro", "砂爆砂塊", "Sabaku Sakai", "Aglomeração Explosiva do Deserto de Areia", "Cacho de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aglomeração de Árvores", "Hashirama faz emergir algumas raízes do solo, que se entrelaçam e lançam o oponente no ar, fazendo com que que o inimigo abaixe sua guarda ou mesmo desestabilize sua defesa. Dessa forma, a tática permite que Hashirama golpeie-o ainda no ar.", "Curto alcance", null, null, "縫連樹", "Hōrenju", "Aglomeração de Árvores", "Forrado da Floresta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha Fria", "Shizune ataca seu adversário algumas vezes, então ela vira e bate-os com um Bisturi de Chakra. Ela termina com eles acertando-os com várias senbon.", "Curto a Médio alcance", null, null, "凛針りんしん", "Rinshin", "Agulha Fria", "Agulhas Penetrantes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha Infernal", "Depois de usar as Agulhas Jizō, estendendo seu cabelo ao redor de seu corpo, o usuário dispara o cabelo em forma de agulha endurecida em múltiplas direções ao inimigo.", "Médio a Longo alcance", null, null, "針地獄", "Hari Jigoku", "Agulha Infernal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha Senbon da Besta-Humana", "Em seu estado transformado, o shinobi do clã Tenrō pode disparar seus cabelos como senbon em todas as direções, dando poucas oportunidades para seu oponente desviar.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha de Cabelo do Coelho", "A Agulha de Cabelo do Coelho é um taijutsu de Kekkei Mōra usado por Kaguya Ōtsutsuki.", "Médio a Longo alcance", "Rank –", null, "兎毛針とげばり", "Togebari", "Agulha de Cabelo do Coelho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha de Chakra", "Agulha de Chakra é a técnica assinatura de Hinoko, desenvolvida durante seu tempo na Academia. Graças a essa técnica, ela foi recrutada para o Anbu desde formada.", "Todos os alcances", null, "Cavalo → Carneiro", "チャクラの針ちゃくらのはり", "Chakura no Hari", "Agulha de Chakra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulha de Água", "Usando o controle de chakra concedido pelo Punho Gentil, Hinata condensa água nas proximidades e molda-a em agulhas para lançar em seus alvos. Usando o Byakugan, esta técnica pode ser utilizada com grande precisão para atingir alvos muito pequenos, tais como as abelhas.", "Curto a Médio alcance", null, null, "水針", "Mizu Hari", "Agulha de Água", "Jutsu Senbon de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulhas Carregadas", "O usuário prepara algumas senbon dentro de sua boca ou mesmo uma que está em sua boca e as dispara em alta velocidade em seu inimigo. Shizune demonstrou ter a capacidade de mergulhar a agulha em veneno e disparar com uma precisão alternativa, o que pode ampliar ou diminuir as suas chances de êxito no inimigo consideravelmente.", "Curto alcance", null, null, null, null, null, null, null, "Agulhas Ocultas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulhas Jizō", "Com esta técnica o usuário se recobre em um número incontável de agulhas afiadas que são, na realidade, seus cabelos espetados. O chakra concede aos fios de cabelo uma dureza e a forma de agulhas ou espinhos, tornando-o dotado de uma capacidade de ferir e matar igualmente a armas metálicas. Além disso, se ativado em resposta a um ataque do oponente, ele pode também ser usado como um contra-ataque, bem como pode ser usado para aprimorar outras técnicas de contato. No anime, o composto do cabelo é duro o suficiente para que ele possa suportar golpes de certos tipos de armas, como flechas.", "Curto alcance", "Rank B", "Tigre → Cavalo → Javali → Carneiro → Rato → Cobra", "針地蔵", "Hari Jizō", "Agulhas de Jizō", null, "Estátua de Espinhos", "Arte Ninja: Agulha Jizo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulhas da Sombra", "Uma técnica simples usando Senbon, o utilizador lança uma Senbon com sinos anexados e outra Senbon sem sinos ao mesmo tempo. A intenção é que o adversário só vá reagir ao som dos sinos e não notar as silenciosas, sendo assim, atingido pela ultima Senbon. Esta técnica se concentra em como o cérebro funciona, instintivamente: reagir ao som, o cérebro 'esquece' sobre as informações fornecidas pelos outros sentidos. No entanto, é muito fácil de escapar do ataque se o adversário reconhece o truque simplesmente se baseia nas Senbon lançadas.", "Curto e Médio Alcance", null, null, "影千本", "Kage Senbon", "Agulhas da Sombra", "Senbon de Sino", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Agulhas de Areia Eletrizadas", "Essa é uma técnica muito semelhante à Garoa de Areia. Gaara lança uma grande quantidade de areia no céu chuvoso sobre o adversário. A areia então se forma em agulhas que se colam no chão e criam uma carga elétrica que drena e neutraliza a eletricidade. Gaara usa isso para desativar a Bola de Plasma de Ranke.", "Todos os alcances", null, null, "砂雷針すならいしん", "Suna Raishin", "Agulhas de Areia Eletrizadas", "Areia de Shukaku (守鶴の砂, Shukaku no Suna)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Akuta", "Esta é uma técnica de Liberação de Terra criada a partir do kinjutsu do País da Terra resgatado por Ōnoki em sua incansável pesquisa. Esta técnica foi designada pelo Terceiro Tsuchikage para proteger a vila de invasores e diminuir o número de vítimas.", null, null, null, "芥", "Akuta", "Descartáveis", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Alucinação", "Kamira é capaz projetar uma Alucinação para prender seu oponente, que faz com que a área ao seu redor seja envolvida pela escuridão. Uma vez dentro do espaço negro, o alvo vai ver figuras grotescas que, em seguida, o pegam e o levam para o vazio onde ele estará à mercê de um dragão espectral. O oni horripilante pode até mesmo estender-se de seu próprio corpo do alvo para atacá-los. Todas essas criaturas podem petrificar a vítima em choque enquanto Kamira aparece a partir acima para sufocar e matar o adversário.", "Curto a Médio alcance", null, null, "幻覚", "Genkaku", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amaterasu", "O Amaterasu é uma técnica ocular do Mangekyō Sharingan. Dito como a técnica de fogo de mais alto nível e a representaçào do 'Mundo Material e Luz' (物質界と光, Busshitsukai para Hikari), Amaterasu é a antípoda do Tsukuyomi.", "Curto alcance", null, "Tigre[2]", "天照あまてらす", "Amaterasu", "Iluminação Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amaterasu: Camada Dupla", "Sasuke e Itachi usam simultaneamente o Amaterasu sobre o inimigo, jogando-o para trás, e então cada um invoca seu Susanoo, antes de disparar uma flecha e um grupo de Yasaka Magatama, respectivamente. O impacto resulta em uma explosão das chamas negras, após aplicarem uma segunda camada de Amaterasu, resultando em uma gigante explosão de chamas negras.", "Todos os alcances", null, null, "天照・二重あまてらす・にじゅう", "Amaterasu: Nijū", "Iluminação Celestial: Camada Dupla", "Amaterasu: Camada Dupla", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amaterasu: Fogo Persistente", "Sasuke cria um orbe de Amaterasu na terceira mão do seu Susanoo, de então são expelidos outros quatro orbes de chamas negras contra os oponentes, explodindo ao entrar em contato com eles. Os orbes são usados como armadilhas a curta distância.", "Curto a Médio alcance", null, null, "天照・残火あまてらす・ざんか", "Amaterasu: Zanka", "Iluminação Celestial: Fogo Persistente", "Amaterasu: Cinzas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amaterasu: Fogo do Envoltório de Chamas", "Após manifestar as costelas do Susanoo, Sasuke é capaz de aumentar seu poder defensivo ao cobri-las com as chamas do Amaterasu, envolvendo-se com um escudo de chamas negras. Ele também pode conduzir as chamas para sua frente e moldá-las em espinhos, para empalar quem quer que tente atacá-lo. Além dos danos que as chamas podem causar, isso tem o efeito psicológico de fazer com que os inimigos hesitem em atacá-lo.", "Curto alcance", null, null, "天照・炎纏火あまてらす・えんてんか", "Amaterasu: Entenka", "Iluminação Celestial: Envoltório de Chamas", "Amaterasu: Acumulação de Chama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-himuro", "Kaguya manipula a umidade presente no ar e a condensa em uma câmara de gelo para aprisionar seu oponente, que explode com a pressão.", "Curto a Médio alcance", null, null, "天之氷室アメノヒムロ", "Amenohimuro", "Câmara de Gelo dos Céus", "Câmara Divina de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-mihashira", "Amenomihashira (天御柱) é uma técnica original criada por Asura Ōtsutsuki, que inclui a transformação da natureza de Liberação de Vento.", "Todos os alcances", null, "Tigre → Cao", "天御柱アメノミハシラ", "Amenomihashira", "Honorável Pilar Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-minaka", "Usando seu terceiro olho, Kaguya é capaz de instantaneamente teleportar alguém, em um determinado intervalo, para dimensões diferentes. Aqueles afetados por ela são pegos completamente desprevenidos, como parece não ter nenhum aviso antes da sua utilização e encontraram-se mergulhando em um rio de lava, e de repente são envoltos em um bloco de gelo. Tendo sido enviado para uma dimensão diferente, a presença de seu chakra é completamente apagada do mundo normal e não pode ser detectada através de meios tradicionais.", "Todos os alcances", "Rank –", null, "天之御中アメノミナカ", "Amenominaka", "Administração dos Céus", "Ame no Minaka", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-subaruboshi no Mikoto", "Depois de balançar sua vara de chakra, o usuário cria uma barragem de anzóis para perfurar e imobilizar o alvo, ou derrubá-lo com uma forte explosão.", "Curto a Médio alcance", null, null, "天昴宿命アメノスバルボシノミコト", "Amenosubaruboshi no Mikoto", "Majestosa Chefe Peluda dos Céus", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-subaruboshi no Mikoto: Caixa de Jade", "Urashiki ergue sua mão em direção a seu alvo e, a partir de sua palma, cria uma esfera de chakra em alta rotação que ele pode usar para golpear seus oponentes à queima roupa ou atirar, explodindo no impacto. Ele também mostrou-se capaz de disparar múltiplas esferas em rápida sucessão, golpeando seus inimigos repetidas vezes até que suas forças se esgotem e não consigam mais ficar de pé.", "Todos os alcances", null, null, "天須波流星命・玉匣", "Ame-no-subaruboshi no Mikoto: Gyokkō", "Majestosa Chefe Peluda dos Céus: Caixa de Jade", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-subaruboshi no Mikoto: Rei Dragão", "Com seu braço estendido em direção ao seu alvo, Urashiki molda seu chakra em uma massiva esfera de energia acompanhada pela aparição de um dragão marinho que a empunha entre suas presas. O dragão carrega a esfera até seu destino e tem poder destrutivo capaz de competir com o Super Rasengan Uzumaki.", "Longo alcance", null, null, "天須波流星命・竜宮­", "Ame-no-subaruboshi no Mikoto: Ryūgū", "Majestosa Chefe Peluda dos Céus: Rei Dragão", null, null, null);
