INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ame-no-tejikara: Chidori", "Sasuke lança dois conjuntos de kunai infundidas com chakra, o primeiro é usado como distração para imobilizar o alvo, o segundo para usar seu Ame-no-tejikara, trocando de lugar com elas, teletransportando-se e aproximando de seu oponente, em seguida, desferindo um Chidori infundido em chamas negras.", "Curto a Médio alcance", null, null, "天手力・千鳥アメノテジカラ・ちどり", "Ame-no-tejikara: Chidori", "Poder da Mão Celestial: Mil Pássaros", "Poder da Mão Celestial: Chidori", null, null);
