INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amieiro (Ultimate Ninja 3)", "Kimimaro corre até seu oponente, e, usando-o como suporte, salta sobre ele. Em seguida, crescem pontas de ossos de suas costas, acertando o oponente.", "Curto alcance", null, null, "夜叉五倍子", "Yashabushi", "Amieiro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Amieiro (Ultimate Ninja Storm)", "Kimimaro golpeia o oponente com sua espada óssea, cria outra em seu cotovelo e ataca o oponente, cria picos de ossos em seus pulsos e ataca o oponente novamente, corta o oponente com sua espada óssea em sentido diagonal e finaliza com forte ataque diagonal contra o oponente, atravessando-o.", "Curto alcance", null, null, "夜叉五倍子やしゃぶし", "Yashabushi", "Amieiro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Anātman", "Gaara executa os selos de mão necessários e a areia circundante sob seus pés puxe-o para baixo. Após o término de sua execução, o usuário faz com que a areia leve-o de volta para a superfície. Esta técnica foi útil para escapar da Rotação do Vento Feroz de Seimei.", "Curto alcance", null, null, "無我むが", "Muga", "Altruísmo", "Anatta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Andorinha Voadora", "Um ninjutsu de adição que aumenta o poder de matar de uma lâmina — como uma kunai ou espada — fluindo chakra nela. O chakra materializado é moldado como uma lâmina, cortando tudo o que toca. Também é possível alterar a distância do ataque da arma, ajustando a quantidade de chakra colocado na lâmina. Esta técnica é muito difícil de ser desviada.", "Curto alcance", "Rank B", null, "飛燕", "Hien", "Andorinha Voadora", null, "Voo da Andorinha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Anel de Fogo", "O usuário expande um anel de chamas fundidas, que podem ser seladas dentro de um projétil de aço para derretê-lo e transformá-lo em uma forma semelhante a uma espiral. As chamas seladas, em seguida, fazem com que uma onda de choque de alta pressão derreta o projétil, que explode em uma tempestade de chamas.", "Curto a Médio alcance", null, null, null, null, "Anel de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Anestesia Secreta", "Esta técnica requer o chakra das cobras de Yomi para ser realizada. A cobra morde o inimigo, paralisando-o temporariamente. A área em torno da marca da mordida em seguida, torna-se arroxeada.", "Curto alcance", null, null, "麻睡窃術", "Masui Sejutsu", "Anestesia Secreta", "Jutsu da Anestesia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aniquilação", "Obito desfere uma série de socos e chutes em seu oponente. Enquanto em combate físico, ele se materializa e desmaterializa por todo o corpo do oponente várias vezes a fim de realizar danos físicos potentes, causando uma injúria ainda mais letal.", "Curto alcance", null, null, "滅却めっきゃく", "Mekkyaku", "Aniquilação", "Destruição Total", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aparência Ilusória", "Para aplicar este genjutsu, o usuário coloca um gatilho para ativar com o alvo, que tem sua mente manipulada, causando a mudança da aparência de objetos ou aliados em sua visão. Quando a aparência transformada é hostil, o alvo provavelmente entrará em conflito com a pessoa que serviu de gatilho.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aperto Assassino", "Esta técnica utiliza fios de aço reforçados com chakra a fim de torná-lo capaz de cortar o alvo, bem como é capaz de cortar um portão de aço ou o casco de um navio com pouca dificuldade. O usuário também pode usar o fio para moldar formas como bem entender, e lançá-la como um ataque de corte poderoso.", "Todos os alcances", null, "Tigre → Javali → Macaco → Tigre", "殺め取り", "Ayametori", "Aperto Assassino", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Apodrecimento do Lobo Sábio", "O Apodrecimento do Lobo Sábio é um ninjutsu único de Saiken.", "Todos os alcances", null, null, "知狼朽", "Chirōkyū", "Apodrecimento do Lobo Sábio", "Dispersão Ácida (酸散, Sansan)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arco da Aranha: Ventania", "Enquanto em seu selo amaldiçoado nível dois e após invocar Kyodaigumo, Kidōmaru sobe em cima dela, e utiliza seu arco para disparar uma poderosa flecha em direção ao oponente, flecha está que, é muito maior do que as outras, além de que quando entra em contato com o oponente, gera uma enorme explosão de energia.", "Longo alcance", null, null, null, null, null, "Arco da Aranha: Ventania", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arco de Guerra da Aranha: Terrível Divisão", "Arco de Guerra da Aranha: Terrível Divisão é uma técnica de tiro destrutivo com arco que é o perfeito matar em um acerto, utilizado por Kidōmaru.", "Longo alcance", null, null, "蜘蛛戦弓・凄裂", "Kumo Senkyū: Suzaku", "Arco de Guerra da Aranha: Terrível Divisão", null, "Arco da Aranha: Grande Dilaceração", null);
