INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Areia de Ferro: Asas de Ferro Negro", "Esta técnica permite ao usuário a habilidade de voar, usando asas criadas a partir de Areia de Ferro. Enquanto no alto, Shinki pode disparar penas para atacar o inimigo.", "Médio a Longo alcance", null, null, "砂鉄・黒鉄乃翼さてつ・くろがねのつばさ", "Satetsu: Kurogane no Tsubasa", "Areia de Ferro: Asas de Ferro Negro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Argila Explosiva", "A Argila Explosiva (起爆粘土, Kibaku Nendo) foi desenvolvida a partir de uma técnica proibida que Deidara roubou de Iwagakure. Usando a capacidade da técnica de amassar chakra em objetos através das bocas nas palmas de suas mãos, Deidara pode infundir argila com chakra. Ele, então, molda-o em uma forma, dependendo da capacidade que ele deseja ter e cospe para fora. Depois de moldar a argila, Deidara pode ampliá-lo em vários tamanhos, dependendo da finalidade da argila. Uma vez alargada, as criações de argila pode ser trazidas à vida e controladas remotamente pelo usuário. Ele ainda pode fazer a argila explodir utilizando o selo de confronto e usualmente diz Katsu (喝) como assinatura. Embora os explosivos sejam poderosos, elas são todas as técnicas baseadas na Liberação de Terra e, como tal, podem ser neutralizadas por uma técnica de Liberação de Relâmpago. Um único aglomerado dessa argila pode explodir em uma vasta área.", "Todos os alcances", "Rank –", null, "起爆粘土きばくねんど", "Kibaku Nendo", "Argila Explosiva", "Argila Detonadora", null, "Argila Explosiva");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Argolas Elétricas", "É uma técnica exclusiva da Espada do Deus do Trovão que permite o usuário criar anéis de relâmpago em torno do inimigo.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arma Interna", "O Caminho Asura executa uma combinação de ataques, começando pela Flecha Flamejante da Incrível Habilidade e terminando com o Ataque Asura, aniquilando totalmente o oponente.", "Curto alcance", null, null, "体内兵器", "Tainai Heiki", "Arma Interna", "Armamento Interno", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arma de Xarope de Amido", "O usuário molda seu chakra, juntamente com a energia natural e, em seguida, converte-o em grande quantidade de água de alta viscosidade dentro de sua boca, antes de cuspi-la para fora com uma quantidade incrível de força. A força semelhante ao projétil da técnica combinada com o seu volume monstruoso lhe dá a capacidade de rapidamente cobrir uma vasta área.", "Todos os alcances", null, null, "水飴鉄砲みずあめてっぽう", "Mizuame Teppō", "Arma de Xarope de Amido", null, "Tiro Gosmento", null);
