INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha Esférica de Genjutsu", "Uma série de armadilhas na dimensão que interligou a Terra com a Lua. Foi criada por uma substância perigosa na forma de bolha a qual é capaz de capturar as suas vítimas num genjutsu muito poderoso produzido pelo Guardião do Portão, um caranguejo gigante que residia e protegia a entrada para a Lua.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha Fūinjutsu", "Uma vez que a técnica é ativada, o alvo é selado em um espaço dimensional diferente, no interior da localização do selamento. Antes de notar que existiam algumas maneiras de escapar do selo, Kakashi tentou quebrar o selo com seu Corte Relâmpago, mas sem sucesso. O único meio de fuga parecia ser criar o selo de sangue adequado.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha Rasteira com Fio", "Trata-se de uma armadilha em que o usuário anexa uma série de fios de aço a papéis explosivos no ambiente. Quando a vítima esbarra acidentalmente no fio, os papéis explosivos são acionados e causam uma série de explosões. O usuário ainda pode combinar essa armadilha com os Papéis da Resma Explosiva para montar a armadilha de forma mais eficaz ou mesmo combiná-la com outra armadilha para um efeito mais certeiro. Shikamaru foi capaz de combinar esta armadilha com sua Técnica do Puxão pela Sombra para prender Hidan completamente com a armadilha por todo o seu corpo.", "Todos os alcances", null, "Explodir: Carneiro[2]", null, null, null, null, null, "Armadilha Rasteira com Fio");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha com Clone", "Esta é uma armadilha alternativa desenvolvida por Hidari.", "Curto alcance", null, null, "分身トラップ­ぶんしんトラップ", "Bunshin Torappu", "Armadilha com Clone", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha da Destruição do Esconderijo", "Uma armadilha temporizada conjunta usada por Orochimaru para destruir qualquer vestígio de um velho esconderijo seu com o parecer de um vórtice gigante de lama. Ela pode ser ativada ou não, mesmo se existirem pessoas no esconderijo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha da Liberação de Vento", "Rin planta no chão um selo previamente preparado que contém uma técnica de Liberação de Vento e então pula para trás. O selo então ativa, liberando um turbilhão crescente de vento, ficando cada vez mais forte até o ápice, enfraquecendo e se dissipando. O turbilhão é forte o suficiente para jogar o oponente alto e causar certo dano.", "Curto alcance", null, null, "風遁トラップふうとんトラップ", "Fūton Torappu", "Armadilha da Liberação de Vento", "Armadilha de Estilo Vento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadilha de Genjutsu", "A Armadilha de Genjutsu trata-se de uma técnica ofensiva utilizada para impedir que a mente de determinado indivíduo seja invadida e tenha suas informações roubadas. Assim que a mente da pessoa é adentrada, o invasor cai em um genjutsu em que seu campo de visão é completamente tingido por um oceano que se propaga em vermelho sob seus pés e então, ele começa a ondular como ondas gigantes em resposta ao intruso.", null, null, null, "幻術トラップげんじゅつ トラップ", "Genjutsu Torappu", "Armadilha de Técnica Ilusória", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura Devoradora", "Log derrete sua armadura para originar um líquido preto do qual ele pode manipular livremente. Devido a isso, o líquido pode ser usado tanto para imobilizar e esmagar um inimigo ou, ainda, em sua forma mais dura, para defender o usuário.", "Curto alcance", null, null, "鎧食いよろいぐい", "Yoroigui", "Armadura Devoradora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Areia", "Usando esta técnica, Gaara pode revestir seu corpo com uma camada de areia, proporcionando uma defesa adicional caso seu Escudo de Areia falhar. Essa técnica é considerada uma defesa absoluta, quando usada em conjunto com o escudo automático. Embora bastante eficaz, manter a armadura requer uma grande quantidade de chakra e resistência. Além disso, nenhum lugar é tão resistente quanto o Escudo de Areia, uma vez que se desprende no momento do impacto, indicando que o único propósito da armadura é absorver o impacto canalizado até o corpo de Gaara durante a batalha. Outra fraqueza é o peso adicional da areia o que leva a uma diminuição dos níveis de velocidade e mobilidade.", "Curto alcance", "Rank –", null, "砂の鎧すなのよろい", "Suna no Yoroi", "Armadura de Areia", null, null, "Armadura Arenosa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Chakra de Bando", "Bando libera uma quantidade enorme de chakra por todo seu corpo, criando uma armadura de chakra impenetrável que resiste por bom tempo a ataques. Todos os danos graves que ele receber nesse tempo, serão drasticamente diminuídos em uma escala assustadora, assim como todos os seus ataques a partir desse momento passarem a receber mais força destrutiva.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Cristal", "Esta técnica de Guren é capaz de envolver seu corpo com uma fina camada de cristal que aprimora sua resistência contra ataques físicos. Além disso, esta armadura concede ataques físicos mais potentes a Guren. O cristal é invisível ao olho nu, sendo visto somente quando a luz é refletida.", "Curto alcance", null, null, "結晶の鎧けっしょうのよろい", "Kesshō no Yoroi", "Armadura de Cristal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Ouro Pegajoso", "Excretando o seu Ouro Pegajoso da Aranha de seus poros, Kidōmaru pode criar rapidamente uma armadura temporária que é capaz de bloquear chakra que endurece quando exposta ao oxigênio, podendo até mesmo bloquear o Punho Gentil do Clã Hyūga.", "Curto alcance", null, null, "粘金の鎧", "Nenkin no Yoroi", "Armadura de Ouro Pegajoso", null, "Armadura de Metal Viscoso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Rocha", "Esta é uma técnica de armadura usada por Fudō a fim de aprimorar suas capacidades físicas.", "Curto alcance", null, null, "岩の鎧いわのよろい", "Iwa no Yoroi", "Armadura de Rocha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura do Ataque Relâmpago", "A Armadura do Ataque Relâmpago permite que seu usuário acumule chakra e o libere na forma de uma potente descarga elétrica que envolve todo o seu corpo. Isso garante um aumento de velocidade ao usuário e o protege, eletrocutando quem faz contato. Esta armadura parece ser muito forte, pois é capaz de fazer frente ao Punho Embriagado de Rock Lee.", null, null, "Carneiro", "雷撃の鎧らいげきのよろい", "Raigeki no Yoroi", "Armadura do Ataque Relâmpago", null, null, "Arte Ninja: Armadura Relâmpago");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arranque a Vapor", "Depois de aumentar a temperatura de seu chakra ao ponto de ebulição, o corpo de Han libera vapor incessantemente para arrancar contra o oponente.", "Curto alcance", null, null, "蒸気驀進じょうきばくしん", "Jōki Bakushin", "Arranque a Vapor", null, null, "Corrida a Vapor");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arremesso de Fio", "O usuário faz com que a marionete, com o cordão de ponta afiada instalado, desenrole ele de sua barriga para diversos fins. O cordão serve para perfurar o oponente, amarrá-lo, cortar Fios de Chakra ou como um impulsionador de voo ao se puxar através dele. A fim de aumentar o potencial da técnica, o usuário pode adicionar um Veneno Mortal para caso ele erre, mas raspe no corpo do inimigo. Além disso, o cordão serve como condutor de eletricidade para manter um prisioneiro.", "Longo alcance", null, null, "ワイヤー射出", "Waiyā Shashutsu", "Arremesso de Fio", "Ejetor de Fio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Final", "Enquanto anda com o Dragão C2, Deidara cria um Clone de Argila e derruba sobre o seu oponente lá do alto, explodindo em seguida e resultando em uma enorme explosão.", "Longo alcance", null, null, "究極芸術", "Kyūkyoku Geijutsu", "Arte Final", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja Canina: Clones Caninos", "Usando o cabelo que ele lançou, Pakkun cria dezenas de clones de si. Estes clones são capazes de flutuar no ar, de uma forma semelhante que o cabelo é capaz de permanecer no ar.", null, null, null, "忍犬忍法・猟犬分身", "Ninken Ninpō: Ryōken Bunshin", "Arte Ninja Canina: Clones Caninos", null, null, "Arte Ninja Canina: Clones do Cabelo Canino");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja Canina: Dança da Nuvem de Poeira", "Com a Arte Ninja Canina: Dança da Nuvem de Poeira, o usuário, usando suas patas, cria uma nuvem de poeira que pode ser usada para esconder seus movimentos. Também pode ser usada para criar buracos em um curto período de tempo.", null, null, null, "忍犬忍法・砂塵の舞", "Ninken Ninpō: Sajin no Mai", "Arte Ninja Canina: Dança da Nuvem de Poeira", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja Magnética: Cintilação Eletromagnética", "Usando seus poderes magnéticos, Jiga é capaz de se mover em alta velocidade, deixando uma imagem residual para confundir seus oponentes.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja Magnética: Infinitos Meteoros", "Jiga usa essa técnica, após magnetizar as paredes de rocha de um poço profundo, para dar-lhe a capacidade de atacar um alvo com muitas pedras. Uma vez magnetizado, Jiga pode manipular completamente a forma das rochas a seu critério. Ele também fez uso desta técnica, a fim de enterrar seu irmão Ruiga ainda vivo.", null, null, null, "磁石忍法・無限隕石", "Jishaku Ninpō: Mugen Inseki", "Arte Magnética: Infinitos Meteoros", null, null, "Arte Magnética Ninja: Infinitos Meteoros");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja Magnética: Visão Eletromagnética", "Esta técnica desencadeia uma onda de areia de ferro, que para as ondas cerebrais de alguém que é pego no ataque. Ao interromper as ondas elétricas do cérebro, o adversário fica incapaz de pensar ou se mover.", "Curto a Médio alcance", null, "Tigre → Selo especifico da técnica", "磁石忍法・電磁幻夢", "Jishaku Ninpō: Denji Genmu", "Arte Magnética: Visão Eletromagnética", null, null, "Arte Ninja dos Imãs: Ilusão Eletromagnética");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Beleza do Luar", "Ino inicia a técnica saltando em frente a lua, em seguida, ela libera e controla várias pétalas, assim ela cega, cerca e confunde o seu adversário com elas. Finalmente, Ino inicia seu declínio e instantaneamente ela cai com os dois pés em cima do rosto do inimigo com uma imensa fúria, dizimando o inimigo e constituindo uma imensa cratera.", "Curto alcance", null, "Cobra → Tigre", "忍法・月下美人にんぽう・げっかびじん", "Ninpō: Gekka Bijin", "Arte Ninja: Beleza do Luar", "Arte Ninja: Rainha da Noite", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Capa de Gato", "Wasabi coloca seu capuz e desenrola um pergaminho, o qual ela ativa. A garota então é envolvida em chakra, que lhe dá características semelhantes às dos gatos, tal como cauda, orelhas e olhos fendidos, bem como unhas estendidas. A técnica pode selecionar uma de muitas espécies diferentes de gatos, garantindo para ela os atributos do gato selecionado. Esta técnica possui um custo de chakra muito alto, visto que Wasabi não pode mantê-la por muito tempo.", null, null, null, "忍法・猫被りにんぽう・ねこかぶり", "Ninpō: Nekokaburi", "Arte Ninja: Capa de Gato", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Casulo", "Com a ajuda dos Fios de Chakra de Chōmei, o usuário cria um casulo que permite suprimir a absorção de chakra de fontes externas, e promover uma defesa adicional ao mesmo tempo. O casulo é extremamente duro, e só pode ser perfurado por uma lâmina de chakra em um ponto fraco específico.", "Curto alcance", null, "Selo do Confronto", "忍法・コクーン", "Ninpō: Kokūn", "Arte Ninja: Casulo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Dança da Neve da Flor de Cerejeira", "Sakura conecta-se com seu oponente através de um genjutsu e começa à lançar quatro pequenas bolsas brancas com papéis explosivos, que estão disfarçados de minúsculos pedaços de papeis brancos aos olhos humanos. A bolsa abre-se sozinha no ar, e Sakura joga diversas kunai em direção as bolsas, onde os papéis explosivos começam à explodir e cair no oponente. Por último, Sakura conecta uma kunai unida por um papel explosivo nos pés de seu oponente, detonando-o e, em seguida, os outros selos que não haviam caído por total das bolsas começam a cair semelhante à uma chuva no oponente. Após a conclusão da técnica, é capaz ver o oponente gravemente ferido ou morto.", "Curto a Médio alcance", null, null, "忍法・雪桜の舞にんぽう・ゆきさくらのまい", "Ninpō: Yuki Sakura no Mai", "Arte Ninja: Dança da Neve da Flor de Cerejeira", "Arte Ninja: Dança de Neve da Sakura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Energia Eólica da Foice da Doninha", "Temari usa seu leque e atinge o solo, criando um turbilhão e coletando algumas rochas. O turbilhão lança seu oponente para muito longe além do ar, onde são atingidos tanto pelo corte de vento como as rochas.", "Longo alcance", null, null, "忍法・カマイタチ最大風力", "Ninpō: Kamaitachi Saidai Fūryoku", "Arte Ninja: Energia Eólica da Foice da Doninha", "Velocidade Máxima da Foice de Vento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Estrutura de Muco Nasal", "Este é um estilo de luta utilizado por Udon Ise com propriedades de Liberação de Água, permitindo-lhe controlar seu catarro da forma que preferir.", "Curto e Médio alcance", null, null, "忍法・ハナミズキにんぽう・はなみずき", "Ninpō: Hanamizuki", "Arte Ninja: Estrutura de Muco Nasal", null, null, "Arte Ninja: Nariz Escorrendo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Formação do Majestoso Ciclo Sexagenário de Genbu: Estilo de Número 108: Liberar", null, "Curto alcance", null, "Tigre[1]", "忍法・玄武六甲覇王陣・第百八式 改", "Ninpō: Genbu Rokkō Haōjin: Dai Hyakuhashiki — Kai", "Arte Ninja: Formação do Majestoso Ciclo Sexagenário de Genbu: Estilo de Número 108: Liberar", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Furacão", "Temari salta e balança seu Leque Dobradiço Gigante para criar duas poderosas rajadas de vento que vão em direção ao oponente, causando danos graves no oponente.", "Longo alcance", null, null, "忍法・旋風", "Ninpō: Senpū", "Arte Ninja: Redemoinho", "Arte Ninja: Furacão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Palma do Dragão Feroz", "Rokkaku Ryūdōin invoca um dragão feito de chakra para atacar o inimigo, causando grandes danos ao oponente. Este ataque também pode reduzir a defesa dos alvos.", "Médio alcance", null, null, "忍法・激龍掌", "Ninpō: Gekiryūshō", "Arte Ninja: Palma do Dragão Feroz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Profusão de Dez Mil Flores", "Ino saca dois maços uma espécie de flor venenosa, pula no ar e, em seguida, gira elegantemente, lançando uma enxurrada de flores roxas venenosas no oponente. As flores explodem no momento do impacto, lançando uma chuva de névoa venenosa e pétalas roxas.", "Médio a Longo alcance", null, null, "忍法・万花繚乱", "Ninpō: Manka Ryōran", "Arte Ninja: Profusão de Dez Mil Flores", "Arte Ninja: Inundado com Flores", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Profusão de Flores", "Ino lança uma ilusão que engana o inimigo, fazendo com que eles vejam uma tempestade de flores. Esta técnica tem diferentes variações que vão desde um campo com pétalas flutuantes a um estouro de pétalas que cercam o inimigo. Uma vez que Ino tem uma chance clara, ela lança a flor acônito venenosa contra o inimigo batendo-lhe na cabeça do oponente.", "Curto a Médio alcance", null, "Cachorro → Selo especifico da técnica → Tigre", "忍法・百花繚乱", "Ninpō: Hyakka Ryōran", "Arte Ninja: Profusão de Flores", "Arte Ninja: Coberta com Flores", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Roda da Flor de Lua Nova", "Ino gira um maço de flores da lua no ar que se transforma em uma lâmina circular, semelhante a um chakram. Ela, então, joga a lâmina no oponente, cortando qualquer coisa em seu caminho. Uma vez que chega no adversário, ela corta através dele e explode em pétalas de flor da lua.", "Longo alcance", null, null, "忍法・新月一輪", "Ninpō: Shingetsu Ichirin", "Arte Ninja: Roda da Flor de Lua Nova", "Arte Ninja: Corte da Flor de Lua", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Técnica da Fantasia", "Esta é uma habilidade geral em que um ninja disfarça-se com um traje de fantasia para enganar o oponente. Ela pode ser usada em conjunto com a Técnica de Transformação, para um efeito mais realista ou mesmo em conjunto com alguma técnica de confusão visual, como convocar gaivotas e outros pássaros. O usuário ainda pode adaptar sua voz para confundir o usuário com essa técnica. Esta habilidade simples, embora pareça inútil devido à Técnica de Transformação, mostrou-se útil, visto que o ninja não faz uso de chakra para se disfarçar.", null, null, null, "忍法・着ぐるみの術にんぽう・きぐるみのじゅつ", "Ninpō: Kigurumi no Jutsu", "Arte Ninja: Técnica da Fantasia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Técnica da Passagem Através da Parede", "A fim de tentar impressionar Tenten, Lee desenvolveu esta arte ninja falsa para lhe permitir atravessar paredes e entrar em uma sala. Lee usou este movimento extremista para mover-se de um lado para o outro em um local e, em seguida, abrir a porta do mesmo.", null, null, "Carneiro", "忍法・壁抜けの術", "Ninpō: Kabenuke no Jutsu", "Arte Ninja: Técnica da Passagem Através da Parede", null, null, "Arte Ninja: Jutsu Fantasma");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Técnica do Corpo da Shuriken Gigante", "O usuário lança uma Fūma Shuriken em direção ao oponente e usa seu chakra para ampliá-la maciçamente, brutalmente golpeando o oponente e enviando-o para voar.", "Curto a Médio alcance", null, "Tigre", "忍法・手裏剣巨身の術", "Ninpō: Shuriken Goshin no Jutsu", "Arte Ninja: Técnica do Corpo da Shuriken Gigante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja: Verdadeira Captura de Espada com as Mãos Nuas", "Com as mãos nuas, é possível parar o ataque de uma espada através do atrito com o lados da lâmina, unindo ambas as mãos com a espada entre elas. O usuário ainda pode utilizar mãos criadas por outras técnicas ou de marionetes para executá-la.", "Curto alcance", null, null, "忍法・真剣白刃取りにんぽう・しんけんしらはどり", "Ninpō: Shinken Shirahadori", "Arte Ninja: Verdadeira Captura de Espada com as Mãos Nuas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena", "A Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena é uma Técnica Proibida acessível somente aos usuários da técnica da Criação do Renascimento.", null, "Rank S", "Tigre[2]", "忍法創造再生 百豪の術", "Ninpō Sōzō Saisei — Byakugō no Jutsu", "Arte Ninja da Criação do Renascimento — Técnica da Força de Uma Centena", null, "Mitótica Regeneração: Uma Centena de Curas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja da Espada Longa: Costura da Aranha da Terra", "Depois de preparar o terreno com o longo fio de aço anexado na Nuibari, o usuário executa o selo de mão necessário, enterra a espada no solo e puxa o fio, que pressiona o membro do oponente que está na área entre os nós do fio. A pressão feita pelo fio é tão grande que é capaz de portar músculo e ossos facilmente. A força exercida pelo fio pode ser controlada pelo usuário através da aplicação de uma força adicional.", "Todos os alcances", null, "Cobra", "長刀忍法・地蜘蛛縫いちょうとうにんぽう・じぐもぬい", "Chōtō Ninpō: Jigumo Nui", "Arte Ninja da Espada Longa: Costura da Aranha da Terra", null, null, "Arte Ninja da Espada Longa: Costura da Aranha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja da Espada Longa: Crucificação de Arame", "O usuário lança a Nuibari a fim de perfurar e prender seus oponentes com os fios de aço de uma forma a deixar seus corpos como se eles estivessem crucificados. O golpe final é dado ao perfurá-los com Nuibari, deixando-os para uma morte lenta e dolorosa.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja da Imitação", null, null, null, "Tigre", "擬忍法", "Gi Ninpō", "Arte Ninja da Imitação", null, null, "Arte Ninja de Mimetismo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja de Flor", "A Arte Ninja de Flor (花忍法, Hana Ninpō) é um subconjunto do ninjutsu e genjutsu envolvendo técnicas secretas orientados de flor usadas por um clã no País dos Vegetais. Os dois usuários conhecidos da Arte Ninja de Flor são Kikunojō e Yurinojō.", "Curto a Médio alcance", null, null, "花忍法はなにんぽう", "Hana Ninpō", "Arte Ninja de Flor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja de Flor: Desabrochar das Cem Flores", "Esta técnica transforma qualquer flor, pétala ou broto ao redor em bombas que podem ser detonadas como papéis explosivos.", "Curto a Médio alcance", null, null, "花忍法・百花斉放はなにんぽう・ひゃっかさいほう", "Hana Ninpõ: Hyakkasaihō", "Arte Ninja das Flores: Desabrochar das Cem Flores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja de Flor: Espelho da Flor da Água", "O usuário cria uma ilusão que envolve o alvo em pétalas de flores, escurecendo sua visão e incapacitando-o de moldar seu chakra, durante a ilusão o ninja cria pétalas reais para envolverem seu oponente, fazendo com que ao se libertar do genjutsu ele tenha a impressão que falhou e ainda está preso.", "Curto alcance", null, null, "花忍法・鏡花水はなにんぽう・きょうかすい", "Hana Ninpō: Kyōkasui", "Arte Ninja de Flor: Espelho da Flor da Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja de Flor: Liberação de Muitas Flores", "Após liberar flores e pétalas ao ar o usuário encurrala seu adversário com elas, em seguida se inflama as flores. Estas chamas, em seguida, atiram-se em direção ao alvo e explodem podendo mudar sua direção durante o voo.", "Curto a Médio alcance", null, null, "花忍法・百花繚乱はなにんぽう・ひゃっこりょうらん", "Hana Ninpō: Hyakkoryōran", "Arte Ninja de Flor: Liberação de Muitas Flores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Ninja de Kagerō: Efêmero", "Kagerō forma quatro asas de chakra para desencadear poderosas explosões de energia, além de também conceder-lhe a capacidade de teletransporte instantâneo. Esta técnica só pode ser usada apenas uma vez na vida, pois a quantidade de chakra usada é enorme, sendo assim, o usuário fica, em estado crítico, à beira da morte.", "Todos os alcances", null, "Serpente → Passaro → Macaco → Boi", "蜉蝣忍法・泡沫カゲロウにんぽう・うたかた", "Kagerō Ninpō: Utakata", "Arte Ninja de Kagerō: Efêmero", null, null, "Arte Ninja Kagero: Efêmera");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Secreta da Técnica Branca Secreta: Coleção de Ilusões", "Depois de tomar o controle de vários corpos ao mesmo tempo, o usuário deixa-os atacar em todo um campo de batalha, resultando em impactos constantes e devastadores. Essa técnica se torna muito útil em combate contra vários inimigos, considerando que os inimigos abaixarão sua guarda em favor da vida do companheiro controlado.", "Todos os alcances", null, null, "白秘技奥義・夢幻の集", "Shirohigi Ōgi: Mugen no Shū", "Arte Secreta Técnica Secreta Branca: Coleção de Ilusões", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Cadeia de Trovão do Lado da Prisão", "Madara posiciona suas sombras mantidas numa formação quadrática e cada uma cria um feixe de relâmpago, indo de sombra a sombra, a qual se aproxima do oponente em meio a formação.", "Curto a médio alcance", null, null, null, null, "Arte Sábia: Cadeia de Trovão do Lado da Prisão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Chamada do Sapo", "Depois de focar o chakra do Senjutsu para suas gargantas, ambos Fukasaku e Shima coaxam juntos em uníssono, liberando ondas de som grandes, altas e imensamente poderosas que efetivamente irritam, distraem e paralisam temporariamente todos os alvos atingidos completamente.", "Curto a Médio alcance", "Rank –", "Bater as maos", "仙法・蛙鳴きせんぽう・かわずなき", "Senpō: Kawazu Naki", "Arte Sábia: Chamada do Sapo", null, "Arte Eremita: O Lamento do Bratáquio", "Arte Sábia: Canção do Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Despacho Relâmpago da Liberação de Yin", "A Arte Sábia: Despacho Relâmpago da Liberação de Yin é um ninjutsu utilizado por Madara Uchiha, após a transformação em Sábio dos Seis Caminhos.", "Curto a Médio alcance", null, "Carneiro Espelhado", "仙法・陰遁雷派せんぽう・いんとんらいは", "Senpō: Inton Raiha", "Arte Sábia: Despacho Relâmpago da Liberação de Yin", "Estilo Oculto: Onda do Trovão", "Arte Eremita: Estilo Oculto: Onda do Trovão", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Feixe de Luz da Liberação de Tempestade", "Após combinar a Liberação de Tempestade com a energia natural, o usuário emite um feixe de luz para bifurcar o inimigo, capaz, inclusive, de cortar a Esfera da Busca da Verdade. Uma vez que a técnica se propaga na velocidade da luz, torna-se quase impossível de desviar.", "Curto a Médio alcance", null, null, "仙法・嵐遁光牙せんぽう・らんとんこうが", "Senpō: Ranton Kōga", "Arte Sábia: Feixe de Luz da Liberação de Tempestade", null, "Arte Eremita: Estilo da Tempestade: Presa de Luz", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Goemon", "Trata-se de uma técnica colaborativa usada por Jiraiya com Shima e Fukasaku.", "Todos os alcances", "Rank –", null, "仙法・五右衛門せんぽう・ごえもん", "Senpō: Goemon", "Arte Sábia: Goemon", null, "Arte Eremita: Execução em Óleo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Grande Bola Rasengan", "Esta é a versão do Modo Sábio da Grande Bola da Esfera Espiral. A técnica comum requer que Naruto faça um clone das sombras para formar o ataque e, em seguida, empunhá-lo. Ao usar a sua variante no Modo Sábio no entanto, ele é capaz de usar dois clones para formar o ataque em ambas as mãos e, em seguida, dar à Grande Bola da Esfera Espiral para os clones de empunharem por si mesmos. Também é maior do que a sua técnica mãe. Esta técnica foi capaz de explodir nas invocações de Pain no alto.", "Curto alcance", null, null, "仙法・大玉螺旋丸", "Senpō: Ōdama Rasengan", "Arte Eremita: Grande Bola da Esfera Espiral", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Liberação de Madeira: Desempenho da Dança do Dragão de Madeira", "Hashirama cria uma gigantesca estátua de madeira que faz com que um dragão de madeira entre em erupção a partir do solo e ataque seu oponente.", "Todos os alcances", null, null, "仙法・木遁・木龍演舞", "Senpō: Mokuton: Mokuryū Enbu", "Arte Sábia: Liberação de Madeira: Desempenho da Dança do Dragão de Madeira", "Arte Sábia: Estilo Madeira: Dança do Dragão de Madeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Liberação de Madeira: Pulso do Solo", "A estátua gigante de Hashirama encosta um dedo no chão, causando uma erupção de madeira pelo chão, que ataca seu oponente.", "Médio a Longo alcance", null, null, "仙法・木遁・大地脈導", "Senpō: Mokuton: Daichi Myakudō", "Arte Sábia: Liberação de Madeira: Pulso do Solo", "Arte Sábia: Estilo Madeira: Pulso do Solo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Nuvem de Poeira da Liberação de Vento", "Shima exala uma nuvem enorme de poeira de sua boca, que é grossa o suficiente para que ela seja capaz de bloquear a visão do alvo completamente. A nuvem de poeira também é grande o suficiente para esconder uma invocação tão grande como Gamabunta.", null, null, "Bater as maos", "仙法・風遁砂埃", "Senpō: Fūton Sunabokori", "Arte Sábia: Nuvem de Poeira da Liberação de Vento", null, null, "Arte Sábia: Estilo Vento: Poeira de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Portão do Grande Deus", "É uma técnica de selamento que é usada no Modo Sábio, o usuário manifesta um número seleto de maciços torii vermelhos que caem de cima para baixo, a fim de fixar um alvo pretendido entre o portão e o chão. O tamanho e a força desses torii é tal que até mesmo pode ser usado para imobilizar o Dez-Caudas. Estes torii são bastante versáteis e podem ser utilizados em várias formas diferentes, este foi exibido após Obito Uchiha tornar-se jinchūriki da besta, Hashirama empilhou os torii uns sobre os outros, numa tentativa de aumentar a sua capacidade para imobilizar o alvo.", "Longo alcance", "Rank –", "Carneiro", "仙法・明神門せんぽう・みょうじんもん", "Senpō: Myōjinmon", "Arte Sábia: Portão do Grande Deus", "Arte Sábia: Portal Divino", "Arte Eremita: Portão Divino", null);
