INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Rasenshuriken de Liberação de Lava", null, "Todos os alcances", null, null, "仙法・熔遁螺旋手裏剣せんぽう・ようとんらせんしゅりけん", "Senpō: Yōton Rasenshuriken", "Arte Sábia: Rasenshuriken de Liberação de Lava", null, "Arte Eremita: Estilo da Lava: Rasen Shuriken", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Reencarnação Inorgânica", "Uma técnica capaz de injetar vida em objetos inorgânicos, através da energia natural presente na atmosfera. Devido à manipulação de energia natural, este poder só pode ser utilizado por usuários do Modo Sábio.", "Curto a Médio alcance", "Rank –", "Boi", "仙法・無機転生せんぽう・むきてんせい", "Senpō: Muki Tensei", "Arte Sábia: Reencarnação Inorgânica", "Arte Sábia: Reanimação Inorgânica", "Arte Sábia: Animação Inorgânica", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Super Rasenshuriken das Bestas com Cauda", "Esta técnica foi utilizada por Naruto após ele receber o poder do Sábio dos Seis Caminhos. Juntamente com oito clones das sombras, Naruto cria nove Rasenshuriken, cada um infundido com as propriedades especiais do chakra de uma Besta com Cauda específica. Depois de lançar todos eles, eles criam várias e grandes explosões. Ele é descrito como 'o ninjutsu mais maravilhoso de todos os tempos' (空前絶後の忍術, Kūzenzetsugo no Ninjutsu).", "Todos os alcances", null, null, "仙法・超尾獣螺旋手裏剣せんほう・ちょうおじゅうらせんしゅりけん", "Senpō: Chō Bijū Rasenshuriken", "Arte Sábia: Super Shuriken Espiral das Bestas com Cauda", "Super Besta de Cauda Rasen Shuriken", "Arte Eremita: Super Shuriken Espiral das Bestas com Cauda", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Série Contínuas de Ultra-Grandes Esferas Espirais", "Depois de criar vários clones das sombras com cada um deles segurando um Rasengan, Naruto então entra no Modo Sábio. Os clones, em seguida, expandem o seu Rasengan em Arte Sábia: Ultra-Grande Bola Rasengan e, simultaneamente, atacam seu inimigo, resultando em uma enorme quantidade de danos, que atordoou até mesmo Kurama.", "Curto alcance", null, null, "仙法・超大玉螺旋多連丸", "Senpō: Chōōdama Rasen Tarengan", "Arte Sábia: Série Contínuas de Ultra-Grandes Esferas Espirais", "Arte Sábia: Mega Barragem de Rasengan Massivos", "Estilo do Sennin: Super-Esferas Múltiplas de Espiral Gigante", "Arte Sábia: Barragem de Rasengan Massivos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Técnica Anfíbia", "A Arte Sábia: Técnica Anfíbia é uma técnica usada por Shima e Fukasaku para se fundir com Jiraiya. Ela maximiza o chakra sábio para a pessoa que eles são fundidos e lhes permite acessar Modo Sábio. Isso também permite que Fukasaku e Shima continuamente reúnam a energia natural para Jiraiya, como ele não pode fazê-lo a si mesmo enquanto estiver em movimento, eliminando assim a fraqueza de esgotar toda a sua energia natural na batalha. Fukasaku e Shima não foram capazes de fundir com Naruto Uzumaki, porque Kurama os rejeitou.", null, null, null, "仙法・両生の術", "Senpō: Ryōsei no Jutsu", "Arte Sábia: Técnica Anfíbia", null, "Estilo Sennin: Jutsu do Anfíbio", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Técnica Branca Violenta", "Depois de formar os selos de mão necessários, o usuário expele uma entidade como dragão de sua boca que carrega uma esfera em sua pata dianteira esquerda. Como o dragão firmemente bobina-se em todo o orbe, ele libera tanto a luz ofuscante e som ensurdecedor. Isto por si só dificulta enormemente aqueles na proximidade, mas, devido à intensidade das vibrações resultantes, a dor paralisante também é induzida. Assim, a técnica tanto brevemente inibe os sentidos do oponente, bem como técnicas que exigem a sua concentração.", "Curto a Médio alcance", "Rank –", "Selo especifico técnica → Rato → Passaro", "仙法・白激の術", "Senpō: Hakugeki no Jutsu", "Arte Sábia: Técnica Branca Violenta", "Arte Sábia: Ataque Branco Extremo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Técnica da Disseminação Raivosa", "Kabuto expele inúmeros dragões brancos — os quais carregam uma esfera roxa em sua pata dianteira esquerda — que atacam o inimigo, bobinando-se em volta da esfera e gerando fortes luzes que cegam o adversário.", "Curto a Médio alcance", null, null, "仙法・散激の術", "Senpō: Sangeki no Jutsu", "Arte Sábia: Técnica da Disseminação Raivosa", "Técnica Eremita: Jutsu da Fúria Dispersa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia: Ultra-Grande Bola Rasengan", "A Arte Sábia: Ultra-Grande Esfera Espiral é um Rasengan gigantesco criado por Naruto Uzumaki, reforçado com chakra sábio. Embora seus efeitos destrutivos não foram vistos, foi usado para distrair Kurama. Presumivelmente, uma vez que é utilizado no Modo Sábio, é ainda mais forte do que a outra versão, que foi dita sendo capaz de esculpir uma montanha se explodida.", "Curto alcance", null, null, "仙法・超大玉螺旋丸", "Senpō: Chō Ōdama Rasengan", "Arte Sábia: Ultra-Grande Esfera Espiral", null, "Estilo do Sennin: Super-Esfera de Espiral Gigante", "Arte Sábia: Super Rasengan Gigante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras", "A Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras é uma técnica que cria uma estátua de madeira titânica de muitas mãos, que pode ser controlada livremente pelo usuário.", "Curto a Médio alcance", "Rank –", null, "仙法木遁・真数千手せんぽうもくとん・しんすうせんじゅ", "Senpō Mokuton: Shin Sūsenju", "Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras", "Arte Sábia: Estilo Madeira: Mil Mãos Verdadeiras", "Arte Eremita: Estilo da Madeira: As Verdadeiras Mil Mãos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte da Espada da Névoa Sangrenta: Mutilação de Ossos", "Usando a Hiramekarei, o usuário cria uma grande quantidade de agulhas de chakra azul claro para atacar o inimigo. Uma vez empalado, as agulhas causam dano no Sistema de Circulação de Chakra, enquanto é restringido pouco a pouco de acordo com o selo de mão do usuário. Em vez de receber uma morte rápida, o inimigo sofre lentamente.", "Curto a Médio alcance", null, "Restringir: Rato", "血霧刀法・骨抜きちぎりとうほう・ほねぬき", "Chigiri Tōhō: Honenuki", "Arte da Espada da Névoa Sangrenta: Mutilação de Ossos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte da Matança Combinada", "Usando sua argila, Deidara cria várias aranhas explosivas e as joga no ar, em seguida, Sasori usa sua marionete do Terceiro Kazekage para criar estilhaços de Areia de Ferro em forma de lâminas que transportam as aranhas até o oponente. O oponente é forçado contra o chão onde as aranhas detonam. A explosão é tão poderosa que Sasori usa sua marionete do Terceiro Kazekage para criar uma barreira de areia de ferro para defender-se.", "Todos os alcances", null, null, "合殺芸術がっさいげいじゅつ", "Gassai Geijutsu", "Arte da Matança Combinada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte do Punho Gentil Golpe de Um Corpo", "Uma técnica nascida a partir de sua habilidade inata de expelir chakra de cada tenketsu em seu corpo, um membro do clã Hyūga pode bater seu oponente com uma rajada de chakra que irá enviá-lo voando para longe do usuário. Mostrou-se também que esta técnica pode ser utilizada com precisão concentrada para atingir o ponto fraco de uma técnica, exemplificado quando Neji Hyūga foi preso pela Técnica da Prisão de Água de Kisame Hoshigaki, e foi capaz de dissipá-la.", "Curto alcance", null, null, "柔拳法一撃身じゅうけんほういちげきしん", "Jūkenhō Ichigekishin", "Arte do Punho Gentil Golpe de Um Corpo", null, "Arte do Punho Leve Impacto Corporal", "Arte do Punho Gentil: Golpe do Corpo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arte do Punho Gentil: Palma Rotatória de Vácuo", "Hanabi realiza um movimento rotatório que atinge o oponente e o arremessa para o alto ou para longe.", "Curto alcance", null, null, "柔拳法·空転掌", "Jūkenpō: Kūtenshō", "Arte do Punho Gentil: Palma Rotatória de Vácuo", "Punho Gentil: Palma Aérea Rotatória", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Artimanha de Entrada Impulsiva", "Esta é uma formação usada para forçar a entrada em um local protegido, mantendo ao menos uma pessoa de cada lado da entrada bloqueada, um atacante e um protetor. Dessa forma, é possível quebrar as defesas inimigas e ainda se precaver de possíveis ataques de dentro do local.", "Médio alcance", null, null, "ボタンフックエントリー", "Botan Fukku Entorī", "Artimanha de Entrada Impulsiva", null, null, "Operação Entrada em Gancho");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Aríete de Lodo Ultra-Viscoso", "Hiruko usa um líquido viscoso estranho ao seu redor durante o ritual de imortalidade do eclipse. Ele é capaz de manipular a viscosidade do líquido, de modo que ele pode controlá-lo. O movimento do líquido é similar a uma mola de metal. Esses pedaços de líquido podem acertar um inimigo com força suficiente para derrubá-lo temporariamente.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Asa Decapitadora", "Usando as asas de Chōmei, Fū envia uma onda de choque contra seu oponente, cortando seu corpo em pedaços.", "Curto à Médio alcance", null, null, "翅斬り", "Hanegiri", "Asa Decapitadora", "Corte no Ventilador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Asa Rotativa", "Transformada em Chōmei, Fū gira com a cauda sem-asa, criando e direcionando um pequeno tornado até seu oponente.", "Médio a Longo alcance", null, null, "翅旋", "Hanetsumuji", "Asa Rotativa", "Lâminas Rotativas", null, null);
