INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Asas Vibrantes Brilhantes", "Transformada em Chōmei, Fū desliza até seu oponente com sua cauda sem-asa, seguido por um ataque de chifre, e terminando com um deslize de todo seu abdômen.", "Curto alcance", null, null, "輝々翻翅", "Kiki Honshi", "Asas Vibrantes Brilhantes", "Lâmina Ofuscante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ascensão Destruidora de Rocha da Folha", "Uma técnica de taijutsu onde o usuário esmaga com seu cotovelo seu oponente usando o outro braço como apoio. Trata-se de um poderoso golpe no alvo e como o nome sugere, é ainda forte o suficiente para destruir coral.", "Curto alcance", null, null, "木ノ葉壊岩升このはかいがんしょう", "Konoha Kaiganshō", "Ascensão Destruidora de Rocha da Folha", null, "Quebra-Rocha da Folha", "Esmagamento de Rocha da Folha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ascensão dos Dragões Gêmeos", "A única técnica de Tenten que tira proveito de suas habilidades superiores com armas. Primeiro, ela coloca dois pergaminhos pequenos em uma posição vertical no chão. Quando ativados, os dois pergaminhos são liberados, enquanto voam e giram ao ar com a forma de dois dragões feitos de fumaça. Tenten então salta entre os dois pergaminhos, e começa a convocar e lançar rapidamente todos os tipos de armas como uma barragem poderosa. Se a barragem inicial vier a falhar, ela pode controlar as armas descartadas com fios ligados à ponta dos seus dedos para um segundo ataque surpresa. No entanto, por as armas serem jogadas, elas são facilmente contrariadas por técnicas de vento.", "Médio alcance", null, "Tigre → Dragao → Macaco → Coelho → Serpente", "双昇龍そうしょうりゅう", "Sōshōryū", "Ascensão dos Dragões Gêmeos", "Ferramenta Ninja: Lâmina de 1000 Greves, Arma de controle: Tensasai", null, "Dragões Gêmeos Ascendentes; Controle do Dragão Nascente");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assalto da Aglomeração de Areia de Ferro", "O Assalto da Aglomeração de Areia de Ferro é uma técnica que reúne um grande volume de areia de ferro e comprime-o em uma forma de alta densidade.", "Todos os alcances", "Rank –", null, "砂鉄結襲さてつけっしゅう", "Satetsu Kesshū", "Ataque da Aglomeração da Areia da Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assalto da Aglomeração de Areia de Ferro: Cinco Unhas Solares", "Sasori manipula a marionete humana do Terceiro Kazekage para liberar areia de ferro e formá-la em múltiplas lanças longas para cercar e atacar o oponente, tornando a chance de escapar difícil.", "Todos os alcances", null, null, "砂鉄結襲・五寸釘さてつけっしゅう・ごすんくぎ", "Satetsu Kesshū: Gosunkugi", "Assalto da Aglomeração de Ferro da Areia: Cinco Unhas Solares", "Ataque da Aglomeração de Areia de Ferro: Seis Polegadas Solares", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assalto da Aglomeração de Areia de Ferro: Martelo", "Sasori usa sua marionete humana do Terceiro Kazekage para manipular uma grande quantidade de areia de ferro em um grande bloco, o qual ele pode usar para esmagar seu oponente.", "Todos os alcances", null, null, "砂鉄結襲・鎚さてつけっしゅう・つち", "Satetsu Kesshū: Tsuchi", "Assalto da Aglomeração de Ferro da Areia: Martelo", "Aglomeração de Areia de Ferro: Clava", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assalto da Aglomeração de Areia de Ferro: Queda", "Depois de formar a areia de ferro em um bloco gigante, o usuário eleva-o acima do oponente e deixa-o cair sobre ele. Devido a ser um objeto pesado, a gravidade propicia um puxão mais forte nele, fazendo com que o tempo que isso leva para chegar ao chão seja muito rápido e com uma grande força por trás dele, tornando-se difícil escapar.", "Curto alcance", null, null, "砂鉄結襲・落さてつけっしゅう・らく", "Satetsu Kesshū: Raku", "Assalto da Aglomeração de Areia de Ferro: Queda", "Areia de Ferro em Massa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assassinato Instantâneo", "Asuma Sarutobi espera seus inimigos atacarem e os golpeia com uma cotovelada forte no estômago, atordoando-os.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assassinato dos Espelhos Demoníacos de Gelo", "Depois que a vítima é presa nos espelhos de gelo de Haku, ele ataca a vítima em alta velocidade, como ele faz com a técnica Espelhos Demoníacos de Cristais de Gelo. Depois disso, um choque de luz azul explode no interior da cúpula, danificando a vítima gravemente. Antes disso acontecer, Haku escapa no último segundo, para evitar danos a si mesmo. Os espelhos depois quebram em um milhão de pedaços de gelo.", "Curto alcance", null, null, "魔鏡氷殺まきょうひょうさつ", "Makyō Hyōsatsu", "Assassinato dos Espelhos Demoníacos de Gelo", "Jutsu Oculto: Profundidade do Diabo Região de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assassinos Mais Malignos", "Itachi cria um clone de água para perfurar e prender o oponente. Kisame corta-o no ar enquanto usa os restos do clone água destruído para invocar um tubarão de água para atacar o oponente. O oponente cai na esteira do Tsukuyomi de Itachi em que o oponente se esvai como se fosse água.", "Curto alcance", null, null, "最凶の刺客さいきょうのしかく", "Saikyō no Shikaku", "Assassinos Mais Malignos", "Os Maléficos Assassinos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação", "Trata-se de uma habilidade suplementar utilizada especialmente em infiltrações. Tal como o nome sugere, a técnica possibilita ao usuário a assimilação — isto é, camuflagem — de seu corpo com algum local.", null, null, "Javali[3]", "同化どうか", "Dōka", null, null, null, "Jutsu de Prevenção ao Ataque");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação: Caixão de Areia", "Esta técnica é uma cópia feita por Nagare da técnica de Gaara, diferenciando apenas na composição de tal, que é usado pó de rocha em vez de areia.", null, null, null, "同化・砂縛柩", "Dōka: Sabaku Kyū", "Assimilação: Caixão de Areia", null, null, "Assimilação: Ataúde de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação: Cortina Vermelha de Veneno", "Depois de assumir as propriedades do solo único, vermelho situado na Sétima Frente, o Exército de Zetsu Branco ganha propriedades venenosas. Sempre que expirarem ou serem cortados em pedaços e mortos, eles expelem um gás venenoso vermelho capaz de deixar seus oponentes inconscientes.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação: Funeral do Deserto", "Esta técnica é uma cópia feita por Nagare da técnica de Gaara, com a excepção da utilização de pó de rocha, ao invés de areia.", "Curto a Médio alcance", null, null, "同化・砂瀑送葬", "Dōka: Sabaku Sōsō", "Assimilação: Funeral do Deserto", null, null, "Assimilação: Enterro de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação: Tanque de Rocha", "Esta técnica é uma versão copiada do Tanque da Bala Humana do Clã Akimichi, a única diferença é que o usuário utiliza a Técnica de Assimilação da Criação de Todas as Coisas para se fundir com uma pedra próxima e coletar a massa necessária para realizá-lo, em vez de criar por si próprio através da conversão de gordura corporal em chakra.", "Curto alcance", null, null, "同化・岩石戦車", "Dōka: Ganseki Sensha", "Assimilação: Tanque de Rocha", null, null, "Assimilação: Tanque de Pedra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Assimilação: Vendaval de Rocha", "Depois de executar a Técnica de Assimilação da Criação de Todas as Coisas e se fundir com um objeto, Nagare controla as rochas que compõem o seu 'corpo' e pele de seu oponente com as rochas capazes de regenerar-se imediatamente depois de qualquer ataque.", "Curto alcance", null, null, "同化・岩吹雪", "Dōka: Iwafubuki", "Assimilação: Vendaval de Rocha", null, null, "Assimilação: Chuva de Pedras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Atacando em Ambos os Lados", "Izumo e Kotetsu se preparam em ângulos diferentes do seu inimigo, geralmente em seus respectivos lados direito e esquerdo, e em seguida, correm velozmente para atacar o adversário com suas lâminas super afiadas. Como as lâminas têm formatos diferentes, o objetivo é principalmente cortar o adversário por igual, de trás pra frente e vise-versa, ou esfaqueá-lo.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Absoluto Rígido Final: Funeral do Deserto", "Gaara bate as mãos, criando uma montanha gigante de areia para levantar seu oponente, aprisionando-o e segurando qualquer parte do corpo, exceto a cabeça. Ele em seguida, invoca uma grande quantidade de areia na forma do Ataque Absoluto Rígido Final: Lança de Shukaku, no qual ele joga em direção a montanha de areia em uma velocidade intensa, onde de fato seu oponente se encontra preso. Devido ao tamanho da lança, ela rasga seu oponente, matando-o e causando uma explosão em uma grande quantidade de areia.", "Longo alcance", null, "Juntar as Maos", "最硬絶対攻撃・砂瀑槍葬", "Saikō Zettai Kōgeki: Sabaku Sōsō", "Ataque Absoluto Rígido Final: Funeral do Deserto", "Último Ataque: Lança de Areia do Funeral", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Absoluto Rígido Final: Lança de Shukaku", "A Lança de Shukaku é a contrapartida de ataque do Escudo de Shukaku. Como o escudo, Gaara esmaga os materiais mais duros do chão usando areia, desta vez na forma de uma alabarda, na forma da mão de Shukaku. Assim como o escudo que ainda não encontrou nada que pudesse quebrar a sua defesa, a alabarda ainda não encontrou uma defesa que não pode romper.", "Curto a Médio alcance", null, null, "最硬絶対攻撃・守鶴の矛", "Saikō Zettai Kōgeki: Shukaku no Hoko", "Ataque Absoluto Rígido Final: Lança de Shukaku", null, null, "Derradeiro Ataque Supremo: Lança de Shukaku");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Asura", "A partir das habilidades do Caminho Asura, o usuário pode liberar um canhão, em alguma parte de seu corpo para disparar uma enorme explosão de chakra, com poder capaz de devastar regiões inteiras.", "Todos os alcances", null, null, "修羅ノ攻しゅらのこう", "Shura no Kō", "Ataque Asura", "Ataque Shura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Contra os Pontos Fracos", "Trata-se de um golpe que é desferido pelo usuário em algum ponto frágil do corpo de seu inimigo, podendo atordoar ou até mesmo desacordar o inimigo.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Desvairado do Selo Amaldiçoado", "Jūgo transforma parcialmente uma parte de seu corpo e, em seguida, corre em alta velocidade para golpear seu inimigo. Dependendo da eficiência do golpe, Jūgo pode matar seu inimigo com um soco.", "Curto alcance", null, null, "呪印乱撃じゅいんらんげき", "Juinrangeki", null, "Cerimônia de Inauguração (地鎮祭, Jichinsai)[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Duplo da Espada de Areia", "Gaara cria dois riachos de areia para perseguir o oponente, um riacho vai pelo ar enquanto o outro vai pelo chão. O riacho de areia no chão ataca o oponente e carrega ele até o ar. Enquanto o que está no ar se une com o do chão para esmagar o oponente colocando ele no centro da colisão.", "Longo alcance", null, null, "砂刃双撃", "Sajin Sōgeki", "Ataque Duplo da Espada de Areia", "Lâmina Dupla de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Especial Rasengan", "Naruto soca seu oponente no chão e cria dois clones, um clone cria um Rasengan mais fraco na mão do outro, que ataca o oponente.", "Curto alcance", null, null, "特攻螺旋丸", "Tokkō Rasengan", "Ataque Especial Esfera Espiral", "Rasengan Carregado", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ataque Forte da Garra do Gato", "Matatabi, Yugito em seu Modo Besta com Cauda ou mesmo Nekomata, desfere uma batida sólida com sua pata direita, podendo ser ela na vertical, para aproveitar seu próprio peso, ou horizontal, para aproveitar um impulso.", "Curto alcance", null, null, "大猫爪撃", "Daibyō Sōgeki", "Ataque Forte da Garra do Gato", "Grande Garra do Gato", null, null);
