INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bandagens Cortantes", "Hiruko pode desdobrar as ataduras em torno de seus braços, e infunde chakra nelas para ser usadas, como tentáculos para ataques de longo alcance. As ataduras são feitas de um material tão forte que elas podem facilmente cortar uma rocha e são muito rápidas.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Banshō Kokuin", "Madara cria um orbe negro com propriedades atrativas e joga em direção ao alvo. A esfera é capaz absorver detritos e explode ao atingir o oponente.", "Curto a Médio alcance", null, null, "万象黒引", "Banshō Kokuin", "Atração Negra de Todas as Coisas", "Puxão Negro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Banshō Ten'in", "Banshō Ten'in é uma técnica derivada do poder do Rinnegan que manipula forças atrativas em que o usuário puxa o objeto em questão para o usuário.", "Todos os alcances", "Rank –", null, "万象天引ばんしょうてんいん", "Banshō Ten'in", "Atração Celestial de Toda Criação", "Puxada Universal", "Atração Universal", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barragem da Garoa de Areia", "A versão mais forte da Garoa de Areia de Gaara. Com essa técnica, Gaara dispara uma infinidade de balas de areia, atirando precisamente no adversário, que podem levar à morte. A velocidade e força que elas são efetuadas é inimaginável, levando a surpreender quando em ação. No confronto de Gaara e Sasuke, essa técnica junto das outras em colaboração possivelmente teria causado graves ferimentos no último, bem como quebrar as costelas frontais do Susanoo.", "Todos os alcances", null, null, "連弾砂時雨", "Rendan Suna Shigure", "Barragem da Garoa de Areia", "Barragem: Chuveiro de Areia", "Chuva de Areia: Disparos Múltiplos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barragem da Lâmina de Vento", "Baki cobre suas mãos com a Lâmina de Vento, entrega uma barragem de socos no oponente, e então acaba com ele com um soco que cria uma grande rajada de vento, jogando o adversário para o ar.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Esférica da Armadilha Devoradora", "Em algum ponto, o usuário dessa técnica cria uma série de selos de barreira, que são camuflados pelo ambiente. Se o alvo não está ciente dos selos camuflados, a presença do alvo acende a armadilha automaticamente. Uma vez ativada, um chakra esférico vermelho escuro é formado, enjaulando o alvo dentro. Qualquer um pego na armadilha, uma vez que preso será comprimido em seu recipiente, devorado, e esmagado.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Estrondosa do Leque de Vento", "Temari usa seu Leque Dobradiço Gigante para criar um turbilhão de vento contra o oponente. Quando o oponente é atingido pelo turbilhão, novamente ela usa o leque, porém para fazer com que o oponente seja jogado para alto.", "Curto a Médio alcance", null, null, "扇風轟壁せんぷうごうへき", "Senpū Gōheki", "Barreira Estrondosa do Leque de Vento", "Parede de Vento Estrondoso[1]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Expansiva", "Cobrindo-se de chakra por todos os lados, o usuário cria um domo etéreo perfeitamente redondo da cor de seu chakra em torno de si. A barreira pode ser expandida para repelir tudo nas proximidades, o que permitiu destroçar a técnica de selamento de Gaara depois de algum tempo de concentração de Urashiki.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Norte Sul", "Menma Uzumaki usa o Hokuto Sennin e Nanto Sennin, que executam selos ritualísticos e batem seus cajados contra o chão, erguendo barreiras para aprisionar múltiplos alvos. Essa técnica abre brecha para que o Shinigami drene o chakra dos alvos.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Sensorial", "A Barreira Sensorial (感知結界, Kanchi Kekkai) é uma barreira gigante, em forma de cúpula colocada sobre uma área designada. A barreira não é visível ao olho padrão; quando vistos com certos dōjutsu como o Sharingan, pode ser visto que existe uma concepção de vários círculos que têm o kanji para fronteira (界), e de barreira (結界, kekkai) inscrito neles. Quando ligado a uma Esfera de Água de Sensoriamento, todos dentro e fora da barreira podem ser detectados e identificados.", "Longo alcance", null, null, "感知結界", "Kanchi Kekkai", "Barreira de Sensoriamento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Talismã: Olho Blindado", "Esta técnica foi criada pelos Oinin de Kirigakure e de acordo com Ao, eles são os únicos que sabem como cancelar a técnica. Quando uma parte específica do corpo do usuário é alvejada, esta técnica é ativada automaticamente. Os talismãs se movem para cobrir a parte específica do corpo e formam uma barreira para protegê-lo.", "Curto alcance", null, null, "結界符・眼装甲", "Kekkaifu: Gansōkō", "Barreira Talismã: Olho Blindado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira Violenta de Ventos Fortes", "Temari manuseia o Leque Dobradiço Gigante para criar um forte vendaval. O oponente é rodeado pelo vento, que puxa-o abruptamente para o céu. Como há nuvens de chuva, o oponente é molhado e eletrocutado por três relâmpagos, em seguida caindo ao chão, imóvel.", "Médio a Longo alcance", null, null, "豪風暴壁ごうふうぼうへき", "Gōfū Bōheki", "Barreira Violenta de Ventos Fortes", "Muralha do Vendaval Indomável", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira: Método de Formação da Cobertura", "Esta técnica cria uma barreira de detecção esférica com o utilizador no centro. Ao comando do usuário, a barreira de detecção pode expandir, envolvendo tudo no espaço. O utilizador é capaz de detectar qualquer coisa que se move no interior do espaço da barreira com seus próprios 'sentidos'. O usuário também pode mover-se, sendo que a barreira irá segui-lo para onde ele for.", "Curto a Médio alcance", "Rank B", null, "結界・天蓋法陣", "Kekkai: Tengai Hōjin", "Barreira: Formação do Método da Cobertura", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira: Prisão do Sapo Cabaça", "Esta técnica invoca um pequeno sapo em forma de cabaça, chamado de 'Sapo Cabaça' (瓢箪蝦蟇, Hyōtan Gama), uma espécie única de sapo indígena do Monte Myōboku. Esta técnica é utilizada para capturar e isolar uma vítima, arrastando-o no estômago do sapo. Isolado do mundo exterior por uma barreira, o espaço dentro do estômago do Sapo Cabaça é muito maior do que o tamanho da superfície do próprio Sapo Cabaça.", null, "Rank B", null, "結界・蝦蟇瓢牢", "Kekkai: Gama Hyōrō", "Barreira: Prisão do Sapo Cabaça", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Auto-Reparação", "A Barreira de Auto-Reparação é um konbijutsu que, ligando o chakra da equipe de ninja, criará um barreira que, se estiver danificada, será constantemente reparada com o referido chakra que emana de seus corpos. A barreira também impede que os que estão fora dela sintam qualquer coisa dentro dela através de métodos tradicionais.", "Longo alcance", null, null, "再生結界", "Saisei Kekkai", "Barreira de Auto Reparação", null, "Barreira de Trinta e Seis Camadas de Auto-Reparação", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Cinco Selos", "Esta técnica cria uma barreira em torno de um lugar, colocando quatro selos que proíbem a passagem de um indivíduo em diferentes localidades ao redor do local a ser protegido, fazendo uma marca de cinco sobre aquele local. As etiquetas são colocadas sobre uma superfície plana, e estão ligadas com o chakra do utilizador. Esta técnica torna toda a gama rodeada pelas etiquetas para um espaço de barreira, e no interior do espaço, a destruição material é 'vedada'. O uso de qualquer tipo de agressão física para tentar destruir algo na barreira é inútil, pois só vai levar a lesões. Para cancelar isso, é necessário procurar as quatro marcas e rasgá-las simultaneamente, o que significa que deve haver ao menos cinco pessoas em uma equipe para cancelá-la.", "Todos os alcances", "Rank C", null, "五封結界", "Gofū Kekkai", "Barreira dos Cinco Selos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Confinamento", "Um tipo de Kekkaijutsu utilizado pelo Time de Barreira de Konoha. Após usar a si mesmos como perímetro para a barreira, uma barreira é erguida e então toma a forma de um domo capaz de segurar mesmo o Nue.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Contração de Furacões", "Temari balança o Leque Dobradiço Gigante para criar dois poderosos furacões. Enquanto um rodeia o oponente, o outro suga-o fortemente. Então, os furacões se unem e dilaceram o corpo do inimigo de modo a restar nada.", "Médio alcance", null, null, "旋風挟壁せんぷうきょうへき", "Senpū Kyōheki", "Barreira de Contração de Furacões", "Barreira de Furacões", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Ferramentas Ninja: Formação de Escamas Reversas", "Esta é uma técnica usada pelos Homens dos Quatro Símbolos Celestiais. Concebido para combater a areia de Gaara, a formação baseia-se em suas armas, dispostos em um triângulo com Gaara no centro. Os três membros usam três canais subterrâneos para conectar a sua formação, que irá convergir sob o seu alvo. Após as armas estarem carregadas com o seus respectivos chakra, eles vão ativar a técnica, enviando uma onda de poder através das vias navegáveis para o alvo central. Isso fará com que um dragão de água suba a partir do solo e envolva o alvo, no caso, Gaara, absorvendo a sua areia e tornando a areia muito mais pesada. Isso faz com que a areia se aglutine e se torne quase impossível de utilizar.", "Longo alcance", null, null, "忍具結界・逆鱗の陣", "Ningu Kekkai: Gyakūrōku no Jin", "Barreira de Ferramentas Ninja: Formação de Escamas Reversas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Liberação de Terra: Domo da Prisão de Terra", "Esta técnica prende uma ou mais vítimas em um domo de terra que é autorreparante ao sofrer danos. Jirōbō ainda era capaz de absorver o chakra de quem estivesse preso dentro do domo, algo que ele aparentemente gostava muito. No entanto, o chakra de Jirōbō não estava distribuído uniformemente por toda a cúpula, uma vez que o polo mais distante dele, em relação ao lado que ele se encontrava, seu chakra era muito menos concentrado, tornando a cúpula mais fraca à ataques nesse ponto, embora ataques físicos fortes ainda fossem necessários para quebrá-la.", "Curto a Médio alcance", "Rank B", "Bater as maos no chão", "土遁結界・土牢堂無どとんけっかい・どろうどうむ", "Doton Kekkai: Dorō Dōmu", "Barreira de Liberação de Terra: Câmara Nula da Prisão de Terra", "Barreira do Estilo Terra: Prisão do Domo de Terra", "Barreira de Terra: Cúpula do Confinamento Vazio", "Barreira de Estilo Terra: Cúpula de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Selamento", "Num grupo de duas a três pessoas, os usuários se dividem igualmente em torno do oponente e aplicam uma fórmula de selamento sobre ele, que se eleva a partir da superfície em onde é desenvolvida e envolve o alvo, danificando-o e selando suas habilidades mais poderosas.", "Curto alcance", null, "Selo do Confronto", "封印結界ふういんけっかい", "Fūin Kekkai", "Barreira de Selamento", "Selo de Barreira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Selamento de Quatro-Cantos", "A Barreira de Selamento de Quatro-Cantos é um Fūinjutsu projetado por Tsunade com a finalidade de selar o Isobu.", null, null, "Iniciar: Cao → Cobra → Javali → Tigre,Procurar: Selo do Confronto,Restringir: Juntar as Maos,Selar: Juntar as Maos", "結界四方封陣けっかいしほうふうじん", "Kekkai Shihō Fūjin", "Barreira de Selamento de Quatro-Cantos", null, null, "Barreira de Selamento de Quatro Pontas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira de Tempestade de Areia", "Com o auxílio de areia pré-existente, o usuário cria uma barreira de tempestade de areia que é capaz de se sobrepor em outras barreiras ativas, como a Barreira Sensorial. A técnica também atrapalha habilidades sensoriais dos inimigos. A barreira pode ser usada ofensivamente para cercar os seus oponentes e limitar o seu campo de visão.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Barreira do Poder Budista: Sinal Auspicioso", "Seis pilares verdes emergem do solo, elevando uma barreira em todos os lados e na parte de cima para repelir todos os ataques inimigos que se aproximam. Como uma proteção adicional, a técnica também pode liberar uma série de agulhas, a partir selo em forma de hexagrama no topo da barreira, atacando os oponentes.", "Curto a Médio alcance", null, null, "法力結界・チャバシラ", "Hōriki Kekkai: Chabashira", "Barreira do Poder Budista: Sinal Auspicioso", null, null, "Barreira de Dharma: Pilares Celestiais");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen: Hélice de Fogo", "Usando o Bashōsen, o usuário desencadeia ondas a partir do leque, provocando um enorme rastro de torrentes de chamas intensas, que vão na direção de seu alvo pretendido.", "Médio alcance", null, null, "芭蕉扇・火の巻ばしょうせん・ひのまき", "Bashōsen: Hi no Maki", "Leque do Palmo de Banana: Hélice de Fogo", "Pergaminho do Fogo Bashosen", "Bashōsen Abanar o Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen: Hélice de Relâmpago", "Acenando o Bashōsen para baixo, o usuário faz com que um relâmpago apareça em torno dos adversários.", "Todos os alcances", null, null, "芭蕉扇・雷の巻ばしょうせん・かみなりのまき", "Bashōsen: Kaminari no Maki", "Leque do Palmo de Banana: Hélice de Relâmpago", "Pergaminho do Relâmpago Bashosen", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen: Hélice de Terra", "Agitando a Bashōsen no chão, o usuário desencadeia uma coluna de terra que sobe em direção ao oponente.", "Curto a Médio alcance", null, null, "芭蕉扇・土の巻ばしょうせん・つちのまき", "Bashōsen: Tsuchi no Maki", "Leque do Palmo de Banana: Hélice de Terra", "Pergaminho da Terra Bashosen", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen: Hélice de Vento", "Usando o Bashōsen, o usuário cria uma forte rajada de vento. Essa rajada é poderosa o suficiente para destruir a máscara de raios de Kakuzu.", "Todos os alcances", null, null, "芭蕉扇・風の巻ばしょうせん・かぜのまき", "Bashōsen: Kaze no Maki", "Leque do Palmo de Banana: Hélice de Vento", "Pergaminho do Vento Bashosen", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen: Hélice de Água", "Usando a Bashōsen, o usuário libera uma torrente de água, usando-a para atacar seu inimigo.", "Longo alcance", null, null, "芭蕉扇・水の巻ばしょうせん・みずのまき", "Bashōsen: Mizu no Maki", "Leque do Palmo de Banana: Hélice de Água", "Pergaminho da Água Bashosen", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bastão do Seis Caminhos", "Bastão do Seis Caminhos (六道の棒, Rikudō no Bō), é como Naruto chama suas armas criadas a partir de suas Esferas da Busca da Verdade.", null, null, null, "六道の棒りくどうのぼう", "Rikudō no Bō", "Bastão do Seis Caminhos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Batida!", "Konohamaru salta sobre seu oponente e, em seguida, salta no chão sobre ele, que leva dano com o impacto.", "Curto alcance", null, null, "ぴった～ん!", "Pittan!", "Batida!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Batida da Sombra", "Isobu ou seus respectivos jinchūriki enquanto em seu estado de transformação, rolam como uma bola, intimidando seus adversários, fazendo pleno uso das suas muitas saliências pontiagudas de uma forma semelhante ao Tanque da Bala Humana Espinhenta do clã Akimichi. Esta manobra também pode ser usada para escapar de ataques.", "Curto alcance", null, null, "影撫で", "Kagenade", "Batida da Sombra", "Queda da Concha Torre (甲櫓落し, Kōro Otoshi)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Beco sem Saída", "Hidan corta seu oponente com sua foice e o amaldiçoa. Kakuzu, então, usa mistura suas técnicas de fogo, relâmpago e vento contra Hidan. Em ritual, o dano infligido a Hidan é passado ao inimigo.", "Curto a Médio alcance", null, null, "恐越死極きょうえつしきょく", "Kyōetsu Shikyoku", "Beco sem Saída", "Morte Suprema Além do Medo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Biguro", "Esta técnica permite a Kakuzu manipular os fios pretos no subsolo para puxar ou imobilizar seus inimigos pelos pés. Os fios pretos são rápidos e, como estão debaixo do inimgo, dificilmente podem ser evitados. Quando preso, o inimigo, mesmo com uma força absurda, não pode rompê-los.", "Curto alcance", null, null, "毘愚呂ビグロ", "Biguro", "Contenção Furtiva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bisturi de Chakra", "O Bisturi de Chakra é um ninjutsu médico que transforma o chakra do usuário em uma pequena lâmina afiada.", "Curto alcance", null, "Tigre → Cavalo → Coelho → Rato → Cao", "チャクラのメス", "Chakura no Mesu", "Bisturi de Chakra", "Lâmina de Dissecção de Chakra", null, "Bisturi de Chakra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bisturi de Chakra: Crueldade", "O usuário corre em direção ao oponente e ataca sucessivas vezes com seu bisturi de chakra e, em seguida, ataca uma vez no tronco. O oponente fica a mercê do usuário ao ser imobilizado pelo corte do bisturi de chakra nos músculos perto da área respiratória.", "Curto alcance", null, null, "チャクラ解剖刀・惨", "Chakura Kaibōtō: Zan", "Bisturi de Chakra: Crueldade", "Lâmina de Dissecção de Chakra: Três", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bisturi de Chakra do Esmagamento da Cobra", "Kabuto entra no subterrâneo e usa seus bisturis de chakra para cortar as pernas do oponente, deixando-o imóvel. Orochimaru, em seguida, invoca uma cobra gigante que esmaga o oponente com sua cabeça.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bloqueio do Escudo", "O Bloqueio do Escudo é uma técnica usada por Gamaken tirando proveito de seu escudo maciço. É uma de suas defesas mais poderosas, capazes de até mesmo defendê-lo de um ataque veloz.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bloqueio do Escudo Mecânico de Luz", "Este escudo de chakra pode ser instalado em qualquer marionete. Um mecanismo dentro da marionete abre os braços em um par de segmentos, através da qual o usuário irradia seu chakra. O chakra se espalha como uma película fina e se transforma em uma folha de proteção, bloqueando todos os ataques físicos e algumas técnicas. Enquanto o escudo bloqueia a Areia de Ferro, mas não impede que ela escoe as articulações das marionetes, que as leva a ser desativadas. Chiyo instalou um em seu próprio braço e nas marionetes dos pais de Sasori. Kankurō posteriormente instalou um no corpo da marionete Sasori.", "Curto alcance", "Rank B", null, "機光盾封", "Kikō Junbū", "Escudo de Luz Bloqueador Mecânico", null, null, "Escudo de Chakra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bofetada Abrupta", "O usuário dá um forte e inesperado tapa no rosto de seu oponente, que o derruba ou o lança para longe.", "Curto alcance", null, null, "ツンツンビンタ", "Tsuntsunbinta", "Bofetada Abrupta", "Tapa Ardido", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola da Besta com Cauda", "A Bola da Besta com Cauda é a técnica final de uma besta com cauda e seu jinchūriki.", "Longo alcance", "Rank S", null, "尾獣玉びじゅうだま", "Bijūdama", "Bola da Besta com Cauda", "Bola Ameaçadora (威鞠, Imari)", "Esfera da Bijū", "Bomba da Besta com Cauda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola da Besta com Cauda Contínua", "A besta com cauda ou o jinchūriki que dominou a mudança de forma com maestria, consegue executar esse golpe, que consiste em disparar várias Bolas da Besta com Cauda descompactadas em rápida sucessão no alvo.", "Longo alcance", "Rank S", null, "連続尾獣玉", "Renzoku Bijūdama", "Bola da Besta com Cauda Contínua", "Canhão de Chakra Sucessivo (チャクラ砲連発, Chakurahō Renpatsu)", "Bijūdama em Série", "Rajada de Bombas da Besta com Cauda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola da Besta com Cauda Instantânea", "Bola da Besta Com Cauda Instantânea é uma Bola da Besta com Cauda sobrecarregada de chakra, que é disparada assim que é gerada, sem a necessidade de ter que engoli-la, como ocorre com a sua versão menos poderosa. Devido à necessidade de uma quantidade enorme de chakra para executar essa técnica, ela só pode ser utilizada por Kurama e Gyūki, de maneira individual, devido as suas vastas reservas de chakra, ou por meio colaborativo, como quando ocorreu quando as cinco bestas com caudas combinaram seus poderes na luta contra Naruto em seu Modo de Chakra da Besta com Cauda.", "Longo alcance", "Rank S", null, "尾獣玉一閃いっせんびじゅうだま", "Issen Bijūdama", "Bola da Besta Com Cauda Instantânea", "Cauda do Demônio[1]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola de Aço", "O usuário cria uma esfera feita de aço. O projétil pode ser lançado a uma distância considerável em direção ao alvo, causando um pesado dano contundente. Quando utilizada em união com a Liberação de Lava, que pode derreter o aço, ele se transforme em uma forma semelhante a uma espiral.", "Curto a Médio alcance", null, null, null, null, "Bola de Aço", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola de Pelos de Rato", "O usuário cospe uma bola de fogo de pelos que assume a forma de um rato em chamas, que se divide continuamente para pelos individuais e da mesma forma em chamas. Estes projéteis flamejantes perseguem os alvos pretendidos antes de explodir, uma vez que eles entram em contato com um objeto.", "Todos os alcances", null, null, "ねずみ毛玉", "Nezumi Kedama", "Bola de Pelos de Rato", null, "Novelo de Rato", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola de Plasma", "Bola de Plasma (荷電障壁殻, Purazuma Bōru) é uma técnica de espadas utilizada por Ranke ao extrair energia da Pedra de Gelel.", "Curto alcance", null, null, "荷電障壁殻", "Purazuma Bōru", "Barreira Protetora de Carga Elétrica", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bolha Esférica", "As Bolhas Esféricas são esferas de energia utilizadas pelas marionetes de Toneri Ōtsutsuki, que mostraram-se capazes de canalizar o chakra do Tenseigan de Hamura em suas mãos e dispará-las contra seu oponente, explodindo no impacto.", "Todos os alcances", null, "Juntar as Maos → Selo Desconhecido", null, null, "Bolha Esférica", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bolha Explosiva", "Após Utakata soprar dezenas de bolhas, elas voam em direção ao oponente e explodem fortemente, derrubando o inimigo para próximo. As explosões também produzem uma grande quantidade de fumaça, que pode ser usada para obstruir a visão do oponente.", null, null, null, "起爆泡", "Kibakuhō", "Bolha Explosiva", null, null, "Arte Ninja: Bomba da Bolha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bolhas de Ocultação de Odor", "O usuário primeiramente sente o odor de seus aliados e produz suor com os seus movimentos, o suor, após se sugado, é então misturado com saliva para criar uma bolha através de sua boca que os permite a ocultarem os seus odores e o próprio odor. Uma versão em maior escala desta técnica pode ser usada para ocultar odor em uma área mais ampla através da produção de bolhas menores criadas pelo mesmo método.", "Todos os Alcances", null, "Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba Ajusta com Enorme Sucesso!", "O usuário entra no subterrâneo e atinge seu oponente por baixo, lançando-o para o alto. Em seguida, o usuário coloca uma bomba no local em que seu oponente irá cair. Quando o adversário cair, ele irá se deparar com uma bomba à sua espera, enquanto o usuário escapa.", "Curto alcance", null, null, "爆弾設置大成功!ばくだんせっちだいせいこう", "Bakudan Secchi Daiseikō!", "Bomba Ajusta com Enorme Sucesso!", "Bomba Colocada com Sucesso!", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba Liger", "Depois de cobrir-se em seu Modo de Chakra de Liberação de Relâmpago, o Quarto Raikage agarra o seu oponente e levanta-lhe no ar. Em seguida, usando sua força extrema, ele esmaga-o de cabeça para o chão em que se assemelha a uma manobra powerbomb. Isso é devastador o suficiente para quebrar uma grande área do próprio solo com o impacto. Darui observou que ninguém jamais sobreviveu a isso antes, e se Sasuke não tivesse usado seu Susanoo, ele teria sido morto. O impacto desta técnica foi forte o suficiente para rachar as costelas do Susanoo de Sasuke.", "Curto alcance", "Rank B", null, "雷我爆弾", "Raigā Bomu", "Bomba Auto-Relâmpago", null, "Bomba Relâmpago", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba Liger dos Dois Pelotões", "Com o oponente no ar, A e B pulam até a altura que o inimigo está e se entrelaçam nele em um movimento extremamente complexo, finalizando com uma queda extremamente brutal com o oponente imobilizado ao meio.", "Curto alcance", null, null, "雷我弐不雷斗爆弾", "Raigāzutsūpuratonbomu", "Duas Bombas de Relâmpago de Auto-Destruição", "Bomba Liger dos Dois Pelotões", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba Poderosa", "A salta no ar e ataca seu oponente com golpes poderosos e, em seguida, agarra-o e joga-o no chão.", "Curto alcance", null, null, "撥破揚爆弾", "Pawaa Bomu", "Bomba de Tempo", "Bomba Poderosa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba de Chakra da Nove-Caudas", "Mecha-Naruto transforma parte de seu corpo em um canhão e dispara um poderoso laser em seu oponente. Essa habilidade é um tanto perigosa, que quase devastou Konoha inteira.", "Longo alcance", null, null, "九尾チャクラボム", "Kyūbi Chakura Bomu", "Bomba de Chakra da Nove-Caudas", "Canhão de Bijū", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bombardeio do Projétil de Borboleta", "Esta técnica é uma ameaça à vida se utilizado em combinação com a pílula alimentar vermelha Akimichi, que queima todo o excesso de gordura do ninja e converte-o em chakra. Como os membros do Clã Akimichi tendem a estar acima do peso para ajudar em suas técnicas, esta pílula pode dar-lhes 100 vezes o seu nível de chakra normal. O chakra intensamente concentrado que é adquirido vaza da espinha dorsal e se torna visível através do atrito com o ombro, aparecendo como uma asa de borboleta. O usuário então concentra o chakra em seu único punho, e em seguida, coloca todo o seu peso corporal por trás do golpe. A enorme quantidade de chakra concentrada é suficiente para mudar a estrutura do corpo do usuário, e o usuário morre, a menos que tenha determinado atendimento médico conforme descrito na Enciclopédia Médica do Clã Nara logo depois.", "Curto alcance", "Rank B", null, "蝶弾爆撃ちょうだんばくげき", "Chōdan Bakugeki", "Bombardeio da Bala da Borboleta", "Bomba Borboleta", "Bombardeio da Borboleta", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bombardeio à Longa Distância", "O usuário revela duas bombas e lança-as em seu oponente, causando uma explosão e jogando o oponente para trás.", "Todos os alcances", null, null, "遠距離爆撃", "Enkyori Bakugeki", "Bombardeio à Longa Distância", "Ataque de Bombas de Longa Distância", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bombas de Ovos Verdes", "O Caminho Animal faz com que o Pássaro Bico-de-Broca Gigante ponha ovos que explodem ao seu comando.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Boneco Amaldiçoado", "O usuário infunde o seu chakra em um boneco que absorve chakra de uma pessoa nas proximidades, tomando sua aparência e personalidade, como um boneco gêmeo. Ele, também, é capaz de criar inúmeros bonecos, bem como roubar a forma humana da maioria dos personagens do jogo.", null, null, null, "呪いの人形", "Noroi no Ningyō", "Boneco Amaldiçoado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Borboleta da Agulha de Fogo", "Ino inicia a técnica criando uma ilusão com inúmeras borboletas encurralando o inimigo. Então, o inimigo instantaneamente se une ao chão, assim ela consegue colocar um papel explosivo no rosto do inimigo e sair em um dissolver da ilusão. Logo a ilusão se cancela, revelando as inúmeras borboletas em uma transformação de papéis explosivos, finalmente os selos estouram e causam uma devastação imensa de chamas incontroláveis, constituindo um imenso incêndio atrás de Ino, que faz uma pose.", "Todos os alcances", null, "Tigre", "蝶針火", "Chōshinka", "Borboleta da Agulha de Fogo", "Borboleta da Agulha de Chama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Boruto Stream", "Utilizando a Liberação de Vento, o usuário pode acelerar seus movimentos criando um efeito similar à visão de um túnel. Para conseguir chegar a altas velocidades, o usuário pode fazer que seus aliados ou até mesmo clones das sombras performem a técnica nele ao propulsionar seus pés.", "Curto alcance", null, null, "ボルトストリームぼるとすとりぃむ", "Boruto Sutorīmu", "Corrente Boruto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braço Monstruoso de Areia", "Usando grandes quantidades de areia, Gaara pode manifestar os braços de Shukaku para lutar contra o seu adversário, geralmente tentando agarrá-lo e/ou esmagá-lo.", "Todos os alcances", null, null, "砂の怪腕すなのかいわん", "Suna no Kaiwan", "Braço Monstruoso de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braço de Areia de Ferro", "Shinki manipula sua areia de ferro para criar um braço que se estende para pegar o oponente e restringi-lo ou agarrar algo. Ele também pode utilizar isso para cobrir seu próprio antebraço, aumentando o poder ofensivo do mesmo. Ao dar um potente soco endurecido com a maciça areia de ferro, ele pode proteger-se do contato direto com seu alvo.", "Curto a Médio alcance", null, null, "砂鉄の腕さてつのうで", "Satetsu no Ude", "Braço de Areia de Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braços de Chakra da Besta com Cauda", "Trata-se de uma habilidade que um jinchūriki é capaz de realizar, quando em sua forma de besta com cauda. O jinchūriki usa o chakra bruto da sua Besta com Cauda para criar braços de forma que ele pode manipular livremente para atacar seus inimigos e para auxiliar na performance de técnicas, além de ser capaz de atacar um adversário mais rápido do que um usuário do Sharingan possa reagir.", "Todos os alcances", null, "Juntar as Maos[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braços de Sombra", "O Zero-Caudas pode criar uma infinidade de braços de seu corpo que perseguem o adversário. Os braços também podem absorver chakra do oponente. Quando usado por Shinnō, ele também poderia formar uma parede para proteger-se com esses braços, o suficiente para bloquear o Rasengan Tornado de Naruto.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Broca Cortadora do Lobo Canino", "Asuma entrega duas barras em todo o peito do oponente e proporciona outra barra para cima para lançar o oponente para o ar, e Asuma salta no ar e oferece duas barras no rosto do oponente e oferece uma barra final para explodir o adversário no chão.", "Curto alcance", null, null, "狼牙伐錐ろうばばっすい", "Rōga Bassui", "Broca Cortadora do Lobo Canino", "Broca da Presa de Lobo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Broca da Planta Carnívora", "Zetsu se esconde sob o solo e coloca seu apêndice de planta carnívora como uma armadilha a fim de capturar presas desavisadas. Quando a vítima cai sobre a armadilha, ele fecha esse apêndice para prendê-la e rotaciona para dilacerá-la, abandonando a presa ao finalizá-la.", "Curto alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Buddha Superiormente Transformado", "Depois de criar uma gigantesca estátua de madeira que possui milhares de mãos, o usuário utiliza estas mãos simultaneamente para entregar milhares de golpes poderosos para um alvo. Devido ao grande volume de socos envolvidos, a defesa contra a totalidade deles é quase impossível. Isso pode ser usado como uma técnica de Tiro-Único, aumentando seu poder destrutivo, mas também resultando com as mãos atrás das costas da estátua serem destruídas. Depois, o usuário pode descartar a parte de atrás da estátua para aumentar a mobilidade da estátua verdadeira de milhares de mãos. Ele também pode ser usado de forma mais conservadora como pode ser visto no uso de Tobi, impedindo que as mãos sejam destruídas e permitindo um outro uso da técnica, mas resulta em poder menos destrutivo.", "Todos os alcances", null, null, "頂上化仏ちょうじょうけぶつ", "Chōjō Kebutsu", "Buddha Superiormente Transformado", null, "Buda Supremo", null);
