INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chama Carmesim", "O usuário acumula chakra de natureza fogo em sua mão e dispara uma série de projéteis que, após colidirem contra seu alvo, explodem em chamas vermelhas. Essa técnica é poderosa o suficiente para demolir todo o território em que ela é usada.", "Longo alcance", null, null, "紅炎くえん", "Kuen", "Chama Carmesim", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chama Combinada", "O usuário dispara inúmeras bolas de fogo através de seu lança-chamas anexado nas mãos de sua marionete, que perseguem seu oponente até que o queime.", "Todos os alcances", null, null, "火炎連弾", "Kaen Rendan", "Chama Combinada", "Barragem de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chama Envoltória do Gato de Fogo", "Matatabi, ou Yugito completamente transformada na besta, salta no ar, gira em alta velocidade e deixa um rastro de fogo em torno de si, um pouco mais frio que seu fogo azul, mas ainda capaz de queimar mortalmente quem encostar.", "Curto alcance", null, null, "纏炎猫火", "Ten'en Byōka", "Chama Envoltória do Gato de Fogo", "Véu do Gato de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chama do Grande Fogo Abanado", "Tobi abana seu gunbai para desencadear três tornados de fogo que explodem no adversário.", "Todos os alcances", null, null, "煽炎豪火", "Sen'en Gōka", "Chama do Grande Fogo Abanado", "Grande Fogo Atiçado", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chamas da Verdade de Samadhi", "As Chamas da Verdade de Samadhi constituem uma técnica de Liberação de Fogo de Koji Kashin, que as descreve como as 'Chamas do Purgatório' (煉獄の炎, Rengoku no Honō).", "Curto a Médio alcance", null, "Rato", "三昧の真火ざんまいのしんか", "Zanmai no Shinka", "Chamas da Verdade de Samadhi", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chegada do Demônio", "Zabuza deixa seu inimigo indefeso ataca seu oponente com o Kubikiribōchō, avança sobre ele com uma aura demoníaca ao seu redor e corta seu oponente.", "Curto alcance", null, null, "鬼来", "Kirai", "Chegada do Demônio", "Corte do Demônio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chibaku Tensei", "Chibaku Tensei é uma técnica usada através dos poderes do Rinnegan.", "Longo alcance", "Rank –", "Ativar: Juntar as maos,Compactar: Cobra[3],Mover: Carneiro[4]", "地爆天星", "Chibaku Tensei", "Corpo Celestial Estourado da Terra", "Devastação Planetária Catastrófica", "Estrela da Devastação", "Devastação Planetária");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chibaku Tensei: Queda Celestial", "Sasuke convoca seu Susanoo e atira uma enxurrada de flechas, então ele manifesta o Corpo Completo — Susanoo e cria vários satélites infundidos em chamas negras, que são jogados sobre seu oponente, causando ondas de choque por todo o planeta e uma explosão que pode ser vista do espaço.", "Todos os alcances", null, "Fogo → Terra", "地爆天星・天堕ちばくてんせい・あまお", "Chibaku Tensei: Amao", "Corpo Celestial Estourado da Terra: Queda Celestial", "Estrela da Devastação: Queda Celestial", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chicote da Concha", "Depois de Kandachi realizar os selos de mão necessários, o Rei Concha usa uma ou mais das línguas com seu chakra para chicotear ou restringir o adversário. O usuário também pode bombear seu chakra para o Rei Concha aumentar o comprimento e velocidade da língua. É mostrado que seções de golpes repetidos com a lingueta pode eventualmente matar a vítima.", "Media a Longo alcance", null, "Tigre → Cao → Cobra", "巻き貝鞭まきかいベン", "Makigai Ben", "Chicote da Concha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chicote do Amor", "O usuário (normalmente um sapo) estende sua língua para fora com uma velocidade incrível e força, entregando um poderoso golpe para o oponente. O sapo também pode usar sua língua para envolver o inimigo e esmagá-lo no chão. Gamabunta ainda foi capaz de usar esta técnica defensivamente para proteger Naruto da areia de Gaara.", "Curto alcance", null, null, "愛の鞭あいのむち", "Ai no Muchi", "Chicote do Amor", null, null, "Chicote de Amor");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori", "O Chidori é a primeira técnica original de Kakashi Hatake, embora mais frequentemente usa uma versão Rank S conhecida como o Corte Relâmpago. Ele a criou em torno do tempo em que ele foi promovido a jōnin, e mais tarde ensinou a Sasuke Uchiha.", "Curto alcance", "Rank A", "Boi → Coelho → Macaco[4] ou (Macaco → Dragao → Rato → Passaro → Boi → Cobra → Cão → Tigre → Macaco)[5]", "千鳥ちどり", "Chidori", "Mil Pássaros", "Cortador de Relâmpago: Corte Original (雷光斬り・真斬, Raikōkiri: Maki)[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori Batendo Asas", "O Chidori Batendo Asas é uma forma mais poderosa do Chidori utilizado por Sasuke Uchiha.", "Curto alcance", null, null, "羽撃く千鳥はばたくちどり", "Habataku Chidori", "Mil Pássaros Batendo Asas", "Chidori Lamentado, Chidori Escuro, Lamento do Chidori", "Revoada dos Mil Pássaros", "Chidori Negro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori Escuro", "O Chidori Escuro é uma das várias versões do Chidori utilizada por Sasuke Uchiha, após receber metade do poder de Hagoromo.", "Curto alcance", null, null, "黒き千鳥くろきちどり", "Kuroki Chidori", "Mil Pássaros Escuros", "Chidori Ônix", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori Senbon", "Esta é uma das muitas variações do Chidori que utiliza a transformação da forma. Em comparação com o Chidori padrão, é ainda mais especializado em velocidade, ao ser transformado em inúmeras senbon afiadas, que são apontadas e disparadas contra o inimigo. Ao aumentar a quantidade de chakra usado, o número de senbon também aumenta proporcionalmente. Quando usado em conjunto com o Sharingan, torna-se muito mais eficaz e mortal, uma vez que permite ao usuário disparar com precisão para os pontos vitais do alvo.", "Curto a Médio alcance", "Rank A", null, "千鳥千本ちどりせんぼん", "Chidori Senbon", "Senbon dos Mil Pássaros", null, "Agulhas dos Mil Pássaros", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori: Divisor Planetário", "Sasuke desfere um poderoso golpe com sua espada em seu oponente, lançando-o para cima e, em seguida, o prende dentro de um Chibaku Tensei, usando-o como núcleo gravitacional para atrair os escombros ao seu redor. O meteoro, então, começa a cair do céu, pegando fogo no processo. Em seguida, o usuário rodeia-se de eletricidade e, com um Chidori em sua mão, golpeia o satélite em queda, resultando em uma explosão que pode ser vista do espaço.", "Longo alcance", null, null, null, null, "Mil Pássaros: Divisão Planetária", "Chidori: Divisão Planetária", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori: Queda", "Sasuke expele um Chidori para o ar. Ele se move extremamente rápido em um padrão zig-zag, de tal forma que ele deixa pós-imagens. Ele ataca o oponente com apenas um golpe. Esta técnica, em combinação com a velocidade de Sasuke, faz com que seja extremamente difícil, se não impossível de evitar.", "Curto alcance", null, null, "千鳥・楽", "Chidori: Raku", "Mil Pássaros: Queda", "Chidori (Mil Pássaros)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chidori: Trovão", "Uma variante do Chidori, que é uma tempestade com relâmpagos Chidori que devasta a área.", "Curto alcance", null, null, "千鳥・雷鳴ちどり・らいめい", "Chidori: Raimei", "Mil Pássaros: Trovão", "Trovão Chidori", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chifre do Lobo do Castelo de Areia", "O usuário envolve um ou mais alvos em um vórtice de areia, que pode esmagá-los em seu interior. Ela também pode ser utilizada como um meio de outras técnicas de areia, como por exemplo a criação de vários olhos para recolher informações ou como preparação para uma técnica destrutiva em massa.", "Curto alcance", null, null, "砂城狼角", "Sunajō Rōkaku", "Chifre Lupino do Castelo de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choque Demolidor", "O Dez-Caudas bate com três de suas caudas sobre o seu oponente.", "Curto alcance", null, null, "潰砕衝", "Kaisaishō", "Choque Demolidor", "Choque Esmagador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choque Desorientador do Corpo", "Uma técnica extremamente de alto nível, usada como um ninjutsu médico. O corpo humano é controlado com sinais elétricos do cérebro, mas uma pessoa que tiver esses sinais elétricos cortados com esta técnica se tornará incapaz de fazer seu movimento corporal como eles querem. Ao transformar o chakra dentro do corpo de alguém e dar-lhe as propriedades da eletricidade, se cria um campo elétrico. Assim que um ataque da mão pousa, a eletricidade é vertida para o sistema nervoso do inimigo, cortando os sinais e desorganizando seu controle corporal. O corpo do alvo se movem de alguma forma, exceto como eles querem que ele se mova. Para os shinobi comum, em batalha, é mencionado mesmo apenas para andar, torna-se impossível. Por ser uma especialista em tratamentos médicos, Tsunade foi capaz de dominar esta técnica.", "Curto alcance", "Rank A", null, "乱身衝", "Ranshinshō", "Choque Desorientador do Corpo", null, "Choque da Desorientação", "Ruptura do Sistema Nervoso");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choque do Canhão de Chakra", "Bandō agarra seu oponente pelo braço e joga-o no chão com uma força tremenda, saltando no processo. Em seguida, enquanto no ar, ele pega o canhão de suas costas e com seu braço direito, ele carrega uma quantidade imensurável de chakra, que é liberada em forma de míssil, explodindo quando em contato com o corpo do oponente.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choque do Furacão da Folha", "Uma variação do Furacão da Folha onde Guy e Lee atacam um único alvo com um chute.", "Curto alcance", null, null, "木ノ葉つむじ旋風このはつむじせんぷう", "Konoha Tsumuji Senpū", "Choque do Furacão da Folha", null, "Redemoinho da Folha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choro Celestial", "O Choro Celestial é um ninjutsu utilizado por Tobirama Senju.", "Curto alcance", null, null, "天泣", "Tenkyū", "Choro Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Choro da Flor", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, executa um corte no oponente com sua espada óssea, bate contra o oponente com sua cauda com picos ósseos e finaliza correndo contra o oponente com sua broca óssea.", "Curto alcance", null, null, "しだれ花", "Shidarehana", "Choro da Flor", "Spiranthes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute Caído", "O usuário realiza um chute poderoso que envia o oponente voando para longe. Ele foi poderoso o suficiente para lançar Killer B através de várias estruturas de Kumogakure.", "Curto alcance", null, null, "ドロップキック", "Doroppu Kikku", "Chute Baixo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute Celestial do Pilar de Madeira", "Yamato ataca seu oponente com uma série de socos e chutes, e em seguida, transforma o seu braço esquerdo em um pilar de madeira que iniciou-se anteriormente do chão, levantando seu oponente pro alto, dando a oportunidade de Yamato ir até o seu alvo e finalizar com um chute fatal.", "Curto alcance", null, null, null, null, null, "Chute Celestial do Pilar de Madeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute Derrapante", "Kisame corta seu oponente com a Samehada, e em seguida, salta executando um chute por baixo.", "Curto alcance", null, null, "蹴滑り", "Kesuberi", "Chute Derrapante", "Chute de Deslize", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute Giratório do Pilar de Madeira", "Yamato faz um pilar de madeira erguer-se a partir do chão e, em seguida, agarra em seu pilar e começa a girar, dando vários chutes em seu oponente.", "Curto alcance", null, null, null, null, null, "Chute Giratório do Pilar de Madeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute da Busca da Verdade", "O usuário chuta a Esfera da Busca da Verdade em seu oponente. O impacto do chute na esfera foi forte o suficiente para deixar uma cratera no tronco da Árvore Divina.", "Curto a Médio alcance", null, null, "求道蹴ぐどうしゅう", "Gudōshū", "Chute da Busca da Verdade", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute da Lança do Céu", "Tsunade salta à grandes alturas e desce colidindo com seu inimigo com uma enorme quantidade de força, sendo poderoso o suficiente para quebrar a caixa torácica do Susanoo de Madara Uchiha.", "Curto alcance", null, null, null, null, null, "Chute da Lança do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute de Avestruz Brilhante", "Condor, enquanto no ar, acerta o oponente com um poderoso chute.", "Curto Alcance", null, null, "シャイニング駝鳥キックシャイニングだちょうキック", "Shainingu Dachō Kikku", "Chute de Avestruz Brilhante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chute em Míssil", "Karin corre contra o oponente e pula com ambos os pés contra o peito dele, lançando-o a metros de distância no chão. O impacto é ainda maior se bater na parede, já que ela possui direcionamento vertical.", "Curto alcance", null, null, "ミサイルキック", "Misairu Kikku", "Chute em Míssil", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chutes Furiosos da Besta-Humana", "Em seu estado transformado, o shinobi do clã Tenrō desfere uma infinidade de chutes poderosos contra o oponente.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chuva Rancorosa", "Esta técnica requer uma quantidade variada de usuários. Os usuários criam uma grande nuvem de tempestade, que por sua vez produz chuva. A grande quantidade de chuva pode se transformar em uma grande onda de água que é capaz de varrer o inimigo de uma área, desconectando seus pés do chão. Enquanto os alvos são levados pela água, seu chakra será drenado continuamente.", "Médio a Longo alcance", null, "Cao", "怨雨うらみあめ", "Uramiame", "Chuva Rancorosa", null, null, "Arte Ninja: Chuva de Rancor");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chuva Verde de Cura", "Sarada empunha oito kunais, cada uma com um selo de cura anexado em si, então ela as arremessa para os pontos cardeais, delimitando o terreno. As kunais se infundem com chakra yang e uma barreira esférica é erguida, curando gradativamente todos que estiverem em seu interior enquanto a técnica permanecer ativa.", "Curto a Médio alcance", null, null, null, null, null, "Chuva Verde de Cura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chuva de Aranhas", "Após Kidōmaru invocar Kyodaigumo, ele libera um saco de ovos cheio de bebês aranhas com aproximadamente o tamanho de um cão pequeno. Kidōmaru, então, corta o saco, fazendo com que as aranhas a chovam sobre o seu adversário. Cada aranha, em seguida, tece um fio de teia enquanto caem, cercando assim o adversário em teias e imobilizando-o.", "Todos os alcances", "Rank B", null, "雨蜘蛛あまぐも", "Amagumo", "Chuva de Aranhas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chuva de Areia de Ferro", "É um ataque de projétil que usa a areia de ferro endurecida em grãos minúsculos para atacar simultaneamente em toda uma vasta gama. As balas são tão rápidas que elas são difíceis de ver. No momento em que o usuário tenha terminado de preparar o ataque, ele evade se tornando extremamente difícil. Usando a força repelente de magnetismo, a velocidade das balas aumenta rapidamente após os disparos. Além disso, o usuário também pode usar um padrão de ataque, onde as balas de areia de ferro são moldadas em agulhas afiadas, aumentando sua capacidade de ferir ou matar o adversário. Uma vez que a areia de ferro tem uma força magnética e também é embebida em veneno, a defesa contra esta técnica é quase impossível. Esta técnica é especialmente ameaçadora para os usuários de marionetes, uma vez que os bonecos podem ser imovelmente renderizados, se a areia se alojar em suas articulações.", "Todos os alcances", "Rank –", "Carneiro", "砂鉄時雨さてつしぐれ", "Satetsu Shigure", "Chuva de Areia de Ferro", null, null, "Areia de Ferro: Chuvisco de Ferro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chuveiro de Agulhas", "Depois de o usuário jogar um guarda-chuva especial equipado com molas para o ar, ele cria um 'chuveiro' granizado de senbon. Este jutsu não pode ser evitado movendo-se para longe uma vez que as agulhas cobrem uma vasta área em todas as direções, controladas por chakra. Embora as agulhas sejam referidas como capazes de perfurar uma chapa de aço com cinco milímetros de espessura, elas podem ainda ser bloqueadas por um escudo suficientemente potente. Elas também podem ser mergulhadas em veneno para aumentar a letalidade.", "Médio alcance", "Rank C", "Tigre", "如雨露千本じょうろせんぼん", "Jouro Senbon", "Chuveiro de Agulhas", "Arte Ninja: Chuveiro de Agulhas", null, "Arte Ninja: Tempestade Senbon");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cicatrização Yin de Feridas de Destruição", "Um Iryō Ninjutsu onde se antecipa o local do inimigo vai atacar e preventivamente aplica tratamento médico a que, reduz os prejuízos ao mínimo. O utilizador concentra chakra para aquela área e começa o processo de recriação da célula antes mesmo da área alvo a ficar danificada. Usando a expressão facial e os movimentos do adversário, velocidade e potência da técnica, o usuário analisa tudo em um instante e com precisão deduz onde o ataque vai pousar. Ainda mais do que prever com precisão o ataque ou instantaneamente medir a situação, uma grande capacidade de resistência é necessária, por isso pode-se dizer que esta técnica é exclusiva para Kabuto.", null, "Rank A", null, "陰癒傷滅", "In'yu Shōmetsu", "Destruição de Ferimentos da Cura Secreta", "Ativação Celular", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ciclone de Vento do Relâmpago", "As máscaras de Kakuzu utilizam a Liberação de Vento: Atsugai e a Liberação de Relâmpago: Gian e atira-os no oponente, o aprisionando em um ciclone de relâmpago que explode depois de alguns ataques.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cilada", "Trata-se de uma série de armadilhas para enfraquecer, capturar, enganar, derrubar ou distrair um inimigo.", null, null, null, "ブービートラップぶうびいとらっぷ", "Būbītorappu", "Cilada", "Armadilha de Esferas de Aço[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cinco Mil Combos de Naruto Uzumaki", "Naruto cria vários clones da sombra e, em seguida, lança seu oponente para o alto. Então, ele cria cinco mil clones da sombra e ataca seu alvo por todos os lados. Finalmente, cada clone acumula o chakra de Kurama em seu punho direito e desferem uma onda de socos, que resulta em uma erupção de chakra alaranjado, que pode ser vista do espaço.", "Curto alcance", null, null, "うずまきナルト五千連弾", "Uzumaki Naruto Gosen Rendan", null, "Naruto Barragem 5 mil de Uzumaki", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cintilação de Clones", "Shisui cria dois clones das sombras que avançam em alta velocidade em direção ao oponente e desferem um corte com kunai, ganhando tempo para que Shisui realize uma nova sequência de golpes.", "Curto a Médio alcance", null, "Selo de Clonagem", "分瞬ぶんしゅん", "Bunshun", "Cintilação de Clones", "Duplo Flash", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cinzas Flamejantes", "Sasuke lança um dragão de fogo contra o oponente e, quando ele está prestes a cair, ele ativa sua Liberação de Chama: Kagutsuchi para golpear o oponente pelas costas e queimá-lo, causando uma pequena nuvem de fumaça.", "Curto alcance", null, "Selo do Confronto", "灰炎", "Haien", "Cinzas Flamejantes", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cipreste", "Kimimaro ataca repetidamente o oponente com ataques alternados, cria ossos que saem de suas principais juntas e finaliza com um ataque de ossos que saem de ambas as suas mãos.", "Curto alcance", null, null, "椹", "Sawara", "Cipreste", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clethra", "Kimimaro corta seu oponente começando por baixo com sua espada óssea, que logo é estendida para duas vezes maior que o comprimento de sua espada óssea normal e, em seguida, corta o oponente na altura dos pés, derrubando-o.", "Curto alcance", null, null, "令法", "Ryōbu", "Clethra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone Bombardeio Suicida", "Depois de criar um clone com a Técnica dos Clones das Sombras, Deidara ingere sua Argila Explosiva para causar uma explosão, criando uma distração que pode ser usada para uma fuga. Com um 'anúncio de atentado suicida' a cautela do inimigo é aumentada e esta oportunidade é usada para trocar de lugar com um clone das sombras. A explosão é forte o suficiente para destruir uma floresta inteira. Esta técnica requer um uso adepto de truques verbais; com este fundamento do uso de muitas 'artes' para brincar com o inimigo, ele certamente cumpre o seu propósito.", "Todos os alcances", "Rank –", null, "自爆分身じばくぶんしん", "Jibaku Bunshin", "Clone Bomba Suicida", "Clone Explosivo", "Jutsu dos Clones Explosivos", "Clone de Autodestruição");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone Parasita", "Esta técnica permite que Zetsu Branco crie clones de si mesmo, os quais ele também é capaz de controlar remotamente. Diferente da maioria das técnicas dessa natureza, esses clones não desaparecem ao receber danos, morrer ou ter seu usuário principal morto, tornando assim um dos atributos mais notáveis dessa habilidade nativa. No decorrer da série, Zetsu Branco apresentou a forma de usá-los em combinação com a Técnica do Substituto, a fim de assumir a forma de outro ninja antes tocado para tomar suas características, inclusive técnicas.", null, null, null, "寄生分身きせいぶんしん", "Kisei Bunshin", "Clone Parasita", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone da Besta Humana", "Esta é uma técnica básica do clã Inuzuka que permite ao animal tomar as características físicas de seu dono.", null, "Rank D", "Tigre", "獣人分身", "Jūjin Bunshin", "Clone da Besta Humana", null, null, "Clone do Homem-Besta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Areia", "Esta técnica é usada para criar clones em forma de si mesmo ou outros, utilizando areia como um meio. A forma do clone pode ser alterada instantaneamente na areia, e do corpo do inimigo pode ser preso. Suas muitas aplicações com ataques de longo alcance podem agir como uma isca e um chamariz. Devido às propriedades da areia, Gaara foi capaz de utilizá-la como uma armadilha, escondendo pó de ouro dentro dela. Este é o lugar onde ele desenha a linha com outros clones.", null, "Rank –", null, "砂分身", "Suna Bunshin", "Clone de Areia", null, null, "Jutsu Clone de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Areia de Ferro", "A técnica consiste em criar um clone a partir da manipulação da areia de ferro para fins variados.", null, null, null, "砂鉄の分身", "Satetsu no Bunshin", "Clone de Areia de Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Argila", "Uma técnica altamente conveniente, característica de Deidara. Acima de todas as outras técnicas de Bunshinjutsu, este clone tem um duplo papel. Ele atua como um substituto e uma armadilha para capturar o inimigo. Feito a partir de argila, ele pode atacar e capturar o inimigo, absorvendo-o em seu próprio corpo. Ele mostra a sua forma real após receber um ataque. Embora a dureza do clone seja baixa, o inimigo é facilmente imobilizado. Se a Argila Explosiva for adicionada ao clone, ela é capaz de explodir com o inimigo preso nele.", null, "Rank –", null, "粘土分身ねんどぶんしん", "Nendo Bunshin", "Clone de Argila", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Cera de Abelha", "O usuário faz um clone feito de cera de abelha e quando ferido o suficiente ele se dissolve de volta para dentro da cera.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Cobras", "Após a implantação do DNA de Orochimaru, Kabuto desenvolve uma grande quantidade de cobras minúsculas que podem fundir-se em um clone de uma pessoa viva, necessitando de uma amostra genética ou em uma serpente maior para combate. Durante o Arco Chikara, Kabuto não só criou um clone de Hidan, mas também um Clone do Nove-Caudas. Os clones das cobras têm uma vida útil curta, fazendo com que eles eventualmente sofram um colapso celular dentro de 48 horas após sua criação.", null, null, null, "クローン蛇", "Kurōn Hebi", "Clone de Cobras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Cobras: Técnica da Serpente", "Kabuto bate a mão sobre a superfície da água com suas cobras especialmente desenvolvidos e emite um raio vermelho-escuro, o que faz com que as cobras assumam a forma de uma cobra gigante azul com uma boca vermelha e sem olhos. A cobra, em seguida, forma cilindros hidráulicos e empurra os inimigos debaixo d'água, na tentativa de afogá-los. Kabuto usou essa técnica dentro do buraco, que é conhecido por acelerar o processo de recriação de suas cobras.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Fumaça", "Esta técnica cria um clone feito de fumaça. Quando é atingido, se transforma em fumaça semelhante à cortina de fumaça que o usuário cria. O clone de fumaça é usado principalmente como uma tática de distração como outras técnicas de clones.", null, null, null, "煙分身", "Kemuri Bunshin", "Clone de Fumaça", null, null, "Clone de Fumaça");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Madeira: Pontapé", "Yamato faz um pilar de madeira sair do chão e bater no oponente, derrubando-o para trás. Um clone então vira o pilar e bate na perna do oponente, derrubando o oponente no chão.", "Todos os alcances", null, null, "木分身・蹴もくぶんしん・しゅう", "Moku Bunshin: Shū", "Clone de Madeira: Pontapé", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone de Papel", "Tirando proveito da sua habilidade de se transformar em papel, Konan se torna livre para criar e recriar clones parecidos com ela a partir desse material. Ao infundir seu chakra em todos os clones, ela os molda em uma réplica perfeita de si e os controla remotamente. Quando o clone é atingido, ele se dispersa, ou na melhor das hipóteses, se desfaz para Konan recriá-lo novamente.", null, null, null, "紙分身", "Kami Bunshin", "Clone de Papel", null, null, "Clone de Papel");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clone do Espelho de Gelo", "Usando seu Espelhos Demoníacos de Cristais de Gelo, Haku cria clones resistentes de gelo capazes de lutar a uma velocidade semelhante à de Haku. Quando são derrotados, os clones se dissipam em água.", null, null, null, "氷鏡分身こおりきょうぶんしん", "Kōrikyō Bunshin", "Clone do Espelho de Gelo", "Clone Espelho de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clones Assassinos do Dez-Caudas", "Usado como uma manobra defensiva, o Dez-Caudas tem a capacidade de produzir inúmeras réplicas de si mesmo a partir de seu corpo principal. Enquanto eles têm muitas variações em termos de sua aparência humanoide, com alguns aparecendo e outros que possuem uma forma animalesca e operacional em quatro patas, estas partes duplicatas muitos dos traços distintivos do Dez-Caudas, como sua boca, bem como protuberâncias cravadas na cabeça e costas. Seus tamanhos também variam, como alguns em detrimento de outras torres, aparecendo semelhante em tamanho às grandes invocações. As cópias são capazes de formar vários tipos de armas, incluindo espécies de halteres, também é capaz de criar apêndices semelhantes a machados e foices apêndices, bem como projéteis de lança e paredes fortificadas para fins defensivos. Apesar de suas diferenças, todas as cópias operam com o único objetivo de proteger o corpo principal do Dez-Caudas de ataque.", null, null, null, "十尾分殺体じゅうびぶんさつたい", "Jūbi Bunsatsutai", "Clones Assassinos do Dez-Caudas", "Clone do Dez-Caudas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clones da Sombra: Superior", "Naruto cria um clone da sombra para jogá-lo em direção ao seu oponente, dando-lhe um soco superior.", "Curto alcance", null, null, "影分身・アッパー", "Kage Bunshin: Appā", "Clones da Sombra: Superior", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clones das Sombras: Grande Rotação", "Naruto cria dois clones da sombras, formando uma corrente com cada Naruto segurando o outro pelos tornozelos. Naruto então gira, batendo os seus oponentes com os clones.", "Curto alcance", null, null, "影分身・大回転かげぶんしん・だいかいてん", "Kage Bunshin: Daikaiten", "Clones das Sombras: Grande Rotação", "Clone das Sombras: Rotação Gigante", null, null);
