INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clones de Rastro", "Shisui usa a Técnica de Cintilação Corporal e move-se nas redondezas, criando diversos clones de impressões vindas de sua velocidade. Através dela, ele pode mover-se entre os clones, e acertar ataques com cada um deles, como se tivessem a massa dele. Se for atacado, ele desvia, deixando o ataque de seu adversário passar por um dos seus rastros. Os clones criados por Shisui podem se desmaterializar quando recebem um ataque e se materializarem para realizar um golpe.", "Curto alcance", null, "Tigre → Cao → Rato", "残像分身ざんぞうぶんしん", "Zanzō Bunshin", "Clones de Rastro", null, null, null);
