INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Combo Tonton", "Shizune transforma Tonton em uma cópia de si mesma e usa, em seguida, um Bisturi de Chakra para atordoar seu oponente. Tonton aparece por trás e salta para cima enquanto Shizune ataca o oponente com um ataque com dois Bisturi de Chakra. Ela pega a ponta da cauda de Tonton enquanto a porca faz uma cambalhota; o oponente entra em colapso. Shizune então percebe que a cauda de Tonton está queimando e começa a curá-la, e Tonton se irrita e bate em Shizune, em seguida.", "Curto alcance", null, null, "ト ン ト ン コ ン ビ", "Tonton Konbi", "Combo Tonton", "Dupla Tonton", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Combo da Besta com Cauda", "Naruto, em seu Modo de Chakra do Nove-Caudas, e Minato combinam seus Rasengan e atacam o oponente com ele. Minato, em seguida, lança sua Kunai do Deus Voador do Trovão no adversário. Os dois ninjas então tocam os punhos, Minato entra em seu Modo de Chakra do Nove-Caudas. Pai e filho, em seguida, usam a Técnica do Deus Voador do Trovão e se teletransportam para a kunai lançada, desferindo uma série de ataques no adversário, formando uma silhueta de chakra amarelo e laranja em forma de Rasengan, o que cria uma enorme explosão, causando grande dano no processo.", "Curto a Médio alcance", null, null, "尾獣連弾びじゅうれんだん", "Bijū Rendan", "Combo da Besta com Cauda", "Dueto Bijū", null, null);
