INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corpo de Areia", "Primeiramente desconstruindo a areia de sua cabaça, o corpo de Gaara também se transforma em areia. Nesta forma, Gaara fica invulnerável a danos e pode viajar por longas distâncias como uma tempestade de areia fluente. A técnica também pode ser aplicada no campo de batalha, transformando partes do corpo para aumentar a capacidade de manobra no campo de batalha.", "Todos os alcances", null, null, null, null, null, null, null, null);
