INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte da Terra", "Zabuza enfia sua Kubikiribōchō no chão abrindo uma enorme cratera, causando dano em inimigos enterrados ou sobre o local, em seguida, ele dá cotoveladas e soca o oponente. Para concluir, ele dá um poderoso golpe em seu oponente, que faz ele cair para baixo.", "Curto alcance", null, null, "地刻じこく", "Jikoku", "Corte da Terra", "Cortador da Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte das Andorinhas Gêmeas", "Tenten acerta repetidamente seu oponente com um par de tonfas, em seguida, ela ataca-o no ar com um bastão de três seções que ela separa para acertar um golpe final para o adversário cair rapidamente.", "Curto alcance", null, null, "双燕刈", "Sōenkai", "Corte das Andorinhas Gêmeas", "Andorinhas Gêmeas Cortantes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte das Sombras", "Sasuke infunde sua espada em chamas e desfere um corte em seu oponente, que explode em chamas negras. Essa técnica é poderosa o suficiente para quebrar as armas de chakra de Kinshiki.", "Curto alcance", null, null, "斬影ざん'えい", "Zan'ei", "Corte das Sombras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte de Ar Celestial Tipo Zero", "No Modo de Chakra do Nove-Caudas, Minato espalha diversas Kunai do Deus Voador do Trovão em torno do oponente cortando-o cada vez que ele deforma o espaço-tempo.", "Curto alcance", null, null, "裂空天光斬空閃零式", "Rekkū Tenkō Zankūsen Reishiki", "Corte de Ar Celestial Tipo Zero", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte de Água Indulgente", "Com sua Kubikiribōchō, Suigetsu desfere uma série de cortes poderosos, capazes de facilmente dilacerar uma porta de ferro maciça.", "Médio alcance", null, null, "水斬三昧みずきざんまい", "Mizuki Zanmai", "Corte de Água Indulgente", "Suizan Zanmai", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte do Deus Voador do Trovão", "Uma técnica que combina o uso de uma arma laminada e ninjutsu de espaço-tempo. O usuário se teletransporta para um alvo, com a arma na mão, para entregar instantaneamente um rápido e devastador corte a seu oponente. A velocidade pura desta técnica, é tal que mesmo um indivíduo que possui o Sharingan é incapaz de reagir a tempo.", "Curto a Médio alcance", "Rank B", null, "飛雷神斬りひらいしんぎり", "Hiraishingiri", "Corte do Deus Voador do Trovão", "Corte do Raijin Voador", "Corte do Deus do Relâmpago", "Fatia do Raijin Voador");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte dos Céus", "Zabuza aproxima se do seu oponente pulando ou correndo chuta seu oponente, e entrega uma série de cortes com sua Kubikiribōchō, terminando com um corte de baixo pra cima o partindo no meio.", "Curto alcance", null, null, "天刻むてんきざむ", "Tenkizamu", "Corte dos Céus", "Cortador do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corte em Vários Pedaços", "Trata-se de um movimento repetitivo de um espadachim rotacionando sobre seu próprio eixo usando uma grande espada.", "Curto alcance", null, null, "微塵斬り", "Mijingiri", "Corte em Vários Pedaços", "Corte Fino", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Costura pelas Sombras: Técnica da Grande Foice da Doninha", "Shikamaru estende sua sombra para capturar e imobilizar o oponente e, em seguida, Temari balança seu Leque Dobradiço Gigante para limpar completamente o campo. Imobilizado pela sombra, o oponente é incapaz de escapar ou evitar à técnica de Temari.", "Todos os alcances", null, "Shikamaru: Rato → Passaro", "影縫い・大カマイタチの術かげぬい・だいカマイタチの術", "Kagenui: Dai Kamaitachi no Jutsu", "Costura pelas Sombras: Técnica da Grande Foice da Doninha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cotovelada", "Depois de cobrir-se com seu Modo de Chakra de Liberação de Relâmpago, A empurra o oponente com seu cotovelo com uma força extrema. Esta técnica foi forte o suficiente para fazer Jūgo bater em uma parede, impossibilitando-o de continuar lutando.", "Curto alcance", null, null, "重流暴", "Erubō", "Explosão do Estilo-Pesado", null, "Cotovelada Rústica", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Crianças Problemáticas da Academia", "Naruto, Konohamaru, e Iruka jogam suas Fūma Shuriken no oponente. Então, Naruto e Konohamaru realizam sua Técnica Sensual, fazendo Iruka voar no oponente, mandando-o para longe. Depois, Iruka bate na cabeça de ambos Konohamaru e Naruto.", "Todos os alcances", null, null, "忍者学校の問題児あかでみいのもんだいじ", "Akademī no Mondaiji", "Crianças Problemáticas da Academia", "Criança Problemática da Academia Ninja", null, null);
