INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cristal: Shuriken Hexagonal", "Guren cria várias shuriken de cristal e lança-as no oponente. As shuriken são de seis lados com picos nos lados de cada lâmina, semelhante a flocos de neve. Como observado, Guren tem a capacidade de criar dezenas de uma vez, dando vantagem em seu combate direto, tornando seus ataques mais letais quando em contato com o corpo do inimigo.", "Todos os alcances", null, null, "結晶・六角手裏剣けっしょう・ろっかくしゅりけん", "Kesshō: Rokkaku Shuriken", "Cristal: Shuriken Hexagonal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cristal: Shuriken Hexagonal Gigante", "Guren forma uma imensa shuriken de cristal, que contêm o modelo de um floco de neve, sendo capaz de lança-la em vários adversários, cortando-os rapidamente. Infelizmente a resistência da técnica não foi suficiente para desfazer as defesas do Três-Caudas.", "Todos os alcances", null, "Selo de Confronto", "結晶・巨大六角手裏剣", "Kesshō: Kyodai Rokkaku Shuriken", "Liberação de Cristal: Shuriken Gigante de Seis Pontas", null, null, "Shuriken Hexagonal de Cristal Gigante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cristal: Shuriken Hexagonal: Dança Selvagem", "O usuário lança uma grande quantidade de shuriken de cristal em seu oponente. Ela pode usar uma forma mais avançada do ataque, onde partículas de cristal a cercam e condensam em numerosas shuriken.", "Todos os alcances", null, "Cobra → Cao → Carneiro → Cavalo → Rato → Macaco → Passaro → Javali → Carneiro", "結晶・六角手裏剣・乱舞けっそう・ろっかくしゅりけん・らんぶ", "Kesshō: Rokkaku Shuriken: Ranbu", "Cristal: Shuriken Hexagonal: Dança Selvagem", null, null, "Shuriken Hexagonal de Cristal: Dança Selvagem");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cristalização do Gelo", "Essa é a técnica assinatura de Renga, com a qual ele é capaz de congelar as partículas de umidade no ar para atingir a forma cristalina do gelo.", "Todos os alcances", null, null, "氷の結晶こおりのけっしょう", "Kōri no Kesshō", "Cristalização do Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Crueldade", "O usuário faz uso do Kamui para se teletransportar em uma posição acima de seu alvo, desferindo um golpe para baixo, acertando o inimigo desprevenido. A fim de aumentar a letalidade da técnica, pode se fazer uso de armas laminadas como uma kunai.", "Curto alcance", null, null, "無惨むざん", "Muzan", "Crueldade", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cuidado onde Pisa!", "Kushina cria um selo no chão que, assim que o oponente pisar, invoca duas grandes frigideiras para caírem sobre sua cabeça.", "Curto alcance", null, null, "足下注意ッ!あしもとちゅういつ", "Ashimoto Chūitsu!", "Cuidado onde Pisa!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cuspe Pegajoso da Aranha", "Sugerido pelo nome, Kidōmaru emite um fluxo de teia de aranha pegajosa de sua boca, Kidōmaru agarra um adversário por perto, permitindo-lhe a segurar-los.", "Curto a Médio alcance", null, null, "蜘蛛粘吐くもねんと", "Kumo Nento", "Cuspe Pegajoso da Aranha", null, "Vômito Viscoso da Aranha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cutiladas Necessárias", "Kisame posiciona a Samehada para cima e corta o oponente várias vezes.", "Curto alcance", null, null, "要削りようけずり", "Yōkezuri", null, "Cutiladas Necessárias", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cúpula de Proteção de Pano", "Maki tem demonstrado que ela é capaz de usar seu pano como meio de defesa, por envolvê-lo em torno dela e qualquer aliado próximo, podendo formar uma barreira protetora. O pano pode ficar em torno de um objeto próximo, como uma rocha, para melhor manter a fortificação. Se o pano for molhado através de uma técnica de água, é permitido que a cúpula possa se defender contra poderosos ataques baseados em calor.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cópia de Ninjutsu", "Kakashi salta no ar e cria dois clones, que logo em seguida fazem selos de mão necessários simultaneamente, um dos clones utiliza a Liberação de Vento: Grande Destruição, o original utiliza a Liberação de Água: Técnica do Projétil do Dragão de Água e o último clone utiliza a Liberação de Fogo: Técnica da Grande Bola de Fogo, as técnicas se chocam causando uma enorme explosão massiva sobre o oponente.", "Todos os alcances", null, null, "コピー忍術コピーにんじゅつ", "Kopī Ninjutsu", "Cópia de Ninjutsu", "Arte Ninja: Cópia de Ninjutsu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Córrego de Areia", "O usuário reúne sua areia abaixo de seus adversários e faz com que eles se afastem-se dele, criando um córrego, sendo usado para incapacitar seus oponentes sem prejudicá-los ou para o usuário se locomover mais facilmente.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Céu Esvoaçante", "Utakata salta no ar, em direção ao oponente, e desfere alguns ataques com seu Cachimbo de Bolhas e com as mãos nuas, terminando a sequência com um chute giratório com ambos os pés, que envia o seu adversário direto ao chão.", "Curto alcance", null, null, "翻天", "Honten", "Céu Esvoaçante", "Planar", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Círculo de Areia", "Gaara ataca seu oponente com uma pequena formação de areia semelhante a um braço, vindo de baixo. Em seguida, ele usa sua areia embaixo dos pés do oponente para levantá-lo. Então, ele atira vários projéteis de areia, e finaliza criando dois pilares de areia em volta do oponente, que giram simultaneamente, antes de explodirem jogando-o para trás.", "Médio alcance", null, null, null, null, null, "Círculo de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Círculo do Grande Atropelamento", "Quando transformado em Kokuō, Han vira-se, erguendo as patas dianteiras, e vem de volta por baixo esmagando seu oponente gravemente.", "Curto alcance", null, null, "大踏円", "Daitōen", "Círculo do Grande Atropelamento", "Grande Círculo Vibrador", null, null);
