INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dōjutsu: Deus do Sol", "Itachi ativa seu Mangekyō Sharingan, fazendo o oponente sentir uma grande pressão abaixo dele. Após a pressão, uma esfera negra gigante engole o usuário e tudo a sua volta, deixando uma enorme cratera no chão.", null, null, null, "瞳術・日神どうじゅつ・にっしん", "Dōjutsu: Nisshin", "Técnica Ocular: Deus do Sol", "Jutsu Visual: O Deus do Sol", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Celestial da Neblina Branca", "As mãos de Hinata ficam brancas, com um revestimento de chakra que flui através delas. Ela golpeia o inimigo, e finaliza reunindo ambas as mãos contra ele. Isso cria uma grande explosão que danifica e joga para trás o adversário.", "Curto alcance", null, null, "白朧天舞はくろうてんぶ", "Hakurō Tenbu", "Dança Celestial da Neblina Branca", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Celestial do Deus Tempestuoso", "Este é um estilo de luta comum de Momoshiki Ōtsutsuki após sua transformação, utilizando a habilidade de Kinshiki Ōtsutsuki em criar armas livremente de acordo com sua vontade e acumular chakra em sua mão para aprimorar a força de seus golpes.", "Curto alcance", null, null, null, null, null, "Dança Celestial do Deus Tempestuoso", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Desordenada da Palma Celestial", "Neji agarra seu oponente, em seguida, girando-o para efetuar três golpes poderosos em suas costas, que o lança para longe.", "Curto alcance", null, null, "天掌乱舞てんしょうらんぶ", "Tenshō Ranbu", "Dança Desordenada da Palma Celestial", "Dança Selvagem das Palmas Celestiais", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Desordenada do Leque", "Trata-se de uma combinação rápida de socos, cotoveladas e pontapés executada por Temari usando seu Leque Dobradiço Gigante fechado, a fim de incapacitar ou ferir o adversário. Esta habilidade é extremamente útil contra vários inimigos, como clones das sombras, devido aos movimentos serem precisos, ainda que não seja o princípio da técnica.", "Curto alcance", null, null, "乱れ扇舞みだれせんぶ", "Midare Senbu", "Dança Desordenada do Leque", "Dança Maluca do Leque", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Desordenada do Pontapé Celestial", "O usuário chuta o joelho de seu oponente, em seguida, chuta seu rosto duas vezes em um rápido movimento, fazendo o oponente ir girando pro alto.", "Curto alcance", null, null, "天蹴乱舞てんしゅうらんぶ", "Tenshū Ranbu", "Dança Desordenada do Pontapé Celestial", "Dança Desordenada dos Pontapés Celestiais", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem", "O usuário manipula sua marionete para perfurar o oponente. A marionete então começa girando contra o adversário, dilacerando-o diversas vezes, e finaliza jogando uma kunai nele.", "Curto Alcance", null, null, "乱舞", "Ranbu", "Dança Selvagem", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem da Kunai", "Rin desfere uma série de golpes de kunai em seu oponente, lança algumas kunai nele, afim de distraí-lo, e, quando ele menos espera, ela prepara uma kunai com papéis explosivos e lança contra ele, criando uma impactante explosão.", "Curto alcance", null, null, "クナイ乱舞", "Kunai Ranbu", "Dança Selvagem da Kunai", "Dança Louca da Kunai", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem da Samehada", "Kisame corta o inimigo arrastando sua Samehada no ombro dele, em seguida, corta-o, drenando seu chakra antes de esfaquear o inimigo e batê-lo no chão.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem da Shuriken Flamejante", "Uma técnica de colaboração usada por Sasuke e Sakura. Sakura lança uma série de shuriken que são incendiadas por Sasuke, usando sua Técnica da Grande Bola de Fogo.", "Curto alcance", null, null, "火炎手裏剣乱舞", "Kaen Shuriken Ranbu", "Dança Selvagem da Shuriken Flamejante", "Dança Selvagem da Shuriken de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem de Ukon", "Após se separar de Sakon, Ukon se aproxima de seu oponente e entrega um pontapé, fazendo o oponente ir para trás, onde, em seguida, Ukon salta para realizar um golpe fatal com suas garras, fazendo liberar um feixe de luz de coloração roxa.", "Curto alcance", null, null, null, null, "Dança Selvagem de Ukon", "Dança Louca de Ukon", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança Selvagem do Fluxo de Fogo", "Trata-se de uma técnica que incorpora o uso simultâneo de várias habilidades do taijutsu. O usuário inicia com um chute, seguido por um soco e outro chute.", "Curto alcance", null, null, "迸火乱舞ほうからんぶ", "Hōka Ranbu", "Dança Selvagem do Fluxo de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança da Camélia", "A segunda das cinco danças de Kimimaro, que ele realiza modificando um de seus ossos do braço para criar uma curta espada de óssea e a empunha. Ele então apunhala caoticamente e de forma contínua, fazendo com que os olhos dos oponentes fiquem para trás. A velocidade se assemelha a imagens residuais, a mão com a espada é visivelmente projetada repetidas vezes. E ainda assim, o movimento é irregular, tornando difícil de prever. Com cada investida a trilha da espada é capaz de mudar. O ataque vem de ângulos inesperados, provocando reação de defesa delicada de uma pessoa a sair da guarda. Embora se possa ter grandes reflexos e habilidade de movimento, é quase impossível manter-se esquivando do ataque feroz até que ele pare. Assim, se mostrar uma abertura, um golpe final será desferido por uma facada em um instante.", "Curto alcance", "Rank –", null, "椿の舞つばきのまい", "Tsubaki no Mai", "Dança da Camélia", null, "Dança das Camélias", "Dança Camélia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança da Clematite: Flor", "Esta técnica é a segunda metade da quarta dança de Kimimaro. Para obter a arma mais dura absolutamente forte, uma quantidade considerável de chakra é necessário. Aprimorado com o mais alto grau de solidez máxima, devido à densa compressão, a arma de osso é extremamente grande. Desde o seu poder destrutivo não tem sentido se o golpe não se conectar com o oponente, Kimimaro primeiro prende o oponente com uma 'vinha'.", "Curto alcance", null, null, "鉄線化の舞・花てっせん か の まい ・ はな", "Tessenka no Mai: Hana", "Dança da Clematite: Flor", null, "Dança da Clêmatis: Flor", "Dança Clematite: Flor");
