INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança da Lua Crescente", "O usuário cria dois clones da sombra antes de continuar a atacar o inimigo, com os clones atacando a esquerda e a direita e o usuário atacando por cima. O ataque ocorre em três frentes simultaneamente, é quase impossível pegar o adversário de surpresa. Além disso, os poderosos ataques de espadas são tão graves que recebendo um golpe, mesmo que de apenas um entre os três, resultará inevitavelmente em uma ferida fatal.", "Curto alcance", "Rank A", null, "木ノ葉流三日月の舞", "Mikazuki no Mai", "Dança da Lua Crescente", null, null, "Estilo Folha: Dança da Lua Crescente");
