INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança da Lâmina da Foice: Lâmina da Elevação Nascente", "Hanzō desfere uma série de cortes laterais com a sua Kusarigama, e em seguida, utiliza-a para trazer o seu oponente perto dele, e gira-a para cima, cortando o seu oponente e jogando-o no ar.", "Curto alcance", null, null, "刃鎌の舞・昇揚の刃", "Hagama no Mai: Shōyō no Yaiba", "Dança da Lâmina da Foice: Lâmina da Elevação Nascente", "Dança da Foice: Lâmina Nascente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dança da Lâmina de Foice: Queda Descendente da Lâmina", "Ficando com alguma distância entre ele e seu oponente, o usuário balança sua kusarigama alto no ar, e leva-a para baixo em seu oponente. No entanto, a técnica mostrou-se bloqueável. No mangá, Nagato, através do corpo de Yahiko, foi capaz de bloquear essa técnica com seu Shinra Tensei e Mifune foi capaz de bloqueá-la com sua Kurosawa.", "Curto alcance", null, null, "刃鎌の舞・落降の刃", "Hagama no Mai: Rakukō no Yaiba", "Dança da Lâmina de Foice: Queda Descendente da Lâmina", "Dança da Foice: Lâmina Mergulhante", null, null);
