INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação Reversa do Estilo Nuvem", "O usuário faz uma rotação poderosa, gerando o impulso necessário para cortar qualquer adversário por trás dele. O usuário também pode utilizar este movimento giratório para enganar um inimigo que está na frente dele. O inimigo se engana pensando que o usuário irá atacá-lo, mas o usuário ataca os alvos atrás dele.", "Curto alcance", null, null, "雲流裏斬り", "Kumoryū Uragiri", "Decapitação Reversa do Estilo Nuvem", null, "Estilo de Esgrima da Nuvem: Corte Pelo Verso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação da Dança de Mil Flashes", "Haku corta seu oponente por mil vezes em várias direções diferentes com várias senbon muito afiadas, deixando o oponente com muitos cortes e com as senbon perfuradas em seu corpo, incapacitando-o de efetuar um ataque após a finalização de seu combo.", "Curto alcance", null, null, "千閃斬舞", "Sensen Zanbu", "Decapitação da Dança de Mil Flashes", "Dança Cortante de Mil Flashes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação da Decepção do Estilo Nuvem", "Usando a Técnica de Substituição do Corpo para fingir ser atacado, o usuário tira proveito do choque momentâneo de seu oponente, e carrega para a frente com um corte lateral de espada.", "Curto alcance", null, null, "雲流騙し斬りくもりゅうだましぎり", "Kumoryū Damashigiri", "Decapitação da Decepção do Estilo Nuvem", null, null, "Estilo Nuvem: Fatia da Decepção");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação da Espada do Sapo", "Uma técnica que apenas sapos podem usar, empregando os seus recursos físicos. Seus quadris e pernas fortes dão luz a um salto extraordinariamente preciso, com o qual ele corta o seu alvo com a Espada do Sapo. Uma vez que o intervalo é coberto em um piscar de olhos, o oponente não pode nem mesmo tentar escapar. Gamabunta usa-o junto com sua enorme dosu (espada Yakuza). Apesar de normalmente ser usado de forma ofensiva, Gamakichi e Gamabunta utilizaram a técnica de forma defensiva para desviar projéteis.", "Médio a Longo alcance", null, null, "蝦蟇ドス斬", "Gamadosuzan", "Decapitação da Espada do Sapo", null, "Decapitação da Lâmina Batráquia", "Corte da Lâmina do Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação da Lua Crescente do Estilo Nuvem", "O usuário movimenta a sua espada em um único e grande arco em forma de lua crescente, que ocorre com uma velocidade feroz de tal forma que a técnica pode ser empregada em situações que se tenha pouco tempo para reagir. O golpe pode ser usado para acertar múltiplos oponentes simultaneamente.", "Curto alcance", null, null, "雲流三日月斬りくもりゅうみかずきぎり", "Kumoryū Mikazukigiri", "Decapitação da Lua Crescente do Estilo Nuvem", null, "Estilo de Esgrima da Nuvem: Corte da Lua Crescente", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação da Névoa Reversa", "Depois de criar vários clones da sombra que cercam o inimigo e ataca-o simultaneamente forçando-o a saltar para o ar, o verdadeiro usuário, então, ataca-o por cima.", "Curto alcance", null, null, "裏霞斬り", "Urakasumigiri", "Decapitação da Névoa Oculta", null, null, null);
