INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação de Chamas do Estilo Nuvem", "O usuário primeiro impregna a sua lâmina com fogo, antes de cortar na direção do inimigo desejado, resultando na criação de grandes arcos de chama intensa que se seguem a trajetória do movimento anterior. Um usuário hábil desta técnica, tal como Atsui, é capaz de cercar sua área imediata com uma parede circular de chamas, incinerando alvos múltiplos, mesmo se eles se aproximam de ângulos numerosos simultaneamente.", "Médio alcance", null, null, "雲流火炎斬りくもりゅうかえんぎり", "Kumoryū Kaengiri", "Decapitação de Chamas do Estilo Nuvem", null, "Estilo de Esgrima da Nuvem: Corta-Chamas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Decapitação de Iai", "Aderindo aos princípios de Iaidō, o usuário executa um golpe rápido e poderoso, com sua espada para cortar o alvo pretendido, antes de usar imediatamente a lâmina depois. Quando usado por Mifune, a técnica era forte o suficiente para cortar a grande cabeça da salamandra de Hanzō, Ibuse.", "Curto alcance", null, null, "居合斬り", "Iaigiri", "Decapitação Iai", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Defesa Absoluta Rígida Final: Escudo de Shukaku", "Esta técnica defensiva é usada por Gaara para criar uma réplica do brinquedo de Shukaku usando um chapéu de aba larga rural, que é aproximadamente três vezes o tamanho de Gaara. Porque a réplica é formada sob extrema pressão e é construída dos mais fortes minerais que Gaara poderia derivar da terra, é praticamente indestrutível, tendo ainda de ser penetrado por qualquer força. Como o nome indica, esta é a mais forte defesa de Gaara.", "Curto alcance", null, null, "最硬絶対防御・守鶴の盾さいこうぜったいぼうぎょ・しゅかくのたて", "Saikō Zettai Bōgyo: Shukaku no Tate", "Defesa Absoluta Rígida Final: Escudo de Shukaku", null, "Defesa Absoluta: Escudo de Shukaku", "Suprema Defesa: Escudo de Shukaku");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deflexão", "É um movimento que permite defletir ataques diretos ao empurrar o braço de seu oponente para o lado e desviar a rota do ataque, podendo inclusive desferir um contra-ataque. Com isso é possível se defender de dois ataques simultaneamente e aplicar o uso do Punho Gentil.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Denominação de Oito Trigramas Onda de Palmas", "Hinata usa um Punho Gentil com as duas mãos e ataca seu oponente várias vezes de todos os ângulos, antes de correr até ele e liberar o chakra de suas mãos, golpeando o oponente e drenando seu chakra.", "Curto alcance", null, null, "八卦宗掌波はっけしゅうしょうは", "Hakkeshū Shōha", "Denominação de Oito Trigramas Onda de Palmas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Derradeira Dispersão de Veneno", "Saiken, ou Utakata Totalmente transformado na besta, se espreme, liberando um poderoso gás venenoso em torno de si, contaminando todos à sua volta.", "Curto alcance", null, null, "極毒散", "Kyokudokusan", "Derradeira Dispersão de Veneno", "Ultra Dispersão de Veneno", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desencadeamento da Hiramekarei", "O Desencadeamento da Hikamerarei é uma habilidade que permite armazenar chakra na Hiramekarei a fim de aumentar seu poder ofensivo.", null, null, null, "ヒラメカレイ解放", "Hiramekarei Kaihō", "Liberação da Hiramekarei", null, "Liberar Hiramekarei", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenho de Imitação da Super Besta", "Esta é uma técnica que permite ao usuário animar os desenhos feitos a partir do método de pintura de Konoha de forma como se a tinta ganhasse vida e faz com que os desenhos ajam conforme as vontades do usuário, tornando-a extremamente versátil.", "Todos os alcances", "Rank B", "Ativar: Selo de Confronto,Dissipar: Selo de Confronto ou Cobra[2]", "超獣偽画ちょうじゅうぎが", "Chōjū Giga", "Desenho de Imitação da Super Besta", "Arte Ninja: Pergaminho das Réplicas de Besta", "Desenho das Superferas", "Arte Ninja: Pergaminho das Super Bestas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenho de Imitação da Super Besta: Esboço Branco", "Sai utiliza a técnica Desenho de Imitação da Super Besta para criar três leões e um grande falcão. Os leões atingem o oponente rapidamente e lançam-o para cima, onde o falcão paira e dispara uma rajada de penas em forma de navalhas super afiadas de tinta no oponente, cobrindo-o e jogando-o de volta para o chão.", "Curto a Médio alcance", null, null, "超獣偽画・白描ちょうじゅぎが・はくびょう", "Chōjū Giga: Hakubyō", "Desenho de Imitação da Super Besta: Esboço Branco", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenho de Imitação da Super Besta: Uma Centena de Demônios Vagantes na Noite", "Sai usa sua técnica de desenhos para criar centenas de leões que caem sobre o oponente como uma chuva de desenhos. Em seguida, Sai produz um dragão gigante, causando sérios danos em seu oponente e deixando-o atordoado por alguns segundos.", "Longo alcance", null, null, "超獣偽画・百鬼夜行", "Chōjū Giga: Hyakki Yakō", "Desenho de Imitação da Super Besta: Uma Centena de Demônios Vagantes na Noite", "Centenas de Demônios nas Paradas da Noite", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenho de Imitação do Super Deus", "O Desenho de Imitação do Super Deus é uma versão melhorada do Desenho de Imitação da Super Besta.", "Curto a Médio alcance", "Rank B", null, "超神偽画ちょうしんぎが", "Chōshin Giga", "Desenho de Imitação do Super Deus", "Arte Ninja: Pergaminho da Super Besta: Punho Alfa Ômega", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenrolar da Teia de Aranha", "Uma teia que captura e contém todos os inimigos que atacam de frente. Kidōmaru pega seu inimigo desprevenido por liberá-la enquanto estiver no ar. Por rapidamente espalhar a bola da linha com seus seis 'pernas' da teia abrange uma grande área. Os fios imbuídos de chakra não podem ser cortados até mesmo por uma adaga. As linhas pegajosas e elásticas são tão fortes que até mesmo um elefante puxando em cada extremidade não quebra-as. O inimigo é incapaz de mover um músculo, quanto mais voltar a lutar. Uma vez capturado, o seu destino está nas mãos de Kidōmaru. Além disso, de acordo com Kidōmaru, ele pode conter mais de cinco toneladas. No entanto, uma vez que a teia é trabalhada a partir do chakra, ela pode ser destruída por técnicas refinadas imbuídas de chakra, como o Punho Gentil, e as chamas intensas do Amaterasu.", "Curto a Médio alcance", null, null, "蜘蛛巣開", "Kumosōkai", "Desenrolar da Teia de Aranha", null, "Morada das Aranhas", "Arte Ninja: Espiral de Teia de Aranha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desenrolar da Teia de Areia", "Gaara cria uma grande teia de areia que ele atira contra seus inimigos para capturá-los. A teia também serve como suporte para seus aliados caminharem em cima.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deserto: Mão", "Gaara usa sua areia para criar mãos, que podem ser usadas para vários fins, incluindo a trituração, captura e contenção de inimigos e outros alvos. O brilho desta técnica é a força e o tamanho ou o volume pelo qual Gaara cria; com seu tamanho que rivaliza com a de uma besta com cauda. Uma vez que os braços capturam ou bloqueiam seu alvo, é quase impossível de se libertar, e deve recorrer a outros métodos. Esta técnica foi forte o suficiente para parar completamente as correntes de chakra da Estátua Demoníaca do Caminho Exterior.", null, null, null, "砂漠・叛奴", "Sabaku: Hando", "Deserto: Rebelião do Escravo", null, "Mãos de Areia", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deslizamento da Grande Bola Explosiva", "Konohamaru utiliza um pergaminho para invocar uma grande bola coberta de papéis explosivos, a qual ele chuta, e em seguida, lança-a até seu oponente, explodindo ao entrar em contato.", "Curto a Médio alcance", null, null, "起爆大玉転がしきばくおうだまくろがし", "Kibaku Ōdama Korogashi", "Deslizamento da Grande Bola Explosiva", "Deslizamento da Esfera de Bomba Gigante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deslizamento do Modo da Cobra", "Orochimaru transforma a metade inferior de seu corpo em uma cauda de serpente, o que aumenta a sua velocidade e agilidade. Uma versão alternativa é Orochimaru derramar sua pele, revelando um novo corpo que já tem uma cauda de serpente.", null, null, null, null, null, null, null, null, null);
