INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Destruição Bruta", "Jirōbō acerta uma cotovelada no rosto do oponente, deixando-o vulnerável, então ele o segura e arremessa-o no chão, causando um impacto de pequeno porte. Após isso, ele agarra o oponente pelo o rosto e o arrasta por uma curta distância, até que finalmente finaliza o jogando para longe.", "Médio alcance", null, null, null, null, null, "Destruição Bruta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Destruição de Bombas Desreguladas", "O usuário expele balões de chiclete com bombas em direção ao seu alvo, que explodem quando rompidas. A explosão de uma pequena esfera é poderosa o suficiente para deixar um alvo inconsciente com o impacto. Essa técnica também é útil para manter os oponentes à distância, uma vez que quando usados em grande quantidade, os balões explosivos impedem que eles se aproximem do usuário.", "Curto a Médio alcance", null, null, "破不流爆弾はふりゅうばくだん", "Hafuryū Bakudan", "Destruição de Bombas Desreguladas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Destruição do Conto de Fadas", "Este é uma konbijutsu onde Naruto peida e Sakura se enfurece (chamando sua Sakura Interior), que causa grande dano ao adversário e também o envenena por causa do peido de Naruto.", "Curto a Médio alcance", null, null, "メルヘン崩し", "Meruhen Kuzushi", "Destruição do Conto de Fadas", "Evasão do Conto de Fadas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desvio da Pétala: Corda de Chakra", "Ino corre para a frente do adversário e desaparece em uma cortina de pétalas roxas. Ela aparece atrás do adversário e concentra seu chakra em uma corda de pétalas, então atinge o adversário com um soco poderoso.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Desvio da Pétala: Fogos de Artifício da Flor", "Ino corre para a frente do adversário e desaparece em uma cortina de pétalas roxas. Ela aparece atrás do adversário e concentra seu chakra em uma corda de pétalas, em seguida, ela chuta o adversário e joga uma kunai anexada a um papel explosivo nele.", "Curto alcance", null, null, null, null, null, null, null, null);
