INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus Demônio", "Kaguya libera uma enxurrada de lanças negras que se ramificam de forma irregular, cobrindo uma vasta área, o que torna a fuga quase impossível. Caso o oponente consiga escapar das agulhas, Kaguya ainda é capaz de manifestar mãos negras para golpeá-los.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus Dragão da Folha", "Esta é a melhor técnica de taijutsu criada pelo lendário mestre de taijutsu, Chen. É iniciada por um pontapé giratório para cima no ponto em que ele rapidamente se torna um tornado. Formando um dragão com longos bigodes, ele ataca o alvo com o objetivo de engoli-los, no momento em que o alvo está dentro dele, ele é cortado pelos ventos nítidos no interior do ataque. Após o início do ataque, o utilizador permanece protegido no interior na base da furacão, pois a superfície exterior do dragão é suficientemente sólida para que uma pessoa bata e seja jogada para longe, fazendo com que a única maneira de entrar na técnica, é sendo engolido.", "Curto a Médio alcance", null, null, "木ノ葉龍神このはりゅうじん", "Konoha Ryūjin", "Dragão Divino da Folha da Árvore", null, null, "Dragão Divino da Folha");
