INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus: Natividade de um Mundo de Árvores", "Após o Tsukuyomi Infinito ser ativado, o usuário manipula as raízes tanto da Árvore Divina ou da forma de árvore do Dez-Caudas, espalhando elas ao redor de todo o mundo para envolver aqueles que estão presos dentro do genjutsu. Por enganarem todos os seres vivos e conectá-los à árvore, semelhante a casulos, isso vai manter seus corpos agora adormecidos vivos por toda a eternidade. Ao mesmo tempo, este assimila cada vítima da Árvore Divina, reagrupando o chakra em um único local, para brotar um novo fruto.", "Todos os alcances", "Rank –", "Ativar: Cobra,Liberar: Rato ou Juntar as Maos[2][Nota 1]", "神・樹界降誕しん・じゅかい こうたん", "Shin: Jukai Kōtan", "Deus: Natividade de um Mundo de Árvores", "Nascimento da Floresta Divina", "Nascimento da Floresta Divina", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus da Estrela de Papel Ardente", "Através do Caminho Deva, Nagato aprisiona seu oponente com o Chibaku Tensei. Uma vez que a esfera está completa, Konan abraça com suas asas de papel, cobrindo-a de inúmeros papéis explosivos. Ela, então, envia um único pássaro de origami que desencadeia as explosões.", "Todos os alcances", null, null, "神の火紙星", "Kami no Hi-shi Hoshi", "Deus da Estrela de Papel Ardente", "Marte de Papel Divino", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus das Chamas", "Primeiro o usuário lança uma técnica de vento contra o inimigo, logo então ele a incendeia, criando um turbilhão de chamas que se torna ainda mais mortal.", "Curto a Médio alcance", null, null, null, null, null, "Deus das Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deus das Marés", "Após imbuir a ponta de seus dedos com chakra, Kinshiki os crava no chão e propaga toda sua energia através dele, provocando uma onda de choque que repele todos nas proximidades, bem como um tremor que destrói tudo nos arredores. Com isso ele foi capaz de demolir toda uma arena com extrema facilidade.", "Todos os alcances", null, null, null, null, null, "Deus das Marés", null, null);
