INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deutzia (Ultimate Ninja 3)", "Kimimaro ataca seu oponente com uma de suas espadas de ossos, joga ele para trás de si e oferece uma segunda pancada forte de espada de ossos, jogando o oponente para longe.", "Curto alcance", null, null, "空木", "Utsugi", "Deutzia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Deutzia (Ultimate Ninja Storm)", "Kimimaro golpeia o oponente sucessivas vezes com sua espada óssea, em seguida cria picos de ossos em seu joelho e em seus pulsos para realizar um golpe mortal. Desgastado, o oponente é derrotado por um chute forte do qual o derruba para longe.", "Curto alcance", null, null, "空木", "Utsugi", "Deutzia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dilaceração do Castelo", "Zabuza chuta seu oponente e proporciona um corte fatal com sua Kubikiribōchō, após cortá-lo, ele chuta o oponente novamente e termina com uma facada frontal, jogando seu oponente para trás ao retirar a espada do corpo do oponente.", "Curto alcance", null, null, "破城", "Hajō", "Dilaceração do Castelo", "Muralha Esmagadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Disparo de Bolhas em Massa", "Utakata libera várias bolhas de sabão, que cercam o inimigo, evitando que ele escape na próxima etapa do processo. Em seguida, Utakata entra em sua transformação completa do Saiken e começa a liberar de sua boca algumas bolhas repletas de carga negativa, formando uma Bola da Besta com Cauda translúcida, graças à mistura com seu Ninjutsu de Bolha de Sabão. Quando está completa e instável, a besta dispara contra o oponente, resultando em uma explosão de carga positiva e aniquilando-o, apenas com sobras das bolhas de carga negativa para trás.", "Longo alcance", null, null, "荷束泡沫かそくほうまつ", "Kasoku Hōmatsu", "Disparo de Bolhas em Massa", "Carga de Bolhas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Disparo do Ataque Aéreo", "A marionete de Kankurō, Karasu, voa no ar com uma modificação especial e libera uma série de mísseis em cima do oponente que explode no impacto. Uma versão avançada desta técnica pode ser usada, onde gás venenoso é adicionando nas bombas, causando um dano extra no adversário.", "Curto a Médio alcance", null, null, "空襲弾", "Kūshūdan", "Tiro de Incursão Aérea", "Ataque Aéreo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Disparo do Corpo", "Em uma velocidade sobre-humana garantida pelo seu Modo de Chakra do Nove-Caudas, Naruto propulsiona-se para o alto, aterrissando logo em seguida um soco sobre o oponente em uma velocidade estonteante.", "Todos os alcances", null, null, "渾身一発", "Konshin Ippatsu", "Disparo do Corpo", "Ataque Total", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dispositivo de Substituição Mental", "Mecha-Naruto chega dentro do alcance e forma um selo de mão que faz então um tipo desconhecido de chakra passar pelas suas mãos e pelo seu adversário; por sua vez trocando suas mentes. O usuário desta técnica é capaz de soar exatamente como e usar qualquer técnica que o original poderia. O usuário também é capaz de usar essa técnica mesmo quando dentro do corpo do oponente.", "Curto alcance", null, "Passaro", null, null, null, "Dispositivo de Substituição Mental", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dissipação de Genjutsu", "A Dissipação de Genjutsu é utilizada para libertar a si ou à outras pessoas de um genjutsu.", "Curto alcance", null, "Tigre", "幻術解げんじゅつかい", "Genjutsu Kai", "Dissipação de Técnica Ilusória", "Quebra de Genjutsu (幻術破り, Genjutsu Buri)[1]", "Kai: Cancelar", "Libertar");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Divisão da Ruína Rotacional", "Darui pula no ar, corta seu inimigo com sua espada, realiza uma sequência de dois chutes para levantá-lo e finaliza com um poderoso corte rotacional, jogando seu oponente no chão.", "Curto alcance", null, null, "廻滅斬り", "Kaimetsugiri", "Divisão da Ruína Rotacional", "Corte da Ruína Rotacional", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Divisão de Chão", "Com o Modo Sábio ativado, Naruto bate o pé no chão, fazendo-o criar uma rachadura gigante.", "Curto alcance", null, null, "地面割り", "Jimenwari", "Divisão de Chão", "Divisor de Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Divisão de Henon", "Lançando o oponente no ar, Sarada salta e finaliza-o com um golpe de seu calcanhar, mandando-o ao chão e gerando uma rachadura no solo.", "Curto alcance", null, null, "淡竹割", "Hachikuwari", "Divisão de Henon", "Corte do Bambu Preto", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Divisão de Terra", "Sakura soca o chão usando a técnica Impacto da Flor de Cerejeira, causando muitas pedras, enviando seu oponente para o ar. Ela então, salta sobre as pedras para alcançar o seu oponente no ar e depois soca-lo através de uma pedra na frente deles, enviando-lhe para bater em dois pilares de pedras pequenas e em um muro de pedra gigante.", "Curto a Médio alcance", null, null, "大地割り", "Daichiwari", "Divisão de Terra", "Costeleta da Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Divisão de Terreno da Transformação Sábia", "Após ativar parcialmente a Transformação Sábia, Jūgo bate com um soco no chão, fazendo a terra entrar em erupção até o alvo pretendido. Essa técnica também pode criar efetivamente barricadas capazes de parar ondas gigantes.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Doki: Cego", "Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki para atacar o inimigo em um ataque giratório seguido de um forte pontapé.", "Longo alcance", null, null, "怒鬼・縛どき・ばく", "Doki: Baku", "Demônios Furiosos: Cego", "Doki: Cego", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Doki: Clava", "Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki, fazendo ele desferir com sua enorme clava, dois golpes consecutivos no oponente.", "Médio alcance", null, null, "怒鬼・棍", "Doki: Kon", "Demônios Furiosos: Clava", "Doki: Porrete", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Doki: Garra", "Utilizando a Flauta Demoníaca, Tayuya invoca e manipula um de seus Doki, fazendo ele desferir dois golpes consecutivos com suas garras no oponente.", "Médio alcance", null, null, "怒鬼・爪", "Doki: Tsume", "Demônios Furiosos: Garra", "Doki: Garra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dragão Gêmeo", "Com sua tremenda força física, o usuário dá um soco destruidor no solo, gerando grande impacto.", "Curto alcance", null, null, "双龍そうりゅう", "Sōryū", "Dragão Gêmeo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dragão de Fumaça", "Desencadeando um fluxo contínuo de fumaça de seu Lançador de Pulso, Kihō pode usar seu chakra para moldar a fumaça na forma de um dragão ligado a seu lançador de pulso. Isto dá-lhe a vantagem de ataques de curto alcance à médio alcance, já que o dragão é totalmente composto por fumaça, ataques físicos são inúteis contra ele. Ele pode formar vários dragões de fumaça de uma vez só. Sua habilidade de batalha principal é envolver e sufocar o alvo. No entanto, como a fumaça é dissolvida quando entra em contato com a água, esta técnica é fraca contra ataques à base de água.", "Curto à Médio Alcance", null, null, "煙龍", "Enryū", "Dragão de Fumaça", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dramingo", "Quando transformado em Son Gokū, Rōshi começa a bater em seu peito, causando ondas de choque que atingem o seu oponente ferozmente.", "Curto alcance", null, null, "怒羅民猴", "Doraminkō", "Gaze Revoltada dos Homens Símios", "Dramingo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Drenagem de Chakra", "A Drenagem de Chakra é uma técnica utilizada através do Caminho Preta, que permite absorver o chakra do oponente através do contato físico e repor as suas próprias reservas.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dupla Calma de Kusanagi", "Sasuke primeiro atinge o oponente no ar. Orochimaru cospe sua Kusanagi para Sasuke, e ele corta o oponente no caminho. Sasuke pega a Kusanagi com a mão livre e pula em direção ao oponente, cortando-o repetidamente com espadas dele e Orochimaru.", "Curto a Médio alcance", null, null, "二凪の草薙", "Futanagi no Kusanagi", "Dupla Calma da Espada Ceifadora de Grama", "Dupla Kusanagi", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dupla Pantera Negra", "Depois de criar um clone de si mesmo, Darui e seu clone simplesmente usam a técnica Liberação de Relâmpago: Pantera Negra para atacar seu inimigo.", "Longo alcance", null, null, "ダブル黒斑差だぶるくろぱんさ", "Daburu Kuropansa", "Dupla Diferença Manchada de Preto", null, null, "Panteras Negras Duplas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ecdise", "Trata-se de uma forma única de substituição criada por Orochimaru, a fim de eliminar os danos de um corpo através da troca de pele.", null, null, null, "脱皮だっぴい", "Dappī", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Eco da Broca Ressonante", "Esta técnica pode ser usada como um termo geral para a maioria das técnicas de Dosu. O Alto-Falante Ressonante de Eco, um dispositivo de metal poroso localizado em seu braço, é usada para amplificar as ondas de som nos movimentos do braço para atacar seu oponente. Através da geração de som suficiente, Dosu pode usar seu chakra para reorientar as ondas no ar até seu alvo. Por danificar o ouvido interno da vítima, a vítima gradualmente fica sem equilíbrio e sente náuseas. No primeiro caso, Rock Lee fica fisicamente doente pelo ataque.", "Curto alcance", "Rank C", "Selo do Confronto", "響鳴穿きょうめいせん", "Kyōmeisen", "Eco da Broca Ressonante", "Eco da Broca do Som[2]", null, "Broca de Som Vibratória");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Efêmera", "A Efêmera é uma técnica de infiltração, o que é uma característica de assinatura do Zetsu Branco e de seus irmãos clones, devido a serem infundidos com as células de Hashirama Senju.", "Todos os alcances", "Rank –", null, "蜉蝣かげろう", "Kagerō", "Efêmera", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Efêmero", "Esta técnica permite que Itachi coloque o seu adversário dentro de um genjutsu usando seu dedo como um meio. Isto induz o genjutsu no alvo. Quando a técnica foi utilizada em Naruto Uzumaki, os corvos reuniram-se na forma de Itachi e se transformaram em shuriken depois que ele realizou o selo carneiro, bombardeando Naruto e, em seguida, tentaram colocá-lo para dormir. Quando ele resistiu o comando sono, Naruto então experimentou a sensação de partes de seu corpo se transformando em seus amigos mais próximos que então o torturaram psicologicamente. Isso está implícito sendo um estágio extra do genjutsu causado por Itachi depois de realizar o selo carneiro. O mesmo vai junto com seu uso de shuriken.", "Curto alcance", null, null, "泡沫", "Utakata", "Efêmero", "Genjutsu: Bolhas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Elefante do Anoitecer", "O Elefante do Anoitecer é um taijutsu proibido de alto nível, que pode ser realizado por aqueles usuários que abriram todos os Oito Portões.", "Curto alcance", "Rank A", null, "夕象せきぞう", "Sekizō", "Elefante do Anoitecer", null, "Elefante da Noite", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Emissão de Chakra", "A Emissão de Chakra é uma técnica defensiva de fluxo de chakra.", null, null, null, "チャクラ放出", "Chakura Hōshutsu", "Emissão de Chakra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Encadernação da Corda do Xarope de Amido", "Depois de moldar o chakra no estômago e converter em água de viscosidade elevada, o usuário a expele para fora, em forma de cordas que restringem os movimentos do adversário. Devido a viscosidade da água, torna-se mais difícil livrar-se da técnica.", "Curto a Médio alcance", null, null, "水飴縛縄", "Mizuame Bakujō", "Encadernação da Corda do Xarope de Amido", null, null, "Corda Entrelaçadora do Xarope de Amido");
