INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Encontro de Cobras", "Se Orochimaru é de alguma forma cortado pelo adversário, cobras saem das metades que foram separadas e se juntam de volta.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Enganação da Chama Explosiva", "Obito lança seu gunbai contra o oponente à uma média distância, puxa o gunbai com o oponente para cima e, enquanto o oponente cai, Obito lança kunai com papéis explosivos anexos, que explodem ao impacto.", "Curto alcance", null, null, "籠絡の爆炎ろうらくのばくえん", "Rōraku no Bakuen", "Enganação da Chama Explosiva", "Explosão da Chama Tentadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Enrolamento Adjacente", "Sendo uma técnica utilizada apenas por cobras, elas ganham a capacidade de enrolar-se em seu alvo a fim de prendê-lo e, por fim, morde-o, incapacitando-o por algum tempo. Esta técnica mostrou-se ser uma técnica muito forte, já que o alvo é preso entre o corpo resistente de uma cobra. Apesar da resistência da técnica, Katsuyu conseguiu sair da incapacitação a partir da sua técnica de divisão.", "Curto alcance", null, null, "巻きつき", "Makitsuki", "Enrolamento Adjacente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Enrolamento em Tentáculos", null, "Médio alcance", null, null, "触手絡め", "Shokushukarame", "Enrolamento em Tentáculos", null, null, null);
