INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Entidades de Chakra", "Uma técnica transmitida na sua família, Gekomatsu cria cópias de chakra da vida shinobi ou falecido, como Jiraiya e Orochimaru. Apenas aqueles que possuem as contas especiais de chakra pode ver as entidades de chakra criados.", null, null, null, null, null, null, "Entidades de Chakra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Entrada Dinâmica", "A Entrada Dinâmica consiste em um movimento de taijutsu em que o objetivo é basicamente que o usuário execute um chute direcionado ao rosto do oponente impulsionado por um salto. Might Guy usa uma arma, como uma kunai, para distrair o inimigo enquanto ele tira vantagem de seu ponto cego, com o intuito de acertar seu golpe. É obviamente difícil de combater, pois foi capaz de pegar Jiraiya com sua guarda baixa.", "Curto alcance", "Rank D", null, "ダイナミック・エントリー", "Dainamikku Entorī", "Entrada Dinâmica", null, null, null);
