INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo de Areia", "Esta é uma técnica única para Gaara, desencadeada pela vontade de sua mãe, Karura.", "Curto alcance", "Rank –", null, "砂の盾すなのたて", "Suna no Tate", "Escudo de Areia", "Escudo Robusto (堅牢な盾, Kenrōnatate)[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo de Areia de Ferro", "Usando a Areia de Ferro, o usuário cria uma barricada espessa à sua frente, a fim de bloquear ataques frontais. Esta capacidade é forte o suficiente para proteger até mesmo contra um forte ataque do Terceiro Kazekage.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo de Aço", "O usuário levanta um escudo de aço a partir do chão, na forma de uma parede defensiva. O escudo é poderoso o suficiente para aguentar uma grande quantidade de balas de areia.", "Curto alcance", null, null, null, null, "Escudo de Aço", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo de Inseto", "Tirando proveito de sua proficiência com insetos, o usuário pode criar um escudo forte o suficiente para resistir à queda de vários entulhos ou suportar ataques baseados em fogo. O escudo pode ser usado para defender tanto o usuário da técnica quando aliados nas proximidades.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo de Pó de Ouro", "Usando seu Pó de Ouro, o quarto Kazekage eleva o pó para cima dele para criar uma barricada espessa, a fim de bloquear a entrada de ataques aéreos, usando gestos com as mãos para ditar a forma que a barreira adota. Esta capacidade é forte o suficiente para proteger até mesmo contra o Granizo de Areia de Gaara.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera Espiral do Medo", "Menma cria um Rasengan de chakra negativo e o rodeia com um anel em forma de disco adicional de chakra, que orbita a esfera principal, fazendo-a se assemelhar a um pequeno anel planetário. Ele corresponde ao Rasenshuriken de Naruto no Modo Sábio.", "Curto alcance", null, null, "螺旋輪虞らせんりんぐ", "Rasenringu", "Esfera Espiral do Medo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera Gigante", "O usuário forma uma grande esfera de energia da cor de seu chakra a partir de alguma de suas extremidades para atirar em seu inimigo. Um único golpe forçou Gamabunta a recuar e a explosão resultante é maior do que o próprio Hōzukijō.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera Protetora de Luz", "Esta é uma técnica em que o Sino de Segurança Místico de Shion ilumina e nega qualquer ocorrência negativa. Ela era poderoso o suficiente para proteger Shion da escuridão do Mōryō.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera da Busca da Verdade", "As Esferas da Busca da Verdade são esferas negras maleáveis de chakra no qual os usuários podem alterar sua forma e obter diversos tipos de efeitos de acordo com a sua vontade.", "Todos os alcances", "Rank –", null, "求道玉ぐどうだま", "Gudōdama", "Esfera da Busca da Verdade", null, "Esferas do Caminho da Verdade", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera da Busca da Verdade Expansiva", "A Esfera da Busca da Verdade Expansiva é um ninjutsu de Kekkei Mōra usado por Kaguya Ōtsutsuki. Ela é composta pelas cinco transformações básicas da natureza e a Liberação de Yin–Yang.", "Todos os alcances", "Rank –", null, "膨張求道玉ぼうちょうぐどうだま", "Bōchō Gudōdama", "Esfera da Busca da Verdade Expansiva", "Esfera da Busca da Verdade Final", "Esfera Expandida do Caminho da Verdade", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera da Maldição da Marionete", "Toneri molda seu chakra na forma de uma esfera de cor verde. A esfera pode ser usada para paralisar ou atordoar um alvo, ou realizar uma lavagem cerebral e controlar qualquer alvo à curta distância.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Absorção Espiral", "Naruto enquanto no Modo de Chakra do Nove-Caudas, usa seus braços de chakra para formar uma Grande Bola da Esfera Espiral, em sua mão, em seguida, joga-o acima de suas costas. Ele então ele usa os braços de chakra extras para agarrar e esmagar os inimigos na esfera, simulando uma 'absorção' das vítimas para o Rasengan.", "Curto alcance", null, null, "螺旋吸丸", "Rasenkyūgan", "Esfera de Absorção Espiral", null, "Espiral Sugadora", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Luz", "O usuário molda seu chakra na palma da mão, formando uma pequena bola de luz que paira sobre ela. A esfera serve como uma lanterna, melhorando a visibilidade. O usuário, por sua vez, pode controlar seu chakra, variando a intensidade da luz ou até mesmo apagá-la.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Odor", "O usuário cospe uma esfera de sopro roxa que se estoura e forma uma nuvem fedorenta, cobrindo uma área extensa. Qualquer um que entrar em contato com este gás fica paralisado por um curto período de tempo. Além disso, a técnica pode impedir o uso do olfato de suas vítimas por um período de tempo mais prolongado, como visto o acontecido com Kiba Inuzuka e Akamaru. O odor é devido à fermentação ocorrida dentro do corpo do usuário.", "Curto a Médio alcance", null, null, "スメル玉", "Sumerudama", "Esfera de Odor", null, null, "Arte Ninja: Esfera de Odor");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Relâmpago", "O usuário cria esferas de relâmpago para lançá-las contra o inimigo. Ao entrar em contato com qualquer corpo, as esferas dispõem de uma grande carga elétrica, mesmo capaz de matar. Dependendo da finalidade utilizada, pode-se criar múltiplas esferas em vez de somente uma. O uso também pode ocorrer por meio das Espadas Gêmeas.", "Curto a Médio alcance", null, "Passaro → Cobra → Macaco → Carneiro[1]", "雷球らいきゅう", "Raikyū", "Esfera de Relâmpago", null, null, "Bola de Raio");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Água", "Utilizando o Kote, Boruto lança uma esfera feita de água que paira no ar e lança outras várias esferas de água menores contra o oponente, como se fossem projéteis, infligindo dano no inimigo.", "Curto a Médio alcance", null, null, "水球すいきゅう", "Suikyū", "Esfera de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera de Água Sensorial", "Esta esfera, feita de água, pode ser utilizada para monitorar as assinaturas de chakra numa dada área. Indivíduos ou grupos são representados por variáveis portes de bolhas dentro da esfera, com assinaturas de chakra maiores suas bolhas serão maiores. A esfera em si não é uma ótima forma de detecção e precisa de relatórios do campo para localizar alvos que devem ser mantidos a par. Quando ligado a uma Barreira Sensorial, essa imprecisão é atenuado um pouco.", "Longo alcance", null, null, "感知水球かんちすいきゅう", "Kanchi Suikyū", "Esfera de Água Sensorial", null, "Técnica do Radar Sensorial", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esferas Espirais Desordenadas", "Naruto, enquanto controlando o chakra de Kurama, forma braços de chakra para formar múltiplos Rasengan. Ele pode, então, atingir vários oponentes ao mesmo tempo segurando o Rasengan com os braços de chakra.", "Curto alcance", null, null, "螺旋乱丸らせんらんがん", "Rasenrangan", "Esferas Espirais Desordenadas", "Múltiplas Esferas Espirais", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esforço da Estrela Especuladora", "Might Guy abre vários portões de chakra e, em seguida, salta pro céu em forma de estrela, caindo rapidamente e atingindo seu oponente, criando uma enorme explosão e destruição no local.", "Curto alcance", null, null, "噂の努力星うわさのどりょくしょう", "Uwasa no Doryokushō", "Esforço da Estrela Especuladora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esguicho do Mar Agitado", "Yagura empunha seu bō e golpeia para baixo, inundando o ambiente. Com o cenário transformado em um enorme lago, o Mizukage afunda junto com o oponente, envolve-se em um turbilhão de água, afastando-o e transformando-se no Isobu, que retrai suas patas e nada em alta velocidade, com o apoio de jatos d'água que saem dos orifícios da casca, onde ficavam as patas, e cabeceando o inimigo para fora da água. Com o adversário no ar, Isobu então cospe um poderoso jato de água diretamente nele.", null, null, null, "荒海飛沫", "Araumi Shibuki", "Esguicho do Mar Agitado", "Espuma do Mar Bravo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esmagar", "O usuário pula sobre o oponente esmagando-o com sua barriga. O usuário ainda pode aproveitar-se do movimento de mola, proporcionado por sua barriga flácida, para executar um combo contra o oponente.", "Curto alcance", null, null, null, null, null, "Esmagar", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Fortalecida", "Esta técnica consiste em reforçar suas lâminas com chakra afim de causar grandes danos e impacto.", "Curto a Médio alcance", null, null, "剛剣ごうけん", "Gōken", "Espada Fortalecida", "Espada Forte", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Gêmea Hiramekarei: Martelo", "Chōjūrō flui seu chakra através da Hiramekarei e aplica a transformação da forma, criando um martelo gigante. Com isso, ele pode acertar os seus alvos com força o suficiente para fazê-los voar através de uma distância considerável, atravessando paredes, mesmo com uma forte defesa como o Susanoo.", "Curto alcance", null, null, "双刀ヒラメカレイ・ハマー", "Sōtō Hiramekarei: Hamā", "Espada Gêmea Hiramekarei: Martelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Ninja dos Fūma: Zanbatō", "Hanzaki salta em direção ao seu inimigo e desfere um poderoso corte descendente com sua zanbatō.", "Curto alcance", null, null, "ふうま忍剣・斬馬刀ふうまにんけん・ざんばとう", "Fūma Ninken: Zanbatō", "Espada Ninja dos Fuuma: Zanbatou", null, null, "Espada Ninja do Vento Demoníaco: Zambatou");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Relâmpago", "Kakashi joga o oponente no ar, onde Yūgao chuta-o e em seguida, ataca-o com sua espada, logo Kakashi finaliza com o Corte Relâmpago.", "Curto Alcance", null, null, "雷剣", "Raiken", "Espada Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Relâmpago Instantânea", "Minato corre em alta velocidade que até parece um desfoque e bate-o quando ele chega ao lado do seu oponente. Em seguida, ele para atrás do oponente e cortá-o com uma kunai.", "Curto alcance", null, null, "瞬雷刃", "Shunraijin", "Espada Relâmpago Instantânea", "Espada Instantânea do Trovão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Rotativa Voadora", "O usuário joga a Kubikiribōchō em seu oponente, causando dano à medida que gira em direção a ele. Se a Kubikiribōchō ficar presa em algo ou acerta o inimigo, o usuário pode ficar sobre ela.", "Longo alcance", null, null, "飛旋剣", "Hisenken", "Espada Rotativa Voadora", "Turno do Corte Voador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Secreta: Corte Nevasca", "Tsubaki canaliza seu chakra através de sua katana ainda embainhada e então ela realiza um ataque de battōjutsu — o desembainhamento da espada com o fito de executar um corte. A partir disso, ela desencadeia uma poderosa onda de choque que culmina na formação de um tornado maciço ao seu redor, repelindo tudo o que estiver nas proximidades. A destruição é ainda maior quando ela desembainha a espada completamente, mas o custo de chakra é maior.", "Todos os alcances", null, null, "秘剣・風雪斬り", "Hiken: Fūsetsugiri", "Espada Secreta: Corte Nevasca", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada Secreta: Luar", "Hayate corre em direção ao seu adversário com um golpe de espada, deixando um rastro de imagem posterior.", "Curto alcance", null, null, "秘剣・月影", "Hiken: Tsukikage", "Espada Secreta: Luar", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada: Lâmina de Vento", "Ashura concentra chakra em sua espada e libera rapidamente contra o inimigo em forma de uma pequena esfera de vento com capacidades cortantes.", "Curto alcance", null, null, "一刀・風刃いっとう・ ふうじん", "Ittō: Fūjin", "Espada: Lâmina de Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Assassinato Relâmpago", null, "Curto alcance", null, null, "暗殺雷光剣あんさつらいこうけん", "Ansatsu Raikōken", "Espada de Assassinato Relâmpago", "Lâmina Assassina Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Chakra: Grande Ferida de Interseção", "Shizune usa Tonton para acertar o oponente, dando-lhe tempo suficiente para usar suas duas espadas de chakra para cortar e matar o oponente. As barras deixam duas grandes marcas no chão.", null, null, null, "チャクラ剣・大交衝", "Chakura Ken: Daikōshō", "Espada de Chakra: Grande Ferida de Interseção", "Espada de Chakra: Grande Cruzamento de Golpe", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kagutsuchi", "A Espada de Kagutsuchi é uma espada de chamas negras empunhada por Sasuke Uchiha.", "Curto alcance", null, null, "加具土命の剣カグツチのつるぎ", "Kagutsuchi no Tsurugi", "Espada da Ferramenta Adicionada do Senhor da Terra", "Espada de Controle de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kagutsuchi: Turbilhão de Chamas", "Sasuke manifesta seu Corpo Completo — Susanoo e executa uma série de cortes com a Espada de Kagutsuchi, desencadeando um turbilhão de chamas negras que engolem seu adversário.", "Curto alcance", null, null, "加具土命の剣・渦炎カグツチのつるぎ・うずえん", "Kagutsuchi no Tsurugi: Uzuen", "Espada da Ferramenta Adicionada do Senhor da Terra: Turbilhão de Chamas", "Espada do Controle de Chamas: Redemoinho de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi: Espada Longa do Céu", "Esta é uma técnica que permite que Orochimaru possa levitar a Espada de Kusanagi e atacar os inimigos a meia-distância. A lâmina se move de acordo com os movimentos dos dedos de Orochimaru. No anime, a lâmina emite uma luz azul brilhante quando ela está levitando.", "Curto a Médio alcance", "Rank B", null, "草薙の剣・空の太刀", "Kusanagi no Tsurugi: Kū no Tachi", "Espada de Kusanagi: Espada Longa do Céu", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi: Lâmina de Chidori", "Esta técnica permite que Sasuke possa canalizar o Chidori através de sua Espada de Kusanagi. Isso torna a lâmina quase indefensável podendo facilmente cortar o aço. Além disso, se uma pessoa é esfaqueada, seu corpo fica dormente devido a uma corrente elétrica que faz com que os músculos fiquem dormentes, tornando impossível para o adversário conseguir se mover. No entanto, pode ser anulada por outras armas que também utilizam o fluxo de chakra da Liberação de Relâmpago, como as espadas usadas por Killer B.", "Curto alcance", "Rank B", null, "草薙の剣・千鳥刀くさなぎのつるぎ・ちどりがたな", "Kusanagi no Tsurugi: Chidorigatana", "Espada de Kusanagi: Lâmina dos Mil Pássaros", "Katana Chidori", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi: Lâminas de Todas as Direções", "Depois de imobilizar o alvo com uma cobra, Orochimaru invoca várias réplicas de sua espada de Kusanagi que atacam o inimigo de todas as direções.", "Curto alcance", null, null, "草薙の剣・八方刃くさなぎのつるぎ・はっぽうは", "Kusanagi no Tsurugi: Happōha", "Espada Ceifadora de Grama: Lâminas de Oito Direções", "Grama Alabarda: Lâmina de Todas Direções", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi: Um Golpe Mortal", "Orochimaru invoca uma cobra gigante, que bate no chão com sua cauda, lançando o seu alvo para o ar. Então, Orochimaru ordena para Espada de Kusanagi esfaquear o seu alvo.", "Curto alcance", null, null, "草薙の剣・一刺死くさなぎのつるぎ・いっしし", "Kusanagi no Tsurugi: Isshishi", "Espada do Corte da Grama: Um Golpe Mortal", "Alabarda da Grama: Morte em um Golpe", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Luz de Chidori", "Sasuke usa sua espada chokutō para canalizar um Chidori extremamente poderoso. Ele parece ter um alcance ainda maior do que a Corrente de Chidori, tornando-o mais eficaz em batalha. Pode notar-se que esta técnica é mais utilizada em água, porque a corrente do raio produzido é transportado em toda a superfície tornando-a mais difícil do inimigo evitar.", "Curto a Médio alcance", null, null, "千鳥光剣ちどりこうけん", "Chidori Kōken", "Espada de Luz dos Mil Pássaros", "Espada Chidori", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Nunoboko", "A Espada de Nunoboko é uma grande espada em forma de dupla hélice, formada a partir da Esfera da Busca da Verdade e originalmente usada por Hagoromo Ōtsutsuki.", "Longo alcance", null, null, "ぬのぼこの剣ぬのぼこのけん", "Nunoboko no Ken", "Espada de Nunoboko", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Relâmpago Infernal", "Saltando acima do oponente, o usuário empunha sua espada infundida com relâmpago e desfere um poderoso golpe perfurante para baixo, deixando um rastro de relâmpago para trás.", "Curto alcance", null, null, "奈落雷光剣ならくらいこうけん", "Naraku Raikōken", "Espada de Relâmpago Infernal", "Espada Relâmpago Naro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Totsuka", "A Espada de Totsuka, também conhecida como Sakegari no Tachi (酒刈太刀, Espada Longa Cortadora de Saquê), é uma variação da Espada de Kusanagi. Ela é embainhada numa jarra de saquê; a lâmina é de fato o líquido dentro do recipiente que é solto e moldado, ao invés do que uma espada convencional. Ela é uma arma etérea, com uma lâmina encantada capaz de selar qualquer coisa que ela perfure. Aqueles que são perfurados por ela são sugados para dentro da jarra e presos num “mundo de sonhos embriagados” como um genjutsu por toda eternidade; e além disso, Itachi também conseguiu remover o selo amaldiçoado de Sasuke ao perfurar e remover Orochimaru do corpo de Sasuke. Orochimaru procurou pela espada por toda sua vida e Zetsu comentou que ele nunca pôde achá-la.", "Longo alcance", null, null, "十拳剣とつかのつるぎ", "Totsuka no Tsurugi", "Espada Longa de Dez Mãos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Vácuo", "O usuário balança sua espada e uma rajada de vento é liberada no arco em que foi balançada. A técnica é forte o suficiente para parar projéteis que são lançados no usuário e mesmo cortar algo denso como uma árvore.", "Curto a Médio alcance", null, null, "真空剣しんくうけん", "Shinkūken", "Espada de Vácuo", null, null, "Lâmina de Choque");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada do Deus do Trovão: Corte", "É uma variante das técnicas usadas por Aoi Rokushō através da Espada do Deus do Trovão.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada do Deus do Trovão: Descarga Elétrica", "Uma técnica desenvolvida por Aoi Rokushō através de suas maestrias utilizando a Espada do Deus do Trovão. Com seu empunhamento, essa se tornou uma de suas técnicas assinaturas.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espadas de Liberação de Relâmpago Super-Vibrantes", "Trata-se de uma técnica de Killer B com a qual ele infunde um objeto perfurante, normalmente suas espadas, com chakra de natureza relâmpago para atacar ou arremessar em direção a seu alvo. O poder de perfuração do objeto é aprimorado devido ao aumento da frequência de vibração do chakra, ao ponto de superar o de técnicas de vento. Com isso é possível perfurar um tronco com relativa facilidade, mesmo se a arma utilizada for um lápis.", "Curto a Médio alcance", null, null, "超ビブラート雷遁刀", "Chō Biburāto Raitontō", "Espadas de Liberação de Relâmpago Super-Vibrantes", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Especialidade da Academia", "Iruka atinge o oponente com vários chutes e golpes de kunai, em seguida ele aponta o dedo para o oponente e, ficando enfurecido, pega um livro e bate com ele sobre a cabeça do oponente.", "Curto alcance", null, null, "忍者学校名物あかでみいめいぶつ", "Akademī Meibutsu", "Especialidade da Academia", "Especialidade da Academia Ninja", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espelho Demoníaco de Gelo Fino", "Trata-se da versão reduzida a um espelho a partir dos Espelhos Demoníacos de Cristais de Gelo de Haku.", "Todos os alcances", null, null, "魔鏡薄氷まきょうはくひょう", "Makyō Hakuhyō", "Espelho Demoníaco de Gelo Fino", "Espelhos dos Cristais de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espelho de Yata", "O Espelho de Yata (八咫鏡, Yata no Kagami) foi um escudo etéreo empunhado pelo Susanoo de Itachi. É dito sendo dotado de todas as cinco transformações da natureza e como tais mudanças características próprias de acordo com os do ataque que recebe, fazendo com que o ataque seja inteiramente ineficaz.", null, null, null, "八咫鏡やたのかがみ", "Yata no Kagami", "Espelho de Yata", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espelhos Demoníacos de Cristais de Gelo", "A capacidade abominável e enorme, transmitida apenas dentro do Clã Yuki. É uma técnica envolvida em muitos mistérios. Dizia-se que nenhum método de existência pode derrotar esta técnica. Em um instante, vários espelhos de gelo são criadas em torno do inimigo, refletindo nada, exceto Haku. O adversário está preso em uma cúpula de 21 espelhos flutuantes feitos de gelo. Doze permanecem ao nível do solo, oito flutuam acima dos 12 primeiro e inclinados em direção ao chão, e o espelho final é acima do resto e virado para o chão. Uma vez que Haku entrou nos espelhos, é possível para ele se mover entre os espelhos na velocidade da luz. É quase impossível de seguir ataques enviados a partir deste movimento incrível. Como todo espelho mostra seu reflexo, pode-se dizer que é impossível ver todos os ataques de Haku. Mesmo que o oponente tente atacar o corpo real, Haku já se muda para outro espelho. Sem olhos como o Sharingan, será impossível manter o controle dele.", "Curto a Médio alcance", "Rank –", "Selo de Mao Especifico da Técnica", "魔鏡氷晶まきょうひょうしょう", "Makyō Hyōshō", "Espelhos Demoníacos de Cristais de Gelo", "Jutsu Oculto: Espelhos de Cristais de Gelo", "Técnica Secreta de Espelho Demoníaco dos Cristais de Gelo", "Jutsu Secreto: Espelhos de Cristais de Gelo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espinho", "Kimimaro acerta uma série de golpes no oponente com duas espadas de ossos até que ele o envia voando no ar, onde ele acerta uma série de golpes muito rápido.", "Curto alcance", null, null, "茨いばら", "Ibara", "Espinho", null, null, null);
