INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esprei de Perfume", "Karin pulveriza uma enorme quantidade de perfume rosa que causa danos medianos ou fracos, mas também envenena o oponente no processo. Além de envenenar o alvo, ele também pode diminuir sua velocidade e defesa. Enquanto no 'Modo Despertar', Karin utiliza uma versão mais agressiva da técnica, chamada de Super Esprei de Perfume (香水スプレー超噴射, Kōsui Supurē Chōfunsha; Bandai: Super Explosão do Spray de Perfume), onde a substância é usada de forma mais concentrada contra o oponente e com maiores danos, além de atuar também como uma investida que destrói a guarda.", "Todos os alcances", null, null, "香水スプレー", "Kōsui Supurē", "Esprei de Perfume", "Spray de Perfume", null, null);
