INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Estilo Takigakure: Lâmina Decapitadora de Água", "Uma técnica secreta de Takigakure que permite o usuário manipular a água em forma de uma espada. Enquanto usado por usuários de Kenjutsu, esta técnica pode ser usada para assaltos, como foi demonstrado por Shibuki. Esta espada demonstra ser resistente e também capaz de cortar a maioria das técnicas da Liberação de Fogo.", "Curto alcance", null, null, "滝隠れ流・水斬りの刃", "Takigakureryū: Mizukiri no Yaiba", "Estilo Takigakure: Lâmina Decapitadora de Água", null, null, "Estilo Cachoeira Secreta: Corte da Espada de Água");
