INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Estrondoso Furacão de Fogo da Folha", "Rock Lee abre cinco dos oito portões e golpeia seu inimigo com um chute tão rápido que sua perna pega fogo. Lee gira no ar, criando um espiral de fogo antes de trazer a perna para baixo através do centro e para o chão. O fogo explode para fora, explodindo qualquer inimigo em estreita proximidade.", "Curto alcance", null, null, "木ノ葉轟炎疾風", "Konoha Gōen Shippū", "Estrondoso Furacão de Fogo da Folha", "Rugido do Vendaval de Chamas da Folha", null, null);
