INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Execução por Beijo", "Esta capacidade de Fūka lhe permitiu absorver todo chakra e técnicas de um oponente por beijá-lo, permitindo que ela possuísse várias afinidades elementares, bem como fazer respeitar os que ela já tem e ter acesso a todas as técnicas elementares de sua vítima. Depois que ela absorveu seu chakra, ela poderia roubar sua alma. Esta técnica também pode ser usada para determinar o que tem afinidades elementares do alvo ao lamber ele. Devido a um corpo sendo incapaz de lidar com tanto chakra, Fūka distribui o chakra ela absorveu em vários corpos, e usa o Mudança de Curso da Cor para mudar para outro, se necessário.", "Curto alcance", null, null, "死刑接吻しけいせっぷん", "Shikei Seppun", "Execução pelo Beijo", null, null, "Beijo Ceifador");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Exemplo — Fūinjutsu", "Iruka utiliza a Formação das Cordas de Luz para prender seu oponente e, em seguida, ele pega um papel explosivo e o coloca sobre a cabeça do oponente, causando uma explosão.", "Curto alcance", null, null, "模範的　封印術", "Mohanteki — Fūinjutsu", "Exemplo — Fūinjutsu", "Jutsu de Selamento Exemplar", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Exemplo — Liberação de Fogo", "Iruka acerta o oponente com vários ataques de kunai, chutes e uma cotovelada, finalizando com um chute para cima. Enquanto o oponente está no ar, Iruka lança uma rajada de fogo.", "Curto alcance", null, null, "模範的　火遁", "Mohanteki — Katon", "Exemplo — Liberação de Fogo", "Estilo Fogo Exemplar", null, null);
